trsuperobject.biblemap.put("b32_2_3", new trsuperobject(32,2,3,"Beni engine, denizin ta dibine fırlattın.Sular sardı çevremi.Azgın dalgalar geçti üzerimden."));
trsuperobject.biblemap.put("b32_2_4", new trsuperobject(32,2,4,"‹Huzurundan kovuldum› dedim,‹Yine de göreceğim kutsal tapınağını.› kutsal tapınağını nasıl göreceğim?››"));
trsuperobject.biblemap.put("b32_2_5", new trsuperobject(32,2,5,"Sular boğacak kadar kuşattı beni,Çevremi enginler sardı,Yosunlar dolaştı başıma."));
trsuperobject.biblemap.put("b32_2_6", new trsuperobject(32,2,6,"Dağların köklerine kadar battım,Dünya sonsuza dek sürgülendi arkamdan;Ama, ya RAB, Tanrım,Canımı sen kurtardın çukurdan."));
trsuperobject.biblemap.put("b32_2_7", new trsuperobject(32,2,7,"Soluğum tükenince seni andım, ya RAB,Duam sana, kutsal tapınağına erişti."));
trsuperobject.biblemap.put("b32_2_8", new trsuperobject(32,2,8,"Değersiz putlara tapanlar,Vefasızlık etmiş olurlar."));
trsuperobject.biblemap.put("b32_2_9", new trsuperobject(32,2,9,"Ama şükranla kurban sunacağım sana,Adağımı yerine getireceğim.Kurtuluş senden gelir, ya RAB!››"));
trsuperobject.biblemap.put("b32_2_10", new trsuperobject(32,2,10,"RAB balığa buyruk verdi ve balık Yunus'u karaya kustu."));
trsuperobject.biblemap.put("b32_3_2", new trsuperobject(32,3,2,"‹‹Kalk, Ninovaya, o büyük kente git ve sana söyleyeceklerimi halka bildir.››"));
trsuperobject.biblemap.put("b32_3_3", new trsuperobject(32,3,3,"Yunus RABbin sözü uyarınca kalkıp Ninovaya gitti. Ninova öyle büyük bir kentti ki, ancak üç günde dolaşılabilirdi."));
trsuperobject.biblemap.put("b32_3_4", new trsuperobject(32,3,4,"Yunus kente girip dolaşmaya başladı. Bir gün geçince, ‹‹Kırk gün sonra Ninova yıkılacak!›› diye ilan etti."));
trsuperobject.biblemap.put("b32_3_5", new trsuperobject(32,3,5,"Ninova halkı Tanrıya inandı. Oruç ilan ederek büyüğünden küçüğüne hepsi çula sarındı."));
trsuperobject.biblemap.put("b32_3_6", new trsuperobject(32,3,6,"Ninova Kralı olanları duyunca, tahtından kalkıp kaftanını çıkardı; çula sarınarak küle oturdu."));
trsuperobject.biblemap.put("b32_3_7", new trsuperobject(32,3,7,"Ardından Ninovada şu buyruğu yayımladı:  ‹‹Kral ve soyluların buyruğudur: Hiçbir insan ya da hayvan -ister sığır, ister davar olsun- ağzına bir şey koymayacak, otlamayacak, içmeyecek."));
trsuperobject.biblemap.put("b32_3_8", new trsuperobject(32,3,8,"Bütün insanlar ve hayvanlar çula sarınsın. Herkes var gücüyle Tanrıya yakararak kötü yoldan, zorbalıktan vazgeçsin."));
trsuperobject.biblemap.put("b32_3_9", new trsuperobject(32,3,9,"Belki o zaman Tanrı fikrini değiştirip bize acır, kızgın öfkesinden döner de yok olmayız.››"));
trsuperobject.biblemap.put("b32_3_10", new trsuperobject(32,3,10,"Tanrı Ninovalılar'ın yaptıklarını, kötü yoldan döndüklerini görünce, onlara acıdı, yapacağını söylediği kötülükten vazgeçti."));
trsuperobject.biblemap.put("b32_4_1", new trsuperobject(32,4,1,"Yunus buna çok gücenip öfkelendi."));
trsuperobject.biblemap.put("b32_4_3", new trsuperobject(32,4,3,"Ya RAB, lütfen şimdi canımı al. Çünkü benim için ölmek yaşamaktan iyidir.››"));
trsuperobject.biblemap.put("b32_4_5", new trsuperobject(32,4,5,"Yunus kentten çıktı, kentin doğusundaki bir yerde durdu. Kendisine bir çardak yaptı, gölgesinde oturup kentin başına neler geleceğini görmek için beklemeye başladı."));
trsuperobject.biblemap.put("b32_4_7", new trsuperobject(32,4,7,"Ama ertesi gün şafak sökerken, Tanrının sağladığı bir bitki kurdu keneotunu kemirip kuruttu."));
trsuperobject.biblemap.put("b32_4_8", new trsuperobject(32,4,8,"Güneş doğunca Tanrı yakıcı bir doğu rüzgarı estirdi. Yunus başına vuran güneşten bayılmak üzereydi. Ölümü dileyerek, ‹‹Benim için ölmek yaşamaktan iyidir›› dedi."));
trsuperobject.biblemap.put("b32_4_9", new trsuperobject(32,4,9,"Ama Tanrı, ‹‹Keneotu yüzünden öfkelenmeye hakkın var mı?›› dedi. Yunus, ‹‹Elbette hakkım var, ölesiye öfkeliyim›› diye karşılık verdi."));
trsuperobject.biblemap.put("b32_4_10", new trsuperobject(32,4,10,"RAB, ‹‹Keneotu bir gecede çıktı ve bir gecede yok oldu›› dedi, ‹‹Sen emek vermediğin, büyütmediğin bir keneotuna acıyorsun da,"));
trsuperobject.biblemap.put("b32_4_11", new trsuperobject(32,4,11,"ben Ninova'ya, o koca kente acımayayım mı? O kentte sağını solundan ayırt edemeyen yüz yirmi bini aşkın insan, çok sayıda hayvan var.››"));

