trsuperobject.biblemap.put("b34_3_3", new trsuperobject(34,3,3,"Saldıran atlılar, çakan kılıçlar,Parıldayan mızraklar, yığın yığın ölüler...  Sayısız ceset.Yürürken ayaklar takılıyor ölülere."));
trsuperobject.biblemap.put("b34_3_4", new trsuperobject(34,3,4,"Her şey o alımlı, büyücü fahişenin sınırsız ahlaksızlığından oldu.Fahişeliğiyle ulusları, büyüleriyle halkları kendine tutsak etti."));
trsuperobject.biblemap.put("b34_3_5", new trsuperobject(34,3,5,"Her Şeye Egemen RAB diyor ki,‹‹Sana karşıyım, ey Ninova!  Savuracağım eteklerini yüzüne.Uluslara çıplaklığını,  Halklara ayıp yerlerini göstereceğim."));
trsuperobject.biblemap.put("b34_3_6", new trsuperobject(34,3,6,"Seni pislikle sıvayıp rezil edeceğim.Dehşetle seyredecek herkes seni."));
trsuperobject.biblemap.put("b34_3_7", new trsuperobject(34,3,7,"Seni kim görse kaçacak.‹Harabeye döndü Ninova› diyecekler,  ‹Kim dövünecek onun için?Nereden bulalım onu avutacak birilerini?››"));
trsuperobject.biblemap.put("b34_3_8", new trsuperobject(34,3,8,"Sen No-Amondan daha mı üstünsün?O kent ki, kanallar arasındaydı,  Suyla çevrelenmişti,Kalesi Nil Irmağı, surlarıysa sulardı."));
