trsuperobject.biblemap.put("b34_3_10", new trsuperobject(34,3,10,"Öyleyken tutsak düştü, halkı sürüldü.Yavruları köşe başlarında paramparça edildi.  Soyluları için kura çekildi,Zincire vuruldu ileri gelenleri."));
trsuperobject.biblemap.put("b34_3_11", new trsuperobject(34,3,11,"Acıyla kendinden geçeceksin, ey Ninova,Düşmanlarından korunacak yer arayacaksın."));
trsuperobject.biblemap.put("b34_3_12", new trsuperobject(34,3,12,"Senin kalelerin incir ağacının ilk olgunlaşan meyvesi gibidir.Bir silkeleyişte yiyenin ağzına düşecekler."));
trsuperobject.biblemap.put("b34_3_13", new trsuperobject(34,3,13,"Askerlerine bak! Kadın gibi hepsi.Kapıların ardına kadar düşmana açık.  Ateş yiyip bitirmiş kapı sürgülerini."));
trsuperobject.biblemap.put("b34_3_14", new trsuperobject(34,3,14,"Kuşatma vakti için su biriktir kendine,Savunmanı güçlendir.  Tuğla yapmak için kili çiğne,Kalıpları hazırla."));
