trsuperobject.biblemap.put("b34_3_16", new trsuperobject(34,3,16,"Tüccarlarının sayısı gökteki yıldızlardan çok.Ama düşmanların genç çekirgeler gibi ülkeyi talan edip gidecekler."));
trsuperobject.biblemap.put("b34_3_17", new trsuperobject(34,3,17,"Koruyucularınla görevlilerin serin günlerde duvarlara konan çekirgeler gibidir,Güneş doğunca uçup kayıplara karışan çekirge sürüsü gibi."));
trsuperobject.biblemap.put("b34_3_18", new trsuperobject(34,3,18,"Ey Asur Kralı, yöneticilerin öldü,Uyudu sonsuza dek soyluların.  Halkın dağlara dağıldı.Onları toplayacak kimse yok."));
trsuperobject.biblemap.put("b34_3_19", new trsuperobject(34,3,19,"Uğradığın felaketten kurtuluş yok, yaraların ölümcül.Başına gelenleri duyanlar sevinçle el ovuşturuyorlar.  Çünkü dinmeyen vahşetinden kim kaçabildi ki?"));

