private void putmapbible37() {

trsuperobject.biblemap.put("b37_1_1", new trsuperobject(37,1,1,"Kral Dariusun krallığının ikinci yılında, altıncı ayın birinci günü RAB Peygamber Hagay aracılığıyla Şealtielin torunu Yahuda Valisi Zerubbabil ve Yehosadak oğlu Başkâhin Yeşuya seslendi:"));
trsuperobject.biblemap.put("b37_1_2", new trsuperobject(37,1,2,"‹‹Her Şeye Egemen RAB diyor ki, ‹Bu halk, RABbin Tapınağını yeniden kurmak için vakit daha gelmedi diyor.››"));
trsuperobject.biblemap.put("b37_1_3", new trsuperobject(37,1,3,"Sonra RAB, Peygamber Hagay aracılığıyla şöyle seslendi:"));
trsuperobject.biblemap.put("b37_1_4", new trsuperobject(37,1,4,"‹‹Bu tapınak yıkık durumdayken, sizin ağaç kaplamalı evlerinizde oturmanızın sırası mı?››"));
trsuperobject.biblemap.put("b37_1_5", new trsuperobject(37,1,5,"Her Şeye Egemen RAB diyor ki, ‹‹Şimdi tuttuğunuz yolları iyi düşünün!"));
trsuperobject.biblemap.put("b37_1_6", new trsuperobject(37,1,6,"Çok ektiniz ama az biçtiniz; yiyorsunuz ama doyamıyorsunuz, içiyorsunuz ama neşelenemiyorsunuz; giyiniyorsunuz ama ısınamıyorsunuz; ücretinizi alıyorsunuz ama paranızı sanki delik keseye koyuyorsunuz.››"));
trsuperobject.biblemap.put("b37_1_7-8", new trsuperobject(37,1,7-8,"Her Şeye Egemen RAB, ‹‹Tuttuğunuz yolları iyi düşünün!›› diyor, ‹‹Dağlara çıkıp kütük getirin, tapınağı yeniden kurun. Öyle ki, ondan hoşnut olayım, yüceltileyim."));
trsuperobject.biblemap.put("b37_1_9", new trsuperobject(37,1,9,"Bol ürün umdunuz ama az topladınız. Eve ne getirdiyseniz üfleyip dağıttım. Acaba neden?›› Böyle soruyor Her Şeye Egemen RAB. ‹‹Yıkık duran tapınağımdan ötürü! Oysa hepiniz kendi evinizle uğraşıyorsunuz."));
trsuperobject.biblemap.put("b37_1_10", new trsuperobject(37,1,10,"İşte bunun içindir ki, gök çiyini, toprak ürününü sizden esirgiyor."));
trsuperobject.biblemap.put("b37_1_11", new trsuperobject(37,1,11,"Ülkeyi -dağlarını, tahılını, yeni şarabını, zeytinyağını, toprağın verdiği ürünleri, insanlarını, hayvanlarını, ellerinizin bütün emeğini- kuraklıkla cezalandırdım.››"));
trsuperobject.biblemap.put("b37_1_12", new trsuperobject(37,1,12,"Şealtielin torunu Zerubbabil, Yehosadak oğlu Başkâhin Yeşu ve sürgünden dönen halkın tümü Tanrıları RABbin sözüne, Onun tarafından gönderilen Peygamber Hagayın sözlerine kulak verdiler. Halk RABden korktu."));
trsuperobject.biblemap.put("b37_1_13", new trsuperobject(37,1,13,"Sonra RABbin ulağı Hagay, RABbin şu sözlerini halka bildirdi: ‹‹RAB, ‹Ben sizinle birlikteyim› diyor.››"));
trsuperobject.biblemap.put("b37_1_14", new trsuperobject(37,1,14,"Böylece RAB Şealtiel'in torunu Yahuda Valisi Zerubbabil'i, Yehosadak oğlu Başkâhin Yeşu'yu ve sürgünden dönen halkın tümünü bu konuda harekete geçirdi. Darius'un krallığının ikinci yılında, altıncı ayın yirmi dördüncü günü gelip Tanrıları Her Şeye Egemen RAB'bin Tapınağı'nda işe başladılar."));
trsuperobject.biblemap.put("b37_2_1", new trsuperobject(37,2,1,"Yedinci ayın yirmi birinci günü RAB Peygamber Hagay aracılığıyla şöyle seslendi:"));
trsuperobject.biblemap.put("b37_2_2", new trsuperobject(37,2,2,"‹‹Şealtielin torunu Yahuda Valisi Zerubbabile, Yehosadak oğlu Başkâhin Yeşuya ve sürgünden dönen halka de ki,"));
trsuperobject.biblemap.put("b37_2_3", new trsuperobject(37,2,3,"‹Aranızda bu tapınağı önceki görkemiyle gören kaldı mı? Şimdi size nasıl görünüyor? Bir hiç olarak görünmüyor mu?"));
trsuperobject.biblemap.put("b37_2_4", new trsuperobject(37,2,4,"Şimdi sen, ey Zerubbabil, yüreklen!› RAB böyle diyor. ‹Ey Yehosadak oğlu Başkâhin Yeşu, yüreklen! Ey ülke halkı, yüreklen!› RAB böyle diyor. ‹İşi sürdürün. Çünkü ben sizinle birlikteyim.› Böyle diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b37_2_5", new trsuperobject(37,2,5,"‹Mısırdan çıktığınızda, size bu konuda söz verdim. Ruhum aranızdadır. Korkmayın!›"));
trsuperobject.biblemap.put("b37_2_6", new trsuperobject(37,2,6,"‹‹Her Şeye Egemen RAB diyor ki, ‹Kısa zamanda bir kez daha yeri, göğü, denizi, karayı sarsacağım."));
trsuperobject.biblemap.put("b37_2_7", new trsuperobject(37,2,7,"Bütün ulusları sarsacağım, değerli eşyalarını buraya getirecekler. Ben de bu tapınağı görkemle dolduracağım.› Böyle diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b37_2_8", new trsuperobject(37,2,8,"‹Gümüş de, altın da benim› diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b37_2_9", new trsuperobject(37,2,9,"‹Yeni tapınağın görkemi, öncekinden daha büyük olacak. Buraya esenlik vereceğim.› Böyle diyor Her Şeye Egemen RAB.›› özlediği kişi buraya gelecek.››"));
trsuperobject.biblemap.put("b37_2_10", new trsuperobject(37,2,10,"Dariusun krallığının ikinci yılında, dokuzuncu ayın yirmi dördüncü günü RAB, Peygamber Hagaya seslendi:"));
trsuperobject.biblemap.put("b37_2_11", new trsuperobject(37,2,11,"‹‹Her Şeye Egemen RAB diyor ki, ‹Kâhinlere yasayla ilgili şu soruyu sor:"));
trsuperobject.biblemap.put("b37_2_12", new trsuperobject(37,2,12,"Eğer biri giysisinin kıvrımları arasında kutsanmış et taşır ve o kıvrım ekmeğe, yemeğe, şaraba, zeytinyağına ya da başka bir yiyeceğe değerse, o yiyecek kutsal olur mu?›› Kâhinler, ‹‹Hayır›› diye yanıtladılar."));
trsuperobject.biblemap.put("b37_2_13", new trsuperobject(37,2,13,"Hagay konuşmasını şöyle sürdürdü: ‹‹Ölüye dokunduğu için kirli sayılan biri, bu yiyeceklerden birine dokunursa, o yiyecek kirlenmiş olur mu?›› Kâhinler, ‹‹Evet, kirlenmiş olur›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b37_2_14", new trsuperobject(37,2,14,"Bunun üzerine Hagay şöyle dedi: ‹‹RAB, ‹Bu halk, bu ulus gözümde böyledir› diyor, ‹Her yaptıkları, sunakta her sundukları da kirlidir.››"));
trsuperobject.biblemap.put("b37_2_15-16", new trsuperobject(37,2,15-16,"‹‹Bugüne dek olanları iyi düşünün; RABbin Tapınağında taş üstüne taş konulmadan önce, yirmi ölçeklik bir tahıl yığınına gelen biri, yalnızca on ölçek bulurdu; şarap teknesinden elli ölçek çıkarmaya varan biri, yalnızca yirmi ölçek bulurdu."));
trsuperobject.biblemap.put("b37_2_17", new trsuperobject(37,2,17,"Ellerinizin bütün emeğini samyeliyle, küfle, doluyla cezalandırdım. Yine de bana dönmediniz.› RAB böyle diyor."));
trsuperobject.biblemap.put("b37_2_18", new trsuperobject(37,2,18,"‹Bugünden, dokuzuncu ayın yirmi dördüncü gününden, RABbin Tapınağının temelinin atıldığı günden başlayarak olacakları iyi düşünün."));
trsuperobject.biblemap.put("b37_2_19", new trsuperobject(37,2,19,"Ambarda hiç tohum kaldı mı? Asma, incir, nar, zeytin ağaçları bugüne dek ürün verdi mi? ‹‹Bugünden başlayarak üzerinize bereket yağdıracağım.››"));
trsuperobject.biblemap.put("b37_2_20", new trsuperobject(37,2,20,"Ayın yirmi dördüncü günü RAB Hagaya ikinci kez seslendi:"));
trsuperobject.biblemap.put("b37_2_21", new trsuperobject(37,2,21,"‹‹Yahuda Valisi Zerubbabile de ki, ben yeri, göğü sarsmak üzereyim."));
trsuperobject.biblemap.put("b37_2_22", new trsuperobject(37,2,22,"Kralların tahtlarını devireceğim, yabancı ulusların gücünü yok edeceğim. Savaş arabalarıyla sürücülerini de devireceğim; atlarla binicileri düşecek, hepsi kardeşinin kılıcıyla öldürülecek."));
trsuperobject.biblemap.put("b37_2_23", new trsuperobject(37,2,23,"‹‹Her Şeye Egemen RAB ‹O gün seni alacağım, ey Şealtiel'in torunu kulum Zerubbabil› diyor, ‹Ve seni mühür yüzüğü gibi yapacağım. Çünkü ben seni seçtim.› Böyle diyor Her Şeye Egemen RAB.››"));

}

