trsuperobject.biblemap.put("b38_3_8", new trsuperobject(38,3,8,"‹‹Ey Başkâhin Yeşu, sen ve önünde oturan kâhin arkadaşların, dinleyin! Çünkü onlar gelecek olayların önbelirtisidir. Dal adındaki kulumu ortaya çıkarıyorum."));
trsuperobject.biblemap.put("b38_3_9", new trsuperobject(38,3,9,"Yeşunun önüne koyduğum taşa bakın! O tek taşın yedi gözüfç var; onun üzerine bir yazıt oyacağım› diyor Her Şeye Egemen RAB, ‹Bir günde bu ülkenin günahını kaldıracağım."));
trsuperobject.biblemap.put("b38_3_10", new trsuperobject(38,3,10,"O gün her biriniz komşusunu asmasının, incir ağacının altında oturmaya çağıracak.› Böyle diyor Her Şeye Egemen RAB.››"));
