trsuperobject.biblemap.put("b38_4_6", new trsuperobject(38,4,6,"Bunun üzerine şöyle dedi: ‹‹RAB Zerubbabile, ‹Güçle kuvvetle değil, ancak benim Ruhumla başaracaksın› diyor. Böyle diyor Her Şeye Egemen RAB."));
trsuperobject.biblemap.put("b38_4_7", new trsuperobject(38,4,7,"Sen kim oluyorsun, ey ulu dağ? Zerubbabilin önünde bir düzlük olacaksın! O tapınağın son taşını çıkarırken, halk da, ‹Ne güzel, ne güzel!› diye bağıracak.››"));
trsuperobject.biblemap.put("b38_4_9", new trsuperobject(38,4,9,"‹‹Bu tapınağın temelini Zerubbabilin elleri attı, tapınağı tamamlayacak olan da onun elleridir. O zaman beni size Her Şeye Egemen RABbin gönderdiğini anlayacaksınız."));
trsuperobject.biblemap.put("b38_4_10", new trsuperobject(38,4,10,"‹‹Küçük işleri yapma gününü kim küçümsüyor? İnsanlar Zerubbabilin elinde çekülü görünce sevinecekler. -‹‹Bu yedi kandil RABbin bütün yeryüzünde dolaşan gözleridir.››-"));
trsuperobject.biblemap.put("b38_4_11", new trsuperobject(38,4,11,"Meleğe, ‹‹Kandilliğin sağındaki ve solundaki bu iki zeytin ağacı nedir?›› diye sordum,"));
