trsuperobject.biblemap.put("b38_4_13", new trsuperobject(38,4,13,"‹‹Bunların anlamını bilmiyor musun?›› diye karşılık verdi. ‹‹Hayır, efendim›› dedim."));
trsuperobject.biblemap.put("b38_4_14", new trsuperobject(38,4,14,"Melek, ‹‹Bunlar bütün dünyanın Rabbi'ne hizmet eden, zeytinyağıyla kutsanmış iki kişidir›› diye açıkladı."));
