trsuperobject.biblemap.put("b38_5_10", new trsuperobject(38,5,10,"Benimle konuşan meleğe, ‹‹Kabı nereye götürüyorlar?›› diye sordum."));
trsuperobject.biblemap.put("b38_5_11", new trsuperobject(38,5,11,"‹‹Kadın için bir ev yapmak üzere Şinar topraklarına›› diye yanıtladı, ‹‹Ev hazır olunca kap oraya, yerine konulacak.››"));
trsuperobject.biblemap.put("b38_6_1", new trsuperobject(38,6,1,"Yine gözlerimi kaldırıp baktım, iki tunç dağın arasından çıkıp gelen dört savaş arabası gördüm."));
