trsuperobject.biblemap.put("b38_6_4", new trsuperobject(38,6,4,"Benimle konuşan meleğe, ‹‹Bunlar ne, efendim?›› diye sordum."));
trsuperobject.biblemap.put("b38_6_5", new trsuperobject(38,6,5,"Melek şöyle karşılık verdi: ‹‹Bunlar bütün dünyanın Rabbine hizmet ettikleri yerden çıkan göğün dört ruhudur."));
trsuperobject.biblemap.put("b38_6_6", new trsuperobject(38,6,6,"Siyah atların çektiği savaş arabası bölgenin kuzeyine, beyaz atlarınki batıya, benekli atlarınki de güneye doğru gidiyor.››"));
trsuperobject.biblemap.put("b38_6_7", new trsuperobject(38,6,7,"Yola çıktıklarında güçlü atlar yeryüzünü dolaşmak üzere gitmek istiyorlardı. Melek, ‹‹Gidin, yeryüzünü dolaşın!›› deyince, gidip yeryüzünü dolaştılar."));
trsuperobject.biblemap.put("b38_6_8", new trsuperobject(38,6,8,"Sonra melek bana seslendi: ‹‹Bak, bölgenin kuzeyine gidenler, orada öfkemi yatıştırdılar.››"));
