trsuperobject.biblemap.put("b4_5_31", new trsuperobject(4,5,31,"Kocası herhangi bir suçtan suçsuz sayılacak, kadınsa suçunun cezasını çekecek.››"));
trsuperobject.biblemap.put("b4_6_2", new trsuperobject(4,6,2,"‹‹İsrail halkına de ki, ‹Eğer bir erkek ya da kadın RABbe adanmış kişi olarak RABbe özel bir adak adamak, kendini RABbe adamak isterse,"));
trsuperobject.biblemap.put("b4_6_3", new trsuperobject(4,6,3,"şaraptan ya da herhangi bir içkiden kaçınacak, şaraptan ya da başka içkilerden yapılmış sirke içmeyecek. Üzüm suyu da içmeyecek. Yaş ya da kuru üzüm yemeyecek."));
trsuperobject.biblemap.put("b4_6_5", new trsuperobject(4,6,5,"‹‹RABbe adanmışlığı süresince başına ustura değmeyecek. Kendini RABbe adadığı günler tamamlanıncaya dek kutsal olacak, saçını uzatacak."));
trsuperobject.biblemap.put("b4_6_6", new trsuperobject(4,6,6,"Kendini RABbe adadığı günler boyunca ölüye yaklaşmayacak."));
trsuperobject.biblemap.put("b4_6_7", new trsuperobject(4,6,7,"Annesi, babası, erkek ya da kız kardeşi ölse bile onlar için kendini kirletmeyecek. Çünkü kendini Tanrısına adama simgesi başı üzerindedir."));
trsuperobject.biblemap.put("b4_6_8", new trsuperobject(4,6,8,"Adanmışlığı süresince RAB için kutsal olacaktır."));
trsuperobject.biblemap.put("b4_6_9", new trsuperobject(4,6,9,"‹‹Eğer ansızın yanında biri ölür, adamış olduğu başını kirletirse, temizlendiği gün, yedinci gün saçını tıraş edecek."));
trsuperobject.biblemap.put("b4_6_10", new trsuperobject(4,6,10,"Sekizinci gün Buluşma Çadırının giriş bölümünde kâhine iki kumru ya da iki güvercin sunacak."));
trsuperobject.biblemap.put("b4_6_11", new trsuperobject(4,6,11,"Kâhin birini günah sunusu, öbürünü yakmalık sunu olarak sunacak. Böylece ölünün yanında bulunmakla kirlenen adam arınacak. O gün başını yeniden adayacak."));
trsuperobject.biblemap.put("b4_6_12", new trsuperobject(4,6,12,"Adanmışlığı süresince kendini RABbe yeniden adayacak. Suç sunusu olarak bir yaşında bir erkek kuzu getirecek. Önceki günler sayılmayacak, çünkü adanmışlığı kirlenmiş sayıldı."));
trsuperobject.biblemap.put("b4_6_13", new trsuperobject(4,6,13,"‹‹Adanmış kişi için yasa şudur: Kendini adamış olduğu günler tamamlanınca, Buluşma Çadırının giriş bölümüne getirilecek."));
trsuperobject.biblemap.put("b4_6_14", new trsuperobject(4,6,14,"Orada RABbe sunularını sunacak: Yakmalık sunu için bir yaşında kusursuz bir erkek kuzu, günah sunusu olarak bir yaşında kusursuz bir dişi kuzu, esenlik sunusu için kusursuz bir koç,"));
trsuperobject.biblemap.put("b4_6_15", new trsuperobject(4,6,15,"tahıl sunusu ve dökmelik sunularla birlikte bir sepet mayasız ekmek, ince undan zeytinyağıyla yoğrulmuş pideler, üzerine yağ sürülmüş mayasız yufkalar."));
trsuperobject.biblemap.put("b4_6_16", new trsuperobject(4,6,16,"‹‹Kâhin bunları günah sunusu ve yakmalık sunu olarak RABbin önünde sunacak."));
trsuperobject.biblemap.put("b4_6_17", new trsuperobject(4,6,17,"Esenlik kurbanı olarak da koçu mayasız ekmek sepetiyle birlikte RABbe sunacak. İlgili tahıl ve dökmelik sunuları da sunacak."));
trsuperobject.biblemap.put("b4_6_18", new trsuperobject(4,6,18,"‹‹Sonra adanmış kişi Buluşma Çadırının giriş bölümünde adadığı saçını tıraş edecek, saçını alıp esenlik kurbanının altındaki ateşe koyacak."));
trsuperobject.biblemap.put("b4_6_19", new trsuperobject(4,6,19,"‹‹Adanmış kişi adadığı saçını tıraş ettikten sonra, kâhin koçun haşlanmış budunu, sepetten alacağı mayasız pide ve mayasız yufkayla birlikte adanmış kişinin eline koyacak."));
trsuperobject.biblemap.put("b4_6_20", new trsuperobject(4,6,20,"Sonra sallamalık sunu olarak RABbin huzurunda bunları sallayacak. Bunlar sallanan döş ve bağış olarak sunulan butla birlikte kutsaldır, kâhin için ayrılacaktır. Bundan sonra adanmış kişi şarap içebilir."));
trsuperobject.biblemap.put("b4_6_21", new trsuperobject(4,6,21,"‹‹Adak adayan adanmış kişiyle ilgili yasa budur. RABbe sunacağı sunu adağı uyarınca olacak. Elinden geldiğince daha fazlasını verebilir. Adanmışlıkla ilgili yasa uyarınca adadığı adağı yerine getirmelidir.››"));
