trsuperobject.biblemap.put("b4_7_77", new trsuperobject(4,7,77,"esenlik kurbanı için iki sığır, beş koç, beş teke, bir yaşında beş kuzu. Okran oğlu Pagielin getirdiği armağanlar bunlardı."));
trsuperobject.biblemap.put("b4_7_78", new trsuperobject(4,7,78,"On ikinci gün Naftali oymağı önderi Enan oğlu Ahira armağanlarını sundu."));
trsuperobject.biblemap.put("b4_7_79", new trsuperobject(4,7,79,"Getirdiği armağanlar şunlardı: 130 kutsal yerin şekeli ağırlığında gümüş bir tabak, yetmiş şekel ağırlığında gümüş bir çanak -ikisi de tahıl sunusu için zeytinyağıyla yoğrulmuş ince un doluydu-"));
trsuperobject.biblemap.put("b4_7_80", new trsuperobject(4,7,80,"buhur dolu on şekel ağırlığında altın bir tabak;"));
trsuperobject.biblemap.put("b4_7_81", new trsuperobject(4,7,81,"yakmalık sunu için bir boğa, bir koç, bir yaşında bir erkek kuzu;"));
trsuperobject.biblemap.put("b4_7_82", new trsuperobject(4,7,82,"günah sunusu için bir teke;"));
trsuperobject.biblemap.put("b4_7_83", new trsuperobject(4,7,83,"esenlik kurbanı için iki sığır, beş koç, beş teke, bir yaşında beş kuzu. Enan oğlu Ahiranın getirdiği armağanlar bunlardı."));
trsuperobject.biblemap.put("b4_7_84", new trsuperobject(4,7,84,"Sunak meshedildiğinde İsrail önderlerinin adanması için sunduğu armağanlar şunlardı: On iki gümüş tabak, on iki gümüş çanak, on iki altın tabak;"));
trsuperobject.biblemap.put("b4_7_85", new trsuperobject(4,7,85,"her gümüş tabağın ağırlığı 130 şekel, her çanağın ağırlığı yetmiş şekeldi. Bütün gümüş eşyaların toplam ağırlığı 2 400 kutsal yerin şekeliydi."));
trsuperobject.biblemap.put("b4_7_86", new trsuperobject(4,7,86,"Buhur dolu on iki altın tabağın her birinin ağırlığı on kutsal yerin şekeliydi. Bütün altın tabakların toplam ağırlığı 120 şekeldi."));
trsuperobject.biblemap.put("b4_7_87", new trsuperobject(4,7,87,"Yakmalık sunu için tahıl sunularıyla birlikte sunulan hayvanların sayısı on iki boğa, on iki koç, bir yaşında on iki erkek kuzuydu; günah sunusu için de on iki teke sunuldu."));
trsuperobject.biblemap.put("b4_7_88", new trsuperobject(4,7,88,"Esenlik kurbanı için sunulan hayvanların sayısı yirmi dört sığır, altmış koç, altmış teke, bir yaşında altmış kuzuydu. Sunak meshedildikten sonra, adanması için verilen armağanlar bunlardı."));
trsuperobject.biblemap.put("b4_7_89", new trsuperobject(4,7,89,"Musa RAB'le konuşmak için Buluşma Çadırı'na girince, Levha Sandığı'nın Bağışlanma Kapağı'nın üstündeki iki Keruv arasından kendisine seslenen sesi duydu. RAB Musa'yla bu şekilde konuştu. sözcüğü Tanrı'nın öfkesinin yatıştırıldığı, halkının günahlarının bağışlanıp Tanrı'yla barıştırıldığı yeri ifade ediyordu."));
trsuperobject.biblemap.put("b4_8_2", new trsuperobject(4,8,2,"‹‹Haruna de ki, yedi kandili kandilliğin önünü aydınlatacak biçimde yerleştirsin.››"));
trsuperobject.biblemap.put("b4_8_3", new trsuperobject(4,8,3,"Harun söyleneni yaptı. RABbin Musaya buyurduğu gibi, kandilleri kandilliğin önüne yerleştirdi."));
trsuperobject.biblemap.put("b4_8_4", new trsuperobject(4,8,4,"Kandillik, ayağından çiçek motiflerine dek dövme altından, RABbin Musaya gösterdiği örneğe göre yapıldı."));
trsuperobject.biblemap.put("b4_8_6", new trsuperobject(4,8,6,"‹‹Levilileri İsraillilerin arasından ayırıp dinsel açıdan arındır."));
trsuperobject.biblemap.put("b4_8_7", new trsuperobject(4,8,7,"Onları arındırmak için şöyle yapacaksın: Günahtan arındırma suyunu üzerlerine serp; bedenlerindeki bütün kılları tıraş etmelerini, giysilerini yıkamalarını sağla. Böylece arınmış olurlar."));
trsuperobject.biblemap.put("b4_8_8", new trsuperobject(4,8,8,"Sonra bir boğa ile tahıl sunusu için zeytinyağıyla yoğrulmuş ince un alsınlar; günah sunusu için sen de başka bir boğa alacaksın."));
trsuperobject.biblemap.put("b4_8_9", new trsuperobject(4,8,9,"Levilileri Buluşma Çadırının önüne getir, bütün İsrail topluluğunu da topla."));
trsuperobject.biblemap.put("b4_8_10", new trsuperobject(4,8,10,"Levilileri RABbin huzuruna getireceksin, İsrailliler ellerini üzerlerine koyacaklar."));
trsuperobject.biblemap.put("b4_8_11", new trsuperobject(4,8,11,"Harun, RABbin hizmetini yapabilmeleri için, İsraillilerin arasından adak olarak Levilileri RABbe adayacak."));
trsuperobject.biblemap.put("b4_8_12", new trsuperobject(4,8,12,"‹‹Levililer ellerini boğaların başına koyacaklar; günahlarını bağışlatmak için boğalardan birini günah sunusu, öbürünü yakmalık sunu olarak RABbe sunacaksın."));
trsuperobject.biblemap.put("b4_8_13", new trsuperobject(4,8,13,"Levililer Harunla oğullarının önünde duracaklar. Onları adak olarak RABbe adayacaksın."));
trsuperobject.biblemap.put("b4_8_14", new trsuperobject(4,8,14,"Levilileri öbür İsraillilerin arasından bu şekilde ayıracaksın. Levililer benim olacak."));
trsuperobject.biblemap.put("b4_8_15", new trsuperobject(4,8,15,"‹‹Sen onları arındırıp adak olarak adadıktan sonra, Levililer Buluşma Çadırındaki hizmeti yerine getirmeye başlayacaklar."));
trsuperobject.biblemap.put("b4_8_16", new trsuperobject(4,8,16,"Çünkü İsrailliler arasından Levililer tümüyle bana verilmiştir. İlk doğanların, İsrailli kadınların doğurdukları ilk erkek çocukların yerine onları kendime ayırdım."));
trsuperobject.biblemap.put("b4_8_17", new trsuperobject(4,8,17,"İsrailliler arasında ilk doğan insan ya da hayvan benimdir. Mısırda ilk doğanları yok ettiğim gün, onları kendime ayırdım."));
trsuperobject.biblemap.put("b4_8_18", new trsuperobject(4,8,18,"İsrailde ilk doğan erkek çocukların yerine Levilileri seçtim."));
trsuperobject.biblemap.put("b4_8_19", new trsuperobject(4,8,19,"İsrailliler kutsal yere yaklaştıklarında belaya uğramamaları için, onların adına Buluşma Çadırındaki hizmeti yerine getirmek ve günahlarını bağışlatmak üzere, onların arasından Levilileri Harunla oğullarına armağan olarak verdim.››"));
trsuperobject.biblemap.put("b4_8_20", new trsuperobject(4,8,20,"Musa, Harun ve bütün İsrail topluluğu Levililer için söyleneni yaptılar. İsrailliler RABbin Musaya Levililerle ilgili verdiği her buyruğu yerine getirdiler."));
trsuperobject.biblemap.put("b4_8_21", new trsuperobject(4,8,21,"Levililer kendilerini günahtan arındırıp giysilerini yıkadılar. Sonra Harun onları RABbin huzurunda adak olarak adadı; onları arındırmak için günahlarını bağışlattı."));
trsuperobject.biblemap.put("b4_8_22", new trsuperobject(4,8,22,"Bundan sonra Levililer Harunla oğullarının sorumluluğu altında Buluşma Çadırındaki hizmetlerini yapmaya geldiler. RABbin Levililere ilişkin Musaya verdiği buyrukları yerine getirdiler."));
trsuperobject.biblemap.put("b4_8_23", new trsuperobject(4,8,23,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b4_8_24", new trsuperobject(4,8,24,"‹‹Levililerle ilgili kural şudur: Yirmi beş ve daha yukarı yaşta olanlar Buluşma Çadırında hizmet edecekler."));
trsuperobject.biblemap.put("b4_8_25", new trsuperobject(4,8,25,"Ancak elli yaşına gelince yaptıkları hizmetten ayrılıp bir daha çadırda çalışmayacaklar."));
trsuperobject.biblemap.put("b4_8_26", new trsuperobject(4,8,26,"Buluşma Çadırı'nda görev yapan kardeşlerine yardımcı olacaklar, ama kendileri hizmet etmeyecekler. Levililer'in sorumluluklarını böyle düzenleyeceksin.››"));
trsuperobject.biblemap.put("b4_9_1", new trsuperobject(4,9,1,"İsraillilerin Mısırdan çıkışlarının ikinci yılının birinci ayında RAB Sina Çölünde Musaya şöyle seslendi:"));
trsuperobject.biblemap.put("b4_9_2", new trsuperobject(4,9,2,"‹‹İsrailliler Fısıh kurbanını belirlenen zamanda kessinler."));
trsuperobject.biblemap.put("b4_9_3", new trsuperobject(4,9,3,"Bütün kurallar, ilkeler uyarınca kurbanı belirlenen zamanda, bu ayın on dördüncü gününün akşamüstü keseceksiniz.››"));
trsuperobject.biblemap.put("b4_9_4", new trsuperobject(4,9,4,"Böylece Musa İsraillilere Fısıh kurbanını kesmelerini söyledi."));
trsuperobject.biblemap.put("b4_9_5", new trsuperobject(4,9,5,"Onlar da Sina Çölünde birinci ayın on dördüncü gününün akşamüstü Fısıh kurbanını kestiler. Her şeyi RABbin Musaya buyurduğu gibi yaptılar."));
trsuperobject.biblemap.put("b4_9_6", new trsuperobject(4,9,6,"Ancak, ölüye dokunduklarından kirli sayılan bazı kişiler o gün Fısıh kurbanını kesemediler. Aynı gün Musayla Haruna gelip"));
trsuperobject.biblemap.put("b4_9_7", new trsuperobject(4,9,7,"Musaya, ‹‹Ölüye dokunduğumuzdan kirli sayılırız›› dediler, ‹‹Ama öbür İsraillilerle birlikte belirlenen zamanda RABbin sunusunu sunmamız neden engellensin?››"));
trsuperobject.biblemap.put("b4_9_8", new trsuperobject(4,9,8,"Musa, ‹‹RABbin sizinle ilgili bana neler söyleyeceğini duyuncaya dek bekleyin›› dedi."));
trsuperobject.biblemap.put("b4_9_10", new trsuperobject(4,9,10,"‹‹İsraillilere de ki, ‹Sizlerden ya da soyunuzdan ölüye dokunduğu için kirli sayılan ya da uzak bir yolculukta bulunan biri RABbin Fısıh kurbanını kesebilir."));
trsuperobject.biblemap.put("b4_9_11", new trsuperobject(4,9,11,"İkinci ayın on dördüncü gününün akşamüstü Fısıh kurbanını kesip mayasız ekmek ve acı otlarla yiyecek."));
trsuperobject.biblemap.put("b4_9_12", new trsuperobject(4,9,12,"Sabaha dek kurbandan bir şey bırakmayacak, kemiklerini kırmayacak. Fısıh kurbanını bütün kuralları uyarınca kesmelidir."));
trsuperobject.biblemap.put("b4_9_13", new trsuperobject(4,9,13,"Ancak, temiz sayılan ve yolculukta olmayan biri Fısıh kurbanını kesmeyi savsaklarsa, halkının arasından atılacaktır. Çünkü belirlenen zamanda RABbin sunusunu sunmamıştır. Günahının cezasını çekecektir."));
trsuperobject.biblemap.put("b4_9_14", new trsuperobject(4,9,14,"‹‹Aranızda yaşayan bir yabancı RABbin Fısıh kurbanını kesmek isterse, Fısıhın kuralları, ilkeleri uyarınca kesmelidir. Yerli ya da yabancı için aynı kuralı uygulamalısınız.››"));
trsuperobject.biblemap.put("b4_9_15", new trsuperobject(4,9,15,"Konut, yani Levha Sandığının bulunduğu çadır kurulduğu gün üstünü bulut kapladı. Konutun üstündeki bulut akşamdan sabaha dek ateşi andırdı."));
trsuperobject.biblemap.put("b4_9_16", new trsuperobject(4,9,16,"Bu hep böyle sürüp gitti. Konutu kaplayan bulut gece ateşi andırıyordu."));
trsuperobject.biblemap.put("b4_9_17", new trsuperobject(4,9,17,"İsrailliler ancak bulut çadırın üzerinden kalkınca göçer, bulut nerede durursa orada konaklarlardı."));
trsuperobject.biblemap.put("b4_9_18", new trsuperobject(4,9,18,"RABbin buyruğu uyarınca göç eder, yine RABbin buyruğu uyarınca konaklarlardı. Bulut konutun üzerinde durdukça yerlerinden ayrılmazlardı."));
trsuperobject.biblemap.put("b4_9_19", new trsuperobject(4,9,19,"Bulut konutun üzerinde uzun süre durduğu zaman RABbin buyruğuna uyar, yola çıkmazlardı."));
trsuperobject.biblemap.put("b4_9_20", new trsuperobject(4,9,20,"Bazen bulut konutun üzerinde birkaç gün kalırdı. Halk da RABbin verdiği buyruğa göre ya konakladığı yerde kalır ya da göç ederdi."));
trsuperobject.biblemap.put("b4_9_21", new trsuperobject(4,9,21,"Bazı günler bulut akşamdan sabaha dek kalır, sabah konutun üzerinden kalkar kalkmaz halk yola çıkardı. Gece olsun, gündüz olsun, bulut konutun üzerinden kalkar kalkmaz halk yola çıkardı."));
trsuperobject.biblemap.put("b4_9_22", new trsuperobject(4,9,22,"Bulut konutun üzerinde iki gün, bir ay ya da uzun süre kalsa bile, İsrailliler konakladıkları yerde kalır, yola koyulmazlardı. Ama bulut kalkar kalkmaz yola çıkarlardı."));
trsuperobject.biblemap.put("b4_9_23", new trsuperobject(4,9,23,"RAB'bin buyruğu uyarınca konaklar ya da yola çıkarlardı. Böylece RAB'bin Musa aracılığıyla verdiği buyruğa uydular."));
trsuperobject.biblemap.put("b4_10_2", new trsuperobject(4,10,2,"‹‹Dövme gümüşten iki borazan yapacaksın; bunları topluluğu çağırmak ve halkın yola çıkması için kullanacaksın."));
trsuperobject.biblemap.put("b4_10_3", new trsuperobject(4,10,3,"İki borazan birden çalınınca, bütün topluluk senin yanında, Buluşma Çadırının girişi önünde toplanacak."));
trsuperobject.biblemap.put("b4_10_4", new trsuperobject(4,10,4,"Yalnız biri çalınırsa, önderler, İsrailin oymak başları senin yanında toplanacak."));
trsuperobject.biblemap.put("b4_10_5", new trsuperobject(4,10,5,"Borazan kısa çalınınca, doğuda konaklayanlar yola çıkacak."));
trsuperobject.biblemap.put("b4_10_6", new trsuperobject(4,10,6,"İkinci kez kısa çalınınca da güneyde konaklayanlar yola çıkacak. Borazanın kısa çalınması oymakların yola çıkması için bir işarettir."));
trsuperobject.biblemap.put("b4_10_7", new trsuperobject(4,10,7,"Topluluğu toplamak için de borazan çaldırt, ama kısa olmasın."));
trsuperobject.biblemap.put("b4_10_8", new trsuperobject(4,10,8,"‹‹Borazanları kâhin olan Harunoğulları çalacak. Borazan çalınması sizler ve gelecek kuşaklar için kalıcı bir kural olacak."));
trsuperobject.biblemap.put("b4_10_9", new trsuperobject(4,10,9,"Sizi sıkıştıran düşmana karşı ülkenizde savaşa çıktığınızda, borazan çalın. O zaman Tanrınız RAB sizi anımsayacak, sizi düşmanlarınızdan kurtaracak."));
trsuperobject.biblemap.put("b4_10_10", new trsuperobject(4,10,10,"Sevinçli olduğunuz günler -kutladığınız bayramlar ve Yeni Ay Törenlerinde- yakmalık sunular ve esenlik kurbanları üzerine borazan çalacaksınız. Böylelikle Tanrınızın önünde anımsanmış olacaksınız. Ben Tanrınız RABbim.››"));
trsuperobject.biblemap.put("b4_10_11", new trsuperobject(4,10,11,"İkinci yılın ikinci ayının yirminci günü bulut Levha Sandığının bulunduğu konutun üzerinden kalktı."));
trsuperobject.biblemap.put("b4_10_12", new trsuperobject(4,10,12,"İsrailliler de Sina Çölünden göç etmeye başladılar. Bulut Paran Çölünde durdu."));
trsuperobject.biblemap.put("b4_10_13", new trsuperobject(4,10,13,"Bu, RABbin Musa aracılığıyla verdiği buyruk uyarınca ilk göç edişleriydi."));
trsuperobject.biblemap.put("b4_10_14", new trsuperobject(4,10,14,"Önce Yahuda sancağı bölükleriyle yola çıktı. Yahuda bölüğüne Amminadav oğlu Nahşon komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_15", new trsuperobject(4,10,15,"İssakar oymağının bölüğüne Suar oğlu Netanel,"));
trsuperobject.biblemap.put("b4_10_16", new trsuperobject(4,10,16,"Zevulun oymağının bölüğüne de Helon oğlu Eliav komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_17", new trsuperobject(4,10,17,"Konut yere indirilince, onu taşıyan Gerşonoğullarıyla Merarioğulları yola koyuldular."));
trsuperobject.biblemap.put("b4_10_18", new trsuperobject(4,10,18,"Sonra Ruben sancağı bölükleriyle yola çıktı. Ruben bölüğüne Şedeur oğlu Elisur komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_19", new trsuperobject(4,10,19,"Şimon oymağının bölüğüne Surişadday oğlu Şelumiel,"));
trsuperobject.biblemap.put("b4_10_20", new trsuperobject(4,10,20,"Gad oymağının bölüğüne de Deuel oğlu Elyasaf komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_21", new trsuperobject(4,10,21,"Kehatlılar kutsal eşyaları taşıyarak yola koyuldular. Bunlar varmadan konut kurulmuş olurdu."));
trsuperobject.biblemap.put("b4_10_22", new trsuperobject(4,10,22,"Efrayim sancağı bölükleriyle yola çıktı. Efrayim bölüğüne Ammihut oğlu Elişama komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_23", new trsuperobject(4,10,23,"Manaşşe oymağının bölüğüne Pedahsur oğlu Gamliel,"));
trsuperobject.biblemap.put("b4_10_24", new trsuperobject(4,10,24,"Benyamin oymağının bölüğüne de Gidoni oğlu Avidan komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_25", new trsuperobject(4,10,25,"En sonunda Dan sancağı ordunun artçı kolu olan bölükleriyle yola çıktı. Dan bölüğüne Ammişadday oğlu Ahiezer komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_26", new trsuperobject(4,10,26,"Aşer oymağının bölüğüne Okran oğlu Pagiel,"));
trsuperobject.biblemap.put("b4_10_27", new trsuperobject(4,10,27,"Naftali oymağının bölüğüne de Enan oğlu Ahira komuta ediyordu."));
trsuperobject.biblemap.put("b4_10_28", new trsuperobject(4,10,28,"Yola koyulduklarında İsrailli bölüklerin yürüyüş düzeni böyleydi."));
trsuperobject.biblemap.put("b4_10_29", new trsuperobject(4,10,29,"Musa, kayınbabası Midyanlı Reuel oğlu Hovava, ‹‹RABbin, ‹Size vereceğim› dediği yere gidiyoruz›› dedi, ‹‹Bizimle gel, sana iyi davranırız. Çünkü RAB İsraile iyilik edeceğine söz verdi.››"));
trsuperobject.biblemap.put("b4_10_30", new trsuperobject(4,10,30,"Hovav, ‹‹Gelmem›› diye yanıtladı, ‹‹Ülkeme, akrabalarımın yanına döneceğim.››"));
trsuperobject.biblemap.put("b4_10_31", new trsuperobject(4,10,31,"Musa, ‹‹Lütfen bizi bırakma›› diye üsteledi, ‹‹Çünkü çölde konaklayacağımız yerleri sen biliyorsun. Sen bize göz olabilirsin."));
trsuperobject.biblemap.put("b4_10_32", new trsuperobject(4,10,32,"Bizimle gelirsen, RABbin yapacağı bütün iyilikleri seninle paylaşırız.››"));
trsuperobject.biblemap.put("b4_10_33", new trsuperobject(4,10,33,"RABbin Dağından ayrılıp üç günlük yol aldılar. Konaklayacakları yeri bulmaları için RABbin Antlaşma Sandığı üç gün boyunca önleri sıra gitti."));
trsuperobject.biblemap.put("b4_10_34", new trsuperobject(4,10,34,"Konakladıkları yerden ayrıldıklarında da RABbin bulutu gündüzün onların üzerinde duruyordu."));
trsuperobject.biblemap.put("b4_10_35", new trsuperobject(4,10,35,"Sandık yola çıkınca Musa,  ‹‹Ya RAB, kalk!  Düşmanların dağılsın,  Senden nefret edenler önünden kaçsın!›› diyordu."));
trsuperobject.biblemap.put("b4_10_36", new trsuperobject(4,10,36,"Sandık konaklayınca da,  ‹‹Ya RAB, binlerce, on binlerce İsrailli'ye dön!›› diyordu."));
trsuperobject.biblemap.put("b4_11_1", new trsuperobject(4,11,1,"Halk çektiği sıkıntılardan ötürü yakınmaya başladı. RAB bunu duyunca öfkelendi, aralarına ateşini göndererek ordugahın kenarlarını yakıp yok etti."));
trsuperobject.biblemap.put("b4_11_2", new trsuperobject(4,11,2,"Halk Musaya yalvardı. Musa RABbe yakarınca ateş söndü."));
trsuperobject.biblemap.put("b4_11_3", new trsuperobject(4,11,3,"Bu nedenle oraya Tavera adı verildi. Çünkü RABbin gönderdiği ateş onların arasında yanmıştı."));
trsuperobject.biblemap.put("b4_11_4", new trsuperobject(4,11,4,"Derken, halkın arasındaki yabancılar başka yiyeceklere özlem duymaya başladılar. İsrailliler de yine ağlayarak, ‹‹Keşke yiyecek biraz et olsaydı!›› dediler,"));
trsuperobject.biblemap.put("b4_11_5", new trsuperobject(4,11,5,"‹‹Mısırda parasız yediğimiz balıkları, salatalıkları, karpuzları, pırasaları, soğanları, sarmısakları anımsıyoruz."));
trsuperobject.biblemap.put("b4_11_6", new trsuperobject(4,11,6,"Şimdiyse yemek yeme isteğimizi yitirdik. Bu mandan başka hiçbir şey gördüğümüz yok.››"));
trsuperobject.biblemap.put("b4_11_7", new trsuperobject(4,11,7,"Man kişniş tohumuna benzerdi, görünüşü de reçine gibiydi."));
trsuperobject.biblemap.put("b4_11_8", new trsuperobject(4,11,8,"Halk çıkıp onu toplar, değirmende öğütür ya da havanda döverdi. Çömlekte haşlayıp pide yaparlardı. Tadı zeytinyağında pişirilmiş yiyeceklere benzerdi."));
trsuperobject.biblemap.put("b4_11_9", new trsuperobject(4,11,9,"Gece ordugaha çiy düşerken, man da birlikte düşerdi."));
trsuperobject.biblemap.put("b4_11_10", new trsuperobject(4,11,10,"Musa herkesin, her ailenin çadırının önünde ağladığını duydu. RAB buna çok öfkelendi. Musa da üzüldü."));
trsuperobject.biblemap.put("b4_11_11", new trsuperobject(4,11,11,"RABbe, ‹‹Kuluna neden kötü davrandın?›› dedi, ‹‹Seni hoşnut etmeyen ne yaptım ki, bu halkın yükünü bana yüklüyorsun?"));
trsuperobject.biblemap.put("b4_11_12", new trsuperobject(4,11,12,"Bütün bu halka ben mi gebe kaldım? Onları ben mi doğurdum? Öyleyse neden emzikteki çocuğu taşıyan bir dadı gibi, atalarına ant içerek söz verdiğin ülkeye onları kucağımda taşımamı istiyorsun?"));
trsuperobject.biblemap.put("b4_11_13", new trsuperobject(4,11,13,"Bütün bu halka verecek eti nereden bulayım? Bana, ‹Bize yiyecek et ver› diye sızlanıp duruyorlar."));
trsuperobject.biblemap.put("b4_11_14", new trsuperobject(4,11,14,"Bu halkı tek başıma taşıyamam, bunca yükü kaldıramam."));
trsuperobject.biblemap.put("b4_11_15", new trsuperobject(4,11,15,"Bana böyle davranacaksan -eğer gözünde lütuf bulduysam- lütfen beni hemen öldür de kendi yıkımımı görmeyeyim.››"));
trsuperobject.biblemap.put("b4_11_16", new trsuperobject(4,11,16,"RAB Musaya, ‹‹Halk arasında önder ve yönetici bildiğin İsrail ileri gelenlerinden yetmiş kişi topla›› dedi, ‹‹Onları Buluşma Çadırına getir, yanında dursunlar."));
trsuperobject.biblemap.put("b4_11_17", new trsuperobject(4,11,17,"Ben inip seninle orada konuşacağım. Senin üzerindeki Ruhtan alıp onlara vereceğim. Halkın yükünü tek başına taşımaman için sana yardım edecekler."));
trsuperobject.biblemap.put("b4_11_18", new trsuperobject(4,11,18,"‹‹Halka de ki, ‹Yarın için kendinizi kutsayın, et yiyeceksiniz. Keşke yiyecek biraz et olsaydı, Mısırda durumumuz iyiydi diye ağladığınızı RAB duydu. Şimdi yemeniz için size et verecek."));
trsuperobject.biblemap.put("b4_11_19", new trsuperobject(4,11,19,"Yalnız bir gün, iki gün, beş, on ya da yirmi gün değil,"));
trsuperobject.biblemap.put("b4_11_20", new trsuperobject(4,11,20,"bir ay boyunca, burnunuzdan gelinceye dek, tiksinene dek yiyeceksiniz. Çünkü aranızda olan RABbi reddettiniz. Onun önünde, Mısırdan neden çıktık diyerek ağladınız.››"));
trsuperobject.biblemap.put("b4_11_21", new trsuperobject(4,11,21,"Musa, ‹‹Aralarında bulunduğum halkın 600 000i yetişkin erkektir›› diye karşılık verdi, ‹‹Oysa sen, ‹Bu halka bir ay boyunca yemesi için et vereceğim› diyorsun."));
trsuperobject.biblemap.put("b4_11_22", new trsuperobject(4,11,22,"Bütün davarlar, sığırlar kesilse, onları doyurur mu? Denizdeki bütün balıklar tutulsa, onları doyurur mu?››"));
trsuperobject.biblemap.put("b4_11_23", new trsuperobject(4,11,23,"RAB, ‹‹Elim kısaldı mı?›› diye yanıtladı, ‹‹Sana söylediklerimin yerine gelip gelmeyeceğini şimdi göreceksin.››"));
trsuperobject.biblemap.put("b4_11_24", new trsuperobject(4,11,24,"Böylece Musa dışarı çıkıp RABbin kendisine söylediklerini halka bildirdi. Halkın ileri gelenlerinden yetmiş adam toplayıp çadırın çevresine yerleştirdi."));
trsuperobject.biblemap.put("b4_11_25", new trsuperobject(4,11,25,"Sonra RAB bulutun içinde inip Musayla konuştu. Musanın üzerindeki Ruhtan alıp yetmiş ileri gelene verdi. Ruhu alınca peygamberlik ettilerse de, daha sonra hiç peygamberlik etmediler."));
trsuperobject.biblemap.put("b4_11_26", new trsuperobject(4,11,26,"Eldat ve Medat adında iki kişi ordugahta kalmıştı. Seçilen yetmiş kişi arasındaydılar ama çadıra gitmemişlerdi. Ruh üzerlerine konunca ordugahta peygamberlik ettiler."));
trsuperobject.biblemap.put("b4_11_27", new trsuperobject(4,11,27,"Bir genç koşup Musaya, ‹‹Eldatla Medat ordugahta peygamberlik ediyor›› diye haber verdi."));
trsuperobject.biblemap.put("b4_11_28", new trsuperobject(4,11,28,"Gençliğinden beri Musanın yardımcısı olan Nun oğlu Yeşu, ‹‹Ey efendim Musa, onlara engel ol!›› dedi."));
trsuperobject.biblemap.put("b4_11_29", new trsuperobject(4,11,29,"Ama Musa, ‹‹Sen benim adıma mı kıskanıyorsun?›› diye yanıtladı, ‹‹Keşke RABbin bütün halkı peygamber olsa da RAB üzerlerine Ruhunu gönderse!››"));
trsuperobject.biblemap.put("b4_11_30", new trsuperobject(4,11,30,"Sonra Musayla İsrailin ileri gelenleri ordugaha döndüler."));
trsuperobject.biblemap.put("b4_11_31", new trsuperobject(4,11,31,"RAB denizden bıldırcın getiren bir rüzgar gönderdi. Rüzgar bıldırcınları ordugahın her yönünden bir günlük yol kadar uzaklığa, yerden iki arşınfö yüksekliğe indirdi."));
trsuperobject.biblemap.put("b4_11_32", new trsuperobject(4,11,32,"Halk bütün gün, bütün gece ve ertesi gün durmadan bıldırcın topladı. Kimse on homerden az toplamadı. Bıldırcınları ordugahın çevresine serdiler."));
trsuperobject.biblemap.put("b4_11_33", new trsuperobject(4,11,33,"Et daha halkın dişleri arasındayken, çiğnemeye vakit kalmadan RAB öfkelendi, onları büyük bir yıkımla cezalandırdı."));
trsuperobject.biblemap.put("b4_11_34", new trsuperobject(4,11,34,"Bu nedenle oraya Kivrot-Hattaava adı verildi. Başka yiyeceklere özlem duyanları oraya gömdüler."));
trsuperobject.biblemap.put("b4_11_35", new trsuperobject(4,11,35,"Halk Kivrot-Hattaava'dan Haserot'a göç edip orada kaldı."));
trsuperobject.biblemap.put("b4_12_1", new trsuperobject(4,12,1,"Musa Kûşlu bir kadınla evlenmişti. Bundan dolayı Miryamla Harun onu yerdiler."));
trsuperobject.biblemap.put("b4_12_2", new trsuperobject(4,12,2,"‹‹RAB yalnız Musa aracılığıyla mı konuştu?›› dediler, ‹‹Bizim aracılığımızla da konuşmadı mı?›› RAB bu yakınmaları duydu."));
trsuperobject.biblemap.put("b4_12_3", new trsuperobject(4,12,3,"Musa yeryüzünde yaşayan herkesten daha alçakgönüllüydü."));
trsuperobject.biblemap.put("b4_12_7", new trsuperobject(4,12,7,"Ama kulum Musa öyle değildir.O bütün evimde sadıktır."));
trsuperobject.biblemap.put("b4_12_8", new trsuperobject(4,12,8,"Onunla bilmecelerle değil,Açıkça, yüzyüze konuşurum.O RABbin suretini görüyor.Öyleyse kulum Musayı yermekten korkmadınız mı?››"));
trsuperobject.biblemap.put("b4_12_10", new trsuperobject(4,12,10,"Bulut çadırın üzerinden ayrıldığında Miryam deri hastalığına yakalanmış, kar gibi bembeyaz olmuştu. Harun Miryama baktı, deri hastalığına yakalandığını gördü."));
trsuperobject.biblemap.put("b4_12_11", new trsuperobject(4,12,11,"Musaya, ‹‹Ey efendim, lütfen akılsızca işlediğimiz günahtan ötürü bizi cezalandırma›› dedi,"));
trsuperobject.biblemap.put("b4_12_12", new trsuperobject(4,12,12,"‹‹Miryam etinin yarısı yenmiş olarak ana rahminden çıkan ölü bir bebeğe benzemesin.››"));
trsuperobject.biblemap.put("b4_12_13", new trsuperobject(4,12,13,"Musa RABbe, ‹‹Ey Tanrı, lütfen Miryamı iyileştir!›› diye yakardı."));
trsuperobject.biblemap.put("b4_12_14", new trsuperobject(4,12,14,"RAB, ‹‹Babası onun yüzüne tükürseydi, yedi gün utanç içinde kalmayacak mıydı?›› diye karşılık verdi, ‹‹Onu yedi gün ordugahtan uzaklaştırın, sonra geri getirilsin.››"));
trsuperobject.biblemap.put("b4_12_15", new trsuperobject(4,12,15,"Böylece Miryam yedi gün ordugahtan uzaklaştırıldı, o geri getirilene dek halk yola çıkmadı."));
trsuperobject.biblemap.put("b4_12_16", new trsuperobject(4,12,16,"Bundan sonra halk Haserot'tan ayrılıp Paran Çölü'nde konakladı."));
trsuperobject.biblemap.put("b4_13_1-2", new trsuperobject(4,13,1-2,"RAB Musaya, ‹‹İsrail halkına vereceğim Kenan ülkesini araştırmak için bazı adamlar gönder›› dedi, ‹‹Ataların her oymağından bir önder gönder.››"));
trsuperobject.biblemap.put("b4_13_3", new trsuperobject(4,13,3,"Musa RABbin buyruğu uyarınca Paran Çölünden adamları gönderdi. Hepsi İsrail halkının önderlerindendi."));
trsuperobject.biblemap.put("b4_13_4", new trsuperobject(4,13,4,"Adları şöyleydi: Ruben oymağından Zakkur oğlu Şammua;"));
trsuperobject.biblemap.put("b4_13_5", new trsuperobject(4,13,5,"Şimon oymağından Hori oğlu Şafat;"));
trsuperobject.biblemap.put("b4_13_6", new trsuperobject(4,13,6,"Yahuda oymağından Yefunne oğlu Kalev;"));
trsuperobject.biblemap.put("b4_13_7", new trsuperobject(4,13,7,"İssakar oymağından Yusuf oğlu Yigal;"));
trsuperobject.biblemap.put("b4_13_8", new trsuperobject(4,13,8,"Efrayim oymağından Nun oğlu Hoşea;"));
trsuperobject.biblemap.put("b4_13_9", new trsuperobject(4,13,9,"Benyamin oymağından Rafu oğlu Palti;"));
trsuperobject.biblemap.put("b4_13_10", new trsuperobject(4,13,10,"Zevulun oymağından Sodi oğlu Gaddiel;"));
trsuperobject.biblemap.put("b4_13_11", new trsuperobject(4,13,11,"Yusuf oymağından -Manaşşe oymağından- Susi oğlu Gaddi;"));
trsuperobject.biblemap.put("b4_13_12", new trsuperobject(4,13,12,"Dan oymağından Gemalli oğlu Ammiel;"));
trsuperobject.biblemap.put("b4_13_13", new trsuperobject(4,13,13,"Aşer oymağından Mikael oğlu Setur;"));
trsuperobject.biblemap.put("b4_13_14", new trsuperobject(4,13,14,"Naftali oymağından Vofsi oğlu Nahbi;"));
trsuperobject.biblemap.put("b4_13_15", new trsuperobject(4,13,15,"Gad oymağından Maki oğlu Geuel."));
trsuperobject.biblemap.put("b4_13_16", new trsuperobject(4,13,16,"Ülkeyi araştırmak üzere Musanın gönderdiği adamlar bunlardı. Musa Nun oğlu Hoşeaya Yeşu adını verdi. gelir."));
trsuperobject.biblemap.put("b4_13_17", new trsuperobject(4,13,17,"Musa, Kenan ülkesini araştırmak üzere onları gönderirken, ‹‹Negeve, dağlık bölgeye gidin›› dedi,"));
trsuperobject.biblemap.put("b4_13_18", new trsuperobject(4,13,18,"‹‹Nasıl bir ülke olduğunu, orada yaşayan halkın güçlü mü zayıf mı, çok mu az mı olduğunu öğrenin."));
trsuperobject.biblemap.put("b4_13_19", new trsuperobject(4,13,19,"Yaşadıkları ülke iyi mi kötü mü, kentleri nasıl, surlu mu değil mi anlayın."));
trsuperobject.biblemap.put("b4_13_20", new trsuperobject(4,13,20,"Toprak nasıl? Verimli mi, kıraç mı? Çevre ağaçlık mı, değil mi? Elinizden geleni yapıp orada yetişen meyvelerden getirin.›› Mevsim üzümün olgunlaşmaya başladığı zamandı."));
trsuperobject.biblemap.put("b4_13_21", new trsuperobject(4,13,21,"Böylece adamlar yola çıkıp ülkeyi Zin Çölünden Levo-Hamata doğru Rehova dek araştırdılar."));
trsuperobject.biblemap.put("b4_13_22", new trsuperobject(4,13,22,"Negevden geçip Anakoğullarından Ahiman, Şeşay ve Talmayın yaşadığı Hevrona vardılar. -Hevron Mısırdaki Soan Kentindenfş yedi yıl önce kurulmuştu.-"));
trsuperobject.biblemap.put("b4_13_23", new trsuperobject(4,13,23,"Eşkol Vadisine varınca, üzerinde bir salkım üzüm olan bir asma dalı kestiler. Adamlardan ikisi dalı bir sırıkta taşıdılar. Yanlarına nar, incir de aldılar."));
trsuperobject.biblemap.put("b4_13_24", new trsuperobject(4,13,24,"İsraillilerin kestiği üzüm salkımından dolayı oraya Eşkol Vadisi adı verildi."));
trsuperobject.biblemap.put("b4_13_25", new trsuperobject(4,13,25,"Kırk gün dolaştıktan sonra adamlar ülkeyi araştırmaktan döndüler."));
trsuperobject.biblemap.put("b4_13_26", new trsuperobject(4,13,26,"Paran Çölündeki Kadeşe, Musayla Harunun ve İsrail topluluğunun yanına geldiler. Onlara ve bütün topluluğa gördüklerini anlatıp ülkenin ürünlerini gösterdiler."));
trsuperobject.biblemap.put("b4_13_27", new trsuperobject(4,13,27,"Musaya, ‹‹Bizi gönderdiğin ülkeye gittik›› dediler, ‹‹Gerçekten süt ve bal akıyor orada! İşte ülkenin ürünleri!"));
trsuperobject.biblemap.put("b4_13_28", new trsuperobject(4,13,28,"Ancak orada yaşayan halk güçlü, kentler de surlu ve çok büyük. Orada Anak soyundan gelen insanları bile gördük."));
trsuperobject.biblemap.put("b4_13_29", new trsuperobject(4,13,29,"Amalekliler Negevde; Hititler, Yevuslular ve Amorlular dağlık bölgede; Kenanlılar da denizin yanında ve Şeria Irmağının kıyısında yaşıyor.››"));
trsuperobject.biblemap.put("b4_13_30", new trsuperobject(4,13,30,"Kalev, Musanın önünde halkı susturup, ‹‹Oraya gidip ülkeyi ele geçirelim. Kesinlikle buna yetecek gücümüz var›› dedi."));
trsuperobject.biblemap.put("b4_13_31", new trsuperobject(4,13,31,"Ne var ki, kendisiyle oraya giden adamlar, ‹‹Bu halka saldıramayız, onlar bizden daha güçlü›› dediler."));
trsuperobject.biblemap.put("b4_13_32", new trsuperobject(4,13,32,"Araştırdıkları ülke hakkında İsrailliler arasında kötü haber yayarak, ‹‹Boydan boya araştırdığımız ülke, içinde yaşayanları yiyip bitiren bir ülkedir›› dediler, ‹‹Üstelik orada gördüğümüz herkes uzun boyluydu."));
trsuperobject.biblemap.put("b4_13_33", new trsuperobject(4,13,33,"Nefiller'i, Nefiller'in soyundan gelen Anaklılar'ı gördük. Onların yanında kendimizi çekirge gibi hissettik, onlara da öyle göründük.››"));
trsuperobject.biblemap.put("b4_14_1", new trsuperobject(4,14,1,"O gece bütün topluluk yüksek sesle bağrışıp ağladı."));
trsuperobject.biblemap.put("b4_14_2", new trsuperobject(4,14,2,"Bütün İsrail halkı Musayla Haruna karşı söylenmeye başladı. Onlara, ‹‹Keşke Mısırda ya da bu çölde ölseydik!›› dediler,"));
trsuperobject.biblemap.put("b4_14_3", new trsuperobject(4,14,3,"‹‹RAB neden bizi bu ülkeye götürüyor? Kılıçtan geçirilelim diye mi? Karılarımız, çocuklarımız tutsak edilecek. Mısıra dönmek bizim için daha iyi değil mi?››"));
trsuperobject.biblemap.put("b4_14_4", new trsuperobject(4,14,4,"Sonra birbirlerine, ‹‹Kendimize bir önder seçip Mısıra dönelim›› dediler."));
trsuperobject.biblemap.put("b4_14_5", new trsuperobject(4,14,5,"Bunun üzerine Musayla Harun İsrail topluluğunun önünde yüzüstü yere kapandılar."));
trsuperobject.biblemap.put("b4_14_6", new trsuperobject(4,14,6,"Ülkeyi araştıranlardan Nun oğlu Yeşuyla Yefunne oğlu Kalev giysilerini yırttılar."));
trsuperobject.biblemap.put("b4_14_7", new trsuperobject(4,14,7,"Sonra bütün İsrail topluluğuna şöyle dediler: ‹‹İçinden geçip araştırdığımız ülke çok iyi bir ülkedir."));
trsuperobject.biblemap.put("b4_14_8", new trsuperobject(4,14,8,"Eğer RAB bizden hoşnut kalırsa, süt ve bal akan o ülkeye bizi götürecek ve orayı bize verecektir."));
trsuperobject.biblemap.put("b4_14_9", new trsuperobject(4,14,9,"Ancak RABbe karşı gelmeyin. Orada yaşayan halktan korkmayın. Onları ekmek yer gibi yiyip bitireceğiz. Koruyucuları onları bırakıp gitti. Ama RAB bizimledir. Onlardan korkmayın!››"));
trsuperobject.biblemap.put("b4_14_10", new trsuperobject(4,14,10,"Topluluk onları taşa tutmayı düşünürken, ansızın RABbin görkemi Buluşma Çadırında bütün İsrail halkına göründü."));
trsuperobject.biblemap.put("b4_14_11", new trsuperobject(4,14,11,"RAB Musaya şöyle dedi: ‹‹Ne zamana dek bu halk bana saygısızlık edecek? Onlara gösterdiğim bunca belirtiye karşın, ne zamana dek bana iman etmeyecekler?"));
trsuperobject.biblemap.put("b4_14_12", new trsuperobject(4,14,12,"Onları salgın hastalıkla cezalandıracağım, mirastan yoksun bırakacağım. Ama seni onlardan daha büyük, daha güçlü bir ulus kılacağım.››"));
trsuperobject.biblemap.put("b4_14_13", new trsuperobject(4,14,13,"Musa, ‹‹Mısırlılar bunu duyacak›› diye karşılık verdi, ‹‹Çünkü bu halkı gücünle onların arasından sen çıkardın."));
trsuperobject.biblemap.put("b4_14_14", new trsuperobject(4,14,14,"Kenan topraklarında yaşayan halka bunu anlatacaklar. Ya RAB, bu halkın arasında olduğunu, onlarla yüz yüze görüştüğünü, bulutunun onların üzerinde durduğunu, gündüz bulut sütunu, gece ateş sütunu içinde onlara yol gösterdiğini duymuşlar."));
trsuperobject.biblemap.put("b4_14_15-16", new trsuperobject(4,14,15-16,"Eğer bu halkı bir insanmış gibi yok edersen, senin ününü duymuş olan bu uluslar, ‹RAB ant içerek söz verdiği ülkeye bu halkı götüremediği için onları çölde yok etti› diyecekler."));
trsuperobject.biblemap.put("b4_14_17", new trsuperobject(4,14,17,"‹‹Şimdi gücünü göster, ya Rab. Demiştin ki,"));
trsuperobject.biblemap.put("b4_14_18", new trsuperobject(4,14,18,"‹RAB tez öfkelenmez, sevgisi engindir, suçu ve isyanı bağışlar. Ancak suçluyu cezasız bırakmaz; babaların işlediği suçun hesabını üçüncü, dördüncü kuşak çocuklarından sorar.›"));
trsuperobject.biblemap.put("b4_14_19", new trsuperobject(4,14,19,"Mısırdan çıkışlarından bugüne dek bu halkı nasıl bağışladıysan, büyük sevgin uyarınca onların suçunu bağışla.››"));
trsuperobject.biblemap.put("b4_14_20", new trsuperobject(4,14,20,"RAB, ‹‹Dileğin üzerine onları bağışladım›› diye yanıtladı,"));
trsuperobject.biblemap.put("b4_14_21", new trsuperobject(4,14,21,"‹‹Ne var ki, varlığım ve yeryüzünü dolduran yüceliğim adına ant içerim ki,"));
trsuperobject.biblemap.put("b4_14_22", new trsuperobject(4,14,22,"yüceliğimi, Mısırda ve çölde gösterdiğim belirtileri görüp de beni on kez sınayan, sözümü dinlemeyen bu kişilerden hiçbiri"));
trsuperobject.biblemap.put("b4_14_23", new trsuperobject(4,14,23,"atalarına ant içerek söz verdiğim ülkeyi görmeyecek. Beni küçümseyenlerden hiçbiri orayı görmeyecek."));
trsuperobject.biblemap.put("b4_14_24", new trsuperobject(4,14,24,"Ama kulum Kalevde başka bir ruh var, o bütün yüreğiyle ardımca yürüdü. Araştırmak için gittiği ülkeye onu götüreceğim, onun soyu orayı miras alacak."));
trsuperobject.biblemap.put("b4_14_25", new trsuperobject(4,14,25,"Amaleklilerle Kenanlılar ovada yaşıyorlar. Siz yarın geri dönün, Kızıldeniz yolundan çöle gidin.››"));
trsuperobject.biblemap.put("b4_14_26-27", new trsuperobject(4,14,26-27,"RAB Musayla Haruna da, ‹‹Bu kötü topluluk ne zamana dek bana söylenecek?›› dedi, ‹‹Bana söylenen İsrail halkının yakınmalarını duydum."));
trsuperobject.biblemap.put("b4_14_28", new trsuperobject(4,14,28,"Onlara RAB şöyle diyor de: ‹Varlığım adına ant içerim ki, söylediklerinizin aynısını size yapacağım:"));
trsuperobject.biblemap.put("b4_14_29", new trsuperobject(4,14,29,"Cesetleriniz bu çöle serilecek. Bana söylenen, yirmi ve daha yukarı yaşta sayılan herkes çölde ölecek."));
trsuperobject.biblemap.put("b4_14_30", new trsuperobject(4,14,30,"Sizi yerleştireceğime ant içtiğim ülkeye Yefunne oğlu Kalevle Nun oğlu Yeşudan başkası girmeyecek."));
trsuperobject.biblemap.put("b4_14_31", new trsuperobject(4,14,31,"Ama tutsak edilecek dediğiniz çocuklarınızı oraya, sizin reddettiğiniz ülkeye götüreceğim; orayı tanıyacaklar."));
trsuperobject.biblemap.put("b4_14_32", new trsuperobject(4,14,32,"Size gelince, cesetleriniz bu çöle serilecek."));
trsuperobject.biblemap.put("b4_14_33", new trsuperobject(4,14,33,"Çocuklarınız, hepiniz ölünceye dek kırk yıl çölde çobanlık edecek ve sizin sadakatsizliğiniz yüzünden sıkıntı çekecekler."));
trsuperobject.biblemap.put("b4_14_34", new trsuperobject(4,14,34,"Ülkeyi araştırdığınız günler kadar -kırk gün, her gün için bir yıldan kırk yıl- suçunuzun cezasını çekeceksiniz. Sizden yüz çevirdiğimi bileceksiniz!›"));
trsuperobject.biblemap.put("b4_14_35", new trsuperobject(4,14,35,"Ben RAB söyledim; bana karşı toplanan bu kötü topluluğa bunları gerçekten yapacağım. Bu çölde yıkıma uğrayacak, burada ölecekler.››"));
trsuperobject.biblemap.put("b4_14_36", new trsuperobject(4,14,36,"Musanın ülkeyi araştırmak üzere gönderdiği adamlar geri dönüp ülke hakkında kötü haber yayarak bütün topluluğun RABbe söylenmesine neden oldular."));
trsuperobject.biblemap.put("b4_14_37", new trsuperobject(4,14,37,"Ülke hakkında kötü haber yayan bu adamlar RABbin önünde ölümcül hastalıktan öldüler."));
trsuperobject.biblemap.put("b4_14_38", new trsuperobject(4,14,38,"Ülkeyi araştırmak üzere gidenlerden yalnız Nun oğlu Yeşuyla Yefunne oğlu Kalev sağ kaldı."));
trsuperobject.biblemap.put("b4_14_39", new trsuperobject(4,14,39,"Musa bu sözleri İsrail halkına bildirince, halk yasa büründü."));
trsuperobject.biblemap.put("b4_14_40", new trsuperobject(4,14,40,"Sabah erkenden kalkıp dağın tepesine çıktılar. ‹‹Günah işledik›› dediler, ‹‹Ama RABbin söz verdiği yere çıkmaya hazırız.››"));
trsuperobject.biblemap.put("b4_14_41", new trsuperobject(4,14,41,"Bunun üzerine Musa, ‹‹Neden RABbin buyruğuna karşı geliyorsunuz?›› dedi, ‹‹Bunu başaramazsınız."));
trsuperobject.biblemap.put("b4_14_42", new trsuperobject(4,14,42,"Savaşa gitmeyin, çünkü RAB sizinle olmayacak. Düşmanlarınızın önünde yenilgiye uğrayacaksınız."));
trsuperobject.biblemap.put("b4_14_43", new trsuperobject(4,14,43,"Amaleklilerle Kenanlılar sizinle orada karşılaşacak ve sizi kılıçtan geçirecekler. Çünkü RABbin ardınca gitmekten vazgeçtiniz. RAB de sizinle olmayacak.››"));
trsuperobject.biblemap.put("b4_14_44", new trsuperobject(4,14,44,"Öyleyken, kendilerine güvenerek dağlık bölgenin tepesine çıktılar. RABbin Antlaşma Sandığı da Musa da ordugahta kaldı."));
trsuperobject.biblemap.put("b4_14_45", new trsuperobject(4,14,45,"Dağlık bölgede yaşayan Amalekliler'le Kenanlılar üzerlerine saldırdılar, Horma Kenti'ne dek onları kovalayıp bozguna uğrattılar."));
trsuperobject.biblemap.put("b4_15_2", new trsuperobject(4,15,2,"‹‹İsrail halkına de ki, ‹Yerleşmek için size vereceğim ülkeye girince,"));
trsuperobject.biblemap.put("b4_15_4", new trsuperobject(4,15,4,"Sunu sunan kişi RABbe tahıl sunusu olarak dörtte bir hin zeytinyağıyla yoğrulmuş onda bir efafü ince un sunacak."));
trsuperobject.biblemap.put("b4_15_5", new trsuperobject(4,15,5,"Yakmalık sunu ya da kurban için, her kuzuya dökmelik sunu olarak dörtte bir hin şarap hazırla."));
trsuperobject.biblemap.put("b4_15_6", new trsuperobject(4,15,6,"‹‹Koç sunarken tahıl sunusu olarak üçte bir hin zeytinyağıyla yoğrulmuş onda iki efa ince un hazırla."));
trsuperobject.biblemap.put("b4_15_7", new trsuperobject(4,15,7,"Dökmelik sunu olarak da üçte bir hin şarap sun. Bunları RABbi hoşnut eden koku olarak sunacaksın."));
trsuperobject.biblemap.put("b4_15_9", new trsuperobject(4,15,9,"boğayla birlikte tahıl sunusu olarak yarım hin zeytinyağıyla yoğrulmuş onda üç efa ince un sun."));
trsuperobject.biblemap.put("b4_15_10", new trsuperobject(4,15,10,"Ayrıca dökmelik sunu olarak yarım hin şarap sun. Yakılan bu sunu RABbi hoşnut eden bir koku olacak."));
trsuperobject.biblemap.put("b4_15_11", new trsuperobject(4,15,11,"Sığır, koç, davar -kuzu ya da keçi- böyle hazırlanacak."));
trsuperobject.biblemap.put("b4_15_12", new trsuperobject(4,15,12,"Kaç hayvan sunacaksan her biri için aynı şeyleri yapacaksın."));
trsuperobject.biblemap.put("b4_15_13", new trsuperobject(4,15,13,"‹‹Her İsrail yerlisi RABbi hoşnut eden koku olarak yakılan bir sunu sunarken bunları aynen yapmalıdır."));
trsuperobject.biblemap.put("b4_15_14", new trsuperobject(4,15,14,"Kuşaklar boyunca aranızda yaşayan bir yabancı ya da yerli olmayan bir konuk, RABbi hoşnut eden koku olarak yakılan bir sunu sunarken, sizin uyguladığınız kuralları uygulamalıdır."));
trsuperobject.biblemap.put("b4_15_15", new trsuperobject(4,15,15,"Sizin ve aranızda yaşayan yabancılar için topluluk aynı kuralları uygulamalıdır. Kuşaklar boyunca kalıcı bir kural olacak bu. RABbin önünde siz nasılsanız, aranızda yaşayan yabancı da aynı olacak."));
trsuperobject.biblemap.put("b4_15_16", new trsuperobject(4,15,16,"Size de aranızda yaşayan yabancıya da aynı yasalar ve kurallar uygulanacak.››"));
trsuperobject.biblemap.put("b4_15_17", new trsuperobject(4,15,17,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b4_15_18", new trsuperobject(4,15,18,"‹‹İsrail halkına de ki, ‹Sizi götüreceğim ülkeye girip"));
trsuperobject.biblemap.put("b4_15_19", new trsuperobject(4,15,19,"o ülkenin ekmeğinden yediğinizde, bir kısmını bana sunacaksınız."));
trsuperobject.biblemap.put("b4_15_20", new trsuperobject(4,15,20,"İlk tahılınızdan sunu olarak bir pide sunacaksınız; bunu harmanınızdan bir sunu olarak sunacaksınız."));
trsuperobject.biblemap.put("b4_15_21", new trsuperobject(4,15,21,"İlk tahılınızdan yapılmış bu sunuyu kuşaklar boyunca RABbe sunacaksınız.››"));
trsuperobject.biblemap.put("b4_15_22-23", new trsuperobject(4,15,22-23,"‹‹Eğer bilmeden günah işlediyseniz, RABbin Musaya verdiği buyruklardan herhangi birini -RABbin buyruk verdiği günden başlayarak Musa aracılığıyla size ve gelecek kuşaklara buyurduğu herhangi bir şeyi- yerine getirmediyseniz"));
trsuperobject.biblemap.put("b4_15_24", new trsuperobject(4,15,24,"ve bu günah bilmeden işlendiyse, bütün topluluk RABbi hoşnut eden koku sunmak için yakmalık sunu olarak istenilen tahıl ve dökmelik sunuyla birlikte bir boğa, günah sunusu olarak da bir teke sunacaktır."));
trsuperobject.biblemap.put("b4_15_25", new trsuperobject(4,15,25,"Kâhin bütün İsrail topluluğunun günahını bağışlatacak, halk bağışlanacak. Çünkü bilmeyerek günah işlediler. İşledikleri günah yüzünden RAB için yakılan sunu olarak sunularını ve günah sunularını sundular."));
trsuperobject.biblemap.put("b4_15_26", new trsuperobject(4,15,26,"Bütün İsrail topluluğu da aranızda yaşayan yabancılar da bağışlanacaktır. Çünkü halk bilmeyerek bu günahı işledi."));
trsuperobject.biblemap.put("b4_15_27", new trsuperobject(4,15,27,"‹‹Eğer biri bilmeden günah işlerse, günah sunusu olarak bir yaşında bir dişi keçi getirmeli."));
trsuperobject.biblemap.put("b4_15_28", new trsuperobject(4,15,28,"Kâhin RABbin önünde, bilmeden günah işleyen kişinin günahını bağışlatacak. Bağışlatma yapılınca kişi bağışlanacak."));
trsuperobject.biblemap.put("b4_15_29", new trsuperobject(4,15,29,"Bilmeden günah işleyen İsrail yerlisi için de aranızda yaşayan yabancı için de aynı yasayı uygulayacaksınız."));
trsuperobject.biblemap.put("b4_15_30", new trsuperobject(4,15,30,"‹‹Yerli ya da yabancı biri bilerek günah işlerse, RABbe saygısızlık etmiştir. Bu kişi halkının arasından atılmalı."));
trsuperobject.biblemap.put("b4_15_31", new trsuperobject(4,15,31,"RABbin sözünü küçümsemiş, buyruklarına karşı gelmiştir. Bu nedenle o kişi halkının arasından kesinlikle atılacak, suçunun cezasını çekecektir.››"));
trsuperobject.biblemap.put("b4_15_32", new trsuperobject(4,15,32,"İsrailliler çöldeyken, Şabat Günü odun toplayan birini buldular."));
trsuperobject.biblemap.put("b4_15_33", new trsuperobject(4,15,33,"Odun toplarken adamı bulanlar onu Musayla Harunun ve bütün topluluğun önüne getirdiler."));
trsuperobject.biblemap.put("b4_15_34", new trsuperobject(4,15,34,"Adama ne yapılacağı belirlenmediğinden onu gözaltında tuttular."));
trsuperobject.biblemap.put("b4_15_35", new trsuperobject(4,15,35,"Derken RAB Musaya, ‹‹O adam öldürülmeli. Bütün topluluk ordugahın dışında onu taşa tutsun›› dedi."));
trsuperobject.biblemap.put("b4_15_36", new trsuperobject(4,15,36,"Böylece topluluk adamı ordugahın dışına çıkardı. RABbin Musaya buyurduğu gibi, onu taşlayarak öldürdüler."));
trsuperobject.biblemap.put("b4_15_37", new trsuperobject(4,15,37,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b4_15_38", new trsuperobject(4,15,38,"‹‹İsrail halkına de ki, ‹Kuşaklar boyunca giysinizin dört yanına püskül dikeceksiniz. Her püskülün üzerine lacivert bir kordon koyacaksınız."));
trsuperobject.biblemap.put("b4_15_39", new trsuperobject(4,15,39,"Öyle ki, püskülleri gördükçe RABbin buyruklarını anımsayasınız. Böylelikle RABbin buyruklarına uyacak, yüreğinizin, gözünüzün istekleri ardınca gitmeyecek, hainlik etmeyeceksiniz."));
trsuperobject.biblemap.put("b4_15_40", new trsuperobject(4,15,40,"Ta ki, bütün buyruklarımı anımsayıp tutasınız ve Tanrınız için kutsal olasınız."));
trsuperobject.biblemap.put("b4_15_41", new trsuperobject(4,15,41,"Tanrınız olmak için sizi Mısır'dan çıkaran Tanrınız RAB benim. Tanrınız RAB benim.››"));
trsuperobject.biblemap.put("b4_16_1-2", new trsuperobject(4,16,1-2,"Levi oğlu Kehat oğlu Yishar oğlu Korah, Ruben soyundan Eliavoğullarından Datan, Aviram ve Pelet oğlu On toplulukça seçilen, tanınmış iki yüz elli İsrailli önderle birlikte Musaya başkaldırdı."));
trsuperobject.biblemap.put("b4_16_3", new trsuperobject(4,16,3,"Hep birlikte Musayla Harunun yanına varıp, ‹‹Çok ileri gittiniz!›› dediler, ‹‹Bütün topluluk, topluluğun her bireyi kutsaldır ve RAB onların arasındadır. Öyleyse neden kendinizi RABbin topluluğundan üstün görüyorsunuz?››"));
trsuperobject.biblemap.put("b4_16_4", new trsuperobject(4,16,4,"Bunu duyan Musa yüzüstü yere kapandı."));
trsuperobject.biblemap.put("b4_16_5", new trsuperobject(4,16,5,"Sonra Korahla yandaşlarına şöyle dedi: ‹‹Sabah RAB kimin kendisine ait olduğunu, kimin kutsal olduğunu açıklayacak ve o kişiyi huzuruna çağıracak. RAB seçeceği kişiyi huzuruna çağıracak."));
trsuperobject.biblemap.put("b4_16_6", new trsuperobject(4,16,6,"Ey Korah ve yandaşları, kendinize buhurdanlar alın."));
trsuperobject.biblemap.put("b4_16_7", new trsuperobject(4,16,7,"Yarın RABbin huzurunda buhurdanlarınızın içine ateş, ateşin üstüne de buhur koyun. RABbin seçeceği kişi, kutsal olan kişidir. Ey Levililer, çok ileri gittiniz!››"));
trsuperobject.biblemap.put("b4_16_8", new trsuperobject(4,16,8,"Musa Korahla konuşmasını şöyle sürdürdü: ‹‹Ey Levililer, beni dinleyin!"));
trsuperobject.biblemap.put("b4_16_9-10", new trsuperobject(4,16,9-10,"İsrailin Tanrısı sizi kendi huzuruna çıkarmak için ayırdı. RABbin Konutunun hizmetini yapmanız, topluluğun önünde durmanız, onlara hizmet etmeniz için sizi İsrail topluluğunun arasından seçti. Sizi ve bütün Levili kardeşlerinizi huzuruna çıkardı. Bu yetmiyormuş gibi kâhinliği de mi istiyorsunuz?"));
trsuperobject.biblemap.put("b4_16_11", new trsuperobject(4,16,11,"Ey Korah, senin ve yandaşlarının böyle toplanması RABbe karşı gelmektir. Harun kim ki, ona dil uzatıyorsunuz?››"));
trsuperobject.biblemap.put("b4_16_12", new trsuperobject(4,16,12,"Sonra Musa Eliavoğulları Datanla Aviramı çağırttı. Ama onlar, ‹‹Gelmeyeceğiz›› dediler,"));
trsuperobject.biblemap.put("b4_16_13", new trsuperobject(4,16,13,"‹‹Bizi çölde öldürtmek için süt ve bal akan ülkeden çıkardın. Bu yetmiyormuş gibi başımıza geçmek istiyorsun."));
trsuperobject.biblemap.put("b4_16_14", new trsuperobject(4,16,14,"Bizi süt ve bal akan ülkeye götürmediğin gibi mülk olarak bize tarlalar, bağlar da vermedin. Bu adamları kör mü sanıyorsun? Hayır, gelmeyeceğiz.››"));
trsuperobject.biblemap.put("b4_16_15", new trsuperobject(4,16,15,"Çok öfkelenen Musa RABbe, ‹‹Onların sunularını önemseme. Onlardan bir eşek bile almadım, üstelik hiçbirine de haksızlık etmedim›› dedi."));
trsuperobject.biblemap.put("b4_16_16", new trsuperobject(4,16,16,"Sonra Koraha, ‹‹Yarın sen ve bütün yandaşların -sen de, onlar da- RABbin önünde bulunmak için gelin›› dedi, ‹‹Harun da gelsin."));
trsuperobject.biblemap.put("b4_16_17", new trsuperobject(4,16,17,"Herkes kendi buhurdanını alıp içine buhur koysun. İki-yüz elli kişi birer buhurdan alıp RABbin önüne getirsin. Harunla sen de buhurdanlarınızı getirin.››"));
trsuperobject.biblemap.put("b4_16_18", new trsuperobject(4,16,18,"Böylece herkes buhurdanını alıp içine ateş, ateşin üstüne de buhur koydu. Sonra Musa ve Harunla birlikte Buluşma Çadırının giriş bölümünde durdular."));
trsuperobject.biblemap.put("b4_16_19", new trsuperobject(4,16,19,"Korah bütün topluluğu Musayla Harunun karşısında Buluşma Çadırının giriş bölümünde toplayınca, RABbin görkemi bütün topluluğa göründü."));
trsuperobject.biblemap.put("b4_16_2-021", new trsuperobject(4,16,2-021,"RAB, Musayla Haruna, ‹‹Bu topluluğun arasından ayrılın da onları bir anda yok edeyim›› dedi."));
trsuperobject.biblemap.put("b4_16_22", new trsuperobject(4,16,22,"Musayla Harun yüzüstü yere kapanarak, ‹‹Ey Tanrı, bütün insan ruhlarının Tanrısı!›› dediler, ‹‹Bir kişi günah işledi diye bütün topluluğa mı öfkeleneceksin?››"));
trsuperobject.biblemap.put("b4_16_23-24", new trsuperobject(4,16,23-24,"RAB Musaya, ‹‹Topluluğa söyle, Korahın, Datanın, Aviramın çadırlarından uzaklaşsınlar›› dedi."));
trsuperobject.biblemap.put("b4_16_25", new trsuperobject(4,16,25,"Musa Datanla Avirama gitti. İsrailin ileri gelenleri onu izledi."));
trsuperobject.biblemap.put("b4_16_26", new trsuperobject(4,16,26,"Topluluğu uyararak, ‹‹Bu kötü adamların çadırlarından uzak durun!›› dedi, ‹‹Onların hiçbir şeyine dokunmayın. Yoksa onların günahları yüzünden canınızdan olursunuz.››"));
trsuperobject.biblemap.put("b4_16_27", new trsuperobject(4,16,27,"Bunun üzerine topluluk Korah, Datan ve Aviramın çadırlarından uzaklaştı. Datanla Aviram çıkıp karıları, küçük büyük çocuklarıyla birlikte çadırlarının önünde durdular."));
trsuperobject.biblemap.put("b4_16_28", new trsuperobject(4,16,28,"Musa şöyle dedi: ‹‹Bütün bunları yapmam için RABbin beni gönderdiğini, kendiliğimden bir şey yapmadığımı şuradan anlayacaksınız:"));
trsuperobject.biblemap.put("b4_16_29", new trsuperobject(4,16,29,"Eğer bu adamlar herkes gibi doğal bir ölümle ölür, herkesin başına gelen bir olayla karşılaşırlarsa, bilin ki beni RAB göndermemiştir."));
trsuperobject.biblemap.put("b4_16_30", new trsuperobject(4,16,30,"Ama RAB yepyeni bir olay yaratırsa, yer yarılıp onları ve onlara ait olan her şeyi yutarsa, ölüler diyarına diri diri inerlerse, bu adamların RABbe saygısızlık ettiklerini anlayacaksınız.››"));
trsuperobject.biblemap.put("b4_16_31", new trsuperobject(4,16,31,"Musa konuşmasını bitirir bitirmez Korah, Datan ve Aviramın altındaki yer yarıldı."));
trsuperobject.biblemap.put("b4_16_32", new trsuperobject(4,16,32,"Yer yarıldı, onları, ailelerini, Korahın adamlarıyla mallarını yuttu."));
trsuperobject.biblemap.put("b4_16_33", new trsuperobject(4,16,33,"Sahip oldukları her şeyle birlikte diri diri ölüler diyarına indiler. Yer onların üzerine kapandı. Topluluğun arasından yok oldular."));
trsuperobject.biblemap.put("b4_16_34", new trsuperobject(4,16,34,"Çığlıklarını duyan çevredeki İsrailliler, ‹‹Yer bizi de yutmasın!›› diyerek kaçıştılar."));
trsuperobject.biblemap.put("b4_16_35", new trsuperobject(4,16,35,"RABbin gönderdiği ateş buhur sunan iki yüz elli adamı yakıp yok etti."));
trsuperobject.biblemap.put("b4_16_36", new trsuperobject(4,16,36,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b4_16_37", new trsuperobject(4,16,37,"‹‹Kâhin Harun oğlu Elazara buhurdanları ateşin içinden çıkarmasını, ateş korlarını az öteye dağıtmasını söyle. Çünkü buhurdanlar kutsaldır."));
trsuperobject.biblemap.put("b4_16_38", new trsuperobject(4,16,38,"İşledikleri günahtan ötürü öldürülen bu adamların buhurdanlarını levha haline getirip sunağı bunlarla kapla. Buhurdanlar RABbe sunuldukları için kutsaldır. Bunlar İsrailliler için bir uyarı olsun.››"));
trsuperobject.biblemap.put("b4_16_39-40", new trsuperobject(4,16,39-40,"Böylece Kâhin Elazar, yanarak ölen adamların getirdiği tunç buhurdanları RABbin Musa aracılığıyla kendisine söylediği gibi alıp döverek sunağı kaplamak için levha haline getirdi. Bu, İsraillilere Harunun soyundan gelenlerden başka hiç kimsenin RABbin önüne çıkıp buhur yakmaması gerektiğini anımsatacaktı. Yoksa o kişi Korahla yandaşları gibi yok olacaktı."));
trsuperobject.biblemap.put("b4_16_41", new trsuperobject(4,16,41,"Ertesi gün bütün İsrail topluluğu Musayla Haruna söylenmeye başladı. ‹‹RABbin halkını siz öldürdünüz›› diyorlardı."));
trsuperobject.biblemap.put("b4_16_42", new trsuperobject(4,16,42,"Topluluk Musayla Haruna karşı toplanıp Buluşma Çadırına doğru yönelince, çadırı ansızın bulut kapladı ve RABbin görkemi göründü."));
trsuperobject.biblemap.put("b4_16_43", new trsuperobject(4,16,43,"Musayla Harun Buluşma Çadırının önüne geldiler."));
trsuperobject.biblemap.put("b4_16_44-45", new trsuperobject(4,16,44-45,"RAB Musaya, ‹‹Bu topluluğun arasından ayrılın da onları birden yok edeyim›› dedi. Musayla Harun yüzüstü yere kapandılar."));
trsuperobject.biblemap.put("b4_16_46", new trsuperobject(4,16,46,"Sonra Musa Haruna, ‹‹Buhurdanını alıp içine sunaktan ateş koy, üstüne de buhur koy›› dedi, ‹‹Günahlarını bağışlatmak için hemen topluluğa git. Çünkü RAB öfkesini yağdırdı. Öldürücü hastalık başladı.››"));
trsuperobject.biblemap.put("b4_16_47", new trsuperobject(4,16,47,"Harun Musanın dediğini yaparak buhurdanını alıp topluluğun ortasına koştu. Halkın arasında öldürücü hastalık başlamıştı. Harun buhur sunarak topluluğun günahını bağışlattı."));
trsuperobject.biblemap.put("b4_16_48", new trsuperobject(4,16,48,"O ölülerle dirilerin arasında durunca, öldürücü hastalık da dindi."));
trsuperobject.biblemap.put("b4_16_49", new trsuperobject(4,16,49,"Korah olayında ölenler dışında, öldürücü hastalıktan ölenlerin sayısı 14 700 kişiydi."));
trsuperobject.biblemap.put("b4_16_50", new trsuperobject(4,16,50,"Öldürücü hastalık dindiğinden, Harun Musa'nın yanına, Buluşma Çadırı'nın giriş bölümüne döndü."));
trsuperobject.biblemap.put("b4_17_2", new trsuperobject(4,17,2,"‹‹İsrail halkına her oymak önderi için bir tane olmak üzere on iki değnek getirmesini söyle. Her önderin adını kendi değneğinin üzerine yaz."));
trsuperobject.biblemap.put("b4_17_3", new trsuperobject(4,17,3,"Levi oymağının değneği üzerine Harunun adını yazacaksın. Her oymak önderi için bir değnek olacak."));
trsuperobject.biblemap.put("b4_17_4", new trsuperobject(4,17,4,"Değnekleri Buluşma Çadırında sizinle buluştuğum Levha Sandığının önüne koy."));
trsuperobject.biblemap.put("b4_17_5", new trsuperobject(4,17,5,"Seçeceğim kişinin değneği filiz verecek. İsrail halkının sizden sürekli yakınmasına son vereceğim.››"));
trsuperobject.biblemap.put("b4_17_6", new trsuperobject(4,17,6,"Musa İsrail halkıyla konuştu. Halkın önderleri, her oymak önderi için bir tane olmak üzere on iki değnek getirdiler. Harunun değneği de aralarındaydı."));
trsuperobject.biblemap.put("b4_17_7", new trsuperobject(4,17,7,"Musa değnekleri Levha Sandığının bulunduğu çadırda RABbin önüne koydu."));
trsuperobject.biblemap.put("b4_17_8", new trsuperobject(4,17,8,"Ertesi gün Musa Levha Sandığının bulunduğu çadıra girdi. Baktı, Levi oymağını temsil eden Harunun değneği filiz vermiş, tomurcuklanıp çiçek açmış, badem yetiştirmiş."));
trsuperobject.biblemap.put("b4_17_9", new trsuperobject(4,17,9,"Musa bütün değnekleri RABbin önünden çıkarıp İsrail halkına gösterdi. Halk değneklere baktı, her biri kendi değneğini aldı."));
trsuperobject.biblemap.put("b4_17_10", new trsuperobject(4,17,10,"RAB Musaya, ‹‹Başkaldıranlara bir uyarı olsun diye Harunun değneğini saklanmak üzere Levha Sandığının önüne koy›› dedi, ‹‹Onların benden yakınmalarına son vereceksin; öyle ki, ölmesinler.››"));
trsuperobject.biblemap.put("b4_17_11", new trsuperobject(4,17,11,"Musa RABbin buyruğu uyarınca davrandı."));
trsuperobject.biblemap.put("b4_17_12", new trsuperobject(4,17,12,"İsrailliler Musaya, ‹‹Yok olacağız! Öleceğiz! Hepimiz yok olacağız!›› dediler,"));
trsuperobject.biblemap.put("b4_17_13", new trsuperobject(4,17,13,"‹‹RAB'bin Konutu'na her yaklaşan ölüyor. Hepimiz mi yok olacağız?››"));
trsuperobject.biblemap.put("b4_18_2", new trsuperobject(4,18,2,"Sen ve oğulların Levha Sandığının bulunduğu çadırın önünde hizmet ederken, atanız Levinin oymağından kardeşlerinizin de size katılıp yardım etmelerini sağlayın."));
trsuperobject.biblemap.put("b4_18_3", new trsuperobject(4,18,3,"Senin sorumluluğun altında çadırda hizmet etsinler. Ancak, siz de onlar da ölmeyesiniz diye kutsal yerin eşyalarına ya da sunağa yaklaşmasınlar."));
trsuperobject.biblemap.put("b4_18_4", new trsuperobject(4,18,4,"Seninle çalışacak ve Buluşma Çadırıyla ilgili bütün hizmetlerden sorumlu olacaklar. Levililer dışında hiç kimse bulunduğunuz yere yaklaşmayacak."));
trsuperobject.biblemap.put("b4_18_5", new trsuperobject(4,18,5,"‹‹Bundan sonra İsrail halkına öfkelenmemem için kutsal yerin ve sunağın hizmetinden sizler sorumlu olacaksınız."));
trsuperobject.biblemap.put("b4_18_6", new trsuperobject(4,18,6,"Ben İsrailliler arasından Levili kardeşlerinizi size bir armağan olarak seçtim. Buluşma Çadırıyla ilgili hizmeti yapmaları için onlar bana adanmıştır."));
trsuperobject.biblemap.put("b4_18_7", new trsuperobject(4,18,7,"Ama sunaktaki ve perdenin ötesindeki kâhinlik görevini sen ve oğulların üstleneceksiniz. Kâhinlik görevini size armağan olarak veriyorum. Sizden başka kutsal yere kim yaklaşırsa öldürülecektir.››"));
trsuperobject.biblemap.put("b4_18_9", new trsuperobject(4,18,9,"Sunakta tümüyle yakılmayan, bana sunulan en kutsal sunulardan şunlar senin olacak: Tahıl, suç ve günah sunuları. En kutsal sunular senin ve oğullarının olacak."));
trsuperobject.biblemap.put("b4_18_10", new trsuperobject(4,18,10,"Bunları en kutsal sunu olarak yiyeceksin. Her erkek onlardan yiyebilir. Onları kutsal sayacaksın."));
trsuperobject.biblemap.put("b4_18_11", new trsuperobject(4,18,11,"‹‹Ayrıca şunlar da senin olacak: İsraillilerin sunduğu sallamalık sunuların bağış kısımlarını sonsuza dek pay olarak sana, oğullarına ve kızlarına veriyorum. Ailende dinsel açıdan temiz olan herkes onları yiyebilir."));
trsuperobject.biblemap.put("b4_18_12", new trsuperobject(4,18,12,"‹‹RABbe verdikleri ilk ürünleri -zeytinyağının, yeni şarabın, tahılın en iyisini- sana veriyorum."));
trsuperobject.biblemap.put("b4_18_13", new trsuperobject(4,18,13,"Ülkede yetişen ilk ürünlerden RABbe getirdiklerinin tümü senin olacak. Ailende dinsel açıdan temiz olan herkes onları yiyebilir."));
trsuperobject.biblemap.put("b4_18_14", new trsuperobject(4,18,14,"‹‹İsrailde RABbe koşulsuz adanan her şey senin olacak."));
trsuperobject.biblemap.put("b4_18_15", new trsuperobject(4,18,15,"İnsan olsun hayvan olsun RABbe adanan her rahmin ilk ürünü senin olacak. Ancak ilk doğan her çocuk ve kirli sayılan hayvanların her ilk doğanı için kesinlikle bedel alacaksın."));
trsuperobject.biblemap.put("b4_18_16", new trsuperobject(4,18,16,"İlk doğanlar bir aylıkken, kendi biçeceğin değer uyarınca, yirmi geradan oluşan kutsal yerin şekeline göre beş şekel gümüş bedel alacaksın."));
trsuperobject.biblemap.put("b4_18_17", new trsuperobject(4,18,17,"‹‹Ancak sığırın, koyunun ya da keçinin ilk doğanı için bedel almayacaksın. Onlar benim için ayrılmıştır. Kanlarını sunağın üzerine dökeceksin, yağlarını RABbi hoşnut eden koku olsun diye yakılan bir sunu olarak yakacaksın."));
trsuperobject.biblemap.put("b4_18_18", new trsuperobject(4,18,18,"Sallamalık sununun göğsü ve sağ budu senin olduğu gibi eti de senin olacak."));
trsuperobject.biblemap.put("b4_18_19", new trsuperobject(4,18,19,"İsraillilerin bana sundukları kutsal sunuların bağış kısımlarını sonsuza dek pay olarak sana, oğullarına ve kızlarına veriyorum. Senin ve soyun için bu RABbin önünde sonsuza dek sürecek bozulmaz bir antlaşmadır.›› antlaşmasıdır››."));
trsuperobject.biblemap.put("b4_18_20", new trsuperobject(4,18,20,"RAB Harunla konuşmasını şöyle sürdürdü: ‹‹Onların ülkesinde mirasın olmayacak, aralarında hiçbir payın olmayacak. İsrailliler arasında payın ve mirasın benim."));
trsuperobject.biblemap.put("b4_18_21", new trsuperobject(4,18,21,"‹‹Buluşma Çadırıyla ilgili yaptıkları hizmete karşılık, İsrailde toplanan bütün ondalıkları pay olarak Levililere veriyorum."));
trsuperobject.biblemap.put("b4_18_22", new trsuperobject(4,18,22,"Bundan böyle öbür İsrailliler Buluşma Çadırına yaklaşmamalı. Yoksa günahlarının bedelini canlarıyla öderler."));
trsuperobject.biblemap.put("b4_18_23", new trsuperobject(4,18,23,"Buluşma Çadırıyla ilgili hizmeti Levililer yapacak, çadıra karşı işlenen suçtan onlar sorumlu olacak. Gelecek kuşaklarınız boyunca kalıcı bir kural olacak bu. İsrailliler arasında onların payı olmayacak."));
trsuperobject.biblemap.put("b4_18_24", new trsuperobject(4,18,24,"Bunun yerine İsraillilerin RABbe armağan olarak verdiği ondalığı miras olarak Levililere veriyorum. Bu yüzden Levililer için, ‹İsrailliler arasında onların mirası olmayacak› dedim.››"));
trsuperobject.biblemap.put("b4_18_25", new trsuperobject(4,18,25,"RAB Musaya şöyle dedi:"));
trsuperobject.biblemap.put("b4_18_26", new trsuperobject(4,18,26,"‹‹Levililere de ki, ‹Pay olarak size verdiğim ondalıkları İsraillilerden alınca, aldığınız ondalığın ondalığını RABbe armağan olarak sunacaksınız."));
trsuperobject.biblemap.put("b4_18_27", new trsuperobject(4,18,27,"Armağanınız harmandan tahıl ya da üzüm sıkma çukurundan bir armağan sayılacaktır."));
trsuperobject.biblemap.put("b4_18_28", new trsuperobject(4,18,28,"Böylelikle siz de İsraillilerden aldığınız bütün ondalıklardan RABbe armağan sunacaksınız. Bu ondalıklardan RABbin armağanını Kâhin Haruna vereceksiniz."));
trsuperobject.biblemap.put("b4_18_29", new trsuperobject(4,18,29,"Aldığınız bütün armağanlardan RAB için bir armağan ayıracaksınız; hepsinin en iyisini, en kutsalını ayıracaksınız.›"));
trsuperobject.biblemap.put("b4_18_30", new trsuperobject(4,18,30,"‹‹Levililere şöyle de: ‹En iyisini sunduğunuzda, geri kalanı harman ya da asma ürünü olarak size sayılacaktır."));
trsuperobject.biblemap.put("b4_18_31", new trsuperobject(4,18,31,"Siz ve aileniz her yerde ondan yiyebilirsiniz. Buluşma Çadırında yaptığınız hizmete karşılık size verilen ücrettir bu."));
trsuperobject.biblemap.put("b4_18_32", new trsuperobject(4,18,32,"En iyisini sunarsanız, bu konuda günah işlememiş olursunuz. Ölmemek için İsrailliler'in sunduğu kutsal sunuları kirletmeyeceksiniz.››"));
trsuperobject.biblemap.put("b4_19_2", new trsuperobject(4,19,2,"‹‹RABbin buyurduğu yasanın kuralı şudur: İsraillilere size kusursuz, özürsüz, boyunduruk takmamış kızıl bir inek getirmelerini söyleyin."));
trsuperobject.biblemap.put("b4_19_3", new trsuperobject(4,19,3,"İnek Kâhin Elazara verilsin; ordugahın dışına çıkarılıp onun önünde kesilecek."));
trsuperobject.biblemap.put("b4_19_4", new trsuperobject(4,19,4,"Kâhin Elazar parmağıyla kanından alıp yedi kez Buluşma Çadırının önüne doğru serpecek."));
trsuperobject.biblemap.put("b4_19_5", new trsuperobject(4,19,5,"Sonra Elazarın gözü önünde inek, derisi, eti, kanı ve gübresiyle birlikte yakılacak."));
trsuperobject.biblemap.put("b4_19_6", new trsuperobject(4,19,6,"Kâhin biraz sedir ağacı, mercanköşkotu ve kırmızı iplik alıp yanmakta olan ineğin üzerine atacak."));
trsuperobject.biblemap.put("b4_19_7", new trsuperobject(4,19,7,"Sonra giysilerini yıkayacak, yıkanacak. Ancak o zaman ordugaha girebilir. Ama akşama dek kirli sayılacaktır."));
trsuperobject.biblemap.put("b4_19_8", new trsuperobject(4,19,8,"İneği yakan kişi de giysilerini yıkayacak, yıkanacak. O da akşama dek kirli sayılacak."));
trsuperobject.biblemap.put("b4_19_9", new trsuperobject(4,19,9,"‹‹Temiz sayılan bir kişi ineğin külünü toplayıp ordugahın dışında temiz sayılan bir yere koyacak. İsrail topluluğu temizlenme suyu için bu külü saklayacak; bu, günahtan arınmak içindir."));
trsuperobject.biblemap.put("b4_19_10", new trsuperobject(4,19,10,"İneğin külünü toplayan adam giysilerini yıkayacak, akşama dek kirli sayılacak. Bu kural hem İsrailliler, hem de aralarında yaşayan yabancılar için kalıcı olacaktır."));
trsuperobject.biblemap.put("b4_19_11", new trsuperobject(4,19,11,"‹‹Herhangi bir insan ölüsüne dokunan kişi yedi gün kirli sayılacaktır."));
trsuperobject.biblemap.put("b4_19_12", new trsuperobject(4,19,12,"Üçüncü ve yedinci gün temizlenme suyuyla kendini arındıracak, böylece paklanmış olacak. Üçüncü ve yedinci gün kendini arındırmazsa, paklanmış sayılmayacak."));
trsuperobject.biblemap.put("b4_19_13", new trsuperobject(4,19,13,"Herhangi bir insan ölüsüne dokunup da kendini arındırmayan kişi RABbin Konutunu kirletmiş olur. O kişi İsrailden atılmalı. Temizlenme suyu üzerine dökülmediği için kirli sayılır, kirliliği üzerinde kalır."));
trsuperobject.biblemap.put("b4_19_14", new trsuperobject(4,19,14,"‹‹Çadırda biri öldüğü zaman uygulanacak kural şudur: Çadıra giren ve çadırda bulunan herkes yedi gün kirli sayılacaktır."));
trsuperobject.biblemap.put("b4_19_15", new trsuperobject(4,19,15,"Kapağı iple bağlanmamış, ağzı açık her kap kirli sayılacaktır."));
trsuperobject.biblemap.put("b4_19_16", new trsuperobject(4,19,16,"‹‹Kırda kılıçla öldürülmüş ya da doğal ölümle ölmüş birine, insan kemiğine ya da mezara her dokunan yedi gün kirli sayılacaktır."));
trsuperobject.biblemap.put("b4_19_17", new trsuperobject(4,19,17,"‹‹Kirli sayılan kişi için bir kabın içine yakılan günah sunusunun külünden koyun, üstüne duru su dökeceksiniz."));
trsuperobject.biblemap.put("b4_19_18", new trsuperobject(4,19,18,"Temiz sayılan bir adam mercanköşkotunu alıp suya batıracak. Sonra çadırın, bütün eşyaların ve orada bulunanların üzerine serpecek. Kemiğe, öldürülmüş ya da doğal ölümle ölmüş kişiye ya da mezara dokunanın üzerine de suyu serpecek."));
trsuperobject.biblemap.put("b4_19_19", new trsuperobject(4,19,19,"Temiz sayılan adam, üçüncü ve yedinci gün kirli sayılanın üzerine suyu serpecek. Yedinci gün onu arındıracak. Arınan kişi giysilerini yıkayacak, yıkanacak ve akşam temiz sayılacak."));
trsuperobject.biblemap.put("b4_19_20", new trsuperobject(4,19,20,"Ancak, kirli sayılan biri kendini arındırmazsa topluluğun arasından atılmalı. Çünkü RABbin Tapınağını kirletmiştir. Temizlenme suyu üzerine dökülmediği için kirli sayılır."));
trsuperobject.biblemap.put("b4_19_21", new trsuperobject(4,19,21,"Onlar için bu kural kalıcı olacaktır. Temizlenme suyunu serpen kişi de giysisini yıkamalı. Temizlenme suyuna dokunan kişi akşama dek kirli sayılacak."));
trsuperobject.biblemap.put("b4_19_22", new trsuperobject(4,19,22,"Kirli sayılan birinin dokunduğu nesne kirli sayılır; o nesneye dokunan da akşama dek kirli sayılır.››"));
trsuperobject.biblemap.put("b4_20_1", new trsuperobject(4,20,1,"İsrail topluluğu birinci ay Zin Çölüne vardı, halk Kadeşte konakladı. Miryam orada öldü ve gömüldü."));
trsuperobject.biblemap.put("b4_20_2", new trsuperobject(4,20,2,"Ancak topluluk için içecek su yoktu. Halk Musayla Haruna karşı toplandı."));
trsuperobject.biblemap.put("b4_20_3", new trsuperobject(4,20,3,"Musaya, ‹‹Keşke kardeşlerimiz RABbin önünde öldüğünde biz de ölseydik!›› diye çıkıştılar,"));
trsuperobject.biblemap.put("b4_20_4", new trsuperobject(4,20,4,"‹‹RABbin topluluğunu neden bu çöle getirdiniz? Biz de hayvanlarımız da ölelim diye mi?"));
trsuperobject.biblemap.put("b4_20_5", new trsuperobject(4,20,5,"Neden bizi bu korkunç yere getirmek için Mısırdan çıkardınız? Ne tahıl, ne incir, ne üzüm ne de nar var. Üstelik içecek su da yok!››"));
trsuperobject.biblemap.put("b4_20_6", new trsuperobject(4,20,6,"Musayla Harun topluluktan ayrılıp Buluşma Çadırının giriş bölümüne gittiler, yüzüstü yere kapandılar. RABbin görkemi onlara göründü."));
trsuperobject.biblemap.put("b4_20_7-8", new trsuperobject(4,20,7-8,"RAB Musaya, ‹‹Değneği al›› dedi, ‹‹Sen ve ağabeyin Harun topluluğu toplayın. Halkın gözü önünde su fışkırması için kayaya buyruk verin. Onlar da hayvanları da içsin diye kayadan onlara su çıkaracaksınız.››"));
trsuperobject.biblemap.put("b4_20_9", new trsuperobject(4,20,9,"Musa kendisine verilen buyruk uyarınca değneği RABbin önünden aldı."));
trsuperobject.biblemap.put("b4_20_10", new trsuperobject(4,20,10,"Musayla Harun topluluğu kayanın önüne topladılar. Musa, ‹‹Ey siz, başkaldıranlar, beni dinleyin!›› dedi, ‹‹Bu kayadan size su çıkaralım mı?››"));
trsuperobject.biblemap.put("b4_20_11", new trsuperobject(4,20,11,"Sonra kolunu kaldırıp değneğiyle kayaya iki kez vurdu. Kayadan bol su fışkırdı, topluluk da hayvanları da içti."));
trsuperobject.biblemap.put("b4_20_12", new trsuperobject(4,20,12,"RAB Musayla Haruna, ‹‹Madem İsraillilerin gözü önünde benim kutsallığımı sayarak bana güvenmediniz›› dedi, ‹‹Bu topluluğu kendilerine vereceğim ülkeye de götürmeyeceksiniz.››"));
trsuperobject.biblemap.put("b4_20_13", new trsuperobject(4,20,13,"Bu sulara Merivafç suları denildi. Çünkü İsrail halkı orada RABbe çıkışmış, RAB de aralarında kutsallığını göstermişti."));
trsuperobject.biblemap.put("b4_20_14", new trsuperobject(4,20,14,"Musa Kadeşten Edom Kralına ulaklarla şu haberi gönderdi: ‹‹Kardeşin İsrail şöyle diyor: ‹Başımıza gelen güçlükleri biliyorsun."));
trsuperobject.biblemap.put("b4_20_15", new trsuperobject(4,20,15,"Atalarımız Mısıra gitmişler. Orada uzun yıllar yaşadık. Mısırlılar atalarımıza da bize de kötü davrandılar."));
trsuperobject.biblemap.put("b4_20_16", new trsuperobject(4,20,16,"Ama biz RABbe yakarınca, yakarışımızı işitti. Bir melek gönderip bizi Mısırdan çıkardı. ‹‹Şimdi senin sınırına yakın bir kent olan Kadeşteyiz."));
trsuperobject.biblemap.put("b4_20_17", new trsuperobject(4,20,17,"İzin ver, ülkenden geçelim. Tarlalardan, bağlardan geçmeyeceğiz, hiçbir kuyudan da su içmeyeceğiz. Sınırından geçinceye dek, sağa sola sapmadan Kral yolundan yolumuza devam edeceğiz.››"));
trsuperobject.biblemap.put("b4_20_18", new trsuperobject(4,20,18,"Ama Edom Kralı, ‹‹Ülkemden geçmeyeceksiniz!›› diye yanıtladı, ‹‹Geçmeye kalkışırsanız kılıçla karşınıza çıkarım.››"));
trsuperobject.biblemap.put("b4_20_19", new trsuperobject(4,20,19,"İsrailliler, ‹‹Yol boyunca geçip gideceğiz›› dediler, ‹‹Eğer biz ya da hayvanlarımız suyundan içersek karşılığını öderiz. Yürüyüp geçmek için senden izin istiyoruz, hepsi bu.››"));
trsuperobject.biblemap.put("b4_20_20", new trsuperobject(4,20,20,"Edom Kralı yine, ‹‹Geçmeyeceksiniz!›› yanıtını verdi. Edomlular İsraillilere saldırmak üzere kalabalık ve güçlü bir orduyla yola çıktılar."));
trsuperobject.biblemap.put("b4_20_21", new trsuperobject(4,20,21,"Edom Kralı ülkesinden geçmelerine izin vermeyince, İsrailliler dönüp ondan uzaklaştılar."));
trsuperobject.biblemap.put("b4_20_22", new trsuperobject(4,20,22,"İsrail topluluğu Kadeşten ayrılıp Hor Dağına geldi."));
trsuperobject.biblemap.put("b4_20_23", new trsuperobject(4,20,23,"RAB, Edom sınırındaki Hor Dağında Musayla Haruna şöyle dedi:"));
trsuperobject.biblemap.put("b4_20_24", new trsuperobject(4,20,24,"‹‹Harun ölüp atalarına kavuşacak. İsrail halkına vereceğim ülkeye girmeyecek. Çünkü ikiniz Meriva sularında verdiğim buyruğa karşı geldiniz."));
trsuperobject.biblemap.put("b4_20_25", new trsuperobject(4,20,25,"Harunla oğlu Elazarı Hor Dağına çıkar."));
trsuperobject.biblemap.put("b4_20_26", new trsuperobject(4,20,26,"Harunun kâhinlik giysilerini üzerinden çıkarıp oğlu Elazara giydir. Harun orada ölüp atalarına kavuşacak.››"));
trsuperobject.biblemap.put("b4_20_27", new trsuperobject(4,20,27,"Musa RABbin buyurduğu gibi yaptı. Bütün topluluğun gözü önünde Hor Dağına çıktılar."));
trsuperobject.biblemap.put("b4_20_28", new trsuperobject(4,20,28,"Musa Harunun kâhinlik giysilerini üzerinden çıkarıp oğlu Elazara giydirdi. Harun orada, dağın tepesinde öldü. Sonra Musayla Elazar dağdan indiler."));
trsuperobject.biblemap.put("b4_20_29", new trsuperobject(4,20,29,"Harun'un öldüğünü öğrenince bütün İsrail halkı onun için otuz gün yas tuttu."));
trsuperobject.biblemap.put("b4_21_1", new trsuperobject(4,21,1,"Negevde yaşayan Kenanlı Arat Kralı, İsraillilerin Atarim yolundan geldiğini duyunca, onlara saldırarak bazılarını tutsak aldı."));
trsuperobject.biblemap.put("b4_21_2", new trsuperobject(4,21,2,"Bunun üzerine İsrailliler, ‹‹Eğer bu halkı tümüyle elimize teslim edersen, kentlerini büsbütün yok edeceğiz›› diyerek RABbe adak adadılar."));
trsuperobject.biblemap.put("b4_21_4", new trsuperobject(4,21,4,"Edom ülkesinin çevresinden geçmek için Kızıldeniz yoluyla Hor Dağından ayrıldılar. Ama yolda halk sabırsızlandı."));
trsuperobject.biblemap.put("b4_21_5", new trsuperobject(4,21,5,"Tanrıdan ve Musadan yakınarak, ‹‹Çölde ölelim diye mi bizi Mısırdan çıkardınız?›› dediler, ‹‹Burada ne ekmek var, ne de su. Ayrıca bu iğrenç yiyecekten de tiksiniyoruz!››"));
trsuperobject.biblemap.put("b4_21_6", new trsuperobject(4,21,6,"Bunun üzerine RAB halkın arasına zehirli yılanlar gönderdi. Yılanlar ısırınca İsraillilerden birçok kişi öldü."));
trsuperobject.biblemap.put("b4_21_7", new trsuperobject(4,21,7,"Halk Musaya gelip, ‹‹RABden ve senden yakınmakla günah işledik. Yalvar da, RAB aramızdan yılanları kaldırsın›› dedi. Bunun üzerine Musa halk için yalvardı."));
trsuperobject.biblemap.put("b4_21_9", new trsuperobject(4,21,9,"Böylece Musa tunç bir yılan yaparak direğin üzerine koydu. Yılan tarafından ısırılan kişiler tunç yılana bakınca yaşadı."));
trsuperobject.biblemap.put("b4_21_10", new trsuperobject(4,21,10,"İsrail halkı yola koyulup Ovotta konakladı."));
trsuperobject.biblemap.put("b4_21_11", new trsuperobject(4,21,11,"Sonra Ovottan ayrılıp doğuda Moava bakan çölde, İye- Haavarimde konakladı."));
trsuperobject.biblemap.put("b4_21_12", new trsuperobject(4,21,12,"Oradan da ayrılıp Zeret Vadisinde konakladı."));
trsuperobject.biblemap.put("b4_21_13", new trsuperobject(4,21,13,"Oradan da ayrılıp Amorluların sınırına dek uzanan çölde, Arnon Vadisinin karşı yakasında konakladılar. Arnon Moavla Amorluların ülkesi arasındaki Moav sınırıdır."));
trsuperobject.biblemap.put("b4_21_14", new trsuperobject(4,21,14,"RABbin Savaşları Kitabında şöyle yazılıdır: ‹‹... Sufa topraklarında Vahev Kenti, vadiler, Arnon Vadisi,"));
trsuperobject.biblemap.put("b4_21_15", new trsuperobject(4,21,15,"Ar Kentine dayanan ve Moav sınırı boyunca uzanan vadilerin yamaçları ...››"));
trsuperobject.biblemap.put("b4_21_16", new trsuperobject(4,21,16,"Oradan RABbin Musaya, ‹‹Halkı bir araya topla, onlara su vereceğim›› dediği kuyuya, Beere doğru yol aldılar."));
trsuperobject.biblemap.put("b4_21_17", new trsuperobject(4,21,17,"O zaman İsrailliler şu ezgiyi söylediler:  ‹‹Suların fışkırsın, ey kuyu!  Ezgi okuyun ona."));
trsuperobject.biblemap.put("b4_21_18", new trsuperobject(4,21,18,"O kuyu ki, onu önderlerle  Halkın soyluları  Asayla, değnekle kazdılar.›› Bundan sonra çölden Mattanaya,"));
trsuperobject.biblemap.put("b4_21_19", new trsuperobject(4,21,19,"Mattanadan Nahaliele, Nahalielden Bamota,"));
trsuperobject.biblemap.put("b4_21_20", new trsuperobject(4,21,20,"Bamottan Moav topraklarındaki vadiye, çöle bakan Pisga Dağının eteklerine gittiler."));
trsuperobject.biblemap.put("b4_21_21", new trsuperobject(4,21,21,"İsrailliler Amorluların Kralı Sihona ulaklarla şu haberi gönderdi:"));
trsuperobject.biblemap.put("b4_21_22", new trsuperobject(4,21,22,"‹‹İzin ver, ülkenden geçelim. Tarlalardan, bağlardan geçmeyeceğiz, hiçbir kuyudan su içmeyeceğiz. Sınırından geçinceye dek, Kral yolundan yolumuza devam edeceğiz.››"));
trsuperobject.biblemap.put("b4_21_23", new trsuperobject(4,21,23,"Ne var ki Sihon, ülkesinden İsraillilerin geçmesine izin vermedi. İsraillilerle savaşmak üzere bütün halkını toplayıp çöle çıktı. Yahesaya varınca, İsraillilere saldırdı."));
trsuperobject.biblemap.put("b4_21_24", new trsuperobject(4,21,24,"İsrailliler onu kılıçtan geçirip Arnondan Yabbuka, Ammonluların sınırına dek uzanan topraklarını aldılar. Az Kenti Ammon sınırını oluşturuyordu."));
trsuperobject.biblemap.put("b4_21_25", new trsuperobject(4,21,25,"İsrailliler Heşbon ve çevresindeki köylerle birlikte Amorluların bütün kentlerini ele geçirerek orada yaşamaya başladılar."));
trsuperobject.biblemap.put("b4_21_26", new trsuperobject(4,21,26,"Heşbon Amorluların Kralı Sihonun kentiydi. Sihon eski Moav Kralına karşı savaşmış, Arnona dek uzanan topraklarını elinden almıştı."));
trsuperobject.biblemap.put("b4_21_27", new trsuperobject(4,21,27,"Bunun için ozanlar şöyle diyor:  ‹‹Heşbona gelin,  Sihonun kenti yeniden kurulsun  Ve sağlamlaştırılsın."));
trsuperobject.biblemap.put("b4_21_28", new trsuperobject(4,21,28,"Heşbondan ateş,  Sihonun kentinden alev çıktı;  Moavın Ar Kentini,  Arnon tepelerinin efendilerini yakıp yok etti."));
trsuperobject.biblemap.put("b4_21_29", new trsuperobject(4,21,29,"Vay sana, ey Moav!  İlah Kemoşun halkı, yok oldun!  Kemoş senin oğullarının Amorluların Kralı Sihona kaçmasını,  Kızlarının ona tutsak olmasını önleyemedi."));
trsuperobject.biblemap.put("b4_21_30", new trsuperobject(4,21,30,"Onları bozguna uğrattık;  Heşbon Divona dek yıkıma uğradı.  Medevaya uzanan Nofaha dek onları yıkıma uğrattık.››"));
trsuperobject.biblemap.put("b4_21_31", new trsuperobject(4,21,31,"Böylece İsrail halkı Amorluların ülkesinde yaşamaya başladı."));
trsuperobject.biblemap.put("b4_21_32", new trsuperobject(4,21,32,"Musa Yazeri araştırmak için adamlar gönderdi. Sonra İsrailliler Yazer çevresindeki köyleri ele geçirerek orada yaşayan Ammonluları kovdular."));
trsuperobject.biblemap.put("b4_21_33", new trsuperobject(4,21,33,"Bundan sonra dönüp Başana doğru ilerlediler. Başan Kralı Ogla ordusu onlarla savaşmak için Edreide karşılarına çıktı."));
trsuperobject.biblemap.put("b4_21_34", new trsuperobject(4,21,34,"RAB Musaya, ‹‹Ondan korkma!›› dedi, ‹‹Çünkü onu da ordusuyla ülkesini de senin eline teslim ettim. Amorluların Heşbonda yaşayan Kralı Sihona yaptığının aynısını ona da yapacaksın.››"));
trsuperobject.biblemap.put("b4_21_35", new trsuperobject(4,21,35,"Böylece İsrail halkı kimseyi sağ bırakmadan Og'la oğullarını ve ordusunu yok etti, ülkeyi ele geçirdi."));
trsuperobject.biblemap.put("b4_22_1", new trsuperobject(4,22,1,"İsrailliler yollarına devam ederek Moav ovalarında, Şeria Irmağının doğusunda, Eriha karşısında konakladılar."));
trsuperobject.biblemap.put("b4_22_2", new trsuperobject(4,22,2,"Sippor oğlu Balak İsraillilerin Amorlulara neler yaptığını duydu."));
trsuperobject.biblemap.put("b4_22_3", new trsuperobject(4,22,3,"İsrail halkı kalabalık olduğundan, Moavlılar onlardan korkarak yılgıya düştü."));
trsuperobject.biblemap.put("b4_22_4", new trsuperobject(4,22,4,"Midyan ileri gelenlerine, ‹‹Öküz kırda nasıl otu yiyip tüketirse, bu topluluk da çevremizdeki her şeyi yiyip bitirecek›› dediler. O sırada Sippor oğlu Balak Moav Kralıydı."));
trsuperobject.biblemap.put("b4_22_5", new trsuperobject(4,22,5,"Balak, Beor oğlu Balamı çağırmak için ulaklar gönderdi. Balam Fırat Irmağı kıyısında, Amav ülkesindeki Petorda yaşıyordu. Balak şöyle dedi: ‹‹Mısırdan çıkıp yeryüzünü kaplayan bir halk yanıbaşıma yerleşti."));
trsuperobject.biblemap.put("b4_22_6", new trsuperobject(4,22,6,"Lütfen gel de benden daha güçlü olan bu halka benim için lanet oku. Olur ki, onları yener, ülkeden kovarız. Çünkü senin kutsadığın kişinin kutsanacağını, lanetlediğin kişinin lanetleneceğini biliyorum.››"));
trsuperobject.biblemap.put("b4_22_7", new trsuperobject(4,22,7,"Moav ve Midyan ileri gelenleri falcılık ücretini alıp gittiler. Balama varınca Balakın bildirisini ona ilettiler."));
trsuperobject.biblemap.put("b4_22_8", new trsuperobject(4,22,8,"Balam onlara, ‹‹Geceyi burada geçirin›› dedi, ‹‹RABbin bana söyleyecekleri uyarınca size yanıt vereceğim.›› Bunun üzerine Moav önderleri geceyi Balamın yanında geçirdiler."));
trsuperobject.biblemap.put("b4_22_9", new trsuperobject(4,22,9,"Tanrı Balama gelip, ‹‹Evinde kalan bu adamlar kim?›› diye sordu."));
trsuperobject.biblemap.put("b4_22_10", new trsuperobject(4,22,10,"Balam Tanrıyı şöyle yanıtladı: ‹‹Sippor oğlu Moav Kralı Balak bana şu bildiriyi gönderdi:"));
trsuperobject.biblemap.put("b4_22_11", new trsuperobject(4,22,11,"‹Mısırdan çıkan halk yeryüzünü kapladı. Gel de benim için onlara lanet oku. Olur ki, onlarla savaşmaya gücüm yeter, onları kovarım.››"));
trsuperobject.biblemap.put("b4_22_12", new trsuperobject(4,22,12,"Ama Tanrı Balama, ‹‹Onlarla gitme! Bu halka lanet okuma, onlar kutsanmış halktır›› dedi."));
trsuperobject.biblemap.put("b4_22_13", new trsuperobject(4,22,13,"Sabah Balam kalktı, Balakın önderlerine, ‹‹Ülkenize dönün. Çünkü RAB sizinle gelmeme izin vermiyor›› dedi."));
trsuperobject.biblemap.put("b4_22_14", new trsuperobject(4,22,14,"Moav önderleri dönüp Balaka, ‹‹Balam bizimle gelmedi›› dediler."));
trsuperobject.biblemap.put("b4_22_15", new trsuperobject(4,22,15,"Bunun üzerine Balak ilk gidenlerden daha çok ve daha saygın başka önderler gönderdi."));
trsuperobject.biblemap.put("b4_22_16", new trsuperobject(4,22,16,"Balama gidip şöyle dediler: ‹‹Sippor oğlu Balak diyor ki, ‹Lütfen yanıma gelmene engel olan hiçbir şeye izin verme."));
trsuperobject.biblemap.put("b4_22_17", new trsuperobject(4,22,17,"Çünkü seni fazlasıyla ödüllendireceğim, ne istersen yapacağım. Ne olur, gel, benim için bu halka lanet oku.››"));
trsuperobject.biblemap.put("b4_22_18", new trsuperobject(4,22,18,"Balam Balakın ulaklarına şu yanıtı verdi: ‹‹Balak sarayını altınla, gümüşle doldurup bana verse bile, Tanrım RABbin buyruğundan öte küçük büyük hiçbir şey yapamam."));
trsuperobject.biblemap.put("b4_22_19", new trsuperobject(4,22,19,"Lütfen siz de bu gece burada kalın, RABbin bana başka bir diyeceği var mı öğreneyim.››"));
trsuperobject.biblemap.put("b4_22_20", new trsuperobject(4,22,20,"O gece Tanrı Balama gelip, ‹‹Madem bu adamlar seni çağırmaya gelmiş, onlarla git; ancak sana ne söylersem onu yap›› dedi."));
trsuperobject.biblemap.put("b4_22_21", new trsuperobject(4,22,21,"Balam sabah kalkıp eşeğine palan vurdu, Moav önderleriyle birlikte gitti."));
trsuperobject.biblemap.put("b4_22_22", new trsuperobject(4,22,22,"Tanrı onun gidişine öfkelendi. RABbin meleği engel olmak için yoluna dikildi. Balam eşeğine binmişti, yanında iki uşağı vardı."));
trsuperobject.biblemap.put("b4_22_23", new trsuperobject(4,22,23,"Eşek, yalın kılıç yolda durmakta olan RABbin meleğini görünce, yoldan sapıp tarlaya girdi. Balam yola döndürmek için eşeği dövdü."));
trsuperobject.biblemap.put("b4_22_24", new trsuperobject(4,22,24,"RABbin meleği iki bağın arasında iki yanı duvarlı dar bir yolda durdu."));
trsuperobject.biblemap.put("b4_22_25", new trsuperobject(4,22,25,"Eşek RABbin meleğini görünce duvara sıkıştı, Balamın ayağını ezdi. Balam eşeği yine dövdü."));
trsuperobject.biblemap.put("b4_22_26", new trsuperobject(4,22,26,"RABbin meleği ilerledi, sağa sola dönüşü olmayan dar bir yerde durdu."));
trsuperobject.biblemap.put("b4_22_27", new trsuperobject(4,22,27,"Eşek RABbin meleğini görünce, Balamın altında yıkıldı. Balam öfkelendi, değneğiyle eşeği dövdü."));
trsuperobject.biblemap.put("b4_22_28", new trsuperobject(4,22,28,"Bunun üzerine RAB eşeği konuşturdu. Eşek Balama, ‹‹Sana ne yaptım ki, üç kez beni böyle dövdün?›› diye sordu."));
trsuperobject.biblemap.put("b4_22_29", new trsuperobject(4,22,29,"Balam, ‹‹Benimle alay ediyorsun›› diye yanıtladı, ‹‹Elimde kılıç olsaydı, seni hemen öldürürdüm.››"));
trsuperobject.biblemap.put("b4_22_30", new trsuperobject(4,22,30,"Eşek, ‹‹Bugüne dek hep üzerine bindiğin eşek değil miyim ben?›› dedi, ‹‹Daha önce sana hiç böyle davrandım mı?›› Balam, ‹‹Hayır›› diye yanıtladı."));
trsuperobject.biblemap.put("b4_22_31", new trsuperobject(4,22,31,"Bundan sonra RAB Balamın gözlerini açtı. Balam yalın kılıç yolda durmakta olan RABbin meleğini gördü, eğilip yüzüstü yere kapandı."));
trsuperobject.biblemap.put("b4_22_32", new trsuperobject(4,22,32,"RABbin meleği, ‹‹Neden üç kez eşeğini dövdün?›› diye sordu, ‹‹Ben seni engellemeye geldim. Çünkü gittiğin yol seni yıkıma götürüyor."));
trsuperobject.biblemap.put("b4_22_33", new trsuperobject(4,22,33,"Eşek beni gördü, üç kez önümden saptı. Eğer yoldan sapmasaydı, seni öldürür, onu sağ bırakırdım.››"));
trsuperobject.biblemap.put("b4_22_34", new trsuperobject(4,22,34,"Balam RABbin meleğine, ‹‹Günah işledim›› dedi, ‹‹Beni engellemek için yolda dikildiğini anlamadım. Uygun görmüyorsan şimdi evime döneyim.››"));
trsuperobject.biblemap.put("b4_22_35", new trsuperobject(4,22,35,"RABbin meleği, ‹‹Adamlarla git›› dedi, ‹‹Ama yalnız sana söyleyeceklerimi söyleyeceksin.›› Böylece Balam Balakın önderleriyle gitti."));
