trsuperobject.biblemap.put("b4_24_15", new trsuperobject(4,24,15,"Sonra Balam şu bildiriyi iletti:  ‹‹Beor oğlu Balam,Gözü açılmış olan,"));
trsuperobject.biblemap.put("b4_24_16", new trsuperobject(4,24,16,"Tanrının sözlerini duyan,Yüceler Yücesinin bilgisine kavuşan,Her Şeye Gücü Yetenin görümlerini gören,Yere kapanan, Tanrının gözlerini açtığı kişi bildiriyor:"));
trsuperobject.biblemap.put("b4_24_17", new trsuperobject(4,24,17,"‹Onu görüyorum, ama şimdilik değil,Ona bakıyorum, ama yakından değil.Yakup soyundan bir yıldız çıkacak,İsrailden bir önder yükselecek.Moavlıların alınlarını,Şetoğullarının başlarını ezecek."));
trsuperobject.biblemap.put("b4_24_18", new trsuperobject(4,24,18,"Düşmanı olan Edom ele geçirilecek,Evet, Seir alınacak,Ama İsrail güçlenecek."));
