trsuperobject.biblemap.put("b4_30_2", new trsuperobject(4,30,2,"‹Eğer bir adam RABbe adak adar ya da ant içerek kendini yükümlülük altına sokarsa, verdiği sözü bozmayacak, ağzından her çıkanı yerine getirecek."));
trsuperobject.biblemap.put("b4_30_3", new trsuperobject(4,30,3,"‹‹Genç bir kadın babasının evindeyken RABbe adak adar ya da kendini yükümlülük altına sokarsa,"));
trsuperobject.biblemap.put("b4_30_4", new trsuperobject(4,30,4,"babası da onun RABbe adadığı adağı ve kendini yükümlülük altına soktuğunu duyar, ona karşı çıkmazsa, kadının adadığı adaklar ve kendini altına soktuğu yükümlülük geçerli sayılacak."));
trsuperobject.biblemap.put("b4_30_5", new trsuperobject(4,30,5,"Ama babası bunları duyduğu gün engel olursa, kadının adadığı adaklar ve kendini altına soktuğu yükümlülük geçerli sayılmayacak; RAB onu bağışlayacak, çünkü babası ona engel olmuştur."));
trsuperobject.biblemap.put("b4_30_6", new trsuperobject(4,30,6,"‹‹Eğer kadın adak adadıktan ya da düşünmeden kendini yükümlülük altına soktuktan sonra evlenirse,"));
trsuperobject.biblemap.put("b4_30_7", new trsuperobject(4,30,7,"kocası da bunu duyar ve aynı gün ona karşı çıkmazsa, adadığı adaklar ve kendini altına soktuğu yükümlülük geçerli sayılacak."));
trsuperobject.biblemap.put("b4_30_8", new trsuperobject(4,30,8,"Ama kocası bunu duyduğu gün engel olur, kadının adadığı adağı ya da düşünmeden kendini altına soktuğu yükümlülüğü geçerli saymazsa, RAB kadını bağışlayacaktır."));
trsuperobject.biblemap.put("b4_30_9", new trsuperobject(4,30,9,"‹‹Dul ya da boşanmış bir kadının adadığı adak, kendini yükümlülük altına soktuğu her şey geçerli sayılacak."));
trsuperobject.biblemap.put("b4_30_10", new trsuperobject(4,30,10,"‹‹Eğer bir kadın evliyken bir adak adar ya da ant içerek kendini yükümlülük altına sokarsa,"));
trsuperobject.biblemap.put("b4_30_11", new trsuperobject(4,30,11,"kocası da bunu duyar, karşı çıkmaz, ona engel olmazsa, kadının adadığı bütün adaklar ya da kendini altına soktuğu her yükümlülük geçerli sayılacak."));
trsuperobject.biblemap.put("b4_30_12", new trsuperobject(4,30,12,"Ama kocası bunları duyduğu gün engel olursa, kadının adadığı bütün adaklar ve kendini altına soktuğu yükümlülük geçerli sayılmayacak. Kocası geçersiz kılmıştır, RAB kadını bağışlayacak."));
trsuperobject.biblemap.put("b4_30_13", new trsuperobject(4,30,13,"Kocası, kadının kendi isteklerini denetlemesi için adadığı adağı ya da ant içerek kendini altına soktuğu yükümlülüğü onaylayabilir ya da geçersiz kılabilir."));
trsuperobject.biblemap.put("b4_30_14", new trsuperobject(4,30,14,"Eğer kocası bir gün içinde bu konuda ona karşı çıkmazsa, bütün adaklarını ya da yükümlülüklerini onaylamış olur. Onları duyduğu gün kadına karşı çıkmamakla onaylamış sayılır."));
trsuperobject.biblemap.put("b4_30_15", new trsuperobject(4,30,15,"Eğer onları duyduktan bir süre sonra engel olursa, kadının suçundan kocası sorumlu olacaktır.››"));
