trsuperobject.biblemap.put("b4_32_8", new trsuperobject(4,32,8,"Ülkeyi araştırsınlar diye Kadeş-Barneadan gönderdiğim babalarınız da aynı şeyi yaptılar."));
trsuperobject.biblemap.put("b4_32_9", new trsuperobject(4,32,9,"Eşkol Vadisine kadar gidip ülkeyi gördükten sonra, RABbin kendilerine vereceği ülkeye gitmemeleri için İsraillilerin gözünü korkuttular."));
trsuperobject.biblemap.put("b4_32_10", new trsuperobject(4,32,10,"O gün RAB öfkelenerek şöyle ant içti:"));
trsuperobject.biblemap.put("b4_32_11", new trsuperobject(4,32,11,"‹Madem bütün yürekleriyle ardımca yürümediler, Mısırdan çıkanlardan yirmi ve daha yukarı yaştakilerin hiçbiri İbrahime, İshaka, Yakupa ant içerek söz verdiğim ülkeyi görmeyecek."));
trsuperobject.biblemap.put("b4_32_12", new trsuperobject(4,32,12,"Kenaz soyundan Yefunne oğlu Kalevle Nun oğlu Yeşudan başkası orayı görmeyecek. Çünkü onlar bütün yürekleriyle ardımca yürüdüler.›"));
trsuperobject.biblemap.put("b4_32_13", new trsuperobject(4,32,13,"İsraillilere öfkelenen RAB, gözünde kötülük yapan o kuşak büsbütün yok oluncaya dek kırk yıl onları çölde dolaştırdı."));
trsuperobject.biblemap.put("b4_32_14", new trsuperobject(4,32,14,"‹‹İşte, ey günahkârlar soyu, babalarınızın yerine siz geçtiniz ve RABbin İsraile daha çok öfkelenmesine neden oluyorsunuz."));
trsuperobject.biblemap.put("b4_32_15", new trsuperobject(4,32,15,"Eğer Onun ardınca yürümekten vazgeçerseniz, bütün bu halkı yine çölde bırakacak; siz de bu halkın yok olmasına neden olacaksınız.››"));
trsuperobject.biblemap.put("b4_32_16", new trsuperobject(4,32,16,"Gadlılarla Rubenliler Musaya yaklaşıp, ‹‹Burada hayvanlarımız için ağıllar yapmamıza, çocuklarımız için yeniden kentler kurmamıza izin ver›› dediler,"));
trsuperobject.biblemap.put("b4_32_17", new trsuperobject(4,32,17,"‹‹Kendimiz de hemen silahlanıp İsraillileri kendilerinin olacak ülkeye götürünceye dek onlara öncülük edeceğiz. Ülke halkı yüzünden çocuklarımız surlu kentlerde yaşayacak."));
trsuperobject.biblemap.put("b4_32_18", new trsuperobject(4,32,18,"Her İsrailli mülküne kavuşmadan evlerimize dönmeyeceğiz."));
trsuperobject.biblemap.put("b4_32_19", new trsuperobject(4,32,19,"Şeria Irmağının karşı yakasında onlarla birlikte mülk almayacağız, çünkü bizim payımız Şeria Irmağının doğu yakasına düştü.››"));
trsuperobject.biblemap.put("b4_32_20", new trsuperobject(4,32,20,"Musa şöyle yanıtladı: ‹‹Bu söylediklerinizi yapar, RABbin önünde savaşa gitmek üzere silahlanıp"));
trsuperobject.biblemap.put("b4_32_21", new trsuperobject(4,32,21,"RAB düşmanlarını kovuncaya dek hepiniz Onun önünde Şeria Irmağının karşı yakasına silahlı olarak geçerseniz,"));
trsuperobject.biblemap.put("b4_32_22", new trsuperobject(4,32,22,"ülke ele geçirildiğinde dönebilir, RABbe ve İsraile karşı yükümlülüğünüzden özgür olursunuz. RABbin önünde bu topraklar sizin olacaktır."));
trsuperobject.biblemap.put("b4_32_23", new trsuperobject(4,32,23,"‹‹Ama söylediklerinizi yapmazsanız, RABbe karşı günah işlemiş olursunuz; günahınızın cezasını çekeceğinizi bilmelisiniz."));
trsuperobject.biblemap.put("b4_32_24", new trsuperobject(4,32,24,"Çocuklarınız için yeniden kentler kurun, davarlarınız için ağıllar yapın. Yeter ki, verdiğiniz sözü yerine getirin.››"));
trsuperobject.biblemap.put("b4_32_25", new trsuperobject(4,32,25,"Gadlılarla Rubenliler, ‹‹Efendimiz, bize buyurduğun gibi yapacağız›› diye yanıtladılar,"));
trsuperobject.biblemap.put("b4_32_26", new trsuperobject(4,32,26,"‹‹Çoluk çocuğumuz, sığırlarımızla öbür hayvanlarımız burada, Gilat kentlerinde kalacak."));
trsuperobject.biblemap.put("b4_32_27", new trsuperobject(4,32,27,"Ama buyurduğun gibi, silahlanmış olan herkes RABbin önünde savaşmak üzere karşı yakaya geçecek.››"));
trsuperobject.biblemap.put("b4_32_28", new trsuperobject(4,32,28,"Musa Gadlılarla Rubenliler hakkında Kâhin Elazara, Nun oğlu Yeşuya ve İsrail oymaklarının aile başlarına buyruk verdi."));
trsuperobject.biblemap.put("b4_32_29", new trsuperobject(4,32,29,"Şöyle dedi: ‹‹Gadlılarla Rubenlilerden silahlanmış olan herkes RABbin önünde sizinle birlikte Şeria Irmağının karşı yakasına geçerse, ülkeyi de ele geçirirseniz, Gilat bölgesini miras olarak onlara vereceksiniz."));
trsuperobject.biblemap.put("b4_32_30", new trsuperobject(4,32,30,"Ama silahlanmış olarak sizinle ırmağın karşı yakasına geçmezlerse, Kenan ülkesinde sizinle miras alacaklar.››"));
trsuperobject.biblemap.put("b4_32_31", new trsuperobject(4,32,31,"Gadlılarla Rubenliler, ‹‹RABbin bize buyurduğunu yapacağız›› dediler,"));
trsuperobject.biblemap.put("b4_32_32", new trsuperobject(4,32,32,"‹‹Silahlanmış olarak RABbin önünde Kenan ülkesine gideceğiz. Ama alacağımız mülk Şeria Irmağının doğu yakasında olacak.››"));
trsuperobject.biblemap.put("b4_32_33", new trsuperobject(4,32,33,"Böylece Musa Gadlılarla Rubenlilere ve Yusuf oğlu Manaşşe oymağının yarısına Amorluların Kralı Sihonun ülkesiyle Başan Kralı Ogun ülkesini ve çevrelerindeki topraklarla kentleri verdi."));
trsuperobject.biblemap.put("b4_32_34-36", new trsuperobject(4,32,34-36,"Gadlılar surlu Divon, Atarot, Aroer, Atrot-Şofan, Yazer, Yogboha, Beytnimra ve Beytharan kentlerini yeniden kurdular, davarları için ağıllar yaptılar."));
trsuperobject.biblemap.put("b4_32_37-38", new trsuperobject(4,32,37-38,"Rubenliler Heşbon, Elale, Kiryatayim, Nevo, Baal-Meon -bu son iki ad değiştirildi- ve Sivma kentlerini yeniden kurdular. Kurdukları kentlere yeni adlar verdiler."));
trsuperobject.biblemap.put("b4_32_39", new trsuperobject(4,32,39,"Manaşşe oğlu Makirin soyundan gelenler gidip Gilatı ele geçirerek, orada yaşayan Amorluları kovdular."));
trsuperobject.biblemap.put("b4_32_40", new trsuperobject(4,32,40,"Böylece Musa Gilatı Manaşşe oğlu Makirin soyundan gelenlere verdi; onlar da oraya yerleştiler."));
