trsuperobject.biblemap.put("b40_5_12", new trsuperobject(40,5,12,"Sevinin, sevinçle coşun! Çünkü göklerdeki ödülünüz büyüktür. Sizden önce yaşayan peygamberlere de böyle zulmettiler.››"));
trsuperobject.biblemap.put("b40_5_13", new trsuperobject(40,5,13,"‹‹Yeryüzünün tuzu sizsiniz. Ama tuz tadını yitirirse, bir daha ona nasıl tuz tadı verilebilir? Artık dışarı atılıp ayak altında çiğnenmekten başka işe yaramaz."));
trsuperobject.biblemap.put("b40_5_14", new trsuperobject(40,5,14,"‹‹Dünyanın ışığı sizsiniz. Tepeye kurulan kent gizlenemez."));
trsuperobject.biblemap.put("b40_5_15", new trsuperobject(40,5,15,"Kimse kandil yakıp tahıl ölçeğinin altına koymaz. Tersine, kandilliğe koyar; evdekilerin hepsine ışık sağlar."));
trsuperobject.biblemap.put("b40_5_16", new trsuperobject(40,5,16,"Sizin ışığınız insanların önünde öyle parlasın ki, iyi işlerinizi görerek göklerdeki Babanızı yüceltsinler!››"));
trsuperobject.biblemap.put("b40_5_17", new trsuperobject(40,5,17,"‹‹Kutsal Yasayı ya da peygamberlerin sözlerini geçersiz kılmak için geldiğimi sanmayın. Ben geçersiz kılmaya değil, tamamlamaya geldim."));
trsuperobject.biblemap.put("b40_5_18", new trsuperobject(40,5,18,"Size doğrusunu söyleyeyim, yer ve gök ortadan kalkmadan, her şey gerçekleşmeden, Kutsal Yasadan ufacık bir harf ya da bir nokta bile yok olmayacak."));
trsuperobject.biblemap.put("b40_5_19", new trsuperobject(40,5,19,"Bu nedenle, bu buyrukların en küçüğünden birini kim çiğner ve başkalarına öyle öğretirse, Göklerin Egemenliğinde en küçük sayılacak. Ama bu buyrukları kim yerine getirir ve başkalarına öğretirse, Göklerin Egemenliğinde büyük sayılacak."));
trsuperobject.biblemap.put("b40_5_20", new trsuperobject(40,5,20,"Size şunu söyleyeyim: Doğruluğunuz din bilginleriyle Ferisilerinkini aşmadıkça, Göklerin Egemenliğine asla giremezsiniz!››"));
trsuperobject.biblemap.put("b40_5_21", new trsuperobject(40,5,21,"‹‹Atalarımıza, ‹Adam öldürmeyeceksin. Öldüren yargılanacak› dendiğini duydunuz."));
trsuperobject.biblemap.put("b40_5_22", new trsuperobject(40,5,22,"Ama ben size diyorum ki, kardeşine öfkelenen herkes yargılanacaktır. Kim kardeşine aşağılayıcı bir söz söylerse, Yüksek Kurulda yargılanacaktır. Kim kardeşine ahmak derse, cehennem ateşini hak edecektir."));
trsuperobject.biblemap.put("b40_5_23-24", new trsuperobject(40,5,23-24,"Bu yüzden, sunakta adak sunarken kardeşinin sana karşı bir şikâyeti olduğunu anımsarsan, adağını orada, sunağın önünde bırak, git önce kardeşinle barış; sonra gelip adağını sun."));
trsuperobject.biblemap.put("b40_5_25", new trsuperobject(40,5,25,"Senden davacı olanla daha yoldayken çabucak anlaş. Yoksa o seni yargıca, yargıç da gardiyana teslim edebilir; sonunda da hapse atılabilirsin."));
trsuperobject.biblemap.put("b40_5_26", new trsuperobject(40,5,26,"Sana doğrusunu söyleyeyim, borcunun son kuruşunu ödemeden oradan asla çıkamazsın.››"));
trsuperobject.biblemap.put("b40_5_27", new trsuperobject(40,5,27,"‹‹Zina etmeyeceksin› dendiğini duydunuz."));
trsuperobject.biblemap.put("b40_5_28", new trsuperobject(40,5,28,"Ama ben size diyorum ki, bir kadına şehvetle bakan her adam, yüreğinde o kadınla zina etmiş olur."));
trsuperobject.biblemap.put("b40_5_29", new trsuperobject(40,5,29,"Eğer sağ gözün günah işlemene neden olursa, onu çıkar at. Çünkü vücudunun bir üyesinin yok olması, bütün vücudunun cehenneme atılmasından iyidir."));
trsuperobject.biblemap.put("b40_5_30", new trsuperobject(40,5,30,"Eğer sağ elin günah işlemene neden olursa, onu kes at. Çünkü vücudunun bir üyesinin yok olması, bütün vücudunun cehenneme gitmesinden iyidir."));
trsuperobject.biblemap.put("b40_5_31", new trsuperobject(40,5,31,"‹‹Kim karısını boşarsa ona boşanma belgesi versin› denmiştir."));
trsuperobject.biblemap.put("b40_5_32", new trsuperobject(40,5,32,"Ama ben size diyorum ki, karısını fuhuş dışında bir nedenle boşayan onu zinaya itmiş olur. Boşanmış bir kadınla evlenen de zina etmiş olur.››"));
trsuperobject.biblemap.put("b40_5_33", new trsuperobject(40,5,33,"‹‹Yine atalarımıza, ‹Yalan yere ant içmeyeceksin, ama Rabbin önünde içtiğin antları yerine getireceksin› dendiğini duydunuz."));
trsuperobject.biblemap.put("b40_5_34-35", new trsuperobject(40,5,34-35,"Oysa ben size diyorum ki, hiç ant içmeyin: Ne gök üzerine, çünkü orası Tanrının tahtıdır; ne yer üzerine, çünkü orası Onun ayak taburesidir; ne de Yeruşalim üzerine, çünkü orası Büyük Kralın kentidir."));
trsuperobject.biblemap.put("b40_5_36", new trsuperobject(40,5,36,"Başınızın üzerine de ant içmeyin. Çünkü saçınızın tek telini ak ya da kara edemezsiniz."));
trsuperobject.biblemap.put("b40_5_37", new trsuperobject(40,5,37,"‹Evet›iniz evet, ‹hayır›ınız hayır olsun. Bundan fazlası Şeytandandır.››"));
trsuperobject.biblemap.put("b40_5_38", new trsuperobject(40,5,38,"‹‹Göze göz, dişe diş› dendiğini duydunuz."));
trsuperobject.biblemap.put("b40_5_39", new trsuperobject(40,5,39,"Ama ben size diyorum ki, kötüye karşı direnmeyin. Sağ yanağınıza bir tokat atana öbür yanağınızı da çevirin."));
trsuperobject.biblemap.put("b40_5_40", new trsuperobject(40,5,40,"Size karşı davacı olup mintanınızı almak isteyene abanızı da verin."));
trsuperobject.biblemap.put("b40_5_41", new trsuperobject(40,5,41,"Sizi bin adım yol yürümeye zorlayanla iki bin adım yürüyün."));
trsuperobject.biblemap.put("b40_5_42", new trsuperobject(40,5,42,"Sizden bir şey dileyene verin, sizden ödünç isteyeni geri çevirmeyin.››"));
trsuperobject.biblemap.put("b40_5_43", new trsuperobject(40,5,43,"‹‹Komşunu seveceksin, düşmanından nefret edeceksin› dendiğini duydunuz."));
trsuperobject.biblemap.put("b40_5_44", new trsuperobject(40,5,44,"Ama ben size diyorum ki, düşmanlarınızı sevin, size zulmedenler için dua edin."));
trsuperobject.biblemap.put("b40_5_45", new trsuperobject(40,5,45,"Öyle ki, göklerdeki Babanızın oğulları olasınız. Çünkü O, güneşini hem kötülerin hem iyilerin üzerine doğdurur; yağmurunu hem doğruların hem eğrilerin üzerine yağdırır."));
trsuperobject.biblemap.put("b40_5_46", new trsuperobject(40,5,46,"Eğer yalnız sizi sevenleri severseniz, ne ödülünüz olur? Vergi görevlileri de öyle yapmıyor mu?"));
trsuperobject.biblemap.put("b40_5_47", new trsuperobject(40,5,47,"Yalnız kardeşlerinize selam verirseniz, fazladan ne yapmış olursunuz? Putperestler de öyle yapmıyor mu?"));
trsuperobject.biblemap.put("b40_5_48", new trsuperobject(40,5,48,"Bu nedenle, göksel Babanız yetkin olduğu gibi, siz de yetkin olun.››"));
trsuperobject.biblemap.put("b40_6_1", new trsuperobject(40,6,1,"‹‹Doğruluğunuzu insanların gözü önünde gösteriş amacıyla sergilemekten kaçının. Yoksa göklerdeki Babanızdan ödül alamazsınız."));
trsuperobject.biblemap.put("b40_6_2", new trsuperobject(40,6,2,"‹‹Bu nedenle, birisine sadaka verirken bunu borazan çaldırarak ilan etmeyin. İkiyüzlüler, insanların övgüsünü kazanmak için havralarda ve sokaklarda böyle yaparlar. Size doğrusunu söyleyeyim, onlar ödüllerini almışlardır."));
trsuperobject.biblemap.put("b40_6_3", new trsuperobject(40,6,3,"Siz sadaka verirken, sol eliniz sağ elinizin ne yaptığını bilmesin."));
trsuperobject.biblemap.put("b40_6_4", new trsuperobject(40,6,4,"Öyle ki, verdiğiniz sadaka gizli kalsın. Gizlice yapılanı gören Babanız sizi ödüllendirecektir.››"));
trsuperobject.biblemap.put("b40_6_5", new trsuperobject(40,6,5,"‹‹Dua ettiğiniz zaman ikiyüzlüler gibi olmayın. Onlar, herkes kendilerini görsün diye havralarda ve caddelerin köşe başlarında dikilip dua etmekten zevk alırlar. Size doğrusunu söyleyeyim, onlar ödüllerini almışlardır."));
trsuperobject.biblemap.put("b40_6_6", new trsuperobject(40,6,6,"Ama siz dua edeceğiniz zaman iç odanıza çekilip kapıyı örtün ve gizlide olan Babanıza dua edin. Gizlilik içinde yapılanı gören Babanız sizi ödüllendirecektir."));
trsuperobject.biblemap.put("b40_6_7", new trsuperobject(40,6,7,"Dua ettiğinizde, putperestler gibi boş sözler tekrarlayıp durmayın. Onlar söz kalabalığıyla seslerini duyurabileceklerini sanırlar."));
trsuperobject.biblemap.put("b40_6_8", new trsuperobject(40,6,8,"Siz onlara benzemeyin! Çünkü Babanız nelere gereksinmeniz olduğunu siz daha Ondan dilemeden önce bilir."));
trsuperobject.biblemap.put("b40_6_9", new trsuperobject(40,6,9,"‹‹Bunun için siz şöyle dua edin:  ‹Göklerdeki Babamız,  Adın kutsal kılınsın."));
trsuperobject.biblemap.put("b40_6_10", new trsuperobject(40,6,10,"Egemenliğin gelsin.  Gökte olduğu gibi, yeryüzünde de  Senin istediğin olsun."));
trsuperobject.biblemap.put("b40_6_11", new trsuperobject(40,6,11,"Bugün bize gündelik ekmeğimizi ver."));
trsuperobject.biblemap.put("b40_6_12", new trsuperobject(40,6,12,"Bize karşı suç işleyenleri bağışladığımız gibi,  Sen de bizim suçlarımızı bağışla."));
trsuperobject.biblemap.put("b40_6_13", new trsuperobject(40,6,13,"Ayartılmamıza izin verme.  Bizi kötü olandan kurtar.  Çünkü egemenlik, güç ve yücelik  Sonsuzlara dek senindir! Amin›."));
trsuperobject.biblemap.put("b40_6_14", new trsuperobject(40,6,14,"‹‹Başkalarının suçlarını bağışlarsanız, göksel Babanız da sizin suçlarınızı bağışlar."));
trsuperobject.biblemap.put("b40_6_15", new trsuperobject(40,6,15,"Ama siz başkalarının suçlarını bağışlamazsanız, Babanız da sizin suçlarınızı bağışlamaz.››"));
trsuperobject.biblemap.put("b40_6_16", new trsuperobject(40,6,16,"‹‹Oruç tuttuğunuz zaman, ikiyüzlüler gibi surat asmayın. Onlar oruç tuttuklarını insanlara belli etmek için kendilerine perişan bir görünüm verirler. Size doğrusunu söyleyeyim, onlar ödüllerini almışlardır."));
trsuperobject.biblemap.put("b40_6_17", new trsuperobject(40,6,17,"Siz oruç tuttuğunuz zaman, başınıza yağ sürüp yüzünüzü yıkayın."));
trsuperobject.biblemap.put("b40_6_18", new trsuperobject(40,6,18,"Öyle ki, insanlara değil, gizlide olan Babanıza oruçlu görünesiniz. Gizlilik içinde yapılanı gören Babanız sizi ödüllendirecektir.››"));
trsuperobject.biblemap.put("b40_6_19", new trsuperobject(40,6,19,"‹‹Yeryüzünde kendinize hazineler biriktirmeyin. Burada güve ve pas onları yiyip bitirir, hırsızlar da girip çalarlar."));
trsuperobject.biblemap.put("b40_6_20", new trsuperobject(40,6,20,"Bunun yerine kendinize gökte hazineler biriktirin. Orada ne güve ne pas onları yiyip bitirir, ne de hırsızlar girip çalar."));
trsuperobject.biblemap.put("b40_6_21", new trsuperobject(40,6,21,"Hazineniz neredeyse, yüreğiniz de orada olacaktır."));
trsuperobject.biblemap.put("b40_6_22", new trsuperobject(40,6,22,"‹‹Bedenin ışığı gözdür. Gözünüz sağlamsa, bütün bedeniniz aydınlık olur."));
trsuperobject.biblemap.put("b40_6_23", new trsuperobject(40,6,23,"Gözünüz bozuksa, bütün bedeniniz karanlık olur. Buna göre, içinizdeki ‹ışık› karanlıksa, ne korkunçtur o karanlık!"));
trsuperobject.biblemap.put("b40_6_24", new trsuperobject(40,6,24,"‹‹Hiç kimse iki efendiye kulluk edemez. Ya birinden nefret edip öbürünü sever, ya da birine bağlanıp öbürünü hor görür. Siz hem Tanrıya, hem de paraya kulluk edemezsiniz.››"));
trsuperobject.biblemap.put("b40_6_25", new trsuperobject(40,6,25,"‹‹Bu nedenle size şunu söylüyorum: ‹Ne yiyip ne içeceğiz?› diye canınız için, ‹Ne giyeceğiz?› diye bedeniniz için kaygılanmayın. Can yiyecekten, beden de giyecekten daha önemli değil mi?"));
trsuperobject.biblemap.put("b40_6_26", new trsuperobject(40,6,26,"Gökte uçan kuşlara bakın! Ne eker, ne biçer, ne de ambarlarda yiyecek biriktirirler. Göksel Babanız yine de onları doyurur. Siz onlardan çok daha değerli değil misiniz?"));
trsuperobject.biblemap.put("b40_6_27", new trsuperobject(40,6,27,"Hangi biriniz kaygılanmakla ömrünü bir anlık uzatabilir?"));
trsuperobject.biblemap.put("b40_6_28", new trsuperobject(40,6,28,"Giyecek konusunda neden kaygılanıyorsunuz? Kır zambaklarının nasıl büyüdüğüne bakın! Ne çalışırlar, ne de iplik eğirirler."));
trsuperobject.biblemap.put("b40_6_29", new trsuperobject(40,6,29,"Ama size şunu söyleyeyim, bütün görkemine karşın Süleyman bile bunlardan biri gibi giyinmiş değildi."));
trsuperobject.biblemap.put("b40_6_30", new trsuperobject(40,6,30,"Bugün var olup yarın ocağa atılacak olan kır otunu böyle giydiren Tanrının sizi de giydireceği çok daha kesin değil mi, ey kıt imanlılar?"));
trsuperobject.biblemap.put("b40_6_31", new trsuperobject(40,6,31,"‹‹Öyleyse, ‹Ne yiyeceğiz?› ‹Ne içeceğiz?› ya da ‹Ne giyeceğiz?› diyerek kaygılanmayın."));
trsuperobject.biblemap.put("b40_6_32", new trsuperobject(40,6,32,"Uluslar hep bu şeylerin peşinden giderler. Oysa göksel Babanız bütün bunlara gereksinmeniz olduğunu bilir."));
trsuperobject.biblemap.put("b40_6_33", new trsuperobject(40,6,33,"Siz öncelikle Onun egemenliğinin ve doğruluğunun ardından gidin, o zaman size bütün bunlar da verilecektir."));
trsuperobject.biblemap.put("b40_6_34", new trsuperobject(40,6,34,"O halde yarın için kaygılanmayın. Yarının kaygısı yarının olsun. Her günün derdi kendine yeter.››"));
trsuperobject.biblemap.put("b40_7_1", new trsuperobject(40,7,1,"‹‹Başkasını yargılamayın ki, siz de yargılanmayasınız."));
trsuperobject.biblemap.put("b40_7_2", new trsuperobject(40,7,2,"Çünkü nasıl yargılarsanız öyle yargılanacaksınız. Hangi ölçekle verirseniz, aynı ölçekle alacaksınız."));
trsuperobject.biblemap.put("b40_7_3", new trsuperobject(40,7,3,"Sen neden kardeşinin gözündeki çöpü görürsün de kendi gözündeki merteği farketmezsin?"));
trsuperobject.biblemap.put("b40_7_4", new trsuperobject(40,7,4,"Kendi gözünde mertek varken kardeşine nasıl, ‹İzin ver, gözündeki çöpü çıkarayım› dersin?"));
trsuperobject.biblemap.put("b40_7_5", new trsuperobject(40,7,5,"Seni ikiyüzlü! Önce kendi gözündeki merteği çıkar, o zaman kardeşinin gözündeki çöpü çıkarmak için daha iyi görürsün."));
trsuperobject.biblemap.put("b40_7_6", new trsuperobject(40,7,6,"‹‹Kutsal olanı köpeklere vermeyin. İncilerinizi domuzların önüne atmayın. Yoksa bunları ayaklarıyla çiğnedikten sonra dönüp sizi parçalayabilirler.››"));
trsuperobject.biblemap.put("b40_7_7", new trsuperobject(40,7,7,"‹‹Dileyin, size verilecek; arayın, bulacaksınız; kapıyı çalın, size açılacaktır."));
trsuperobject.biblemap.put("b40_7_8", new trsuperobject(40,7,8,"Çünkü her dileyen alır, arayan bulur, kapı çalana açılır."));
trsuperobject.biblemap.put("b40_7_9", new trsuperobject(40,7,9,"Hanginiz kendisinden ekmek isteyen oğluna taş verir?"));
trsuperobject.biblemap.put("b40_7_10", new trsuperobject(40,7,10,"Ya da balık isterse yılan verir?"));
trsuperobject.biblemap.put("b40_7_11", new trsuperobject(40,7,11,"Sizler kötü yürekli olduğunuz halde çocuklarınıza güzel armağanlar vermeyi biliyorsanız, göklerdeki Babanızın, kendisinden dileyenlere güzel armağanlar vereceği çok daha kesin değil mi?"));
trsuperobject.biblemap.put("b40_7_12", new trsuperobject(40,7,12,"‹‹İnsanların size nasıl davranmasını istiyorsanız, siz de onlara öyle davranın. Çünkü Kutsal Yasanın ve peygamberlerin söylediği budur.››"));
trsuperobject.biblemap.put("b40_7_13", new trsuperobject(40,7,13,"‹‹Dar kapıdan girin. Çünkü yıkıma götüren kapı geniş ve yol enlidir. Bu kapıdan girenler çoktur."));
trsuperobject.biblemap.put("b40_7_14", new trsuperobject(40,7,14,"Oysa yaşama götüren kapı dar, yol da çetindir. Bu yolu bulanlar azdır.››"));
trsuperobject.biblemap.put("b40_7_15", new trsuperobject(40,7,15,"‹‹Sahte peygamberlerden sakının! Onlar size kuzu postuna bürünerek yaklaşırlar, ama özde yırtıcı kurtlardır."));
trsuperobject.biblemap.put("b40_7_16", new trsuperobject(40,7,16,"Onları meyvelerinden tanıyacaksınız. Dikenli bitkilerden üzüm, devedikenlerinden incir toplanabilir mi?"));
trsuperobject.biblemap.put("b40_7_17", new trsuperobject(40,7,17,"Bunun gibi, her iyi ağaç iyi meyve verir, kötü ağaç ise kötü meyve verir."));
trsuperobject.biblemap.put("b40_7_18", new trsuperobject(40,7,18,"İyi ağaç kötü meyve, kötü ağaç da iyi meyve veremez."));
trsuperobject.biblemap.put("b40_7_19", new trsuperobject(40,7,19,"İyi meyve vermeyen her ağaç kesilip ateşe atılır."));
trsuperobject.biblemap.put("b40_7_20", new trsuperobject(40,7,20,"Böylece sahte peygamberleri meyvelerinden tanıyacaksınız."));
trsuperobject.biblemap.put("b40_7_21", new trsuperobject(40,7,21,"‹‹Bana, ‹Ya Rab, ya Rab!› diye seslenen herkes Göklerin Egemenliğine girmeyecek. Ancak göklerdeki Babamın isteğini yerine getiren girecektir."));
trsuperobject.biblemap.put("b40_7_22", new trsuperobject(40,7,22,"O gün birçokları bana diyecek ki, ‹Ya Rab, ya Rab! Biz senin adınla peygamberlik etmedik mi? Senin adınla cinler kovmadık mı? Senin adınla birçok mucize yapmadık mı?›"));
trsuperobject.biblemap.put("b40_7_23", new trsuperobject(40,7,23,"O zaman ben de onlara açıkça, ‹Sizi hiç tanımadım, uzak durun benden, ey kötülük yapanlar!› diyeceğim.››"));
trsuperobject.biblemap.put("b40_7_24", new trsuperobject(40,7,24,"‹‹İşte bu sözlerimi duyup uygulayan herkes, evini kaya üzerine kuran akıllı adama benzer."));
trsuperobject.biblemap.put("b40_7_25", new trsuperobject(40,7,25,"Yağmur yağar, seller basar, yeller eser, eve saldırır; ama ev yıkılmaz. Çünkü kaya üzerine kurulmuştur."));
trsuperobject.biblemap.put("b40_7_26", new trsuperobject(40,7,26,"Bu sözlerimi duyup da uygulamayan herkes, evini kum üzerine kuran budala adama benzer."));
trsuperobject.biblemap.put("b40_7_27", new trsuperobject(40,7,27,"Yağmur yağar, seller basar, yeller eser, evi sarsar. Ev yıkılır; yıkılışı da korkunç olur.››"));
trsuperobject.biblemap.put("b40_7_28", new trsuperobject(40,7,28,"İsa konuşmasını bitirince, halk Onun öğretişine şaşıp kaldı."));
trsuperobject.biblemap.put("b40_7_29", new trsuperobject(40,7,29,"Çünkü onlara kendi din bilginleri gibi değil, yetkili biri gibi öğretiyordu."));
trsuperobject.biblemap.put("b40_8_1", new trsuperobject(40,8,1,"İsa dağdan inince büyük bir kalabalık Onun ardından gitti."));
trsuperobject.biblemap.put("b40_8_2", new trsuperobject(40,8,2,"Bu sırada cüzamlı bir adam yaklaşıp, ‹‹Ya Rab, istersen beni temiz kılabilirsin›› diyerek Onun ayaklarına kapandı."));
trsuperobject.biblemap.put("b40_8_3", new trsuperobject(40,8,3,"İsa elini uzatıp adama dokundu, ‹‹İsterim, temiz ol!›› dedi. Adam anında cüzamdan temizlendi."));
trsuperobject.biblemap.put("b40_8_4", new trsuperobject(40,8,4,"Sonra İsa adama, ‹‹Sakın kimseye bir şey söyleme!›› dedi. ‹‹Git, kâhine görün ve cüzamdan temizlendiğini herkese kanıtlamak için Musanın buyurduğu sunuyu sun.››"));
trsuperobject.biblemap.put("b40_8_5-6", new trsuperobject(40,8,5-6,"İsa Kefarnahuma varınca bir yüzbaşı Ona gelip, ‹‹Ya Rab›› diye yalvardı, ‹‹Uşağım felç oldu, evde yatıyor; korkunç acı çekiyor.››"));
trsuperobject.biblemap.put("b40_8_7", new trsuperobject(40,8,7,"İsa, ‹‹Gelip onu iyileştireceğim›› dedi."));
trsuperobject.biblemap.put("b40_8_8", new trsuperobject(40,8,8,"Ama yüzbaşı, ‹‹Ya Rab, evime girmene layık değilim›› dedi, ‹‹Yeter ki bir söz söyle, uşağım iyileşir."));
trsuperobject.biblemap.put("b40_8_9", new trsuperobject(40,8,9,"Ben de buyruk altında bir adamım, benim de buyruğumda askerlerim var. Birine, ‹Git› derim, gider; ötekine, ‹Gel› derim, gelir; köleme, ‹Şunu yap› derim, yapar.››"));
trsuperobject.biblemap.put("b40_8_10", new trsuperobject(40,8,10,"İsa, duyduğu bu sözlere hayran kaldı. Ardından gelenlere, ‹‹Size doğrusunu söyleyeyim›› dedi, ‹‹Ben İsrailde böyle imanı olan birini görmedim."));
trsuperobject.biblemap.put("b40_8_11", new trsuperobject(40,8,11,"Size şunu söyleyeyim, doğudan ve batıdan birçok insan gelecek, Göklerin Egemenliğinde İbrahimle, İshakla ve Yakupla birlikte sofraya oturacaklar."));
trsuperobject.biblemap.put("b40_8_12", new trsuperobject(40,8,12,"Ama bu egemenliğin asıl mirasçıları dışarıdaki karanlığa atılacak. Orada ağlayış ve diş gıcırtısı olacak.››"));
trsuperobject.biblemap.put("b40_8_13", new trsuperobject(40,8,13,"Sonra İsa yüzbaşıya, ‹‹Git, inandığın gibi olsun›› dedi. Ve uşak o anda iyileşti."));
trsuperobject.biblemap.put("b40_8_14", new trsuperobject(40,8,14,"İsa Petrusun evine geldiğinde onun kaynanasının ateşler içinde yattığını gördü."));
trsuperobject.biblemap.put("b40_8_15", new trsuperobject(40,8,15,"Eline dokununca kadının ateşi düştü. Kadın kalkıp İsaya hizmet etmeye başladı."));
trsuperobject.biblemap.put("b40_8_16", new trsuperobject(40,8,16,"Akşam olunca birçok cinliyi kendisine getirdiler. İsa onlardaki kötü ruhları tek sözle kovdu, hastaların hepsini iyileştirdi."));
trsuperobject.biblemap.put("b40_8_17", new trsuperobject(40,8,17,"Bu, Peygamber Yeşaya aracılığıyla bildirilen şu söz yerine gelsin diye oldu:  ‹‹Zayıflıklarımızı O kaldırdı,  Hastalıklarımızı O üstlendi.››"));
trsuperobject.biblemap.put("b40_8_18", new trsuperobject(40,8,18,"İsa, çevresindeki kalabalığı görünce gölün karşı yakasına geçilmesini buyurdu."));
trsuperobject.biblemap.put("b40_8_19", new trsuperobject(40,8,19,"O sırada din bilginlerinden biri Ona yaklaşıp, ‹‹Öğretmenim›› dedi, ‹‹Nereye gidersen, senin ardından geleceğim.››"));
trsuperobject.biblemap.put("b40_8_20", new trsuperobject(40,8,20,"İsa ona, ‹‹Tilkilerin ini, kuşların yuvası var, ama İnsanoğlunun başını yaslayacak bir yeri yok›› dedi."));
trsuperobject.biblemap.put("b40_8_21", new trsuperobject(40,8,21,"Başka bir öğrencisi İsaya, ‹‹Ya Rab, izin ver, önce gidip babamı gömeyim›› dedi."));
trsuperobject.biblemap.put("b40_8_22", new trsuperobject(40,8,22,"İsa ona, ‹‹Ardımdan gel›› dedi. ‹‹Bırak ölüleri, kendi ölülerini kendileri gömsün.››"));
trsuperobject.biblemap.put("b40_8_23", new trsuperobject(40,8,23,"İsa tekneye binince, ardından öğrencileri de bindi."));
trsuperobject.biblemap.put("b40_8_24", new trsuperobject(40,8,24,"Gölde ansızın büyük bir fırtına koptu. Öyle ki, dalgalar teknenin üzerinden aşıyordu. İsa bu arada uyuyordu."));
trsuperobject.biblemap.put("b40_8_25", new trsuperobject(40,8,25,"Öğrenciler gidip Onu uyandırarak, ‹‹Ya Rab, kurtar bizi, yoksa öleceğiz!›› dediler."));
trsuperobject.biblemap.put("b40_8_26", new trsuperobject(40,8,26,"İsa, ‹‹Neden korkuyorsunuz, ey kıt imanlılar?›› dedi. Sonra kalkıp rüzgarı ve gölü azarladı. Ortalık sütliman oldu."));
trsuperobject.biblemap.put("b40_8_27", new trsuperobject(40,8,27,"Hepsi hayret içinde kaldı. ‹‹Bu nasıl bir adam ki, rüzgar da göl de Onun sözünü dinliyor?›› dediler."));
trsuperobject.biblemap.put("b40_8_28", new trsuperobject(40,8,28,"İsa gölün karşı yakasında Gadaralıların memleketine vardı. Orada Onu mezarlık mağaralardan çıkan iki cinli karşıladı. Bunlar öyle tehlikeliydi ki, kimse o yoldan geçemiyordu."));
trsuperobject.biblemap.put("b40_8_29", new trsuperobject(40,8,29,"İsaya, ‹‹Ey Tanrının Oğlu, bizden ne istiyorsun?›› diye bağırdılar. ‹‹Buraya, vaktinden önce bize işkence etmek için mi geldin?››"));
trsuperobject.biblemap.put("b40_8_30", new trsuperobject(40,8,30,"Onlardan uzakta otlayan büyük bir domuz sürüsü vardı."));
trsuperobject.biblemap.put("b40_8_31", new trsuperobject(40,8,31,"Cinler İsaya, ‹‹Bizi kovacaksan, şu domuz sürüsüne gönder›› diye yalvardılar."));
trsuperobject.biblemap.put("b40_8_32", new trsuperobject(40,8,32,"İsa onlara, ‹‹Gidin!›› dedi. Cinler de adamlardan çıkıp domuzların içine girdiler. O anda bütün sürü dik yamaçtan aşağı koşuşarak göle atlayıp boğuldu."));
trsuperobject.biblemap.put("b40_8_33", new trsuperobject(40,8,33,"Domuzları güdenler kaçıp kente gittiler. Cinli adamlarla ilgili haberler dahil, olup bitenlerin hepsini anlattılar."));
trsuperobject.biblemap.put("b40_8_34", new trsuperobject(40,8,34,"Bunun üzerine bütün kent halkı İsa'yı karşılamaya çıktı. O'nu görünce bölgelerinden ayrılması için yalvardılar."));
trsuperobject.biblemap.put("b40_9_1", new trsuperobject(40,9,1,"İsa tekneye binip karşı kıyıya geçti ve kendi kentine gitti."));
trsuperobject.biblemap.put("b40_9_2", new trsuperobject(40,9,2,"Kendisine, yatak üzerinde felçli bir adam getirdiler. İsa onların imanını görünce felçliye, ‹‹Cesur ol, oğlum, günahların bağışlandı›› dedi."));
trsuperobject.biblemap.put("b40_9_3", new trsuperobject(40,9,3,"Bunun üzerine bazı din bilginleri içlerinden, ‹‹Bu adam Tanrıya küfrediyor!›› dediler."));
trsuperobject.biblemap.put("b40_9_4", new trsuperobject(40,9,4,"Onların ne düşündüklerini bilen İsa dedi ki, ‹‹Yüreğinizde neden kötü düşüncelere yer veriyorsunuz?"));
trsuperobject.biblemap.put("b40_9_5", new trsuperobject(40,9,5,"Hangisi daha kolay? ‹Günahların bağışlandı› demek mi, yoksa ‹Kalk, yürü› demek mi?"));
trsuperobject.biblemap.put("b40_9_6", new trsuperobject(40,9,6,"Ne var ki, İnsanoğlunun yeryüzünde günahları bağışlama yetkisine sahip olduğunu bilesiniz diye...›› Sonra felçliye, ‹‹Kalk, yatağını topla, evine git!›› dedi."));
trsuperobject.biblemap.put("b40_9_7", new trsuperobject(40,9,7,"Adam da kalkıp evine gitti."));
trsuperobject.biblemap.put("b40_9_8", new trsuperobject(40,9,8,"Halk bunu görünce korkuya kapıldı. İnsana böyle bir yetki veren Tanrıyı yücelttiler."));
trsuperobject.biblemap.put("b40_9_9", new trsuperobject(40,9,9,"İsa oradan geçerken, vergi toplama yerinde oturan birini gördü. Matta adındaki bu adama, ‹‹Ardımdan gel›› dedi. Adam da kalkıp İsanın ardından gitti."));
trsuperobject.biblemap.put("b40_9_10", new trsuperobject(40,9,10,"Sonra İsa, Mattanın evinde sofrada otururken, birçok vergi görevlisiyle günahkâr gelip Onunla ve öğrencileriyle birlikte sofraya oturdu."));
trsuperobject.biblemap.put("b40_9_11", new trsuperobject(40,9,11,"Bunu gören Ferisiler, İsanın öğrencilerine, ‹‹Sizin öğretmeniniz neden vergi görevlileri ve günahkârlarla birlikte yemek yiyor?›› diye sordular."));
trsuperobject.biblemap.put("b40_9_12", new trsuperobject(40,9,12,"İsa bunu duyunca şöyle dedi: ‹‹Sağlamların değil, hastaların hekime ihtiyacı var."));
trsuperobject.biblemap.put("b40_9_13", new trsuperobject(40,9,13,"Gidin de, ‹Ben kurban değil, merhamet isterim› sözünün anlamını öğrenin. Çünkü ben doğru kişileri değil, günahkârları çağırmaya geldim.››"));
trsuperobject.biblemap.put("b40_9_14", new trsuperobject(40,9,14,"Bu arada Yahyanın öğrencileri gelip İsaya, ‹‹Neden biz ve Ferisiler oruç tutuyoruz da senin öğrencilerin tutmuyor?›› diye sordular."));
trsuperobject.biblemap.put("b40_9_15", new trsuperobject(40,9,15,"İsa şöyle karşılık verdi: ‹‹Güvey aralarındayken, davetliler yas tutar mı? Ama güveyin aralarından alınacağı günler gelecek, o zaman oruç tutacaklar."));
trsuperobject.biblemap.put("b40_9_16", new trsuperobject(40,9,16,"Hiç kimse eski giysiyi yeni kumaş parçasıyla yamamaz. Çünkü yeni kumaş çeker, giysiden kopar, yırtık daha beter olur."));
trsuperobject.biblemap.put("b40_9_17", new trsuperobject(40,9,17,"Hiç kimse yeni şarabı eski tulumlara doldurmaz. Yoksa tulumlar patlar; hem şarap dökülür, hem de tulumlar mahvolur. Yeni şarap yeni tulumlara konur, böylece her ikisi de korunmuş olur.››"));
trsuperobject.biblemap.put("b40_9_18", new trsuperobject(40,9,18,"İsa onlara bu sözleri söylerken bir havra yöneticisi gelip Onun önünde yere kapanarak, ‹‹Kızım az önce öldü. Ama sen gelip elini onun üzerine koyarsan, dirilecek›› dedi."));
trsuperobject.biblemap.put("b40_9_19", new trsuperobject(40,9,19,"İsa kalkıp öğrencileriyle birlikte adamın ardından gitti."));
trsuperobject.biblemap.put("b40_9_20", new trsuperobject(40,9,20,"Tam o sırada, on iki yıldır kanaması olan bir kadın İsanın arkasından yetişip giysisinin eteğine dokundu."));
trsuperobject.biblemap.put("b40_9_21", new trsuperobject(40,9,21,"İçinden, ‹‹Giysisine bir dokunsam kurtulurum›› diyordu."));
trsuperobject.biblemap.put("b40_9_22", new trsuperobject(40,9,22,"İsa arkasına dönüp onu görünce, ‹‹Cesur ol, kızım! İmanın seni kurtardı›› dedi. Ve kadın o anda iyileşti."));
trsuperobject.biblemap.put("b40_9_23-24", new trsuperobject(40,9,23-24,"İsa, yöneticinin evine varıp kaval çalanlarla gürültülü kalabalığı görünce, ‹‹Çekilin!›› dedi. ‹‹Kız ölmedi, uyuyor.›› Onlar ise kendisiyle alay ettiler."));
trsuperobject.biblemap.put("b40_9_25", new trsuperobject(40,9,25,"Kalabalık dışarı çıkarılınca İsa içeri girip kızın elini tuttu, kız ayağa kalktı."));
trsuperobject.biblemap.put("b40_9_26", new trsuperobject(40,9,26,"Bu haber bütün bölgeye yayıldı."));
trsuperobject.biblemap.put("b40_9_27", new trsuperobject(40,9,27,"İsa oradan ayrılırken iki kör, ‹‹Ey Davut Oğlu, halimize acı!›› diye feryat ederek Onun ardından gittiler."));
trsuperobject.biblemap.put("b40_9_28", new trsuperobject(40,9,28,"İsa eve girince körler yanına geldi. Onlara, ‹‹İstediğinizi yapabileceğime inanıyor musunuz?›› diye sordu. Körler, ‹‹İnanıyoruz, ya Rab!›› dediler."));
trsuperobject.biblemap.put("b40_9_29", new trsuperobject(40,9,29,"Bunun üzerine İsa körlerin gözlerine dokunarak, ‹‹İmanınıza göre olsun›› dedi."));
trsuperobject.biblemap.put("b40_9_30", new trsuperobject(40,9,30,"Ve adamların gözleri açıldı. İsa, ‹‹Sakın kimse bunu bilmesin›› diyerek onları sıkı sıkı uyardı."));
trsuperobject.biblemap.put("b40_9_31", new trsuperobject(40,9,31,"Onlar ise çıkıp İsayla ilgili haberi bütün bölgeye yaydılar."));
trsuperobject.biblemap.put("b40_9_32", new trsuperobject(40,9,32,"Adamlar çıkarken İsaya dilsiz bir cinli getirdiler."));
trsuperobject.biblemap.put("b40_9_33", new trsuperobject(40,9,33,"Cin kovulunca adamın dili çözüldü. Halk hayret içinde, ‹‹İsrailde böylesi hiç görülmemiştir›› diyordu."));
trsuperobject.biblemap.put("b40_9_34", new trsuperobject(40,9,34,"Ferisiler ise, ‹‹Cinleri, cinlerin önderinin gücüyle kovuyor›› diyorlardı."));
trsuperobject.biblemap.put("b40_9_35", new trsuperobject(40,9,35,"İsa bütün kent ve köyleri dolaşarak havralarda öğretiyor, göksel egemenliğin Müjdesini duyuruyor, her hastalığı, her illeti iyileştiriyordu."));
trsuperobject.biblemap.put("b40_9_36", new trsuperobject(40,9,36,"Kalabalıkları görünce onlara acıdı. Çünkü çobansız koyunlar gibi şaşkın ve perişandılar."));
trsuperobject.biblemap.put("b40_9_37", new trsuperobject(40,9,37,"O zaman İsa öğrencilerine, ‹‹Ürün bol, ama işçi az›› dedi,"));
trsuperobject.biblemap.put("b40_9_38", new trsuperobject(40,9,38,"‹‹Bu nedenle ürünün sahibi Rab'be yalvarın, ürününü kaldıracak işçiler göndersin.››"));
trsuperobject.biblemap.put("b40_10_1", new trsuperobject(40,10,1,"İsa on iki öğrencisini yanına çağırıp onlara kötü ruhlar üzerinde yetki verdi. Böylece kötü ruhları kovacak, her hastalığı, her illeti iyileştireceklerdi."));
trsuperobject.biblemap.put("b40_10_2-4", new trsuperobject(40,10,2-4,"Bu on iki elçinin adları şöyle: Birincisi Petrus adıyla bilinen Simun, onun kardeşi Andreas, Zebedinin oğulları Yakup ve Yuhanna, Filipus ve Bartalmay, Tomas ve vergi görevlisi Matta, Alfay oğlu Yakup ve Taday, Yurtsever Simun ve İsaya ihanet eden Yahuda İskariot."));
trsuperobject.biblemap.put("b40_10_5", new trsuperobject(40,10,5,"İsa Onikileri şu buyrukla halkın arasına gönderdi: ‹‹Öteki ulusların arasına girmeyin. Samiriyelilerin kentlerine de uğramayın."));
trsuperobject.biblemap.put("b40_10_6", new trsuperobject(40,10,6,"Bunun yerine, İsrail halkının yitik koyunlarına gidin."));
trsuperobject.biblemap.put("b40_10_7", new trsuperobject(40,10,7,"Gittiğiniz her yerde Göklerin Egemenliğinin yaklaştığını duyurun."));
trsuperobject.biblemap.put("b40_10_8", new trsuperobject(40,10,8,"Hastaları iyileştirin, ölüleri diriltin, cüzamlıları temiz kılın, cinleri kovun. Karşılıksız aldınız, karşılıksız verin."));
trsuperobject.biblemap.put("b40_10_9", new trsuperobject(40,10,9,"Kuşağınıza altın, gümüş, ya da bakır para koymayın."));
trsuperobject.biblemap.put("b40_10_10", new trsuperobject(40,10,10,"Yolculuk için ne torba, ne yedek mintan, ne çarık, ne de değnek alın. Çünkü işçi yiyeceğini hak eder."));
trsuperobject.biblemap.put("b40_10_11", new trsuperobject(40,10,11,"Hangi kent ya da köye girerseniz, orada saygıdeğer birini arayın ve ayrılıncaya dek onunla kalın."));
trsuperobject.biblemap.put("b40_10_12", new trsuperobject(40,10,12,"Onun evine girerken, evdekilere esenlik dileyin."));
trsuperobject.biblemap.put("b40_10_13", new trsuperobject(40,10,13,"Eğer evdekiler buna layıksa, dilediğiniz esenlik üzerlerinde kalsın; layık değillerse, size geri dönsün."));
trsuperobject.biblemap.put("b40_10_14", new trsuperobject(40,10,14,"Sizi kabul etmez, sözlerinizi dinlemezlerse o evden ya da kentten ayrılırken, ayaklarınızın tozunu silkin."));
trsuperobject.biblemap.put("b40_10_15", new trsuperobject(40,10,15,"Size doğrusunu söyleyeyim, yargı günü o kentin hali Sodomla Gomora bölgesinin halinden beter olacaktır.››"));
trsuperobject.biblemap.put("b40_10_16", new trsuperobject(40,10,16,"‹‹İşte, sizi koyunlar gibi kurtların arasına gönderiyorum. Yılan gibi zeki, güvercin gibi saf olun."));
trsuperobject.biblemap.put("b40_10_17", new trsuperobject(40,10,17,"İnsanlardan sakının. Çünkü sizi mahkemelere verecek, havralarında kamçılayacaklar."));
trsuperobject.biblemap.put("b40_10_18", new trsuperobject(40,10,18,"Benden ötürü valilerin, kralların önüne çıkarılacak, böylece onlara ve uluslara tanıklık edeceksiniz."));
trsuperobject.biblemap.put("b40_10_19", new trsuperobject(40,10,19,"Sizleri mahkemeye verdiklerinde, neyi nasıl söyleyeceğinizi düşünerek kaygılanmayın. Ne söyleyeceğiniz o anda size bildirilecek."));
trsuperobject.biblemap.put("b40_10_20", new trsuperobject(40,10,20,"Çünkü konuşan siz değil, aracılığınızla konuşan Babanızın Ruhu olacak."));
trsuperobject.biblemap.put("b40_10_21", new trsuperobject(40,10,21,"‹‹Kardeş kardeşi, baba çocuğunu ölüme teslim edecek. Çocuklar anne babaya başkaldırıp onları öldürtecek."));
trsuperobject.biblemap.put("b40_10_22", new trsuperobject(40,10,22,"Benim adımdan ötürü herkes sizden nefret edecek. Ama sonuna kadar dayanan kurtulacaktır."));
trsuperobject.biblemap.put("b40_10_23", new trsuperobject(40,10,23,"Bir kentte size zulmettikleri zaman ötekine kaçın. Size doğrusunu söyleyeyim, İnsanoğlu gelinceye dek İsrailin bütün kentlerini dolaşmış olmayacaksınız."));
trsuperobject.biblemap.put("b40_10_24", new trsuperobject(40,10,24,"‹‹Öğrenci öğretmeninden, köle efendisinden üstün değildir."));
trsuperobject.biblemap.put("b40_10_25", new trsuperobject(40,10,25,"Öğrencinin öğretmeni gibi, kölenin de efendisi gibi olması yeterlidir. İnsanlar evin efendisine Baalzevul derlerse, ev halkına neler demezler!››"));
trsuperobject.biblemap.put("b40_10_26", new trsuperobject(40,10,26,"‹‹Bu yüzden onlardan korkmayın. Çünkü örtülü olup da açığa çıkarılmayacak, gizli olup da bilinmeyecek hiçbir şey yoktur."));
trsuperobject.biblemap.put("b40_10_27", new trsuperobject(40,10,27,"Size karanlıkta söylediklerimi, siz gün ışığında söyleyin. Kulağınıza fısıldananı, damlardan duyurun."));
trsuperobject.biblemap.put("b40_10_28", new trsuperobject(40,10,28,"Bedeni öldüren, ama canı öldüremeyenlerden korkmayın. Canı da bedeni de cehennemde mahvedebilen Tanrıdan korkun."));
trsuperobject.biblemap.put("b40_10_29", new trsuperobject(40,10,29,"İki serçe bir meteliğe satılmıyor mu? Ama Babanızın izni olmadan bunlardan bir teki bile yere düşmez."));
trsuperobject.biblemap.put("b40_10_30", new trsuperobject(40,10,30,"Size gelince, başınızdaki bütün saçlar bile sayılıdır."));
trsuperobject.biblemap.put("b40_10_31", new trsuperobject(40,10,31,"Onun için korkmayın, siz birçok serçeden daha değerlisiniz."));
trsuperobject.biblemap.put("b40_10_32", new trsuperobject(40,10,32,"‹‹İnsanların önünde beni açıkça kabul eden herkesi, ben de göklerdeki Babamın önünde açıkça kabul edeceğim."));
trsuperobject.biblemap.put("b40_10_33", new trsuperobject(40,10,33,"İnsanların önünde beni inkâr edeni, ben de göklerdeki Babamın önünde inkâr edeceğim.››"));
trsuperobject.biblemap.put("b40_10_34", new trsuperobject(40,10,34,"‹‹Yeryüzüne barış getirmeye geldiğimi sanmayın! Barış değil, kılıç getirmeye geldim."));
trsuperobject.biblemap.put("b40_10_35", new trsuperobject(40,10,35,"Çünkü ben babayla oğulun, anneyle kızın, gelinle kaynananın arasına ayrılık sokmaya geldim."));
trsuperobject.biblemap.put("b40_10_36", new trsuperobject(40,10,36,"‹İnsanın düşmanı kendi ev halkı olacak.›"));
trsuperobject.biblemap.put("b40_10_37", new trsuperobject(40,10,37,"Annesini ya da babasını beni sevdiğinden çok seven bana layık değildir. Oğlunu ya da kızını beni sevdiğinden çok seven bana layık değildir."));
trsuperobject.biblemap.put("b40_10_38", new trsuperobject(40,10,38,"Çarmıhını yüklenip ardımdan gelmeyen bana layık değildir."));
trsuperobject.biblemap.put("b40_10_39", new trsuperobject(40,10,39,"Canını kurtaran onu yitirecek. Canını benim uğruma yitiren ise onu kurtaracaktır."));
trsuperobject.biblemap.put("b40_10_40", new trsuperobject(40,10,40,"‹‹Sizi kabul eden beni kabul etmiş olur. Beni kabul eden de beni göndereni kabul etmiş olur."));
trsuperobject.biblemap.put("b40_10_41", new trsuperobject(40,10,41,"Bir peygamberi peygamber olduğu için kabul eden, peygambere yaraşan bir ödül alacaktır. Doğru birini doğru olduğu için kabul eden, doğru kişiye yaraşan bir ödül alacaktır."));
trsuperobject.biblemap.put("b40_10_42", new trsuperobject(40,10,42,"Bu sıradan kişilerden birine, öğrencim olduğu için bir bardak soğuk su bile veren, size doğrusunu söyleyeyim, ödülsüz kalmayacaktır.››"));
trsuperobject.biblemap.put("b40_11_1", new trsuperobject(40,11,1,"İsa, on iki öğrencisine bu buyrukları verdikten sonra onların kentlerinde öğretmek ve Tanrı sözünü duyurmak üzere oradan ayrıldı."));
trsuperobject.biblemap.put("b40_11_2-3", new trsuperobject(40,11,2-3,"Tutukevinde bulunan Yahya, Mesihin yaptığı işleri duyunca, Ona gönderdiği öğrencileri aracılığıyla şunu sordu: ‹‹Gelecek Olan sen misin, yoksa başkasını mı bekleyelim?››"));
trsuperobject.biblemap.put("b40_11_4", new trsuperobject(40,11,4,"İsa onlara şöyle karşılık verdi: ‹‹Gidin, işitip gördüklerinizi Yahyaya bildirin."));
trsuperobject.biblemap.put("b40_11_5", new trsuperobject(40,11,5,"Körlerin gözleri açılıyor, kötürümler yürüyor, cüzamlılar temiz kılınıyor, sağırlar işitiyor, ölüler diriliyor ve Müjde yoksullara duyuruluyor."));
trsuperobject.biblemap.put("b40_11_6", new trsuperobject(40,11,6,"Benden ötürü sendeleyip düşmeyene ne mutlu!››"));
trsuperobject.biblemap.put("b40_11_7", new trsuperobject(40,11,7,"Yahyanın öğrencileri ayrılırken İsa halka Yahyadan söz etmeye başladı. ‹‹Çöle ne görmeye gittiniz?›› dedi. ‹‹Rüzgarda sallanan bir kamış mı?"));
trsuperobject.biblemap.put("b40_11_8", new trsuperobject(40,11,8,"Söyleyin, ne görmeye gittiniz? Pahalı giysiler giymiş bir adam mı? Oysa pahalı giysi giyenler, kral saraylarında bulunur."));
trsuperobject.biblemap.put("b40_11_9", new trsuperobject(40,11,9,"Öyleyse ne görmeye gittiniz? Bir peygamber mi? Evet! Size şunu söyleyeyim, gördüğünüz kişi peygamberden de üstündür."));
trsuperobject.biblemap.put("b40_11_10", new trsuperobject(40,11,10,"‹İşte, habercimi senin önünden gönderiyorum;  O önden gidip senin yolunu hazırlayacak›"));
trsuperobject.biblemap.put("b40_11_11", new trsuperobject(40,11,11,"Size doğrusunu söyleyeyim, kadından doğanlar arasında Vaftizci Yahyadan daha üstün biri çıkmamıştır. Bununla birlikte, Göklerin Egemenliğinde en küçük olan ondan üstündür."));
trsuperobject.biblemap.put("b40_11_12", new trsuperobject(40,11,12,"Vaftizci Yahyanın ortaya çıktığı günden bu yana Göklerin Egemenliği zorlanıyor, zorlu kişiler onu ele geçirmeye çalışıyor."));
trsuperobject.biblemap.put("b40_11_13", new trsuperobject(40,11,13,"Yahyaya dek bütün peygamberlerle Kutsal Yasa, olacakları önceden bildirdiler."));
trsuperobject.biblemap.put("b40_11_14", new trsuperobject(40,11,14,"Eğer bunu kabul etmek isterseniz, gelecek olan İlyas odur."));
trsuperobject.biblemap.put("b40_11_15", new trsuperobject(40,11,15,"Kulağı olan, işitsin!"));
trsuperobject.biblemap.put("b40_11_16-17", new trsuperobject(40,11,16-17,"‹‹Bu kuşağın insanlarını neye benzeteyim? Çarşı meydanlarında oturup arkadaşlarına,  ‹Size kaval çaldık, oynamadınız;  Ağıt yaktık, dövünmediniz›"));
trsuperobject.biblemap.put("b40_11_18", new trsuperobject(40,11,18,"Yahya geldiği zaman oruç tutup içkiden kaçındı, ona ‹cinli› diyorlar."));
trsuperobject.biblemap.put("b40_11_19", new trsuperobject(40,11,19,"İnsanoğlu geldiği zaman yiyip içti. Bu kez de diyorlar ki, ‹Şu obur ve ayyaş adama bakın! Vergi görevlileri ve günahkârlarla dost oldu!› Ne var ki bilgelik, ortaya koyduğu işlerle doğrulanır.››"));
trsuperobject.biblemap.put("b40_11_2-021", new trsuperobject(40,11,2-021,"Sonra İsa, mucizelerinin çoğunu yapmış olduğu kentleri, tövbe etmedikleri için şöyle azarlamaya başladı: ‹‹Vay haline, ey Horazin! Vay haline, ey Beytsayda! Sizlerde yapılan mucizeler Sur ve Saydada yapılmış olsaydı, çoktan çul kuşanıp kül içinde oturarak tövbe etmiş olurlardı."));
trsuperobject.biblemap.put("b40_11_22", new trsuperobject(40,11,22,"Size şunu söyleyeyim, yargı günü sizin haliniz Sur ve Saydanın halinden beter olacaktır!"));
trsuperobject.biblemap.put("b40_11_23", new trsuperobject(40,11,23,"Ya sen, ey Kefarnahum, göğe mi çıkarılacaksın? Hayır, ölüler diyarına indirileceksin! Çünkü sende yapılan mucizeler Sodomda yapılmış olsaydı, bugüne dek ayakta kalırdı."));
trsuperobject.biblemap.put("b40_11_24", new trsuperobject(40,11,24,"Sana şunu söyleyeyim, yargı günü senin halin Sodom bölgesinin halinden beter olacaktır!››"));
trsuperobject.biblemap.put("b40_11_25", new trsuperobject(40,11,25,"İsa bundan sonra şöyle dedi: ‹‹Baba, yerin ve göğün Rabbi! Bu gerçekleri bilge ve akıllı kişilerden gizleyip küçük çocuklara açtığın için sana şükrederim."));
trsuperobject.biblemap.put("b40_11_26", new trsuperobject(40,11,26,"Evet Baba, senin isteğin buydu."));
trsuperobject.biblemap.put("b40_11_27", new trsuperobject(40,11,27,"‹‹Babam her şeyi bana teslim etti. Oğulu, Babadan başka kimse tanımaz. Babayı da Oğuldan ve Oğulun Onu tanıtmak istediği kişilerden başkası tanımaz."));
trsuperobject.biblemap.put("b40_11_28", new trsuperobject(40,11,28,"‹‹Ey bütün yorgunlar ve yükü ağır olanlar! Bana gelin, ben size rahat veririm."));
trsuperobject.biblemap.put("b40_11_29", new trsuperobject(40,11,29,"Boyunduruğumu yüklenin, benden öğrenin. Çünkü ben yumuşak huylu, alçakgönüllüyüm. Böylece canlarınız rahata kavuşur."));
trsuperobject.biblemap.put("b40_11_30", new trsuperobject(40,11,30,"Boyunduruğumu taşımak kolay, yüküm hafiftir.››"));
trsuperobject.biblemap.put("b40_12_1", new trsuperobject(40,12,1,"O sıralarda, bir Şabat Günü İsa ekinler arasından geçiyordu. Öğrencileri acıkınca başakları koparıp yemeye başladılar."));
trsuperobject.biblemap.put("b40_12_2", new trsuperobject(40,12,2,"Bunu gören Ferisiler İsaya, ‹‹Bak, öğrencilerin Şabat Günü yasak olanı yapıyor›› dediler."));
trsuperobject.biblemap.put("b40_12_3", new trsuperobject(40,12,3,"İsa onlara, ‹‹Davutla yanındakiler acıkınca Davutun ne yaptığını okumadınız mı?›› diye sordu."));
trsuperobject.biblemap.put("b40_12_4", new trsuperobject(40,12,4,"‹‹Tanrının evine girdi, kendisinin ve yanındakilerin yemesi yasak olan, ancak kâhinlerin yiyebileceği adak ekmeklerini yedi."));
trsuperobject.biblemap.put("b40_12_5", new trsuperobject(40,12,5,"Ayrıca kâhinlerin her hafta tapınakta Şabat Günüyle ilgili buyruğu çiğnedikleri halde suçlu sayılmadıklarını Kutsal Yasada okumadınız mı?"));
trsuperobject.biblemap.put("b40_12_6", new trsuperobject(40,12,6,"Size şunu söyleyeyim, burada tapınaktan daha üstün bir şey var."));
trsuperobject.biblemap.put("b40_12_7", new trsuperobject(40,12,7,"Eğer siz, ‹Ben kurban değil, merhamet isterim› sözünün anlamını bilseydiniz, suçsuzları yargılamazdınız."));
trsuperobject.biblemap.put("b40_12_8", new trsuperobject(40,12,8,"Çünkü İnsanoğlu Şabat Gününün de Rabbidir.››"));
trsuperobject.biblemap.put("b40_12_9", new trsuperobject(40,12,9,"İsa oradan ayrılıp onların havrasına gitti."));
trsuperobject.biblemap.put("b40_12_10", new trsuperobject(40,12,10,"Orada eli sakat bir adam vardı. İsayı suçlamak amacıyla kendisine, ‹‹Şabat Günü bir hastayı iyileştirmek Kutsal Yasaya uygun mudur?›› diye sordular."));
trsuperobject.biblemap.put("b40_12_11", new trsuperobject(40,12,11,"İsa onlara şu karşılığı verdi: ‹‹Hanginizin bir koyunu olur da Şabat Günü çukura düşerse onu tutup çıkarmaz?"));
trsuperobject.biblemap.put("b40_12_12", new trsuperobject(40,12,12,"İnsan koyundan çok daha değerlidir! Demek ki, Şabat Günü iyilik yapmak Yasaya uygundur.››"));
trsuperobject.biblemap.put("b40_12_13", new trsuperobject(40,12,13,"Sonra adama, ‹‹Elini uzat›› dedi. Adam elini uzattı. Eli öteki gibi yine sapasağlam oluverdi."));
trsuperobject.biblemap.put("b40_12_14", new trsuperobject(40,12,14,"Bunun üzerine Ferisiler dışarı çıktılar, İsayı yok etmek için anlaştılar."));
trsuperobject.biblemap.put("b40_12_15", new trsuperobject(40,12,15,"İsa bunu bildiği için oradan ayrıldı. Birçok kişi ardından gitti. İsa hepsini iyileştirdi."));
trsuperobject.biblemap.put("b40_12_16", new trsuperobject(40,12,16,"Kim olduğunu açıklamamaları için onları uyardı."));
trsuperobject.biblemap.put("b40_12_17-18", new trsuperobject(40,12,17-18,"Bu, Peygamber Yeşaya aracılığıyla bildirilen şu söz yerine gelsin diye oldu:  ‹‹İşte Kulum, Onu ben seçtim.  Gönlümün hoşnut olduğu sevgili Kulum Odur.  Ruhumu Onun üzerine koyacağım,  O da adaleti uluslara bildirecek."));
trsuperobject.biblemap.put("b40_12_19", new trsuperobject(40,12,19,"Çekişip bağırmayacak,  Sokaklarda kimse Onun sesini duymayacak."));
trsuperobject.biblemap.put("b40_12_20", new trsuperobject(40,12,20,"Ezilmiş kamışı kırmayacak,  Tüten fitili söndürmeyecek,  Ve sonunda adaleti zafere ulaştıracak."));
trsuperobject.biblemap.put("b40_12_21", new trsuperobject(40,12,21,"Uluslar da Onun adına umut bağlayacak.››"));
trsuperobject.biblemap.put("b40_12_22", new trsuperobject(40,12,22,"Daha sonra İsaya kör ve dilsiz bir cinli getirdiler. İsa adamı iyileştirdi. Adam konuşmaya, görmeye başladı."));
trsuperobject.biblemap.put("b40_12_23", new trsuperobject(40,12,23,"Bütün kalabalık şaşırıp kaldı. ‹‹Bu, Davutun Oğlu olabilir mi?›› diye soruyorlardı."));
trsuperobject.biblemap.put("b40_12_24", new trsuperobject(40,12,24,"Ferisiler bunu duyunca, ‹‹Bu adam cinleri, ancak cinlerin önderi Baalzevulun gücüyle kovuyor›› dediler."));
trsuperobject.biblemap.put("b40_12_25", new trsuperobject(40,12,25,"Onların ne düşündüğünü bilen İsa şöyle dedi: ‹‹Kendi içinde bölünen ülke yıkılır. Kendi içinde bölünen kent ya da ev ayakta kalamaz."));
trsuperobject.biblemap.put("b40_12_26", new trsuperobject(40,12,26,"Eğer Şeytan Şeytanı kovarsa, kendi içinde bölünmüş demektir. Bu durumda onun egemenliği nasıl ayakta kalabilir?"));
trsuperobject.biblemap.put("b40_12_27", new trsuperobject(40,12,27,"Eğer ben cinleri Baalzevulun gücüyle kovuyorsam, sizin adamlarınız kimin gücüyle kovuyor? Bu durumda sizi kendi adamlarınız yargılayacak."));
trsuperobject.biblemap.put("b40_12_28", new trsuperobject(40,12,28,"Ama ben cinleri Tanrının Ruhuyla kovuyorsam, Tanrının Egemenliği üzerinize gelmiş demektir."));
trsuperobject.biblemap.put("b40_12_29", new trsuperobject(40,12,29,"‹‹Bir kimse güçlü adamın evine girip malını nasıl çalabilir? Ancak onu bağladıktan sonra evini soyabilir."));
trsuperobject.biblemap.put("b40_12_30", new trsuperobject(40,12,30,"‹‹Benden yana olmayan bana karşıdır. Benimle birlikte toplamayan dağıtıyor demektir."));
trsuperobject.biblemap.put("b40_12_31", new trsuperobject(40,12,31,"Bunun için size diyorum ki, insanların işlediği her günah, ettiği her küfür bağışlanacak; ama Ruha edilen küfür bağışlanmayacaktır."));
trsuperobject.biblemap.put("b40_12_32", new trsuperobject(40,12,32,"İnsanoğluna karşı bir söz söyleyen, bağışlanacak; ama Kutsal Ruha karşı bir söz söyleyen, ne bu çağda, ne de gelecek çağda bağışlanacaktır."));
trsuperobject.biblemap.put("b40_12_33", new trsuperobject(40,12,33,"‹‹Ya ağacı iyi, meyvesini de iyi sayın; ya da ağacı kötü, meyvesini de kötü sayın. Çünkü her ağaç meyvesinden tanınır."));
trsuperobject.biblemap.put("b40_12_34", new trsuperobject(40,12,34,"Sizi engerekler soyu! Kötü olan sizler nasıl iyi sözler söyleyebilirsiniz? Çünkü ağız yürekten taşanı söyler."));
trsuperobject.biblemap.put("b40_12_35", new trsuperobject(40,12,35,"İyi insan içindeki iyilik hazinesinden iyilik, kötü insan içindeki kötülük hazinesinden kötülük çıkarır."));
trsuperobject.biblemap.put("b40_12_36", new trsuperobject(40,12,36,"Size şunu söyleyeyim, insanlar söyledikleri her boş söz için yargı günü hesap verecekler."));
trsuperobject.biblemap.put("b40_12_37", new trsuperobject(40,12,37,"Kendi sözlerinizle aklanacak, yine kendi sözlerinizle suçlu çıkarılacaksınız.››"));
trsuperobject.biblemap.put("b40_12_38", new trsuperobject(40,12,38,"Bunun üzerine bazı din bilginleri ve Ferisiler, ‹‹Öğretmenimiz, senden doğaüstü bir belirti görmek istiyoruz›› dediler."));
trsuperobject.biblemap.put("b40_12_39", new trsuperobject(40,12,39,"İsa onlara şu karşılığı verdi: ‹‹Kötü ve vefasız kuşak bir belirti istiyor! Ama ona Peygamber Yunusun belirtisinden başka bir belirti gösterilmeyecektir."));
trsuperobject.biblemap.put("b40_12_40", new trsuperobject(40,12,40,"Yunus, nasıl üç gün üç gece o koca balığın karnında kaldıysa, İnsanoğlu da üç gün üç gece yerin bağrında kalacaktır."));
trsuperobject.biblemap.put("b40_12_41", new trsuperobject(40,12,41,"Ninova halkı, yargı günü bu kuşakla birlikte kalkıp bu kuşağı yargılayacak. Çünkü Ninovalılar, Yunusun çağrısı üzerine tövbe ettiler. Bakın, Yunustan daha üstün olan buradadır."));
trsuperobject.biblemap.put("b40_12_42", new trsuperobject(40,12,42,"Güney Kraliçesi, yargı günü bu kuşakla birlikte kalkıp bu kuşağı yargılayacak. Çünkü kraliçe, Süleymanın bilgece sözlerini dinlemek için dünyanın ta öbür ucundan gelmişti. Bakın, Süleymandan daha üstün olan buradadır."));
trsuperobject.biblemap.put("b40_12_43", new trsuperobject(40,12,43,"‹‹Kötü ruh insandan çıkınca kurak yerlerde dolanıp huzur arar, ama bulamaz."));
trsuperobject.biblemap.put("b40_12_44", new trsuperobject(40,12,44,"O zaman, ‹Çıktığım eve, kendi evime döneyim› der. Eve gelince orayı bomboş, süpürülmüş, düzeltilmiş bulur."));
trsuperobject.biblemap.put("b40_12_45", new trsuperobject(40,12,45,"Bunun üzerine gider, yanına kendisinden kötü yedi ruh daha alır ve eve girip yerleşirler. Böylece o kişinin son durumu ilkinden beter olur. Bu kötü kuşağın başına gelecek olan da budur.››"));
trsuperobject.biblemap.put("b40_12_46", new trsuperobject(40,12,46,"İsa daha halka konuşurken, annesiyle kardeşleri geldi. Dışarıda durmuş, Onunla konuşmak istiyorlardı."));
trsuperobject.biblemap.put("b40_12_47", new trsuperobject(40,12,47,"Birisi İsaya, ‹‹Bak, annenle kardeşlerin dışarıda duruyor, seninle görüşmek istiyorlar›› dedi."));
trsuperobject.biblemap.put("b40_12_48", new trsuperobject(40,12,48,"İsa, kendisiyle konuşana, ‹‹Kimdir annem, kimdir kardeşlerim?›› karşılığını verdi."));
trsuperobject.biblemap.put("b40_12_49", new trsuperobject(40,12,49,"Eliyle öğrencilerini göstererek, ‹‹İşte annem, işte kardeşlerim!›› dedi."));
trsuperobject.biblemap.put("b40_12_50", new trsuperobject(40,12,50,"‹‹Göklerdeki Babam'ın isteğini kim yerine getirirse, kardeşim, kızkardeşim ve annem odur.››"));
trsuperobject.biblemap.put("b40_13_1", new trsuperobject(40,13,1,"Aynı gün İsa evden çıktı, gidip göl kıyısında oturdu."));
trsuperobject.biblemap.put("b40_13_2", new trsuperobject(40,13,2,"Çevresinde büyük bir kalabalık toplandı. Bu yüzden İsa tekneye binip oturdu. Bütün kalabalık kıyıda duruyordu."));
trsuperobject.biblemap.put("b40_13_3", new trsuperobject(40,13,3,"İsa onlara benzetmelerle birçok şey anlattı. ‹‹Bakın›› dedi, ‹‹Ekincinin biri tohum ekmeye çıktı."));
trsuperobject.biblemap.put("b40_13_4", new trsuperobject(40,13,4,"Ektiği tohumlardan kimi yol kenarına düştü. Kuşlar gelip bunları yedi."));
trsuperobject.biblemap.put("b40_13_5", new trsuperobject(40,13,5,"Kimi, toprağı az, kayalık yerlere düştü; toprak derin olmadığından hemen filizlendi."));
trsuperobject.biblemap.put("b40_13_6", new trsuperobject(40,13,6,"Ne var ki, güneş doğunca kavruldular, kök salamadıkları için kuruyup gittiler."));
trsuperobject.biblemap.put("b40_13_7", new trsuperobject(40,13,7,"Kimi, dikenler arasına düştü. Dikenler büyüdü, filizleri boğdu."));
trsuperobject.biblemap.put("b40_13_8", new trsuperobject(40,13,8,"Kimi ise iyi toprağa düştü. Bazısı yüz, bazısı altmış, bazısı da otuz kat ürün verdi."));
trsuperobject.biblemap.put("b40_13_9", new trsuperobject(40,13,9,"Kulağı olan işitsin!››"));
trsuperobject.biblemap.put("b40_13_10", new trsuperobject(40,13,10,"Öğrencileri gelip İsaya, ‹‹Halka neden benzetmelerle konuşuyorsun?›› diye sordular."));
trsuperobject.biblemap.put("b40_13_11", new trsuperobject(40,13,11,"İsa şöyle yanıtladı: ‹‹Göklerin Egemenliğinin sırlarını bilme ayrıcalığı size verildi, ama onlara verilmedi."));
trsuperobject.biblemap.put("b40_13_12", new trsuperobject(40,13,12,"Çünkü kimde varsa, ona daha çok verilecek, bolluğa kavuşturulacak. Ama kimde yoksa, elindeki de alınacak."));
trsuperobject.biblemap.put("b40_13_13", new trsuperobject(40,13,13,"Onlara benzetmelerle konuşmamın nedeni budur. Çünkü,  ‹Gördükleri halde görmezler,  Duydukları halde duymaz ve anlamazlar.›"));
trsuperobject.biblemap.put("b40_13_14", new trsuperobject(40,13,14,"‹‹Böylece Yeşayanın peygamberlik sözü onlar için gerçekleşmiş oldu:  ‹Duyacak duyacak, ama hiç anlamayacaksınız,  Bakacak bakacak, ama hiç görmeyeceksiniz!"));
trsuperobject.biblemap.put("b40_13_15", new trsuperobject(40,13,15,"Çünkü bu halkın yüreği duygusuzlaştı,  Kulakları ağırlaştı.  Gözlerini kapadılar.  Öyle ki, gözleri görmesin,  Kulakları duymasın, yürekleri anlamasın  Ve bana dönmesinler.  Dönselerdi, onları iyileştirirdim.›"));
trsuperobject.biblemap.put("b40_13_16", new trsuperobject(40,13,16,"‹‹Ama ne mutlu size ki, gözleriniz görüyor, kulaklarınız işitiyor!"));
trsuperobject.biblemap.put("b40_13_17", new trsuperobject(40,13,17,"Size doğrusunu söyleyeyim, nice peygamberler, nice doğru kişiler sizin gördüklerinizi görmek istediler, ama göremediler. Sizin işittiklerinizi işitmek istediler, ama işitemediler."));
trsuperobject.biblemap.put("b40_13_18", new trsuperobject(40,13,18,"‹‹Şimdi ekinciyle ilgili benzetmeyi siz dinleyin."));
trsuperobject.biblemap.put("b40_13_19", new trsuperobject(40,13,19,"Kim göksel egemenlikle ilgili sözü işitir de anlamazsa, kötü olan gelir, onun yüreğine ekileni söker götürür. Yol kenarına ekilen tohum işte budur."));
trsuperobject.biblemap.put("b40_13_2-021", new trsuperobject(40,13,2-021,"Kayalık yerlere ekilen ise işittiği sözü hemen sevinçle kabul eden, ama kök salamadığı için ancak bir süre dayanan kişidir. Böyle biri Tanrı sözünden ötürü sıkıntı ya da zulme uğrayınca hemen sendeleyip düşer."));
trsuperobject.biblemap.put("b40_13_22", new trsuperobject(40,13,22,"Dikenler arasında ekilen de şudur: Sözü işitir, ama dünyasal kaygılar ve zenginliğin aldatıcılığı sözü boğar ve ürün vermesini engeller."));
trsuperobject.biblemap.put("b40_13_23", new trsuperobject(40,13,23,"İyi toprağa ekilen tohum ise, sözü işitip anlayan birine benzer. Böylesi elbette ürün verir, kimi yüz, kimi altmış, kimi de otuz kat.››"));
trsuperobject.biblemap.put("b40_13_24", new trsuperobject(40,13,24,"İsa onlara başka bir benzetme anlattı: ‹‹Göklerin Egemenliği, tarlasına iyi tohum eken adama benzer›› dedi."));
trsuperobject.biblemap.put("b40_13_25", new trsuperobject(40,13,25,"‹‹Herkes uyurken, adamın düşmanı geldi, buğdayın arasına delice ekip gitti."));
trsuperobject.biblemap.put("b40_13_26", new trsuperobject(40,13,26,"Ekin gelişip başak salınca, deliceler de göründü."));
trsuperobject.biblemap.put("b40_13_27", new trsuperobject(40,13,27,"‹‹Mal sahibinin köleleri gelip ona şöyle dediler: ‹Efendimiz, sen tarlana iyi tohum ekmedin mi? Bu deliceler nereden çıktı?›"));
trsuperobject.biblemap.put("b40_13_28", new trsuperobject(40,13,28,"‹‹Mal sahibi, ‹Bunu bir düşman yapmıştır› dedi. ‹‹Gidip deliceleri toplamamızı ister misin?› diye sordu köleler."));
trsuperobject.biblemap.put("b40_13_29", new trsuperobject(40,13,29,"‹‹Hayır› dedi adam. ‹Deliceleri toplarken belki buğdayı da sökersiniz."));
trsuperobject.biblemap.put("b40_13_30", new trsuperobject(40,13,30,"Bırakın biçim vaktine dek birlikte büyüsünler. Biçim vakti orakçılara, önce deliceleri toplayın diyeceğim, yakmak için demet yapın. Buğdayı ise toplayıp ambarıma koyun.››"));
trsuperobject.biblemap.put("b40_13_31", new trsuperobject(40,13,31,"İsa onlara bir benzetme daha anlattı: ‹‹Göklerin Egemenliği, bir adamın tarlasına ektiği hardal tanesine benzer›› dedi."));
trsuperobject.biblemap.put("b40_13_32", new trsuperobject(40,13,32,"‹‹Hardal tohumların en küçüğü olduğu halde, gelişince bahçe bitkilerinin boyunu aşar, ağaç olur. Böylece kuşlar gelip dallarında barınır.››"));
trsuperobject.biblemap.put("b40_13_33", new trsuperobject(40,13,33,"İsa onlara başka bir benzetme anlattı: ‹‹Göklerin Egemenliği, bir kadının üç ölçek una karıştırdığı mayaya benzer. Sonunda bütün hamur kabarır.››"));
trsuperobject.biblemap.put("b40_13_34", new trsuperobject(40,13,34,"İsa bütün bunları halka benzetmelerle anlattı. Benzetme kullanmadan onlara hiçbir şey anlatmazdı."));
trsuperobject.biblemap.put("b40_13_35", new trsuperobject(40,13,35,"Bu, peygamber aracılığıyla bildirilen şu söz yerine gelsin diye oldu:  ‹‹Ağzımı benzetmeler anlatarak açacağım,  Dünyanın kuruluşundan beri  Gizli kalmış sırları dile getireceğim.››"));
trsuperobject.biblemap.put("b40_13_36", new trsuperobject(40,13,36,"Bundan sonra İsa halktan ayrılıp eve gitti. Öğrencileri yanına gelip, ‹‹Tarladaki delicelerle ilgili benzetmeyi bize açıkla›› dediler."));
trsuperobject.biblemap.put("b40_13_37", new trsuperobject(40,13,37,"İsa, ‹‹İyi tohumu eken, İnsanoğludur›› diye karşılık verdi."));
trsuperobject.biblemap.put("b40_13_38", new trsuperobject(40,13,38,"‹‹Tarla ise dünyadır. İyi tohum, göksel egemenliğin oğulları, deliceler de kötü olanın oğullarıdır."));
trsuperobject.biblemap.put("b40_13_39", new trsuperobject(40,13,39,"Deliceleri eken düşman, İblistir. Biçim vakti, çağın sonu; orakçılar ise meleklerdir."));
trsuperobject.biblemap.put("b40_13_40", new trsuperobject(40,13,40,"‹‹Deliceler nasıl toplanıp yakılırsa, çağın sonunda da böyle olacaktır."));
trsuperobject.biblemap.put("b40_13_41-42", new trsuperobject(40,13,41-42,"İnsanoğlu meleklerini gönderecek, onlar da insanları günaha düşüren her şeyi, kötülük yapan herkesi Onun egemenliğinden toplayıp kızgın fırına atacaklar. Orada ağlayış ve diş gıcırtısı olacaktır."));
trsuperobject.biblemap.put("b40_13_43", new trsuperobject(40,13,43,"Doğru kişiler o zaman Babalarının egemenliğinde güneş gibi parlayacaklar. Kulağı olan işitsin!››"));
trsuperobject.biblemap.put("b40_13_44", new trsuperobject(40,13,44,"‹‹Göklerin Egemenliği, tarlada saklı bir defineye benzer. Onu bulan yeniden sakladı, sevinçle koşup gitti, varını yoğunu satıp tarlayı satın aldı."));
trsuperobject.biblemap.put("b40_13_45", new trsuperobject(40,13,45,"‹‹Yine Göklerin Egemenliği, güzel inciler arayan bir tüccara benzer."));
trsuperobject.biblemap.put("b40_13_46", new trsuperobject(40,13,46,"Tüccar, çok değerli bir inci bulunca gitti, varını yoğunu satıp o inciyi satın aldı.››"));
trsuperobject.biblemap.put("b40_13_47", new trsuperobject(40,13,47,"‹‹Yine Göklerin Egemenliği, denize atılan ve her çeşit balığı toplayan ağa benzer."));
trsuperobject.biblemap.put("b40_13_48", new trsuperobject(40,13,48,"Ağ dolunca onu kıyıya çekerler. Oturup işe yarayan balıkları kaplara koyar, yaramayanları atarlar."));
trsuperobject.biblemap.put("b40_13_49-50", new trsuperobject(40,13,49-50,"Çağın sonunda da böyle olacak. Melekler gelecek, kötü kişileri doğruların arasından ayırıp kızgın fırına atacaklar. Orada ağlayış ve diş gıcırtısı olacaktır.››"));
trsuperobject.biblemap.put("b40_13_51", new trsuperobject(40,13,51,"İsa, ‹‹Bütün bunları anladınız mı?›› diye sordu. ‹‹Evet›› karşılığını verdiler."));
trsuperobject.biblemap.put("b40_13_52", new trsuperobject(40,13,52,"O da onlara, ‹‹İşte böylece Göklerin Egemenliği için eğitilmiş her din bilgini, hazinesinden hem yeni hem eski değerler çıkaran bir mal sahibine benzer›› dedi."));
trsuperobject.biblemap.put("b40_13_53", new trsuperobject(40,13,53,"İsa bütün bu benzetmeleri anlattıktan sonra oradan ayrıldı."));
trsuperobject.biblemap.put("b40_13_54", new trsuperobject(40,13,54,"Kendi memleketine gitti ve oradaki havrada halka öğretmeye başladı. Halk şaşıp kalmıştı. ‹‹Adamın bu bilgeliği ve mucizeler yaratan gücü nereden geliyor?›› diyorlardı."));
trsuperobject.biblemap.put("b40_13_55", new trsuperobject(40,13,55,"‹‹Marangozun oğlu değil mi bu? Annesinin adı Meryem değil mi? Yakup, Yusuf, Simun ve Yahuda Onun kardeşleri değil mi?"));
trsuperobject.biblemap.put("b40_13_56", new trsuperobject(40,13,56,"Kızkardeşlerinin hepsi aramızda yaşamıyor mu? O halde Onun bütün bu yaptıkları nereden geliyor?››"));
trsuperobject.biblemap.put("b40_13_57", new trsuperobject(40,13,57,"Ve gücenip Onu reddettiler. Ama İsa onlara şöyle dedi: ‹‹Bir peygamber, kendi memleketinden ve evinden başka yerde hor görülmez.››"));
trsuperobject.biblemap.put("b40_13_58", new trsuperobject(40,13,58,"İmansızlıkları yüzünden İsa orada pek fazla mucize yapmadı."));
trsuperobject.biblemap.put("b40_14_1-2", new trsuperobject(40,14,1-2,"O günlerde İsayla ilgili haberleri duyan bölge kralı Hirodes, adamlarına, ‹‹Bu, Vaftizci Yahyadır›› dedi. ‹‹Ölümden dirildi. Olağanüstü güçlerin onda etkin olmasının nedeni budur.››"));
trsuperobject.biblemap.put("b40_14_3", new trsuperobject(40,14,3,"Hirodes, kardeşi Filipusun karısı Hirodiya yüzünden Yahyayı tutuklatmış, bağlatıp zindana attırmıştı."));
trsuperobject.biblemap.put("b40_14_4", new trsuperobject(40,14,4,"Çünkü Yahya Hirodese, ‹‹O kadınla evlenmen Kutsal Yasaya aykırıdır›› demişti."));
trsuperobject.biblemap.put("b40_14_5", new trsuperobject(40,14,5,"Hirodes Yahyayı öldürtmek istemiş, ama halktan korkmuştu. Çünkü halk Yahyayı peygamber sayıyordu."));
trsuperobject.biblemap.put("b40_14_6-7", new trsuperobject(40,14,6-7,"Hirodesin doğum günü şenliği sırasında Hirodiyanın kızı ortaya çıkıp dans etti. Bu, Hirodesin öyle hoşuna gitti ki, ant içerek kıza ne dilerse vereceğini söyledi."));
trsuperobject.biblemap.put("b40_14_8", new trsuperobject(40,14,8,"Kız, annesinin kışkırtmasıyla, ‹‹Bana şimdi, bir tepsi üzerinde Vaftizci Yahyanın başını ver›› dedi."));
trsuperobject.biblemap.put("b40_14_9", new trsuperobject(40,14,9,"Kral buna çok üzüldüyse de, konuklarının önünde içtiği anttan ötürü bu dileğin yerine getirilmesini buyurdu."));
trsuperobject.biblemap.put("b40_14_10", new trsuperobject(40,14,10,"Adam gönderip zindanda Yahyanın başını kestirdi."));
trsuperobject.biblemap.put("b40_14_11", new trsuperobject(40,14,11,"Kesik baş tepsiyle getirilip kıza verildi, kız da bunu annesine götürdü."));
trsuperobject.biblemap.put("b40_14_12", new trsuperobject(40,14,12,"Yahyanın öğrencileri gelip cesedi aldılar ve gömdüler. Sonra gidip İsaya haber verdiler."));
trsuperobject.biblemap.put("b40_14_13", new trsuperobject(40,14,13,"İsa bunu duyunca, tek başına tenha bir yere çekilmek üzere bir tekneyle oradan ayrıldı. Bunu öğrenen halk, kentlerden çıkıp Onu yaya olarak izledi."));
trsuperobject.biblemap.put("b40_14_14", new trsuperobject(40,14,14,"İsa tekneden inince büyük bir kalabalıkla karşılaştı. Onlara acıdı ve hasta olanlarını iyileştirdi."));
trsuperobject.biblemap.put("b40_14_15", new trsuperobject(40,14,15,"Akşama doğru öğrencileri yanına gelip, ‹‹Burası ıssız bir yer›› dediler, ‹‹Vakit de geç oldu. Halkı salıver de köylere gidip kendilerine yiyecek alsınlar.››"));
trsuperobject.biblemap.put("b40_14_16", new trsuperobject(40,14,16,"İsa, ‹‹Gitmelerine gerek yok, onlara siz yiyecek verin›› dedi."));
trsuperobject.biblemap.put("b40_14_17", new trsuperobject(40,14,17,"Öğrenciler, ‹‹Burada beş ekmekle iki balıktan başka bir şeyimiz yok ki›› dediler."));
trsuperobject.biblemap.put("b40_14_18", new trsuperobject(40,14,18,"İsa, ‹‹Onları buraya, bana getirin›› dedi."));
trsuperobject.biblemap.put("b40_14_19", new trsuperobject(40,14,19,"Halka çayıra oturmalarını buyurduktan sonra, beş ekmekle iki balığı aldı, gözlerini göğe kaldırarak şükretti; sonra ekmekleri bölüp öğrencilerine verdi, onlar da halka dağıttılar."));
trsuperobject.biblemap.put("b40_14_20", new trsuperobject(40,14,20,"Herkes yiyip doydu. Artakalan parçalardan on iki sepet dolusu topladılar."));
trsuperobject.biblemap.put("b40_14_21", new trsuperobject(40,14,21,"Yemek yiyenlerin sayısı, kadın ve çocuklar hariç, yaklaşık beş bin erkekti."));
trsuperobject.biblemap.put("b40_14_22", new trsuperobject(40,14,22,"Bundan hemen sonra İsa öğrencilerine, tekneye binip kendisinden önce karşı yakaya geçmelerini buyurdu. Bu arada halkı evlerine gönderecekti."));
trsuperobject.biblemap.put("b40_14_23", new trsuperobject(40,14,23,"Halkı gönderdikten sonra dua etmek için tek başına dağa çıktı. Akşam olurken orada yalnızdı."));
trsuperobject.biblemap.put("b40_14_24", new trsuperobject(40,14,24,"O sırada tekne kıyıdan bir hayli uzakta dalgalarla boğuşuyordu. Çünkü rüzgar karşı yönden esiyordu."));
trsuperobject.biblemap.put("b40_14_25", new trsuperobject(40,14,25,"Sabaha karşı İsa, gölün üstünde yürüyerek onlara yaklaştı."));
trsuperobject.biblemap.put("b40_14_26", new trsuperobject(40,14,26,"Öğrenciler, Onun gölün üstünde yürüdüğünü görünce dehşete kapıldılar. ‹‹Bu bir hayalet!›› diyerek korkuyla bağrıştılar."));
trsuperobject.biblemap.put("b40_14_27", new trsuperobject(40,14,27,"Ama İsa hemen onlara seslenerek, ‹‹Cesur olun, benim, korkmayın!›› dedi."));
trsuperobject.biblemap.put("b40_14_28", new trsuperobject(40,14,28,"Petrus buna karşılık, ‹‹Ya Rab›› dedi, ‹‹Eğer sen isen, buyruk ver suyun üstünden yürüyerek sana geleyim.››"));
trsuperobject.biblemap.put("b40_14_29", new trsuperobject(40,14,29,"İsa, ‹‹Gel!›› dedi. Petrus da tekneden indi, suyun üstünden yürüyerek İsaya yaklaştı."));
trsuperobject.biblemap.put("b40_14_30", new trsuperobject(40,14,30,"Ama rüzgarın ne kadar güçlü estiğini görünce korktu, batmaya başladı. ‹‹Ya Rab, beni kurtar!›› diye bağırdı."));
trsuperobject.biblemap.put("b40_14_31", new trsuperobject(40,14,31,"İsa hemen elini uzatıp onu tuttu. Ona, ‹‹Ey kıt imanlı, neden kuşku duydun?›› dedi."));
trsuperobject.biblemap.put("b40_14_32", new trsuperobject(40,14,32,"Onlar tekneye bindikten sonra rüzgar dindi."));
trsuperobject.biblemap.put("b40_14_33", new trsuperobject(40,14,33,"Teknedekiler, ‹‹Sen gerçekten Tanrının Oğlusun›› diyerek Ona tapındılar."));
trsuperobject.biblemap.put("b40_14_34", new trsuperobject(40,14,34,"Gölü aşıp Ginnesarda karaya çıktılar."));
trsuperobject.biblemap.put("b40_14_35", new trsuperobject(40,14,35,"Yöre halkı İsayı tanıyınca çevreye haber saldı. Bütün hastaları Ona getirdiler."));
trsuperobject.biblemap.put("b40_14_36", new trsuperobject(40,14,36,"Giysisinin eteğine bir dokunsak diye yalvarıyorlardı. Dokunanların hepsi iyileşti."));
trsuperobject.biblemap.put("b40_15_1-2", new trsuperobject(40,15,1-2,"Bu sırada Yeruşalimden bazı Ferisiler ve din bilginleri İsaya gelip, ‹‹Öğrencilerin neden atalarımızın töresini çiğniyor?›› diye sordular, ‹‹Yemekten önce ellerini yıkamıyorlar.››"));
trsuperobject.biblemap.put("b40_15_3", new trsuperobject(40,15,3,"İsa onlara şu karşılığı verdi: ‹‹Ya siz, neden töreniz uğruna Tanrı buyruğunu çiğniyorsunuz?"));
trsuperobject.biblemap.put("b40_15_4", new trsuperobject(40,15,4,"Çünkü Tanrı şöyle buyurdu: ‹Annene babana saygı göstereceksin›; ‹Annesine ya da babasına söven kesinlikle öldürülecektir.›"));
trsuperobject.biblemap.put("b40_15_5-6", new trsuperobject(40,15,5-6,"Ama siz, ‹Her kim anne ya da babasına, benden alacağın bütün yardım Tanrıya adanmıştır derse, artık babasına saygı göstermek zorunda değildir› diyorsunuz. Böylelikle, töreniz uğruna Tanrının sözünü geçersiz kılmış oluyorsunuz."));
trsuperobject.biblemap.put("b40_15_7-8", new trsuperobject(40,15,7-8,"Ey ikiyüzlüler! Yeşayanın sizinle ilgili şu peygamberlik sözü ne kadar yerindedir:  ‹Bu halk dudaklarıyla beni sayar,  Ama yürekleri benden uzak."));
trsuperobject.biblemap.put("b40_15_9", new trsuperobject(40,15,9,"Bana boşuna taparlar.  Çünkü öğrettikleri, sadece insan buyruklarıdır.››"));
trsuperobject.biblemap.put("b40_15_10", new trsuperobject(40,15,10,"İsa, halkı yanına çağırıp onlara, ‹‹Dinleyin ve şunu belleyin›› dedi."));
trsuperobject.biblemap.put("b40_15_11", new trsuperobject(40,15,11,"‹‹Ağızdan giren şey insanı kirletmez. İnsanı kirleten ağızdan çıkandır.››"));
trsuperobject.biblemap.put("b40_15_12", new trsuperobject(40,15,12,"Bu sırada öğrencileri Ona gelip, ‹‹Biliyor musun?›› dediler, ‹‹Ferisiler bu sözü duyunca gücendiler.››"));
trsuperobject.biblemap.put("b40_15_13", new trsuperobject(40,15,13,"İsa şu karşılığı verdi: ‹‹Göksel Babamın dikmediği her fidan kökünden sökülecektir."));
trsuperobject.biblemap.put("b40_15_14", new trsuperobject(40,15,14,"Bırakın onları; onlar körlerin kör kılavuzlarıdır. Eğer kör köre kılavuzluk ederse, ikisi de çukura düşer.››"));
trsuperobject.biblemap.put("b40_15_15", new trsuperobject(40,15,15,"Petrus, ‹‹Bu benzetmeyi bize açıkla›› dedi."));
trsuperobject.biblemap.put("b40_15_16", new trsuperobject(40,15,16,"‹‹Siz de mi hâlâ anlamıyorsunuz?›› diye sordu İsa."));
trsuperobject.biblemap.put("b40_15_17", new trsuperobject(40,15,17,"‹‹Ağza giren her şeyin mideye indiğini, oradan da helaya atıldığını bilmiyor musunuz?"));
trsuperobject.biblemap.put("b40_15_18", new trsuperobject(40,15,18,"Ne var ki ağızdan çıkan, yürekten kaynaklanır. İnsanı kirleten de budur."));
trsuperobject.biblemap.put("b40_15_19", new trsuperobject(40,15,19,"Çünkü kötü düşünceler, cinayet, zina, fuhuş, hırsızlık, yalan yere tanıklık ve iftira hep yürekten kaynaklanır."));
trsuperobject.biblemap.put("b40_15_20", new trsuperobject(40,15,20,"İnsanı kirleten bunlardır. Yıkanmamış ellerle yemek yemek insanı kirletmez.››"));
trsuperobject.biblemap.put("b40_15_21", new trsuperobject(40,15,21,"İsa oradan ayrılıp Sur ve Sayda bölgesine geçti."));
trsuperobject.biblemap.put("b40_15_22", new trsuperobject(40,15,22,"O yöreden Kenanlı bir kadın İsaya gelip, ‹‹Ya Rab, ey Davut Oğlu, halime acı! Kızım cine tutuldu, çok kötü durumda›› diye feryat etti."));
trsuperobject.biblemap.put("b40_15_23", new trsuperobject(40,15,23,"İsa kadına hiçbir karşılık vermedi. Öğrencileri yaklaşıp, ‹‹Sal şunu, gitsin!›› diye rica ettiler. ‹‹Arkamızdan bağırıp duruyor.››"));
trsuperobject.biblemap.put("b40_15_24", new trsuperobject(40,15,24,"İsa, ‹‹Ben yalnız İsrail halkının kaybolmuş koyunlarına gönderildim›› diye yanıtladı."));
trsuperobject.biblemap.put("b40_15_25", new trsuperobject(40,15,25,"Kadın ise yaklaşıp, ‹‹Ya Rab, bana yardım et!›› diyerek Onun önünde yere kapandı."));
trsuperobject.biblemap.put("b40_15_26", new trsuperobject(40,15,26,"İsa ona, ‹‹Çocukların ekmeğini alıp köpeklere atmak doğru değildir›› dedi."));
trsuperobject.biblemap.put("b40_15_27", new trsuperobject(40,15,27,"Kadın, ‹‹Haklısın, ya Rab›› dedi. ‹‹Ama köpekler de efendilerinin sofrasından düşen kırıntıları yer.››"));
trsuperobject.biblemap.put("b40_15_28", new trsuperobject(40,15,28,"O zaman İsa ona şu karşılığı verdi: ‹‹Ey kadın, imanın büyük! Dilediğin gibi olsun.›› Ve kadının kızı o saatte iyileşti."));
trsuperobject.biblemap.put("b40_15_29", new trsuperobject(40,15,29,"İsa oradan ayrıldı, Celile Gölünün kıyısından geçerek dağa çıkıp oturdu."));
trsuperobject.biblemap.put("b40_15_30", new trsuperobject(40,15,30,"Yanına büyük bir kalabalık geldi. Beraberlerinde kötürüm, kör, çolak, dilsiz ve daha birçok hasta getirdiler. Hastaları Onun ayaklarının dibine bıraktılar. O da onları iyileştirdi."));
trsuperobject.biblemap.put("b40_15_31", new trsuperobject(40,15,31,"Halk, dilsizlerin konuştuğunu, çolakların iyileştiğini, körlerin gördüğünü, kötürümlerin yürüdüğünü görünce şaştı ve İsrailin Tanrısını yüceltti."));
trsuperobject.biblemap.put("b40_15_32", new trsuperobject(40,15,32,"İsa öğrencilerini yanına çağırıp, ‹‹Halka acıyorum›› dedi. ‹‹Üç gündür yanımdalar, yiyecek hiçbir şeyleri yok. Onları aç aç evlerine göndermek istemiyorum, yolda bayılabilirler.››"));
trsuperobject.biblemap.put("b40_15_33", new trsuperobject(40,15,33,"Öğrenciler kendisine, ‹‹Böyle ıssız bir yerde bu kadar kalabalığı doyuracak ekmeği nereden bulalım?›› dediler."));
trsuperobject.biblemap.put("b40_15_34", new trsuperobject(40,15,34,"İsa, ‹‹Kaç ekmeğiniz var?›› diye sordu. ‹‹Yedi ekmekle birkaç küçük balığımız var›› dediler."));
trsuperobject.biblemap.put("b40_15_35", new trsuperobject(40,15,35,"Bunun üzerine İsa, halka yere oturmalarını buyurdu."));
trsuperobject.biblemap.put("b40_15_36", new trsuperobject(40,15,36,"Yedi ekmekle balıkları aldı, şükredip bunları böldü, öğrencilerine verdi. Onlar da halka dağıttılar."));
trsuperobject.biblemap.put("b40_15_37", new trsuperobject(40,15,37,"Herkes yiyip doydu. Artakalan parçalardan yedi küfe dolusu topladılar."));
trsuperobject.biblemap.put("b40_15_38", new trsuperobject(40,15,38,"Yemek yiyenlerin sayısı, kadın ve çocuklar hariç, dört bin erkekti."));
trsuperobject.biblemap.put("b40_15_39", new trsuperobject(40,15,39,"İsa, halkı evlerine gönderdikten sonra tekneye binip Magadan bölgesine geçti."));
trsuperobject.biblemap.put("b40_16_1", new trsuperobject(40,16,1,"Ferisilerle Sadukiler İsanın yanına geldiler. Onu denemek amacıyla kendilerine gökten bir belirti göstermesini istediler."));
trsuperobject.biblemap.put("b40_16_2", new trsuperobject(40,16,2,"İsa onlara şu karşılığı verdi: ‹‹Akşam, ‹Gökyüzü kızıl olduğuna göre hava iyi olacak› dersiniz."));
trsuperobject.biblemap.put("b40_16_3", new trsuperobject(40,16,3,"Sabah, ‹Bugün gök kızıl ve bulutlu, hava bozacak› dersiniz. Gökyüzünün görünümünü yorumlayabiliyorsunuz da, zamanın belirtilerini yorumlayamıyor musunuz?"));
trsuperobject.biblemap.put("b40_16_4", new trsuperobject(40,16,4,"Kötü ve vefasız kuşak bir belirti istiyor! Ama ona Yunusun belirtisinden başka bir belirti gösterilmeyecek.›› Sonra İsa onları bırakıp gitti."));
trsuperobject.biblemap.put("b40_16_5", new trsuperobject(40,16,5,"Öğrenciler gölün karşı yakasına geçerken ekmek almayı unutmuşlardı."));
trsuperobject.biblemap.put("b40_16_6", new trsuperobject(40,16,6,"İsa onlara, ‹‹Dikkatli olun, Ferisilerin ve Sadukilerin mayasından kaçının!›› dedi."));
trsuperobject.biblemap.put("b40_16_7", new trsuperobject(40,16,7,"Onlar ise kendi aralarında tartışarak, ‹‹Ekmek almadığımız için böyle diyor›› dediler."));
trsuperobject.biblemap.put("b40_16_8", new trsuperobject(40,16,8,"Bunun farkında olan İsa şöyle dedi: ‹‹Ey kıt imanlılar! Ekmeğiniz yok diye niçin tartışıyorsunuz?"));
trsuperobject.biblemap.put("b40_16_9-10", new trsuperobject(40,16,9-10,"Hâlâ anlamıyor musunuz? Beş ekmekle beş bin kişinin doyduğunu, kaç sepet dolusu yemek fazlası topladığınızı hatırlamıyor musunuz? Yedi ekmekle dört bin kişinin doyduğunu, kaç küfe dolusu yemek fazlası topladığınızı hatırlamıyor musunuz?"));
trsuperobject.biblemap.put("b40_16_11", new trsuperobject(40,16,11,"Ben size, ‹Ferisilerin ve Sadukilerin mayasından kaçının› derken, ekmekten söz etmediğimi nasıl olur da anlamazsınız?››"));
trsuperobject.biblemap.put("b40_16_12", new trsuperobject(40,16,12,"Ekmek mayasından değil de, Ferisilerle Sadukilerin öğretisinden kaçının dediğini o zaman anladılar."));
trsuperobject.biblemap.put("b40_16_13", new trsuperobject(40,16,13,"İsa, Filipus Sezariyesi bölgesine geldiğinde öğrencilerine şunu sordu: ‹‹Halk, İnsanoğlunun kim olduğunu söylüyor?››"));
trsuperobject.biblemap.put("b40_16_14", new trsuperobject(40,16,14,"Öğrencileri şu karşılığı verdiler: ‹‹Kimi Vaftizci Yahya, kimi İlyas, kimi de Yeremya ya da peygamberlerden biridir diyor.››"));
trsuperobject.biblemap.put("b40_16_15", new trsuperobject(40,16,15,"İsa onlara, ‹‹Siz ne dersiniz›› dedi, ‹‹Sizce ben kimim?››"));
trsuperobject.biblemap.put("b40_16_16", new trsuperobject(40,16,16,"Simun Petrus, ‹‹Sen, yaşayan Tanrının Oğlu Mesihsin›› yanıtını verdi."));
trsuperobject.biblemap.put("b40_16_17", new trsuperobject(40,16,17,"İsa ona, ‹‹Ne mutlu sana, Yunus oğlu Simun!›› dedi. ‹‹Bu sırrı sana açan insan değil, göklerdeki Babamdır."));
trsuperobject.biblemap.put("b40_16_18", new trsuperobject(40,16,18,"Ben de sana şunu söyleyeyim, sen Petrussun ve ben kilisemi bu kayanın üzerine kuracağım. Ölüler diyarının kapıları ona karşı direnemeyecek."));
trsuperobject.biblemap.put("b40_16_19", new trsuperobject(40,16,19,"Göklerin Egemenliğinin anahtarlarını sana vereceğim. Yeryüzünde bağlayacağın her şey göklerde de bağlanmış olacak; yeryüzünde çözeceğin her şey göklerde de çözülmüş olacak.››"));
trsuperobject.biblemap.put("b40_16_20", new trsuperobject(40,16,20,"Bu sözlerden sonra İsa, kendisinin Mesih olduğunu kimseye söylememeleri için öğrencilerini uyardı."));
trsuperobject.biblemap.put("b40_16_21", new trsuperobject(40,16,21,"Bundan sonra İsa, kendisinin Yeruşalime gitmesi, ileri gelenler, başkâhinler ve din bilginlerinin elinden çok acı çekmesi, öldürülmesi ve üçüncü gün dirilmesi gerektiğini öğrencilerine anlatmaya başladı."));
trsuperobject.biblemap.put("b40_16_22", new trsuperobject(40,16,22,"Bunun üzerine Petrus Onu bir kenara çekip azarlamaya başladı. ‹‹Tanrı korusun, ya Rab! Senin başına asla böyle bir şey gelmeyecek!›› dedi."));
trsuperobject.biblemap.put("b40_16_23", new trsuperobject(40,16,23,"Ama İsa Petrusa dönüp, ‹‹Çekil önümden, Şeytan!›› dedi, ‹‹Bana engel oluyorsun. Düşüncelerin Tanrıya değil, insana özgüdür.››"));
trsuperobject.biblemap.put("b40_16_24", new trsuperobject(40,16,24,"Sonra İsa, öğrencilerine şunları söyledi: ‹‹Ardımdan gelmek isteyen kendini inkâr etsin, çarmıhını yüklenip beni izlesin."));
trsuperobject.biblemap.put("b40_16_25", new trsuperobject(40,16,25,"Canını kurtarmak isteyen onu yitirecek, canını benim uğruma yitiren ise onu kurtaracaktır."));
trsuperobject.biblemap.put("b40_16_26", new trsuperobject(40,16,26,"İnsan bütün dünyayı kazanıp da canından olursa, bunun kendisine ne yararı olur? İnsan kendi canına karşılık ne verebilir?"));
trsuperobject.biblemap.put("b40_16_27", new trsuperobject(40,16,27,"İnsanoğlu, Babasının görkemi içinde melekleriyle gelecek ve herkese, yaptığının karşılığını verecektir."));
trsuperobject.biblemap.put("b40_16_28", new trsuperobject(40,16,28,"Size doğrusunu söyleyeyim, burada bulunanlar arasında, İnsanoğlu'nun kendi egemenliği içinde gelişini görmeden ölümü tatmayacak olanlar var.››"));
trsuperobject.biblemap.put("b40_17_1", new trsuperobject(40,17,1,"Altı gün sonra İsa, yanına yalnız Petrus, Yakup ve Yakupun kardeşi Yuhannayı alarak yüksek bir dağa çıktı."));
trsuperobject.biblemap.put("b40_17_2", new trsuperobject(40,17,2,"Onların gözü önünde İsanın görünümü değişti. Yüzü güneş gibi parladı, giysileri ışık gibi bembeyaz oldu."));
trsuperobject.biblemap.put("b40_17_3", new trsuperobject(40,17,3,"O anda Musayla İlyas öğrencilere göründü. İsayla konuşuyorlardı."));
trsuperobject.biblemap.put("b40_17_4", new trsuperobject(40,17,4,"Petrus İsaya, ‹‹Ya Rab›› dedi, ‹‹Burada bulunmamız ne iyi oldu! İstersen burada üç çardak kurayım: Biri sana, biri Musaya, biri de İlyasa.››"));
trsuperobject.biblemap.put("b40_17_5", new trsuperobject(40,17,5,"Petrus daha konuşurken parlak bir bulut onlara gölge saldı. Buluttan gelen bir ses, ‹‹Sevgili Oğlum budur, Ondan hoşnudum. Onu dinleyin!›› dedi."));
trsuperobject.biblemap.put("b40_17_6", new trsuperobject(40,17,6,"Öğrenciler bunu işitince, dehşet içinde yüzüstü yere kapandılar."));
trsuperobject.biblemap.put("b40_17_7", new trsuperobject(40,17,7,"İsa gelip onlara dokundu, ‹‹Kalkın, korkmayın!›› dedi."));
trsuperobject.biblemap.put("b40_17_8", new trsuperobject(40,17,8,"Başlarını kaldırınca İsadan başka kimseyi göremediler."));
trsuperobject.biblemap.put("b40_17_9", new trsuperobject(40,17,9,"Dağdan inerlerken İsa onlara, ‹‹İnsanoğlu ölümden dirilmeden, gördüklerinizi kimseye söylemeyin›› diye buyurdu."));
trsuperobject.biblemap.put("b40_17_10", new trsuperobject(40,17,10,"Öğrencileri Ona şunu sordular: ‹‹Peki, din bilginleri neden önce İlyasın gelmesi gerektiğini söylüyorlar?››"));
trsuperobject.biblemap.put("b40_17_11", new trsuperobject(40,17,11,"İsa, ‹‹İlyas gerçekten gelecek ve her şeyi yeniden düzene koyacak›› diye yanıtladı."));
trsuperobject.biblemap.put("b40_17_12", new trsuperobject(40,17,12,"‹‹Size şunu söyleyeyim, İlyas zaten geldi, ama onu tanımadılar, ona yapmadıklarını bırakmadılar. Aynı şekilde İnsanoğlu da onların elinden acı çekecektir.››"));
trsuperobject.biblemap.put("b40_17_13", new trsuperobject(40,17,13,"O zaman öğrenciler İsanın kendilerine Vaftizci Yahyadan söz ettiğini anladılar."));
trsuperobject.biblemap.put("b40_17_14", new trsuperobject(40,17,14,"Kalabalığın yanına vardıklarında bir adam İsaya yaklaşıp önünde diz çöktü."));
trsuperobject.biblemap.put("b40_17_15", new trsuperobject(40,17,15,"‹‹Ya Rab›› dedi, ‹‹Oğlumun haline acı! Sarası var, çok acı çekiyor. Sık sık ateşe, suya düşüyor."));
trsuperobject.biblemap.put("b40_17_16", new trsuperobject(40,17,16,"Onu senin öğrencilerine getirdim, ama iyileştiremediler.››"));
trsuperobject.biblemap.put("b40_17_17", new trsuperobject(40,17,17,"İsa, ‹‹Ey imansız ve sapmış kuşak!›› dedi. ‹‹Sizinle daha ne kadar kalacağım? Size daha ne kadar katlanacağım? Çocuğu buraya, bana getirin.››"));
trsuperobject.biblemap.put("b40_17_18", new trsuperobject(40,17,18,"İsa cini azarlayınca, cin çocuktan çıktı, çocuk o anda iyileşti."));
trsuperobject.biblemap.put("b40_17_19", new trsuperobject(40,17,19,"Sonra öğrenciler tek başlarına İsaya gelip, ‹‹Biz cini neden kovamadık?›› diye sordular."));
trsuperobject.biblemap.put("b40_17_2-021", new trsuperobject(40,17,2-021,"İsa, ‹‹İmanınız kıt olduğu için›› karşılığını verdi. ‹‹Size doğrusunu söyleyeyim, bir hardal tanesi kadar imanınız olsa şu dağa, ‹Buradan şuraya göç› derseniz, göçer; sizin için imkânsız bir şey olmayacaktır.››"));
trsuperobject.biblemap.put("b40_17_22-23", new trsuperobject(40,17,22-23,"Celilede bir araya geldiklerinde İsa onlara, ‹‹İnsanoğlu, insanların eline teslim edilecek ve öldürülecek, ama üçüncü gün dirilecek›› dedi. Öğrenciler buna çok kederlendiler."));
trsuperobject.biblemap.put("b40_17_24", new trsuperobject(40,17,24,"Kefarnahuma geldiklerinde, iki dirhemlik tapınak vergisini toplayanlar Petrusa gelip, ‹‹Öğretmeniniz tapınak vergisini ödemiyor mu?›› diye sordular."));
trsuperobject.biblemap.put("b40_17_25", new trsuperobject(40,17,25,"Petrus, ‹‹Ödüyor›› dedi. Petrus eve gelince, daha kendisi bir şey söylemeden İsa ona, ‹‹Simun, ne dersin?›› dedi. ‹‹Dünya kralları gümrük ya da vergiyi kimlerden alır? Kendi oğullarından mı, yabancılardan mı?››"));
trsuperobject.biblemap.put("b40_17_26", new trsuperobject(40,17,26,"Petrusun, ‹‹Yabancılardan›› demesi üzerine İsa, ‹‹O halde oğullar muaftır›› dedi."));
trsuperobject.biblemap.put("b40_17_27", new trsuperobject(40,17,27,"‹‹Ama vergi toplayanları gücendirmeyelim. Göle gidip oltanı at. Tuttuğun ilk balığı çıkar, onun ağzını aç, dört dirhemlik bir akçe bulacaksın. Parayı al, ikimizin vergisi olarak onlara ver.››"));
trsuperobject.biblemap.put("b40_18_1", new trsuperobject(40,18,1,"Bu sırada öğrencileri İsaya yaklaşıp, ‹‹Göklerin Egemenliğinde en büyük kimdir?›› diye sordular."));
trsuperobject.biblemap.put("b40_18_2-3", new trsuperobject(40,18,2-3,"İsa, yanına küçük bir çocuk çağırdı, onu orta yere dikip şöyle dedi: ‹‹Size doğrusunu söyleyeyim, yolunuzdan dönüp küçük çocuklar gibi olmazsanız, Göklerin Egemenliğine asla giremezsiniz."));
trsuperobject.biblemap.put("b40_18_4", new trsuperobject(40,18,4,"Kim bu çocuk gibi alçakgönüllü olursa, Göklerin Egemenliğinde en büyük odur."));
trsuperobject.biblemap.put("b40_18_5", new trsuperobject(40,18,5,"Böyle bir çocuğu benim adım uğruna kabul eden, beni kabul etmiş olur."));
trsuperobject.biblemap.put("b40_18_6", new trsuperobject(40,18,6,"‹‹Ama kim bana iman eden bu küçüklerden birini günaha düşürürse, boynuna kocaman bir değirmen taşı asılıp denizin dibine atılması kendisi için daha iyi olur."));
trsuperobject.biblemap.put("b40_18_7", new trsuperobject(40,18,7,"İnsanı günaha düşüren tuzaklardan ötürü vay dünyanın haline! Böyle tuzakların olması kaçınılmazdır. Ama bu tuzaklara aracılık eden kişinin vay haline!"));
trsuperobject.biblemap.put("b40_18_8", new trsuperobject(40,18,8,"‹‹Eğer elin ya da ayağın günah işlemene neden olursa, onu kesip at. Tek el, tek ayakla yaşama kavuşman, iki elle, iki ayakla sönmez ateşe atılmandan iyidir."));
trsuperobject.biblemap.put("b40_18_9", new trsuperobject(40,18,9,"Eğer gözün günah işlemene neden olursa, onu çıkar at. Tek gözle yaşama kavuşman, iki gözle cehennem ateşine atılmandan iyidir."));
trsuperobject.biblemap.put("b40_18_1-011", new trsuperobject(40,18,1-011,"‹‹Bu küçüklerden birini bile hor görmekten sakının! Size şunu söyleyeyim, onların göklerdeki melekleri, göklerdeki Babamın yüzünü her zaman görürler.››"));
trsuperobject.biblemap.put("b40_18_12", new trsuperobject(40,18,12,"‹‹Siz ne dersiniz? Bir adamın yüz koyunu olsa ve bunlardan biri yolunu şaşırsa, doksan dokuzunu dağlarda bırakıp yolunu şaşıranı aramaya gitmez mi?"));
trsuperobject.biblemap.put("b40_18_13", new trsuperobject(40,18,13,"Size doğrusunu söyleyeyim, eğer onu bulursa, yolunu şaşırmamış doksan dokuz koyun için sevindiğinden daha çok onun için sevinir."));
trsuperobject.biblemap.put("b40_18_14", new trsuperobject(40,18,14,"Bunun gibi, göklerdeki Babanız da bu küçüklerden hiçbirinin kaybolmasını istemez.››"));
trsuperobject.biblemap.put("b40_18_15", new trsuperobject(40,18,15,"‹‹Eğer kardeşin sana karşı günah işlerse, ona git, suçunu kendisine göster. Her şey yalnız ikinizin arasında kalsın. Kardeşin seni dinlerse, onu kazanmış olursun."));
trsuperobject.biblemap.put("b40_18_16", new trsuperobject(40,18,16,"Ama dinlemezse, yanına bir ya da iki kişi daha al ki, söylenen her şey iki ya da üç tanığın sözüyle doğrulansın."));
trsuperobject.biblemap.put("b40_18_17", new trsuperobject(40,18,17,"Onları da dinlemezse, durumu inanlılar topluluğuna bildir. Topluluğu da dinlemezse, onu putperest ya da vergi görevlisi say."));
trsuperobject.biblemap.put("b40_18_18", new trsuperobject(40,18,18,"‹‹Size doğrusunu söyleyeyim, yeryüzünde bağlayacağınız her şey gökte de bağlanmış olacak. Yeryüzünde çözeceğiniz her şey gökte de çözülmüş olacak."));
trsuperobject.biblemap.put("b40_18_19", new trsuperobject(40,18,19,"Yine size şunu söyleyeyim, yeryüzünde aranızdan iki kişi, dileyecekleri herhangi bir şey için anlaşırlarsa, göklerdeki Babam dileklerini yerine getirir."));
trsuperobject.biblemap.put("b40_18_20", new trsuperobject(40,18,20,"Nerede iki ya da üç kişi benim adımla toplanırsa, ben de orada, aralarındayım.››"));
trsuperobject.biblemap.put("b40_18_21", new trsuperobject(40,18,21,"Bunun üzerine Petrus İsaya gelip, ‹‹Ya Rab›› dedi, ‹‹Kardeşim bana karşı kaç kez günah işlerse onu bağışlamalıyım? Yedi kez mi?››"));
trsuperobject.biblemap.put("b40_18_22", new trsuperobject(40,18,22,"İsa, ‹‹Yedi kez değil›› dedi. ‹‹Yetmiş kere yedi kez derim sana."));
trsuperobject.biblemap.put("b40_18_23", new trsuperobject(40,18,23,"Şöyle ki, Göklerin Egemenliği, köleleriyle hesaplaşmak isteyen bir krala benzer."));
trsuperobject.biblemap.put("b40_18_24", new trsuperobject(40,18,24,"Kral hesap görmeye başladığında kendisine, borcu on bin talantı bulan bir köle getirildi."));
trsuperobject.biblemap.put("b40_18_25", new trsuperobject(40,18,25,"Kölenin ödeme gücü olmadığından efendisi onun, karısının, çocuklarının ve bütün malının satılıp borcun ödenmesini buyurdu."));
trsuperobject.biblemap.put("b40_18_26", new trsuperobject(40,18,26,"Köle yere kapanıp efendisine, ‹Ne olur, sabret! Bütün borcumu ödeyeceğim› dedi."));
trsuperobject.biblemap.put("b40_18_27", new trsuperobject(40,18,27,"Efendisi köleye acıdı, borcunu bağışlayıp onu salıverdi."));
trsuperobject.biblemap.put("b40_18_28", new trsuperobject(40,18,28,"‹‹Ama köle çıkıp gitti, kendisine yüz dinar borcu olan başka bir köleye rastladı. Onu yakalayıp, ‹Borcunu öde› diyerek boğazına sarıldı."));
trsuperobject.biblemap.put("b40_18_29", new trsuperobject(40,18,29,"Bu köle yüzüstü yere kapandı, ‹Ne olur, sabret! Borcumu ödeyeceğim› diye yalvardı."));
trsuperobject.biblemap.put("b40_18_30", new trsuperobject(40,18,30,"Ama ilk köle bunu reddetti. Gitti, borcunu ödeyinceye dek adamı zindana kapattı."));
trsuperobject.biblemap.put("b40_18_31", new trsuperobject(40,18,31,"Öteki köleler, olanları görünce çok üzüldüler. Efendilerine gidip bütün olup bitenleri anlattılar."));
trsuperobject.biblemap.put("b40_18_32", new trsuperobject(40,18,32,"‹‹Bunun üzerine efendisi köleyi yanına çağırdı. ‹Ey kötü köle!› dedi. ‹Bana yalvardığın için bütün borcunu bağışladım."));
trsuperobject.biblemap.put("b40_18_33", new trsuperobject(40,18,33,"Benim sana acıdığım gibi, senin de köle arkadaşına acıman gerekmez miydi?›"));
trsuperobject.biblemap.put("b40_18_34", new trsuperobject(40,18,34,"Bu öfkeyle efendisi, bütün borcunu ödeyinceye dek onu işkencecilere teslim etti."));
trsuperobject.biblemap.put("b40_18_35", new trsuperobject(40,18,35,"‹‹Eğer her biriniz kardeşini gönülden bağışlamazsa, göksel Babam da size öyle davranacaktır.››"));
trsuperobject.biblemap.put("b40_19_1", new trsuperobject(40,19,1,"İsa konuşmasını bitirdikten sonra Celileden ayrılıp Yahudiyenin Şeria Irmağının karşı yakasındaki topraklarına geçti."));
trsuperobject.biblemap.put("b40_19_2", new trsuperobject(40,19,2,"Büyük halk toplulukları da Onun ardından gitti. Hasta olanları orada iyileştirdi."));
trsuperobject.biblemap.put("b40_19_3", new trsuperobject(40,19,3,"İsanın yanına gelen bazı Ferisiler, Onu denemek amacıyla şunu sordular: ‹‹Bir adamın, herhangi bir nedenle karısını boşaması Kutsal Yasaya uygun mudur?››"));
trsuperobject.biblemap.put("b40_19_4-5", new trsuperobject(40,19,4-5,"İsa şu karşılığı verdi: ‹‹Kutsal Yazıları okumadınız mı? Yaradan başlangıçtan ‹İnsanları erkek ve dişi olarak yarattı› ve şöyle dedi: ‹Bu nedenle adam annesini babasını bırakıp karısına bağlanacak, ikisi tek beden olacak.›"));
trsuperobject.biblemap.put("b40_19_6", new trsuperobject(40,19,6,"Şöyle ki, onlar artık iki değil, tek bedendir. O halde Tanrının birleştirdiğini, insan ayırmasın.››"));
trsuperobject.biblemap.put("b40_19_7", new trsuperobject(40,19,7,"Ferisiler İsaya, ‹‹Öyleyse›› dediler, ‹‹Musa neden erkeğin boşanma belgesi verip karısını boşayabileceğini söyledi?››"));
trsuperobject.biblemap.put("b40_19_8", new trsuperobject(40,19,8,"İsa onlara, ‹‹İnatçı olduğunuz için Musa karılarınızı boşamanıza izin verdi›› dedi. ‹‹Başlangıçta bu böyle değildi."));
trsuperobject.biblemap.put("b40_19_9", new trsuperobject(40,19,9,"Ben size şunu söyleyeyim, karısını fuhuştan başka bir nedenle boşayıp başkasıyla evlenen, zina etmiş olur. Boşanan kadınla evlenen de zina etmiş olur.››"));
trsuperobject.biblemap.put("b40_19_10", new trsuperobject(40,19,10,"Öğrenciler İsaya, ‹‹Eğer erkekle karısı arasındaki ilişki buysa, hiç evlenmemek daha iyi!›› dediler."));
trsuperobject.biblemap.put("b40_19_11", new trsuperobject(40,19,11,"İsa onlara, ‹‹Herkes bu sözü kabul edemez, ancak Tanrının güç verdiği kişiler kabul edebilir›› dedi."));
trsuperobject.biblemap.put("b40_19_12", new trsuperobject(40,19,12,"‹‹Çünkü kimisi doğuştan hadımdır, kimisi insanlar tarafından hadım edilir, kimisi de Göklerin Egemenliği uğruna kendini hadım sayar. Bunu kabul edebilen etsin!››"));
trsuperobject.biblemap.put("b40_19_13-14", new trsuperobject(40,19,13-14,"O sırada bazıları küçük çocukları İsanın yanına getirdiler; ellerini onların üzerine koyup dua etmesini istediler. Öğrenciler onları azarlayınca İsa, ‹‹Bırakın çocukları›› dedi. ‹‹Bana gelmelerine engel olmayın! Çünkü Göklerin Egemenliği böylelerinindir.››"));
trsuperobject.biblemap.put("b40_19_15", new trsuperobject(40,19,15,"Ellerini onların üzerine koyduktan sonra oradan ayrıldı."));
trsuperobject.biblemap.put("b40_19_16", new trsuperobject(40,19,16,"Adamın biri İsaya gelip, ‹‹Öğretmenim, sonsuz yaşama kavuşmak için nasıl bir iyilik yapmalıyım?›› diye sordu."));
trsuperobject.biblemap.put("b40_19_17", new trsuperobject(40,19,17,"İsa, ‹‹Bana neden iyilik hakkında soru soruyorsun?›› dedi. ‹‹İyi olan yalnız biri var. Yaşama kavuşmak istiyorsan, Onun buyruklarını yerine getir.››"));
trsuperobject.biblemap.put("b40_19_18-19", new trsuperobject(40,19,18-19,"‹‹Hangi buyrukları?›› diye sordu adam. İsa şu karşılığı verdi: ‹‹Adam öldürmeyeceksin, zina etmeyeceksin, çalmayacaksın, yalan yere tanıklık etmeyeceksin, annene babana saygı göstereceksin› ve ‹Komşunu kendin gibi seveceksin.››"));
trsuperobject.biblemap.put("b40_19_20", new trsuperobject(40,19,20,"Genç adam, ‹‹Bunların hepsini yerine getirdim›› dedi, ‹‹Daha ne eksiğim var?››"));
trsuperobject.biblemap.put("b40_19_21", new trsuperobject(40,19,21,"İsa ona, ‹‹Eğer eksiksiz olmak istiyorsan, git, varını yoğunu sat, parasını yoksullara ver; böylece göklerde hazinen olur. Sonra gel, beni izle›› dedi."));
trsuperobject.biblemap.put("b40_19_22", new trsuperobject(40,19,22,"Genç adam bu sözleri işitince üzüntü içinde oradan uzaklaştı. Çünkü çok malı vardı."));
trsuperobject.biblemap.put("b40_19_23", new trsuperobject(40,19,23,"İsa öğrencilerine, ‹‹Size doğrusunu söyleyeyim›› dedi, ‹‹Zengin kişi Göklerin Egemenliğine zor girecek."));
trsuperobject.biblemap.put("b40_19_24", new trsuperobject(40,19,24,"Yine şunu söyleyeyim ki, devenin iğne deliğinden geçmesi, zenginin Tanrı Egemenliğine girmesinden daha kolaydır.››"));
trsuperobject.biblemap.put("b40_19_25", new trsuperobject(40,19,25,"Bunu işiten öğrenciler büsbütün şaşırdılar, ‹‹Öyleyse kim kurtulabilir?›› diye sordular."));
trsuperobject.biblemap.put("b40_19_26", new trsuperobject(40,19,26,"İsa onlara bakarak, ‹‹İnsanlar için bu imkânsız, ama Tanrı için her şey mümkündür›› dedi."));
trsuperobject.biblemap.put("b40_19_27", new trsuperobject(40,19,27,"Bunun üzerine Petrus Ona, ‹‹Bak›› dedi, ‹‹Biz her şeyi bırakıp senin ardından geldik, kazancımız ne olacak?››"));
trsuperobject.biblemap.put("b40_19_28", new trsuperobject(40,19,28,"İsa onlara, ‹‹Size doğrusunu söyleyeyim›› dedi, ‹‹Her şey yenilendiğinde, İnsanoğlu görkemli tahtına oturduğunda, siz, evet ardımdan gelen sizler, on iki tahta oturup İsrailin on iki oymağını yargılayacaksınız."));
trsuperobject.biblemap.put("b40_19_29", new trsuperobject(40,19,29,"Benim adım uğruna evlerini, kardeşlerini, anne ya da babasını, çocuklarını ya da topraklarını bırakan herkes, bunların yüz katını elde edecek ve sonsuz yaşamı miras alacak."));
trsuperobject.biblemap.put("b40_19_30", new trsuperobject(40,19,30,"Ne var ki, birincilerin birçoğu sonuncu, sonuncuların birçoğu da birinci olacak.››"));
trsuperobject.biblemap.put("b40_20_1", new trsuperobject(40,20,1,"‹‹Göklerin Egemenliği, sabah erkenden bağında çalışacak işçi aramaya çıkan toprak sahibine benzer."));
trsuperobject.biblemap.put("b40_20_2", new trsuperobject(40,20,2,"Adam, işçilerle günlüğü bir dinara anlaşıp onları bağına gönderdi."));
trsuperobject.biblemap.put("b40_20_3", new trsuperobject(40,20,3,"‹‹Saat dokuza doğru tekrar dışarı çıktı, çarşı meydanında boş duran başka adamlar gördü."));
trsuperobject.biblemap.put("b40_20_4-5", new trsuperobject(40,20,4-5,"Onlara, ‹Siz de bağa gidip çalışın. Hakkınız neyse, veririm› dedi, onlar da bağa gittiler. ‹‹Öğleyin ve saat üçe doğru yine çıkıp aynı şeyi yaptı."));
trsuperobject.biblemap.put("b40_20_6", new trsuperobject(40,20,6,"Saat beşe doğru çıkınca, orada duran başka işçiler gördü. Onlara, ‹Neden bütün gün burada boş duruyorsunuz?› diye sordu."));
trsuperobject.biblemap.put("b40_20_7", new trsuperobject(40,20,7,"‹‹Kimse bize iş vermedi ki› dediler. ‹‹Onlara, ‹Siz de bağa gidin, çalışın› dedi."));
trsuperobject.biblemap.put("b40_20_8", new trsuperobject(40,20,8,"‹‹Akşam olunca, bağın sahibi kâhyasına, ‹İşçileri çağır› dedi. ‹Sonuncudan başlayarak ilkine kadar, hepsine ücretlerini ver.›"));
trsuperobject.biblemap.put("b40_20_9", new trsuperobject(40,20,9,"‹‹Saat beşe doğru işe başlayanlar gelip kâhyadan birer dinar aldılar."));
trsuperobject.biblemap.put("b40_20_10", new trsuperobject(40,20,10,"İlk başlayanlar gelince daha çok alacaklarını sandılar, ama onlara da birer dinar verildi."));
trsuperobject.biblemap.put("b40_20_11", new trsuperobject(40,20,11,"Paralarını alınca bağ sahibine söylenmeye başladılar:"));
trsuperobject.biblemap.put("b40_20_12", new trsuperobject(40,20,12,"‹En son çalışanlar yalnız bir saat çalıştı› dediler. ‹Ama onları günün yükünü ve sıcağını çeken bizlerle bir tuttun!›"));
trsuperobject.biblemap.put("b40_20_13", new trsuperobject(40,20,13,"‹‹Bağ sahibi onlardan birine şöyle karşılık verdi: ‹Arkadaş, sana haksızlık etmiyorum ki! Seninle bir dinara anlaşmadık mı?"));
trsuperobject.biblemap.put("b40_20_14", new trsuperobject(40,20,14,"Hakkını al, git! Sana verdiğimi sonuncuya da vermek istiyorum."));
trsuperobject.biblemap.put("b40_20_15", new trsuperobject(40,20,15,"Kendi paramla istediğimi yapmaya hakkım yok mu? Yoksa cömertliğimi kıskanıyor musun?›"));
trsuperobject.biblemap.put("b40_20_16", new trsuperobject(40,20,16,"‹‹İşte böylece sonuncular birinci, birinciler de sonuncu olacak.››"));
trsuperobject.biblemap.put("b40_20_17-18", new trsuperobject(40,20,17-18,"İsa Yeruşalime giderken, yolda on iki öğrencisini bir yana çekip onlara özel olarak şunu söyledi: ‹‹Şimdi Yeruşalime gidiyoruz. İnsanoğlu, başkâhinlerin ve din bilginlerinin eline teslim edilecek, onlar da Onu ölüm cezasına çarptıracaklar."));
trsuperobject.biblemap.put("b40_20_19", new trsuperobject(40,20,19,"Onunla alay etmeleri, kamçılayıp çarmıha germeleri için Onu öteki uluslara teslim edecekler. Ne var ki O, üçüncü gün dirilecek.››"));
trsuperobject.biblemap.put("b40_20_20", new trsuperobject(40,20,20,"O sırada Zebedi oğullarının annesi oğullarıyla birlikte İsaya yaklaştı. Önünde yere kapanarak kendisinden bir dileği olduğunu söyledi."));
trsuperobject.biblemap.put("b40_20_21", new trsuperobject(40,20,21,"İsa kadına, ‹‹Ne istiyorsun?›› diye sordu. Kadın, ‹‹Buyruk ver, senin egemenliğinde bu iki oğlumdan biri sağında, biri solunda otursun›› dedi."));
trsuperobject.biblemap.put("b40_20_22", new trsuperobject(40,20,22,"‹‹Siz ne dilediğinizi bilmiyorsunuz›› diye karşılık verdi İsa. ‹‹Benim içeceğim kâseden siz içebilir misiniz?›› ‹‹Evet, içebiliriz›› dediler."));
trsuperobject.biblemap.put("b40_20_23", new trsuperobject(40,20,23,"İsa onlara, ‹‹Elbette benim kâsemden içeceksiniz›› dedi, ‹‹Ama sağımda ya da solumda oturmanıza izin vermek benim elimde değil. Babam bu yerleri belirli kişiler için hazırlamıştır.››"));
trsuperobject.biblemap.put("b40_20_24", new trsuperobject(40,20,24,"Bunu işiten on öğrenci iki kardeşe kızdılar."));
trsuperobject.biblemap.put("b40_20_25", new trsuperobject(40,20,25,"Ama İsa onları yanına çağırıp şöyle dedi: ‹‹Bilirsiniz ki, ulusların önderleri onlara egemen kesilir, ileri gelenleri de ağırlıklarını hissettirirler."));
trsuperobject.biblemap.put("b40_20_26", new trsuperobject(40,20,26,"Sizin aranızda böyle olmayacak. Aranızda büyük olmak isteyen, ötekilerin hizmetkârı olsun."));
trsuperobject.biblemap.put("b40_20_27", new trsuperobject(40,20,27,"Aranızda birinci olmak isteyen, ötekilerin kulu olsun."));
trsuperobject.biblemap.put("b40_20_28", new trsuperobject(40,20,28,"Nitekim İnsanoğlu, hizmet edilmeye değil, hizmet etmeye ve canını birçokları için fidye olarak vermeye geldi.››"));
trsuperobject.biblemap.put("b40_20_29", new trsuperobject(40,20,29,"Erihadan ayrılırlarken büyük bir kalabalık İsanın ardından gitti."));
trsuperobject.biblemap.put("b40_20_30", new trsuperobject(40,20,30,"Yol kenarında oturan iki kör, İsanın oradan geçmekte olduğunu duyunca, ‹‹Ya Rab, ey Davut Oğlu, halimize acı!›› diye bağırdılar."));
trsuperobject.biblemap.put("b40_20_31", new trsuperobject(40,20,31,"Kalabalık onları azarlayarak susturmak istediyse de onlar, ‹‹Ya Rab, ey Davut Oğlu, halimize acı!›› diyerek daha çok bağırdılar."));
trsuperobject.biblemap.put("b40_20_32", new trsuperobject(40,20,32,"İsa durup onları çağırdı. ‹‹Sizin için ne yapmamı istiyorsunuz?›› diye sordu."));
trsuperobject.biblemap.put("b40_20_33", new trsuperobject(40,20,33,"Onlar da, ‹‹Ya Rab, gözlerimiz açılsın›› dediler."));
trsuperobject.biblemap.put("b40_20_34", new trsuperobject(40,20,34,"İsa onlara acıdı, gözlerine dokundu. O anda yeniden görmeye başladılar ve O'nun ardından gittiler."));
trsuperobject.biblemap.put("b40_21_1-2", new trsuperobject(40,21,1-2,"Yeruşalime yaklaşıp Zeytin Dağının yamacındaki Beytfaci Köyüne geldiklerinde İsa, iki öğrencisini önden gönderdi. Onlara, ‹‹Karşınızdaki köye gidin›› dedi, ‹‹Hemen orada bağlı bir dişi eşek ve yanında bir sıpa bulacaksınız. Onları çözüp bana getirin."));
trsuperobject.biblemap.put("b40_21_3", new trsuperobject(40,21,3,"Size bir şey diyen olursa, ‹Rabbin bunlara ihtiyacı var, hemen geri gönderecek› dersiniz.››"));
trsuperobject.biblemap.put("b40_21_4", new trsuperobject(40,21,4,"Bu olay, peygamber aracılığıyla bildirilen şu söz yerine gelsin diye oldu:"));
trsuperobject.biblemap.put("b40_21_5", new trsuperobject(40,21,5,"‹‹Siyon kızına deyin ki,  ‹İşte, alçakgönüllü Kralın,  Eşeğe, evet sıpaya,  Eşek yavrusuna binmiş  Sana geliyor.››"));
trsuperobject.biblemap.put("b40_21_6", new trsuperobject(40,21,6,"Öğrenciler gidip İsanın kendilerine buyurduğu gibi yaptılar."));
trsuperobject.biblemap.put("b40_21_7", new trsuperobject(40,21,7,"Eşekle sıpayı getirip üzerlerine giysilerini yaydılar, İsa sıpaya bindi."));
trsuperobject.biblemap.put("b40_21_8", new trsuperobject(40,21,8,"Halkın büyük bir bölümü giysilerini yolun üzerine serdi. Bazıları da ağaçlardan dal kesip yola seriyordu."));
trsuperobject.biblemap.put("b40_21_9", new trsuperobject(40,21,9,"Önden giden ve arkadan gelen kalabalıklar şöyle bağırıyorlardı:  ‹‹Davut Oğluna hozana!  Rabbin adıyla gelene övgüler olsun,  En yücelerde hozana!››"));
trsuperobject.biblemap.put("b40_21_10", new trsuperobject(40,21,10,"İsa Yeruşalime girdiği zaman bütün kent, ‹‹Bu kimdir?›› diyerek çalkandı."));
trsuperobject.biblemap.put("b40_21_11", new trsuperobject(40,21,11,"Kalabalıklar, ‹‹Bu, Celilenin Nasıra Kentinden Peygamber İsadır›› diyordu."));
trsuperobject.biblemap.put("b40_21_12", new trsuperobject(40,21,12,"İsa, tapınağın avlusuna girerek oradaki bütün alıcı ve satıcıları dışarı kovdu. Para bozanların masalarını, güvercin satanların sehpalarını devirdi."));
trsuperobject.biblemap.put("b40_21_13", new trsuperobject(40,21,13,"Onlara şöyle dedi: ‹‹Evime dua evi denecek› diye yazılmıştır. Ama siz onu haydut inine çevirdiniz!››"));
trsuperobject.biblemap.put("b40_21_14", new trsuperobject(40,21,14,"İsa tapınaktayken kendisine gelen kör ve kötürümleri iyileştirdi."));
trsuperobject.biblemap.put("b40_21_15", new trsuperobject(40,21,15,"Ne var ki, başkâhinlerle din bilginleri, Onun yarattığı harikaları ve tapınakta, ‹‹Davut Oğluna hozana!›› diye bağıran çocukları görünce öfkelendiler."));
trsuperobject.biblemap.put("b40_21_16", new trsuperobject(40,21,16,"İsaya, ‹‹Bunların ne söylediğini duyuyor musun?›› diye sordular. ‹‹Duyuyorum›› dedi İsa. ‹‹Siz şu sözü hiç okumadınız mı? ‹Küçük çocukların ve emziktekilerin dudaklarından kendine övgüler döktürdün.››"));
trsuperobject.biblemap.put("b40_21_17", new trsuperobject(40,21,17,"İsa onları bırakıp kentten çıktı. Beytanyaya dönüp geceyi orada geçirdi."));
trsuperobject.biblemap.put("b40_21_18", new trsuperobject(40,21,18,"İsa sabah erkenden kente dönerken acıkmıştı."));
trsuperobject.biblemap.put("b40_21_19", new trsuperobject(40,21,19,"Yol kenarında gördüğü bir incir ağacına yaklaştı. Ağaçta yapraktan başka bir şey bulamayınca ağaca, ‹‹Artık sonsuza dek sende meyve yetişmesin!›› dedi. İncir ağacı o anda kurudu."));
trsuperobject.biblemap.put("b40_21_20", new trsuperobject(40,21,20,"Öğrenciler bunu görünce şaşkına döndüler. ‹‹İncir ağacı birdenbire nasıl kurudu?›› diye sordular."));
trsuperobject.biblemap.put("b40_21_21", new trsuperobject(40,21,21,"İsa onlara şu karşılığı verdi: ‹‹Size doğrusunu söyleyeyim, eğer imanınız olur da kuşku duymazsanız, yalnız incir ağacına olanı yapmakla kalmazsınız; şu dağa, ‹Kalk, denize atıl› derseniz, dediğiniz olacaktır."));
trsuperobject.biblemap.put("b40_21_22", new trsuperobject(40,21,22,"İmanla dua ederseniz, dilediğiniz her şeyi alırsınız.››"));
trsuperobject.biblemap.put("b40_21_23", new trsuperobject(40,21,23,"İsa tapınağa girmiş öğretiyordu. Bu sırada başkâhinler ve halkın ileri gelenleri Onun yanına gelerek, ‹‹Bunları hangi yetkiyle yapıyorsun, bu yetkiyi sana kim verdi?›› diye sordular."));
trsuperobject.biblemap.put("b40_21_24", new trsuperobject(40,21,24,"İsa onlara şu karşılığı verdi: ‹‹Ben de size bir soru soracağım. Bana yanıt verirseniz, ben de size bunları hangi yetkiyle yaptığımı söylerim."));
trsuperobject.biblemap.put("b40_21_25", new trsuperobject(40,21,25,"Yahyanın vaftiz etme yetkisi nereden geldi, Tanrıdan mı, insanlardan mı?›› Bunu aralarında şöyle tartışmaya başladılar: ‹‹Tanrıdan› dersek, bize, ‹Öyleyse ona niçin inanmadınız?› diyecek."));
trsuperobject.biblemap.put("b40_21_26", new trsuperobject(40,21,26,"Yok eğer ‹İnsanlardan› dersek... Halkın tepkisinden korkuyoruz. Çünkü herkes Yahyayı peygamber sayıyor.››"));
trsuperobject.biblemap.put("b40_21_27", new trsuperobject(40,21,27,"İsaya, ‹‹Bilmiyoruz›› diye yanıt verdiler. İsa, ‹‹Ben de size bunları hangi yetkiyle yaptığımı söylemeyeceğim›› dedi."));
trsuperobject.biblemap.put("b40_21_28", new trsuperobject(40,21,28,"‹‹Ama şuna ne dersiniz? Bir adamın iki oğlu vardı. Adam birincisine gidip, ‹Oğlum, git bugün bağda çalış› dedi."));
trsuperobject.biblemap.put("b40_21_29", new trsuperobject(40,21,29,"‹‹Oğlu, ‹Gitmem!› dedi. Ama sonra pişman olup gitti."));
trsuperobject.biblemap.put("b40_21_30", new trsuperobject(40,21,30,"‹‹Adam ikinci oğluna gidip aynı şeyi söyledi. O, ‹Olur, efendim› dedi, ama gitmedi."));
trsuperobject.biblemap.put("b40_21_31", new trsuperobject(40,21,31,"‹‹İkisinden hangisi babasının isteğini yerine getirmiş oldu?›› ‹‹Birincisi›› diye karşılık verdiler. İsa da onlara, ‹‹Size doğrusunu söyleyeyim, vergi görevlileriyle fahişeler, Tanrının Egemenliğine sizden önce giriyorlar›› dedi."));
trsuperobject.biblemap.put("b40_21_32", new trsuperobject(40,21,32,"‹‹Yahya size doğruluk yolunu göstermeye geldi, ona inanmadınız. Oysa vergi görevlileriyle fahişeler ona inandılar. Siz bunu gördükten sonra bile pişman olup ona inanmadınız.››"));
trsuperobject.biblemap.put("b40_21_33", new trsuperobject(40,21,33,"‹‹Bir benzetme daha dinleyin: Toprak sahibi bir adam, bağ dikti, çevresini çitle çevirdi, üzüm sıkma çukuru kazdı, bir de bekçi kulesi yaptı. Sonra bağı bağcılara kiralayıp yolculuğa çıktı."));
trsuperobject.biblemap.put("b40_21_34", new trsuperobject(40,21,34,"Bağbozumu yaklaşınca, üründen kendisine düşeni almaları için kölelerini bağcılara yolladı."));
trsuperobject.biblemap.put("b40_21_35", new trsuperobject(40,21,35,"Bağcılar adamın kölelerini yakaladı, birini dövdü, birini öldürdü, ötekini de taşladı."));
trsuperobject.biblemap.put("b40_21_36", new trsuperobject(40,21,36,"Bağ sahibi bu kez ilkinden daha çok sayıda köle yolladı. Bağcılar bunlara da aynı şeyi yaptılar."));
trsuperobject.biblemap.put("b40_21_37", new trsuperobject(40,21,37,"Sonunda bağ sahibi, ‹Oğlumu sayarlar› diyerek bağcılara onu yolladı."));
trsuperobject.biblemap.put("b40_21_38", new trsuperobject(40,21,38,"‹‹Ama bağcılar adamın oğlunu görünce birbirlerine, ‹Mirasçı bu; gelin, onu öldürüp mirasına konalım› dediler."));
trsuperobject.biblemap.put("b40_21_39", new trsuperobject(40,21,39,"Böylece onu yakaladılar, bağdan atıp öldürdüler."));
trsuperobject.biblemap.put("b40_21_40", new trsuperobject(40,21,40,"Bu durumda bağın sahibi geldiği zaman bağcılara ne yapacak?››"));
trsuperobject.biblemap.put("b40_21_41", new trsuperobject(40,21,41,"İsaya şu karşılığı verdiler: ‹‹Bu korkunç adamları korkunç bir şekilde yok edecek; bağı da, ürününü kendisine zamanında verecek olan başka bağcılara kiralayacak.››"));
trsuperobject.biblemap.put("b40_21_42", new trsuperobject(40,21,42,"İsa onlara şunu sordu: ‹‹Kutsal Yazılarda şu sözleri hiç okumadınız mı?  ‹Yapıcıların reddettiği taş,  İşte köşenin baş taşı oldu.  Rabbin işidir bu,  Gözümüzde harika bir iş!›"));
trsuperobject.biblemap.put("b40_21_43", new trsuperobject(40,21,43,"‹‹Bu nedenle size şunu söyleyeyim, Tanrının Egemenliği sizden alınacak ve bunun ürünlerini yetiştiren bir ulusa verilecek."));
trsuperobject.biblemap.put("b40_21_44", new trsuperobject(40,21,44,"‹‹Bu taşın üzerine düşen, paramparça olacak; taş da kimin üzerine düşerse, onu ezip toz edecek.››"));
trsuperobject.biblemap.put("b40_21_45", new trsuperobject(40,21,45,"Başkâhinler ve Ferisiler, İsanın anlattığı benzetmeleri duyunca bunları kendileri için söylediğini anladılar."));
trsuperobject.biblemap.put("b40_21_46", new trsuperobject(40,21,46,"O'nu tutuklamak istedilerse de, halkın tepkisinden korktular. Çünkü halk, O'nu peygamber sayıyordu."));
trsuperobject.biblemap.put("b40_22_1-2", new trsuperobject(40,22,1-2,"İsa söz alıp onlara yine benzetmelerle şöyle seslendi: ‹‹Göklerin Egemenliği, oğlu için düğün şöleni hazırlayan bir krala benzer."));
trsuperobject.biblemap.put("b40_22_3", new trsuperobject(40,22,3,"Kral şölene davet ettiklerini çağırmak üzere kölelerini gönderdi, ama davetliler gelmek istemedi."));
trsuperobject.biblemap.put("b40_22_4", new trsuperobject(40,22,4,"‹‹Kral yine başka kölelerini gönderirken onlara dedi ki, ‹Davetlilere şunu söyleyin: Bakın, ben ziyafetimi hazırladım. Sığırlarım, besili hayvanlarım kesildi. Her şey hazır, buyrun şölene!›"));
trsuperobject.biblemap.put("b40_22_5", new trsuperobject(40,22,5,"‹‹Ama davetliler aldırmadılar. Biri tarlasına, biri ticaretine gitti."));
trsuperobject.biblemap.put("b40_22_6", new trsuperobject(40,22,6,"Öbürleri de kralın kölelerini yakalayıp hırpaladılar ve öldürdüler."));
trsuperobject.biblemap.put("b40_22_7", new trsuperobject(40,22,7,"Kral öfkelendi. Ordularını gönderip o katilleri yok etti, kentlerini ateşe verdi."));
trsuperobject.biblemap.put("b40_22_8", new trsuperobject(40,22,8,"‹‹Sonra kölelerine şöyle dedi: ‹Düğün şöleni hazır, ama çağırdıklarım buna layık değilmiş."));
trsuperobject.biblemap.put("b40_22_9", new trsuperobject(40,22,9,"Gidin yol kavşaklarına, kimi bulursanız düğüne çağırın.›"));
trsuperobject.biblemap.put("b40_22_10", new trsuperobject(40,22,10,"Böylece köleler yollara döküldü, iyi kötü kimi buldularsa, hepsini topladılar. Düğün yeri konuklarla doldu."));
trsuperobject.biblemap.put("b40_22_11", new trsuperobject(40,22,11,"‹‹Kral konukları görmeye geldiğinde, orada düğün giysisi giymemiş bir adam gördü."));
trsuperobject.biblemap.put("b40_22_12", new trsuperobject(40,22,12,"Ona, ‹Arkadaş, düğün giysisi giymeden buraya nasıl girdin?› diye sorunca, adamın dili tutuldu."));
trsuperobject.biblemap.put("b40_22_13", new trsuperobject(40,22,13,"‹‹O zaman kral, uşaklarına, ‹Şunun ellerini ayaklarını bağlayın, dışarıya, karanlığa atın!› dedi. ‹Orada ağlayış ve diş gıcırtısı olacaktır.›"));
trsuperobject.biblemap.put("b40_22_14", new trsuperobject(40,22,14,"‹‹Çünkü çağrılanlar çok, ama seçilenler azdır.››"));
trsuperobject.biblemap.put("b40_22_15", new trsuperobject(40,22,15,"Bunun üzerine Ferisiler çıkıp gittiler. İsayı, kendi söyleyeceği sözlerle tuzağa düşürmek amacıyla düzen kurdular."));
trsuperobject.biblemap.put("b40_22_16", new trsuperobject(40,22,16,"Hirodes yanlılarıyla birlikte gönderdikleri kendi öğrencileri İsaya gelip, ‹‹Öğretmenimiz›› dediler, ‹‹Senin dürüst biri olduğunu, Tanrı yolunu dürüstçe öğrettiğini, kimseyi kayırmadığını biliyoruz. Çünkü insanlar arasında ayrım yapmazsın."));
trsuperobject.biblemap.put("b40_22_17", new trsuperobject(40,22,17,"Peki, söyle bize, sence Sezara vergi vermek Kutsal Yasaya uygun mu, değil mi?››"));
trsuperobject.biblemap.put("b40_22_18", new trsuperobject(40,22,18,"İsa onların kötü niyetlerini bildiğinden, ‹‹Ey ikiyüzlüler!›› dedi. ‹‹Beni neden deniyorsunuz?"));
trsuperobject.biblemap.put("b40_22_19", new trsuperobject(40,22,19,"Vergi öderken kullandığınız parayı gösterin bana!›› Ona bir dinar getirdiler."));
trsuperobject.biblemap.put("b40_22_20", new trsuperobject(40,22,20,"İsa, ‹‹Bu resim, bu yazı kimin?›› diye sordu."));
trsuperobject.biblemap.put("b40_22_21", new trsuperobject(40,22,21,"‹‹Sezarın›› dediler. O zaman İsa, ‹‹Öyleyse Sezarın hakkını Sezara, Tanrının hakkını Tanrıya verin›› dedi."));
trsuperobject.biblemap.put("b40_22_22", new trsuperobject(40,22,22,"Bu sözleri duyunca şaştılar, İsayı bırakıp gittiler."));
trsuperobject.biblemap.put("b40_22_23-24", new trsuperobject(40,22,23-24,"Ölümden sonra diriliş olmadığını söyleyen Sadukiler, aynı gün İsaya gelip şunu sordular: ‹‹Öğretmenimiz, Musa şöyle buyurmuştur: ‹Eğer bir adam çocuk sahibi olmadan ölürse, kardeşi onun karısını alsın, soyunu sürdürsün.›"));
trsuperobject.biblemap.put("b40_22_25", new trsuperobject(40,22,25,"Aramızda yedi kardeş vardı. İlki evlendi ve öldü. Çocuğu olmadığından karısını kardeşine bıraktı."));
trsuperobject.biblemap.put("b40_22_26", new trsuperobject(40,22,26,"İkincisi, üçüncüsü, yedincisine kadar hepsine aynı şey oldu."));
trsuperobject.biblemap.put("b40_22_27", new trsuperobject(40,22,27,"Hepsinden sonra kadın da öldü."));
trsuperobject.biblemap.put("b40_22_28", new trsuperobject(40,22,28,"Buna göre diriliş günü kadın bu yedi kardeşten hangisinin karısı olacak? Çünkü hepsi de onunla evlendi.››"));
trsuperobject.biblemap.put("b40_22_29", new trsuperobject(40,22,29,"İsa onlara, ‹‹Siz Kutsal Yazıları ve Tanrının gücünü bilmediğiniz için yanılıyorsunuz›› diye karşılık verdi."));
trsuperobject.biblemap.put("b40_22_30", new trsuperobject(40,22,30,"‹‹Dirilişten sonra insanlar ne evlenir, ne de evlendirilir, gökteki melekler gibidirler."));
trsuperobject.biblemap.put("b40_22_31", new trsuperobject(40,22,31,"Ölülerin dirilmesi konusuna gelince, Tanrının size bildirdiği şu sözü okumadınız mı?"));
trsuperobject.biblemap.put("b40_22_32", new trsuperobject(40,22,32,"‹Ben İbrahimin Tanrısı, İshakın Tanrısı ve Yakupun Tanrısıyım› diyor. Tanrı ölülerin değil, dirilerin Tanrısıdır.››"));
trsuperobject.biblemap.put("b40_22_33", new trsuperobject(40,22,33,"Bunları işiten halk, Onun öğretişine şaşıp kaldı."));
trsuperobject.biblemap.put("b40_22_34", new trsuperobject(40,22,34,"Ferisiler, İsanın Sadukileri susturduğunu duyunca bir araya toplandılar."));
trsuperobject.biblemap.put("b40_22_35-36", new trsuperobject(40,22,35-36,"Onlardan biri, bir Kutsal Yasa uzmanı, İsayı denemek amacıyla Ona şunu sordu: ‹‹Öğretmenim, Kutsal Yasada en önemli buyruk hangisidir?››"));
trsuperobject.biblemap.put("b40_22_37", new trsuperobject(40,22,37,"İsa ona şu karşılığı verdi: ‹‹Tanrın Rabbi bütün yüreğinle, bütün canınla ve bütün aklınla seveceksin.›"));
trsuperobject.biblemap.put("b40_22_38", new trsuperobject(40,22,38,"İşte ilk ve en önemli buyruk budur."));
trsuperobject.biblemap.put("b40_22_39", new trsuperobject(40,22,39,"İlkine benzeyen ikinci buyruk da şudur: ‹Komşunu kendin gibi seveceksin.›"));
trsuperobject.biblemap.put("b40_22_40", new trsuperobject(40,22,40,"Kutsal Yasanın tümü ve peygamberlerin sözleri bu iki buyruğa dayanır.››"));
trsuperobject.biblemap.put("b40_22_41-42", new trsuperobject(40,22,41-42,"Ferisiler toplu haldeyken İsa onlara şunu sordu: ‹‹Mesihle ilgili olarak ne düşünüyorsunuz? O kimin oğludur?›› Onlar da, ‹‹Davutun Oğlu›› dediler."));
trsuperobject.biblemap.put("b40_22_43", new trsuperobject(40,22,43,"İsa şöyle dedi: ‹‹O halde nasıl oluyor da Davut, Ruhtan esinlenerek Ondan ‹Rab› diye söz ediyor? Şöyle diyor Davut:"));
trsuperobject.biblemap.put("b40_22_44", new trsuperobject(40,22,44,"‹Rab Rabbime dedi ki,  Ben düşmanlarını  Ayaklarının altına serinceye dek  Sağımda otur.›"));
trsuperobject.biblemap.put("b40_22_45", new trsuperobject(40,22,45,"Davut Ondan Rab diye söz ettiğine göre, O nasıl Davutun Oğlu olur?››"));
trsuperobject.biblemap.put("b40_22_46", new trsuperobject(40,22,46,"İsa'ya hiç kimse karşılık veremedi. O günden sonra artık kimse de O'na bir şey sormaya cesaret edemedi."));
trsuperobject.biblemap.put("b40_23_1-2", new trsuperobject(40,23,1-2,"Bundan sonra İsa halka ve öğrencilerine şöyle seslendi: ‹‹Din bilginleri ve Ferisiler Musanın kürsüsünde otururlar."));
trsuperobject.biblemap.put("b40_23_3", new trsuperobject(40,23,3,"Bu nedenle size söylediklerinin tümünü yapın ve yerine getirin, ama onların yaptıklarını yapmayın. Çünkü söyledikleri şeyleri kendileri yapmazlar."));
trsuperobject.biblemap.put("b40_23_4", new trsuperobject(40,23,4,"Ağır ve taşınması güç yükleri bağlayıp başkalarının sırtına yüklerler, kendileriyse bu yükleri taşımak için parmaklarını bile oynatmak istemezler."));
trsuperobject.biblemap.put("b40_23_5", new trsuperobject(40,23,5,"‹‹Yaptıklarının tümünü gösteriş için yaparlar. Örneğin, hamaillerini büyük, giysilerinin püsküllerini uzun yaparlar."));
trsuperobject.biblemap.put("b40_23_6", new trsuperobject(40,23,6,"Şölenlerde başköşeye, havralarda en seçkin yerlere kurulmaya bayılırlar."));
trsuperobject.biblemap.put("b40_23_7", new trsuperobject(40,23,7,"Meydanlarda selamlanmaktan ve insanların kendilerini ‹Rabbî› diye çağırmalarından zevk duyarlar."));
trsuperobject.biblemap.put("b40_23_8", new trsuperobject(40,23,8,"‹‹Kimse sizi ‹Rabbî› diye çağırmasın. Çünkü sizin tek öğretmeniniz var ve hepiniz kardeşsiniz."));
trsuperobject.biblemap.put("b40_23_9", new trsuperobject(40,23,9,"Yeryüzünde kimseye ‹Baba› demeyin. Çünkü tek Babanız var, O da göksel Babadır."));
trsuperobject.biblemap.put("b40_23_10", new trsuperobject(40,23,10,"Kimse sizi ‹Önder› diye çağırmasın. Çünkü tek önderiniz var, O da Mesihtir."));
trsuperobject.biblemap.put("b40_23_11", new trsuperobject(40,23,11,"Aranızda en üstün olan, ötekilerin hizmetkârı olsun."));
trsuperobject.biblemap.put("b40_23_12", new trsuperobject(40,23,12,"Kendini yücelten alçaltılacak, kendini alçaltan yüceltilecektir."));
trsuperobject.biblemap.put("b40_23_13-14", new trsuperobject(40,23,13-14,"‹‹Vay halinize ey din bilginleri ve Ferisiler, ikiyüzlüler! Göklerin Egemenliğinin kapısını insanların yüzüne kapıyorsunuz; ne kendiniz içeri giriyor, ne de girmek isteyenleri bırakıyorsunuz!"));
trsuperobject.biblemap.put("b40_23_15", new trsuperobject(40,23,15,"‹‹Vay halinize ey din bilginleri ve Ferisiler, ikiyüzlüler! Tek bir kişiyi dininize döndürmek için denizleri, kıtaları dolaşırsınız. Dininize döneni de kendinizden iki kat cehennemlik yaparsınız."));
trsuperobject.biblemap.put("b40_23_16", new trsuperobject(40,23,16,"‹‹Vay halinize kör kılavuzlar! Diyorsunuz ki, ‹Tapınak üzerine ant içenin andı sayılmaz, ama tapınaktaki altın üzerine ant içen, andını yerine getirmek zorundadır.›"));
trsuperobject.biblemap.put("b40_23_17", new trsuperobject(40,23,17,"Budalalar, körler! Hangisi daha önemli, altın mı, altını kutsal kılan tapınak mı?"));
trsuperobject.biblemap.put("b40_23_18", new trsuperobject(40,23,18,"Yine diyorsunuz ki, ‹Sunak üzerine ant içenin andı sayılmaz, ama sunaktaki adağın üzerine ant içen, andını yerine getirmek zorundadır.›"));
trsuperobject.biblemap.put("b40_23_19", new trsuperobject(40,23,19,"Ey körler! Hangisi daha önemli, adak mı, adağı kutsal kılan sunak mı?"));
trsuperobject.biblemap.put("b40_23_20", new trsuperobject(40,23,20,"Öyleyse sunak üzerine ant içen, hem sunağın hem de sunaktaki her şeyin üzerine ant içmiş olur."));
trsuperobject.biblemap.put("b40_23_21", new trsuperobject(40,23,21,"Tapınak üzerine ant içen de hem tapınak, hem de tapınakta yaşayan Tanrı üzerine ant içmiş olur."));
trsuperobject.biblemap.put("b40_23_22", new trsuperobject(40,23,22,"Gök üzerine ant içen, Tanrının tahtı ve tahtta oturanın üzerine ant içmiş olur."));
trsuperobject.biblemap.put("b40_23_23", new trsuperobject(40,23,23,"‹‹Vay halinize ey din bilginleri ve Ferisiler, ikiyüzlüler! Siz nanenin, dereotunun ve kimyonun ondalığını verirsiniz de, Kutsal Yasanın daha önemli konularını -adaleti, merhameti, sadakati- ihmal edersiniz. Ondalık vermeyi ihmal etmeden asıl bunları yerine getirmeniz gerekirdi."));
trsuperobject.biblemap.put("b40_23_24", new trsuperobject(40,23,24,"Ey kör kılavuzlar! Küçük sineği süzer ayırır, ama deveyi yutarsınız!"));
trsuperobject.biblemap.put("b40_23_25", new trsuperobject(40,23,25,"‹‹Vay halinize ey din bilginleri ve Ferisiler, ikiyüzlüler! Bardağın ve çanağın dışını temizlersiniz, oysa bunların içi açgözlülük ve taşkınlıkla doludur."));
trsuperobject.biblemap.put("b40_23_26", new trsuperobject(40,23,26,"Ey kör Ferisi! Sen önce bardağın ve çanağın içini temizle ki, dıştan da temiz olsunlar."));
trsuperobject.biblemap.put("b40_23_27", new trsuperobject(40,23,27,"‹‹Vay halinize ey din bilginleri ve Ferisiler, ikiyüzlüler! Siz dıştan güzel görünen, ama içi ölü kemikleri ve her türlü pislikle dolu badanalı mezarlara benzersiniz."));
trsuperobject.biblemap.put("b40_23_28", new trsuperobject(40,23,28,"Dıştan insanlara doğru görünürsünüz, ama içte ikiyüzlülük ve kötülükle dolusunuz."));
trsuperobject.biblemap.put("b40_23_29", new trsuperobject(40,23,29,"‹‹Vay halinize ey din bilginleri ve Ferisiler, ikiyüzlüler! Peygamberlerin mezarlarını yapar, doğru kişilerin anıtlarını donatırsınız."));
trsuperobject.biblemap.put("b40_23_30", new trsuperobject(40,23,30,"‹Atalarımızın yaşadığı günlerde yaşasaydık, onlarla birlikte peygamberlerin kanına girmezdik› diyorsunuz."));
trsuperobject.biblemap.put("b40_23_31", new trsuperobject(40,23,31,"Böylece, peygamberleri öldürenlerin torunları olduğunuza kendiniz tanıklık ediyorsunuz."));
trsuperobject.biblemap.put("b40_23_32", new trsuperobject(40,23,32,"Haydi, atalarınızın başlattığı işi bitirin!"));
trsuperobject.biblemap.put("b40_23_33", new trsuperobject(40,23,33,"‹‹Sizi yılanlar, engerekler soyu! Cehennem cezasından nasıl kaçacaksınız?"));
trsuperobject.biblemap.put("b40_23_34", new trsuperobject(40,23,34,"İşte bunun için size peygamberler, bilge kişiler ve din bilginleri gönderiyorum. Bunlardan kimini öldürecek, çarmıha gereceksiniz. Kimini havralarınızda kamçılayacak, kentten kente kovalayacaksınız."));
trsuperobject.biblemap.put("b40_23_35", new trsuperobject(40,23,35,"Böylelikle, doğru kişi olan Habilin kanından, tapınakla sunak arasında öldürdüğünüz Berekya oğlu Zekeriyanın kanına kadar, yeryüzünde akıtılan her doğru kişinin kanından sorumlu tutulacaksınız."));
trsuperobject.biblemap.put("b40_23_36", new trsuperobject(40,23,36,"Size doğrusunu söyleyeyim, bunların hepsinden bu kuşak sorumlu tutulacaktır."));
trsuperobject.biblemap.put("b40_23_37", new trsuperobject(40,23,37,"‹‹Ey Yeruşalim! Peygamberleri öldüren, kendisine gönderilenleri taşlayan Yeruşalim! Tavuğun civcivlerini kanatları altına topladığı gibi ben de kaç kez senin çocuklarını toplamak istedim, ama siz istemediniz."));
trsuperobject.biblemap.put("b40_23_38", new trsuperobject(40,23,38,"Bakın, eviniz ıssız bırakılacak!"));
trsuperobject.biblemap.put("b40_23_39", new trsuperobject(40,23,39,"Size şunu söyleyeyim: ‹Rab'bin adıyla gelene övgüler olsun!› diyeceğiniz zamana dek beni bir daha görmeyeceksiniz.››"));
trsuperobject.biblemap.put("b40_24_1", new trsuperobject(40,24,1,"İsa tapınaktan çıkıp giderken, öğrencileri, tapınağın binalarını Ona göstermek için yanına geldiler."));
trsuperobject.biblemap.put("b40_24_2", new trsuperobject(40,24,2,"İsa onlara, ‹‹Bütün bunları görüyor musunuz?›› dedi. ‹‹Size doğrusunu söyleyeyim, burada taş üstünde taş kalmayacak, hepsi yıkılacak!››"));
trsuperobject.biblemap.put("b40_24_3", new trsuperobject(40,24,3,"İsa, Zeytin Dağında otururken öğrencileri yalnız olarak yanına geldiler. ‹‹Söyle bize›› dediler, ‹‹Bu dediklerin ne zaman olacak, senin gelişini ve çağın bitimini gösteren belirti ne olacak?››"));
trsuperobject.biblemap.put("b40_24_4", new trsuperobject(40,24,4,"İsa onlara şu karşılığı verdi: ‹‹Sakın kimse sizi saptırmasın!"));
trsuperobject.biblemap.put("b40_24_5", new trsuperobject(40,24,5,"Birçokları, ‹Mesih benim› diyerek benim adımla gelip birçok kişiyi aldatacaklar."));
trsuperobject.biblemap.put("b40_24_6", new trsuperobject(40,24,6,"Savaş gürültüleri, savaş haberleri duyacaksınız. Sakın korkmayın! Bunların olması gerek, ama bu daha son demek değildir."));
trsuperobject.biblemap.put("b40_24_7", new trsuperobject(40,24,7,"Ulus ulusa, devlet devlete savaş açacak; yer yer kıtlıklar, depremler olacak."));
trsuperobject.biblemap.put("b40_24_8", new trsuperobject(40,24,8,"Bütün bunlar, doğum sancılarının başlangıcıdır."));
trsuperobject.biblemap.put("b40_24_9", new trsuperobject(40,24,9,"‹‹O zaman sizi sıkıntıya sokacak, öldürecekler. Benim adımdan ötürü bütün uluslar sizden nefret edecek."));
trsuperobject.biblemap.put("b40_24_10", new trsuperobject(40,24,10,"O zaman birçok kişi imandan sapacak, birbirlerini ele verecek ve birbirlerinden nefret edecekler."));
trsuperobject.biblemap.put("b40_24_11", new trsuperobject(40,24,11,"Birçok sahte peygamber türeyecek ve bunlar birçok kişiyi saptıracak."));
trsuperobject.biblemap.put("b40_24_12", new trsuperobject(40,24,12,"Kötülüklerin çoğalmasından ötürü birçoklarının sevgisi soğuyacak."));
trsuperobject.biblemap.put("b40_24_13", new trsuperobject(40,24,13,"Ama sonuna kadar dayanan kurtulacaktır."));
trsuperobject.biblemap.put("b40_24_14", new trsuperobject(40,24,14,"Göksel egemenliğin bu Müjdesi bütün uluslara tanıklık olmak üzere dünyanın her yerinde duyurulacak. İşte o zaman son gelecektir."));
trsuperobject.biblemap.put("b40_24_15-16", new trsuperobject(40,24,15-16,"‹‹Peygamber Danielin sözünü ettiği yıkıcı iğrenç şeyin kutsal yerde dikildiğini gördüğünüz zaman -okuyan anlasın- Yahudiyede bulunanlar dağlara kaçsın."));
trsuperobject.biblemap.put("b40_24_17", new trsuperobject(40,24,17,"Damda olan, evindeki eşyalarını almak için aşağı inmesin."));
trsuperobject.biblemap.put("b40_24_18", new trsuperobject(40,24,18,"Tarlada olan, abasını almak için geri dönmesin."));
trsuperobject.biblemap.put("b40_24_19", new trsuperobject(40,24,19,"O günlerde gebe olan, çocuk emziren kadınların vay haline!"));
trsuperobject.biblemap.put("b40_24_20", new trsuperobject(40,24,20,"Dua edin ki, kaçışınız kışa ya da Şabat Gününe rastlamasın."));
trsuperobject.biblemap.put("b40_24_21", new trsuperobject(40,24,21,"Çünkü o günlerde öyle korkunç bir sıkıntı olacak ki, dünyanın başlangıcından bu yana böylesi olmamış, bundan sonra da olmayacaktır."));
trsuperobject.biblemap.put("b40_24_22", new trsuperobject(40,24,22,"O günler kısaltılmamış olsaydı, hiç kimse kurtulamazdı. Ama seçilmiş olanlar uğruna o günler kısaltılacak."));
trsuperobject.biblemap.put("b40_24_23", new trsuperobject(40,24,23,"Eğer o zaman biri size, ‹İşte Mesih burada›, ya da ‹İşte şurada› derse, inanmayın."));
trsuperobject.biblemap.put("b40_24_24", new trsuperobject(40,24,24,"Çünkü sahte mesihler, sahte peygamberler türeyecek; bunlar büyük belirtiler ve harikalar yapacaklar. Öyle ki, ellerinden gelse, seçilmiş olanları bile saptıracaklar."));
trsuperobject.biblemap.put("b40_24_25", new trsuperobject(40,24,25,"İşte size önceden söylüyorum."));
trsuperobject.biblemap.put("b40_24_26", new trsuperobject(40,24,26,"‹‹Bunun için size, ‹İşte Mesih çölde› derlerse gitmeyin. ‹Bakın, iç odalarda› derlerse inanmayın."));
trsuperobject.biblemap.put("b40_24_27", new trsuperobject(40,24,27,"Çünkü İnsanoğlunun gelişi, doğuda çakıp batıya kadar her taraftan görülen şimşek gibi olacaktır."));
trsuperobject.biblemap.put("b40_24_28", new trsuperobject(40,24,28,"‹‹Leş neredeyse, akbabalar oraya üşüşecek."));
trsuperobject.biblemap.put("b40_24_29", new trsuperobject(40,24,29,"‹‹O günlerin sıkıntısından hemen sonra,  ‹Güneş kararacak,  Ay ışık vermez olacak,  Yıldızlar gökten düşecek,  Göksel güçler sarsılacak.›"));
trsuperobject.biblemap.put("b40_24_30", new trsuperobject(40,24,30,"‹‹O zaman İnsanoğlunun belirtisi gökte görünecek. Yeryüzündeki bütün halklar ağlayıp dövünecek, İnsanoğlunun gökteki bulutlar üzerinde büyük güç ve görkemle geldiğini görecekler."));
trsuperobject.biblemap.put("b40_24_31", new trsuperobject(40,24,31,"Kendisi güçlü bir borazan sesiyle meleklerini gönderecek. Melekler Onun seçtiklerini göğün bir ucundan öbür ucuna dek, dünyanın dört bucağından toplayacaklar."));
trsuperobject.biblemap.put("b40_24_32", new trsuperobject(40,24,32,"‹‹İncir ağacından ders alın! Dalları filizlenip yaprakları sürünce, yaz mevsiminin yakın olduğunu anlarsınız."));
trsuperobject.biblemap.put("b40_24_33", new trsuperobject(40,24,33,"Aynı şekilde, bütün bunların gerçekleştiğini gördüğünüzde bilin ki, İnsanoğlu yakındır, kapıdadır."));
trsuperobject.biblemap.put("b40_24_34", new trsuperobject(40,24,34,"Size doğrusunu söyleyeyim, bütün bunlar olmadan bu kuşak ortadan kalkmayacak."));
trsuperobject.biblemap.put("b40_24_35", new trsuperobject(40,24,35,"Yer ve gök ortadan kalkacak, ama benim sözlerim asla ortadan kalkmayacaktır.››"));
trsuperobject.biblemap.put("b40_24_36", new trsuperobject(40,24,36,"‹‹O günü ve saati, ne gökteki melekler, ne de Oğul bilir; Babadan başka kimse bilmez."));
trsuperobject.biblemap.put("b40_24_37", new trsuperobject(40,24,37,"Nuhun günlerinde nasıl olduysa, İnsanoğlunun gelişinde de öyle olacak."));
trsuperobject.biblemap.put("b40_24_38", new trsuperobject(40,24,38,"Nuhun gemiye bindiği güne dek, tufandan önceki günlerde insanlar yiyip içiyor, evlenip evlendiriliyorlardı."));
trsuperobject.biblemap.put("b40_24_39", new trsuperobject(40,24,39,"Tufan gelinceye, hepsini süpürüp götürünceye dek başlarına geleceklerden habersizdiler. İnsanoğlunun gelişi de öyle olacak."));
trsuperobject.biblemap.put("b40_24_40", new trsuperobject(40,24,40,"O gün tarlada bulunan iki kişiden biri alınacak, biri bırakılacak."));
trsuperobject.biblemap.put("b40_24_41", new trsuperobject(40,24,41,"Değirmende buğday öğüten iki kadından biri alınacak, biri bırakılacak."));
trsuperobject.biblemap.put("b40_24_42", new trsuperobject(40,24,42,"‹‹Bunun için uyanık kalın. Çünkü Rabbinizin geleceği günü bilemezsiniz."));
trsuperobject.biblemap.put("b40_24_43", new trsuperobject(40,24,43,"Ama şunu bilin ki, ev sahibi, hırsızın gece hangi saatte geleceğini bilse, uyanık kalır, evinin soyulmasına fırsat vermez."));
trsuperobject.biblemap.put("b40_24_44", new trsuperobject(40,24,44,"Bunun için siz de hazır olun! Çünkü İnsanoğlu beklemediğiniz saatte gelecektir."));
trsuperobject.biblemap.put("b40_24_45", new trsuperobject(40,24,45,"‹‹Efendinin, hizmetkârlarına vaktinde yiyecek vermek için başlarına atadığı güvenilir ve akıllı köle kimdir?"));
trsuperobject.biblemap.put("b40_24_46", new trsuperobject(40,24,46,"Efendisi eve döndüğünde işinin başında bulacağı o köleye ne mutlu!"));
trsuperobject.biblemap.put("b40_24_47", new trsuperobject(40,24,47,"Size doğrusunu söyleyeyim, efendisi onu bütün malının üzerinde yetkili kılacak."));
trsuperobject.biblemap.put("b40_24_48", new trsuperobject(40,24,48,"Ama o köle kötü olur da içinden, ‹Efendim gecikiyor› der ve öteki köleleri dövmeye başlarsa, sarhoşlarla birlikte yiyip içerse, efendisi, onun beklemediği günde, ummadığı saatte gelecek, onu şiddetle cezalandırıp ikiyüzlülerle bir tutacak. Orada ağlayış ve diş gıcırtısı olacaktır.››"));
trsuperobject.biblemap.put("b40_25_1", new trsuperobject(40,25,1,"‹‹O zaman Göklerin Egemenliği, kandillerini alıp güveyi karşılamaya çıkan on kıza benzeyecek."));
trsuperobject.biblemap.put("b40_25_2", new trsuperobject(40,25,2,"Bunların beşi akıllı, beşi akılsızdı."));
trsuperobject.biblemap.put("b40_25_3", new trsuperobject(40,25,3,"Akılsızlar yanlarına kandillerini aldılar, ama yağ almadılar."));
trsuperobject.biblemap.put("b40_25_4", new trsuperobject(40,25,4,"Akıllılar ise, kandilleriyle birlikte kaplar içinde yağ da aldılar."));
trsuperobject.biblemap.put("b40_25_5", new trsuperobject(40,25,5,"Güvey gecikince hepsini uyku bastı, dalıp uyudular."));
trsuperobject.biblemap.put("b40_25_6", new trsuperobject(40,25,6,"‹‹Gece yarısı bir ses yankılandı: ‹İşte güvey geliyor, onu karşılamaya çıkın!›"));
trsuperobject.biblemap.put("b40_25_7", new trsuperobject(40,25,7,"Bunun üzerine kızların hepsi kalkıp kandillerini tazelediler."));
trsuperobject.biblemap.put("b40_25_8", new trsuperobject(40,25,8,"‹‹Akılsızlar akıllılara, ‹Kandillerimiz sönüyor, bize yağ verin!› dediler."));
trsuperobject.biblemap.put("b40_25_9", new trsuperobject(40,25,9,"‹‹Akıllılar, ‹Olmaz! Hem bize hem size yetmeyebilir. En iyisi satıcılara gidin, kendinize yağ alın› dediler."));
trsuperobject.biblemap.put("b40_25_10", new trsuperobject(40,25,10,"‹‹Ne var ki, onlar yağ satın almaya giderlerken güvey geldi. Hazırlıklı olan kızlar, onunla birlikte düğün şölenine girdiler ve kapı kapandı."));
trsuperobject.biblemap.put("b40_25_11", new trsuperobject(40,25,11,"‹‹Daha sonra gelen öbür kızlar, ‹Efendimiz, efendimiz, aç kapıyı bize!› dediler."));
trsuperobject.biblemap.put("b40_25_12", new trsuperobject(40,25,12,"‹‹Güvey ise, ‹Size doğrusunu söyleyeyim, sizi tanımıyorum› dedi."));
trsuperobject.biblemap.put("b40_25_13", new trsuperobject(40,25,13,"‹‹Bu nedenle uyanık kalın. Çünkü o günü ve o saati bilemezsiniz.››"));
trsuperobject.biblemap.put("b40_25_14", new trsuperobject(40,25,14,"‹‹Göksel egemenlik, yolculuğa çıkan bir adamın kölelerini çağırıp malını onlara emanet etmesine benzer."));
trsuperobject.biblemap.put("b40_25_15", new trsuperobject(40,25,15,"‹‹Adam, her birinin yeteneğine göre, birine beş, birine iki, birine de bir talant vererek yola çıktı."));
trsuperobject.biblemap.put("b40_25_16", new trsuperobject(40,25,16,"Beş talant alan, hemen gidip bu parayı işletti ve beş talant daha kazandı."));
trsuperobject.biblemap.put("b40_25_17", new trsuperobject(40,25,17,"İki talant alan da iki talant daha kazandı."));
trsuperobject.biblemap.put("b40_25_18", new trsuperobject(40,25,18,"Bir talant alan ise gidip toprağı kazdı ve efendisinin parasını sakladı."));
trsuperobject.biblemap.put("b40_25_19", new trsuperobject(40,25,19,"‹‹Uzun zaman sonra bu kölelerin efendisi döndü, onlarla hesaplaşmaya oturdu."));
trsuperobject.biblemap.put("b40_25_20", new trsuperobject(40,25,20,"Beş talant alan gelip beş talant daha getirdi, ‹Efendimiz› dedi, ‹Bana beş talant emanet etmiştin; bak, beş talant daha kazandım.›"));
trsuperobject.biblemap.put("b40_25_21", new trsuperobject(40,25,21,"‹‹Efendisi ona, ‹Aferin, iyi ve güvenilir köle!› dedi. ‹Sen küçük işlerde güvenilir olduğunu gösterdin, ben de seni büyük işlerin başına geçireceğim. Gel, efendinin şenliğine katıl!›"));
trsuperobject.biblemap.put("b40_25_22", new trsuperobject(40,25,22,"‹‹İki talant alan da geldi, ‹Efendimiz› dedi, ‹Bana iki talant emanet etmiştin; bak, iki talant daha kazandım.›"));
trsuperobject.biblemap.put("b40_25_23", new trsuperobject(40,25,23,"‹‹Efendisi ona, ‹Aferin, iyi ve güvenilir köle!› dedi. ‹Sen küçük işlerde güvenilir olduğunu gösterdin, ben de seni büyük işlerin başına geçireceğim. Gel, efendinin şenliğine katıl!›"));
trsuperobject.biblemap.put("b40_25_24", new trsuperobject(40,25,24,"‹‹Sonra bir talant alan geldi, ‹Efendimiz› dedi, ‹Senin sert bir adam olduğunu biliyordum. Ekmediğin yerden biçer, harman savurmadığın yerden devşirirsin."));
trsuperobject.biblemap.put("b40_25_25", new trsuperobject(40,25,25,"Bu nedenle korktum, gidip senin verdiğin talantı toprağa gömdüm. İşte, al paranı!›"));
trsuperobject.biblemap.put("b40_25_26-27", new trsuperobject(40,25,26-27,"‹‹Efendisi ona şu karşılığı verdi: ‹Kötü ve tembel köle! Ekmediğim yerden biçtiğimi, harman savurmadığım yerden devşirdiğimi bildiğine göre paramı faize vermeliydin. Ben de geldiğimde onu faiziyle geri alırdım..."));
trsuperobject.biblemap.put("b40_25_28", new trsuperobject(40,25,28,"Haydi, elindeki talantı alın, on talantı olana verin!"));
trsuperobject.biblemap.put("b40_25_29", new trsuperobject(40,25,29,"Çünkü kimde varsa, ona daha çok verilecek ve o bolluk içinde olacak. Ama kimde yoksa, kendisinde olan da elinden alınacak."));
trsuperobject.biblemap.put("b40_25_30", new trsuperobject(40,25,30,"Şu yararsız köleyi dışarıya, karanlığa atın. Orada ağlayış ve diş gıcırtısı olacaktır.››"));
trsuperobject.biblemap.put("b40_25_31", new trsuperobject(40,25,31,"‹‹İnsanoğlu kendi görkemi içinde bütün melekleriyle birlikte gelince, görkemli tahtına oturacak."));
trsuperobject.biblemap.put("b40_25_32", new trsuperobject(40,25,32,"Ulusların hepsi Onun önünde toplanacak, O da koyunları keçilerden ayıran bir çoban gibi, insanları birbirinden ayıracak."));
trsuperobject.biblemap.put("b40_25_33", new trsuperobject(40,25,33,"Koyunları sağına, keçileri soluna alacak."));
trsuperobject.biblemap.put("b40_25_34", new trsuperobject(40,25,34,"‹‹O zaman Kral, sağındaki kişilere, ‹Sizler, Babamın kutsadıkları, gelin!› diyecek. ‹Dünya kurulduğundan beri sizin için hazırlanmış olan egemenliği miras alın!"));
trsuperobject.biblemap.put("b40_25_35", new trsuperobject(40,25,35,"Çünkü acıkmıştım, bana yiyecek verdiniz; susamıştım, bana içecek verdiniz; yabancıydım, beni içeri aldınız."));
trsuperobject.biblemap.put("b40_25_36", new trsuperobject(40,25,36,"Çıplaktım, beni giydirdiniz; hastaydım, benimle ilgilendiniz; zindandaydım, yanıma geldiniz.›"));
trsuperobject.biblemap.put("b40_25_37", new trsuperobject(40,25,37,"‹‹O vakit doğru kişiler Ona şu karşılığı verecek: ‹Ya Rab, seni ne zaman aç görüp doyurduk, susuz görüp su verdik?"));
trsuperobject.biblemap.put("b40_25_38", new trsuperobject(40,25,38,"Ne zaman seni yabancı görüp içeri aldık, ya da çıplak görüp giydirdik?"));
trsuperobject.biblemap.put("b40_25_39", new trsuperobject(40,25,39,"Seni ne zaman hasta ya da zindanda görüp yanına geldik?›"));
trsuperobject.biblemap.put("b40_25_40", new trsuperobject(40,25,40,"‹‹Kral da onları şöyle yanıtlayacak: ‹Size doğrusunu söyleyeyim, bu en basit kardeşlerimden biri için yaptığınızı, benim için yapmış oldunuz.›"));
trsuperobject.biblemap.put("b40_25_41", new trsuperobject(40,25,41,"‹‹Sonra solundakilere şöyle diyecek: ‹Ey lanetliler, çekilin önümden! İblisle melekleri için hazırlanmış sönmez ateşe gidin!"));
trsuperobject.biblemap.put("b40_25_42-43", new trsuperobject(40,25,42-43,"Çünkü acıkmıştım, bana yiyecek vermediniz; susamıştım, bana içecek vermediniz; yabancıydım, beni içeri almadınız; çıplaktım, beni giydirmediniz; hastaydım, zindandaydım, benimle ilgilenmediniz.›"));
trsuperobject.biblemap.put("b40_25_44", new trsuperobject(40,25,44,"‹‹O vakit onlar da şöyle karşılık verecekler: ‹Ya Rab, seni ne zaman aç, susuz, yabancı, çıplak, hasta ya da zindanda gördük de yardım etmedik?›"));
trsuperobject.biblemap.put("b40_25_45", new trsuperobject(40,25,45,"‹‹Kral da onlara şu yanıtı verecek: ‹Size doğrusunu söyleyeyim, mademki bu en basit kardeşlerimden biri için bunu yapmadınız, benim için de yapmamış oldunuz.›"));
trsuperobject.biblemap.put("b40_25_46", new trsuperobject(40,25,46,"‹‹Bunlar sonsuz azaba, doğrular ise sonsuz yaşama gidecekler.››"));
trsuperobject.biblemap.put("b40_26_1-2", new trsuperobject(40,26,1-2,"İsa bütün bunları anlattıktan sonra öğrencilerine, ‹‹İki gün sonra Fısıh Bayramı olduğunu biliyorsunuz›› dedi, ‹‹İnsanoğlu çarmıha gerilmek üzere ele verilecek.››"));
trsuperobject.biblemap.put("b40_26_3", new trsuperobject(40,26,3,"Bu sırada başkâhinlerle halkın ileri gelenleri, Kayafa adındaki başkâhinin sarayında toplandılar."));
trsuperobject.biblemap.put("b40_26_4", new trsuperobject(40,26,4,"İsayı hileyle tutuklayıp öldürmek için düzen kurdular."));
trsuperobject.biblemap.put("b40_26_5", new trsuperobject(40,26,5,"Ama, ‹‹Bayramda olmasın ki, halk arasında kargaşalık çıkmasın›› diyorlardı."));
trsuperobject.biblemap.put("b40_26_6-7", new trsuperobject(40,26,6-7,"İsa Beytanyada cüzamlı Simunun evindeyken, yanına bir kadın geldi. Kadın kaymaktaşından bir kap içinde çok değerli, güzel kokulu yağ getirmişti. İsa sofrada otururken, kadın yağı Onun başına döktü."));
trsuperobject.biblemap.put("b40_26_8", new trsuperobject(40,26,8,"Öğrenciler bunu görünce kızdılar. ‹‹Nedir bu savurganlık?›› dediler."));
trsuperobject.biblemap.put("b40_26_9", new trsuperobject(40,26,9,"‹‹Bu yağ pahalıya satılabilir, parası yoksullara verilebilirdi.››"));
trsuperobject.biblemap.put("b40_26_10", new trsuperobject(40,26,10,"Söylenenleri farkeden İsa, öğrencilerine, ‹‹Kadını neden üzüyorsunuz?›› dedi. ‹‹Benim için güzel bir şey yaptı."));
trsuperobject.biblemap.put("b40_26_11", new trsuperobject(40,26,11,"Yoksullar her zaman aranızdadır, ama ben her zaman aranızda olmayacağım."));
trsuperobject.biblemap.put("b40_26_12", new trsuperobject(40,26,12,"Kadın bu güzel kokulu yağı, beni gömülmeye hazırlamak için bedenimin üzerine boşalttı."));
trsuperobject.biblemap.put("b40_26_13", new trsuperobject(40,26,13,"Size doğrusunu söyleyeyim, bu Müjde dünyanın neresinde duyurulursa, bu kadının yaptığı da onun anılması için anlatılacak.››"));
trsuperobject.biblemap.put("b40_26_14-15", new trsuperobject(40,26,14-15,"O sırada Onikilerden biri -adı Yahuda İskariot olanı- başkâhinlere giderek, ‹‹Onu ele verirsem bana ne verirsiniz?›› dedi. Otuz gümüş tartıp ona verdiler."));
trsuperobject.biblemap.put("b40_26_16", new trsuperobject(40,26,16,"Yahuda o andan itibaren İsayı ele vermek için fırsat kollamaya başladı."));
trsuperobject.biblemap.put("b40_26_17", new trsuperobject(40,26,17,"Mayasız Ekmek Bayramının ilk günü öğrenciler İsanın yanına gelerek, ‹‹Fısıh yemeğini yemen için nerede hazırlık yapmamızı istersin?›› diye sordular."));
trsuperobject.biblemap.put("b40_26_18", new trsuperobject(40,26,18,"İsa onlara, ‹‹Kente varıp o adamın evine gidin›› dedi. ‹‹Ona şöyle deyin: ‹Öğretmen diyor ki, zamanım yaklaştı. Fısıh Bayramını, öğrencilerimle birlikte senin evinde kutlayacağım.››"));
trsuperobject.biblemap.put("b40_26_19", new trsuperobject(40,26,19,"Öğrenciler, İsanın buyruğunu yerine getirerek Fısıh yemeği için hazırlık yaptılar."));
trsuperobject.biblemap.put("b40_26_20", new trsuperobject(40,26,20,"Akşam olunca İsa on iki öğrencisiyle yemeğe oturdu."));
trsuperobject.biblemap.put("b40_26_21", new trsuperobject(40,26,21,"Yemek yerlerken, ‹‹Size doğrusunu söyleyeyim, sizden biri bana ihanet edecek›› dedi."));
trsuperobject.biblemap.put("b40_26_22", new trsuperobject(40,26,22,"Bu söz onları kedere boğdu. Teker teker, ‹‹Ya Rab, beni demek istemedin ya?›› diye sormaya başladılar."));
trsuperobject.biblemap.put("b40_26_23", new trsuperobject(40,26,23,"O da, ‹‹Bana ihanet edecek olan›› dedi, ‹‹Elindeki ekmeği benimle birlikte sahana batırandır."));
trsuperobject.biblemap.put("b40_26_24", new trsuperobject(40,26,24,"İnsanoğlu, kendisi için yazılmış olduğu gibi gidiyor, ama İnsanoğluna ihanet edenin vay haline! O adam hiç doğmamış olsaydı, kendisi için daha iyi olurdu.››"));
trsuperobject.biblemap.put("b40_26_25", new trsuperobject(40,26,25,"Ona ihanet edecek olan Yahuda, ‹‹Rabbî, yoksa beni mi demek istedin?›› diye sordu. İsa ona, ‹‹Söylediğin gibidir›› karşılığını verdi."));
trsuperobject.biblemap.put("b40_26_26", new trsuperobject(40,26,26,"Yemek sırasında İsa eline ekmek aldı, şükredip ekmeği böldü ve öğrencilerine verdi. ‹‹Alın, yiyin›› dedi, ‹‹Bu benim bedenimdir.››"));
trsuperobject.biblemap.put("b40_26_27", new trsuperobject(40,26,27,"Sonra bir kâse alıp şükretti ve bunu öğrencilerine vererek, ‹‹Hepiniz bundan için›› dedi."));
trsuperobject.biblemap.put("b40_26_28", new trsuperobject(40,26,28,"‹‹Çünkü bu benim kanımdır, günahların bağışlanması için birçokları uğruna akıtılan antlaşma kanıdır."));
trsuperobject.biblemap.put("b40_26_29", new trsuperobject(40,26,29,"Size şunu söyleyeyim, Babamın egemenliğinde sizinle birlikte tazesini içeceğim o güne dek, asmanın bu ürününden bir daha içmeyeceğim.››"));
trsuperobject.biblemap.put("b40_26_30", new trsuperobject(40,26,30,"İlahi söyledikten sonra dışarı çıkıp Zeytin Dağına doğru gittiler."));
trsuperobject.biblemap.put("b40_26_31", new trsuperobject(40,26,31,"Bu arada İsa öğrencilerine, ‹‹Bu gece hepiniz benden ötürü sendeleyip düşeceksiniz›› dedi. ‹‹Çünkü şöyle yazılmıştır:  ‹Çobanı vuracağım,  Sürüdeki koyunlar darmadağın olacak.›"));
trsuperobject.biblemap.put("b40_26_32", new trsuperobject(40,26,32,"Ama ben dirildikten sonra sizden önce Celileye gideceğim.››"));
trsuperobject.biblemap.put("b40_26_33", new trsuperobject(40,26,33,"Petrus Ona, ‹‹Herkes senden ötürü sendeleyip düşse de ben asla düşmem›› dedi."));
trsuperobject.biblemap.put("b40_26_34", new trsuperobject(40,26,34,"‹‹Sana doğrusunu söyleyeyim›› dedi İsa, ‹‹Bu gece horoz ötmeden beni üç kez inkâr edeceksin.››"));
trsuperobject.biblemap.put("b40_26_35", new trsuperobject(40,26,35,"Petrus, ‹‹Seninle birlikte ölmem gerekse bile seni asla inkâr etmem›› dedi. Öğrencilerin hepsi de aynı şeyi söyledi."));
trsuperobject.biblemap.put("b40_26_36", new trsuperobject(40,26,36,"Sonra İsa öğrencileriyle birlikte Getsemani denen yere geldi. Öğrencilerine, ‹‹Ben şuraya gidip dua edeceğim, siz burada oturun›› dedi."));
trsuperobject.biblemap.put("b40_26_37", new trsuperobject(40,26,37,"Petrus ile Zebedinin iki oğlunu yanına aldı. Kederlenmeye, ağır bir sıkıntı duymaya başlamıştı."));
trsuperobject.biblemap.put("b40_26_38", new trsuperobject(40,26,38,"Onlara, ‹‹Ölesiye kederliyim›› dedi. ‹‹Burada kalın, benimle birlikte uyanık durun.››"));
trsuperobject.biblemap.put("b40_26_39", new trsuperobject(40,26,39,"Biraz ilerledi, yüzüstü yere kapanıp dua etmeye başladı. ‹‹Baba›› dedi, ‹‹Mümkünse bu kâse benden uzaklaştırılsın. Yine de benim değil, senin istediğin olsun.››"));
trsuperobject.biblemap.put("b40_26_40", new trsuperobject(40,26,40,"Öğrencilerin yanına döndüğünde onları uyumuş buldu. Petrusa, ‹‹Demek ki benimle birlikte bir saat uyanık kalamadınız!›› dedi."));
trsuperobject.biblemap.put("b40_26_41", new trsuperobject(40,26,41,"‹‹Uyanık durup dua edin ki, ayartılmayasınız. Ruh isteklidir, ama beden güçsüzdür.››"));
trsuperobject.biblemap.put("b40_26_42", new trsuperobject(40,26,42,"İsa ikinci kez uzaklaşıp dua etti. ‹‹Baba›› dedi, ‹‹Eğer ben içmeden bu kâsenin uzaklaştırılması mümkün değilse, senin istediğin olsun.››"));
trsuperobject.biblemap.put("b40_26_43", new trsuperobject(40,26,43,"Geri geldiğinde öğrencilerini yine uyumuş buldu. Onların göz kapaklarına ağırlık çökmüştü."));
trsuperobject.biblemap.put("b40_26_44", new trsuperobject(40,26,44,"Onları bırakıp tekrar uzaklaştı, yine aynı sözlerle üçüncü kez dua etti."));
trsuperobject.biblemap.put("b40_26_45", new trsuperobject(40,26,45,"Sonra öğrencilerin yanına dönerek, ‹‹Hâlâ uyuyor, dinleniyor musunuz?›› dedi. ‹‹İşte saat yaklaştı, İnsanoğlu günahkârların eline veriliyor."));
trsuperobject.biblemap.put("b40_26_46", new trsuperobject(40,26,46,"Kalkın, gidelim. İşte bana ihanet eden geldi!››"));
trsuperobject.biblemap.put("b40_26_47", new trsuperobject(40,26,47,"İsa daha konuşurken, Onikilerden biri olan Yahuda geldi. Yanında, başkâhinlerle halkın ileri gelenleri tarafından gönderilmiş kılıçlı sopalı büyük bir kalabalık vardı."));
trsuperobject.biblemap.put("b40_26_48", new trsuperobject(40,26,48,"İsaya ihanet eden Yahuda, ‹‹Kimi öpersem, İsa Odur, Onu tutuklayın›› diye onlarla sözleşmişti."));
trsuperobject.biblemap.put("b40_26_49", new trsuperobject(40,26,49,"Dosdoğru İsaya gidip, ‹‹Selam, Rabbî!›› diyerek Onu öptü."));
trsuperobject.biblemap.put("b40_26_50", new trsuperobject(40,26,50,"İsa, ‹‹Arkadaş, ne yapacaksan yap!›› dedi. Bunun üzerine adamlar yaklaştı, İsayı yakalayıp tutukladılar."));
trsuperobject.biblemap.put("b40_26_51", new trsuperobject(40,26,51,"İsayla birlikte olanlardan biri, ani bir hareketle kılıcını çekti, başkâhinin kölesine vurup kulağını uçurdu."));
trsuperobject.biblemap.put("b40_26_52", new trsuperobject(40,26,52,"O zaman İsa ona, ‹‹Kılıcını yerine koy!›› dedi. ‹‹Kılıç çekenlerin hepsi kılıçla ölecek."));
trsuperobject.biblemap.put("b40_26_53", new trsuperobject(40,26,53,"Yoksa Babamdan yardım isteyemez miyim sanıyorsun? İstesem, hemen şu an bana on iki tümenden fazla melek gönderir."));
trsuperobject.biblemap.put("b40_26_54", new trsuperobject(40,26,54,"Ama böyle olması gerektiğini bildiren Kutsal Yazılar o zaman nasıl yerine gelir?››"));
trsuperobject.biblemap.put("b40_26_55", new trsuperobject(40,26,55,"Bundan sonra İsa kalabalığa dönüp şöyle seslendi: ‹‹Niçin bir haydutmuşum gibi beni kılıç ve sopalarla yakalamaya geldiniz? Her gün tapınakta oturup öğretiyordum, beni tutuklamadınız."));
trsuperobject.biblemap.put("b40_26_56", new trsuperobject(40,26,56,"Ama bütün bunlar, peygamberlerin yazdıkları yerine gelsin diye oldu.›› O zaman öğrencilerin hepsi Onu bırakıp kaçtı."));
trsuperobject.biblemap.put("b40_26_57", new trsuperobject(40,26,57,"İsayı tutuklayanlar, Onu başkâhin Kayafaya götürdüler. Din bilginleriyle ileri gelenler de orada toplanmışlardı."));
trsuperobject.biblemap.put("b40_26_58", new trsuperobject(40,26,58,"Petrus, İsayı uzaktan, ta başkâhinin avlusuna kadar izledi. Sonucu görmek için içeri girip nöbetçilerin yanına oturdu."));
trsuperobject.biblemap.put("b40_26_59", new trsuperobject(40,26,59,"Başkâhinlerle Yüksek Kurulun öteki üyeleri, İsayı ölüm cezasına çarptırmak için kendisine karşı yalancı tanıklar arıyorlardı."));
trsuperobject.biblemap.put("b40_26_6-061", new trsuperobject(40,26,6-061,"Ortaya birçok yalancı tanık çıktığı halde, aradıklarını bulamadılar. Sonunda ortaya çıkan iki kişi şöyle dedi: ‹‹Bu adam, ‹Ben Tanrının Tapınağını yıkıp üç günde yeniden kurabilirim› dedi.››"));
trsuperobject.biblemap.put("b40_26_62", new trsuperobject(40,26,62,"Başkâhin ayağa kalkıp İsaya, ‹‹Hiç yanıt vermeyecek misin?›› dedi. ‹‹Nedir bunların sana karşı ettiği bu tanıklıklar?››"));
trsuperobject.biblemap.put("b40_26_63", new trsuperobject(40,26,63,"İsa susmaya devam etti. Başkâhin ise Ona, ‹‹Yaşayan Tanrı adına ant içmeni buyuruyorum, söyle bize, Tanrının Oğlu Mesih sen misin?›› dedi."));
trsuperobject.biblemap.put("b40_26_64", new trsuperobject(40,26,64,"İsa, ‹‹Söylediğin gibidir›› karşılığını verdi. ‹‹Üstelik size şunu söyleyeyim, bundan sonra İnsanoğlunun, Kudretli Olanın sağında oturduğunu ve göğün bulutları üzerinde geldiğini göreceksiniz.››"));
trsuperobject.biblemap.put("b40_26_65", new trsuperobject(40,26,65,"Bunun üzerine başkâhin giysilerini yırtarak, ‹‹Tanrıya küfretti!›› dedi. ‹‹Artık tanıklara ne ihtiyacımız var? İşte küfürü işittiniz."));
trsuperobject.biblemap.put("b40_26_66", new trsuperobject(40,26,66,"Buna ne diyorsunuz?›› ‹‹Ölümü hak etti!›› diye karşılık verdiler."));
trsuperobject.biblemap.put("b40_26_67-68", new trsuperobject(40,26,67-68,"Bunun üzerine İsanın yüzüne tükürüp Onu yumrukladılar. Bazıları da Onu tokatlayıp, ‹‹Ey Mesih, peygamberliğini göster bakalım, sana vuran kim?›› dediler."));
trsuperobject.biblemap.put("b40_26_69", new trsuperobject(40,26,69,"Petrus ise dışarıda, avluda oturuyordu. Bir hizmetçi kız yanına gelip, ‹‹Sen de Celileli İsayla birlikteydin›› dedi."));
trsuperobject.biblemap.put("b40_26_70", new trsuperobject(40,26,70,"Ama Petrus bunu herkesin önünde inkâr ederek, ‹‹Neden söz ettiğini anlamıyorum›› dedi."));
trsuperobject.biblemap.put("b40_26_71", new trsuperobject(40,26,71,"Sonra avlu kapısının önüne çıktı. Onu gören başka bir hizmetçi kız orada bulunanlara, ‹‹Bu adam Nasıralı İsayla birlikteydi›› dedi."));
trsuperobject.biblemap.put("b40_26_72", new trsuperobject(40,26,72,"Petrus ant içerek, ‹‹Ben o adamı tanımıyorum›› diye yine inkâr etti."));
trsuperobject.biblemap.put("b40_26_73", new trsuperobject(40,26,73,"Orada duranlar az sonra Petrusa yaklaşıp, ‹‹Gerçekten sen de onlardansın. Konuşman seni ele veriyor›› dediler."));
trsuperobject.biblemap.put("b40_26_74", new trsuperobject(40,26,74,"Petrus kendine lanet okuyup ant içerek, ‹‹O adamı tanımıyorum!›› dedi. Tam o anda horoz öttü."));
trsuperobject.biblemap.put("b40_26_75", new trsuperobject(40,26,75,"Petrus, İsa'nın, ‹‹Horoz ötmeden beni üç kez inkâr edeceksin›› dediğini hatırladı ve dışarı çıkıp acı acı ağladı."));
trsuperobject.biblemap.put("b40_27_1", new trsuperobject(40,27,1,"Sabah olunca bütün başkâhinlerle halkın ileri gelenleri, İsayı ölüm cezasına çarptırmak konusunda anlaştılar."));
trsuperobject.biblemap.put("b40_27_2", new trsuperobject(40,27,2,"Onu bağladılar ve götürüp Vali Pilatusa teslim ettiler."));
trsuperobject.biblemap.put("b40_27_3", new trsuperobject(40,27,3,"İsaya ihanet eden Yahuda, Onun mahkûm edildiğini görünce yaptığına pişman oldu. Otuz gümüşü başkâhinlere ve ileri gelenlere geri götürdü."));
trsuperobject.biblemap.put("b40_27_4", new trsuperobject(40,27,4,"‹‹Ben suçsuz birini ele vermekle günah işledim›› dedi. Onlar ise, ‹‹Bundan bize ne? Onu sen düşün›› dediler."));
trsuperobject.biblemap.put("b40_27_5", new trsuperobject(40,27,5,"Yahuda paraları tapınağın içine fırlatarak oradan ayrıldı, gidip kendini astı."));
trsuperobject.biblemap.put("b40_27_6", new trsuperobject(40,27,6,"Paraları toplayan başkâhinler, ‹‹Kan bedeli olan bu paraları tapınağın hazinesine koymak doğru olmaz›› dediler."));
trsuperobject.biblemap.put("b40_27_7", new trsuperobject(40,27,7,"Kendi aralarında anlaşarak bu parayla yabancılar için mezarlık yapmak üzere Çömlekçi Tarlasını satın aldılar."));
trsuperobject.biblemap.put("b40_27_8", new trsuperobject(40,27,8,"Bunun için bu tarlaya bugüne dek ‹‹Kan Tarlası›› denilmiştir."));
trsuperobject.biblemap.put("b40_27_9-10", new trsuperobject(40,27,9-10,"Böylece Peygamber Yeremya aracılığıyla bildirilen şu söz yerine gelmiş oldu:  ‹‹İsrailoğullarından kimilerinin  Ona biçtikleri değerin karşılığı olan  Otuz gümüşü aldılar;  Rabbin bana buyurduğu gibi,  Çömlekçi Tarlasını satın almak için harcadılar.››"));
trsuperobject.biblemap.put("b40_27_11", new trsuperobject(40,27,11,"İsa valinin önüne çıkarıldı. Vali Ona, ‹‹Sen Yahudilerin Kralı mısın?›› diye sordu. İsa, ‹‹Söylediğin gibidir›› dedi."));
trsuperobject.biblemap.put("b40_27_12", new trsuperobject(40,27,12,"Başkâhinlerle ileri gelenler Onu suçlayınca hiç karşılık vermedi."));
trsuperobject.biblemap.put("b40_27_13", new trsuperobject(40,27,13,"Pilatus Ona, ‹‹Senin aleyhinde yaptıkları bunca tanıklığı duymuyor musun?›› dedi."));
trsuperobject.biblemap.put("b40_27_14", new trsuperobject(40,27,14,"İsa tek konuda bile ona yanıt vermedi. Vali buna çok şaştı."));
trsuperobject.biblemap.put("b40_27_15", new trsuperobject(40,27,15,"Her Fısıh Bayramında vali, halkın istediği bir tutukluyu salıvermeyi adet edinmişti."));
trsuperobject.biblemap.put("b40_27_16", new trsuperobject(40,27,16,"O günlerde Barabba adında ünlü bir tutuklu vardı."));
trsuperobject.biblemap.put("b40_27_17", new trsuperobject(40,27,17,"Halk bir araya toplandığında, Pilatus onlara, ‹‹Sizin için kimi salıvermemi istersiniz, Barabbayı mı, Mesih denen İsayı mı?›› diye sordu."));
trsuperobject.biblemap.put("b40_27_18", new trsuperobject(40,27,18,"İsayı kıskançlıktan ötürü kendisine teslim ettiklerini biliyordu."));
trsuperobject.biblemap.put("b40_27_19", new trsuperobject(40,27,19,"Pilatus yargı kürsüsünde otururken karısı ona, ‹‹O doğru adama dokunma. Dün gece rüyamda Onun yüzünden çok sıkıntı çektim›› diye haber gönderdi."));
trsuperobject.biblemap.put("b40_27_20", new trsuperobject(40,27,20,"Başkâhinler ve ileri gelenler ise, Barabbanın salıverilmesini ve İsanın öldürülmesini istesinler diye halkı kışkırttılar."));
trsuperobject.biblemap.put("b40_27_21", new trsuperobject(40,27,21,"Vali onlara şunu sordu: ‹‹Sizin için hangisini salıvermemi istersiniz?›› ‹‹Barabbayı›› dediler."));
trsuperobject.biblemap.put("b40_27_22", new trsuperobject(40,27,22,"Pilatus, ‹‹Öyleyse Mesih denen İsayı ne yapayım?›› diye sordu. Hep bir ağızdan, ‹‹Çarmıha gerilsin!›› dediler."));
trsuperobject.biblemap.put("b40_27_23", new trsuperobject(40,27,23,"Pilatus, ‹‹O ne kötülük yaptı ki?›› diye sordu. Onlar ise daha yüksek sesle, ‹‹Çarmıha gerilsin!›› diye bağrışıp durdular."));
trsuperobject.biblemap.put("b40_27_24", new trsuperobject(40,27,24,"Pilatus, elinden bir şey gelmediğini, tersine, bir kargaşalığın başladığını görünce su aldı, kalabalığın önünde ellerini yıkayıp şöyle dedi: ‹‹Bu adamın kanından ben sorumlu değilim. Bu işe siz bakın!››"));
trsuperobject.biblemap.put("b40_27_25", new trsuperobject(40,27,25,"Bütün halk şu karşılığı verdi: ‹‹Onun kanının sorumluluğu bizim ve çocuklarımızın üzerinde olsun!››"));
trsuperobject.biblemap.put("b40_27_26", new trsuperobject(40,27,26,"Bunun üzerine Pilatus onlar için Barabbayı salıverdi. İsayı ise kamçılattıktan sonra çarmıha gerilmek üzere askerlere teslim etti."));
trsuperobject.biblemap.put("b40_27_27", new trsuperobject(40,27,27,"Sonra valinin askerleri İsayı vali konağına götürüp bütün taburu başına topladılar."));
trsuperobject.biblemap.put("b40_27_28", new trsuperobject(40,27,28,"Onu soyup üzerine kırmızı bir kaftan geçirdiler."));
trsuperobject.biblemap.put("b40_27_29", new trsuperobject(40,27,29,"Dikenlerden bir taç örüp başına koydular, sağ eline de bir kamış tutturdular. Önünde diz çöküp, ‹‹Selam, ey Yahudilerin Kralı!›› diyerek Onunla alay ettiler."));
trsuperobject.biblemap.put("b40_27_30", new trsuperobject(40,27,30,"Üzerine tükürdüler, kamışı alıp başına vurdular."));
trsuperobject.biblemap.put("b40_27_31", new trsuperobject(40,27,31,"Onunla böyle alay ettikten sonra kaftanı üzerinden çıkarıp kendi giysilerini giydirdiler ve çarmıha germeye götürdüler."));
trsuperobject.biblemap.put("b40_27_32", new trsuperobject(40,27,32,"Dışarı çıktıklarında Simun adında Kireneli bir adama rastladılar. İsanın çarmıhını ona zorla taşıttılar."));
trsuperobject.biblemap.put("b40_27_33-34", new trsuperobject(40,27,33-34,"Golgota, yani Kafatası denilen yere vardıklarında içmesi için İsaya ödle karışık şarap verdiler. İsa bunu tadınca içmek istemedi."));
trsuperobject.biblemap.put("b40_27_35", new trsuperobject(40,27,35,"Askerler Onu çarmıha gerdikten sonra kura çekerek giysilerini aralarında paylaştılar."));
trsuperobject.biblemap.put("b40_27_36", new trsuperobject(40,27,36,"Sonra oturup yanında nöbet tuttular."));
trsuperobject.biblemap.put("b40_27_37", new trsuperobject(40,27,37,"Başının üzerine,"));
trsuperobject.biblemap.put("b40_27_38", new trsuperobject(40,27,38,"İsayla birlikte, biri sağında öbürü solunda olmak üzere iki haydut da çarmıha gerildi."));
trsuperobject.biblemap.put("b40_27_39-40", new trsuperobject(40,27,39-40,"Oradan geçenler başlarını sallayıp İsaya sövüyor, ‹‹Hani sen tapınağı yıkıp üç günde yeniden kuracaktın? Haydi, kurtar kendini! Tanrının Oğluysan çarmıhtan in!›› diyorlardı."));
trsuperobject.biblemap.put("b40_27_41-42", new trsuperobject(40,27,41-42,"Başkâhinler, din bilginleri ve ileri gelenler de aynı şekilde Onunla alay ederek, ‹‹Başkalarını kurtardı, kendini kurtaramıyor›› diyorlardı. ‹‹İsrailin Kralı imiş! Şimdi çarmıhtan aşağı insin de Ona iman edelim."));
trsuperobject.biblemap.put("b40_27_43", new trsuperobject(40,27,43,"Tanrıya güveniyordu; Tanrı Onu seviyorsa, kurtarsın bakalım! Çünkü, ‹Ben Tanrının Oğluyum› demişti.››"));
trsuperobject.biblemap.put("b40_27_44", new trsuperobject(40,27,44,"İsayla birlikte çarmıha gerilen haydutlar da Ona aynı şekilde hakaret ettiler."));
trsuperobject.biblemap.put("b40_27_45", new trsuperobject(40,27,45,"Öğleyin on ikiden üçe kadar bütün ülkenin üzerine karanlık çöktü."));
trsuperobject.biblemap.put("b40_27_46", new trsuperobject(40,27,46,"Saat üçe doğru İsa yüksek sesle, ‹‹Eli, Eli, lema şevaktani?›› yani, ‹‹Tanrım, Tanrım, beni neden terk ettin?›› diye bağırdı."));
trsuperobject.biblemap.put("b40_27_47", new trsuperobject(40,27,47,"Orada duranlardan bazıları bunu işitince, ‹‹Bu adam İlyası çağırıyor›› dediler."));
trsuperobject.biblemap.put("b40_27_48", new trsuperobject(40,27,48,"İçlerinden biri hemen koşup bir sünger getirdi, ekşi şaraba batırıp bir kamışın ucuna takarak İsaya içirdi."));
trsuperobject.biblemap.put("b40_27_49", new trsuperobject(40,27,49,"Öbürleri ise, ‹‹Dur bakalım, İlyas gelip Onu kurtaracak mı?›› dediler."));
trsuperobject.biblemap.put("b40_27_50", new trsuperobject(40,27,50,"İsa, yüksek sesle bir kez daha bağırdı ve ruhunu teslim etti."));
trsuperobject.biblemap.put("b40_27_51", new trsuperobject(40,27,51,"O anda tapınaktaki perde yukarıdan aşağıya yırtılarak ikiye bölündü. Yer sarsıldı, kayalar yarıldı."));
trsuperobject.biblemap.put("b40_27_52", new trsuperobject(40,27,52,"Mezarlar açıldı, ölmüş olan birçok kutsal kişinin cesetleri dirildi."));
trsuperobject.biblemap.put("b40_27_53", new trsuperobject(40,27,53,"Bunlar mezarlarından çıkıp İsanın dirilişinden sonra kutsal kente girdiler ve birçok kimseye göründüler."));
trsuperobject.biblemap.put("b40_27_54", new trsuperobject(40,27,54,"İsayı bekleyen yüzbaşı ve beraberindeki askerler, depremi ve öbür olayları görünce dehşete kapıldılar, ‹‹Bu gerçekten Tanrının Oğluydu!›› dediler."));
trsuperobject.biblemap.put("b40_27_55", new trsuperobject(40,27,55,"Orada, olup bitenleri uzaktan izleyen birçok kadın vardı. Bunlar, Celileden İsanın ardından gelip Ona hizmet etmişlerdi."));
trsuperobject.biblemap.put("b40_27_56", new trsuperobject(40,27,56,"Aralarında Mecdelli Meryem, Yakup ile Yusufun annesi Meryem ve Zebedi oğullarının annesi de vardı."));
trsuperobject.biblemap.put("b40_27_57", new trsuperobject(40,27,57,"Akşama doğru Yusuf adında zengin bir Aramatyalı geldi. O da İsanın bir öğrencisiydi."));
trsuperobject.biblemap.put("b40_27_58", new trsuperobject(40,27,58,"Pilatusa gidip İsanın cesedini istedi. Pilatus da cesedin ona verilmesini buyurdu."));
trsuperobject.biblemap.put("b40_27_59-60", new trsuperobject(40,27,59-60,"Yusuf cesedi aldı, temiz keten beze sardı, kayaya oydurduğu kendi yeni mezarına yatırdı. Mezarın girişine büyük bir taş yuvarlayıp oradan ayrıldı."));
trsuperobject.biblemap.put("b40_27_61", new trsuperobject(40,27,61,"Mecdelli Meryem ile öteki Meryem ise orada, mezarın karşısında oturuyorlardı."));
trsuperobject.biblemap.put("b40_27_62-63", new trsuperobject(40,27,62-63,"Ertesi gün, yani Hazırlık Gününden sonraki gün, başkâhinlerle Ferisiler Pilatusun önünde toplanarak, ‹‹Efendimiz›› dediler, ‹‹O aldatıcının, daha yaşarken, ‹Ben öldükten üç gün sonra dirileceğim› dediğini hatırlıyoruz."));
trsuperobject.biblemap.put("b40_27_64", new trsuperobject(40,27,64,"Onun için buyruk ver de üçüncü güne dek mezarı güvenlik altına alsınlar. Yoksa öğrencileri gelir, cesedini çalar ve halka, ‹Ölümden dirildi› derler. Son aldatmaca ilkinden beter olur.››"));
trsuperobject.biblemap.put("b40_27_65", new trsuperobject(40,27,65,"Pilatus onlara, ‹‹Yanınıza asker alın, gidip mezarı dilediğiniz gibi güvenlik altına alın›› dedi."));
trsuperobject.biblemap.put("b40_27_66", new trsuperobject(40,27,66,"Onlar da askerlerle birlikte gittiler, taşı mühürleyip mezarı güvenlik altına aldılar."));
trsuperobject.biblemap.put("b40_28_1", new trsuperobject(40,28,1,"Şabat Gününü izleyen haftanın ilk günü, tan yeri ağarırken, Mecdelli Meryem ile öbür Meryem mezarı görmeye gittiler."));
trsuperobject.biblemap.put("b40_28_2", new trsuperobject(40,28,2,"Ansızın büyük bir deprem oldu. Rabbin bir meleği gökten indi ve mezara gidip taşı bir yana yuvarlayarak üzerine oturdu."));
trsuperobject.biblemap.put("b40_28_3", new trsuperobject(40,28,3,"Görünüşü şimşek gibi, giysileri ise kar gibi bembeyazdı."));
trsuperobject.biblemap.put("b40_28_4", new trsuperobject(40,28,4,"Nöbetçiler korkudan titremeye başladılar, sonra ölü gibi yere yıkıldılar."));
trsuperobject.biblemap.put("b40_28_5", new trsuperobject(40,28,5,"Melek kadınlara şöyle seslendi: ‹‹Korkmayın! Çarmıha gerilen İsayı aradığınızı biliyorum."));
trsuperobject.biblemap.put("b40_28_6", new trsuperobject(40,28,6,"O burada yok; söylemiş olduğu gibi dirildi. Gelin, Onun yattığı yeri görün."));
trsuperobject.biblemap.put("b40_28_7", new trsuperobject(40,28,7,"Çabuk gidin, öğrencilerine şöyle deyin: ‹İsa ölümden dirildi. Sizden önce Celileye gidiyor, kendisini orada göreceksiniz.› İşte ben size söylemiş bulunuyorum.››"));
trsuperobject.biblemap.put("b40_28_8", new trsuperobject(40,28,8,"Kadınlar korku ve büyük sevinç içinde hemen mezardan uzaklaştılar; koşarak İsanın öğrencilerine haber vermeye gittiler."));
trsuperobject.biblemap.put("b40_28_9", new trsuperobject(40,28,9,"İsa ansızın karşılarına çıktı, ‹‹Selam!›› dedi. Yaklaşıp İsanın ayaklarına sarılarak Ona tapındılar."));
trsuperobject.biblemap.put("b40_28_10", new trsuperobject(40,28,10,"O zaman İsa, ‹‹Korkmayın!›› dedi. ‹‹Gidip kardeşlerime haber verin, Celileye gitsinler, beni orada görecekler.››"));
trsuperobject.biblemap.put("b40_28_11", new trsuperobject(40,28,11,"Kadınlar daha yoldayken nöbetçi askerlerden bazıları kente giderek olup bitenleri başkâhinlere bildirdiler."));
trsuperobject.biblemap.put("b40_28_12-13", new trsuperobject(40,28,12-13,"Başkâhinler ileri gelenlerle birlikte toplanıp birbirlerine danıştıktan sonra askerlere yüklü para vererek dediler ki, ‹‹Siz şöyle diyeceksiniz: ‹Öğrencileri geceleyin geldi, biz uyurken Onun cesedini çalıp götürdüler.›"));
trsuperobject.biblemap.put("b40_28_14", new trsuperobject(40,28,14,"Eğer bu haber valinin kulağına gidecek olursa biz onu yatıştırır, size bir zarar gelmesini önleriz.››"));
trsuperobject.biblemap.put("b40_28_15", new trsuperobject(40,28,15,"Böylece askerler parayı aldılar ve kendilerine söylendiği gibi yaptılar. Bu söylenti Yahudiler arasında bugün de yaygındır."));
trsuperobject.biblemap.put("b40_28_16", new trsuperobject(40,28,16,"On bir öğrenci Celileye, İsanın kendilerine bildirdiği dağa gittiler."));
trsuperobject.biblemap.put("b40_28_17", new trsuperobject(40,28,17,"İsayı gördükleri zaman Ona tapındılar. Ama bazıları kuşku içindeydi."));
trsuperobject.biblemap.put("b40_28_18", new trsuperobject(40,28,18,"İsa yanlarına gelip kendilerine şunları söyledi: ‹‹Gökte ve yeryüzünde bütün yetki bana verildi."));
trsuperobject.biblemap.put("b40_28_19", new trsuperobject(40,28,19,"Bu nedenle gidin, bütün ulusları öğrencilerim olarak yetiştirin; onları Baba, Oğul ve Kutsal Ruhun adıyla vaftiz edin;"));
trsuperobject.biblemap.put("b40_28_20", new trsuperobject(40,28,20,"size buyurduğum her şeye uymayı onlara öğretin. İşte ben, dünyanın sonuna dek her an sizinle birlikteyim.››"));

