trsuperobject.biblemap.put("b41_1_6", new trsuperobject(41,1,6,"Yahyanın deve tüyünden giysisi, belinde deri kuşağı vardı. Çekirge ve yaban balı yerdi."));
trsuperobject.biblemap.put("b41_1_7", new trsuperobject(41,1,7,"Şu haberi yayıyordu: ‹‹Benden sonra benden daha güçlü olan geliyor. Eğilip Onun çarıklarının bağını çözmeye bile layık değilim."));
