trsuperobject.biblemap.put("b41_1_9", new trsuperobject(41,1,9,"O günlerde Celilenin Nasıra Kentinden çıkıp gelen İsa, Yahya tarafından Şeria Irmağında vaftiz edildi."));
trsuperobject.biblemap.put("b41_1_10", new trsuperobject(41,1,10,"Tam sudan çıkarken, göklerin yarıldığını ve Ruhun güvercin gibi üzerine indiğini gördü."));
