trsuperobject.biblemap.put("b41_1_13", new trsuperobject(41,1,13,"İsa çölde kaldığı kırk gün boyunca Şeytan tarafından denendi. Yabanıl hayvanlar arasındaydı, melekler Ona hizmet ediyordu."));
trsuperobject.biblemap.put("b41_1_14", new trsuperobject(41,1,14,"Yahyanın tutuklanmasından sonra İsa, Tanrının Müjdesini duyura duyura Celileye gitti."));
trsuperobject.biblemap.put("b41_1_15", new trsuperobject(41,1,15,"‹‹Zaman doldu›› diyordu, ‹‹Tanrının Egemenliği yaklaştı. Tövbe edin, Müjdeye inanın!››"));
trsuperobject.biblemap.put("b41_1_16", new trsuperobject(41,1,16,"İsa, Celile Gölünün kıyısından geçerken, göle ağ atmakta olan Simun ile kardeşi Andreası gördü. Bu adamlar balıkçıydı."));
trsuperobject.biblemap.put("b41_1_17", new trsuperobject(41,1,17,"İsa onlara, ‹‹Ardımdan gelin›› dedi, ‹‹Sizleri insan tutan balıkçılar yapacağım.››"));
