trsuperobject.biblemap.put("b41_1_19", new trsuperobject(41,1,19,"İsa biraz ileri gidince Zebedinin oğulları Yakupla Yuhannayı gördü. Teknede ağlarını onarıyorlardı."));
trsuperobject.biblemap.put("b41_1_20", new trsuperobject(41,1,20,"Hemen onları çağırdı. Onlar da babaları Zebediyi işçilerle birlikte teknede bırakıp İsanın ardından gittiler."));
