trsuperobject.biblemap.put("b41_1_22", new trsuperobject(41,1,22,"Halk Onun öğretişine şaşıp kaldı. Çünkü onlara din bilginleri gibi değil, yetkili biri gibi öğretiyordu."));
trsuperobject.biblemap.put("b41_1_23-24", new trsuperobject(41,1,23-24,"Tam o sırada havrada bulunan ve kötü ruha tutulmuş bir adam, ‹‹Ey Nasıralı İsa, bizden ne istiyorsun?›› diye bağırdı. ‹‹Bizi mahvetmeye mi geldin? Senin kim olduğunu biliyorum, Tanrının Kutsalısın sen!››"));
trsuperobject.biblemap.put("b41_1_25", new trsuperobject(41,1,25,"İsa, ‹‹Sus, çık adamdan!›› diyerek kötü ruhu azarladı."));
trsuperobject.biblemap.put("b41_1_26", new trsuperobject(41,1,26,"Kötü ruh adamı sarstı ve büyük bir çığlık atarak içinden çıktı."));
trsuperobject.biblemap.put("b41_1_27", new trsuperobject(41,1,27,"Herkes şaşıp kaldı. Birbirlerine, ‹‹Bu nasıl şey?›› diye sormaya başladılar. ‹‹Yepyeni bir öğreti! Kötü ruhlara bile yetkiyle buyruk veriyor, onlar da sözünü dinliyor.››"));
trsuperobject.biblemap.put("b41_1_28", new trsuperobject(41,1,28,"Böylece İsayla ilgili haber, Celile bölgesinin her yerine hızla yayıldı."));
trsuperobject.biblemap.put("b41_1_29", new trsuperobject(41,1,29,"İsa havradan çıkar çıkmaz, Yakup ve Yuhanna ile birlikte Simun ve Andreasın evine gitti."));
trsuperobject.biblemap.put("b41_1_30", new trsuperobject(41,1,30,"Simunun kaynanası ateşler içinde yatıyordu. Durumu hemen İsaya bildirdiler."));
trsuperobject.biblemap.put("b41_1_31", new trsuperobject(41,1,31,"O da hastaya yaklaştı, elinden tutup kaldırdı. Kadının ateşi düştü, onlara hizmet etmeye başladı."));
trsuperobject.biblemap.put("b41_1_32", new trsuperobject(41,1,32,"Akşam olup güneş batınca, bütün hastaları ve cinlileri İsaya getirdiler."));
trsuperobject.biblemap.put("b41_1_33", new trsuperobject(41,1,33,"Bütün kent halkı kapıya toplanmıştı."));
trsuperobject.biblemap.put("b41_1_34", new trsuperobject(41,1,34,"İsa, çeşitli hastalıklara yakalanmış birçok kişiyi iyileştirdi, birçok cini kovdu. Cinlerin konuşmasına izin vermiyordu. Çünkü onlar kendisinin kim olduğunu biliyorlardı."));
trsuperobject.biblemap.put("b41_1_35", new trsuperobject(41,1,35,"Sabah çok erkenden, ortalık henüz ağarmadan İsa kalktı, evden çıkıp ıssız bir yere gitti, orada dua etmeye başladı."));
trsuperobject.biblemap.put("b41_1_36", new trsuperobject(41,1,36,"Simun ile yanındakiler İsayı aramaya çıktılar."));
trsuperobject.biblemap.put("b41_1_37", new trsuperobject(41,1,37,"Onu bulunca, ‹‹Herkes seni arıyor!›› dediler."));
trsuperobject.biblemap.put("b41_1_38", new trsuperobject(41,1,38,"İsa onlara, ‹‹Başka yerlere, yakın kasabalara gidelim›› dedi. ‹‹Oralarda da Tanrı sözünü duyurayım. Bunun için çıkıp geldim.››"));
