trsuperobject.biblemap.put("b41_2_4", new trsuperobject(41,2,4,"Kalabalıktan Ona yaklaşamadıkları için, bulunduğu yerin üzerindeki damı delip açarak felçliyi üstünde yattığı şilteyle birlikte aşağı indirdiler."));
trsuperobject.biblemap.put("b41_2_5", new trsuperobject(41,2,5,"İsa onların imanını görünce felçliye, ‹‹Oğlum, günahların bağışlandı›› dedi."));
trsuperobject.biblemap.put("b41_2_6-7", new trsuperobject(41,2,6-7,"Orada oturan bazı din bilginleri ise içlerinden şöyle düşündüler: ‹‹Bu adam neden böyle konuşuyor? Tanrıya küfrediyor! Tanrıdan başka kim günahları bağışlayabilir?››"));
trsuperobject.biblemap.put("b41_2_8", new trsuperobject(41,2,8,"Akıllarından geçeni hemen ruhunda sezen İsa onlara, ‹‹Aklınızdan neden böyle şeyler geçiriyorsunuz?›› dedi."));
trsuperobject.biblemap.put("b41_2_9", new trsuperobject(41,2,9,"‹‹Hangisi daha kolay, felçliye, ‹Günahların bağışlandı› demek mi, yoksa, ‹Kalk, şilteni topla, yürü› demek mi?"));
trsuperobject.biblemap.put("b41_2_1-011", new trsuperobject(41,2,1-011,"Ne var ki, İnsanoğlunun yeryüzünde günahları bağışlama yetkisine sahip olduğunu bilesiniz diye...›› Sonra felçliye, ‹‹Sana söylüyorum, kalk, şilteni topla, evine git!›› dedi."));
trsuperobject.biblemap.put("b41_2_12", new trsuperobject(41,2,12,"Adam kalktı, hemen şiltesini topladı, hepsinin gözü önünde çıkıp gitti. Herkes şaşakalmıştı. Tanrıyı övüyorlar, ‹‹Böylesini hiç görmemiştik›› diyorlardı."));
trsuperobject.biblemap.put("b41_2_13", new trsuperobject(41,2,13,"İsa yine çıkıp göl kıyısına gitti. Bütün halk yanına geldi, O da onlara öğretmeye başladı."));
trsuperobject.biblemap.put("b41_2_14", new trsuperobject(41,2,14,"Yoldan geçerken, vergi toplama yerinde oturan Alfay oğlu Leviyi gördü. Ona, ‹‹Ardımdan gel›› dedi. Levi de kalkıp İsanın ardından gitti."));
trsuperobject.biblemap.put("b41_2_15", new trsuperobject(41,2,15,"Sonra İsa, Levinin evinde yemek yerken, birçok vergi görevlisiyle günahkâr Onunla ve öğrencileriyle birlikte sofraya oturmuştu. Onu izleyen böyle birçok kişi vardı."));
trsuperobject.biblemap.put("b41_2_16", new trsuperobject(41,2,16,"Ferisilerden bazı din bilginleri, Onu günahkârlar ve vergi görevlileriyle birlikte yemekte görünce öğrencilerine, ‹‹Niçin vergi görevlileri ve günahkârlarla birlikte yemek yiyor?›› diye sordular."));
trsuperobject.biblemap.put("b41_2_17", new trsuperobject(41,2,17,"Bunu duyan İsa onlara, ‹‹Sağlamların değil, hastaların hekime ihtiyacı var›› dedi. ‹‹Ben doğru kişileri değil, günahkârları çağırmaya geldim.››"));
trsuperobject.biblemap.put("b41_2_18", new trsuperobject(41,2,18,"Yahyanın öğrencileriyle Ferisiler oruç tutarken, bazı kişiler İsaya gelip, ‹‹Yahyanın ve Ferisilerin öğrencileri oruç tutuyor da senin öğrencilerin neden tutmuyor?›› diye sordular."));
trsuperobject.biblemap.put("b41_2_19", new trsuperobject(41,2,19,"İsa şöyle karşılık verdi: ‹‹Güvey aralarında olduğu sürece davetliler oruç tutar mı? Güvey aralarında oldukça oruç tutmazlar!"));
trsuperobject.biblemap.put("b41_2_20", new trsuperobject(41,2,20,"Ama güveyin aralarından alınacağı günler gelecek, onlar işte o zaman, o gün oruç tutacaklar."));
trsuperobject.biblemap.put("b41_2_21", new trsuperobject(41,2,21,"Hiç kimse eski giysiyi yeni kumaş parçasıyla yamamaz. Yoksa yeni yama çeker, eski giysiden kopar, yırtık daha beter olur."));
trsuperobject.biblemap.put("b41_2_22", new trsuperobject(41,2,22,"Hiç kimse yeni şarabı eski tulumlara doldurmaz. Yoksa şarap tulumları patlatır, şarap da tulumlar da mahvolur. Yeni şarap yeni tulumlara doldurulur.››"));
trsuperobject.biblemap.put("b41_2_23", new trsuperobject(41,2,23,"Bir Şabat Günü İsa ekinler arasından geçiyordu. Öğrencileri yolda giderken başakları koparmaya başladılar."));
trsuperobject.biblemap.put("b41_2_24", new trsuperobject(41,2,24,"Ferisiler İsaya, ‹‹Bak, Şabat Günü yasak olanı neden yapıyorlar?›› dediler."));
trsuperobject.biblemap.put("b41_2_25", new trsuperobject(41,2,25,"İsa onlara, ‹‹Davutla yanındakiler aç ve muhtaç kalınca Davutun ne yaptığını hiç okumadınız mı?›› diye sordu."));
trsuperobject.biblemap.put("b41_2_26", new trsuperobject(41,2,26,"‹‹Başkâhin Aviyatarın zamanında Davut, Tanrının evine girdi, kâhinlerden başkasının yemesi yasak olan adak ekmeklerini yedi ve yanındakilere de verdi.››"));
trsuperobject.biblemap.put("b41_2_27", new trsuperobject(41,2,27,"Sonra onlara, ‹‹İnsan Şabat Günü için değil, Şabat Günü insan için yaratıldı›› dedi."));
