trsuperobject.biblemap.put("b42_9_7-8", new trsuperobject(42,9,7-8,"Bölgenin kralı Hirodes bütün bu olanları duyunca şaşkına döndü. Çünkü bazıları Yahyanın ölümden dirildiğini, bazıları İlyasın göründüğünü, başkaları ise eski peygamberlerden birinin dirildiğini söylüyordu."));
trsuperobject.biblemap.put("b42_9_9", new trsuperobject(42,9,9,"Hirodes, ‹‹Yahyanın başını ben kestirdim. Şimdi hakkında böyle haberler duyduğum bu adam kim?›› diyor ve İsayı görmenin bir yolunu arıyordu."));
trsuperobject.biblemap.put("b42_9_10", new trsuperobject(42,9,10,"Elçiler geri dönünce, yaptıkları her şeyi İsaya anlattılar. Sonra İsa yalnızca onları yanına alıp Beytsayda denilen bir kente çekildi."));
trsuperobject.biblemap.put("b42_9_11", new trsuperobject(42,9,11,"Bunu öğrenen halk Onun ardından gitti. İsa onları ilgiyle karşıladı, kendilerine Tanrının Egemenliğinden söz etti ve şifaya ihtiyacı olanları iyileştirdi."));
trsuperobject.biblemap.put("b42_9_12", new trsuperobject(42,9,12,"Günbatımına doğru Onikiler gelip Ona, ‹‹Halkı salıver de çevredeki köylere ve çiftliklere gidip kendilerine barınak ve yiyecek bulsunlar. Çünkü ıssız bir yerdeyiz›› dediler."));
trsuperobject.biblemap.put("b42_9_13", new trsuperobject(42,9,13,"İsa, ‹‹Onlara siz yiyecek verin›› dedi. ‹‹Beş ekmekle iki balıktan başka bir şeyimiz yok›› dediler. ‹‹Yoksa bunca halk için yiyecek almaya biz mi gidelim?››"));
trsuperobject.biblemap.put("b42_9_14", new trsuperobject(42,9,14,"Orada yaklaşık beş bin erkek vardı. İsa öğrencilerine, ‹‹Halkı yaklaşık ellişer kişilik kümeler halinde yere oturtun›› dedi."));
trsuperobject.biblemap.put("b42_9_15", new trsuperobject(42,9,15,"Öğrenciler öyle yapıp herkesi yere oturttular."));
trsuperobject.biblemap.put("b42_9_16", new trsuperobject(42,9,16,"İsa, beş ekmekle iki balığı aldı, gözlerini göğe kaldırarak şükretti; sonra bunları böldü ve halka dağıtmaları için öğrencilerine verdi."));
trsuperobject.biblemap.put("b42_9_17", new trsuperobject(42,9,17,"Herkes yiyip doydu. Artakalan parçalardan on iki sepet dolusu toplandı."));
trsuperobject.biblemap.put("b42_9_18", new trsuperobject(42,9,18,"Bir gün İsa tek başına dua ediyordu, öğrencileri de yanındaydı. İsa onlara, ‹‹Halk benim kim olduğumu söylüyor?›› diye sordu."));
trsuperobject.biblemap.put("b42_9_19", new trsuperobject(42,9,19,"Şöyle yanıtladılar: ‹‹Vaftizci Yahya diyorlar. Ama kimi İlyas, kimi de eski peygamberlerden biri dirilmiş, diyor.››"));
trsuperobject.biblemap.put("b42_9_20", new trsuperobject(42,9,20,"İsa onlara, ‹‹Siz ne dersiniz›› dedi, ‹‹Sizce ben kimim?›› Petrus, ‹‹Sen Tanrının Mesihisin›› yanıtını verdi."));
trsuperobject.biblemap.put("b42_9_21", new trsuperobject(42,9,21,"İsa, onları uyararak bunu hiç kimseye söylememelerini buyurdu."));
trsuperobject.biblemap.put("b42_9_22", new trsuperobject(42,9,22,"İnsanoğlunun çok acı çekmesi, ileri gelenler, başkâhinler ve din bilginlerince reddedilmesi, öldürülmesi ve üçüncü gün dirilmesi gerektiğini söyledi."));
trsuperobject.biblemap.put("b42_9_23", new trsuperobject(42,9,23,"Sonra hepsine, ‹‹Ardımdan gelmek isteyen kendini inkâr etsin, her gün çarmıhını yüklenip beni izlesin›› dedi,"));
trsuperobject.biblemap.put("b42_9_24", new trsuperobject(42,9,24,"‹‹Canını kurtarmak isteyen onu yitirecek, canını benim uğruma yitiren ise onu kurtaracaktır."));
trsuperobject.biblemap.put("b42_9_25", new trsuperobject(42,9,25,"İnsan bütün dünyayı kazanıp da canını yitirirse, canından olursa, bunun kendisine ne yararı olur?"));
