trsuperobject.biblemap.put("b42_9_27", new trsuperobject(42,9,27,"Size gerçeği söyleyeyim, burada bulunanlar arasında, Tanrının Egemenliğini görmeden ölümü tatmayacak olanlar var.››"));
trsuperobject.biblemap.put("b42_9_28", new trsuperobject(42,9,28,"Bu sözleri söyledikten yaklaşık sekiz gün sonra İsa, yanına Petrus, Yuhanna ve Yakupu alarak dua etmek üzere dağa çıktı."));
trsuperobject.biblemap.put("b42_9_29", new trsuperobject(42,9,29,"İsa dua ederken yüzünün görünümü değişti, giysileri şimşek gibi parıldayan bir beyazlığa büründü."));
trsuperobject.biblemap.put("b42_9_3-031", new trsuperobject(42,9,3-031,"O anda görkem içinde beliren iki kişi İsayla konuşmaya başladılar. Bunlar Musa ile İlyastı. İsanın yakında Yeruşalimde gerçekleşecek olan ayrılışını konuşuyorlardı."));
trsuperobject.biblemap.put("b42_9_32", new trsuperobject(42,9,32,"Petrus ile yanındakilerin üzerine uyku çökmüştü. Ama uykuları iyice dağılınca İsanın görkemini ve yanında duran iki kişiyi gördüler."));
