trsuperobject.biblemap.put("b42_9_47-48", new trsuperobject(42,9,47-48,"Akıllarından geçeni bilen İsa, küçük bir çocuğu tutup yanına çekti ve onlara şöyle dedi: ‹‹Bu çocuğu benim adım uğruna kabul eden, beni kabul etmiş olur. Beni kabul eden de beni göndereni kabul etmiş olur. Aranızda en küçük kim ise, işte en büyük odur.››"));
trsuperobject.biblemap.put("b42_9_49", new trsuperobject(42,9,49,"Yuhanna buna karşılık, ‹‹Efendimiz›› dedi, ‹‹Senin adınla cin kovan birini gördük, ama bizimle birlikte seni izlemediği için ona engel olmaya çalıştık.››"));
trsuperobject.biblemap.put("b42_9_50", new trsuperobject(42,9,50,"İsa, ‹‹Ona engel olmayın!›› dedi. ‹‹Size karşı olmayan, sizden yanadır.››"));
trsuperobject.biblemap.put("b42_9_51", new trsuperobject(42,9,51,"Göğe alınacağı gün yaklaşınca İsa, kararlı adımlarla Yeruşalime doğru yola çıktı."));
trsuperobject.biblemap.put("b42_9_52", new trsuperobject(42,9,52,"Kendi önünden haberciler gönderdi. Bunlar, kendisi için hazırlık yapmak üzere gidip Samiriyelilere ait bir köye girdiler."));
