trsuperobject.biblemap.put("b42_9_58", new trsuperobject(42,9,58,"İsa ona, ‹‹Tilkilerin ini, kuşların yuvası var, ama İnsanoğlunun başını yaslayacak bir yeri yok›› dedi."));
trsuperobject.biblemap.put("b42_9_59", new trsuperobject(42,9,59,"Bir başkasına, ‹‹Ardımdan gel›› dedi. Adam ise, ‹‹İzin ver, önce gidip babamı gömeyim›› dedi."));
trsuperobject.biblemap.put("b42_9_60", new trsuperobject(42,9,60,"İsa ona şöyle dedi: ‹‹Bırak ölüleri, kendi ölülerini kendileri gömsün. Sen gidip Tanrının Egemenliğini duyur.››"));
trsuperobject.biblemap.put("b42_9_61", new trsuperobject(42,9,61,"Bir başkası, ‹‹Ya Rab›› dedi, ‹‹Senin ardından geleceğim ama, izin ver, önce evimdekilerle vedalaşayım.››"));
trsuperobject.biblemap.put("b42_9_62", new trsuperobject(42,9,62,"İsa ona, ‹‹Sabanı tutup da geriye bakan, Tanrı'nın Egemenliği'ne layık değildir›› dedi."));
trsuperobject.biblemap.put("b42_10_1", new trsuperobject(42,10,1,"Bu olaylardan sonra Rab yetmiş kişi daha görevlendirdi. Bunları ikişer ikişer, kendisinin gideceği her kente, her yere kendi önünden gönderdi."));
