trsuperobject.biblemap.put("b42_10_6", new trsuperobject(42,10,6,"Orada esenliksever biri varsa, dilediğiniz esenlik onun üzerinde kalacak; yoksa, size dönecektir."));
trsuperobject.biblemap.put("b42_10_7", new trsuperobject(42,10,7,"Girdiğiniz evde kalın, size ne verirlerse onu yiyip için. Çünkü işçi ücretini hak eder. Evden eve taşınmayın."));
trsuperobject.biblemap.put("b42_10_8", new trsuperobject(42,10,8,"‹‹Bir kente girdiğinizde sizi kabul ederlerse, önünüze konulanı yiyin."));
trsuperobject.biblemap.put("b42_10_9", new trsuperobject(42,10,9,"Orada bulunan hastaları iyileştirin ve kendilerine, ‹Tanrının Egemenliği size yaklaştı› deyin."));
trsuperobject.biblemap.put("b42_10_1-011", new trsuperobject(42,10,1-011,"Ama bir kente girdiğinizde sizi kabul etmezlerse, o kentin caddelerine çıkıp şöyle deyin: ‹Kentinizden ayaklarımızda kalan tozu bile size karşı silkiyoruz. Yine de şunu bilin ki, Tanrının Egemenliği yaklaştı.›"));
trsuperobject.biblemap.put("b42_10_12", new trsuperobject(42,10,12,"Size şunu söyleyeyim, yargı günü o kentin hali Sodom Kentinin halinden beter olacaktır."));
trsuperobject.biblemap.put("b42_10_13", new trsuperobject(42,10,13,"‹‹Vay haline, ey Horazin! Vay haline, ey Beytsayda! Sizlerde yapılan mucizeler Sur ve Saydada yapılmış olsaydı, çoktan çul kuşanıp kül içinde oturarak tövbe etmiş olurlardı."));
