trsuperobject.biblemap.put("b43_3_27", new trsuperobject(43,3,27,"Yahya şöyle yanıt verdi: ‹‹İnsan, kendisine gökten verilmedikçe hiçbir şey alamaz."));
trsuperobject.biblemap.put("b43_3_28", new trsuperobject(43,3,28,"‹Ben Mesih değilim, ama Onun öncüsü olarak gönderildim› dediğime siz kendiniz tanıksınız."));
trsuperobject.biblemap.put("b43_3_29", new trsuperobject(43,3,29,"Gelin kiminse, güvey odur. Ama güveyin yanında duran ve onu dinleyen dostu onun sesini işitince çok sevinir. İşte benim sevincim böylece tamamlandı."));
trsuperobject.biblemap.put("b43_3_30", new trsuperobject(43,3,30,"O büyümeli, bense küçülmeliyim.››"));
trsuperobject.biblemap.put("b43_3_31", new trsuperobject(43,3,31,"Yukarıdan gelen, herkesten üstündür. Dünyadan olan dünyaya aittir ve dünyadan söz eder. Gökten gelen ise, herkesten üstündür."));
trsuperobject.biblemap.put("b43_3_32", new trsuperobject(43,3,32,"Ne görmüş ne işitmişse ona tanıklık eder, ama tanıklığını kimse kabul etmez."));
trsuperobject.biblemap.put("b43_3_33", new trsuperobject(43,3,33,"Onun tanıklığını kabul eden, Tanrının gerçek olduğuna mührünü basmıştır."));
trsuperobject.biblemap.put("b43_3_34", new trsuperobject(43,3,34,"Tanrının gönderdiği kişi Tanrının sözlerini söyler. Çünkü Tanrı, Ruhu ölçüyle vermez."));
trsuperobject.biblemap.put("b43_3_35", new trsuperobject(43,3,35,"Baba Oğulu sever; her şeyi Ona teslim etmiştir."));
trsuperobject.biblemap.put("b43_3_36", new trsuperobject(43,3,36,"Oğul'a iman edenin sonsuz yaşamı vardır. Ama Oğul'un sözünü dinlemeyen yaşamı görmeyecektir. Tanrı'nın gazabı böylesinin üzerinde kalır."));
trsuperobject.biblemap.put("b43_4_1-3", new trsuperobject(43,4,1-3,"Ferisiler, İsanın Yahyadan daha çok öğrenci edinip vaftiz ettiğini duydular -aslında İsanın kendisi değil, öğrencileri vaftiz ediyorlardı- İsa bunu öğrenince Yahudiyeden ayrılıp yine Celileye gitti."));
trsuperobject.biblemap.put("b43_4_4", new trsuperobject(43,4,4,"Giderken Samiriyeden geçmesi gerekiyordu."));
trsuperobject.biblemap.put("b43_4_5", new trsuperobject(43,4,5,"Böylece Samiriyenin Sihar denilen kentine geldi. Burası Yakupun kendi oğlu Yusufa vermiş olduğu toprağın yakınındaydı."));
trsuperobject.biblemap.put("b43_4_6", new trsuperobject(43,4,6,"Yakupun kuyusu da oradaydı. İsa, yolculuktan yorulmuş olduğu için kuyunun yanına oturmuştu. Saat on iki sularıydı."));
trsuperobject.biblemap.put("b43_4_7", new trsuperobject(43,4,7,"Samiriyeli bir kadın su çekmeye geldi. İsa ona, ‹‹Bana su ver, içeyim›› dedi."));
trsuperobject.biblemap.put("b43_4_8", new trsuperobject(43,4,8,"İsanın öğrencileri yiyecek satın almak için kente gitmişlerdi."));
trsuperobject.biblemap.put("b43_4_9", new trsuperobject(43,4,9,"Samiriyeli kadın, ‹‹Sen Yahudisin, bense Samiriyeli bir kadınım›› dedi, ‹‹Nasıl olur da benden su istersin?›› Çünkü Yahudilerin Samiriyelilerle ilişkileri yoktur."));
trsuperobject.biblemap.put("b43_4_10", new trsuperobject(43,4,10,"İsa kadına şu yanıtı verdi: ‹‹Eğer sen Tanrının armağanını ve sana, ‹Bana su ver, içeyim› diyenin kim olduğunu bilseydin, sen Ondan dilerdin, O da sana yaşam suyunu verirdi.››"));
trsuperobject.biblemap.put("b43_4_11", new trsuperobject(43,4,11,"Kadın, ‹‹Efendim›› dedi, ‹‹Su çekecek bir şeyin yok, kuyu da derin, yaşam suyunu nereden bulacaksın?"));
trsuperobject.biblemap.put("b43_4_12", new trsuperobject(43,4,12,"Sen, bu kuyuyu bize vermiş, kendisi, oğulları ve davarları ondan içmiş olan atamız Yakuptan daha mı büyüksün?››"));
trsuperobject.biblemap.put("b43_4_13", new trsuperobject(43,4,13,"İsa şöyle yanıt verdi: ‹‹Bu sudan her içen yine susayacak."));
trsuperobject.biblemap.put("b43_4_14", new trsuperobject(43,4,14,"Oysa benim vereceğim sudan içen sonsuza dek susamaz. Benim vereceğim su, içende sonsuz yaşam için fışkıran bir pınar olacak.››"));
trsuperobject.biblemap.put("b43_4_15", new trsuperobject(43,4,15,"Kadın, ‹‹Efendim›› dedi, ‹‹Bu suyu bana ver. Böylece ne susayayım, ne de su çekmek için buraya kadar geleyim.››"));
trsuperobject.biblemap.put("b43_4_16", new trsuperobject(43,4,16,"İsa, ‹‹Git, kocanı çağır ve buraya gel›› dedi."));
trsuperobject.biblemap.put("b43_4_17", new trsuperobject(43,4,17,"Kadın, ‹‹Kocam yok›› diye yanıtladı. İsa, ‹‹Kocam yok demekle doğruyu söyledin›› dedi."));
trsuperobject.biblemap.put("b43_4_18", new trsuperobject(43,4,18,"‹‹Beş kocaya vardın. Şimdi birlikte yaşadığın adam kocan değil. Doğruyu söyledin.››"));
trsuperobject.biblemap.put("b43_4_19", new trsuperobject(43,4,19,"Kadın, ‹‹Efendim, anlıyorum, sen bir peygambersin›› dedi."));
trsuperobject.biblemap.put("b43_4_20", new trsuperobject(43,4,20,"‹‹Atalarımız bu dağda tapındılar, ama sizler tapılması gereken yerin Yeruşalimde olduğunu söylüyorsunuz.››"));
trsuperobject.biblemap.put("b43_4_21", new trsuperobject(43,4,21,"İsa ona şöyle dedi: ‹‹Kadın, bana inan, öyle bir saat geliyor ki, Babaya ne bu dağda, ne de Yeruşalimde tapınacaksınız!"));
trsuperobject.biblemap.put("b43_4_22", new trsuperobject(43,4,22,"Siz bilmediğinize tapıyorsunuz, biz bildiğimize tapıyoruz. Çünkü kurtuluş Yahudilerdendir."));
trsuperobject.biblemap.put("b43_4_23", new trsuperobject(43,4,23,"Ama içtenlikle tapınanların Babaya ruhta ve gerçekte tapınacakları saat geliyor. İşte, o saat şimdidir. Baba da kendisine böyle tapınanları arıyor."));
trsuperobject.biblemap.put("b43_4_24", new trsuperobject(43,4,24,"Tanrı ruhtur, Ona tapınanlar da ruhta ve gerçekte tapınmalıdırlar.››"));
trsuperobject.biblemap.put("b43_4_25", new trsuperobject(43,4,25,"Kadın İsaya, ‹‹Mesih denilen meshedilmiş Olanın geleceğini biliyorum›› dedi, ‹‹O gelince bize her şeyi bildirecek.››"));
trsuperobject.biblemap.put("b43_4_26", new trsuperobject(43,4,26,"İsa, ‹‹Seninle konuşan ben, Oyum›› dedi."));
trsuperobject.biblemap.put("b43_4_27", new trsuperobject(43,4,27,"Bu sırada İsanın öğrencileri geldiler. Onun bir kadınla konuşmasına şaştılar. Bununla birlikte hiçbiri, ‹‹Ne istiyorsun?›› ya da, ‹‹O kadınla neden konuşuyorsun?›› demedi."));
trsuperobject.biblemap.put("b43_4_28-29", new trsuperobject(43,4,28-29,"Sonra kadın su testisini bırakarak kente gitti ve halka şöyle dedi: ‹‹Gelin, yaptığım her şeyi bana söyleyen adamı görün. Acaba Mesih bu mudur?››"));
trsuperobject.biblemap.put("b43_4_30", new trsuperobject(43,4,30,"Halk da kentten çıkıp İsaya doğru gelmeye başladı."));
trsuperobject.biblemap.put("b43_4_31", new trsuperobject(43,4,31,"Bu arada öğrencileri Ona, ‹‹Rabbî, yemek ye!›› diye rica ediyorlardı."));
trsuperobject.biblemap.put("b43_4_32", new trsuperobject(43,4,32,"Ama İsa, ‹‹Benim, sizin bilmediğiniz bir yiyeceğim var›› dedi."));
trsuperobject.biblemap.put("b43_4_33", new trsuperobject(43,4,33,"Öğrenciler birbirlerine, ‹‹Acaba biri Ona yiyecek mi getirdi?›› diye sordular."));
trsuperobject.biblemap.put("b43_4_34", new trsuperobject(43,4,34,"İsa, ‹‹Benim yemeğim, beni gönderenin isteğini yerine getirmek ve Onun işini tamamlamaktır›› dedi."));
trsuperobject.biblemap.put("b43_4_35", new trsuperobject(43,4,35,"‹‹Sizler, ‹Ekinleri biçmeye daha dört ay var› demiyor musunuz? İşte, size söylüyorum, başınızı kaldırıp tarlalara bakın. Ekinler sararmış, biçilmeye hazır!"));
trsuperobject.biblemap.put("b43_4_36", new trsuperobject(43,4,36,"Eken ve biçen birlikte sevinsinler diye, biçen kişi şimdiden ücretini alır ve sonsuz yaşam için ürün toplar."));
trsuperobject.biblemap.put("b43_4_37", new trsuperobject(43,4,37,"‹Biri eker, başkası biçer› sözü bu durumda doğrudur."));
trsuperobject.biblemap.put("b43_4_38", new trsuperobject(43,4,38,"Ben sizi, emek vermediğiniz bir ürünü biçmeye gönderdim. Başkaları emek verdiler, siz ise onların emeğinden yararlandınız.››"));
trsuperobject.biblemap.put("b43_4_39", new trsuperobject(43,4,39,"O kentten birçok Samiriyeli, ‹‹Yaptığım her şeyi bana söyledi›› diye tanıklık eden kadının sözü üzerine İsaya iman etti."));
trsuperobject.biblemap.put("b43_4_40", new trsuperobject(43,4,40,"Samiriyeliler Ona gelip yanlarında kalması için rica ettiler. O da orada iki gün kaldı."));
trsuperobject.biblemap.put("b43_4_41", new trsuperobject(43,4,41,"Onun sözü üzerine daha birçokları iman etti."));
trsuperobject.biblemap.put("b43_4_42", new trsuperobject(43,4,42,"Bunlar kadına, ‹‹Bizim iman etmemizin nedeni artık senin sözlerin değil›› diyorlardı. ‹‹Kendimiz işittik, Onun gerçekten dünyanın Kurtarıcısı olduğunu biliyoruz.››"));
trsuperobject.biblemap.put("b43_4_43", new trsuperobject(43,4,43,"Bu iki günden sonra İsa oradan ayrılıp Celileye gitti."));
trsuperobject.biblemap.put("b43_4_44", new trsuperobject(43,4,44,"İsanın kendisi, bir peygamberin kendi memleketinde saygı görmediğine tanıklık etmişti."));
trsuperobject.biblemap.put("b43_4_45", new trsuperobject(43,4,45,"Celileye geldiği zaman Celileliler Onu iyi karşıladılar. Çünkü onlar da bayram için gitmişler ve bayramda Onun Yeruşalimde yaptığı her şeyi görmüşlerdi."));
trsuperobject.biblemap.put("b43_4_46", new trsuperobject(43,4,46,"İsa yine, suyu şaraba çevirdiği Celilenin Kana Köyüne geldi. Orada saraya bağlı bir memur vardı. Oğlu Kefarnahumda hastaydı."));
trsuperobject.biblemap.put("b43_4_47", new trsuperobject(43,4,47,"Adam, İsanın Yahudiyeden Celileye geldiğini işitince yanına gitti, evine gelip ölmek üzere olan oğlunu iyileştirmesi için Ona yalvardı."));
trsuperobject.biblemap.put("b43_4_48", new trsuperobject(43,4,48,"İsa adama, ‹‹Sizler belirtiler ve harikalar görmedikçe iman etmeyeceksiniz›› dedi."));
trsuperobject.biblemap.put("b43_4_49", new trsuperobject(43,4,49,"Saray memuru İsaya, ‹‹Efendim, çocuğum ölmeden yetiş!›› dedi."));
trsuperobject.biblemap.put("b43_4_50", new trsuperobject(43,4,50,"İsa, ‹‹Git, oğlun yaşayacak›› dedi. Adam, İsanın söylediği söze iman ederek gitti."));
trsuperobject.biblemap.put("b43_4_51", new trsuperobject(43,4,51,"Daha yoldayken köleleri onu karşılayıp oğlunun yaşadığını bildirdiler."));
trsuperobject.biblemap.put("b43_4_52", new trsuperobject(43,4,52,"Adam onlara, oğlunun iyileşmeye başladığı saati sordu. ‹‹Dün öğle üstü saat birde ateşi düştü›› dediler."));
trsuperobject.biblemap.put("b43_4_53", new trsuperobject(43,4,53,"Baba bunun, İsanın, ‹‹Oğlun yaşayacak›› dediği saat olduğunu anladı. Kendisi ve bütün ev halkı iman etti."));
trsuperobject.biblemap.put("b43_4_54", new trsuperobject(43,4,54,"İsa, bu ikinci belirtiyi de Yahudiye'den Celile'ye döndükten sonra gerçekleştirdi."));
trsuperobject.biblemap.put("b43_5_1", new trsuperobject(43,5,1,"İsa bundan sonra Yahudilerin bir bayramı nedeniyle Yeruşalime gitti."));
trsuperobject.biblemap.put("b43_5_2", new trsuperobject(43,5,2,"Yeruşalimde Koyun Kapısı yanında, İbranicede Beytesta denilen beş eyvanlı bir havuz vardır."));
trsuperobject.biblemap.put("b43_5_3-4", new trsuperobject(43,5,3-4,"Bu eyvanların altında kör, kötürüm, felçli hastalardan bir kalabalık yatardı."));
trsuperobject.biblemap.put("b43_5_5", new trsuperobject(43,5,5,"Orada otuz sekiz yıldır hasta olan bir adam vardı."));
trsuperobject.biblemap.put("b43_5_6", new trsuperobject(43,5,6,"İsa hasta yatan bu adamı görünce ve uzun zamandır bu durumda olduğunu anlayınca, ‹‹İyi olmak ister misin?›› diye sordu."));
trsuperobject.biblemap.put("b43_5_7", new trsuperobject(43,5,7,"Hasta şöyle yanıt verdi: ‹‹Efendim, su çalkandığı zaman beni havuza indirecek kimsem yok, tam gireceğim an benden önce başkası giriyor.››"));
trsuperobject.biblemap.put("b43_5_8", new trsuperobject(43,5,8,"İsa ona, ‹‹Kalk, şilteni topla ve yürü›› dedi."));
trsuperobject.biblemap.put("b43_5_9", new trsuperobject(43,5,9,"Adam o anda iyileşti. Şiltesini toplayıp yürümeye başladı. O gün Şabat Günüydü."));
trsuperobject.biblemap.put("b43_5_10", new trsuperobject(43,5,10,"Bu yüzden Yahudi yetkililer iyileşen adama, ‹‹Bugün Şabat Günü›› dediler, ‹‹Şilteni toplaman yasaktır.››"));
trsuperobject.biblemap.put("b43_5_11", new trsuperobject(43,5,11,"Ama adam onlara şöyle yanıt verdi: ‹‹Beni iyileştiren kişi bana, ‹Şilteni topla ve yürü› dedi.››"));
trsuperobject.biblemap.put("b43_5_12", new trsuperobject(43,5,12,"‹‹Sana, ‹Şilteni topla ve yürü› diyen adam kim?›› diye sordular."));
trsuperobject.biblemap.put("b43_5_13", new trsuperobject(43,5,13,"İyileşen adam ise Onun kim olduğunu bilmiyordu. Orası kalabalıktı, İsa da çekilip gitmişti."));
trsuperobject.biblemap.put("b43_5_14", new trsuperobject(43,5,14,"İsa daha sonra adamı tapınakta buldu. ‹‹Bak, iyi oldun. Artık günah işleme de başına daha kötü bir şey gelmesin›› dedi."));
trsuperobject.biblemap.put("b43_5_15", new trsuperobject(43,5,15,"Adam gidip Yahudi yetkililere kendisini iyileştirenin İsa olduğunu bildirdi."));
trsuperobject.biblemap.put("b43_5_16", new trsuperobject(43,5,16,"Şabat Günü böyle şeyler yaptığı için İsaya zulmetmeye başladılar."));
trsuperobject.biblemap.put("b43_5_17", new trsuperobject(43,5,17,"Ama İsa onlara şu karşılığı verdi: ‹‹Babam hâlâ çalışmaktadır, ben de çalışıyorum.››"));
trsuperobject.biblemap.put("b43_5_18", new trsuperobject(43,5,18,"İşte bu nedenle Yahudi yetkililer Onu öldürmek için daha çok gayret ettiler. Çünkü yalnız Şabat Günü düzenini bozmakla kalmamış, Tanrının kendi Babası olduğunu söyleyerek kendisini Tanrıya eşit kılmıştı."));
trsuperobject.biblemap.put("b43_5_19", new trsuperobject(43,5,19,"İsa Yahudi yetkililere şöyle karşılık verdi: ‹‹Size doğrusunu söyleyeyim, Oğul, Babanın yaptıklarını görmedikçe kendiliğinden bir şey yapamaz. Baba ne yaparsa Oğul da aynı şeyi yapar."));
trsuperobject.biblemap.put("b43_5_20", new trsuperobject(43,5,20,"Çünkü Baba Oğulu sever ve yaptıklarının hepsini Ona gösterir. Şaşasınız diye Ona bunlardan daha büyük işler de gösterecektir."));
trsuperobject.biblemap.put("b43_5_21", new trsuperobject(43,5,21,"Baba nasıl ölüleri diriltip onlara yaşam veriyorsa, Oğul da dilediği kimselere yaşam verir."));
trsuperobject.biblemap.put("b43_5_22", new trsuperobject(43,5,22,"Baba kimseyi yargılamaz, bütün yargılama işini Oğula vermiştir."));
trsuperobject.biblemap.put("b43_5_23", new trsuperobject(43,5,23,"Öyle ki, herkes Babayı onurlandırdığı gibi Oğulu onurlandırsın. Oğulu onurlandırmayan, Onu gönderen Babayı da onurlandırmaz."));
trsuperobject.biblemap.put("b43_5_24", new trsuperobject(43,5,24,"‹‹Size doğrusunu söyleyeyim, sözümü işitip beni gönderene iman edenin sonsuz yaşamı vardır. Böyle biri yargılanmaz, ölümden yaşama geçmiştir."));
trsuperobject.biblemap.put("b43_5_25", new trsuperobject(43,5,25,"Size doğrusunu söyleyeyim, ölülerin Tanrı Oğlunun sesini işitecekleri ve işitenlerin yaşayacakları saat geliyor, geldi bile."));
trsuperobject.biblemap.put("b43_5_26", new trsuperobject(43,5,26,"Çünkü Baba, kendisinde yaşam olduğu gibi, Oğula da kendisinde yaşam olma özelliğini verdi."));
trsuperobject.biblemap.put("b43_5_27", new trsuperobject(43,5,27,"Ona yargılama yetkisini de verdi. Çünkü O İnsanoğludur."));
trsuperobject.biblemap.put("b43_5_28", new trsuperobject(43,5,28,"Buna şaşmayın. Mezarda olanların hepsinin Onun sesini işitecekleri saat geliyor."));
trsuperobject.biblemap.put("b43_5_29", new trsuperobject(43,5,29,"Ve onlar mezarlarından çıkacaklar. İyilik yapmış olanlar yaşamak, kötülük yapmış olanlar yargılanmak üzere dirilecekler.››"));
trsuperobject.biblemap.put("b43_5_30", new trsuperobject(43,5,30,"‹‹Ben kendiliğimden hiçbir şey yapamam. İşittiğim gibi yargılarım ve benim yargım adildir. Çünkü amacım kendi istediğimi değil, beni gönderenin istediğini yapmaktır."));
trsuperobject.biblemap.put("b43_5_31", new trsuperobject(43,5,31,"Eğer kendim için ben tanıklık edersem, tanıklığım geçerli olmaz."));
trsuperobject.biblemap.put("b43_5_32", new trsuperobject(43,5,32,"Ama benim için tanıklık eden başka biri vardır. Onun benim için ettiği tanıklığın geçerli olduğunu bilirim."));
trsuperobject.biblemap.put("b43_5_33", new trsuperobject(43,5,33,"Siz Yahyaya adamlar gönderdiniz, o da gerçeğe tanıklık etti."));
trsuperobject.biblemap.put("b43_5_34", new trsuperobject(43,5,34,"İnsanın tanıklığını kabul ettiğim için değil, kurtulmanız için bunları söylüyorum."));
trsuperobject.biblemap.put("b43_5_35", new trsuperobject(43,5,35,"Yahya, yanan ve ışık saçan bir çıraydı. Sizler onun ışığında bir süre için coşmak istediniz."));
trsuperobject.biblemap.put("b43_5_36", new trsuperobject(43,5,36,"Ama benim, Yahyanınkinden daha büyük bir tanıklığım var. Tamamlamam için Babanın bana verdiği işler, şu yaptığım işler, beni Babanın gönderdiğine tanıklık ediyor."));
trsuperobject.biblemap.put("b43_5_37", new trsuperobject(43,5,37,"Beni gönderen Baba da benim için tanıklık etmiştir. Siz hiçbir zaman ne Onun sesini işittiniz, ne de şeklini gördünüz."));
trsuperobject.biblemap.put("b43_5_38", new trsuperobject(43,5,38,"Onun sözü sizde yaşamıyor. Çünkü Onun gönderdiği kişiye iman etmiyorsunuz."));
trsuperobject.biblemap.put("b43_5_39", new trsuperobject(43,5,39,"Kutsal Yazıları araştırıyorsunuz. Çünkü bunlar aracılığıyla sonsuz yaşama sahip olduğunuzu sanıyorsunuz. Bana tanıklık eden de bu yazılardır!"));
trsuperobject.biblemap.put("b43_5_40", new trsuperobject(43,5,40,"Öyleyken siz, yaşama kavuşmak için bana gelmek istemiyorsunuz."));
trsuperobject.biblemap.put("b43_5_41", new trsuperobject(43,5,41,"‹‹İnsanlardan övgü kabul etmiyorum."));
trsuperobject.biblemap.put("b43_5_42", new trsuperobject(43,5,42,"Ama ben sizi bilirim, içinizde Tanrı sevgisi yoktur."));
trsuperobject.biblemap.put("b43_5_43", new trsuperobject(43,5,43,"Ben Babamın adına geldim, ama beni kabul etmiyorsunuz. Oysa başka birisi kendi adına gelirse, onu kabul edeceksiniz."));
trsuperobject.biblemap.put("b43_5_44", new trsuperobject(43,5,44,"Birbirinizden övgüler kabul ediyor, ama tek olan Tanrının övgüsünü kazanmaya çalışmıyorsunuz. Bu durumda nasıl iman edebilirsiniz?"));
trsuperobject.biblemap.put("b43_5_45", new trsuperobject(43,5,45,"Babanın önünde sizi suçlayacağımı sanmayın. Sizi suçlayan, umut bağladığınız Musadır."));
trsuperobject.biblemap.put("b43_5_46", new trsuperobject(43,5,46,"Musaya iman etmiş olsaydınız, bana da iman ederdiniz. Çünkü o benim hakkımda yazmıştır."));
trsuperobject.biblemap.put("b43_5_47", new trsuperobject(43,5,47,"Ama onun yazılarına iman etmezseniz, benim sözlerime nasıl iman edeceksiniz?››"));
trsuperobject.biblemap.put("b43_6_1", new trsuperobject(43,6,1,"Bundan sonra İsa, Celile -Taberiye- Gölünün karşı yakasına geçti."));
trsuperobject.biblemap.put("b43_6_2", new trsuperobject(43,6,2,"Ardından büyük bir kalabalık gidiyordu. Çünkü hastalar üzerinde yaptığı mucizeleri görmüşlerdi."));
trsuperobject.biblemap.put("b43_6_3", new trsuperobject(43,6,3,"İsa dağa çıkıp orada öğrencileriyle birlikte oturdu."));
trsuperobject.biblemap.put("b43_6_4", new trsuperobject(43,6,4,"Yahudilerin Fısıh Bayramı yakındı."));
trsuperobject.biblemap.put("b43_6_5", new trsuperobject(43,6,5,"İsa başını kaldırıp büyük bir kalabalığın kendisine doğru geldiğini görünce Filipusa, ‹‹Bunları doyurmak için nereden ekmek alalım?›› diye sordu."));
trsuperobject.biblemap.put("b43_6_6", new trsuperobject(43,6,6,"Bu sözü onu denemek için söyledi, aslında kendisi ne yapacağını biliyordu."));
trsuperobject.biblemap.put("b43_6_7", new trsuperobject(43,6,7,"Filipus Ona şu yanıtı verdi: ‹‹Her birinin bir lokma yiyebilmesi için iki yüz dinarlık ekmek bile yetmez.››"));
trsuperobject.biblemap.put("b43_6_8-9", new trsuperobject(43,6,8-9,"Öğrencilerinden biri, Simun Petrusun kardeşi Andreas, İsaya dedi ki, ‹‹Burada beş arpa ekmeğiyle iki balığı olan bir çocuk var. Ama bu kadar adam için bunlar nedir ki?››"));
trsuperobject.biblemap.put("b43_6_10", new trsuperobject(43,6,10,"İsa, ‹‹Halkı yere oturtun›› dedi. Orası çayırlıktı. Böylece halk yere oturdu. Yaklaşık beş bin erkek vardı."));
trsuperobject.biblemap.put("b43_6_11", new trsuperobject(43,6,11,"İsa ekmekleri aldı, şükrettikten sonra oturanlara dağıttı. Balıklardan da istedikleri kadar verdi."));
trsuperobject.biblemap.put("b43_6_12", new trsuperobject(43,6,12,"Herkes doyunca İsa öğrencilerine, ‹‹Artakalan parçaları toplayın, hiçbir şey ziyan olmasın›› dedi."));
trsuperobject.biblemap.put("b43_6_13", new trsuperobject(43,6,13,"Onlar da topladılar. Yedikleri beş arpa ekmeğinden artakalan parçalarla on iki sepet doldurdular."));
trsuperobject.biblemap.put("b43_6_14", new trsuperobject(43,6,14,"Halk, İsanın yaptığı mucizeyi görünce, ‹‹Gerçekten dünyaya gelecek olan peygamber budur›› dedi."));
trsuperobject.biblemap.put("b43_6_15", new trsuperobject(43,6,15,"İsa onların gelip kendisini kral yapmak üzere zorla götüreceklerini bildiğinden tek başına yine dağa çekildi."));
trsuperobject.biblemap.put("b43_6_16", new trsuperobject(43,6,16,"Akşam olunca öğrencileri göle indiler."));
trsuperobject.biblemap.put("b43_6_17", new trsuperobject(43,6,17,"Bir tekneye binerek gölün karşı yakasındaki Kefarnahuma doğru yol aldılar. Karanlık basmış, İsa henüz yanlarına gelmemişti."));
trsuperobject.biblemap.put("b43_6_18", new trsuperobject(43,6,18,"Güçlü bir rüzgar estiğinden göl kabarmaya başladı."));
trsuperobject.biblemap.put("b43_6_19", new trsuperobject(43,6,19,"Öğrenciler üç mil kadar kürek çektikten sonra, İsanın gölün üstünde yürüyerek tekneye yaklaştığını görünce korktular."));
trsuperobject.biblemap.put("b43_6_20", new trsuperobject(43,6,20,"Ama İsa, ‹‹Korkmayın, benim!›› dedi."));
trsuperobject.biblemap.put("b43_6_21", new trsuperobject(43,6,21,"Bunun üzerine Onu tekneye almak istediler. O anda tekne gidecekleri kıyıya ulaştı."));
trsuperobject.biblemap.put("b43_6_22", new trsuperobject(43,6,22,"Ertesi gün, gölün karşı yakasında kalan halk, önceden orada sadece bir tek tekne bulunduğunu, İsanın kendi öğrencileriyle birlikte bu tekneye binmediğini, öğrencilerinin yalnız gittiklerini anladı."));
trsuperobject.biblemap.put("b43_6_23", new trsuperobject(43,6,23,"Rabbin şükretmesinden sonra halkın ekmek yediği yerin yakınına Taberiyeden başka tekneler geldi."));
trsuperobject.biblemap.put("b43_6_24", new trsuperobject(43,6,24,"Halk, İsanın ve öğrencilerinin orada olmadığını görünce teknelere binerek Kefarnahuma, İsayı aramaya gitti."));
trsuperobject.biblemap.put("b43_6_25", new trsuperobject(43,6,25,"Onu gölün karşı yakasında buldukları zaman, ‹‹Rabbî, buraya ne zaman geldin?›› diye sordular."));
trsuperobject.biblemap.put("b43_6_26", new trsuperobject(43,6,26,"İsa şöyle yanıt verdi: ‹‹Size doğrusunu söyleyeyim, doğaüstü belirtiler gördüğünüz için değil, ekmeklerden yiyip doyduğunuz için beni arıyorsunuz."));
trsuperobject.biblemap.put("b43_6_27", new trsuperobject(43,6,27,"Geçici yiyecek için değil, sonsuz yaşam boyunca kalıcı yiyecek için çalışın. Bunu size İnsanoğlu verecek. Çünkü Baba Tanrı Ona bu onayı vermiştir.››"));
trsuperobject.biblemap.put("b43_6_28", new trsuperobject(43,6,28,"Onlar da şunu sordular: ‹‹Tanrının istediği işleri yapmak için ne yapmalıyız?››"));
trsuperobject.biblemap.put("b43_6_29", new trsuperobject(43,6,29,"İsa, ‹‹Tanrının işi Onun gönderdiği kişiye iman etmenizdir›› diye yanıt verdi."));
trsuperobject.biblemap.put("b43_6_30", new trsuperobject(43,6,30,"Bunun üzerine, ‹‹Görüp sana iman etmemiz için nasıl bir belirti gerçekleştireceksin? Ne yapacaksın?›› dediler."));
trsuperobject.biblemap.put("b43_6_31", new trsuperobject(43,6,31,"‹‹Atalarımız çölde man yediler. Yazılmış olduğu gibi, ‹Yemeleri için onlara gökten ekmek verdi.››"));
trsuperobject.biblemap.put("b43_6_32", new trsuperobject(43,6,32,"İsa onlara dedi ki, ‹‹Size doğrusunu söyleyeyim, gökten ekmeği size Musa vermedi, gökten size gerçek ekmeği Babam verir."));
trsuperobject.biblemap.put("b43_6_33", new trsuperobject(43,6,33,"Çünkü Tanrının ekmeği, gökten inen ve dünyaya yaşam verendir.››"));
trsuperobject.biblemap.put("b43_6_34", new trsuperobject(43,6,34,"Onlar da, ‹‹Efendimiz, bizlere her zaman bu ekmeği ver!›› dediler."));
trsuperobject.biblemap.put("b43_6_35", new trsuperobject(43,6,35,"İsa, ‹‹Yaşam ekmeği Benim. Bana gelen asla acıkmaz, bana iman eden hiçbir zaman susamaz›› dedi."));
trsuperobject.biblemap.put("b43_6_36", new trsuperobject(43,6,36,"‹‹Ama ben size dedim ki, ‹Beni gördünüz, yine de iman etmiyorsunuz.›"));
trsuperobject.biblemap.put("b43_6_37", new trsuperobject(43,6,37,"Babanın bana verdiklerinin hepsi bana gelecek ve bana geleni asla kovmam."));
trsuperobject.biblemap.put("b43_6_38", new trsuperobject(43,6,38,"Çünkü kendi isteğimi değil, beni gönderenin isteğini yerine getirmek için gökten indim."));
trsuperobject.biblemap.put("b43_6_39", new trsuperobject(43,6,39,"Beni gönderenin isteği, bana verdiklerinden hiçbirini yitirmemem, son gün hepsini diriltmemdir."));
trsuperobject.biblemap.put("b43_6_40", new trsuperobject(43,6,40,"Çünkü Babamın isteği, Oğulu gören ve Ona iman eden herkesin sonsuz yaşama kavuşmasıdır. Ben de böylelerini son günde dirilteceğim.››"));
trsuperobject.biblemap.put("b43_6_41", new trsuperobject(43,6,41,"‹‹Gökten inmiş olan ekmek Benim›› dediği için Yahudiler Ona karşı söylenmeye başladılar."));
trsuperobject.biblemap.put("b43_6_42", new trsuperobject(43,6,42,"‹‹Yusuf oğlu İsa değil mi bu?›› diyorlardı. ‹‹Annesini de, babasını da tanıyoruz. Şimdi nasıl oluyor da, ‹Gökten indim› diyor?››"));
trsuperobject.biblemap.put("b43_6_43", new trsuperobject(43,6,43,"İsa, ‹‹Aranızda söylenmeyin›› dedi."));
trsuperobject.biblemap.put("b43_6_44", new trsuperobject(43,6,44,"‹‹Beni gönderen Baba bir kimseyi bana çekmedikçe, o kimse bana gelemez. Bana geleni de son günde dirilteceğim."));
trsuperobject.biblemap.put("b43_6_45", new trsuperobject(43,6,45,"Peygamberlerin yazdığı gibi, ‹Tanrı onların hepsine kendi yollarını öğretecektir.› Babayı işiten ve Ondan öğrenen herkes bana gelir."));
trsuperobject.biblemap.put("b43_6_46", new trsuperobject(43,6,46,"Bu, bir kimsenin Babayı gördüğü anlamına gelmez. Babayı sadece Tanrıdan gelen görmüştür."));
trsuperobject.biblemap.put("b43_6_47", new trsuperobject(43,6,47,"Size doğrusunu söyleyeyim, iman edenin sonsuz yaşamı vardır."));
trsuperobject.biblemap.put("b43_6_48", new trsuperobject(43,6,48,"Yaşam ekmeği Benim."));
trsuperobject.biblemap.put("b43_6_49", new trsuperobject(43,6,49,"Atalarınız çölde man yediler, yine de öldüler."));
trsuperobject.biblemap.put("b43_6_50", new trsuperobject(43,6,50,"Gökten inen öyle bir ekmek var ki, ondan yiyen ölmeyecek."));
trsuperobject.biblemap.put("b43_6_51", new trsuperobject(43,6,51,"Gökten inmiş olan diri ekmek Benim. Bu ekmekten yiyen sonsuza dek yaşayacak. Dünyanın yaşamı uğruna vereceğim ekmek de benim bedenimdir.››"));
trsuperobject.biblemap.put("b43_6_52", new trsuperobject(43,6,52,"Bunun üzerine Yahudiler, ‹‹Bu adam yememiz için bedenini bize nasıl verebilir?›› diyerek birbirleriyle çekişmeye başladılar."));
trsuperobject.biblemap.put("b43_6_53", new trsuperobject(43,6,53,"İsa onlara şöyle dedi: ‹‹Size doğrusunu söyleyeyim, İnsanoğlunun bedenini yiyip kanını içmedikçe, sizde yaşam olmaz."));
trsuperobject.biblemap.put("b43_6_54", new trsuperobject(43,6,54,"Bedenimi yiyenin, kanımı içenin sonsuz yaşamı vardır ve ben onu son günde dirilteceğim."));
trsuperobject.biblemap.put("b43_6_55", new trsuperobject(43,6,55,"Çünkü bedenim gerçek yiyecek, kanım gerçek içecektir."));
trsuperobject.biblemap.put("b43_6_56", new trsuperobject(43,6,56,"Bedenimi yiyip kanımı içen bende yaşar, ben de onda."));
trsuperobject.biblemap.put("b43_6_57", new trsuperobject(43,6,57,"Yaşayan Baba beni gönderdiği ve ben Babanın aracılığıyla yaşadığım gibi, bedenimi yiyen de benim aracılığımla yaşayacak."));
trsuperobject.biblemap.put("b43_6_58", new trsuperobject(43,6,58,"İşte gökten inmiş olan ekmek budur. Atalarınızın yedikleri man gibi değildir. Atalarınız öldüler. Oysa bu ekmeği yiyen sonsuza dek yaşar.››"));
trsuperobject.biblemap.put("b43_6_59", new trsuperobject(43,6,59,"İsa bu sözleri Kefarnahumda havrada öğretirken söyledi."));
trsuperobject.biblemap.put("b43_6_60", new trsuperobject(43,6,60,"Öğrencilerinin birçoğu bunu işitince, ‹‹Bu söz çok çetin, kim kabul edebilir?›› dediler."));
trsuperobject.biblemap.put("b43_6_61", new trsuperobject(43,6,61,"Öğrencilerinin buna karşı söylendiğini anlayan İsa, ‹‹Bu sizi şaşırtıyor mu?›› dedi."));
trsuperobject.biblemap.put("b43_6_62", new trsuperobject(43,6,62,"‹‹Ya İnsanoğlunun önceden bulunduğu yere yükseldiğini görürseniz...?"));
trsuperobject.biblemap.put("b43_6_63", new trsuperobject(43,6,63,"Yaşam veren Ruhtur. Beden bir yarar sağlamaz. Sizlere söylediğim sözler ruhtur, yaşamdır."));
trsuperobject.biblemap.put("b43_6_64", new trsuperobject(43,6,64,"Yine de aranızda iman etmeyenler var.›› İsa iman etmeyenlerin ve kendisine ihanet edecek kişinin kim olduğunu baştan beri biliyordu."));
trsuperobject.biblemap.put("b43_6_65", new trsuperobject(43,6,65,"‹‹Sizlere, ‹Babanın bana yöneltmediği hiç kimse bana gelemez› dememin nedeni budur›› dedi."));
trsuperobject.biblemap.put("b43_6_66", new trsuperobject(43,6,66,"Bunun üzerine öğrencilerinin birçoğu geri döndüler, artık Onunla dolaşmaz oldular."));
trsuperobject.biblemap.put("b43_6_67", new trsuperobject(43,6,67,"İsa o zaman Onikilere, ‹‹Siz de mi ayrılmak istiyorsunuz?›› diye sordu."));
trsuperobject.biblemap.put("b43_6_68", new trsuperobject(43,6,68,"Simun Petrus şu yanıtı verdi: ‹‹Rab, biz kime gidelim? Sonsuz yaşamın sözleri sendedir."));
trsuperobject.biblemap.put("b43_6_69", new trsuperobject(43,6,69,"İman ediyor ve biliyoruz ki, sen Tanrının Kutsalısın.››"));
trsuperobject.biblemap.put("b43_6_70", new trsuperobject(43,6,70,"İsa onlara şu karşılığı verdi: ‹‹Siz Onikileri seçen ben değil miyim? Buna karşın içinizden biri iblistir.››"));
trsuperobject.biblemap.put("b43_6_71", new trsuperobject(43,6,71,"Simun İskariot'un oğlu Yahuda'dan söz ediyordu. Çünkü Yahuda Onikiler'den biri olduğu halde İsa'ya ihanet edecekti."));
trsuperobject.biblemap.put("b43_7_1", new trsuperobject(43,7,1,"Bundan sonra İsa Celilede dolaşmaya başladı. Yahudi yetkililer Onu öldürmeyi amaçladıkları için Yahudiyede dolaşmak istemiyordu."));
trsuperobject.biblemap.put("b43_7_2", new trsuperobject(43,7,2,"Yahudilerin Çardak Bayramı yaklaşmıştı."));
trsuperobject.biblemap.put("b43_7_3", new trsuperobject(43,7,3,"Bu nedenle İsanın kardeşleri Ona, ‹‹Buradan ayrıl, Yahudiyeye git›› dediler, ‹‹Öğrencilerin de yaptığın işleri görsünler."));
trsuperobject.biblemap.put("b43_7_4", new trsuperobject(43,7,4,"Çünkü kendini açıkça tanıtmak isteyen bir kimse yaptıklarını gizlemez. Mademki bu şeyleri yapıyorsun, kendini dünyaya göster!››"));
trsuperobject.biblemap.put("b43_7_5", new trsuperobject(43,7,5,"Kardeşleri bile Ona iman etmiyorlardı."));
trsuperobject.biblemap.put("b43_7_6", new trsuperobject(43,7,6,"İsa onlara, ‹‹Benim zamanım daha gelmedi›› dedi, ‹‹Oysa sizin için zaman hep uygundur."));
trsuperobject.biblemap.put("b43_7_7", new trsuperobject(43,7,7,"Dünya sizden nefret edemez, ama benden nefret ediyor. Çünkü yaptıklarının kötü olduğuna tanıklık ediyorum."));
trsuperobject.biblemap.put("b43_7_8", new trsuperobject(43,7,8,"Siz bu bayramı kutlamaya gidin. Ben şimdilik gitmeyeceğim. Çünkü benim zamanım daha dolmadı.››"));
trsuperobject.biblemap.put("b43_7_9", new trsuperobject(43,7,9,"İsa bu sözleri söyleyip Celilede kaldı."));
trsuperobject.biblemap.put("b43_7_10", new trsuperobject(43,7,10,"Ne var ki, kardeşleri bayramı kutlamaya gidince, kendisi de gitti. Ancak açıktan açığa değil, gizlice gitti."));
trsuperobject.biblemap.put("b43_7_11", new trsuperobject(43,7,11,"Yahudi yetkililer Onu bayram sırasında arıyor, ‹‹O nerede?›› diye soruyorlardı."));
trsuperobject.biblemap.put("b43_7_12", new trsuperobject(43,7,12,"Kalabalık arasında Onunla ilgili bir sürü laf fısıldanıyordu. Bazıları, ‹‹İyi adamdır››, bazıları da, ‹‹Hayır, tam tersine, halkı saptırıyor›› diyorlardı."));
trsuperobject.biblemap.put("b43_7_13", new trsuperobject(43,7,13,"Bununla birlikte yetkililerden korktukları için, hiç kimse Ondan açıkça söz etmiyordu."));
trsuperobject.biblemap.put("b43_7_14", new trsuperobject(43,7,14,"Bayramın yarısı geçmişti. İsa tapınağa gidip öğretmeye başladı."));
trsuperobject.biblemap.put("b43_7_15", new trsuperobject(43,7,15,"Yahudiler şaşırdılar. ‹‹Bu adam hiç öğrenim görmediği halde, nasıl bu kadar bilgili olabilir?›› dediler."));
trsuperobject.biblemap.put("b43_7_16", new trsuperobject(43,7,16,"İsa onlara, ‹‹Benim öğretim benim değil, beni gönderenindir›› diye karşılık verdi."));
trsuperobject.biblemap.put("b43_7_17", new trsuperobject(43,7,17,"‹‹Eğer bir kimse Tanrının isteğini yerine getirmek istiyorsa, bu öğretinin Tanrıdan mı olduğunu, yoksa kendiliğimden mi konuştuğumu bilecektir."));
trsuperobject.biblemap.put("b43_7_18", new trsuperobject(43,7,18,"Kendiliğinden konuşan kendini yüceltmek ister, ama kendisini göndereni yüceltmek isteyen doğrudur ve Onda haksızlık yoktur."));
trsuperobject.biblemap.put("b43_7_19", new trsuperobject(43,7,19,"Musa size Kutsal Yasayı vermedi mi? Yine de hiçbiriniz Yasayı yerine getirmiyor. Neden beni öldürmek istiyorsunuz?››"));
trsuperobject.biblemap.put("b43_7_20", new trsuperobject(43,7,20,"Kalabalık, ‹‹Cin çarpmış seni!›› dedi. ‹‹Seni öldürmek isteyen kim?››"));
trsuperobject.biblemap.put("b43_7_21", new trsuperobject(43,7,21,"İsa, ‹‹Ben bir mucize yaptım, hepiniz şaşkına döndünüz›› diye yanıt verdi."));
trsuperobject.biblemap.put("b43_7_22", new trsuperobject(43,7,22,"‹‹Musa size sünneti buyurduğu için -aslında bu, Musadan değil, atalarınızdan kalmadır- Şabat Günü birini sünnet edersiniz."));
trsuperobject.biblemap.put("b43_7_23", new trsuperobject(43,7,23,"Musanın Yasası bozulmasın diye Şabat Günü biri sünnet ediliyor da, Şabat Günü bir adamı tamamen iyileştirdim diye bana neden kızıyorsunuz?"));
trsuperobject.biblemap.put("b43_7_24", new trsuperobject(43,7,24,"Dış görünüşe göre yargılamayın, yargınız adil olsun.››"));
trsuperobject.biblemap.put("b43_7_25", new trsuperobject(43,7,25,"Yeruşalimlilerin bazıları, ‹‹Öldürmek istedikleri adam bu değil mi?›› diyorlardı."));
trsuperobject.biblemap.put("b43_7_26", new trsuperobject(43,7,26,"‹‹Bakın, açıkça konuşuyor, Ona bir şey demiyorlar. Yoksa önderler Onun Mesih olduğunu gerçekten kabul ettiler mi?"));
trsuperobject.biblemap.put("b43_7_27", new trsuperobject(43,7,27,"Ama biz bu adamın nereden geldiğini biliyoruz. Oysa Mesih geldiği zaman Onun nereden geldiğini kimse bilmeyecek.››"));
trsuperobject.biblemap.put("b43_7_28", new trsuperobject(43,7,28,"O sırada tapınakta öğreten İsa yüksek sesle şöyle dedi: ‹‹Hem beni tanıyorsunuz, hem de nereden olduğumu biliyorsunuz! Ben kendiliğimden gelmedim. Beni gönderen gerçektir. Onu siz tanımıyorsunuz."));
trsuperobject.biblemap.put("b43_7_29", new trsuperobject(43,7,29,"Ben Onu tanırım. Çünkü ben Ondanım, beni O gönderdi.››"));
trsuperobject.biblemap.put("b43_7_30", new trsuperobject(43,7,30,"Bunun üzerine Onu yakalamak istediler, ama kimse Ona el sürmedi. Çünkü Onun saati henüz gelmemişti."));
trsuperobject.biblemap.put("b43_7_31", new trsuperobject(43,7,31,"Halktan birçok kişi ise Ona iman etti. ‹‹Mesih gelince, bunun yaptıklarından daha mı çok mucize yapacak?›› diyorlardı."));
trsuperobject.biblemap.put("b43_7_32", new trsuperobject(43,7,32,"Ferisiler halkın İsa hakkında böyle fısıldaştığını duydular. Başkâhinler ve Ferisiler Onu yakalamak için görevliler gönderdiler."));
trsuperobject.biblemap.put("b43_7_33", new trsuperobject(43,7,33,"İsa, ‹‹Kısa bir süre daha sizinleyim›› dedi, ‹‹Sonra beni gönderene gideceğim."));
trsuperobject.biblemap.put("b43_7_34", new trsuperobject(43,7,34,"Beni arayacaksınız ama bulamayacaksınız. Ve benim bulunduğum yere siz gelemezsiniz.››"));
trsuperobject.biblemap.put("b43_7_35", new trsuperobject(43,7,35,"Bunun üzerine Yahudiler birbirlerine, ‹‹Bu adam nereye gidecek de biz Onu bulamayacağız?›› dediler. ‹‹Yoksa Grekler arasında dağılmış olanlara gidip Greklere mi öğretecek?"));
trsuperobject.biblemap.put("b43_7_36", new trsuperobject(43,7,36,"‹Beni arayacaksınız ama bulamayacaksınız. Ve benim bulunduğum yere siz gelemezsiniz› diyor. Ne demek istiyor?››"));
trsuperobject.biblemap.put("b43_7_37", new trsuperobject(43,7,37,"Bayramın son ve en önemli günü İsa ayağa kalktı, yüksek sesle şöyle dedi: ‹‹Bir kimse susamışsa bana gelsin, içsin."));
trsuperobject.biblemap.put("b43_7_38", new trsuperobject(43,7,38,"Kutsal Yazıda dendiği gibi, bana iman edenin ‹içinden diri su ırmakları akacaktır.››"));
trsuperobject.biblemap.put("b43_7_39", new trsuperobject(43,7,39,"Bunu, kendisine iman edenlerin alacağı Ruhla ilgili olarak söylüyordu. Ruh henüz verilmemişti. Çünkü İsa henüz yüceltilmemişti."));
trsuperobject.biblemap.put("b43_7_40", new trsuperobject(43,7,40,"Halktan bazıları bu sözleri işitince, ‹‹Gerçekten beklediğimiz peygamber budur›› dediler."));
trsuperobject.biblemap.put("b43_7_41", new trsuperobject(43,7,41,"Bazıları da, ‹‹Bu Mesihtir›› diyorlardı. Başkaları ise, ‹‹Olamaz! Mesih Celileden mi gelecek?›› dediler."));
trsuperobject.biblemap.put("b43_7_42", new trsuperobject(43,7,42,"‹‹Kutsal Yazıda, ‹Mesih, Davutun soyundan, Davutun yaşadığı Beytlehem Kentinden gelecek› denmemiş midir?››"));
trsuperobject.biblemap.put("b43_7_43", new trsuperobject(43,7,43,"Böylece İsadan dolayı halk arasında ayrılık doğdu."));
trsuperobject.biblemap.put("b43_7_44", new trsuperobject(43,7,44,"Bazıları Onu yakalamak istedilerse de, kimse Ona el sürmedi."));
trsuperobject.biblemap.put("b43_7_45", new trsuperobject(43,7,45,"Görevliler geri dönünce, başkâhinlerle Ferisiler, ‹‹Niçin Onu getirmediniz?›› diye sordular."));
trsuperobject.biblemap.put("b43_7_46", new trsuperobject(43,7,46,"Görevliler, ‹‹Hiç kimse hiçbir zaman bu adamın konuştuğu gibi konuşmamıştır›› karşılığını verdiler."));
trsuperobject.biblemap.put("b43_7_47", new trsuperobject(43,7,47,"Ferisiler, ‹‹Yoksa siz de mi aldandınız?›› dediler."));
trsuperobject.biblemap.put("b43_7_48", new trsuperobject(43,7,48,"‹‹Önderlerden ya da Ferisilerden Ona iman eden oldu mu hiç?"));
trsuperobject.biblemap.put("b43_7_49", new trsuperobject(43,7,49,"Kutsal Yasayı bilmeyen bu halk lanetlidir.››"));
trsuperobject.biblemap.put("b43_7_5-051", new trsuperobject(43,7,5-051,"İçlerinden biri, daha önce İsaya gelen Nikodim, onlara şöyle dedi: ‹‹Yasamıza göre, bir adamı dinlemeden, ne yaptığını öğrenmeden onu yargılamak doğru mu?››"));
trsuperobject.biblemap.put("b43_7_52", new trsuperobject(43,7,52,"Ona, ‹‹Yoksa sen de mi Celiledensin?›› diye karşılık verdiler. ‹‹Araştır, bak, Celileden peygamber çıkmaz.››"));
trsuperobject.biblemap.put("b43_7_53", new trsuperobject(43,7,53,"Bundan sonra herkes evine gitti."));
trsuperobject.biblemap.put("b43_8_1", new trsuperobject(43,8,1,"İsa ise Zeytin Dağına gitti."));
trsuperobject.biblemap.put("b43_8_2", new trsuperobject(43,8,2,"Ertesi sabah erkenden yine tapınağa döndü. Bütün halk Onun yanına geliyordu. O da oturup onlara öğretmeye başladı."));
trsuperobject.biblemap.put("b43_8_3-4", new trsuperobject(43,8,3-4,"Din bilginleri ve Ferisiler, zina ederken yakalanmış bir kadın getirdiler. Kadını orta yere çıkararak İsaya, ‹‹Öğretmen, bu kadın tam zina ederken yakalandı›› dediler."));
trsuperobject.biblemap.put("b43_8_5", new trsuperobject(43,8,5,"‹‹Musa, Yasada bize böyle kadınların taşlanmasını buyurdu, sen ne dersin?››"));
trsuperobject.biblemap.put("b43_8_6", new trsuperobject(43,8,6,"Bunları İsayı denemek amacıyla söylüyorlardı; Onu suçlayabilmek için bir neden arıyorlardı. İsa eğilmiş, parmağıyla toprağa yazı yazıyordu."));
trsuperobject.biblemap.put("b43_8_7", new trsuperobject(43,8,7,"Durmadan aynı soruyu sormaları üzerine doğruldu ve, ‹‹İçinizde kim günahsızsa, ilk taşı o atsın!›› dedi."));
trsuperobject.biblemap.put("b43_8_8", new trsuperobject(43,8,8,"Sonra yine eğildi, toprağa yazmaya başladı."));
trsuperobject.biblemap.put("b43_8_9", new trsuperobject(43,8,9,"Bunu işittikleri zaman, başta yaşlılar olmak üzere, birer birer dışarı çıkıp İsayı yalnız bıraktılar. Kadın ise orta yerde duruyordu."));
trsuperobject.biblemap.put("b43_8_10", new trsuperobject(43,8,10,"İsa doğrulup ona, ‹‹Kadın, nerede onlar? Hiçbiri seni yargılamadı mı?›› diye sordu."));
trsuperobject.biblemap.put("b43_8_11", new trsuperobject(43,8,11,"Kadın, ‹‹Hiçbiri, Efendim›› dedi. İsa, ‹‹Ben de seni yargılamıyorum›› dedi. ‹‹Git, artık bundan sonra günah işleme!››"));
trsuperobject.biblemap.put("b43_8_12", new trsuperobject(43,8,12,"İsa yine halka seslenip şöyle dedi: ‹‹Ben dünyanın ışığıyım. Benim ardımdan gelen, asla karanlıkta yürümez, yaşam ışığına sahip olur.››"));
trsuperobject.biblemap.put("b43_8_13", new trsuperobject(43,8,13,"Ferisiler, ‹‹Sen kendin için tanıklık ediyorsun, tanıklığın geçerli değil›› dediler."));
trsuperobject.biblemap.put("b43_8_14", new trsuperobject(43,8,14,"İsa onlara şu karşılığı verdi: ‹‹Kendim için tanıklık etsem bile tanıklığım geçerlidir. Çünkü nereden geldiğimi ve nereye gideceğimi biliyorum. Oysa siz nereden geldiğimi, nereye gideceğimi bilmiyorsunuz."));
trsuperobject.biblemap.put("b43_8_15", new trsuperobject(43,8,15,"Siz insan gözüyle yargılıyorsunuz. Ben kimseyi yargılamam."));
trsuperobject.biblemap.put("b43_8_16", new trsuperobject(43,8,16,"Yargılasam bile benim yargım doğrudur. Çünkü ben yalnız değilim, ben ve beni gönderen Baba, birlikte yargılarız."));
trsuperobject.biblemap.put("b43_8_17", new trsuperobject(43,8,17,"Yasanızda da, ‹İki kişinin tanıklığı geçerlidir› diye yazılmıştır."));
trsuperobject.biblemap.put("b43_8_18", new trsuperobject(43,8,18,"Kendim için tanıklık eden bir ben varım, bir de beni gönderen Baba benim için tanıklık ediyor.››"));
trsuperobject.biblemap.put("b43_8_19", new trsuperobject(43,8,19,"O zaman Ona, ‹‹Baban nerede?›› diye sordular. İsa şu karşılığı verdi: ‹‹Siz ne beni tanırsınız, ne de Babamı. Beni tanısaydınız, Babamı da tanırdınız.››"));
trsuperobject.biblemap.put("b43_8_20", new trsuperobject(43,8,20,"İsa bu sözleri tapınakta öğretirken, bağış toplanan yerde söyledi. Kimse Onu yakalamadı. Çünkü saati henüz gelmemişti."));
trsuperobject.biblemap.put("b43_8_21", new trsuperobject(43,8,21,"İsa yine onlara, ‹‹Ben gidiyorum. Beni arayacaksınız ve günahınızın içinde öleceksiniz. Benim gideceğim yere siz gelemezsiniz›› dedi."));
trsuperobject.biblemap.put("b43_8_22", new trsuperobject(43,8,22,"Yahudi yetkililer, ‹‹Yoksa kendini mi öldürecek?›› dediler. ‹‹Çünkü, ‹Benim gideceğim yere siz gelemezsiniz› diyor.››"));
trsuperobject.biblemap.put("b43_8_23", new trsuperobject(43,8,23,"İsa onlara, ‹‹Siz aşağıdansınız, ben yukarıdanım›› dedi. ‹‹Siz bu dünyadansınız, ben bu dünyadan değilim."));
trsuperobject.biblemap.put("b43_8_24", new trsuperobject(43,8,24,"İşte bu nedenle size, ‹Günahlarınızın içinde öleceksiniz› dedim. Benim O olduğuma iman etmezseniz, günahlarınızın içinde öleceksiniz.››"));
trsuperobject.biblemap.put("b43_8_25", new trsuperobject(43,8,25,"Ona, ‹‹Sen kimsin?›› diye sordular. İsa, ‹‹Başlangıçtan beri size ne söyledimse, Oyum›› dedi."));
trsuperobject.biblemap.put("b43_8_26", new trsuperobject(43,8,26,"‹‹Sizinle ilgili söyleyecek ve sizleri yargılayacak çok şeyim var. Beni gönderen gerçektir. Ben Ondan işittiklerimi dünyaya bildiriyorum.››"));
trsuperobject.biblemap.put("b43_8_27", new trsuperobject(43,8,27,"İsanın kendilerine Babadan söz ettiğini anlamadılar."));
trsuperobject.biblemap.put("b43_8_28", new trsuperobject(43,8,28,"Bu nedenle İsa şöyle dedi: ‹‹İnsanoğlunu yukarı kaldırdığınız zaman benim O olduğumu, kendiliğimden hiçbir şey yapmadığımı, ama tıpkı Babanın bana öğrettiği gibi konuştuğumu anlayacaksınız."));
trsuperobject.biblemap.put("b43_8_29", new trsuperobject(43,8,29,"Beni gönderen benimledir, O beni yalnız bırakmadı. Çünkü ben her zaman Onu hoşnut edeni yaparım.››"));
trsuperobject.biblemap.put("b43_8_30", new trsuperobject(43,8,30,"Bu sözler üzerine birçokları Ona iman etti."));
trsuperobject.biblemap.put("b43_8_31-32", new trsuperobject(43,8,31-32,"İsa kendisine iman etmiş olan Yahudilere, ‹‹Eğer benim sözüme bağlı kalırsanız, gerçekten öğrencilerim olursunuz. Gerçeği bileceksiniz ve gerçek sizi özgür kılacak›› dedi."));
trsuperobject.biblemap.put("b43_8_33", new trsuperobject(43,8,33,"‹‹Biz İbrahimin soyundanız›› diye karşılık verdiler, ‹‹Hiçbir zaman kimseye kölelik etmedik. Nasıl oluyor da sen, ‹Özgür olacaksınız› diyorsun?››"));
trsuperobject.biblemap.put("b43_8_34", new trsuperobject(43,8,34,"İsa, ‹‹Size doğrusunu söyleyeyim, günah işleyen herkes günahın kölesidir›› dedi."));
trsuperobject.biblemap.put("b43_8_35", new trsuperobject(43,8,35,"‹‹Köle ev halkının sürekli bir üyesi değildir, ama oğul sürekli üyesidir."));
trsuperobject.biblemap.put("b43_8_36", new trsuperobject(43,8,36,"Bunun için, Oğul sizi özgür kılarsa, gerçekten özgür olursunuz."));
trsuperobject.biblemap.put("b43_8_37", new trsuperobject(43,8,37,"İbrahimin soyundan olduğunuzu biliyorum. Yine de beni öldürmek istiyorsunuz. Çünkü yüreğinizde sözüme yer vermiyorsunuz."));
trsuperobject.biblemap.put("b43_8_38", new trsuperobject(43,8,38,"Ben Babamın yanında gördüklerimi söylüyorum, siz de babanızdan işittiklerinizi yapıyorsunuz.››"));
trsuperobject.biblemap.put("b43_8_39", new trsuperobject(43,8,39,"‹‹Bizim babamız İbrahimdir›› diye karşılık verdiler. İsa, ‹‹İbrahimin çocukları olsaydınız, İbrahimin yaptıklarını yapardınız›› dedi."));
trsuperobject.biblemap.put("b43_8_40", new trsuperobject(43,8,40,"‹‹Ama şimdi beni -Tanrıdan işittiği gerçeği sizlere bildireni- öldürmek istiyorsunuz. İbrahim bunu yapmadı."));
trsuperobject.biblemap.put("b43_8_41", new trsuperobject(43,8,41,"Siz babanızın yaptıklarını yapıyorsunuz.›› ‹‹Biz zinadan doğmadık. Bir tek Babamız var, o da Tanrıdır›› dediler."));
trsuperobject.biblemap.put("b43_8_42", new trsuperobject(43,8,42,"İsa, ‹‹Tanrı Babanız olsaydı, beni severdiniz›› dedi. ‹‹Çünkü ben Tanrıdan çıkıp geldim. Kendiliğimden gelmedim, beni O gönderdi."));
trsuperobject.biblemap.put("b43_8_43", new trsuperobject(43,8,43,"Söylediklerimi neden anlamıyorsunuz? Benim sözümü dinlemeye dayanamıyorsunuz da ondan."));
trsuperobject.biblemap.put("b43_8_44", new trsuperobject(43,8,44,"Siz babanız İblistensiniz ve babanızın arzularını yerine getirmek istiyorsunuz. O başlangıçtan beri katildi. Gerçeğe bağlı kalmadı. Çünkü onda gerçek yoktur. Yalan söylemesi doğaldır. Çünkü o yalancıdır ve yalanın babasıdır."));
trsuperobject.biblemap.put("b43_8_45", new trsuperobject(43,8,45,"Ama ben gerçeği söylüyorum. İşte bunun için bana iman etmiyorsunuz."));
trsuperobject.biblemap.put("b43_8_46", new trsuperobject(43,8,46,"Hanginiz bana günahlı olduğumu kanıtlayabilir? Gerçeği söylüyorsam, niçin bana iman etmiyorsunuz?"));
trsuperobject.biblemap.put("b43_8_47", new trsuperobject(43,8,47,"Tanrıdan olan, Tanrının sözlerini dinler. İşte siz Tanrıdan olmadığınız için dinlemiyorsunuz.››"));
trsuperobject.biblemap.put("b43_8_48", new trsuperobject(43,8,48,"Yahudiler Ona şu karşılığı verdiler: ‹‹Sen, cin çarpmış bir Samiriyelisin› demekte haklı değil miyiz?››"));
trsuperobject.biblemap.put("b43_8_49", new trsuperobject(43,8,49,"İsa, ‹‹Beni cin çarpmadı›› dedi. ‹‹Ben Babamı onurlandırıyorum, ama siz beni aşağılıyorsunuz."));
trsuperobject.biblemap.put("b43_8_50", new trsuperobject(43,8,50,"Ben kendimi yüceltmek istemiyorum, ama bunu isteyen ve yargılayan biri vardır."));
trsuperobject.biblemap.put("b43_8_51", new trsuperobject(43,8,51,"Size doğrusunu söyleyeyim, bir kimse sözüme uyarsa, ölümü asla görmeyecektir.››"));
trsuperobject.biblemap.put("b43_8_52", new trsuperobject(43,8,52,"Yahudiler, ‹‹Seni cin çarptığını şimdi anlıyoruz›› dediler. ‹‹İbrahim öldü, peygamberler de öldü. Oysa sen, ‹Bir kimse sözüme uyarsa, ölümü asla tatmayacaktır› diyorsun."));
trsuperobject.biblemap.put("b43_8_53", new trsuperobject(43,8,53,"Yoksa sen babamız İbrahimden üstün müsün? O öldü, peygamberler de öldü. Sen kendini kim sanıyorsun?››"));
trsuperobject.biblemap.put("b43_8_54", new trsuperobject(43,8,54,"İsa şu karşılığı verdi: ‹‹Eğer ben kendimi yüceltirsem, yüceliğim hiçtir. Beni yücelten, ‹Tanrımız› diye çağırdığınız Babamdır."));
trsuperobject.biblemap.put("b43_8_55", new trsuperobject(43,8,55,"Siz Onu tanımıyorsunuz, ama ben tanıyorum. Onu tanımadığımı söylersem, sizin gibi yalancı olurum. Ama ben Onu tanıyor ve sözüne uyuyorum."));
trsuperobject.biblemap.put("b43_8_56", new trsuperobject(43,8,56,"Babanız İbrahim günümü göreceği için sevinçle coşmuştu. Gördü ve sevindi.››"));
trsuperobject.biblemap.put("b43_8_57", new trsuperobject(43,8,57,"Yahudiler, ‹‹Sen daha elli yaşında bile değilsin. İbrahimi de mi gördün?›› dediler."));
trsuperobject.biblemap.put("b43_8_58", new trsuperobject(43,8,58,"İsa, ‹‹Size doğrusunu söyleyeyim, İbrahim doğmadan önce ben varım›› dedi."));
trsuperobject.biblemap.put("b43_8_59", new trsuperobject(43,8,59,"O zaman İsa'yı taşlamak için yerden taş aldılar, ama O gizlenip tapınaktan çıktı."));
trsuperobject.biblemap.put("b43_9_1", new trsuperobject(43,9,1,"İsa yolda giderken doğuştan kör bir adam gördü."));
trsuperobject.biblemap.put("b43_9_2", new trsuperobject(43,9,2,"Öğrencileri İsaya, ‹‹Rabbî, kim günah işledi de bu adam kör doğdu? Kendisi mi, yoksa annesi babası mı?›› diye sordular."));
trsuperobject.biblemap.put("b43_9_3", new trsuperobject(43,9,3,"İsa şu yanıtı verdi: ‹‹Ne kendisi, ne de annesi babası günah işledi. Tanrının işleri onun yaşamında görülsün diye kör doğdu."));
trsuperobject.biblemap.put("b43_9_4", new trsuperobject(43,9,4,"Beni gönderenin işlerini vakit daha gündüzken yapmalıyız. Gece geliyor, o zaman kimse çalışamaz."));
trsuperobject.biblemap.put("b43_9_5", new trsuperobject(43,9,5,"Dünyada olduğum sürece dünyanın ışığı Benim.››"));
trsuperobject.biblemap.put("b43_9_6", new trsuperobject(43,9,6,"Bu sözleri söyledikten sonra yere tükürdü, tükürükle çamur yaptı ve çamuru adamın gözlerine sürdü."));
trsuperobject.biblemap.put("b43_9_7", new trsuperobject(43,9,7,"Adama, ‹‹Git, Şiloah Havuzunda yıkan›› dedi. Şiloah, gönderilmiş anlamına gelir. Adam gidip yıkandı, gözleri açılmış olarak döndü."));
trsuperobject.biblemap.put("b43_9_8", new trsuperobject(43,9,8,"Komşuları ve onu daha önce dilenirken görenler, ‹‹Oturup dilenen adam değil mi bu?›› dediler."));
trsuperobject.biblemap.put("b43_9_9", new trsuperobject(43,9,9,"Kimi, ‹‹Evet, odur›› dedi, kimi de ‹‹Hayır, ama ona benziyor›› dedi. Kendisi ise, ‹‹Ben oyum›› dedi."));
trsuperobject.biblemap.put("b43_9_10", new trsuperobject(43,9,10,"‹‹Öyleyse, gözlerin nasıl açıldı?›› diye sordular."));
trsuperobject.biblemap.put("b43_9_11", new trsuperobject(43,9,11,"O da şöyle yanıt verdi: ‹‹İsa adındaki adam çamur yapıp gözlerime sürdü ve bana, ‹Şiloaha git, yıkan› dedi. Ben de gidip yıkandım ve gözlerim açıldı.››"));
trsuperobject.biblemap.put("b43_9_12", new trsuperobject(43,9,12,"Ona, ‹‹Nerede O?›› diye sordular. ‹‹Bilmiyorum›› dedi."));
trsuperobject.biblemap.put("b43_9_13", new trsuperobject(43,9,13,"Eskiden kör olan adamı Ferisilerin yanına götürdüler."));
trsuperobject.biblemap.put("b43_9_14", new trsuperobject(43,9,14,"İsanın çamur yapıp adamın gözlerini açtığı gün Şabat Günüydü."));
trsuperobject.biblemap.put("b43_9_15", new trsuperobject(43,9,15,"Bu nedenle Ferisiler de adama gözlerinin nasıl açıldığını sordular. O da, ‹‹İsa gözlerime çamur sürdü, yıkandım ve şimdi görüyorum›› dedi."));
trsuperobject.biblemap.put("b43_9_16", new trsuperobject(43,9,16,"Bunun üzerine Ferisilerin bazıları, ‹‹Bu adam Tanrıdan değildir›› dediler. ‹‹Çünkü Şabat Gününü tutmuyor.›› Ama başkaları, ‹‹Günahkâr bir adam nasıl bu tür belirtiler gerçekleştirebilir?›› dediler. Böylece aralarında ayrılık doğdu."));
trsuperobject.biblemap.put("b43_9_17", new trsuperobject(43,9,17,"Eskiden kör olan adama yine sordular: ‹‹Senin gözlerini açtığına göre, Onun hakkında sen ne diyorsun?›› Adam, ‹‹O bir peygamberdir›› dedi."));
trsuperobject.biblemap.put("b43_9_18", new trsuperobject(43,9,18,"Yahudi yetkililer, gözleri açılan adamın annesiyle babasını çağırmadan onun daha önce kör olduğuna ve gözlerinin açıldığına inanmadılar."));
trsuperobject.biblemap.put("b43_9_19", new trsuperobject(43,9,19,"Onlara, ‹‹Kör doğdu dediğiniz oğlunuz bu mu? Peki, şimdi nasıl görüyor?›› diye sordular."));
trsuperobject.biblemap.put("b43_9_20", new trsuperobject(43,9,20,"Adamın annesiyle babası şu karşılığı verdiler: ‹‹Bunun bizim oğlumuz olduğunu ve kör doğduğunu biliyoruz."));
trsuperobject.biblemap.put("b43_9_21", new trsuperobject(43,9,21,"Ama şimdi nasıl gördüğünü, gözlerini kimin açtığını bilmiyoruz, ona sorun. Ergin yaştadır, kendisi için kendisi konuşsun.››"));
trsuperobject.biblemap.put("b43_9_22", new trsuperobject(43,9,22,"Yahudi yetkililerden korktukları için böyle konuştular. Çünkü yetkililer, İsanın Mesih olduğunu açıkça söyleyeni havra dışı etmek için aralarında sözbirliği etmişlerdi."));
trsuperobject.biblemap.put("b43_9_23", new trsuperobject(43,9,23,"Bundan dolayı adamın annesiyle babası, ‹‹Ergin yaştadır, ona sorun›› dediler."));
trsuperobject.biblemap.put("b43_9_24", new trsuperobject(43,9,24,"Eskiden kör olan adamı ikinci kez çağırıp, ‹‹Tanrı hakkı için doğruyu söyle›› dediler, ‹‹Biz bu adamın günahkâr olduğunu biliyoruz.››"));
trsuperobject.biblemap.put("b43_9_25", new trsuperobject(43,9,25,"O da şöyle yanıt verdi: ‹‹Onun günahkâr olup olmadığını bilmiyorum. Bildiğim bir şey var, kördüm, şimdi görüyorum.››"));
trsuperobject.biblemap.put("b43_9_26", new trsuperobject(43,9,26,"O zaman ona, ‹‹Sana ne yaptı? Gözlerini nasıl açtı?›› dediler."));
trsuperobject.biblemap.put("b43_9_27", new trsuperobject(43,9,27,"Onlara, ‹‹Size demin söyledim, ama dinlemediniz›› dedi. ‹‹Niçin yeniden işitmek istiyorsunuz? Yoksa siz de mi Onun öğrencileri olmak niyetindesiniz?››"));
trsuperobject.biblemap.put("b43_9_28", new trsuperobject(43,9,28,"Adama söverek, ‹‹Onun öğrencisi sensin!›› dediler. ‹‹Biz Musanın öğrencileriyiz."));
trsuperobject.biblemap.put("b43_9_29", new trsuperobject(43,9,29,"Tanrının Musayla konuştuğunu biliyoruz. Ama bu adamın nereden geldiğini bilmiyoruz.››"));
trsuperobject.biblemap.put("b43_9_30", new trsuperobject(43,9,30,"Adam onlara şu karşılığı verdi: ‹‹Şaşılacak şey! Onun nereden geldiğini bilmiyorsunuz, ama gözlerimi O açtı."));
trsuperobject.biblemap.put("b43_9_31", new trsuperobject(43,9,31,"Tanrının, günahkârları dinlemediğini biliriz. Ama Tanrı, kendisine tapan ve isteğini yerine getiren kişiyi dinler."));
trsuperobject.biblemap.put("b43_9_32", new trsuperobject(43,9,32,"Dünya var olalı, bir kimsenin doğuştan kör olan birinin gözlerini açtığı duyulmamıştır."));
trsuperobject.biblemap.put("b43_9_33", new trsuperobject(43,9,33,"Bu adam Tanrıdan olmasaydı, hiçbir şey yapamazdı.››"));
trsuperobject.biblemap.put("b43_9_34", new trsuperobject(43,9,34,"Onlar buna karşılık, ‹‹Tamamen günah içinde doğdun, sen mi bize ders vereceksin?›› diyerek onu dışarı attılar."));
trsuperobject.biblemap.put("b43_9_35", new trsuperobject(43,9,35,"İsa adamı kovduklarını duydu. Onu bularak, ‹‹Sen İnsanoğluna iman ediyor musun?›› diye sordu."));
trsuperobject.biblemap.put("b43_9_36", new trsuperobject(43,9,36,"Adam şu yanıtı verdi: ‹‹Efendim, O kimdir? Söyle de kendisine iman edeyim.››"));
trsuperobject.biblemap.put("b43_9_37", new trsuperobject(43,9,37,"İsa, ‹‹Onu gördün. Şimdi seninle konuşan Odur›› dedi."));
trsuperobject.biblemap.put("b43_9_38", new trsuperobject(43,9,38,"Adam, ‹‹Rab, iman ediyorum!›› diyerek İsaya tapındı."));
trsuperobject.biblemap.put("b43_9_39", new trsuperobject(43,9,39,"İsa, ‹‹Görmeyenler görsün, görenler kör olsun diye yargıçlık etmek üzere bu dünyaya geldim›› dedi."));
trsuperobject.biblemap.put("b43_9_40", new trsuperobject(43,9,40,"Onun yanında bulunan bazı Ferisiler bu sözleri işitince, ‹‹Yoksa biz de mi körüz?›› diye sordular."));
trsuperobject.biblemap.put("b43_9_41", new trsuperobject(43,9,41,"İsa, ‹‹Kör olsaydınız günahınız olmazdı›› dedi, ‹‹Ama şimdi, ‹Görüyoruz› dediğiniz için günahınız duruyor.››"));
trsuperobject.biblemap.put("b43_10_1", new trsuperobject(43,10,1,"‹‹Size doğrusunu söyleyeyim, koyun ağılına kapıdan girmeyip başka yoldan giren kişi hırsız ve hayduttur."));
trsuperobject.biblemap.put("b43_10_2", new trsuperobject(43,10,2,"Kapıdan giren ise koyunların çobanıdır."));
trsuperobject.biblemap.put("b43_10_3", new trsuperobject(43,10,3,"Kapıyı bekleyen ona kapıyı açar. Koyunlar çobanın sesini işitirler, o da kendi koyunlarını adlarıyla çağırır ve onları dışarı götürür."));
trsuperobject.biblemap.put("b43_10_4", new trsuperobject(43,10,4,"Kendi koyunlarının hepsini dışarı çıkarınca önlerinden gider, koyunlar da onu izler. Çünkü onun sesini tanırlar."));
trsuperobject.biblemap.put("b43_10_5", new trsuperobject(43,10,5,"Bir yabancının peşinden gitmezler, ondan kaçarlar. Çünkü yabancıların sesini tanımazlar.››"));
trsuperobject.biblemap.put("b43_10_6", new trsuperobject(43,10,6,"İsa onlara bu örneği anlattıysa da, ne demek istediğini anlamadılar."));
trsuperobject.biblemap.put("b43_10_7", new trsuperobject(43,10,7,"Bunun için İsa yine, ‹‹Size doğrusunu söyleyeyim›› dedi, ‹‹Ben koyunların kapısıyım."));
trsuperobject.biblemap.put("b43_10_8", new trsuperobject(43,10,8,"Benden önce gelenlerin hepsi hırsız ve hayduttu, ama koyunlar onları dinlemedi."));
trsuperobject.biblemap.put("b43_10_9", new trsuperobject(43,10,9,"Kapı Benim. Bir kimse benim aracılığımla içeri girerse kurtulur. Girer, çıkar ve otlak bulur."));
trsuperobject.biblemap.put("b43_10_10", new trsuperobject(43,10,10,"Hırsız ancak çalıp öldürmek ve yok etmek için gelir. Bense insanlar yaşama, bol yaşama sahip olsunlar diye geldim."));
trsuperobject.biblemap.put("b43_10_11", new trsuperobject(43,10,11,"Ben iyi çobanım. İyi çoban koyunları uğruna canını verir."));
trsuperobject.biblemap.put("b43_10_12", new trsuperobject(43,10,12,"Koyunların çobanı ve sahibi olmayan ücretli adam, kurdun geldiğini görünce koyunları bırakıp kaçar. Kurt da onları kapar ve dağıtır."));
trsuperobject.biblemap.put("b43_10_13", new trsuperobject(43,10,13,"Adam kaçar. Çünkü ücretlidir ve koyunlar için kaygı duymaz."));
trsuperobject.biblemap.put("b43_10_14-15", new trsuperobject(43,10,14-15,"Ben iyi çobanım. Benimkileri tanırım. Baba beni tanıdığı, ben de Babayı tanıdığım gibi, benimkiler de beni tanır. Ben koyunlarımın uğruna canımı veririm."));
trsuperobject.biblemap.put("b43_10_16", new trsuperobject(43,10,16,"Bu ağıldan olmayan başka koyunlarım var. Onları da getirmeliyim. Benim sesimi işitecekler ve tek sürü, tek çoban olacak."));
trsuperobject.biblemap.put("b43_10_17", new trsuperobject(43,10,17,"Canımı, tekrar geri almak üzere veririm. Bunun için Baba beni sever."));
trsuperobject.biblemap.put("b43_10_18", new trsuperobject(43,10,18,"Canımı kimse benden alamaz; ben onu kendiliğimden veririm. Onu vermeye de tekrar geri almaya da yetkim var. Bu buyruğu Babamdan aldım.››"));
trsuperobject.biblemap.put("b43_10_19", new trsuperobject(43,10,19,"Bu sözlerden dolayı Yahudiler arasında yine ayrılık doğdu."));
trsuperobject.biblemap.put("b43_10_20", new trsuperobject(43,10,20,"Birçoğu, ‹‹Onu cin çarpmış, delidir. Niçin Onu dinliyorsunuz?›› diyordu."));
trsuperobject.biblemap.put("b43_10_21", new trsuperobject(43,10,21,"Başkaları ise, ‹‹Bunlar, cin çarpmış bir adamın sözleri değil›› dediler. ‹‹Cin, körlerin gözlerini açabilir mi?››"));
trsuperobject.biblemap.put("b43_10_22", new trsuperobject(43,10,22,"O sırada Yeruşalimde Tapınağın Açılışını Anma Bayramı kutlanıyordu. Mevsim kıştı."));
trsuperobject.biblemap.put("b43_10_23", new trsuperobject(43,10,23,"İsa tapınağın avlusunda, Süleymanın Eyvanında yürüyordu."));
trsuperobject.biblemap.put("b43_10_24", new trsuperobject(43,10,24,"Yahudi yetkililer Onun çevresini sararak, ‹‹Bizi daha ne kadar zaman kuşkuda bırakacaksın?›› dediler. ‹‹Eğer Mesih isen, bize açıkça söyle.››"));
trsuperobject.biblemap.put("b43_10_25", new trsuperobject(43,10,25,"İsa onlara şu karşılığı verdi: ‹‹Size söyledim, ama iman etmiyorsunuz. Babamın adıyla yaptığım işler bana tanıklık ediyor."));
trsuperobject.biblemap.put("b43_10_26", new trsuperobject(43,10,26,"Ama siz iman etmiyorsunuz. Çünkü benim koyunlarımdan değilsiniz."));
trsuperobject.biblemap.put("b43_10_27", new trsuperobject(43,10,27,"Koyunlarım sesimi işitir. Ben onları tanırım, onlar da beni izler."));
trsuperobject.biblemap.put("b43_10_28", new trsuperobject(43,10,28,"Onlara sonsuz yaşam veririm; asla mahvolmayacaklar. Onları hiç kimse elimden kapamaz."));
trsuperobject.biblemap.put("b43_10_29", new trsuperobject(43,10,29,"Onları bana veren Babam her şeyden üstündür. Onları Babanın elinden kapmaya kimsenin gücü yetmez."));
trsuperobject.biblemap.put("b43_10_30", new trsuperobject(43,10,30,"Ben ve Baba biriz.››"));
trsuperobject.biblemap.put("b43_10_31", new trsuperobject(43,10,31,"Yahudi yetkililer Onu taşlamak için yerden yine taş aldılar."));
trsuperobject.biblemap.put("b43_10_32", new trsuperobject(43,10,32,"İsa onlara, ‹‹Size Babadan kaynaklanan birçok iyi işler gösterdim›› dedi. ‹‹Bu işlerden hangisi için beni taşlıyorsunuz?››"));
trsuperobject.biblemap.put("b43_10_33", new trsuperobject(43,10,33,"Şöyle yanıt verdiler: ‹‹Seni iyi işlerden ötürü değil, küfür ettiğin için taşlıyoruz. İnsan olduğun halde Tanrı olduğunu ileri sürüyorsun.››"));
trsuperobject.biblemap.put("b43_10_34", new trsuperobject(43,10,34,"İsa şu karşılığı verdi: ‹‹Yasanızda, ‹Siz ilahlarsınız, dedim› diye yazılı değil mi?"));
trsuperobject.biblemap.put("b43_10_35", new trsuperobject(43,10,35,"Tanrı, kendilerine sözünü gönderdiği kimseleri ilahlar diye adlandırır. Kutsal Yazı da geçerliliğini yitirmez."));
trsuperobject.biblemap.put("b43_10_36", new trsuperobject(43,10,36,"Baba beni kendine ayırıp dünyaya gönderdi. Öyleyse ‹Tanrının Oğluyum› dediğim için bana nasıl ‹Küfür ediyorsun› dersiniz?"));
trsuperobject.biblemap.put("b43_10_37", new trsuperobject(43,10,37,"Eğer Babamın işlerini yapmıyorsam, bana iman etmeyin."));
trsuperobject.biblemap.put("b43_10_38", new trsuperobject(43,10,38,"Ama yapıyorsam, bana iman etmeseniz bile, yaptığım işlere iman edin. Öyle ki, Babanın bende, benim de Babada olduğumu bilesiniz ve anlayasınız.››"));
trsuperobject.biblemap.put("b43_10_39", new trsuperobject(43,10,39,"Onu yine yakalamaya çalıştılarsa da, ellerinden sıyrılıp kurtuldu."));
trsuperobject.biblemap.put("b43_10_40", new trsuperobject(43,10,40,"Tekrar Şeria Irmağının karşı yakasına, Yahyanın başlangıçta vaftiz ettiği yere gitti ve orada kaldı."));
trsuperobject.biblemap.put("b43_10_41", new trsuperobject(43,10,41,"Birçokları, ‹‹Yahya hiç mucize yapmadı, ama bu adam için söylediklerinin hepsi doğru çıktı›› diyerek İsaya geldiler."));
trsuperobject.biblemap.put("b43_10_42", new trsuperobject(43,10,42,"Ve orada birçokları O'na iman etti."));
trsuperobject.biblemap.put("b43_11_1", new trsuperobject(43,11,1,"Meryem ile kızkardeşi Martanın köyü olan Beytanyadan Lazar adında bir adam hastalanmıştı."));
trsuperobject.biblemap.put("b43_11_2", new trsuperobject(43,11,2,"Meryem, Rabbe güzel kokulu yağ sürüp saçlarıyla Onun ayaklarını silen kadındı. Hasta Lazar ise Meryemin kardeşiydi."));
trsuperobject.biblemap.put("b43_11_3", new trsuperobject(43,11,3,"İki kızkardeş İsaya, ‹‹Rab, sevdiğin kişi hasta›› diye haber gönderdiler."));
trsuperobject.biblemap.put("b43_11_4", new trsuperobject(43,11,4,"İsa bunu işitince, ‹‹Bu hastalık ölümle sonuçlanmayacak; Tanrının yüceliğine, Tanrı Oğlunun yüceltilmesine hizmet edecek›› dedi."));
trsuperobject.biblemap.put("b43_11_5", new trsuperobject(43,11,5,"İsa Martayı, kızkardeşini ve Lazarı severdi."));
trsuperobject.biblemap.put("b43_11_6-7", new trsuperobject(43,11,6-7,"Bu nedenle, Lazarın hasta olduğunu duyunca bulunduğu yerde iki gün daha kaldıktan sonra öğrencilere, ‹‹Yahudiyeye dönelim›› dedi."));
trsuperobject.biblemap.put("b43_11_8", new trsuperobject(43,11,8,"Öğrenciler, ‹‹Rabbî›› dediler, ‹‹Yahudi yetkililer demin seni taşlamaya kalkıştılar. Yine oraya mı gidiyorsun?››"));
trsuperobject.biblemap.put("b43_11_9", new trsuperobject(43,11,9,"İsa şu karşılığı verdi: ‹‹Günün on iki saati yok mu? Gündüz yürüyen sendelemez. Çünkü bu dünyanın ışığını görür."));
trsuperobject.biblemap.put("b43_11_10", new trsuperobject(43,11,10,"Oysa gece yürüyen sendeler. Çünkü kendisinde ışık yoktur.››"));
trsuperobject.biblemap.put("b43_11_11", new trsuperobject(43,11,11,"Bu sözleri söyledikten sonra, ‹‹Dostumuz Lazar uyudu›› diye ekledi, ‹‹Onu uyandırmaya gidiyorum.››"));
trsuperobject.biblemap.put("b43_11_12", new trsuperobject(43,11,12,"Öğrenciler, ‹‹Ya Rab›› dediler, ‹‹Uyuduysa iyileşecektir.››"));
trsuperobject.biblemap.put("b43_11_13", new trsuperobject(43,11,13,"İsa Lazarın ölümünden söz ediyordu, ama onlar olağan uykudan söz ettiğini sanmışlardı."));
trsuperobject.biblemap.put("b43_11_14", new trsuperobject(43,11,14,"Bunun üzerine İsa açıkça, ‹‹Lazar öldü›› dedi."));
trsuperobject.biblemap.put("b43_11_15", new trsuperobject(43,11,15,"‹‹İman edesiniz diye, orada bulunmadığıma sizin için seviniyorum. Şimdi onun yanına gidelim.››"));
trsuperobject.biblemap.put("b43_11_16", new trsuperobject(43,11,16,"‹‹İkiz›› diye anılan Tomas öbür öğrencilere, ‹‹Biz de gidelim, Onunla birlikte ölelim!›› dedi."));
trsuperobject.biblemap.put("b43_11_17", new trsuperobject(43,11,17,"İsa Beytanyaya yaklaşınca Lazarın dört gündür mezarda olduğunu öğrendi."));
trsuperobject.biblemap.put("b43_11_18", new trsuperobject(43,11,18,"Beytanya, Yeruşalime on beş ok atımı kadar uzaklıktaydı."));
trsuperobject.biblemap.put("b43_11_19", new trsuperobject(43,11,19,"Birçok Yahudi, kardeşlerini yitiren Martayla Meryemi avutmaya gelmişti."));
trsuperobject.biblemap.put("b43_11_20", new trsuperobject(43,11,20,"Marta İsanın geldiğini duyunca Onu karşılamaya çıktı, Meryem ise evde kaldı."));
trsuperobject.biblemap.put("b43_11_21", new trsuperobject(43,11,21,"Marta İsaya, ‹‹Ya Rab›› dedi, ‹‹Burada olsaydın, kardeşim ölmezdi."));
trsuperobject.biblemap.put("b43_11_22", new trsuperobject(43,11,22,"Şimdi bile, Tanrıdan ne dilersen Tanrının onu sana vereceğini biliyorum.››"));
trsuperobject.biblemap.put("b43_11_23", new trsuperobject(43,11,23,"İsa, ‹‹Kardeşin dirilecektir›› dedi."));
trsuperobject.biblemap.put("b43_11_24", new trsuperobject(43,11,24,"Marta, ‹‹Son gün, diriliş günü onun dirileceğini biliyorum›› dedi."));
trsuperobject.biblemap.put("b43_11_25", new trsuperobject(43,11,25,"İsa ona, ‹‹Diriliş ve yaşam Benim›› dedi. ‹‹Bana iman eden kişi ölse de yaşayacaktır."));
trsuperobject.biblemap.put("b43_11_26", new trsuperobject(43,11,26,"Yaşayan ve bana iman eden asla ölmeyecek. Buna iman ediyor musun?››"));
trsuperobject.biblemap.put("b43_11_27", new trsuperobject(43,11,27,"Marta, ‹‹Evet, ya Rab›› dedi. ‹‹Senin, dünyaya gelecek olan Tanrının Oğlu Mesih olduğuna iman ettim.››"));
trsuperobject.biblemap.put("b43_11_28", new trsuperobject(43,11,28,"Bunu söyledikten sonra gidip kızkardeşi Meryemi gizlice çağırdı. ‹‹Öğretmen burada, seni çağırıyor›› dedi."));
trsuperobject.biblemap.put("b43_11_29", new trsuperobject(43,11,29,"Meryem bunu işitince hemen kalkıp İsanın yanına gitti."));
trsuperobject.biblemap.put("b43_11_30", new trsuperobject(43,11,30,"İsa henüz köye varmamıştı, hâlâ Martanın kendisini karşıladığı yerdeydi."));
trsuperobject.biblemap.put("b43_11_31", new trsuperobject(43,11,31,"Meryemle birlikte evde bulunan ve kendisini teselli eden Yahudiler, onun hızla kalkıp dışarı çıktığını gördüler. Ağlamak için mezara gittiğini sanarak onu izlediler."));
trsuperobject.biblemap.put("b43_11_32", new trsuperobject(43,11,32,"Meryem İsanın bulunduğu yere vardı. Onu görünce ayaklarına kapanarak, ‹‹Ya Rab›› dedi, ‹‹Burada olsaydın, kardeşim ölmezdi.››"));
trsuperobject.biblemap.put("b43_11_33", new trsuperobject(43,11,33,"Meryemin ve onunla gelen Yahudilerin ağladığını gören İsanın ruhunu hüzün kapladı, yüreği sızladı."));
trsuperobject.biblemap.put("b43_11_34", new trsuperobject(43,11,34,"‹‹Onu nereye koydunuz?›› diye sordu. Ona, ‹‹Ya Rab, gel gör›› dediler."));
trsuperobject.biblemap.put("b43_11_35", new trsuperobject(43,11,35,"İsa ağladı."));
trsuperobject.biblemap.put("b43_11_36", new trsuperobject(43,11,36,"Yahudiler, ‹‹Bakın, onu ne kadar seviyormuş!›› dediler."));
trsuperobject.biblemap.put("b43_11_37", new trsuperobject(43,11,37,"Ama içlerinden bazıları, ‹‹Körün gözlerini açan bu kişi, Lazarın ölümünü de önleyemez miydi?›› dediler."));
trsuperobject.biblemap.put("b43_11_38", new trsuperobject(43,11,38,"İsa yine derinden hüzünlenerek mezara vardı. Mezar bir mağaraydı, girişinde de bir taş duruyordu."));
trsuperobject.biblemap.put("b43_11_39", new trsuperobject(43,11,39,"İsa, ‹‹Taşı çekin!›› dedi. Ölenin kızkardeşi Marta, ‹‹Rab, o artık kokmuştur, öleli dört gün oldu›› dedi."));
trsuperobject.biblemap.put("b43_11_40", new trsuperobject(43,11,40,"İsa ona, ‹‹Ben sana, ‹İman edersen Tanrının yüceliğini göreceksin› demedim mi?›› dedi."));
trsuperobject.biblemap.put("b43_11_41", new trsuperobject(43,11,41,"Bunun üzerine taşı çektiler. İsa gözlerini gökyüzüne kaldırarak şöyle dedi: ‹‹Baba, beni işittiğin için sana şükrediyorum."));
trsuperobject.biblemap.put("b43_11_42", new trsuperobject(43,11,42,"Beni her zaman işittiğini biliyordum. Ama bunu, çevrede duran halk için, beni senin gönderdiğine iman etsinler diye söyledim.››"));
trsuperobject.biblemap.put("b43_11_43", new trsuperobject(43,11,43,"Bunları söyledikten sonra yüksek sesle, ‹‹Lazar, dışarı çık!›› diye bağırdı."));
trsuperobject.biblemap.put("b43_11_44", new trsuperobject(43,11,44,"Ölü, elleri ayakları sargılarla bağlı, yüzü peşkirle sarılmış olarak dışarı çıktı. İsa oradakilere, ‹‹Onu çözün, bırakın gitsin›› dedi."));
trsuperobject.biblemap.put("b43_11_45", new trsuperobject(43,11,45,"O zaman, Meryeme gelen ve İsanın yaptıklarını gören Yahudilerin birçoğu İsaya iman etti."));
trsuperobject.biblemap.put("b43_11_46", new trsuperobject(43,11,46,"Ama içlerinden bazıları Ferisilere giderek İsanın yaptıklarını onlara bildirdiler."));
trsuperobject.biblemap.put("b43_11_47", new trsuperobject(43,11,47,"Bunun üzerine başkâhinler ve Ferisiler, Yüksek Kurulu toplayıp dediler ki, ‹‹Ne yapacağız? Bu adam birçok doğaüstü belirti gerçekleştiriyor."));
trsuperobject.biblemap.put("b43_11_48", new trsuperobject(43,11,48,"Böyle devam etmesine izin verirsek, herkes Ona iman edecek. Romalılar da gelip kutsal yerimizi ve ulusumuzu ortadan kaldıracaklar.››"));
trsuperobject.biblemap.put("b43_11_49", new trsuperobject(43,11,49,"İçlerinden biri, o yıl başkâhin olan Kayafa, ‹‹Hiçbir şey bilmiyorsunuz›› dedi."));
trsuperobject.biblemap.put("b43_11_50", new trsuperobject(43,11,50,"‹‹Bütün ulus yok olacağına, halk uğruna bir tek adamın ölmesi sizin için daha uygun. Bunu anlamıyor musunuz?››"));
trsuperobject.biblemap.put("b43_11_51-52", new trsuperobject(43,11,51-52,"Bunu kendiliğinden söylemiyordu. O yılın başkâhini olarak İsanın, ulusun uğruna, ve yalnız ulusun uğruna değil, Tanrının dağılmış çocuklarını toplayıp birleştirmek için de öleceğine ilişkin peygamberlikte bulunuyordu."));
trsuperobject.biblemap.put("b43_11_53", new trsuperobject(43,11,53,"Böylece o günden itibaren İsayı öldürmek için düzen kurmaya başladılar."));
trsuperobject.biblemap.put("b43_11_54", new trsuperobject(43,11,54,"Bu yüzden İsa artık Yahudiler arasında açıkça dolaşmaz oldu. Oradan ayrılarak çöle yakın bir yere, Efrayim denilen kente gitti. Öğrencileriyle birlikte orada kaldı."));
trsuperobject.biblemap.put("b43_11_55", new trsuperobject(43,11,55,"Yahudilerin Fısıh Bayramı yakındı. Taşradakilerin birçoğu bayramdan önce arınmak için Yeruşalime gitti."));
trsuperobject.biblemap.put("b43_11_56", new trsuperobject(43,11,56,"Orada İsayı arayıp durdular. Tapınaktayken birbirlerine, ‹‹Ne dersiniz, bayrama hiç gelmeyecek mi?›› diye soruyorlardı."));
trsuperobject.biblemap.put("b43_11_57", new trsuperobject(43,11,57,"Başkâhinlerle Ferisiler O'nu yakalayabilmek için, yerini bilenlerin haber vermesini buyurmuşlardı."));
trsuperobject.biblemap.put("b43_12_1", new trsuperobject(43,12,1,"İsa, Fısıh Bayramından altı gün önce, ölümden dirilttiği Lazarın bulunduğu Beytanyaya geldi."));
trsuperobject.biblemap.put("b43_12_2", new trsuperobject(43,12,2,"Orada kendisi için bir ziyafet düzenlediler. Marta hizmet ediyordu. İsayla birlikte sofrada oturanlardan biri de Lazardı."));
trsuperobject.biblemap.put("b43_12_3", new trsuperobject(43,12,3,"Meryem, çok değerli saf hintsümbülü yağından yarım litre kadar getirerek İsanın ayaklarına sürdü ve saçlarıyla ayaklarını sildi. Ev yağın güzel kokusuyla doldu."));
trsuperobject.biblemap.put("b43_12_4-5", new trsuperobject(43,12,4-5,"Ama öğrencilerinden biri, İsaya sonradan ihanet eden Yahuda İskariot, ‹‹Bu yağ neden üç yüz dinara satılıp parası yoksullara verilmedi?›› dedi."));
trsuperobject.biblemap.put("b43_12_6", new trsuperobject(43,12,6,"Bunu, yoksullarla ilgilendiği için değil, hırsız olduğu için söylüyordu. Ortak para kutusu ondaydı ve kutuya konulandan aşırıyordu."));
trsuperobject.biblemap.put("b43_12_7", new trsuperobject(43,12,7,"İsa, ‹‹Kadını rahat bırak›› dedi. ‹‹Bunu benim gömüleceğim gün için saklasın."));
trsuperobject.biblemap.put("b43_12_8", new trsuperobject(43,12,8,"Yoksullar her zaman aranızdadır, ama ben her zaman aranızda olmayacağım.››"));
trsuperobject.biblemap.put("b43_12_9", new trsuperobject(43,12,9,"Yahudilerden büyük bir kalabalık İsanın Beytanyada bulunduğunu öğrendi ve yalnız İsa için değil, Onun ölümden dirilttiği Lazarı da görmek için oraya geldi."));
trsuperobject.biblemap.put("b43_12_10", new trsuperobject(43,12,10,"Başkâhinler ise Lazarı da öldürmeyi tasarladılar."));
trsuperobject.biblemap.put("b43_12_11", new trsuperobject(43,12,11,"Çünkü onun yüzünden birçok Yahudi gidip İsaya iman ediyordu."));
trsuperobject.biblemap.put("b43_12_12", new trsuperobject(43,12,12,"Ertesi gün, bayramı kutlamaya gelen büyük kalabalık İsanın Yeruşalime gelmekte olduğunu duydu."));
trsuperobject.biblemap.put("b43_12_13", new trsuperobject(43,12,13,"Hurma dalları alarak Onu karşılamaya çıktılar. ‹‹Hozana! Rabbin adıyla gelene, İsrailin Kralına övgüler olsun!›› diye bağırıyorlardı."));
trsuperobject.biblemap.put("b43_12_14-15", new trsuperobject(43,12,14-15,"İsa bir sıpa bulup üzerine bindi. Yazılmış olduğu gibi,  ‹‹Korkma, ey Siyon kızı!  İşte, Kralın sıpaya binmiş geliyor.››"));
trsuperobject.biblemap.put("b43_12_16", new trsuperobject(43,12,16,"Öğrencileri ilkin bunları anlamadılar. Ama İsa yüceltildikten sonra bu sözlerin Onun hakkında yazıldığını, halkın bunları Onun için yaptığını hatırladılar."));
trsuperobject.biblemap.put("b43_12_17", new trsuperobject(43,12,17,"Lazarı mezardan çağırıp ölümden dirilttiği sırada İsayla birlikte bulunan kalabalık buna tanıklık etti."));
trsuperobject.biblemap.put("b43_12_18", new trsuperobject(43,12,18,"İsanın bu doğaüstü belirtiyi gerçekleştirdiğini duyan halk Onu karşılamaya çıktı."));
trsuperobject.biblemap.put("b43_12_19", new trsuperobject(43,12,19,"Ferisiler ise birbirlerine, ‹‹Görüyorsunuz, elinizden hiçbir şey gelmiyor. Bütün dünya Onun peşine takıldı›› dediler."));
trsuperobject.biblemap.put("b43_12_20", new trsuperobject(43,12,20,"Bayramda tapınmak üzere Yeruşalime gidenler arasında bazı Grekler vardı."));
trsuperobject.biblemap.put("b43_12_21", new trsuperobject(43,12,21,"Bunlar, Celilenin Beytsayda Kentinden olan Filipusa gelerek, ‹‹Efendimiz, İsayı görmek istiyoruz›› diye rica ettiler."));
trsuperobject.biblemap.put("b43_12_22", new trsuperobject(43,12,22,"Filipus gitti, bunu Andreasa bildirdi. Andreas ve Filipus da gidip İsaya haber verdiler."));
trsuperobject.biblemap.put("b43_12_23", new trsuperobject(43,12,23,"İsa, ‹‹İnsanoğlunun yüceltileceği saat geldi›› diye karşılık verdi."));
trsuperobject.biblemap.put("b43_12_24", new trsuperobject(43,12,24,"‹‹Size doğrusunu söyleyeyim, buğday tanesi toprağa düşüp ölmedikçe yalnız kalır. Ama ölürse çok ürün verir."));
trsuperobject.biblemap.put("b43_12_25", new trsuperobject(43,12,25,"Canını seven onu yitirir. Ama bu dünyada canını gözden çıkaran onu sonsuz yaşam için koruyacaktır."));
trsuperobject.biblemap.put("b43_12_26", new trsuperobject(43,12,26,"Bana hizmet etmek isteyen, ardımdan gelsin. Ben neredeysem bana hizmet eden de orada olacak. Baba, bana hizmet edeni onurlandıracaktır."));
trsuperobject.biblemap.put("b43_12_27", new trsuperobject(43,12,27,"Şimdi yüreğim sıkılıyor, ne diyeyim? ‹Baba, beni bu saatten kurtar› mı diyeyim? Ama ben bu amaç için bu saate geldim."));
trsuperobject.biblemap.put("b43_12_28", new trsuperobject(43,12,28,"Baba, adını yücelt!›› Bunun üzerine gökten bir ses geldi: ‹‹Adımı yücelttim ve yine yücelteceğim.››"));
trsuperobject.biblemap.put("b43_12_29", new trsuperobject(43,12,29,"Orada duran ve bunu işiten kalabalık, ‹‹Gök gürledi›› dedi. Başkaları, ‹‹Bir melek Onunla konuştu›› dedi."));
trsuperobject.biblemap.put("b43_12_30", new trsuperobject(43,12,30,"İsa, ‹‹Bu ses benim için değil, sizin içindi›› dedi."));
trsuperobject.biblemap.put("b43_12_31", new trsuperobject(43,12,31,"‹‹Bu dünya şimdi yargılanıyor. Bu dünyanın egemeni şimdi dışarı atılacak."));
trsuperobject.biblemap.put("b43_12_32", new trsuperobject(43,12,32,"Ben yerden yukarı kaldırıldığım zaman bütün insanları kendime çekeceğim.››"));
trsuperobject.biblemap.put("b43_12_33", new trsuperobject(43,12,33,"İsa bunu, nasıl öleceğini belirtmek için söylüyordu."));
trsuperobject.biblemap.put("b43_12_34", new trsuperobject(43,12,34,"Kalabalık Ona şöyle karşılık verdi: ‹‹Kutsal Yasadan öğrendiğimize göre Mesih sonsuza dek kalacaktır. Nasıl oluyor da sen, ‹İnsanoğlu yukarı kaldırılmalıdır› diyorsun? Kimdir bu İnsanoğlu?››"));
trsuperobject.biblemap.put("b43_12_35", new trsuperobject(43,12,35,"İsa, ‹‹Işık kısa bir süre daha aranızdadır›› dedi. ‹‹Karanlıkta kalmamak için ışığınız varken yürüyün. Karanlıkta yürüyen nereye gittiğini bilmez."));
trsuperobject.biblemap.put("b43_12_36", new trsuperobject(43,12,36,"Sizde ışık varken ışığa iman edin ki, ışık oğulları olasınız.›› İsa bu sözleri söyledikten sonra uzaklaşıp onlardan gizlendi."));
trsuperobject.biblemap.put("b43_12_37", new trsuperobject(43,12,37,"Gözleri önünde bunca doğaüstü belirti gerçekleştirdiği halde Ona iman etmediler."));
trsuperobject.biblemap.put("b43_12_38", new trsuperobject(43,12,38,"Bütün bunlar Peygamber Yeşayanın söylediği şu söz yerine gelsin diye oldu:  ‹‹Rab, verdiğimiz habere kim inandı?  Rabbin gücü kime açıklandı?››"));
trsuperobject.biblemap.put("b43_12_39-40", new trsuperobject(43,12,39-40,"İşte bu yüzden iman edemiyorlardı. Nitekim Yeşaya başka bir yerde de şöyle demişti:  ‹‹Tanrı onların gözlerini kör etti  Ve yüreklerini nasırlaştırdı.  Öyle ki, gözleri görmesin,  Yürekleri anlamasın  Ve bana dönmesinler.  Dönselerdi, onları iyileştirirdim.››"));
trsuperobject.biblemap.put("b43_12_41", new trsuperobject(43,12,41,"Bunları söyleyen Yeşaya, İsanın yüceliğini görmüş ve Onun hakkında konuşmuştu."));
trsuperobject.biblemap.put("b43_12_42", new trsuperobject(43,12,42,"Bununla birlikte, önderlerin bile birçoğu İsaya iman etti. Ama Ferisiler yüzünden, havra dışı edilmemek için iman ettiklerini açıkça söylemediler."));
trsuperobject.biblemap.put("b43_12_43", new trsuperobject(43,12,43,"Çünkü insandan gelen övgüyü, Tanrıdan gelen övgüden daha çok seviyorlardı."));
trsuperobject.biblemap.put("b43_12_44", new trsuperobject(43,12,44,"İsa yüksek sesle, ‹‹Bana iman eden bana değil, beni gönderene iman etmiş olur›› dedi."));
trsuperobject.biblemap.put("b43_12_45", new trsuperobject(43,12,45,"‹‹Beni gören beni göndereni de görür."));
trsuperobject.biblemap.put("b43_12_46", new trsuperobject(43,12,46,"Bana iman eden hiç kimse karanlıkta kalmasın diye, dünyaya ışık olarak geldim."));
trsuperobject.biblemap.put("b43_12_47", new trsuperobject(43,12,47,"Sözlerimi işitip de onlara uymayanı ben yargılamam. Çünkü ben dünyayı yargılamaya değil, dünyayı kurtarmaya geldim."));
trsuperobject.biblemap.put("b43_12_48", new trsuperobject(43,12,48,"Beni reddeden ve sözlerimi kabul etmeyen kişiyi yargılayacak biri var. O kişiyi son günde yargılayacak olan, söylediğim sözdür."));
trsuperobject.biblemap.put("b43_12_49", new trsuperobject(43,12,49,"Çünkü ben kendiliğimden konuşmadım. Beni gönderen Babanın kendisi ne söylemem ve ne konuşmam gerektiğini bana buyurdu."));
trsuperobject.biblemap.put("b43_12_50", new trsuperobject(43,12,50,"O'nun buyruğunun sonsuz yaşam olduğunu biliyorum. Bunun için ne söylüyorsam, Baba'nın bana söylediği gibi söylüyorum.››"));
trsuperobject.biblemap.put("b43_13_1", new trsuperobject(43,13,1,"Fısıh Bayramından önceydi. İsa, bu dünyadan ayrılıp Babaya gideceği saatin geldiğini biliyordu. Dünyada kendisine ait olanları hep sevmişti; sonuna kadar da sevdi."));
trsuperobject.biblemap.put("b43_13_2", new trsuperobject(43,13,2,"Akşam yemeği sırasında İblis, Simun İskariotun oğlu Yahudanın yüreğine İsaya ihanet etme isteğini koymuştu bile."));
trsuperobject.biblemap.put("b43_13_3", new trsuperobject(43,13,3,"İsa, Babanın her şeyi kendisine teslim ettiğini, kendisinin Tanrıdan çıkıp geldiğini ve Tanrıya döneceğini biliyordu."));
trsuperobject.biblemap.put("b43_13_4", new trsuperobject(43,13,4,"Yemekten kalktı, üstlüğünü bir yana koydu, bir havlu alıp beline doladı."));
trsuperobject.biblemap.put("b43_13_5", new trsuperobject(43,13,5,"Sonra bir leğene su doldurup öğrencilerin ayaklarını yıkamaya ve beline doladığı havluyla kurulamaya başladı."));
trsuperobject.biblemap.put("b43_13_6", new trsuperobject(43,13,6,"İsa, Simun Petrusa geldi. Simun, ‹‹Ya Rab, ayaklarımı sen mi yıkayacaksın?›› dedi."));
trsuperobject.biblemap.put("b43_13_7", new trsuperobject(43,13,7,"İsa ona şu yanıtı verdi: ‹‹Ne yaptığımı şimdi anlayamazsın, ama sonra anlayacaksın.››"));
trsuperobject.biblemap.put("b43_13_8", new trsuperobject(43,13,8,"Petrus, ‹‹Benim ayaklarımı asla yıkamayacaksın!›› dedi. İsa, ‹‹Yıkamazsam yanımda yerin olmaz›› diye yanıtladı."));
trsuperobject.biblemap.put("b43_13_9", new trsuperobject(43,13,9,"Simun Petrus, ‹‹Ya Rab, o halde yalnız ayaklarımı değil, ellerimi ve başımı da yıka!›› dedi."));
trsuperobject.biblemap.put("b43_13_10", new trsuperobject(43,13,10,"İsa ona dedi ki, ‹‹Yıkanmış olan tamamen temizdir; ayaklarının yıkanmasından başka şeye ihtiyacı yoktur. Sizler temizsiniz, ama hepiniz değil.››"));
trsuperobject.biblemap.put("b43_13_11", new trsuperobject(43,13,11,"İsa, kendisine kimin ihanet edeceğini biliyordu. Bu nedenle, ‹‹Hepiniz temiz değilsiniz›› demişti."));
trsuperobject.biblemap.put("b43_13_12", new trsuperobject(43,13,12,"Onların ayaklarını yıkadıktan sonra giyinip yine sofraya oturdu. ‹‹Size ne yaptığımı anlıyor musunuz?›› dedi."));
trsuperobject.biblemap.put("b43_13_13", new trsuperobject(43,13,13,"‹‹Siz beni Öğretmen ve Rab diye çağırıyorsunuz. Doğru söylüyorsunuz, öyleyim."));
trsuperobject.biblemap.put("b43_13_14", new trsuperobject(43,13,14,"Ben Rab ve Öğretmen olduğum halde ayaklarınızı yıkadım; öyleyse, sizler de birbirinizin ayaklarını yıkamalısınız."));
trsuperobject.biblemap.put("b43_13_15", new trsuperobject(43,13,15,"Size yaptığımın aynısını yapmanız için bir örnek gösterdim."));
trsuperobject.biblemap.put("b43_13_16", new trsuperobject(43,13,16,"Size doğrusunu söyleyeyim, köle efendisinden, elçi de kendisini gönderenden üstün değildir."));
trsuperobject.biblemap.put("b43_13_17", new trsuperobject(43,13,17,"Bildiğiniz bu şeyleri yaparsanız, ne mutlu size!››"));
trsuperobject.biblemap.put("b43_13_18", new trsuperobject(43,13,18,"‹‹Hepiniz için söylemiyorum, ben seçtiklerimi bilirim. Ama, ‹Ekmeğimi yiyen bana ihanet etti› diyen Kutsal Yazının yerine gelmesi için böyle olacak."));
trsuperobject.biblemap.put("b43_13_19", new trsuperobject(43,13,19,"Size şimdiden, bunlar olmadan önce söylüyorum ki, bunlar olunca, benim O olduğuma inanasınız."));
trsuperobject.biblemap.put("b43_13_20", new trsuperobject(43,13,20,"Size doğrusunu söyleyeyim, benim gönderdiğim herhangi bir kimseyi kabul eden beni kabul etmiş olur. Beni kabul eden de beni göndereni kabul etmiş olur.››"));
trsuperobject.biblemap.put("b43_13_21", new trsuperobject(43,13,21,"İsa bunları söyledikten sonra ruhunda derin bir sıkıntı duydu. Açıkça konuşarak, ‹‹Size doğrusunu söyleyeyim, sizden biri bana ihanet edecek›› dedi."));
trsuperobject.biblemap.put("b43_13_22", new trsuperobject(43,13,22,"Öğrenciler, kimden söz ettiğini merak ederek birbirlerine baktılar."));
trsuperobject.biblemap.put("b43_13_23", new trsuperobject(43,13,23,"Öğrencilerinden biri İsanın göğsüne yaslanmıştı. İsa onu severdi."));
trsuperobject.biblemap.put("b43_13_24", new trsuperobject(43,13,24,"Simun Petrus bu öğrenciye, kimden söz ettiğini İsaya sorması için işaret etti."));
trsuperobject.biblemap.put("b43_13_25", new trsuperobject(43,13,25,"O da İsanın göğsüne yaslanmış durumda, ‹‹Ya Rab, kimdir o?›› diye sordu."));
trsuperobject.biblemap.put("b43_13_26", new trsuperobject(43,13,26,"İsa, ‹‹Lokmayı sahana batırıp kime verirsem odur›› diye yanıtladı. Sonra lokmayı batırıp Simun İskariotun oğlu Yahudaya verdi."));
trsuperobject.biblemap.put("b43_13_27", new trsuperobject(43,13,27,"Yahuda lokmayı alır almaz Şeytan onun içine girdi. İsa da ona, ‹‹Yapacağını tez yap!›› dedi."));
trsuperobject.biblemap.put("b43_13_28", new trsuperobject(43,13,28,"Sofrada oturanların hiçbiri, İsanın ona bu sözleri neden söylediğini anlamadı."));
