trsuperobject.biblemap.put("b5_1_3", new trsuperobject(5,1,3,"Mısırdan çıktıktan sonra kırkıncı yılın on birinci ayının birinci günü, Musa RABbin, kendisi aracılığıyla İsraillilere neler buyurduğunu anlattı."));
trsuperobject.biblemap.put("b5_1_4", new trsuperobject(5,1,4,"Bu olay Musa Heşbonda yaşayan Amorluların Kralı Sihonu, Aştarotta ve Edreide yaşayan Başan Kralı Ogu bozguna uğrattıktan sonra oldu."));
trsuperobject.biblemap.put("b5_1_5", new trsuperobject(5,1,5,"Musa Şeria Irmağının doğu yakasındaki Moav topraklarında bu yasayı şöyle açıklamaya başladı:"));
trsuperobject.biblemap.put("b5_1_6", new trsuperobject(5,1,6,"‹‹Tanrımız RAB Horevde bize, ‹Bu dağda yeteri kadar kaldınız› dedi,"));
trsuperobject.biblemap.put("b5_1_7", new trsuperobject(5,1,7,"‹Haydi kalkın, Aravada, dağlık bölgede, Şefelada, Negevde ve Akdeniz kıyısında yaşayan bütün komşu halklara, Amorluların dağlık bölgesine, büyük Fırat Irmağına kadar uzanan Kenanlılar ülkesine ve Lübnana gidin."));
trsuperobject.biblemap.put("b5_1_8", new trsuperobject(5,1,8,"Bu toprakları size verdim. Gidin, atalarınıza, İbrahime, İshaka, Yakupa ve soylarına ant içerek söz verdiğim toprakları mülk edinin.››"));
trsuperobject.biblemap.put("b5_1_9", new trsuperobject(5,1,9,"‹‹O sırada size, ‹Tek başıma yükünüzü taşıyamam› dedim,"));
trsuperobject.biblemap.put("b5_1_10", new trsuperobject(5,1,10,"‹Tanrınız RAB sizi çoğalttı. Bugün göklerdeki yıldızlar kadar çoğaldınız."));
trsuperobject.biblemap.put("b5_1_11", new trsuperobject(5,1,11,"Atalarınızın Tanrısı RAB sizi bin kat daha çoğaltsın ve söz verdiği gibi kutsasın!"));
trsuperobject.biblemap.put("b5_1_12", new trsuperobject(5,1,12,"Sorunlarınıza, yükünüze, davalarınıza ben tek başıma nasıl katlanabilirim?"));
trsuperobject.biblemap.put("b5_1_13", new trsuperobject(5,1,13,"Kendinize her oymaktan bilge, anlayışlı, deneyimli adamlar seçin. Onları size önder atayacağım.›"));
trsuperobject.biblemap.put("b5_1_14", new trsuperobject(5,1,14,"‹‹Siz de bunun iyi olduğunu onayladınız."));
trsuperobject.biblemap.put("b5_1_15", new trsuperobject(5,1,15,"Böylece oymaklarınızın bilge ve deneyimli kişiler olan ileri gelenlerini size önder atadım. Onlara biner, yüzer, ellişer, onar kişilik toplulukların sorumluluğunu verdim. Oymaklarınız için de yöneticiler görevlendirdim."));
trsuperobject.biblemap.put("b5_1_16", new trsuperobject(5,1,16,"Ayrıca yargıçlarınıza, ‹Kardeşleriniz arasındaki sorunları dinleyin› dedim, ‹Bir adamla İsrailli kardeşi ya da bir yabancı arasındaki davalarda adaletle karar verin."));
trsuperobject.biblemap.put("b5_1_17", new trsuperobject(5,1,17,"Yargılarken kimseyi kayırmayın; küçüğe de, büyüğe de aynı gözle bakın. Hiç kimseden korkmayın. Yargı Tanrıya özgüdür. Çözemeyeceğiniz bir sorun olursa bana getirin, ben gerekeni yaparım.›"));
trsuperobject.biblemap.put("b5_1_18", new trsuperobject(5,1,18,"O sırada yapmanız gereken her şeyi size buyurmuştum.››"));
trsuperobject.biblemap.put("b5_1_19", new trsuperobject(5,1,19,"‹‹Sonra Tanrımız RABbin bize buyurduğu gibi Horevden ayrıldık, Amorluların dağlık bölgesine giden yoldan geçerek gördüğünüz o geniş ve korkunç çölü aşıp Kadeş-Barneaya vardık."));
trsuperobject.biblemap.put("b5_1_20", new trsuperobject(5,1,20,"Size, ‹Tanrımız RABbin bize vereceği Amorluların dağlık bölgesine vardınız› dedim,"));
trsuperobject.biblemap.put("b5_1_21", new trsuperobject(5,1,21,"‹İşte, Tanrınız RAB size ülkeyi verdi. Haydi, atalarınızın Tanrısı RABbin size söylediği gibi, gidip orayı mülk edinin. Korkmayın, yılmayın.›"));
trsuperobject.biblemap.put("b5_1_22", new trsuperobject(5,1,22,"‹‹O zaman hepiniz bana gelip, ‹Ülkeyi araştırmak için önümüzden adamlar gönderelim› dediniz, ‹Hangi yoldan gideceğiz, hangi kentlere uğrayacağız? Bilgi versinler.›"));
trsuperobject.biblemap.put("b5_1_23", new trsuperobject(5,1,23,"‹‹Bu düşünceyi benimsedim. Her oymaktan birer kişi olmak üzere aranızdan on iki kişi seçtim."));
trsuperobject.biblemap.put("b5_1_24", new trsuperobject(5,1,24,"Bunlar dağlık bölgeye çıkarak Eşkol Vadisine varıp ülkeyi araştırdılar."));
trsuperobject.biblemap.put("b5_1_25", new trsuperobject(5,1,25,"Dönüşte orada yetişen meyvelerden getirdiler ve, ‹Tanrımız RABbin bize vereceği ülke verimlidir› diye haber verdiler."));
trsuperobject.biblemap.put("b5_1_26", new trsuperobject(5,1,26,"‹‹Ne var ki, siz oraya gitmek istemediniz. Tanrınız RABbin buyruğuna karşı geldiniz."));
trsuperobject.biblemap.put("b5_1_27", new trsuperobject(5,1,27,"Çadırlarınızda söylenerek, ‹RAB bizden nefret ediyor› dediniz, ‹Bizi Amorluların eline verip yok etmek için Mısırdan çıkardı."));
trsuperobject.biblemap.put("b5_1_28", new trsuperobject(5,1,28,"Oraya niye gidelim? Kardeşlerimiz yöre halkının bizden daha güçlü, daha uzun boylu olduğunu söyleyerek cesaretimizi kırdılar. Kentler büyükmüş, göğe dek yükselen surlarla çevriliymiş. Orada Anaklıları da görmüşler.›"));
trsuperobject.biblemap.put("b5_1_29", new trsuperobject(5,1,29,"‹‹Oysa ben size, ‹Onlardan korkmayın, yılmayın› dedim,"));
trsuperobject.biblemap.put("b5_1_3-031", new trsuperobject(5,1,3-031,"‹Önünüzden giden Tanrınız RAB sizin için savaşacak. Gözünüzün önünde Mısırda ve çölde sizler için yaptıklarının aynısını yapacak. Tanrınız RABbin buraya varıncaya dek, çocuğunu taşıyan bir adam gibi sizi nasıl yol boyunca taşıdığını gördünüz.›"));
trsuperobject.biblemap.put("b5_1_32", new trsuperobject(5,1,32,"Bütün bunlara karşın Tanrınız RABbe güvenmediniz."));
trsuperobject.biblemap.put("b5_1_33", new trsuperobject(5,1,33,"O RAB ki, çadırlarınızı kurmanız için size yer aramak, gideceğiniz yolu göstermek için geceleyin ateşte, gündüzün bulutta önünüzsıra gitti.››"));
trsuperobject.biblemap.put("b5_1_34", new trsuperobject(5,1,34,"‹‹RAB yakınmalarınızı duyunca öfkelendi ve şöyle ant içti:"));
trsuperobject.biblemap.put("b5_1_35-36", new trsuperobject(5,1,35-36,"‹Atalarınıza ant içerek söz verdiğim o verimli ülkeyi, bu kötü kuşaktan Yefunne oğlu Kalev dışında hiç kimse görmeyecek. Yalnız o görecek, ayak bastığı toprakları ona ve soyuna vereceğim. Çünkü o bütün yüreğiyle RABbin yolunda yürüdü.›"));
trsuperobject.biblemap.put("b5_1_37", new trsuperobject(5,1,37,"‹‹Sizin yüzünüzden RAB bana da öfkelenerek, ‹Sen de o ülkeye girmeyeceksin› dedi,"));
trsuperobject.biblemap.put("b5_1_38", new trsuperobject(5,1,38,"‹Ama yardımcın Nun oğlu Yeşu oraya girecek. Onu yüreklendir. İsraillilerin ülkeyi mülk edinmesini o sağlayacak."));
trsuperobject.biblemap.put("b5_1_39", new trsuperobject(5,1,39,"Tutsak olacak dediğiniz küçükleriniz, bugün iyiyle kötüyü ayırt edemeyen çocuklarınız oraya girecekler. Ülkeyi onlara vereceğim, orayı onlar mülk edinecekler."));
trsuperobject.biblemap.put("b5_1_40", new trsuperobject(5,1,40,"Ama siz geri dönün, Kızıldeniz yolundan çöle gidin.››"));
