trsuperobject.biblemap.put("b5_1_42", new trsuperobject(5,1,42,"‹‹Ama RAB bana şöyle dedi: ‹Söyle onlara, savaşa gitmesinler. Çünkü sizinle olmayacağım. Düşmanlarınızın önünde yenilgiye uğrayacaksınız.›"));
trsuperobject.biblemap.put("b5_1_43", new trsuperobject(5,1,43,"‹‹Sizi uyardım, ama dinlemediniz. RABbin buyruğuna karşı geldiniz. Kendinize güvenerek dağlık bölgeye çıktınız."));
trsuperobject.biblemap.put("b5_1_44", new trsuperobject(5,1,44,"Dağlık bölgede yaşayan Amorlular size karşı çıktılar. Arılar gibi sizi kovaladılar. Seirden Horma Kentine dek sizi bozguna uğrattılar."));
trsuperobject.biblemap.put("b5_1_45", new trsuperobject(5,1,45,"Geri döndünüz ve RABbin önünde ağladınız. Ama RAB ne ağlayışınızı duydu, ne de size kulak astı."));
trsuperobject.biblemap.put("b5_1_46", new trsuperobject(5,1,46,"Uzun süre Kadeş'te kaldınız.››"));
trsuperobject.biblemap.put("b5_2_1", new trsuperobject(5,2,1,"‹‹Sonunda geri dönüp RABbin bana buyurduğu gibi Kızıldeniz yolundan çöle gittik. Uzun süre Seir dağlık bölgesinde dolanıp durduk."));
trsuperobject.biblemap.put("b5_2_2-3", new trsuperobject(5,2,2-3,"‹‹RAB bana, ‹Bu dağlık bölgenin çevresinde yeterince dolaştınız› dedi, ‹Şimdi kuzeye gidin.›"));
trsuperobject.biblemap.put("b5_2_4", new trsuperobject(5,2,4,"Sonra halka şu buyrukları vermemi söyledi: ‹Seirde yaşayan kardeşlerinizin, Esavoğullarının ülkesinden geçeceksiniz. Sizden korkacaklar. Çok dikkatli davranın."));
trsuperobject.biblemap.put("b5_2_5", new trsuperobject(5,2,5,"Onları savaşa kışkırtmayın. Size onların ülkesinden hiçbir toprak parçası, ayağınızı basacak bir yer bile vermeyeceğim. Çünkü Seir dağlık bölgesini mülk olarak Esava verdim."));
trsuperobject.biblemap.put("b5_2_6", new trsuperobject(5,2,6,"Yiyeceklerinizi, içeceklerinizi onlardan para karşılığında alacaksınız.›"));
trsuperobject.biblemap.put("b5_2_7", new trsuperobject(5,2,7,"‹‹Tanrınız RAB el attığınız her işte sizi kutsadı. Bu geniş çölde dolanıp durduğunuz sürece sizi korudu. Tanrınız RAB geçirdiğiniz bu kırk yıl boyunca sizlerleydi ve hiçbir eksiğiniz olmadı."));
trsuperobject.biblemap.put("b5_2_8", new trsuperobject(5,2,8,"‹‹Böylece Seirde yaşayan kardeşlerimizin, Esavoğullarının yanından geçtik. Eylat ve Esyon-Geverden Aravaya giden yoldan saparak yolculuğumuzu Moav Çölü yolundan sürdürdük."));
trsuperobject.biblemap.put("b5_2_9", new trsuperobject(5,2,9,"‹‹RAB bana, ‹Moavlılara düşman gözüyle bakma, onları savaşa kışkırtma› dedi, ‹Onların ülkesinden hiçbir toprak parçasını sana mülk olarak vermeyeceğim. Çünkü Ar Kentini Lut soyuna verdim.››"));
trsuperobject.biblemap.put("b5_2_10", new trsuperobject(5,2,10,"Daha önce orada Anaklılar kadar uzun boylu, güçlü ve kalabalık olan Emliler yaşıyordu."));
trsuperobject.biblemap.put("b5_2_11", new trsuperobject(5,2,11,"Emliler Anaklılar gibi Refalılardan sayılırdı. Ama Moavlılar onlara Emliler adını takmıştı."));
trsuperobject.biblemap.put("b5_2_12", new trsuperobject(5,2,12,"Daha önce Seirde Horlular yaşardı. Esavoğulları orayı onların elinden aldı. İsraillilerin RABbin mülk edinmek için kendilerine verdiği ülkede yaptıkları gibi, Esavoğulları da Horluları yok edip yerlerine yerleştiler.-"));
trsuperobject.biblemap.put("b5_2_13", new trsuperobject(5,2,13,"‹‹RAB, ‹Haydi kalkın, Zeret Vadisinden geçin› dedi. Biz de Zeret Vadisinden geçtik."));
trsuperobject.biblemap.put("b5_2_14", new trsuperobject(5,2,14,"Kadeş-Barneadan yola çıkıp Zeret Vadisinden geçinceye dek otuz sekiz yıl yol aldık. RABbin içtiği ant uyarınca, İsrail halkından o kuşağın bütün savaşçıları yok olmuştu."));
trsuperobject.biblemap.put("b5_2_15", new trsuperobject(5,2,15,"RAB, ordugahtaki bütün savaşçıları ortadan kaldırıncaya dek onları cezalandırmıştı."));
