trsuperobject.biblemap.put("b5_2_17", new trsuperobject(5,2,17,"RAB bana şöyle dedi:"));
trsuperobject.biblemap.put("b5_2_18", new trsuperobject(5,2,18,"‹Bugün Moav topraklarından ve Ar Kentinden geçeceksin."));
trsuperobject.biblemap.put("b5_2_19", new trsuperobject(5,2,19,"Ammonlulara yaklaştığında onlara düşman gözüyle bakma, onları savaşa kışkırtma. Çünkü mülk edinmen için Ammonluların ülkesinden sana hiçbir toprak parçası vermeyeceğim. O ülkeyi mülk olarak Lut soyuna verdim.››"));
trsuperobject.biblemap.put("b5_2_20", new trsuperobject(5,2,20,"Bu bölge Refalılar ülkesi diye bilinir. Refalılar önceden orada yaşıyordu. Ammonlular onlara Zamzumlular adını takmıştı."));
trsuperobject.biblemap.put("b5_2_21", new trsuperobject(5,2,21,"Zamzumlular Anaklılar kadar uzun boylu, güçlü ve kalabalıktılar. Ama RAB onları Ammonluların önünde yok etti. Ammonlular Zamzumluların topraklarını alıp yerlerine yerleştiler."));
trsuperobject.biblemap.put("b5_2_22", new trsuperobject(5,2,22,"RAB Seirde yaşayan Esavoğulları için de aynısını yapmış, Horluları onların önünde yok etmişti. Esavoğulları Horluların topraklarını almış, yerlerine yerleşmişlerdi. Bugün de orada yaşıyorlar."));
trsuperobject.biblemap.put("b5_2_23", new trsuperobject(5,2,23,"Gazzeye kadar uzanan köylerde yaşayan Avvalıları da Kaftordan gelen Kaftorlular yok edip yerlerine yerleştiler.-"));
trsuperobject.biblemap.put("b5_2_24", new trsuperobject(5,2,24,"‹‹Haydi kalkın! Arnon Vadisinden geçin! İşte Heşbon Kralı Amorlu Sihonu ve ülkesini elinize teslim ettim. Ona saldırın ve ülkesini mülk edinmeye başlayın."));
trsuperobject.biblemap.put("b5_2_25", new trsuperobject(5,2,25,"Bugünden başlayarak göğün altındaki uluslara korkunuzu, dehşetinizi salacağım. Haberinizi duyunca korkuyla titreyecekler.››"));
trsuperobject.biblemap.put("b5_2_26", new trsuperobject(5,2,26,"‹‹Bundan sonra Kedemot Çölünden Heşbon Kralı Sihona barış önerileriyle ulaklar gönderdim. Öneriler şöyleydi:"));
trsuperobject.biblemap.put("b5_2_27", new trsuperobject(5,2,27,"‹İzin ver, ülkenden geçelim. Dosdoğru ana yoldan, sağa sola sapmadan geçeceğiz."));
trsuperobject.biblemap.put("b5_2_28-29", new trsuperobject(5,2,28-29,"Yiyeceğimizi, içeceğimizi para karşılığında bize vereceksin. Yeter ki ülkenden geçelim. Seirde yaşayan Esavoğulları ile Ar Kentinde yaşayan Moavlılar sınırlarından geçmemize izin verdiler. Şeria Irmağından geçip Tanrımız RABbin bize vereceği ülkeye gitmemize sen de izin ver.›"));
trsuperobject.biblemap.put("b5_2_30", new trsuperobject(5,2,30,"Ne var ki, Heşbon Kralı Sihon ülkesinden geçmemize izin vermek istemedi. Tanrınız RAB, şimdi olduğu gibi, Sihonu elinize teslim etmek için yüreğini duygusuzlaştırıp onu inatçı yaptı."));
trsuperobject.biblemap.put("b5_2_31", new trsuperobject(5,2,31,"‹‹RAB bana, ‹İşte Sihonu ve ülkesini senin eline teslim etmeye başladım. Haydi, ülkeyi ele geçir ve mülk edinmeye başla› dedi."));
trsuperobject.biblemap.put("b5_2_32", new trsuperobject(5,2,32,"Sihon bizimle savaşmak için Yahesada bütün halkıyla karşımıza çıktı."));
trsuperobject.biblemap.put("b5_2_33", new trsuperobject(5,2,33,"Tanrımız RAB onu elimize teslim etti. Onu, oğullarını ve bütün halkını yok ettik."));
trsuperobject.biblemap.put("b5_2_34", new trsuperobject(5,2,34,"Bütün kentlerini ele geçirdik, hepsini yok ettik. Kadın, erkek, çocuk, kimseyi sağ bırakmadık."));
trsuperobject.biblemap.put("b5_2_35", new trsuperobject(5,2,35,"Hayvanlara ve ele geçirdiğimiz kentlerdeki mallara ise el koyduk."));
trsuperobject.biblemap.put("b5_2_36", new trsuperobject(5,2,36,"Arnon Vadisi kıyısında Aroerden ve vadideki kentten Gilata dek, ele geçirmediğimiz hiçbir kent kalmadı. Tanrımız RAB hepsini elimize teslim etti."));
trsuperobject.biblemap.put("b5_2_37", new trsuperobject(5,2,37,"Ama Tanrımız RAB'bin buyruğu uyarınca, Ammonlular'ın ülkesine -Yabbuk Irmağı kıyılarına, dağlık bölgedeki kentlere- yaklaşmadınız.››"));
trsuperobject.biblemap.put("b5_3_1", new trsuperobject(5,3,1,"‹‹Bundan sonra dönüp Başana doğru ilerledik. Başan Kralı Ogla ordusu bizimle savaşmak için Edreide karşımıza çıktı."));
trsuperobject.biblemap.put("b5_3_3", new trsuperobject(5,3,3,"‹‹Böylece Tanrımız RAB, Başan Kralı Ogu ve halkını da elimize teslim etti. Hiçbirini sağ bırakmadan hepsini yok ettik."));
trsuperobject.biblemap.put("b5_3_4", new trsuperobject(5,3,4,"Bütün kentlerini ele geçirdik. Ele geçirmediğimiz tek kent kalmadı. Hepsi altmış kentti: Başanda Ogun ülkesi olan bütün Argov bölgesi."));
trsuperobject.biblemap.put("b5_3_5", new trsuperobject(5,3,5,"Bütün bu kentler yüksek surlarla, kapılarla, sürgülerle sağlamlaştırılmıştı. Bunlardan başka surla çevrilmemiş birçok köy de vardı."));
trsuperobject.biblemap.put("b5_3_6", new trsuperobject(5,3,6,"Heşbon Kralı Sihona yaptığımız gibi hepsini yok ettik. Her kenti, kadın, erkek ve çocuklarla birlikte, tümüyle yok ettik."));
trsuperobject.biblemap.put("b5_3_7", new trsuperobject(5,3,7,"Hayvanlara ve kentlerdeki mallara ise el koyduk."));
trsuperobject.biblemap.put("b5_3_8", new trsuperobject(5,3,8,"‹‹Arnon Vadisinden Hermon Dağına kadar Şeria Irmağının doğu yakasındaki toprakları iki Amorlu kralın elinden aldık."));
trsuperobject.biblemap.put("b5_3_9", new trsuperobject(5,3,9,"Saydalılar Hermona Siryon, Amorlularsa Senir derler.-"));
trsuperobject.biblemap.put("b5_3_10", new trsuperobject(5,3,10,"Ovadaki bütün kentleri, bütün Gilatı, Ogun ülkesine ait kentler olan Salka ve Edreiye uzanan bütün Başanı ele geçirdik.››"));
trsuperobject.biblemap.put("b5_3_11", new trsuperobject(5,3,11,"Refalılardan yalnız Başan Kralı Og sağ kalmıştı. Ogun Ammonluların Rabba Kentindeki yatağı demirdendi. O gün kullanılan arşın ölçüsüne göre uzunluğu dokuz, eni dört arşındı.-"));
trsuperobject.biblemap.put("b5_3_12", new trsuperobject(5,3,12,"‹‹O sırada ele geçirdiğimiz topraklardan Arnon Vadisi yakınındaki Aroer Kentinin kuzeyini, Gilat dağlık bölgesinin yarısıyla oradaki kentleri Ruben ve Gad oymaklarına verdim."));
trsuperobject.biblemap.put("b5_3_13", new trsuperobject(5,3,13,"Gilatın geri kalan bölümünü ve Ogun ülkesi Başanı Manaşşe oymağının yarısına verdim. Başandaki Argov bölgesi Refalılar ülkesi diye bilinirdi."));
trsuperobject.biblemap.put("b5_3_14", new trsuperobject(5,3,14,"Manaşşe soyundan Yair, Geşur ile Maaka sınırına dek uzanan bütün Argov bölgesini aldı. Başan denilen bölgeye kendi adını verdi. Orası bugün de Havvot-Yairfç diye anılıyor."));
trsuperobject.biblemap.put("b5_3_15", new trsuperobject(5,3,15,"Makire Gilatı verdim."));
trsuperobject.biblemap.put("b5_3_16", new trsuperobject(5,3,16,"Gilatla Arnon Vadisi arasında kalan toprakları Ruben ve Gad oymaklarına verdim. Vadinin ortası onların sınırıydı; Ammonlularla sınırları ise Yabbuk Irmağıydı."));
trsuperobject.biblemap.put("b5_3_17", new trsuperobject(5,3,17,"Aravada da sınır Şeria Irmağıydı; Kinneretten Arava -Lut- Gölüne, doğuda Pisga yamaçlarının aşağısına kadar uzanıyordu. gelir."));
trsuperobject.biblemap.put("b5_3_18", new trsuperobject(5,3,18,"‹‹O zaman size şöyle buyruk verdim: ‹Tanrınız RAB mülk edinmek için bu ülkeyi size verdi. Bütün savaşçılarınız silahlı olarak İsrailli kardeşlerinizin önüsıra gitsin."));
trsuperobject.biblemap.put("b5_3_19-20", new trsuperobject(5,3,19-20,"Ancak RAB sizi rahata erdirdiği gibi onları da rahata erdirene ve onlar Tanrınız RABbin Şeria Irmağının karşı yakasında kendilerine vereceği toprakları ele geçirene kadar, kadınlarınız, çocuklarınız ve hayvanlarınız -biliyorum, birçok hayvanınız var- size verdiğim kentlerde kalsın. Ondan sonra, her biriniz size verdiğim toprağa dönebilir.››"));
trsuperobject.biblemap.put("b5_3_21", new trsuperobject(5,3,21,"‹‹O zaman Yeşuya, ‹Tanrın RABbin bu iki krala neler yaptığını gözlerinle gördün› dedim, ‹RAB gideceğin bütün ülkelere aynısını yapacak."));
trsuperobject.biblemap.put("b5_3_22", new trsuperobject(5,3,22,"Onlardan korkmayın! Tanrınız RAB sizin için savaşacak.›"));
trsuperobject.biblemap.put("b5_3_23", new trsuperobject(5,3,23,"‹‹Sonra RABbe yalvardım:"));
trsuperobject.biblemap.put("b5_3_24", new trsuperobject(5,3,24,"‹Ey Egemen RAB, büyüklüğünü ve güçlü elini bana göstermeye başladın. Gökte ve yerde senin yaptığın yüce işleri yapabilecek başka bir tanrı yok!"));
trsuperobject.biblemap.put("b5_3_25", new trsuperobject(5,3,25,"İzin ver de Şeria Irmağından geçip karşı yakadaki o verimli ülkeyi, o güzel dağlık bölgeyi ve Lübnanı göreyim.›"));
trsuperobject.biblemap.put("b5_3_26", new trsuperobject(5,3,26,"‹‹Ama RAB sizin yüzünüzden bana öfkelendi, yalvarışıma kulak asmadı. Bana, ‹Yeter artık!› dedi, ‹Bir daha bu konudan söz etme bana."));
trsuperobject.biblemap.put("b5_3_27", new trsuperobject(5,3,27,"Pisga Dağına çık. Batıya, kuzeye, güneye, doğuya bak. Gözlerinle gör. Çünkü Şeria Irmağından geçmeyeceksin."));
trsuperobject.biblemap.put("b5_3_28", new trsuperobject(5,3,28,"Yeşuya görev ver. Onu güçlendir ve yüreklendir. Çünkü bu halk Şeria Irmağından onun önderliğinde geçecek. Göreceğin toprakları halka o miras olarak verecek.›"));
trsuperobject.biblemap.put("b5_3_29", new trsuperobject(5,3,29,"Böylece Beytpeor'un karşısındaki vadide kaldık.››"));
trsuperobject.biblemap.put("b5_4_1", new trsuperobject(5,4,1,"‹‹Şimdi, ey İsrail, size öğrettiğim kurallara, ilkelere kulak verin. Yaşamak, ülkeye girmek ve atalarınızın Tanrısı RABbin size vereceği toprakları mülk edinmek için bunlara uyun."));
trsuperobject.biblemap.put("b5_4_2", new trsuperobject(5,4,2,"Size verdiğim buyruklara hiçbir şey eklemeyin, hiçbir şey çıkarmayın. Ama size bildirdiğim Tanrınız RABbin buyruklarına uyun."));
trsuperobject.biblemap.put("b5_4_3", new trsuperobject(5,4,3,"‹‹RABbin Baal-Peorda neler yaptığını kendi gözlerinizle gördünüz. Tanrınız RAB, Baal-Peora tapan herkesi aranızdan yok etti."));
trsuperobject.biblemap.put("b5_4_5", new trsuperobject(5,4,5,"‹‹İşte, Tanrım RABbin buyruğu uyarınca size kurallar, ilkeler verdim. Öyle ki, mülk edinmek için gideceğiniz ülkede bunlara uyasınız."));
trsuperobject.biblemap.put("b5_4_6", new trsuperobject(5,4,6,"Onlara sımsıkı bağlanın. Çünkü ne denli bilge ve anlayışlı olduğunuzu uluslara bunlar gösterecek. Bu kuralları duyunca, uluslar, ‹Bu büyük ulus gerçekten bilge ve anlayışlı bir halk!› diyecek."));
trsuperobject.biblemap.put("b5_4_7", new trsuperobject(5,4,7,"Tanrımız RAB her çağırdığımızda bize yakın olur. Tanrısı kendisine böylesine yakın olan başka bir büyük ulus var mı?"));
trsuperobject.biblemap.put("b5_4_8", new trsuperobject(5,4,8,"Bugün size verdiğim bu yasa gibi adil kuralları, ilkeleri olan başka bir büyük ulus var mı?"));
trsuperobject.biblemap.put("b5_4_9", new trsuperobject(5,4,9,"‹‹Ancak gördüklerinizi unutmamaya, yaşamınız boyunca aklınızdan çıkarmamaya dikkat edin ve uyanık olun. Bunları çocuklarınıza, torunlarınıza anlatın."));
trsuperobject.biblemap.put("b5_4_10", new trsuperobject(5,4,10,"Horevde Tanrınız RABbin önünde durduğunuz günü anımsayın. RAB bana şöyle dedi: ‹Sözlerimi dinlemesi için halkı topla. Öyle ki, yaşamları boyunca benden korkmayı öğrensinler, çocuklarına da öğretsinler.›"));
trsuperobject.biblemap.put("b5_4_11", new trsuperobject(5,4,11,"‹‹Yaklaşıp dağın eteğinde durdunuz. Dağ göklere dek yükselen alevle tutuşmuştu. Kara bulutlar ve koyu bir karanlık vardı."));
trsuperobject.biblemap.put("b5_4_12", new trsuperobject(5,4,12,"RAB size ateşin içinden seslendi. Siz konuşulanı duydunuz, ama konuşanı görmediniz. Yalnız bir ses duydunuz."));
trsuperobject.biblemap.put("b5_4_13", new trsuperobject(5,4,13,"RAB uymanızı buyurduğu antlaşmayı, yani On Buyruku size açıkladı. Onları iki taş levha üstüne yazdı."));
trsuperobject.biblemap.put("b5_4_14", new trsuperobject(5,4,14,"Mülk edinmek için gideceğiniz ülkede uymanız gereken kuralları, ilkeleri size öğretmemi buyurdu.››"));
trsuperobject.biblemap.put("b5_4_15", new trsuperobject(5,4,15,"‹‹RAB Horevde ateşin içinden size seslendiği gün hiçbir suret görmediniz. Bu nedenle kendinize çok dikkat edin."));
trsuperobject.biblemap.put("b5_4_16-18", new trsuperobject(5,4,16-18,"Öyle ki, kendiniz için erkek ya da kadın, yerde yaşayan hayvan ya da gökte uçan kuş, küçük kara hayvanı ya da aşağıda suda yaşayan balık suretinde, heykel biçiminde put yaparak yoldan sapmayasınız."));
trsuperobject.biblemap.put("b5_4_19", new trsuperobject(5,4,19,"Gözlerinizi göklere kaldırıp güneşi, ayı, yıldızları -gök cisimlerini- görünce sakın aldanmayın; eğilip onlara tapmayın. Tanrınız RAB bunları göğün altındaki halklara pay olarak vermiştir."));
trsuperobject.biblemap.put("b5_4_20", new trsuperobject(5,4,20,"Size gelince, RAB, bugün olduğu gibi kendi halkı olmanız için, sizi alıp demir eritme ocağından, Mısırdan çıkardı."));
trsuperobject.biblemap.put("b5_4_21", new trsuperobject(5,4,21,"‹‹RAB sizin yüzünüzden bana öfkelendi. Şeria Irmağının karşı yakasına geçmemem ve Tanrınız RABbin size mülk olarak vereceği o verimli ülkeye girmemem için ant içti."));
trsuperobject.biblemap.put("b5_4_22", new trsuperobject(5,4,22,"Ben bu toprakta öleceğim. Şeria Irmağından geçmeyeceğim. Ama siz karşıya geçecek ve o verimli ülkeyi mülk edineceksiniz."));
trsuperobject.biblemap.put("b5_4_23", new trsuperobject(5,4,23,"Tanrınız RABbin sizinle yaptığı antlaşmayı unutmamaya, kendinize Tanrınız RABbin yasakladığı herhangi bir şeyin suretinde put yapmamaya dikkat edin."));
trsuperobject.biblemap.put("b5_4_24", new trsuperobject(5,4,24,"Çünkü Tanrınız RAB yakıp yok eden bir ateştir; kıskanç bir Tanrıdır."));
trsuperobject.biblemap.put("b5_4_25", new trsuperobject(5,4,25,"‹‹Ülkede uzun zaman oturduktan, çocuk ve torun sahibi olduktan sonra yoldan sapar, kendinize herhangi bir şeyin suretinde put yapar, Tanrınız RABbin gözünde kötü olanı yaparak onu öfkelendirirseniz,"));
trsuperobject.biblemap.put("b5_4_26", new trsuperobject(5,4,26,"bugün size karşı yeri göğü tanık gösteririm ki, mülk edinmek için Şeria Irmağından geçip gideceğiniz ülkede kesinlikle ve çabucak öleceksiniz. Orada uzun süre yaşamayacak, büsbütün yok olacaksınız."));
trsuperobject.biblemap.put("b5_4_27", new trsuperobject(5,4,27,"RAB sizi başka halkların arasına dağıtacak. RABbin sizi süreceği ulusların arasında sayıca az olacaksınız."));
trsuperobject.biblemap.put("b5_4_28", new trsuperobject(5,4,28,"Orada görmeyen, duymayan, yemeyen, koku almayan, insan eliyle yapılmış, ağaçtan, taştan tanrılara tapacaksınız."));
trsuperobject.biblemap.put("b5_4_29", new trsuperobject(5,4,29,"Ama Tanrınız RABbi arayacaksınız. Bütün yüreğinizle, bütün canınızla ararsanız, Onu bulacaksınız."));
trsuperobject.biblemap.put("b5_4_30", new trsuperobject(5,4,30,"Sıkıntıya düştüğünüzde ve bütün bu olaylar başınıza geldiğinde, sonunda Tanrınız RABbe dönecek, Onun sözüne kulak vereceksiniz."));
trsuperobject.biblemap.put("b5_4_31", new trsuperobject(5,4,31,"Çünkü Tanrınız RAB acıyan bir Tanrıdır. Sizi bırakmaz, yok etmez ve atalarınıza ant içerek yaptığı antlaşmayı unutmaz."));
trsuperobject.biblemap.put("b5_4_32", new trsuperobject(5,4,32,"‹‹Siz doğmadan önceki geçmiş günleri, Tanrının yeryüzünde insanı yarattığı günden bu yana geçen zamanı soruşturun. Göklerin bir ucundan öbür ucuna sorun. Bu kadar önemli bir olay hiç oldu mu, ya da buna benzer bir olay duyuldu mu?"));
trsuperobject.biblemap.put("b5_4_33", new trsuperobject(5,4,33,"Ateşin içinden seslenen Tanrının sesini sizin gibi duyup da sağ kalan başka bir ulus var mı?"));
trsuperobject.biblemap.put("b5_4_34", new trsuperobject(5,4,34,"Hiçbir tanrı Tanrınız RABbin Mısırda gözlerinizin önünde sizin için yaptığı gibi denemelerle, belirtilerle, şaşılası işlerle, savaşla, güçlü ve kudretli elle, büyük ve ürkütücü olaylarla gidip başka bir ulustan kendine bir ulus almaya kalkıştı mı?"));
trsuperobject.biblemap.put("b5_4_35", new trsuperobject(5,4,35,"‹‹Bu olaylar RABbin Tanrı olduğunu ve Ondan başkası olmadığını bilesiniz diye size gösterildi."));
trsuperobject.biblemap.put("b5_4_36", new trsuperobject(5,4,36,"O sizi yola getirmek için gökten size sesini duyurdu. Yeryüzünde size büyük ateşini gösterdi. Ateşin içinden size sözlerini duyurdu."));
trsuperobject.biblemap.put("b5_4_37", new trsuperobject(5,4,37,"Atalarınızı sevdiği ve onların soyunu seçtiği için sizi büyük gücüyle Mısırdan kendisi çıkardı."));
trsuperobject.biblemap.put("b5_4_38", new trsuperobject(5,4,38,"Amacı sizden daha büyük, daha güçlü ulusları önünüzden kovmak, onların ülkelerine girmenizi sağlamak, bugün olduğu gibi mülk edinmeniz için ülkelerini size vermekti."));
trsuperobject.biblemap.put("b5_4_39", new trsuperobject(5,4,39,"‹‹Bunun için, bugün RABbin yukarıda göklerde, aşağıda yeryüzünde Tanrı olduğunu, Ondan başkası olmadığını bilin ve bunu aklınızdan çıkarmayın."));
trsuperobject.biblemap.put("b5_4_40", new trsuperobject(5,4,40,"Size ve sizden sonra gelen çocuklarınıza iyilik sağlaması ve Tanrınız RABbin sonsuza dek size vereceği bu topraklarda uzun yıllar yaşamanız için bugün size bildirdiğim RABbin kurallarına, buyruklarına uyun.››"));
trsuperobject.biblemap.put("b5_4_41", new trsuperobject(5,4,41,"Bundan sonra Musa Şeria Irmağının doğusunda üç kent ayırdı."));
trsuperobject.biblemap.put("b5_4_42", new trsuperobject(5,4,42,"Öyle ki, önceden kin beslemediği bir komşusunu istemeyerek öldüren biri bu kentlerden birine kaçıp canını kurtarabilsin."));
trsuperobject.biblemap.put("b5_4_43", new trsuperobject(5,4,43,"Bu kentler şunlardı: Rubenliler için ovadaki kırsal bölgede Beser, Gadlılar için Gilattaki Ramot, Manaşşeliler için Başandaki Golan."));
trsuperobject.biblemap.put("b5_4_44", new trsuperobject(5,4,44,"Musanın İsraillilere anlattığı yasa budur."));
trsuperobject.biblemap.put("b5_4_45", new trsuperobject(5,4,45,"Mısırdan çıktıktan sonra Musanın İsraillilere bildirdiği yasalar, kurallar, ilkeler bunlardır."));
trsuperobject.biblemap.put("b5_4_46", new trsuperobject(5,4,46,"Musa bunları Şeria Irmağının doğu yakasında, Beytpeor karşısındaki vadide bildirdi. Burası daha önce Heşbonda oturan Amorluların Kralı Sihona ait topraklardı. Musa ile İsrailliler Mısırdan çıktıklarında Sihonu bozguna uğratmışlardı."));
trsuperobject.biblemap.put("b5_4_47", new trsuperobject(5,4,47,"Onun ve Başan Kralı Ogun ülkesini, yani Şeria Irmağının doğusunda yaşayan iki Amorlu kralın ülkesini ele geçirmişlerdi."));
trsuperobject.biblemap.put("b5_4_48", new trsuperobject(5,4,48,"Bu topraklar, Arnon Vadisi kıyısındaki Aroer Kentinden Sion, yani Hermon Dağına kadar uzanıyor,"));
trsuperobject.biblemap.put("b5_4_49", new trsuperobject(5,4,49,"Pisga Dağı'nın eteğindeki Arava Gölü'ne dek uzanan, Şeria Irmağı'nın doğu yakasındaki bütün Arava'yı kapsıyordu."));
trsuperobject.biblemap.put("b5_5_1", new trsuperobject(5,5,1,"Musa bütün İsraillileri bir araya toplayarak şöyle dedi: ‹‹Ey İsrail, bugün size bildireceğim kurallara, ilkelere kulak verin! Onları öğrenin ve onlara uymaya dikkat edin!"));
trsuperobject.biblemap.put("b5_5_2", new trsuperobject(5,5,2,"Tanrımız RAB Horev Dağında bizimle bir antlaşma yaptı."));
trsuperobject.biblemap.put("b5_5_5", new trsuperobject(5,5,5,"O zaman RABbin sözünü size bildirmek için RAB ile sizin aranızda durdum. Çünkü siz ateşten korkup dağa çıkmadınız. RAB şöyle seslendi:"));
trsuperobject.biblemap.put("b5_5_6", new trsuperobject(5,5,6,"‹‹Seni Mısırdan, köle olduğun ülkeden çıkaran Tanrın RAB benim."));
trsuperobject.biblemap.put("b5_5_7", new trsuperobject(5,5,7,"‹‹Benden başka tanrın olmayacak."));
trsuperobject.biblemap.put("b5_5_8", new trsuperobject(5,5,8,"‹‹Kendine yukarıda gökyüzünde, aşağıda yeryüzünde ya da yer altındaki sularda yaşayan herhangi bir canlıya benzer put yapmayacaksın."));
trsuperobject.biblemap.put("b5_5_9", new trsuperobject(5,5,9,"Putların önünde eğilmeyecek, onlara tapmayacaksın. Çünkü ben, Tanrın RAB, kıskanç bir Tanrıyım. Benden nefret edenin babasının işlediği suçun hesabını çocuklarından, üçüncü, dördüncü kuşaklardan sorarım."));
trsuperobject.biblemap.put("b5_5_10", new trsuperobject(5,5,10,"Ama beni seven, buyruklarıma uyan binlerce kuşağa sevgi gösteririm."));
trsuperobject.biblemap.put("b5_5_11", new trsuperobject(5,5,11,"‹‹Tanrın RABbin adını boş yere ağzına almayacaksın. Çünkü RAB, adını boş yere ağzına alanları cezasız bırakmayacaktır."));
trsuperobject.biblemap.put("b5_5_12", new trsuperobject(5,5,12,"‹‹Tanrın RABbin buyruğu uyarınca Şabat Gününü tut ve kutsal say."));
trsuperobject.biblemap.put("b5_5_13", new trsuperobject(5,5,13,"Altı gün çalışacak, bütün işlerini yapacaksın."));
trsuperobject.biblemap.put("b5_5_14", new trsuperobject(5,5,14,"Ama yedinci gün bana, Tanrın RABbe Şabat Günü olarak adanmıştır. O gün sen, oğlun, kızın, erkek ve kadın kölen, öküzün, eşeğin ya da herhangi bir hayvanın, aranızdaki yabancılar dahil, hiçbir iş yapmayacaksınız. Öyle ki, senin gibi erkek ve kadın kölelerin de dinlensinler."));
trsuperobject.biblemap.put("b5_5_15", new trsuperobject(5,5,15,"Mısırda köle olduğunu ve Tanrın RABbin seni oradan güçlü ve kudretli eliyle çıkardığını anımsayacaksın. Tanrın RAB bu yüzden Şabat Gününü tutmanı buyurdu."));
trsuperobject.biblemap.put("b5_5_16", new trsuperobject(5,5,16,"‹‹Tanrın RABbin buyruğu uyarınca annene babana saygı göster. Öyle ki, ömrün uzun olsun ve Tanrın RABbin sana vereceği ülkede üzerine iyilik gelsin."));
trsuperobject.biblemap.put("b5_5_17", new trsuperobject(5,5,17,"‹‹Adam öldürmeyeceksin."));
trsuperobject.biblemap.put("b5_5_18", new trsuperobject(5,5,18,"‹‹Zina etmeyeceksin."));
trsuperobject.biblemap.put("b5_5_19", new trsuperobject(5,5,19,"‹‹Çalmayacaksın."));
trsuperobject.biblemap.put("b5_5_20", new trsuperobject(5,5,20,"‹‹Komşuna karşı yalan yere tanıklık etmeyeceksin."));
trsuperobject.biblemap.put("b5_5_21", new trsuperobject(5,5,21,"‹‹Komşunun karısına kötü gözle bakmayacaksın. Komşunun evine, tarlasına, erkek ve kadın kölesine, öküzüne, eşeğine, hiçbir şeyine göz dikmeyeceksin.›"));
trsuperobject.biblemap.put("b5_5_22", new trsuperobject(5,5,22,"‹‹RAB bu sözleri dağda ateşin, bulutun, koyu karanlığın içinden bütün topluluğunuza yüksek sesle söyledi. Başka bir şey eklemedi. Sonra bunları iki taş levha üstüne yazıp bana verdi."));
trsuperobject.biblemap.put("b5_5_23", new trsuperobject(5,5,23,"‹‹Dağ alev alev yanarken karanlığın içinden sesi duyduğunuzda bütün oymak başlarınız ve ileri gelenlerinizle bana yaklaştınız."));
trsuperobject.biblemap.put("b5_5_24", new trsuperobject(5,5,24,"‹Tanrımız RAB bize yüceliğini ve büyüklüğünü gösterdi› dediniz, ‹Ateşin içinden sesini duyduk. Bugün Tanrının insanla konuştuğunu ve insanın ölmediğini gördük."));
trsuperobject.biblemap.put("b5_5_25", new trsuperobject(5,5,25,"Neden şimdi ölelim? Bu büyük ateş bizi yakıp yok edecek. Tanrımız RABbin sesini bir daha duyarsak öleceğiz."));
trsuperobject.biblemap.put("b5_5_26", new trsuperobject(5,5,26,"Ateşin içinden seslenen, yaşayan Tanrının sesini bizim gibi duyup da sağ kalan var mı?"));
trsuperobject.biblemap.put("b5_5_27", new trsuperobject(5,5,27,"Sen git, Tanrımız RABbin söyleyeceklerini dinle. Sonra Tanrımız RABbin bütün söylediklerini bize anlat. Biz de kulak verip uyacağız.›"));
trsuperobject.biblemap.put("b5_5_28", new trsuperobject(5,5,28,"‹‹RAB benimle yaptığınız konuşmayı duyunca, şöyle dedi: ‹Bu halkın sana neler söylediğini duydum. Bütün söyledikleri doğrudur."));
trsuperobject.biblemap.put("b5_5_29", new trsuperobject(5,5,29,"Keşke benden korksalardı ve bütün buyruklarıma uymak için her zaman yürekten istekli olsalardı! O zaman kendilerine ve çocuklarına sürekli iyilik gelirdi."));
trsuperobject.biblemap.put("b5_5_30", new trsuperobject(5,5,30,"‹‹Git, çadırlarına dönmelerini söyle."));
trsuperobject.biblemap.put("b5_5_31", new trsuperobject(5,5,31,"Ama sen burada yanımda dur. Sana bütün buyrukları, kuralları, ilkeleri vereceğim. Bunları halka sen öğreteceksin. Öyle ki, mülk edinmek için kendilerine vereceğim ülkede hepsine uysunlar.›"));
trsuperobject.biblemap.put("b5_5_32", new trsuperobject(5,5,32,"‹‹Tanrınız RABbin size buyurduklarına uymaya özen gösterin. Onlardan sağa sola sapmayın."));
trsuperobject.biblemap.put("b5_5_33", new trsuperobject(5,5,33,"Tanrınız RAB'bin size buyurduğu yollarda yürüyün. Öyle ki, mülk edineceğiniz ülkede sağ kalasınız, başarılı ve uzun ömürlü olasınız.››"));
trsuperobject.biblemap.put("b5_6_1", new trsuperobject(5,6,1,"‹‹Tanrınız RABbin size öğretmek için bana verdiği buyruklar, kurallar, ilkeler bunlardır. Mülk edinmek için gideceğiniz ülkede onlara uyun."));
trsuperobject.biblemap.put("b5_6_2", new trsuperobject(5,6,2,"Yaşamınız boyunca siz, çocuklarınız ve torunlarınız, size verdiğim bütün kurallara, buyruklara uyarak Tanrınız RABden korkun ki, ömrünüz uzun olsun."));
trsuperobject.biblemap.put("b5_6_3", new trsuperobject(5,6,3,"Kulak ver, ey İsrail! Söz dinleyin ki, üzerinize iyilik gelsin, atalarınızın Tanrısı RABbin size verdiği söz uyarınca süt ve bal akan ülkede bol bol çoğalasınız."));
trsuperobject.biblemap.put("b5_6_4", new trsuperobject(5,6,4,"‹‹Dinle, ey İsrail! Tanrımız RAB tek RABdir."));
trsuperobject.biblemap.put("b5_6_5", new trsuperobject(5,6,5,"Tanrınız RABbi bütün yüreğinizle, bütün canınızla, bütün gücünüzle seveceksiniz."));
trsuperobject.biblemap.put("b5_6_6", new trsuperobject(5,6,6,"Bugün size verdiğim bu buyrukları aklınızda tutun."));
trsuperobject.biblemap.put("b5_6_7", new trsuperobject(5,6,7,"Onları çocuklarınıza benimsetin. Evinizde otururken, yolda yürürken, yatarken, kalkarken onlardan söz edin."));
trsuperobject.biblemap.put("b5_6_8", new trsuperobject(5,6,8,"Bir belirti olarak onları ellerinize bağlayın, alın sargısı olarak takın."));
trsuperobject.biblemap.put("b5_6_9", new trsuperobject(5,6,9,"Evlerinizin kapı sövelerine, kentlerinizin kapılarına yazın.›› yalnız ve yalnız RAB›› veya ‹‹RAB Tanrımızdır, RAB tektir››."));
trsuperobject.biblemap.put("b5_6_1-011", new trsuperobject(5,6,1-011,"‹‹Tanrınız RAB atalarınıza, İbrahime, İshaka, Yakupa içtiği ant uyarınca, sizi vereceği ülkeye -inşa etmediğiniz büyük ve güzel kentleri, biriktirmediğiniz iyi eşyalarla dolu evleri, siz emek vermeden kazılmış sarnıçları, dikmediğiniz bağları, zeytinlikleri olan ülkeye- götürecek. Orada yiyip doyacaksınız."));
trsuperobject.biblemap.put("b5_6_12", new trsuperobject(5,6,12,"O zaman dikkat edin! Sizi Mısırdan, köle olduğunuz ülkeden çıkaran RABbi unutmayın."));
trsuperobject.biblemap.put("b5_6_13", new trsuperobject(5,6,13,"‹‹Tanrınız RABden korkacaksınız; Ona kulluk edecek ve Onun adıyla ant içeceksiniz."));
trsuperobject.biblemap.put("b5_6_14", new trsuperobject(5,6,14,"Başka ilahların, çevrenizdeki ulusların taptığı hiçbir ilahın ardınca gitmeyeceksiniz."));
trsuperobject.biblemap.put("b5_6_15", new trsuperobject(5,6,15,"Çünkü aranızda olan Tanrınız RAB kıskanç bir Tanrıdır. Öfkelenirse sizi yeryüzünden yok eder."));
trsuperobject.biblemap.put("b5_6_16", new trsuperobject(5,6,16,"Massada olduğu gibi, Tanrınız RABbi denemeyeceksiniz."));
trsuperobject.biblemap.put("b5_6_17", new trsuperobject(5,6,17,"Tanrınız RABbin buyruklarına, size verdiği yasalara, kurallara uymaya dikkat edeceksiniz."));
trsuperobject.biblemap.put("b5_6_18", new trsuperobject(5,6,18,"RABbin gözünde iyi ve doğru olanı yapacaksınız. Öyle ki, üzerinize iyilik gelsin, RABbin atalarınıza ant içerek söz verdiği verimli ülkeyi mülk edinesiniz."));
trsuperobject.biblemap.put("b5_6_19", new trsuperobject(5,6,19,"RAB de sözü uyarınca bütün düşmanlarınızı önünüzden kovacak."));
trsuperobject.biblemap.put("b5_6_20", new trsuperobject(5,6,20,"‹‹Gelecekte çocuklarınız size, ‹Tanrımız RABbin size verdiği yasaların, kuralların, ilkelerin anlamı nedir?› diye sorunca,"));
trsuperobject.biblemap.put("b5_6_21", new trsuperobject(5,6,21,"onlara şöyle diyeceksiniz: ‹Mısırda firavunun köleleriydik. RAB bizi güçlü eliyle oradan çıkardı."));
trsuperobject.biblemap.put("b5_6_22", new trsuperobject(5,6,22,"Gözlerimizin önünde Mısıra, firavuna, ailesine karşı belirtiler, büyük ve korkunç işler yaptı."));
trsuperobject.biblemap.put("b5_6_23", new trsuperobject(5,6,23,"Atalarımıza ant içerek söz verdiği ülkeye götürmek ve orayı bize vermek için bizi Mısırdan çıkardı."));
trsuperobject.biblemap.put("b5_6_24", new trsuperobject(5,6,24,"Sürekli üzerimize iyilik gelmesi ve bugün olduğu gibi sağ kalmamız için Tanrımız RAB bütün bu kurallara uymamızı ve kendisinden korkmamızı buyurdu."));
trsuperobject.biblemap.put("b5_6_25", new trsuperobject(5,6,25,"Tanrımız RAB'bin önünde, verdiği bu buyruklara uymaya dikkat edersek, bunu bize doğruluk sayacaktır.››"));
trsuperobject.biblemap.put("b5_7_1", new trsuperobject(5,7,1,"‹‹Tanrınız RAB mülk edinmek üzere gideceğiniz ülkeye sizi götürdüğünde, önünüzden birçok ulusu -Hititleri, Girgaşlıları, Amorluları, Kenanlıları, Perizlileri, Hivlileri, Yevusluları, sizden daha büyük ve daha güçlü yedi ulusu- kovacak."));
trsuperobject.biblemap.put("b5_7_2", new trsuperobject(5,7,2,"Tanrınız RAB bu ulusları elinize teslim ettiğinde, onları bozguna uğrattığınızda, tümünü yok etmelisiniz. Bu uluslarla antlaşma yapmayacaksınız, onlara acımayacaksınız."));
trsuperobject.biblemap.put("b5_7_3", new trsuperobject(5,7,3,"Kız alıp vermeyeceksiniz. Kızlarınızı oğullarına vermeyeceksiniz; oğullarınıza da onlardan kız almayacaksınız."));
trsuperobject.biblemap.put("b5_7_4", new trsuperobject(5,7,4,"Çünkü onlar oğullarınızı beni izlemekten saptıracak, başka ilahlara tapmalarına neden olacaklardır. O zaman RAB size öfkelenecek ve sizi çabucak yok edecek."));
trsuperobject.biblemap.put("b5_7_5", new trsuperobject(5,7,5,"Onlara şöyle yapacaksınız: Sunaklarını yıkacak, dikili taşlarını parçalayacak, Aşera putlarını devirecek, öbür putlarını yakacaksınız."));
trsuperobject.biblemap.put("b5_7_6", new trsuperobject(5,7,6,"‹‹Siz Tanrınız RAB için kutsal bir halksınız. Tanrınız RAB, öz halkı olmanız için, yeryüzündeki bütün halkların arasından sizi seçti."));
trsuperobject.biblemap.put("b5_7_9", new trsuperobject(5,7,9,"Tanrınız RABbin Tanrı olduğunu bilin. O güvenilir Tanrıdır. Kendisini sevenlerin, buyruklarına uyanların bininci kuşağına kadar antlaşmasına bağlı kalır."));
trsuperobject.biblemap.put("b5_7_10", new trsuperobject(5,7,10,"Kendisinden nefret edenlere ise üzerlerine yıkım göndererek karşılık verir. RAB kendisinden nefret edene karşılık vermekte gecikmeyecek."));
trsuperobject.biblemap.put("b5_7_11", new trsuperobject(5,7,11,"Onun için, bugün size bildirdiğim buyruklara, kurallara, ilkelere uymaya dikkat edin.››"));
trsuperobject.biblemap.put("b5_7_12", new trsuperobject(5,7,12,"‹‹Bu ilkeleri dinler, onlara özenle uyarsanız, Tanrınız RAB atalarınıza ant içerek verdiği söz uyarınca sizinle yaptığı antlaşmaya bağlı kalacak."));
trsuperobject.biblemap.put("b5_7_13", new trsuperobject(5,7,13,"Sizi sevecek, kutsayacak, çoğaltacak. Atalarınıza ant içerek size söz verdiği ülkede rahminizin meyvesini, toprağınızın ürününü -tahılını, yeni şarabını, zeytinyağını- sığırlarınızın buzağılarını, sürülerinizin kuzularını bereketli kılacak."));
trsuperobject.biblemap.put("b5_7_14", new trsuperobject(5,7,14,"Öbür halklardan daha çok kutsanmış olacaksınız. Erkekleriniz, kadınlarınız, hayvanlarınız arasında döl vermeyen olmayacak."));
trsuperobject.biblemap.put("b5_7_15", new trsuperobject(5,7,15,"RAB her türlü hastalığı sizden uzaklaştıracak. Mısırda gördüğünüz korkunç hastalıklardan hiçbirini size vermeyecek. Bütün bu hastalıkları sizden nefret edenlere verecek."));
trsuperobject.biblemap.put("b5_7_16", new trsuperobject(5,7,16,"Tanrınız RABbin elinize teslim edeceği halkların tümünü yok edeceksiniz. Onlara acımayacaksınız. İlahlarına tapmayacaksınız. Çünkü bu sizin için tuzak olacaktır."));
trsuperobject.biblemap.put("b5_7_17", new trsuperobject(5,7,17,"‹‹Bu uluslar bizden daha güçlü. Onları nasıl kovabiliriz?› diye düşünebilirsiniz."));
trsuperobject.biblemap.put("b5_7_18", new trsuperobject(5,7,18,"Onlardan korkmayacaksınız. Tanrınız RABbin firavuna ve bütün Mısıra yaptıklarını her zaman anımsayın."));
trsuperobject.biblemap.put("b5_7_19", new trsuperobject(5,7,19,"Tanrınız RABbin sizi Mısırdan çıkarmak için yaptığı büyük denemeleri, belirtileri, şaşılası işleri, güçlü ve kudretli elini gözlerinizle gördünüz. Tanrınız RAB şimdi korktuğunuz bütün bu halklara aynısını yapacaktır."));
trsuperobject.biblemap.put("b5_7_20", new trsuperobject(5,7,20,"Sizden gizlenerek sağ kalmış olanların üzerine, hepsi yok olana dek eşekarısı gönderecek."));
trsuperobject.biblemap.put("b5_7_21", new trsuperobject(5,7,21,"Onlardan yılmayacaksınız. Aranızda olan Tanrınız RAB ulu ve heybetli bir Tanrıdır."));
trsuperobject.biblemap.put("b5_7_22", new trsuperobject(5,7,22,"Bu ulusları önünüzden azar azar kovacak. Onları birden ortadan kaldıramazsınız. Yoksa çevrenizde yabanıl hayvanlar çoğalır."));
trsuperobject.biblemap.put("b5_7_23", new trsuperobject(5,7,23,"Tanrınız RAB onları elinize teslim edecek ve hepsi yok oluncaya dek onları şaşkına çevirecek."));
trsuperobject.biblemap.put("b5_7_24", new trsuperobject(5,7,24,"Krallarını elinize teslim edecek; adlarını göğün altından sileceksiniz. Onları yok edene dek kimse size karşı duramayacak."));
trsuperobject.biblemap.put("b5_7_25", new trsuperobject(5,7,25,"İlahlarını simgeleyen putları yakacaksınız; üzerlerindeki altına, gümüşe göz dikmeyecek, bunları kendinize ayırmayacaksınız. Öyle ki, tuzağa düşmeyesiniz. Bu putlar Tanrınız RABbin gözünde iğrençtir."));
trsuperobject.biblemap.put("b5_7_26", new trsuperobject(5,7,26,"Bu iğrenç şeyleri evinize getirmeyeceksiniz, yoksa siz de onlar gibi yok olursunuz. Onlardan çok nefret edecek, tiksineceksiniz; çünkü onlar yok olmaya mahkûmdur.›› Dehşet ya da bir çeşit hastalık anlamına da gelebilir."));
trsuperobject.biblemap.put("b5_8_1", new trsuperobject(5,8,1,"‹‹Bugün size bildirdiğim buyruklara tam tamına uyun ki, yaşayasınız, çoğalasınız ve gidip RABbin atalarınıza ant içerek söz verdiği ülkeyi mülk edinesiniz."));
trsuperobject.biblemap.put("b5_8_2", new trsuperobject(5,8,2,"Tanrınız RABbin sizi kırk yıl boyunca çölde dolaştırdığı uzun yolculuğu anımsayın! Buyruklarına uyup uymayacağınızı, amacınızın ne olduğunu öğrenmek için sizi sıkıntılara sokarak sınadı."));
trsuperobject.biblemap.put("b5_8_3", new trsuperobject(5,8,3,"Sizi aç bırakarak sıkıntıya soktu. Sonra sizin de atalarınızın da bilmediği man ile sizi doyurdu. İnsanın yalnız ekmekle yaşamadığını, RABbin ağzından çıkan her sözle yaşadığını size öğretmek için yaptı bunu."));
trsuperobject.biblemap.put("b5_8_4", new trsuperobject(5,8,4,"Kırk yıl ne giysileriniz eskidi, ne de ayaklarınız şişti."));
trsuperobject.biblemap.put("b5_8_5", new trsuperobject(5,8,5,"Tanrınız RABbin, çocuğunu eğiten bir baba gibi, sizi nasıl eğittiğini anlayın."));
trsuperobject.biblemap.put("b5_8_6", new trsuperobject(5,8,6,"‹‹Onun için, Tanrınız RABbin buyruklarına uyun. Yollarında yürüyün, Ondan korkun."));
trsuperobject.biblemap.put("b5_8_7", new trsuperobject(5,8,7,"Tanrınız RAB sizi verimli bir ülkeye götürüyor. Öyle bir ülke ki, ırmakları, pınarları, derelerden tepelerden çıkan su kaynakları vardır;"));
trsuperobject.biblemap.put("b5_8_8", new trsuperobject(5,8,8,"buğdayı, arpası, üzümü, inciri, narı, zeytinyağı, balı vardır."));
trsuperobject.biblemap.put("b5_8_9", new trsuperobject(5,8,9,"Sıkıntısız ekmek yiyebileceğiniz, hiçbir şeye gereksinim duymayacağınız bir ülkedir. Öyle bir ülke ki, kayaları demirdir, dağlarından bakır çıkarabilirsiniz."));
trsuperobject.biblemap.put("b5_8_10", new trsuperobject(5,8,10,"‹‹Yiyip doyunca, size verdiği verimli ülke için Tanrınız RABbe övgüler sunun."));
trsuperobject.biblemap.put("b5_8_11", new trsuperobject(5,8,11,"Tanrınız RABbi unutmamaya dikkat edin. Bugün size bildirdiğim buyruklarını, ilkelerini, kurallarını savsaklamayın."));
trsuperobject.biblemap.put("b5_8_12", new trsuperobject(5,8,12,"Yiyip doyduğunuzda, güzel evler yapıp yerleştiğinizde,"));
trsuperobject.biblemap.put("b5_8_13", new trsuperobject(5,8,13,"sığırlarınız, davarlarınız çoğaldığında, altınınız, gümüşünüz ve her şeyiniz arttığında,"));
trsuperobject.biblemap.put("b5_8_14", new trsuperobject(5,8,14,"böbürlenmemeye ve sizi Mısırdan, köle olduğunuz ülkeden çıkaran Tanrınız RABbi unutmamaya dikkat edin."));
trsuperobject.biblemap.put("b5_8_15", new trsuperobject(5,8,15,"RAB o büyük ve korkunç çölde, zehirli yılanlarla, akreplerle dolu o kurak, susuz toprakta sizi yürüttü. Size sert kayadan su çıkardı."));
trsuperobject.biblemap.put("b5_8_16", new trsuperobject(5,8,16,"Atalarınızın bilmediği man ile sizi çölde doyurdu. Sizi sıkıntıya soktu, sınadı. Öyle ki, sonunda üzerinize iyilik gelsin."));
trsuperobject.biblemap.put("b5_8_17", new trsuperobject(5,8,17,"‹Bu serveti toplayan kendi yeteneğimiz, güçlü elimizdir› diye düşünebilirsiniz."));
trsuperobject.biblemap.put("b5_8_18", new trsuperobject(5,8,18,"Ancak bu serveti toplama yeteneğini size verenin Tanrınız RAB olduğunu anımsayın. Atalarınıza ant içerek yaptığı antlaşmayı sürdürmek amacıyla bugün de bunu yapıyor."));
trsuperobject.biblemap.put("b5_8_19", new trsuperobject(5,8,19,"‹‹Tanrınız RABbi unutur, başka ilahların ardınca giderseniz, onlara tapar, önlerinde yere kapanırsanız, bugün size açıkça belirtirim ki, tamamen yok olacaksınız."));
trsuperobject.biblemap.put("b5_8_20", new trsuperobject(5,8,20,"Tanrınız RAB önünüzden ulusları yok ettiği gibi, sözüne kulak vermediğiniz için sizi de yok edecek."));
trsuperobject.biblemap.put("b5_9_1", new trsuperobject(5,9,1,"‹‹Ey İsrail, kulak ver! Bugün sizden daha büyük, daha güçlü ulusların topraklarını mülk edinmek için Şeria Irmağından geçeceksiniz. Onların kentleri büyük, surları göğe dek yükseliyor."));
trsuperobject.biblemap.put("b5_9_2", new trsuperobject(5,9,2,"Bu güçlü, uzun boylu halk Anaklılardır. Onları biliyorsunuz. ‹Kim Anaklılara karşı durabilir?› deyişini duydunuz."));
trsuperobject.biblemap.put("b5_9_3", new trsuperobject(5,9,3,"Bilin ki, yakıp yok eden ateş olan Tanrınız RAB önünüzden gidecek. Onları ortadan kaldıracak, size boyun eğmelerini sağlayacak. Onları kovacaksınız, RABbin verdiği söz uyarınca bir çırpıda yok edeceksiniz.››"));
trsuperobject.biblemap.put("b5_9_4", new trsuperobject(5,9,4,"‹‹Tanrınız RAB bu ulusları önünüzden kovunca, ‹RAB doğruluğumuzdan ötürü bu ülkeyi mülk edinelim diye bizi buraya getirdi› diye düşünmeyin. Çünkü RAB, bu ulusları yaptıkları kötülükler yüzünden önünüzden kovuyor."));
trsuperobject.biblemap.put("b5_9_5", new trsuperobject(5,9,5,"Onların topraklarını mülk edinmeye gitmenizin nedeni doğruluğunuz, erdeminiz değildir. Tanrınız RAB bu ulusları kötülükleri yüzünden ve atalarınız İbrahime, İshaka, Yakupa ant içerek verdiği sözü yerine getirmek için önünüzden kovacak."));
trsuperobject.biblemap.put("b5_9_6", new trsuperobject(5,9,6,"Şunu anlayın ki, Tanrınız RABbin bu verimli toprakları mülk edinesiniz diye size vermesinin nedeni doğruluğunuz değildir. Çünkü siz dikbaşlı bir halksınız."));
trsuperobject.biblemap.put("b5_9_7", new trsuperobject(5,9,7,"‹‹Tanrınız RABbi çölde nasıl kızdırdığınızı anımsayın, hiç unutmayın. Mısırdan çıktığınız günden buraya varıncaya dek, RABbe sürekli karşı geldiniz."));
trsuperobject.biblemap.put("b5_9_8", new trsuperobject(5,9,8,"Horev Dağında RABbi öyle kızdırdınız ki, sizi yok edecek kadar öfkelendi."));
trsuperobject.biblemap.put("b5_9_9", new trsuperobject(5,9,9,"Daha önce taş levhaları -RABbin sizinle yaptığı antlaşmanın levhalarını- almak için dağa çıkmıştım; orada kırk gün, kırk gece kaldım. Ne yedim, ne içtim."));
trsuperobject.biblemap.put("b5_9_10", new trsuperobject(5,9,10,"RAB Tanrı parmağıyla yazmış olduğu iki taş levhayı bana verdi. Bu levhalar, dağda toplandığınız gün RABbin ateşin içinden size bildirdiği bütün buyrukları içermekteydi."));
trsuperobject.biblemap.put("b5_9_11", new trsuperobject(5,9,11,"Kırk gün, kırk gece sonra RAB bana iki taş levhayı, antlaşma levhalarını verdi."));
trsuperobject.biblemap.put("b5_9_12", new trsuperobject(5,9,12,"‹‹Haydi, buradan hemen in› dedi, ‹Çünkü Mısırdan çıkardığın halkın yoldan çıktı. Onlara buyurduğum yoldan hemen saptılar. Kendilerine dökme bir put yaptılar.›"));
trsuperobject.biblemap.put("b5_9_13", new trsuperobject(5,9,13,"Sonra RAB bana, ‹Bu halkı gördüm› dedi, ‹İşte dikbaşlı bir halk!"));
trsuperobject.biblemap.put("b5_9_14", new trsuperobject(5,9,14,"Bırak da onları yok edeyim; adlarını da göğün altından sileyim. Seni onlardan daha güçlü, daha büyük bir ulus kılayım.›"));
trsuperobject.biblemap.put("b5_9_15", new trsuperobject(5,9,15,"‹‹Dönüp dağdan aşağıya indim. Dağ alev alev yanıyordu. Antlaşmanın iki levhası iki elimdeydi."));
trsuperobject.biblemap.put("b5_9_16", new trsuperobject(5,9,16,"Tanrınız RABbe karşı günah işlediğinizi gördüm. Kendinize buzağıya benzer bir dökme put yapmıştınız. RABbin size buyurduğu yoldan hemen sapmıştınız."));
trsuperobject.biblemap.put("b5_9_17", new trsuperobject(5,9,17,"Bu yüzden iki levhayı fırlatıp attım, gözünüzün önünde parçaladım."));
trsuperobject.biblemap.put("b5_9_18", new trsuperobject(5,9,18,"Bir kez daha RABbin huzurunda bir şey yemeden, içmeden kırk gün kırk gece yere kapanıp kaldım. Çünkü günah işlemiştiniz; RABbin gözünde kötü olanı yaparak Onu öfkelendirmiştiniz."));
trsuperobject.biblemap.put("b5_9_19", new trsuperobject(5,9,19,"RABbin kızgın öfkesi karşısında korktum. Öfkesi sizi yok edecek kadar alevlenmişti. Ama RAB yakarışımı yine duydu."));
trsuperobject.biblemap.put("b5_9_20", new trsuperobject(5,9,20,"RAB Haruna da onu yok edecek kadar öfkelenmişti. O sırada Harun için de yakardım."));
trsuperobject.biblemap.put("b5_9_21", new trsuperobject(5,9,21,"Yaptığınız günahlı nesneyi, o buzağıya benzer dökme putu alıp yaktım. Parçalayıp ince toz haline getirinceye dek ezdim. Sonra tozu dağdan akan dereye attım."));
trsuperobject.biblemap.put("b5_9_22", new trsuperobject(5,9,22,"‹‹Taverada, Massada, Kivrot-Hattaavada da RABbi öfkelendirdiniz."));
trsuperobject.biblemap.put("b5_9_23", new trsuperobject(5,9,23,"RAB sizi Kadeş-Barneadan gönderirken, ‹Gidin, size vereceğim ülkeyi mülk edinin› diye buyurmuştu. Sizse Tanrınız RABbin buyruğuna karşı geldiniz. Ona güvenmediniz, sözüne kulak vermediniz."));
trsuperobject.biblemap.put("b5_9_24", new trsuperobject(5,9,24,"Sizi tanıdığım günden bu yana RABbe sürekli karşı geldiniz."));
trsuperobject.biblemap.put("b5_9_25", new trsuperobject(5,9,25,"‹‹RAB sizi yok edeceğini söylediği için, kırk gün kırk gece Onun önünde yere kapanıp kaldım."));
trsuperobject.biblemap.put("b5_9_26", new trsuperobject(5,9,26,"RABbe şöyle yakardım: ‹Ey Egemen RAB, büyük kudretinle kurtarıp güçlü elinle Mısırdan çıkardığın halkını, kendi mirasını yok etme."));
trsuperobject.biblemap.put("b5_9_27", new trsuperobject(5,9,27,"Kulların İbrahimi, İshakı, Yakupu anımsa. Bu halkın dikbaşlılığını, kötülüğünü, günahını dikkate alma."));
trsuperobject.biblemap.put("b5_9_28", new trsuperobject(5,9,28,"Yoksa bizi çıkardığın ülkenin halkı, ‹RAB söz verdiği ülkeye götüremediği, onlardan nefret ettiği için çölde yok etmek amacıyla onları Mısırdan çıkardı› diyecek."));
trsuperobject.biblemap.put("b5_9_29", new trsuperobject(5,9,29,"Oysa onlar, büyük güçle ve kudretli elinle Mısır'dan çıkardığın kendi halkın ve mirasındır.››"));
trsuperobject.biblemap.put("b5_10_1", new trsuperobject(5,10,1,"‹‹O zaman RAB bana, ‹Öncekiler gibi iki taş levha kes ve dağa, yanıma çık› dedi, ‹Ağaçtan bir sandık yap."));
trsuperobject.biblemap.put("b5_10_2", new trsuperobject(5,10,2,"Parçaladığın önceki levhalara yazılı buyrukları yeni levhalara yazacağım. Sonra onları sandığa koyacaksın.›"));
trsuperobject.biblemap.put("b5_10_3", new trsuperobject(5,10,3,"‹‹Böylece akasya ağacından bir sandık yaptım. Öncekiler gibi iki taş levha kestim. İki levhayı da alıp dağa çıktım."));
trsuperobject.biblemap.put("b5_10_5", new trsuperobject(5,10,5,"Sonra dönüp dağdan indim. RABbin buyruğu uyarınca, levhaları yaptığım sandığa koydum. Orada duruyorlar.››"));
trsuperobject.biblemap.put("b5_10_6", new trsuperobject(5,10,6,"İsrailliler Yaakanoğullarına ait kuyulardan ayrılıp Moseraya gittiler. Harun orada öldü ve gömüldü. Yerine oğlu Elazar kâhin oldu."));
trsuperobject.biblemap.put("b5_10_7", new trsuperobject(5,10,7,"İsrailliler oradan Gudgodaya, sonra da akarsular bölgesi olan Yotvataya göç ettiler."));
trsuperobject.biblemap.put("b5_10_8", new trsuperobject(5,10,8,"O zaman RAB, kendi Antlaşma Sandığını taşıması, kendisine hizmet etmek üzere önünde durması ve Onun adıyla kutsaması için Levililer oymağını ayırdı. Bugün de aynı görevi yapıyorlar."));
trsuperobject.biblemap.put("b5_10_9", new trsuperobject(5,10,9,"Bu yüzden Levililer kardeşleri olan öbür oymaklar gibi pay ve mülk almadılar. Tanrınız RABbin onlara verdiği söz uyarınca onların mirası RABdir.-"));
trsuperobject.biblemap.put("b5_10_10", new trsuperobject(5,10,10,"‹‹Daha önce yaptığım gibi dağda kırk gün, kırk gece kaldım. RAB yine yakarışımı duydu ve sizi yok etmek istemedi."));
trsuperobject.biblemap.put("b5_10_11", new trsuperobject(5,10,11,"Sonra, ‹Kalk, git› dedi, ‹Onları atalarına ant içerek söz verdiğim ülkeye götür. Gidip orayı mülk edinsinler.››"));
trsuperobject.biblemap.put("b5_10_12", new trsuperobject(5,10,12,"‹‹Şimdi, ey İsrail halkı, Tanrınız RAB sizden ne istiyor? Yalnız şunu istiyor: Tanrınız RABden korkun, Onun yollarında yürüyün, Onu sevin; bütün yüreğinizle, bütün canınızla Ona kulluk edin;"));
trsuperobject.biblemap.put("b5_10_13", new trsuperobject(5,10,13,"üzerinize iyilik gelsin diye bugün size bildirdiğim buyruklarına, kurallarına uyun."));
trsuperobject.biblemap.put("b5_10_14", new trsuperobject(5,10,14,"Gökler de, göklerin gökleri de, yeryüzü ve içindeki her şey Tanrınız RABbindir."));
trsuperobject.biblemap.put("b5_10_15", new trsuperobject(5,10,15,"Öyleyken RAB atalarınızı sevdi, onlara bağlandı. Bugün olduğu gibi, onların soyu olan sizleri bütün halkların arasından seçti."));
trsuperobject.biblemap.put("b5_10_16", new trsuperobject(5,10,16,"Yüreklerinizi RABbe adayın, bundan böyle dikbaşlı olmayın."));
trsuperobject.biblemap.put("b5_10_17", new trsuperobject(5,10,17,"Çünkü Tanrınız RAB, tanrıların Tanrısı, rablerin Rabbidir. O kimseyi kayırmayan, rüşvet almayan, ulu, güçlü, heybetli Tanrıdır."));
trsuperobject.biblemap.put("b5_10_18", new trsuperobject(5,10,18,"Öksüzlerin, dul kadınların hakkını gözetir. Yabancıları sever, onlara yiyecek, giyecek sağlar."));
trsuperobject.biblemap.put("b5_10_19", new trsuperobject(5,10,19,"Siz de yabancıları seveceksiniz. Çünkü Mısırda siz de yabancıydınız."));
trsuperobject.biblemap.put("b5_10_20", new trsuperobject(5,10,20,"Tanrınız RABden korkun, Ona kulluk edin. Ona bağlı kalın ve Onun adıyla ant için."));
trsuperobject.biblemap.put("b5_10_21", new trsuperobject(5,10,21,"O övgünüzdür. Gözlerinizle gördüğünüz o büyük, heybetli belirtileri sizin için gerçekleştiren Tanrınızdır."));
trsuperobject.biblemap.put("b5_10_22", new trsuperobject(5,10,22,"Mısır'a giden atalarınız yetmiş kişiydi. Şimdiyse Tanrınız RAB sizi göklerdeki yıldızlar kadar çoğalttı.›› sünnet edin››."));
trsuperobject.biblemap.put("b5_11_1", new trsuperobject(5,11,1,"‹‹Tanrınız RABbi sevin. Uyarılarına, kurallarına, ilkelerine, buyruklarına her zaman uyun."));
trsuperobject.biblemap.put("b5_11_2", new trsuperobject(5,11,2,"Unutmayın ki, Tanrınız RABbin tedibini görüp yaşayan çocuklarınız değil, sizsiniz: Büyüklüğünü, güçlü elini, kudretini,"));
trsuperobject.biblemap.put("b5_11_3", new trsuperobject(5,11,3,"belirtilerini, Mısırda firavuna ve bütün ülkesine yaptıklarını;"));
trsuperobject.biblemap.put("b5_11_4", new trsuperobject(5,11,4,"Mısır ordusuna, atlarına, savaş arabalarına neler yaptığını; Mısırlılar sizi kovalarken onları nasıl Kızıldenizin suları altında bıraktığını, onları nasıl yok ettiğini gördünüz."));
trsuperobject.biblemap.put("b5_11_5", new trsuperobject(5,11,5,"Buraya varıncaya dek RABbin çölde sizin için neler yaptığını;"));
trsuperobject.biblemap.put("b5_11_6", new trsuperobject(5,11,6,"Rubenoğullarından Eliavın oğulları Datanla Avirama neler ettiğini; bütün İsrailin gözü önünde yerin nasıl yarıldığını, onları, ailelerini, çadırlarını ve onlara ait her canlıyı nasıl yuttuğunu gören çocuklarınız değil, sizsiniz."));
trsuperobject.biblemap.put("b5_11_8", new trsuperobject(5,11,8,"‹‹Bugün size bildirdiğim buyrukların tümüne uyun ki, güçlü olasınız, mülk edinmek üzere Şeria Irmağından geçip ülkeyi ele geçiresiniz."));
trsuperobject.biblemap.put("b5_11_10", new trsuperobject(5,11,10,"Mülk edinmek için gideceğiniz ülke, çıkmış olduğunuz Mısır gibi değildir. Orada tohumunuzu eker, sebze bahçesi gibi zorluklafı sulardınız."));
trsuperobject.biblemap.put("b5_11_11", new trsuperobject(5,11,11,"Mülk edinmek üzere gideceğiniz ülkenin ise dağları, dereleri vardır. Toprağı gökten yağan yağmurla sulanır."));
trsuperobject.biblemap.put("b5_11_12", new trsuperobject(5,11,12,"Orası Tanrınız RABbin kayırdığı bir ülkedir. Tanrınız RAB orayı bütün yıl sürekli gözetir."));
trsuperobject.biblemap.put("b5_11_13", new trsuperobject(5,11,13,"‹‹Tanrınız RABbi sevmek, bütün yüreğinizle, bütün canınızla Ona kulluk etmek için bugün size bildirdiğim buyruklara iyice kulak verirseniz,"));
trsuperobject.biblemap.put("b5_11_14", new trsuperobject(5,11,14,"RAB ülkenize ilk ve son yağmuru vaktinde yağdıracak. Öyle ki, tahılınızı, yeni şarabınızı, zeytinyağınızı toplayasınız."));
trsuperobject.biblemap.put("b5_11_15", new trsuperobject(5,11,15,"RAB tarlalarda hayvanlarınız için ot sağlayacak, siz de yiyip doyacaksınız."));
trsuperobject.biblemap.put("b5_11_16", new trsuperobject(5,11,16,"Sakının, ayartılıp yoldan çıkmayasınız; başka ilahlara tapmayasınız, önlerinde eğilmeyesiniz."));
trsuperobject.biblemap.put("b5_11_17", new trsuperobject(5,11,17,"Öyle ki, RAB size öfkelenmesin; yağmur yağmasın, toprak ürün vermesin diye gökleri kapamasın; size vereceği verimli ülkede çabucak yok olmayasınız."));
trsuperobject.biblemap.put("b5_11_18", new trsuperobject(5,11,18,"‹‹Bu sözlerimi aklınızda ve yüreğinizde tutun. Bir belirti olarak ellerinize bağlayın, alın sargısı olarak takın."));
trsuperobject.biblemap.put("b5_11_19", new trsuperobject(5,11,19,"Onları çocuklarınıza öğretin. Evinizde otururken, yolda yürürken, yatarken, kalkarken onlardan söz edin."));
trsuperobject.biblemap.put("b5_11_20", new trsuperobject(5,11,20,"Evlerinizin kapı sövelerine, kentlerinizin kapılarına yazın."));
trsuperobject.biblemap.put("b5_11_21", new trsuperobject(5,11,21,"Öyle ki, RABbin atalarınıza vermeye söz verdiği topraklar üzerinde sizin de, çocuklarınızın da ömrü uzun olsun ve yeryüzünün üstünde gökler olduğu sürece orada yaşayasınız."));
trsuperobject.biblemap.put("b5_11_22", new trsuperobject(5,11,22,"‹‹Uymanız için size bildirdiğim bu buyrukları eksiksiz yerine getirir, Tanrınız RABbi sever, yollarında yürür, Ona bağlı kalırsanız,"));
trsuperobject.biblemap.put("b5_11_23", new trsuperobject(5,11,23,"RAB bu ulusların tümünü önünüzden kovacak. Sizden daha büyük, daha güçlü ulusların topraklarını mülk edineceksiniz."));
trsuperobject.biblemap.put("b5_11_24", new trsuperobject(5,11,24,"Ayak basacağınız her yer sizin olacak. Sınırlarınız çölden Lübnana, Fırat Irmağından Akdenize kadar uzanacak."));
trsuperobject.biblemap.put("b5_11_25", new trsuperobject(5,11,25,"Hiç kimse size karşı koyamayacak. Tanrınız RAB, size verdiği söz uyarınca, ayak basacağınız her yere dehşetinizi, korkunuzu saçacaktır."));
trsuperobject.biblemap.put("b5_11_26", new trsuperobject(5,11,26,"‹‹Bakın, bugün önünüze kutsamayı ve laneti koyuyorum:"));
trsuperobject.biblemap.put("b5_11_27", new trsuperobject(5,11,27,"Bugün size bildirdiğim Tanrınız RABbin buyruklarına uyarsanız kutsanacaksınız."));
trsuperobject.biblemap.put("b5_11_28", new trsuperobject(5,11,28,"Ama Tanrınız RABbin buyruklarını dinlemez, bilmediğiniz başka ilahların ardınca giderek bugün size buyurduğum yoldan saparsanız, lanete uğrayacaksınız."));
trsuperobject.biblemap.put("b5_11_29", new trsuperobject(5,11,29,"Tanrınız RAB mülk edinmek için gideceğiniz ülkeye sizi götürdüğünde, Gerizim Dağında kutsama yapacak, Eval Dağında lanet okuyacaksınız."));
trsuperobject.biblemap.put("b5_11_30", new trsuperobject(5,11,30,"Bu iki dağ Şeria Irmağının karşı yakasında, yolun batısında, Aravada oturan Kenanlılar ülkesinde, Gilgal karşısında, More meşeliği yanındadır."));
trsuperobject.biblemap.put("b5_11_31", new trsuperobject(5,11,31,"Tanrınız RABbin size vereceği ülkeyi mülk edinmek için Şeria Irmağından geçmek üzeresiniz. Orayı ele geçirip yerleştiğinizde,"));
trsuperobject.biblemap.put("b5_11_32", new trsuperobject(5,11,32,"bugün size bildirdiğim bütün kurallara, ilkelere uymaya dikkat edin.››"));
trsuperobject.biblemap.put("b5_12_1", new trsuperobject(5,12,1,"‹‹Atalarınızın Tanrısı RABbin mülk edinmek için size verdiği ülkede yaşamınız boyunca uymanız gereken kurallar, ilkeler şunlardır:"));
trsuperobject.biblemap.put("b5_12_2", new trsuperobject(5,12,2,"Topraklarını alacağınız ulusların ilahlarına taptıkları yüksek dağlardaki, tepelerdeki, bol yapraklı her ağacın altındaki yerleri tümüyle yıkacaksınız."));
trsuperobject.biblemap.put("b5_12_3", new trsuperobject(5,12,3,"Sunaklarını yıkacak, dikili taşlarını parçalayacak, Aşera putlarını yakacak, öbür putlarını parça parça edeceksiniz. İlahlarının adlarını oradan sileceksiniz."));
trsuperobject.biblemap.put("b5_12_4", new trsuperobject(5,12,4,"‹‹Siz Tanrınız RABbe bu biçimde tapmamalısınız."));
trsuperobject.biblemap.put("b5_12_5", new trsuperobject(5,12,5,"Tanrınız RABbin adını yerleştirmek için bütün oymaklarınız arasından seçeceği yere, konutuna yönelmeli, oraya gitmelisiniz."));
trsuperobject.biblemap.put("b5_12_6", new trsuperobject(5,12,6,"Yakmalık sunularınızı, kurbanlarınızı, ondalıklarınızı, bağışlarınızı, dilek adaklarınızı, gönülden verdiğiniz sunuları, sığırlarınızın ve davarlarınızın ilk doğanlarını oraya götüreceksiniz."));
trsuperobject.biblemap.put("b5_12_7", new trsuperobject(5,12,7,"Orada, sizi kutsayan Tanrınız RABbin huzurunda, siz de aileleriniz de yiyeceksiniz ve el attığınız her işte sevinç bulacaksınız."));
trsuperobject.biblemap.put("b5_12_8", new trsuperobject(5,12,8,"‹‹Bugün burada yaptığımızı yapmayın; herkes kendi gözünde doğru olanı yapıyor."));
trsuperobject.biblemap.put("b5_12_9", new trsuperobject(5,12,9,"Çünkü Tanrınız RABbin size vereceği dinlenme yerine, mülke daha ulaşmadınız."));
trsuperobject.biblemap.put("b5_12_10", new trsuperobject(5,12,10,"Ama Şeria Irmağından geçip Tanrınız RABbin mülk olarak size vereceği ülkeye yerleşeceksiniz. RAB sizi çevrenizdeki bütün düşmanlarınızdan kurtarıp rahata kavuşturacak. Güvenlik içinde yaşayacaksınız."));
trsuperobject.biblemap.put("b5_12_11", new trsuperobject(5,12,11,"Tanrınız RAB adını yerleştirmek için bir yer seçecek. Size buyurduğum her şeyi oraya götüreceksiniz: Yakmalık sunularınızı, kurbanlarınızı, ondalıklarınızı, bağışlarınızı, RABbe adadığınız bütün özel adaklarınızı."));
trsuperobject.biblemap.put("b5_12_12", new trsuperobject(5,12,12,"Siz, oğullarınız, kızlarınız, erkek ve kadın köleleriniz, kentlerinizde yaşayan Levililer Tanrınız RABbin huzurunda sevineceksiniz. Çünkü Levililerin sizin gibi kendilerine ait payları ve mülkleri yoktur."));
trsuperobject.biblemap.put("b5_12_13", new trsuperobject(5,12,13,"Yakmalık sunularınızı herhangi bir yerde sunmamaya dikkat edin."));
trsuperobject.biblemap.put("b5_12_14", new trsuperobject(5,12,14,"Yakmalık sunularınızı RABbin oymaklarınızın birinde seçeceği yerde sunacaksınız. Size buyurduğum her şeyi orada yapacaksınız."));
trsuperobject.biblemap.put("b5_12_15", new trsuperobject(5,12,15,"‹‹Tanrınız RABbin sizi kutsadığı ölçüde, yaşadığınız kentlerde dilediğiniz kadar hayvan kesip etini yiyebilirsiniz. Dinsel açıdan temiz ya da kirli kişi, bu eti ceylan ya da geyik eti yer gibi yiyebilir."));
trsuperobject.biblemap.put("b5_12_16", new trsuperobject(5,12,16,"Ancak kan yemeyeceksiniz. Kanı su gibi toprağa akıtacaksınız."));
trsuperobject.biblemap.put("b5_12_17", new trsuperobject(5,12,17,"Tahılınızın, yeni şarabınızın, zeytinyağınızın ondalığını, sığırlarınızın, davarlarınızın ilk doğanlarını, adadıklarınızın tümünü, gönülden verdiğiniz sunuları, bağışlarınızı yaşadığınız kentlerde yememelisiniz."));
trsuperobject.biblemap.put("b5_12_18", new trsuperobject(5,12,18,"Siz, oğullarınız, kızlarınız, erkek ve kadın köleleriniz, kentlerinizde oturan Levililer bunları Tanrınız RABbin huzurunda, Onun seçeceği yerde yiyeceksiniz. Tanrınız RABbin huzurunda el attığınız her işte sevinç bulacaksınız."));
trsuperobject.biblemap.put("b5_12_19", new trsuperobject(5,12,19,"Ülkede yaşadığınız sürece Levilileri yüzüstü bırakmamaya dikkat edin."));
trsuperobject.biblemap.put("b5_12_20", new trsuperobject(5,12,20,"‹‹Tanrınız RAB size verdiği söz uyarınca sınırınızı genişlettiğinde, et yemeye istek duyup, ‹Et yiyeceğiz› derseniz, dilediğiniz kadar et yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_12_21", new trsuperobject(5,12,21,"Tanrınız RABbin adını yerleştirmek için seçeceği yer sizden uzaksa, buyruğum uyarınca RABbin size verdiği sığırlardan, davarlardan kesebilirsiniz. Kentlerinizde dilediğiniz kadar et yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_12_22", new trsuperobject(5,12,22,"Dinsel açıdan temiz ya da kirli kişi bu eti ceylan ya da geyik eti yer gibi yiyebilir."));
trsuperobject.biblemap.put("b5_12_23", new trsuperobject(5,12,23,"Ama kan yememeye dikkat edin. Çünkü ete can veren kandır. Etle birlikte canı yememelisiniz."));
trsuperobject.biblemap.put("b5_12_24", new trsuperobject(5,12,24,"Kan yememelisiniz; kanı su gibi toprağa akıtacaksınız."));
trsuperobject.biblemap.put("b5_12_25", new trsuperobject(5,12,25,"Kan yemeyeceksiniz. Öyle ki, size ve sizden sonra gelen çocuklarınıza iyilik gelsin. Böylece RABbin gözünde doğru olanı yapmış olursunuz."));
trsuperobject.biblemap.put("b5_12_26", new trsuperobject(5,12,26,"‹‹Kutsal sunularınızı, dilek adaklarınızı alıp RABbin seçeceği yere gideceksiniz."));
trsuperobject.biblemap.put("b5_12_27", new trsuperobject(5,12,27,"Yakmalık sunularınızı, eti ve kanı Tanrınız RABbin sunağında sunacaksınız. Kurbanınızın kanı Tanrınız RABbin sunağına akacak. Ama eti yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_12_28", new trsuperobject(5,12,28,"Size bildirdiğim bütün bu buyruklara iyice uyun ki, size ve sizden sonra gelen çocuklarınıza sürekli iyilik gelsin. Böylece Tanrınız RABbin gözünde iyi ve doğru olanı yapmış olacaksınız.››"));
trsuperobject.biblemap.put("b5_12_29", new trsuperobject(5,12,29,"‹‹Tanrınız RAB topraklarını alacağınız ulusları önünüzden yok edecek. Topraklarını miras alıp orada yaşadığınızda"));
trsuperobject.biblemap.put("b5_12_30", new trsuperobject(5,12,30,"ve onları yok ettiğinizde, onların tuzaklarına düşmekten sakının. İlahlarına yönelip, ‹Bu uluslar ilahlarına nasıl tapıyorlardı? Biz de aynısını yapalım› demeyin."));
trsuperobject.biblemap.put("b5_12_31", new trsuperobject(5,12,31,"Tanrınız RABbe bu biçimde tapınmayacaksınız. Onlar ilahlarına RABbin tiksindiği iğrenç şeyler sunuyorlar. Oğullarını, kızlarını bile yakarak ilahlarına kurban ediyorlar."));
trsuperobject.biblemap.put("b5_12_32", new trsuperobject(5,12,32,"‹‹Size bildirdiğim bütün buyruklara iyice uyun. Bunlara hiçbir şey eklemeyin, hiçbir şey çıkarmayın."));
trsuperobject.biblemap.put("b5_13_1", new trsuperobject(5,13,1,"‹‹Aranızdan bir peygamber ya da düş gören biri çıkarsa, bir belirtiyi ya da şaşılası bir olayı önceden bildirirse,"));
trsuperobject.biblemap.put("b5_13_2", new trsuperobject(5,13,2,"‹Bilmediğiniz başka ilahlara yönelip tapınalım› derse, söz ettiği belirti, şaşılası olay gerçekleşse bile,"));
trsuperobject.biblemap.put("b5_13_3", new trsuperobject(5,13,3,"o peygamberi ya da düş göreni dinlememelisiniz. Tanrınız RAB kendisini bütün yüreğinizle, bütün canınızla sevip sevmediğinizi anlamak için sizi sınamaktadır."));
trsuperobject.biblemap.put("b5_13_4", new trsuperobject(5,13,4,"Tanrınız RABbin ardınca yürüyün, Ondan korkun. Buyruklarına uyun, Onun sözüne kulak verin. Ona kulluk edin, Ona bağlı kalın."));
trsuperobject.biblemap.put("b5_13_5", new trsuperobject(5,13,5,"O peygamber ya da düş gören öldürülecek. O, sizi Mısırdan çıkaran, köle olduğunuz ülkeden kurtaran Tanrınız RABbe karşı gelmeye kışkırttı. Tanrınız RABbin yürümenizi buyurduğu yoldan sizi saptırmaya çalıştı. Aranızdaki kötülüğü ortadan kaldırmalısınız."));
trsuperobject.biblemap.put("b5_13_6-7", new trsuperobject(5,13,6-7,"‹‹Öz kardeşin, oğlun, kızın, sevdiğin karın ya da en yakın dostun seni gizlice ayartmaya çalışır, senin ve atalarının önceden bilmediğiniz, dünyanın bir ucundan öbür ucuna dek uzakta, yakında, çevrenizde yaşayan halkların ilahları için, ‹Haydi gidelim, bu ilahlara tapalım› derse,"));
trsuperobject.biblemap.put("b5_13_8", new trsuperobject(5,13,8,"ona uymayacak, onu dinlemeyeceksin. Ona acımayacak, sevecenlik göstermeyecek, onu korumayacaksın."));
trsuperobject.biblemap.put("b5_13_9", new trsuperobject(5,13,9,"Onu kesinlikle öldüreceksin. Onu önce sen, sonra bütün halk taşa tutsun."));
trsuperobject.biblemap.put("b5_13_10", new trsuperobject(5,13,10,"Taşlayarak öldürün onu. Çünkü Mısırdan, köle olduğunuz ülkeden sizi çıkaran Tanrınız RABden sizi saptırmaya çalıştı."));
trsuperobject.biblemap.put("b5_13_11", new trsuperobject(5,13,11,"Böylece bütün İsrail bunu duyup korkacak. Bir daha aranızda buna benzer kötü bir şey yapmayacaklar."));
trsuperobject.biblemap.put("b5_13_12-13", new trsuperobject(5,13,12-13,"‹‹Tanrınız RABbin yaşamanız için size vereceği kentlerin birinde, içinizden kötü kişiler çıktığını ve, ‹Haydi, bilmediğiniz başka ilahlara tapalım› diyerek kentlerinde yaşayan halkı saptırdıklarını duyarsanız,"));
trsuperobject.biblemap.put("b5_13_14", new trsuperobject(5,13,14,"araştıracak, inceleyecek, iyice soruşturacaksınız. Duyduklarınız gerçekse ve bu iğrenç olayın aranızda yapıldığı kanıtlanırsa,"));
trsuperobject.biblemap.put("b5_13_15", new trsuperobject(5,13,15,"o kentte yaşayanları kesinlikle kılıçtan geçireceksiniz. Kenti yok edip orada yaşayan bütün halkı ve hayvanları kılıçtan geçireceksiniz."));
trsuperobject.biblemap.put("b5_13_16", new trsuperobject(5,13,16,"Yağmalanan malların tümünü toplayıp meydanın ortasına yığın. Kenti ve malları Tanrınız RABbe tümüyle yakmalık sunu olarak yakın. Kent sonsuza dek yıkıntı halinde bırakılacak. Yeniden onarılmayacak."));
trsuperobject.biblemap.put("b5_13_17", new trsuperobject(5,13,17,"Yok edilecek mallardan hiçbir şey almayın. Böylece RABbin kızgın öfkesi yatışacak ve RAB atalarınıza içtiği ant uyarınca size acıyacak, sevecenlik gösterecek, sizi çoğaltacaktır."));
trsuperobject.biblemap.put("b5_13_18", new trsuperobject(5,13,18,"Çünkü Tanrınız RAB'bin sözünü dinleyeceksiniz. Böylece bugün size bildirdiğim buyruklara uyup O'nun gözünde doğru olanı yapmış olacaksınız.››"));
trsuperobject.biblemap.put("b5_14_1", new trsuperobject(5,14,1,"‹‹Siz Tanrınız RABbin çocuklarısınız. Ölülere ağıt yakmak için bedenlerinizi yaralamayacaksınız. İki kaş arasındaki tüyleri almayacaksınız."));
trsuperobject.biblemap.put("b5_14_2", new trsuperobject(5,14,2,"Tanrınız RAB için kutsal bir halksınız. RAB öz halkı olmanız için yeryüzündeki bütün halkların arasından sizi seçti."));
trsuperobject.biblemap.put("b5_14_3", new trsuperobject(5,14,3,"‹‹İğrenç sayılan hiçbir şey yemeyeceksiniz."));
trsuperobject.biblemap.put("b5_14_4", new trsuperobject(5,14,4,"Şu hayvanların etini yiyebilirsiniz: Sığır, koyun, keçi,"));
trsuperobject.biblemap.put("b5_14_5", new trsuperobject(5,14,5,"geyik, ceylan, karaca, yaban keçisi, gazal, ahu, dağ koyunu."));
trsuperobject.biblemap.put("b5_14_6", new trsuperobject(5,14,6,"Çatal ve yarık tırnaklı, geviş getiren her hayvanın etini yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_14_7", new trsuperobject(5,14,7,"Ancak geviş getiren, çatal ve yarık tırnaklı hayvanlardan etini yememeniz gerekenler şunlardır: Deve, tavşan, kaya tavşanı. Bunlar geviş getirir, ama çatal tırnaklı değildir. Sizin için kirli sayılırlar."));
trsuperobject.biblemap.put("b5_14_8", new trsuperobject(5,14,8,"Domuz çatal tırnaklıdır, ama geviş getirmez. Sizin için kirli sayılır. Bu hayvanların etini yemeyecek, leşine dokunmayacaksınız."));
trsuperobject.biblemap.put("b5_14_9", new trsuperobject(5,14,9,"‹‹Suda yaşayan hayvanlardan şunların etini yiyebilirsiniz: Pullu ve yüzgeçli canlıların etini yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_14_10", new trsuperobject(5,14,10,"Ama pulsuz ve yüzgeçsiz canlıların hiçbirini yemeyeceksiniz. Bunlar sizin için kirli sayılır."));
trsuperobject.biblemap.put("b5_14_11", new trsuperobject(5,14,11,"‹‹Temiz sayılan bütün kuşları yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_14_12", new trsuperobject(5,14,12,"Etini yemeyeceğiniz kuşlar şunlardır: Kartal, kuzu kartalı, kara akbaba,"));
trsuperobject.biblemap.put("b5_14_13", new trsuperobject(5,14,13,"çaylak, doğan türleri,"));
trsuperobject.biblemap.put("b5_14_14", new trsuperobject(5,14,14,"bütün karga türleri,"));
trsuperobject.biblemap.put("b5_14_15", new trsuperobject(5,14,15,"baykuş, puhu, martı, atmaca türleri,"));
trsuperobject.biblemap.put("b5_14_16", new trsuperobject(5,14,16,"kukumav, büyük baykuş, peçeli baykuş,"));
trsuperobject.biblemap.put("b5_14_17", new trsuperobject(5,14,17,"ishakkuşu, akbaba, karabatak,"));
trsuperobject.biblemap.put("b5_14_18", new trsuperobject(5,14,18,"leylek, balıkçıl türleri, ibibik, yarasa."));
trsuperobject.biblemap.put("b5_14_19", new trsuperobject(5,14,19,"Bütün kanatlı böcekler sizin için kirli sayılır. Hiçbirini yemeyeceksiniz."));
trsuperobject.biblemap.put("b5_14_20", new trsuperobject(5,14,20,"Ama temiz sayılan kanatlı yaratıkların tümünü yiyebilirsiniz."));
trsuperobject.biblemap.put("b5_14_21", new trsuperobject(5,14,21,"‹‹Kendiliğinden ölen hiçbir hayvanın etini yemeyeceksiniz. Ölü hayvanı yemesi için kentlerinizde yaşayan bir yabancıya verebilir ya da öteki yabancılara satabilirsiniz. Siz Tanrınız RAB için kutsal bir halksınız. ‹‹Oğlağı anasının sütünde haşlamayın.››"));
trsuperobject.biblemap.put("b5_14_22", new trsuperobject(5,14,22,"‹‹Her yıl tarlalarınızda yetişen ürünlerin ondalığını bir yana ayıracaksınız."));
trsuperobject.biblemap.put("b5_14_23", new trsuperobject(5,14,23,"Tahılınızın, yeni şarabınızın, zeytinyağınızın ondalığını, sığırlarınızın ve davarlarınızın ilk doğanlarını, Tanrınız RABbin adını yerleştirmek için seçeceği yerde Onun önünde yiyeceksiniz. Bunu yapın ki, her zaman Ondan korkmayı öğrenesiniz."));
trsuperobject.biblemap.put("b5_14_24", new trsuperobject(5,14,24,"Tanrınız RABbin adını yerleştirmek için seçeceği yer uzaksa, yol Tanrınız RABbin size verimli kıldığı ürünlerin ondalığını oraya taşıyamayacak kadar uzunsa,"));
trsuperobject.biblemap.put("b5_14_25", new trsuperobject(5,14,25,"ondalığınızı gümüşe çevirin. Gümüşü alıp Tanrınız RABbin seçeceği yere gidin."));
trsuperobject.biblemap.put("b5_14_26", new trsuperobject(5,14,26,"Gümüşü dilediğiniz şekilde kullanın: Sığır, davar, şarap, içki ya da canınızın istediği başka bir şey alın. Siz ve aileniz orada, Tanrınız RABbin önünde yiyecek ve sevineceksiniz."));
trsuperobject.biblemap.put("b5_14_27", new trsuperobject(5,14,27,"‹‹Kentlerinizde yaşayan Levilileri yüzüstü bırakmayın. Onların sizin gibi payları ve mülkleri yoktur."));
trsuperobject.biblemap.put("b5_14_28", new trsuperobject(5,14,28,"Her üç yılın sonunda, o yılın ürününün bütün ondalığını getirip kentlerinizde toplayın."));
trsuperobject.biblemap.put("b5_14_29", new trsuperobject(5,14,29,"Öyle ki, sizin gibi payları ve mülkleri olmayan Levililer, kentlerinizde yaşayan yabancılar, öksüzler, dul kadınlar gelsinler, yiyip doysunlar. Bunu yaparsanız, Tanrınız RAB el attığınız her işte sizi kutsayacaktır.››"));
trsuperobject.biblemap.put("b5_15_1", new trsuperobject(5,15,1,"‹‹Her yedi yılın sonunda size borçlu olanları bağışlayacaksınız."));
trsuperobject.biblemap.put("b5_15_2", new trsuperobject(5,15,2,"Borçları bağışlama işini şöyle yapacaksınız: Her alacaklı, komşusunun borcunu bağışlayacak. Borcun ödenmesi için komşusunu ya da kardeşini zorlamayacak. Çünkü RABbin borçları bağışlama yılı duyurulmuştur."));
trsuperobject.biblemap.put("b5_15_3", new trsuperobject(5,15,3,"Yabancıdan borcunu alabilirsin. Ama İsrailli kardeşinin borcunu bağışlayacaksın."));
trsuperobject.biblemap.put("b5_15_4-5", new trsuperobject(5,15,4-5,"‹‹Aranızda yoksul kimse olmayacak. Tanrınız RABbin mülk edinmek için size vereceği ülkede Tanrınız RABbin sözünü can kulağıyla dinler, bugün size bildirdiğim bütün bu buyruklara özenle uyarsanız, O sizi kesinlikle kutsayacaktır."));
trsuperobject.biblemap.put("b5_15_6", new trsuperobject(5,15,6,"Tanrınız RAB verdiği söz uyarınca sizi kutsayacak. Siz birçok ulusa ödünç vereceksiniz, ama siz ödünç almayacaksınız. Siz birçok ulusu yöneteceksiniz, ama onlar sizi yönetmeyecek."));
trsuperobject.biblemap.put("b5_15_7", new trsuperobject(5,15,7,"‹‹Tanrınız RABbin size vereceği ülkenin herhangi bir kentinde yaşayan kardeşlerinizden biri yoksulsa, yüreğinizi katılaştırmayın, yoksul kardeşinize elisıkı davranmayın."));
trsuperobject.biblemap.put("b5_15_8", new trsuperobject(5,15,8,"Tersine, eliniz açık olsun; gereksinimlerini karşılayacak kadar ona ödünç verin."));
trsuperobject.biblemap.put("b5_15_9", new trsuperobject(5,15,9,"‹Yedinci yıl, borçları bağışlama yılı yakındır› diyerek yüreğinizde kötü düşünce barındırmaktan sakının. Öyle ki, yoksul kardeşinize karşı elisıkı davranıp ona yardım etmekten kaçınmayasınız. Yoksul kardeşiniz sizden RABbe yakınabilir, siz de günah işlemiş olursunuz."));
trsuperobject.biblemap.put("b5_15_10", new trsuperobject(5,15,10,"Ona bol bol verin, verirken yüreğinizde isteksizlik olmasın. Bundan ötürü Tanrınız RAB bütün işlerinizde ve el attığınız her şeyde sizi kutsayacaktır."));
trsuperobject.biblemap.put("b5_15_11", new trsuperobject(5,15,11,"Ülkede her zaman yoksullar olacak. Bunun için, ülkenizde yaşayan kardeşlerinize, yoksullara, gereksinimi olanlara eliaçık davranmanızı buyuruyorum.››"));
trsuperobject.biblemap.put("b5_15_12", new trsuperobject(5,15,12,"‹‹Eğer İbrani kardeşlerinizden bir erkek ya da kadın size satılırsa, altı yıl size kölelik edecek, yedinci yıl onu özgür bırakacaksınız."));
trsuperobject.biblemap.put("b5_15_13", new trsuperobject(5,15,13,"Onu özgür bırakırken, eli boş göndermeyin."));
trsuperobject.biblemap.put("b5_15_14", new trsuperobject(5,15,14,"Ona davarlarınızdan, tahılınızdan, şarabınızdan bol bol verin. Tanrınız RABbin sizi kutsadığı oranda ona vereceksiniz."));
trsuperobject.biblemap.put("b5_15_15", new trsuperobject(5,15,15,"Mısırda köle olduğunuzu, Tanrınız RABbin sizi kurtardığını anımsayın. Bu buyruğu bugün size bunun için veriyorum."));
trsuperobject.biblemap.put("b5_15_16", new trsuperobject(5,15,16,"‹‹Eğer köleniz sizi ve ailenizi seviyorsa, sizden hoşnutsa, ‹Yanınızdan ayrılmak istemiyorum› derse,"));
trsuperobject.biblemap.put("b5_15_17", new trsuperobject(5,15,17,"bir biz alıp kölenin kulak memesinden sokarak kapıya geçirin; o zaman yaşam boyu köleniz olarak kalacaktır. Kadın kölelerinize de aynı şeyi yapın."));
trsuperobject.biblemap.put("b5_15_18", new trsuperobject(5,15,18,"Kölenizi özgür bırakınca üzülmemelisiniz. Size hizmet ettiği bu altı yıl boyunca ücretli bir işçiden iki kat fazla iş görmüştür. Tanrınız RAB yaptığınız her işte sizi kutsayacaktır.››"));
trsuperobject.biblemap.put("b5_15_19", new trsuperobject(5,15,19,"‹‹Sığır ve davarlarınızın içinde ilk doğan her erkek hayvanı Tanrınız RABbe ayıracaksınız. Sığırınızın ilk doğan öküzüyle iş yapmayacak, sürünüzün ilk doğan koyununu kırkmayacaksınız."));
trsuperobject.biblemap.put("b5_15_20", new trsuperobject(5,15,20,"Siz ve aileniz her yıl Tanrınız RABbin önünde, Onun seçeceği yerde onları yiyeceksiniz."));
trsuperobject.biblemap.put("b5_15_21", new trsuperobject(5,15,21,"Bir hayvanın özürü varsa, topal ya da körse, herhangi bir ciddi sakatlığı varsa, onu Tanrınız RABbe kurban etmeyin."));
trsuperobject.biblemap.put("b5_15_22", new trsuperobject(5,15,22,"Bu durumdaki hayvanları kentlerinizde yiyebilirsiniz. Dinsel açıdan temiz ya da kirli kişi bunların etini ceylan ya da geyik eti yer gibi yiyebilir."));
trsuperobject.biblemap.put("b5_15_23", new trsuperobject(5,15,23,"Ancak kan yemeyeceksiniz. Kanı su gibi toprağa akıtacaksınız.››"));
trsuperobject.biblemap.put("b5_16_1", new trsuperobject(5,16,1,"‹‹Aviv ayını tutun ve Tanrınız RABbin Fısıh Bayramını kutlayın. Tanrınız RAB Aviv ayında geceleyin sizi Mısırdan çıkardı."));
trsuperobject.biblemap.put("b5_16_2", new trsuperobject(5,16,2,"Tanrınız RABbin adını yerleştirmek için seçeceği yerde davarlardan, sığırlardan Fısıh kurbanlarını keseceksiniz."));
trsuperobject.biblemap.put("b5_16_3", new trsuperobject(5,16,3,"Kurban etiyle birlikte mayalı ekmek yemeyeceksiniz. Yedi gün mayasız ekmek -sıkıntıda yenilen ekmek- yiyeceksiniz. Siz Mısırdan aceleyle çıktınız. Öyle ki, yaşadığınız sürece Mısırdan çıktığınız günü anımsayasınız."));
trsuperobject.biblemap.put("b5_16_4", new trsuperobject(5,16,4,"Yedi gün ülkenizin hiçbir yerinde maya bulunmasın. Akşam kurban edeceğiniz hayvanların etinden ilk günün sabahına bir şey bırakmayacaksınız."));
trsuperobject.biblemap.put("b5_16_5", new trsuperobject(5,16,5,"‹‹Fısıh kurbanlarını Tanrınız RABbin size vereceği kentlerden birinde kesmeyeceksiniz;"));
