trsuperobject.biblemap.put("b5_16_17", new trsuperobject(5,16,17,"Her biriniz Tanrınız RABbin sizi kutsadığı oranda armağanlar götürmeli.››"));
trsuperobject.biblemap.put("b5_16_18", new trsuperobject(5,16,18,"‹‹Tanrınız RABbin size vereceği kentlerde her oymağınız için yargıçlar, yöneticiler atayacaksınız. Onlar halkı gerçek adaletle yargılayacaklar."));
trsuperobject.biblemap.put("b5_16_19", new trsuperobject(5,16,19,"Yargılarken haksızlık yapmayacak, kimseyi kayırmayacaksınız. Rüşvet almayacaksınız. Çünkü rüşvet bilge kişinin gözlerini kör eder, haklıyı haksız çıkarır."));
trsuperobject.biblemap.put("b5_16_20", new trsuperobject(5,16,20,"Yaşamak ve Tanrınız RABbin size vereceği ülkeyi miras almak için doğruluğun, yalnız doğruluğun ardınca gidin."));
trsuperobject.biblemap.put("b5_16_21", new trsuperobject(5,16,21,"‹‹Tanrınız RAB için yapacağınız sunağın yanına ağaçtan bir Aşera putu dikmeyeceksiniz."));
