trsuperobject.biblemap.put("b5_19_13", new trsuperobject(5,19,13,"Ona acımayacaksınız. İsraili suçsuz kanı dökme günahından arındırmalısınız ki, üzerinize iyilik gelsin."));
trsuperobject.biblemap.put("b5_19_14", new trsuperobject(5,19,14,"‹‹Tanrınız RABbin mülk edinmek için size vereceği ülkede payınıza düşen mirasta komşunuzun önceden belirlenen sınırını değiştirmeyeceksiniz.››"));
trsuperobject.biblemap.put("b5_19_15", new trsuperobject(5,19,15,"‹‹Herhangi bir suç ya da günah konusunda birini suçlu çıkarmak için bir tanık yetmez. Her sorun iki ya da üç tanığın tanıklığıyla açıklığa kavuşturulacaktır."));
trsuperobject.biblemap.put("b5_19_16", new trsuperobject(5,19,16,"‹‹Eğer yalancı bir tanık kötü amaçla birini suçlarsa,"));
