trsuperobject.biblemap.put("b5_20_10", new trsuperobject(5,20,10,"‹‹Bir kente saldırmadan önce, kent halkına barış önerin."));
trsuperobject.biblemap.put("b5_20_11", new trsuperobject(5,20,11,"Barış önerinizi benimser, kapılarını size açarlarsa, kentte yaşayanların tümü sizin için angaryasına çalışacak, size hizmet edecekler."));
trsuperobject.biblemap.put("b5_20_12", new trsuperobject(5,20,12,"Ama barış önerinizi geri çevirir, sizinle savaşmak isterlerse, kenti kuşatın."));
trsuperobject.biblemap.put("b5_20_13", new trsuperobject(5,20,13,"Tanrınız RAB kenti elinize teslim edince, orada yaşayan bütün erkekleri kılıçtan geçirin."));
trsuperobject.biblemap.put("b5_20_14", new trsuperobject(5,20,14,"Kadınları, çocukları, hayvanları ve kentteki her şeyi yağmalayabilirsiniz. Tanrınız RABbin size verdiği düşman malını kullanabilirsiniz."));
trsuperobject.biblemap.put("b5_20_15", new trsuperobject(5,20,15,"Yakınınızdaki uluslara ait olmayan sizden çok uzak kentlerin tümüne böyle davranacaksınız."));
trsuperobject.biblemap.put("b5_20_16", new trsuperobject(5,20,16,"‹‹Ancak Tanrınız RABbin miras olarak size vereceği bu halkların kentlerinde soluk alan hiçbir canlıyı yaşatmayacaksınız."));
trsuperobject.biblemap.put("b5_20_17", new trsuperobject(5,20,17,"Tanrınız RABbin size buyurduğu gibi, onları -Hitit, Amor, Kenan, Periz, Hiv ve Yevus halklarını- tümüyle yok edeceksiniz."));
trsuperobject.biblemap.put("b5_20_18", new trsuperobject(5,20,18,"Öyle ki, ilahlarına taparken yaptıkları iğrençliklere uymayı size öğretemesinler, siz de Tanrınız RABbe karşı günah işlemeyesiniz."));
trsuperobject.biblemap.put("b5_20_19", new trsuperobject(5,20,19,"‹‹Bir kentle savaşırken, kenti ele geçirmek için kuşatma uzun sürerse, ağaçlarına balta vurup yok etmeyeceksiniz. Ağaçların ürünlerini yiyebilirsiniz, ama onları kesmeyeceksiniz. Çünkü kırdaki ağaçlar insan değil ki kuşatma altına alasınız."));
trsuperobject.biblemap.put("b5_20_20", new trsuperobject(5,20,20,"Yalnız ürün vermediğini bildiğiniz ağaçları kesip yok edebilirsiniz. Sizinle savaşan kenti ele geçirene dek kesilen ağaçları kuşatma işinde kullanabilirsiniz.››"));
trsuperobject.biblemap.put("b5_21_1", new trsuperobject(5,21,1,"‹‹Tanrınız RABbin mülk edinmek için size vereceği ülkede, kırda yere düşmüş, kimin öldürdüğü bilinmeyen birini görürseniz,"));
trsuperobject.biblemap.put("b5_21_2", new trsuperobject(5,21,2,"ileri gelenleriniz ve yargıçlarınız gidip ölünün çevredeki kentlere olan uzaklığını ölçsünler."));
trsuperobject.biblemap.put("b5_21_3", new trsuperobject(5,21,3,"Ölüye en yakın kentin ileri gelenleri işe koşulmamış, boyunduruk takmamış bir düve alacaklar."));
trsuperobject.biblemap.put("b5_21_4", new trsuperobject(5,21,4,"Düveyi toprağı sürülmemiş, ekilmemiş ve içinde sürekli akan bir dere olan bir vadiye getirecekler. Orada, derede düvenin boynunu kıracaklar."));
trsuperobject.biblemap.put("b5_21_5", new trsuperobject(5,21,5,"Levili kâhinler de oraya gidecek. Çünkü Tanrınız RAB, onları kendisine hizmet etsinler, Onun adıyla kutsasınlar diye seçti. Kavga, saldırı davalarına da onlar bakacak."));
trsuperobject.biblemap.put("b5_21_6", new trsuperobject(5,21,6,"Ölüye en yakın kentin ileri gelenleri, derede boynu kırılan düvenin üzerinde ellerini yıkayacaklar."));
trsuperobject.biblemap.put("b5_21_7", new trsuperobject(5,21,7,"Sonra şöyle bir açıklama yapacaklar: ‹Bu kanı ellerimiz dökmedi, kimin yaptığını gözlerimiz de görmedi."));
trsuperobject.biblemap.put("b5_21_8", new trsuperobject(5,21,8,"Ya RAB, kurtardığın halkın İsraillileri bağışla. Halkını dökülen suçsuz kanından sorumlu tutma.› Böylece kan dökme günahından bağışlanacaklar."));
trsuperobject.biblemap.put("b5_21_10", new trsuperobject(5,21,10,"‹‹Düşmanlarınızla savaşmaya çıktığınızda ve Tanrınız RAB onları elinize teslim ettiğinde, tutsaklar alır ve"));
trsuperobject.biblemap.put("b5_21_11", new trsuperobject(5,21,11,"aralarında sevdiğiniz güzel bir kadın görürseniz, onu kendinize eş olarak alabilirsiniz."));
trsuperobject.biblemap.put("b5_21_12", new trsuperobject(5,21,12,"Onu evinize götürün. Başını tıraş etsin, tırnaklarını kessin."));
trsuperobject.biblemap.put("b5_21_13", new trsuperobject(5,21,13,"Üzerinden tutsaklık giysilerini çıkarsın. Evinizde otursun. Anne babası için bir ay yas tutsun. Sonra kadını alan kişi onunla yatabilir. Erkek ona koca, kadın da ona karı olacak."));
trsuperobject.biblemap.put("b5_21_14", new trsuperobject(5,21,14,"Kadından hoşnut kalmazsa, onu özgür bıraksın. Kadınla yattığı için onu parayla satmasın, ona köle gibi davranmasın.››"));
trsuperobject.biblemap.put("b5_21_15", new trsuperobject(5,21,15,"‹‹Eğer bir adamın iki karısı varsa, birini seviyor, öbüründen hoşlanmıyorsa; iki kadın da kendisine oğullar doğurmuşsa; ilk oğul hoşlanmadığı kadının oğluysa;"));
trsuperobject.biblemap.put("b5_21_16", new trsuperobject(5,21,16,"adam malını miras olarak oğullarına bölüştürdüğü gün sevdiği kadının oğlunu kayırıp ona ilk oğulluk hakkını veremez."));
trsuperobject.biblemap.put("b5_21_17", new trsuperobject(5,21,17,"Hoşlanmadığı kadının oğlunu ilk doğan oğul olarak tanıyacak ve ona bütün malından iki pay verecektir. Çünkü bu oğul babasının gücünün ilk ürünüdür. İlk oğulluk hakkı onun olacak.››"));
trsuperobject.biblemap.put("b5_21_18", new trsuperobject(5,21,18,"‹‹Eğer bir adamın dikbaşlı, başkaldıran, annesinin ve babasının sözünü dinlemeyen, onların tedibine aldırmayan bir oğlu varsa,"));
trsuperobject.biblemap.put("b5_21_19", new trsuperobject(5,21,19,"annesiyle babası onu tutup kent kapısında görev yapan kent ileri gelenlerine götürecekler."));
trsuperobject.biblemap.put("b5_21_20", new trsuperobject(5,21,20,"Onlara şöyle diyecekler: ‹Oğlumuz dikbaşlı, başkaldıran bir çocuktur. Sözümüzü dinlemiyor. Savurgan ve içkicidir.›"));
trsuperobject.biblemap.put("b5_21_21", new trsuperobject(5,21,21,"Bunun üzerine kentin bütün erkekleri onu taşlayarak öldürecekler. Aranızdaki kötülüğü ortadan kaldıracaksınız. Bütün İsrailliler bunu duyup korkacaklar.››"));
trsuperobject.biblemap.put("b5_21_22", new trsuperobject(5,21,22,"‹‹Eğer bir adam bir günahtan ötürü ölüm cezasına çarptırılıp öldürülür ve ölüsü ağaca asılırsa,"));
trsuperobject.biblemap.put("b5_21_23", new trsuperobject(5,21,23,"ölüyü gece ağaçta asılı bırakmamalısınız. O gün kesinlikle gömmelisiniz. Asılan kişi Tanrı tarafından lanetlenmiştir. Tanrınız RAB'bin mülk olarak size vereceği ülkeyi kirletmeyeceksiniz."));
trsuperobject.biblemap.put("b5_22_1", new trsuperobject(5,22,1,"‹‹Kardeşinin yolunu yitirmiş sığırını ya da koyununu görünce, onları görmezlikten gelme. Sığırı ya da koyunu kesinlikle kardeşine geri götüreceksin."));
trsuperobject.biblemap.put("b5_22_2", new trsuperobject(5,22,2,"Kardeşin sana uzaksa ya da hayvanın kime ait olduğunu bilmiyorsan evine götür. Kardeşin sığırını ya da koyununu aramaya çıkıncaya dek hayvan evinde kalsın. Sonra ona geri verirsin."));
trsuperobject.biblemap.put("b5_22_3", new trsuperobject(5,22,3,"Kardeşinin eşeğini, giysisini ya da yitirdiği başka bir şeyini gördüğünde, aynı biçimde davranacaksın. Görmezlikten gelmeyeceksin."));
trsuperobject.biblemap.put("b5_22_4", new trsuperobject(5,22,4,"‹‹Kardeşinin eşeğini ya da sığırını yolda düşmüş gördüğünde, görmezlikten gelme. Hayvanı ayağa kaldırması için kesinlikle kardeşine yardım edeceksin."));
trsuperobject.biblemap.put("b5_22_5", new trsuperobject(5,22,5,"‹‹Kadınlar erkek giysisi, erkekler de kadın giysisi giymesin. Tanrınız RAB bu gibi şeyleri yapanlardan tiksinir."));
trsuperobject.biblemap.put("b5_22_6", new trsuperobject(5,22,6,"‹‹Yolda rastlantıyla ağaçta ya da yerde bir kuş yuvası görürseniz, ana kuş yavruların ya da yumurtaların üzerinde oturuyorsa, anayı yavrularıyla birlikte almayacaksınız."));
trsuperobject.biblemap.put("b5_22_7", new trsuperobject(5,22,7,"Yavruları kendiniz için alabilirsiniz, ama anayı kesinlikle özgür bırakacaksınız. Öyle ki, üzerinize iyilik gelsin ve ömrünüz uzun olsun."));
trsuperobject.biblemap.put("b5_22_8", new trsuperobject(5,22,8,"‹‹Yeni bir ev yaparken, dama korkuluk yapacaksın. Öyle ki, biri damdan düşüp ölürse ailen sorumlu sayılmasın."));
trsuperobject.biblemap.put("b5_22_9", new trsuperobject(5,22,9,"‹‹Bağına iki çeşit tohum ekmeyeceksin. Yoksa ektiğin tohumun da bağın da ürününü kullanamazsın."));
trsuperobject.biblemap.put("b5_22_10", new trsuperobject(5,22,10,"‹‹Çift sürmek için eşeği öküzle birlikte koşmayacaksın."));
trsuperobject.biblemap.put("b5_22_11", new trsuperobject(5,22,11,"‹‹Yünle ketenden dokunmuş karışık kumaştan giysi giymeyeceksin."));
trsuperobject.biblemap.put("b5_22_12", new trsuperobject(5,22,12,"‹‹Giysinin dört yerine püskül dikeceksin.››"));
trsuperobject.biblemap.put("b5_22_13", new trsuperobject(5,22,13,"‹‹Bir adam bir kadın alır, yattıktan sonra ondan hoşlanmazsa,"));
trsuperobject.biblemap.put("b5_22_14", new trsuperobject(5,22,14,"ona suç yükler, adını kötüler, ‹Bu kadınla evlendim ama onunla yatınca erden olmadığını gördüm› derse,"));
trsuperobject.biblemap.put("b5_22_15", new trsuperobject(5,22,15,"kadının annesiyle babası kızlarının erden olduğuna ilişkin kanıtı alıp kapıda görevli kent ileri gelenlerine getirecekler."));
trsuperobject.biblemap.put("b5_22_16-17", new trsuperobject(5,22,16-17,"Kadının babası ileri gelenlere, ‹Kızımı bu adamla evlendirdim ama o kızımdan hoşlanmıyor› diyecek, ‹Şimdi kızımı suçluyor, onun erden olmadığını söylüyor. İşte kızımın erden olduğunun kanıtı!› Sonra anne-baba kızlarının erden olduğunu kanıtlayan yatak çarşafını ileri gelenlerin önüne serip gösterecekler."));
trsuperobject.biblemap.put("b5_22_18", new trsuperobject(5,22,18,"Kent ileri gelenleri de adamı cezalandıracaklar."));
trsuperobject.biblemap.put("b5_22_19", new trsuperobject(5,22,19,"Ceza olarak ondan yüz gümüş alıp kadının babasına verecekler. Çünkü adam İsrailli bir erden kızın adını kötülemiştir. Kadın adamın karısı kalacak ve adam yaşamı boyunca onu boşayamayacaktır."));
trsuperobject.biblemap.put("b5_22_20", new trsuperobject(5,22,20,"‹‹Ancak bu sav doğruysa, kızın erden olduğuna ilişkin bir kanıt bulunamazsa,"));
trsuperobject.biblemap.put("b5_22_21", new trsuperobject(5,22,21,"kızı baba evinin kapısına çıkaracaklar. Kent halkı taşlayarak kızı öldürecek. Babasının evindeyken fuhuş yapmakla İsrailde iğrençlik yapmıştır. Aranızdaki kötülüğü ortadan kaldıracaksınız."));
trsuperobject.biblemap.put("b5_22_22", new trsuperobject(5,22,22,"‹‹Eğer bir adam başka birinin karısıyla yatarken yakalanırsa, hem kadınla yatan adam, hem kadın, ikisi de öldürülecek. İsrailden kötülüğü atacaksınız."));
trsuperobject.biblemap.put("b5_22_23", new trsuperobject(5,22,23,"‹‹Eğer bir adam kentte başka biriyle nişanlı erden bir kızla karşılaşır ve onunla yatarsa,"));
trsuperobject.biblemap.put("b5_22_24", new trsuperobject(5,22,24,"ikisini de kentin kapısına götürecek, taşlayarak öldüreceksiniz. Çünkü kız kentte olduğu halde yardım istemek için bağırmadı; adam da komşusunun karısıyla ilişki kurdu. Aranızdaki kötülüğü ortadan kaldıracaksınız."));
trsuperobject.biblemap.put("b5_22_25", new trsuperobject(5,22,25,"‹‹Eğer bir adam kırda nişanlı bir kızla karşılaşır, onu yakalayıp tecavüz ederse, yalnız tecavüz eden adam öldürülecek."));
trsuperobject.biblemap.put("b5_22_26", new trsuperobject(5,22,26,"Kıza hiçbir şey yapmayacaksınız. Çünkü kızın ölümü hak edecek bir günahı yoktur. Bu, komşusuna saldırıp onu öldüren adamın davasına benzer."));
trsuperobject.biblemap.put("b5_22_27", new trsuperobject(5,22,27,"Adam kızı kırda gördüğünde nişanlı kız bağırmışsa da onu kurtaran olmamıştır."));
trsuperobject.biblemap.put("b5_22_28", new trsuperobject(5,22,28,"‹‹Eğer bir adam nişanlı olmayan erden bir kızla karşılaşır, tutup onunla yatarsa ve bu ortaya çıkarsa,"));
trsuperobject.biblemap.put("b5_22_29", new trsuperobject(5,22,29,"kızla yatan adam kızın babasına elli gümüş verecek. Kıza tecavüz ettiği için onu karı olarak alacak ve yaşamı boyunca onu boşayamayacaktır."));
trsuperobject.biblemap.put("b5_22_30", new trsuperobject(5,22,30,"‹‹Kimse babasının karısını almayacak, babasının evlilik yatağına leke sürmeyecektir.››"));
trsuperobject.biblemap.put("b5_23_1", new trsuperobject(5,23,1,"‹‹Erkeklik bezi ezilmiş ya da erkeklik organı kesilmiş kişi RABbin topluluğuna girmeyecek."));
trsuperobject.biblemap.put("b5_23_2", new trsuperobject(5,23,2,"‹‹Yasa dışı doğan biri RABbin topluluğuna girmeyecek. Soyundan gelenler de onuncu kuşağa dek RABbin topluluğuna girmeyecektir."));
trsuperobject.biblemap.put("b5_23_3", new trsuperobject(5,23,3,"‹‹Ammonlu ya da Moavlı biri RABbin topluluğuna girmeyecek. Onların soyundan gelenler de onuncu kuşağa dek asla RABbin topluluğuna girmeyecek."));
trsuperobject.biblemap.put("b5_23_4", new trsuperobject(5,23,4,"Mısırdan çıktığınızda yolda sizi ekmek ve suyla karşılamadılar. Aram-Naharayimdeki Petor Kentinden Beor oğlu Balamı size lanet okuması için ücretle tuttular."));
trsuperobject.biblemap.put("b5_23_5", new trsuperobject(5,23,5,"Ne var ki Tanrınız RAB Balamı dinlemek istemedi. Sizin için laneti kutsamaya çevirdi. Çünkü Tanrınız RAB sizi seviyor."));
trsuperobject.biblemap.put("b5_23_6", new trsuperobject(5,23,6,"Kuşaklar boyunca onların esenliği ve iyiliği için çalışmayın."));
trsuperobject.biblemap.put("b5_23_7", new trsuperobject(5,23,7,"‹‹Edomlulardan iğrenmeyeceksiniz. Onlar kardeşinizdir. Mısırlılardan da iğrenmeyeceksiniz. Çünkü onların ülkesinde yabancı olarak yaşadınız."));
trsuperobject.biblemap.put("b5_23_8", new trsuperobject(5,23,8,"Onlardan doğan üçüncü kuşak çocuklar RABbin topluluğuna girebilir.››"));
trsuperobject.biblemap.put("b5_23_9", new trsuperobject(5,23,9,"‹‹Düşmanlarınızla savaşmak üzere ordugah kurduğunuzda, her kötülükten sakınacaksınız."));
trsuperobject.biblemap.put("b5_23_10", new trsuperobject(5,23,10,"Aranızda gece menisi boşaldığı için dinsel açıdan kirli biri varsa, ordugahın dışına çıkıp orada kalsın."));
trsuperobject.biblemap.put("b5_23_11", new trsuperobject(5,23,11,"Akşama doğru yıkansın, gün batımında ordugaha dönsün."));
trsuperobject.biblemap.put("b5_23_12", new trsuperobject(5,23,12,"‹‹İhtiyaçlarınızı gidermek için ordugahın dışında bir yeriniz olmalı."));
trsuperobject.biblemap.put("b5_23_13", new trsuperobject(5,23,13,"Donatımınız arasında yeri kazmak için bir gereç bulunsun. İhtiyacınızı gidereceğiniz zaman bir çukur kazın, sonra da dışkınızı örtün."));
trsuperobject.biblemap.put("b5_23_14", new trsuperobject(5,23,14,"Tanrınız RAB sizi kurtarmak ve düşmanlarınızı elinize teslim etmek için ordugahın ortasında dolaşır. Ordugahınız kutsal olsun ki, RAB aranızda yakışıksız bir şey görüp sizden ayrılmasın.››"));
trsuperobject.biblemap.put("b5_23_15", new trsuperobject(5,23,15,"‹‹Efendisinden kaçıp size sığınan köleyi efendisine teslim etmeyeceksiniz."));
trsuperobject.biblemap.put("b5_23_16", new trsuperobject(5,23,16,"Bırakın kendi seçeceği yerde, beğendiği bir kentte aranızda yaşasın. Ona baskı yapmayacaksınız."));
trsuperobject.biblemap.put("b5_23_17", new trsuperobject(5,23,17,"‹‹Putperest törenlerinde fuhuş yapan İsrailli bir kadın ya da erkek olmasın."));
trsuperobject.biblemap.put("b5_23_18", new trsuperobject(5,23,18,"Fuhuş yapan kadın ya da erkeğin kazancını adak olarak Tanrınız RABbin Tapınağına götürmeyeceksiniz. İkisi de Tanrınız RABbin gözünde iğrençtir."));
trsuperobject.biblemap.put("b5_23_19", new trsuperobject(5,23,19,"‹‹Kardeşinize para, yiyecek ya da faiz getiren başka bir şey ödünç verdiğinizde, ondan faiz almayacaksınız."));
trsuperobject.biblemap.put("b5_23_20", new trsuperobject(5,23,20,"Yabancıdan faiz alabilirsiniz ama kardeşinizden almayacaksınız. Böyle yapın ki, mülk edinmek için gideceğiniz ülkede el attığınız her işte Tanrınız RAB sizi kutsasın."));
trsuperobject.biblemap.put("b5_23_21", new trsuperobject(5,23,21,"‹‹Tanrınız RABbe bir dilek adağı adadığınızda yerine getirmeyi savsaklamayın. Tanrınız RAB sizden kesinlikle bunu isteyecektir. Yerine getirmezseniz size günah sayılacaktır."));
trsuperobject.biblemap.put("b5_23_22", new trsuperobject(5,23,22,"Ama adak adamaktan çekinirsen günah sayılmaz."));
trsuperobject.biblemap.put("b5_23_23", new trsuperobject(5,23,23,"Ağzınızdan çıkanı yapmaya dikkat edin. Çünkü Tanrınız RABbe adağı gönülden adadınız."));
trsuperobject.biblemap.put("b5_23_24", new trsuperobject(5,23,24,"‹‹Komşunuzun bağına girdiğinizde doyuncaya dek üzüm yiyebilirsiniz, ama torbanıza koymayacaksınız."));
trsuperobject.biblemap.put("b5_23_25", new trsuperobject(5,23,25,"Komşunuzun ekin tarlasına girdiğinizde elinizle başak koparabilirsiniz, ama ekinlere orak salmayacaksınız."));
trsuperobject.biblemap.put("b5_24_1", new trsuperobject(5,24,1,"‹‹Eğer bir adam evlendiği kadında yakışıksız bir şey bulur, bundan ötürü ondan hoşlanmaz, boşanma belgesi yazıp ona verir ve onu evinden kovarsa,"));
trsuperobject.biblemap.put("b5_24_2", new trsuperobject(5,24,2,"kadın adamın evinden ayrıldıktan sonra başka biriyle evlenirse,"));
trsuperobject.biblemap.put("b5_24_3", new trsuperobject(5,24,3,"ikinci kocası da ondan hoşlanmaz, boşanma belgesi yazıp verir, onu evinden kovarsa ya da ikinci adam ölürse,"));
trsuperobject.biblemap.put("b5_24_4", new trsuperobject(5,24,4,"kadını boşayan ilk kocası onunla yeniden evlenemez. Çünkü kadın kirlenmiştir. Bu RABbin gözünde iğrençtir. Tanrınız RABbin mülk olarak size vereceği ülkeyi günaha sürüklemeyin."));
trsuperobject.biblemap.put("b5_24_5", new trsuperobject(5,24,5,"‹‹Yeni evli bir adam savaşa gitmeyecek, ona herhangi bir görev verilmeyecek. Bir yıl özgürce evinde kalıp karısını mutlu edecek."));
trsuperobject.biblemap.put("b5_24_6", new trsuperobject(5,24,6,"‹‹Rehin olarak ne değirmeni, ne de üst taşını alın. Bunu yapmakla adamın yaşamını rehin almış olursunuz."));
trsuperobject.biblemap.put("b5_24_7", new trsuperobject(5,24,7,"‹‹İsrailli kardeşlerinden birini kaçırıp ona kötü davranan ya da onu satan adam yakalanırsa ölmeli. Aranızdaki kötülüğü ortadan kaldıracaksınız."));
trsuperobject.biblemap.put("b5_24_8", new trsuperobject(5,24,8,"‹‹Deri hastalığı konusunda, Levili kâhinlerin size bütün öğrettiklerini yapmaya çok dikkat edin. Onlara verdiğim buyruklara özenle uyun."));
trsuperobject.biblemap.put("b5_24_9", new trsuperobject(5,24,9,"Siz Mısırdan çıktıktan sonra Tanrınız RABbin yolda Miryama neler yaptığını anımsayın."));
trsuperobject.biblemap.put("b5_24_10", new trsuperobject(5,24,10,"‹‹Komşuna herhangi bir şey ödünç verdiğinde, vereceği rehini almak için onun evine girmeyeceksin."));
trsuperobject.biblemap.put("b5_24_11", new trsuperobject(5,24,11,"Dışarıda bekleyeceksin. Ödünç verdiğin kişi rehini kendisi sana getirsin."));
trsuperobject.biblemap.put("b5_24_12", new trsuperobject(5,24,12,"Eğer yoksul biriyse, onun rehini elinde olduğu sürece yatağa girmeyeceksin."));
trsuperobject.biblemap.put("b5_24_13", new trsuperobject(5,24,13,"Ondan aldığın giysiyi gün batımında ona kesinlikle geri vereceksin ki, onunla yatabilsin. O da seni kutsayacak. Bu yaptığın, Tanrın RABbin önünde sana doğruluk sayılacak."));
trsuperobject.biblemap.put("b5_24_14", new trsuperobject(5,24,14,"‹‹Ücretle çalışan, gereksinimi olan, yoksul bir soydaşınızı ya da kentlerinizin birinde yaşayan bir yabancıyı sömürmeyeceksiniz."));
trsuperobject.biblemap.put("b5_24_15", new trsuperobject(5,24,15,"Ücretini her gün, güneş batmadan ödeyeceksiniz. Yoksul olduğu için güvencesi odur. Yoksa sana karşı RABbe haykırır ve sen de günah işlemiş sayılırsın."));
trsuperobject.biblemap.put("b5_24_16", new trsuperobject(5,24,16,"‹‹Ne babalar çocuklarının günahından ötürü öldürülecek, ne de çocuklar babalarının. Herkes kendi günahı için öldürülecek."));
trsuperobject.biblemap.put("b5_24_17", new trsuperobject(5,24,17,"‹‹Yabancıya ya da öksüze haksızlık etmeyeceksiniz. Dul kadının giysisini rehin almayacaksınız."));
trsuperobject.biblemap.put("b5_24_18", new trsuperobject(5,24,18,"Mısırda köle olduğunuzu, Tanrınız RABbin sizi oradan kurtardığını anımsayın. Bunun için böyle davranmanızı buyuruyorum."));
trsuperobject.biblemap.put("b5_24_19", new trsuperobject(5,24,19,"‹‹Tarlanızdaki ekini biçtiğinizde, gözden kaçan bir demet olursa, almak için geri dönmeyin. Onu yabancıya, öksüze, dul kadına bırakın. Öyle ki, Tanrınız RAB el attığınız her işte sizi kutsasın."));
trsuperobject.biblemap.put("b5_24_20", new trsuperobject(5,24,20,"Zeytin ağaçlarınızı dövüp ürününü topladığınızda, dallarda kalanı toplamak için geri dönmeyeceksiniz. Kalanları yabancıya, öksüze, dul kadına bırakacaksınız."));
trsuperobject.biblemap.put("b5_24_21", new trsuperobject(5,24,21,"Bağbozumunda artakalan üzümleri toplamak için geri dönmeyeceksiniz. Yabancıya, öksüze, dul kadına bırakacaksınız."));
trsuperobject.biblemap.put("b5_24_22", new trsuperobject(5,24,22,"Mısır'da köle olduğunuzu anımsayın. Bunun için böyle davranmanızı buyuruyorum."));
trsuperobject.biblemap.put("b5_25_1", new trsuperobject(5,25,1,"‹‹Kişiler arasında bir sorun çıktığında, taraflar mahkemeye gittiğinde, yargıçlar davaya bakacak; suçsuzu aklayacak, suçluyu cezaya çarptıracaklar."));
trsuperobject.biblemap.put("b5_25_2", new trsuperobject(5,25,2,"Eğer suçlu kişi kamçılanmayı hak ettiyse, yargıç onu yere yatırtacak ve önünde suçu oranında sayıyla kamçılatacak."));
trsuperobject.biblemap.put("b5_25_3", new trsuperobject(5,25,3,"Suçluya kırk kırbaçtan fazla vurulmamalı. Kırbaç sayısı kırkı aşarsa, kardeşiniz gözünüzde aşağılanabilir."));
trsuperobject.biblemap.put("b5_25_4", new trsuperobject(5,25,4,"‹‹Harman döven öküzün ağzını bağlamayacaksın."));
trsuperobject.biblemap.put("b5_25_5", new trsuperobject(5,25,5,"‹‹Birlikte oturan kardeşlerden biri oğlu olmadan ölürse, ölenin dulu aile dışından biriyle evlenmemeli. Ölenin kardeşi dul kalan kadına gidecek. Onu kendine karı olarak alacak, ona kayınbiraderlik görevini yapacak."));
trsuperobject.biblemap.put("b5_25_6", new trsuperobject(5,25,6,"Kadının doğuracağı ilk oğul, ölen kardeşin adını sürdürsün. Öyle ki, ölenin adı İsrailden silinmesin."));
trsuperobject.biblemap.put("b5_25_7", new trsuperobject(5,25,7,"Ama adam kardeşinin dul karısıyla evlenmek istemiyorsa, dul kadın kent kapısında görev yapan ileri gelenlere gidip şöyle diyecek: ‹Kayınbiraderim İsrailde kardeşinin adını yaşatmayı kabul etmiyor. Bana kayınbiraderlik görevini yapmak istemiyor.›"));
trsuperobject.biblemap.put("b5_25_8", new trsuperobject(5,25,8,"Kentin ileri gelenleri adamı çağırıp onunla konuşacaklar. Eğer adam, ‹Onunla evlenmek istemiyorum› diye üstelerse,"));
trsuperobject.biblemap.put("b5_25_9", new trsuperobject(5,25,9,"kardeşinin dul karısı ileri gelenlerin önünde adamın yanına gidecek, onun ayağındaki çarığı çıkaracak, yüzüne tükürecek ve, ‹Kardeşine soy yetiştirmek istemeyen adama böyle yapılır› diyecek."));
trsuperobject.biblemap.put("b5_25_10", new trsuperobject(5,25,10,"Adamın soyu İsrailde ‹Çarığı çıkarılanın soyu› diye bilinecek."));
trsuperobject.biblemap.put("b5_25_11", new trsuperobject(5,25,11,"‹‹Eğer iki adam kavgaya tutuşur da birinin karısı kocasını dövenin elinden kurtarmak için gelip elini uzatır, öbür adamın erkeklik organını tutarsa,"));
trsuperobject.biblemap.put("b5_25_12", new trsuperobject(5,25,12,"kadının elini keseceksiniz; ona acımayacaksınız."));
trsuperobject.biblemap.put("b5_25_13", new trsuperobject(5,25,13,"‹‹Torbanızda biri ağır, öbürü hafif iki türlü tartı olmayacak."));
trsuperobject.biblemap.put("b5_25_14", new trsuperobject(5,25,14,"Evinizde biri büyük, öbürü küçük iki türlü ölçü olmayacak."));
trsuperobject.biblemap.put("b5_25_15", new trsuperobject(5,25,15,"Tartınız da ölçünüz de eksiksiz ve doğru olacak. Öyle ki, Tanrınız RABbin size vereceği ülkede ömrünüz uzun olsun."));
trsuperobject.biblemap.put("b5_25_16", new trsuperobject(5,25,16,"Tanrınız RAB bunları yapandan da, haksızlık edenden de tiksinir. ölçek."));
trsuperobject.biblemap.put("b5_25_17", new trsuperobject(5,25,17,"‹‹Siz Mısırdan çıktıktan sonra Amaleklilerin yolda size neler yaptığını anımsayın."));
trsuperobject.biblemap.put("b5_25_18", new trsuperobject(5,25,18,"Siz yorgun ve bitkinken yolda size saldırdılar; geride kalan bütün güçsüzleri öldürdüler. Tanrıdan korkmadılar."));
trsuperobject.biblemap.put("b5_25_19", new trsuperobject(5,25,19,"Tanrınız RAB mülk edinmek için miras olarak size vereceği ülkede sizi çevrenizdeki bütün düşmanlardan kurtarıp rahata kavuşturunca, Amalekliler'in anısını gökler altından sileceksiniz. Bunu unutmayın!››"));
trsuperobject.biblemap.put("b5_26_1", new trsuperobject(5,26,1,"‹‹Tanrınız RABbin miras olarak size vereceği ülkeye girip orayı mülk edinerek yerleştiğinizde,"));
trsuperobject.biblemap.put("b5_26_2", new trsuperobject(5,26,2,"Tanrınız RABbin size vereceği ülkenin topraklarından topladığınız bütün ürünlerin ilk yetişenlerini alıp sepete koyacaksınız. Sonra Tanrınız RABbin adını yerleştirmek için seçeceği yere gideceksiniz."));
trsuperobject.biblemap.put("b5_26_3", new trsuperobject(5,26,3,"O dönemde görevli kâhine gidip, ‹RABbin bize ant içerek atalarımıza söz verdiği ülkeye geldiğimi Tanrın RABbe bugün bildiriyorum› diyeceksiniz."));
trsuperobject.biblemap.put("b5_26_4", new trsuperobject(5,26,4,"Kâhin sepeti elinizden alıp Tanrınız RABbin sunağının önüne koyacak."));
trsuperobject.biblemap.put("b5_26_5", new trsuperobject(5,26,5,"Sonra Tanrınız RABbin önünde şu açıklamayı yapacaksınız: ‹Babam göçebe bir Aramlıydı. Sayıca az kişiyle Mısıra gidip orada yaşamaya başladı. Orada büyük, güçlü, kalabalık bir ulus oldu."));
trsuperobject.biblemap.put("b5_26_6", new trsuperobject(5,26,6,"Mısırlılar bize kötü davranarak baskı yaptılar. Bizi ağır işlere zorladılar."));
trsuperobject.biblemap.put("b5_26_7", new trsuperobject(5,26,7,"Atalarımızın Tanrısı RABbe yakardık. RAB yakarışımızı duydu; çektiğimiz sıkıntıyı, emeği, bize yapılan baskıyı gördü."));
trsuperobject.biblemap.put("b5_26_8", new trsuperobject(5,26,8,"Bunun üzerine güçlü elle, kudretle, büyük ve ürkütücü olaylarla, belirtilerle, şaşılası işlerle bizi Mısırdan çıkardı."));
trsuperobject.biblemap.put("b5_26_9", new trsuperobject(5,26,9,"Bizi buraya getirdi; bu toprakları, süt ve bal akan ülkeyi bize verdi."));
trsuperobject.biblemap.put("b5_26_10", new trsuperobject(5,26,10,"Şimdi, ya RAB, bize verdiğin toprağın ürününün ilk yetişenini getiriyorum.› Sonra sepeti Tanrınız RABbin önüne koyup Onun önünde yere kapanacaksınız."));
trsuperobject.biblemap.put("b5_26_11", new trsuperobject(5,26,11,"Sizler, Levililer ve aranızda yaşayan yabancılar Tanrınız RABbin size ve ailenize verdiği bütün iyi şeyler için sevineceksiniz."));
trsuperobject.biblemap.put("b5_26_12", new trsuperobject(5,26,12,"‹‹Üçüncü yıl, ondalığı verme yılı, bütün ürününüzün ondalığını bir yana ayırın. Ayırma işini bitirdiğinizde, ondalığı Levililere, yabancılara, öksüzlere ve dul kadınlara vereceksiniz. Öyle ki, onlar da kentlerinizde yiyip doysunlar."));
trsuperobject.biblemap.put("b5_26_13", new trsuperobject(5,26,13,"Sonra Tanrınız RABbe, ‹Bana buyurduğun gibi, RABbe ayırdıklarımı evden çıkarıp Levililere, yabancılara, öksüzlere ve dul kadınlara verdim› diyeceksiniz, ‹Buyruklarından ayrılmadım, hiç birini unutmadım."));
trsuperobject.biblemap.put("b5_26_14", new trsuperobject(5,26,14,"Ne yas tutarken ayırdıklarımdan yedim, ne dinsel açıdan kirliyken onlara dokundum, ne de ölülere sundum. Tanrım RABbin sözüne kulak verdim. Bana bütün buyurduklarını yaptım."));
trsuperobject.biblemap.put("b5_26_15", new trsuperobject(5,26,15,"Kutsal konutundan, göklerden aşağıya bak! Halkın İsraili ve atalarımıza içtiğin ant uyarınca bize verdiğin ülkeyi, süt ve bal akan ülkeyi kutsa.››"));
trsuperobject.biblemap.put("b5_26_16", new trsuperobject(5,26,16,"‹‹Bugün Tanrınız RAB bu kurallara, ilkelere uymanızı buyuruyor. Onlara bütün yüreğinizle, canınızla uymaya dikkat edin."));
trsuperobject.biblemap.put("b5_26_17", new trsuperobject(5,26,17,"Bugün RABbin Tanrınız olduğunu, Onun yollarında yürüyeceğinizi, kurallarına, buyruklarına, ilkelerine uyacağınızı, Onun sözünü dinleyeceğinizi açıkladınız."));
trsuperobject.biblemap.put("b5_26_18", new trsuperobject(5,26,18,"Bugün RAB, size verdiği söz uyarınca, öz halkı olduğunuzu açıkladı. Bütün buyruklarına uyacaksınız."));
trsuperobject.biblemap.put("b5_26_19", new trsuperobject(5,26,19,"Tanrınız RAB sizi övgüde, ünde, onurda yarattığı bütün uluslardan üstün kılacağını, verdiği söz uyarınca kendisi için kutsal bir halk olacağınızı açıkladı.››"));
trsuperobject.biblemap.put("b5_27_1", new trsuperobject(5,27,1,"Musa ile İsrail ileri gelenleri halka şöyle dediler: ‹‹Bugün size ilettiğim bütün buyruklara uyun."));
trsuperobject.biblemap.put("b5_27_2", new trsuperobject(5,27,2,"Şeria Irmağından Tanrınız RABbin size vereceği ülkeye geçince, büyük taşlar dikip kireçleyeceksiniz."));
trsuperobject.biblemap.put("b5_27_3", new trsuperobject(5,27,3,"Atalarınızın Tanrısı RABbin size verdiği söz uyarınca Onun size vereceği ülkeye, süt ve bal akan ülkeye girince, bu yasanın bütün sözlerini taşlara yazacaksınız."));
trsuperobject.biblemap.put("b5_27_4", new trsuperobject(5,27,4,"Şeria Irmağından geçince, bugün size buyurduğum gibi, bu taşları Eval Dağına dikip kireçleyeceksiniz."));
trsuperobject.biblemap.put("b5_27_5", new trsuperobject(5,27,5,"Orada Tanrınız RABbe taşlardan bir sunak yapacaksınız. Bu taşlara demir alet uygulamayacaksınız."));
trsuperobject.biblemap.put("b5_27_6", new trsuperobject(5,27,6,"Tanrınız RABbin sunağını yontulmamış taşlardan yapacak, üzerinde Tanrınız RABbe yakmalık sunular sunacaksınız."));
trsuperobject.biblemap.put("b5_27_7", new trsuperobject(5,27,7,"Esenlik sunularını orada kesip yiyecek ve Tanrınız RABbin önünde sevineceksiniz."));
trsuperobject.biblemap.put("b5_27_8", new trsuperobject(5,27,8,"Taşlara bu yasanın bütün sözlerini okunaklı bir biçimde yazacaksınız.››"));
trsuperobject.biblemap.put("b5_27_9", new trsuperobject(5,27,9,"Sonra Musa ile Levili kâhinler bütün İsraillilere, ‹‹Ey İsrail, sus ve kulak ver!›› diye seslendiler, ‹‹Bugün Tanrınız RABbin halkı oldunuz."));
trsuperobject.biblemap.put("b5_27_10", new trsuperobject(5,27,10,"Tanrınız RABbin sözüne kulak verin, bugün size ilettiğim buyruklarına, kurallarına uyun.››"));
trsuperobject.biblemap.put("b5_27_11", new trsuperobject(5,27,11,"O gün Musa halka şöyle dedi:"));
trsuperobject.biblemap.put("b5_27_12", new trsuperobject(5,27,12,"‹‹Şeria Irmağından geçince, halkı kutsamak için Gerizim Dağında duracak oymaklar şunlardır: Şimon, Levi, Yahuda, İssakar, Yusuf, Benyamin."));
trsuperobject.biblemap.put("b5_27_13", new trsuperobject(5,27,13,"Lanetlemek için Eval Dağında şu oymaklar duracak: Ruben, Gad, Aşer, Zevulun, Dan, Naftali."));
trsuperobject.biblemap.put("b5_27_14", new trsuperobject(5,27,14,"Levililer bütün İsrail halkına yüksek sesle şöyle diyecekler:"));
trsuperobject.biblemap.put("b5_27_15", new trsuperobject(5,27,15,"‹‹RABbin tiksindiği el işi oyma ya da dökme put yapana ve onu gizlice dikene lanet olsun!› ‹‹Bütün halk, ‹Amin!› diye karşılık verecek."));
trsuperobject.biblemap.put("b5_27_16", new trsuperobject(5,27,16,"‹‹Annesine, babasına saygısızca davranana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_17", new trsuperobject(5,27,17,"‹‹Komşusunun sınırını değiştirene lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_18", new trsuperobject(5,27,18,"‹‹Kör olanı yoldan saptırana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_19", new trsuperobject(5,27,19,"‹‹Yabancıya, öksüze, dul kadına haksızlık edene lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_20", new trsuperobject(5,27,20,"‹‹Babasının karısıyla yatana lanet olsun! Çünkü o babasının evlilik yatağına leke sürmüştür.› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_21", new trsuperobject(5,27,21,"‹‹Herhangi bir hayvanla cinsel ilişki kurana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_22", new trsuperobject(5,27,22,"‹‹Annesinden ya da babasından olan kızkardeşiyle yatana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_23", new trsuperobject(5,27,23,"‹‹Kaynanasıyla yatana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_24", new trsuperobject(5,27,24,"‹‹Komşusunu gizlice öldürene lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_25", new trsuperobject(5,27,25,"‹‹Suçsuz birini öldürmek için rüşvet alana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek."));
trsuperobject.biblemap.put("b5_27_26", new trsuperobject(5,27,26,"‹‹Bu yasanın sözlerine uymayan ve onları onaylamayana lanet olsun!› ‹‹Bütün halk, ‹Amin!› diyecek.››"));
trsuperobject.biblemap.put("b5_28_1", new trsuperobject(5,28,1,"‹‹Eğer Tanrınız RABbin sözünü iyice dinler ve bugün size ilettiğim bütün buyruklarına uyarsanız, Tanrınız RAB sizi yeryüzündeki bütün uluslardan üstün kılacaktır."));
trsuperobject.biblemap.put("b5_28_2", new trsuperobject(5,28,2,"Tanrınız RABbin sözünü dinlerseniz, şu bereketler üzerinize gelecek ve sizinle olacak:"));
trsuperobject.biblemap.put("b5_28_3", new trsuperobject(5,28,3,"‹‹Kentte de tarlada da kutsanacaksınız."));
trsuperobject.biblemap.put("b5_28_4", new trsuperobject(5,28,4,"‹‹Rahminizin meyvesi kutsanacak. Toprağınızın ürünü, hayvanlarınızın dölü -sığırlarınızın buzağıları, sürülerinizin kuzuları- bereketli olacak."));
trsuperobject.biblemap.put("b5_28_5", new trsuperobject(5,28,5,"‹‹Sepetiniz ve hamur tekneniz bereketli olacak."));
trsuperobject.biblemap.put("b5_28_6", new trsuperobject(5,28,6,"‹‹İçeri girdiğinizde de dışarı çıktığınızda da kutsanacaksınız."));
trsuperobject.biblemap.put("b5_28_7", new trsuperobject(5,28,7,"‹‹RAB size saldıran düşmanlarınızı önünüzde bozguna uğratacak. Onlar size bir yoldan saldıracak, ama önünüzden yedi yoldan kaçacaklar."));
trsuperobject.biblemap.put("b5_28_8", new trsuperobject(5,28,8,"‹‹RABbin buyruğuyla ambarlarınız dolu olacak. El attığınız her işte RAB sizi kutsayacak. Tanrınız RAB size vereceği ülkede sizi kutsayacak."));
trsuperobject.biblemap.put("b5_28_9", new trsuperobject(5,28,9,"‹‹Tanrınız RABbin buyruklarına uyar, Onun yollarında yürürseniz, RAB size içtiği ant uyarınca sizi kendisi için kutsal bir halk olarak koruyacaktır."));
