package tr.bible;

public class trbible50 {

public trbible50() {

putmapbible50();

}

private void putmapbible50() {

trsuperobject.biblemap.put("b50_1_1", new trsuperobject(50,1,1,"Mesih İsanın kulları ben Pavlus ve Timoteostan Filipideki gözetmenler ve görevlilerle birlikte Mesih İsaya ait bütün kutsallara selam!"));
trsuperobject.biblemap.put("b50_1_2", new trsuperobject(50,1,2,"Babamız Tanrıdan ve Rab İsa Mesihten sizlere lütuf ve esenlik olsun."));
trsuperobject.biblemap.put("b50_1_3", new trsuperobject(50,1,3,"Sizi hatırladıkça Tanrıma şükrediyorum."));
trsuperobject.biblemap.put("b50_1_4-5", new trsuperobject(50,1,4-5,"İlk günden şimdiye dek Müjdenin yayılmasındaki işbirliğinizden dolayı her duamda hepiniz için her zaman sevinçle dilekte bulunuyorum."));
trsuperobject.biblemap.put("b50_1_6", new trsuperobject(50,1,6,"Sizde iyi bir işe başlamış olan Tanrının bunu Mesih İsanın gününe dek bitireceğine güvenim var."));
trsuperobject.biblemap.put("b50_1_7", new trsuperobject(50,1,7,"Hepiniz için böyle düşünmekte haklıyım. Her an yüreğimdesiniz. İster zincire vurulmuş, ister Müjdeyi savunup doğrulamakta olayım, hepiniz benimle birlikte Tanrının lütfuna ortaksınız."));
trsuperobject.biblemap.put("b50_1_8", new trsuperobject(50,1,8,"Hepinizi Mesih İsanın sevgisiyle nasıl özlediğime Tanrı tanıktır."));
trsuperobject.biblemap.put("b50_1_9", new trsuperobject(50,1,9,"Duam şu ki, sevginiz, bilgi ve her tür sezgiyle durmadan artsın."));
trsuperobject.biblemap.put("b50_1_1-011", new trsuperobject(50,1,1-011,"Öyle ki, üstün değerleri ayırt edebilesiniz ve böylece Tanrının yüceltilip övülmesi için İsa Mesih aracılığıyla gelen doğruluk meyvesiyle dolarak Mesihin gününde saf ve kusursuz olasınız."));
trsuperobject.biblemap.put("b50_1_12", new trsuperobject(50,1,12,"Kardeşler, şunu bilmenizi isterim: Başıma gelenler daha çok Müjdenin yayılmasına yaramıştır."));
trsuperobject.biblemap.put("b50_1_13", new trsuperobject(50,1,13,"Sonuç olarak bütün saray muhafızları dahil, herkes Mesih uğruna zincire vurulduğumu öğrendi."));
trsuperobject.biblemap.put("b50_1_14", new trsuperobject(50,1,14,"Kardeşlerin çoğu da zincire vuruluşumdan ötürü Rabbe güvenerek Tanrının sözünü korkusuzca söylemekte daha da cesur davranıyorlar."));
trsuperobject.biblemap.put("b50_1_15", new trsuperobject(50,1,15,"Gerçi kimi Mesihi kıskançlık ve rekabetle, kimiyse iyi niyetle duyuruyor."));
trsuperobject.biblemap.put("b50_1_16", new trsuperobject(50,1,16,"Sonuncular, Müjdeyi savunmaya atandığımı bilerek bunu sevgiyle yapıyorlar."));
trsuperobject.biblemap.put("b50_1_17", new trsuperobject(50,1,17,"Ötekilerse Mesihi temiz yürekle değil, bencil tutkularla duyuruyorlar. Böylece tutukluluğumda bana sıkıntı vereceklerini sanıyorlar."));
trsuperobject.biblemap.put("b50_1_18", new trsuperobject(50,1,18,"Ama ne önemi var? İster art niyetle ister içtenlikle olsun, her durumda Mesih duyurulmuş oluyor. Buna seviniyorum, sevineceğim de."));
trsuperobject.biblemap.put("b50_1_19", new trsuperobject(50,1,19,"Çünkü dualarınızla ve İsa Mesihin Ruhu yardımıyla bunun bana kurtuluş getireceğini biliyorum."));
trsuperobject.biblemap.put("b50_1_20", new trsuperobject(50,1,20,"Hiçbir şekilde utandırılmayacağımı, yaşasam da ölsem de Mesihin her zamanki gibi şimdi de bedenimde yüceltilmesi için tam bir cesaret gösterebileceğimi bekliyor ve umut ediyorum."));
trsuperobject.biblemap.put("b50_1_21", new trsuperobject(50,1,21,"Çünkü benim için, yaşamak Mesihtir, ölmek kazançtır."));
trsuperobject.biblemap.put("b50_1_22", new trsuperobject(50,1,22,"Hayatta kalırsam yararlı işler yapacağım. Ama hangisini seçeceğimi bilemiyorum."));
trsuperobject.biblemap.put("b50_1_23", new trsuperobject(50,1,23,"İki seçenek arasında kaldım. Dünyadan ayrılıp Mesihle birlikte olmayı arzuluyorum; bu çok daha iyi."));
trsuperobject.biblemap.put("b50_1_24", new trsuperobject(50,1,24,"Ama hayatta kalmam sizin için daha gereklidir."));
trsuperobject.biblemap.put("b50_1_25", new trsuperobject(50,1,25,"Bundan emin olarak kalacağımı biliyorum. İmanda gelişip sevinmeniz için hepinizle birlikte olmaya devam edeceğim."));
trsuperobject.biblemap.put("b50_1_26", new trsuperobject(50,1,26,"Öyle ki, tekrar yanınıza geldiğimde, Mesih İsada benimle daha çok övünebilesiniz."));
trsuperobject.biblemap.put("b50_1_27-28", new trsuperobject(50,1,27-28,"Ancak yaşayışınız Mesihin Müjdesine layık olsun. Öyle ki, gelip sizi görsem de gelmesem de sizinle ilgili haberleri, tek bir ruhta dimdik durduğunuzu, Müjdede açıklanan inanç uğruna tek can halinde birlikte mücadele ettiğinizi, size karşı olanlardan hiçbir şekilde yılmadığınızı duyayım. Böyle davranmanız onlara bir belirtidir - kendilerinin mahvolacağını, sizlerin ise kurtulacağını gösteren bir belirti. Bu da Tanrının işidir."));
trsuperobject.biblemap.put("b50_1_29", new trsuperobject(50,1,29,"Çünkü Mesih uğruna size yalnız Mesih'e iman etmek değil, daha önce bende gördüğünüz ve hâlâ sürdürdüğümü duyduğunuz zorlu çabanın aynısını göstererek Mesih uğruna acı çekmek ayrıcalığı da verildi."));
trsuperobject.biblemap.put("b50_2_1-2", new trsuperobject(50,2,1-2,"Böylece Mesihten gelen bir cesaret, sevgiden doğan bir teselli ve Ruhla bir paydaşlık varsa, yürekten bir sevgi ve sevecenlik varsa, aynı düşüncede, sevgide, ruhta ve amaçta birleşerek sevincimi tamamlayın."));
trsuperobject.biblemap.put("b50_2_3", new trsuperobject(50,2,3,"Hiçbir şeyi bencil tutkularla ya da boş övünmeyle yapmayın. Her biriniz alçakgönüllülükle öbürünü kendinden üstün saysın."));
trsuperobject.biblemap.put("b50_2_4", new trsuperobject(50,2,4,"Yalnız kendi yararını değil, başkalarının yararını da gözetsin."));
trsuperobject.biblemap.put("b50_2_5", new trsuperobject(50,2,5,"Mesih İsadaki düşünce sizde de olsun."));
trsuperobject.biblemap.put("b50_2_6", new trsuperobject(50,2,6,"Mesih, Tanrı özüne sahip olduğu halde, Tanrıya eşitliği sımsıkı sarılacak bir hak saymadı."));
trsuperobject.biblemap.put("b50_2_7-8", new trsuperobject(50,2,7-8,"Ama kul özünü alıp insan benzeyişinde doğarak ululuğunu bir yana bıraktı. İnsan biçimine bürünmüş olarak ölüme, çarmıh üzerinde ölüme bile boyun eğip kendini alçalttı."));
trsuperobject.biblemap.put("b50_2_9", new trsuperobject(50,2,9,"Bunun için de Tanrı Onu pek çok yükseltti ve Ona her adın üstünde olan adı bağışladı."));
trsuperobject.biblemap.put("b50_2_1-011", new trsuperobject(50,2,1-011,"Öyle ki, İsanın adı anıldığında gökteki, yerdeki ve yer altındakilerin hepsi diz çöksün ve her dil, Baba Tanrının yüceltilmesi için İsa Mesihin Rab olduğunu açıkça söylesin."));
trsuperobject.biblemap.put("b50_2_12", new trsuperobject(50,2,12,"Öyleyse sevgili kardeşlerim, her zaman söz dinlediğiniz gibi, yalnız ben aranızdayken değil, ama özellikle aranızda olmadığım şu anda da kurtuluşunuzu saygı ve korkuyla etkin kılın."));
trsuperobject.biblemap.put("b50_2_13", new trsuperobject(50,2,13,"Çünkü kendisini hoşnut edeni hem istemeniz hem de yapmanız için sizde etkin olan Tanrıdır."));
trsuperobject.biblemap.put("b50_2_14-16", new trsuperobject(50,2,14-16,"Her şeyi söylenmeden ve çekişmeden yapın ki, yaşam sözüne sımsıkı sarılarak aralarında evrendeki yıldızlar gibi parladığınız bu eğri ve sapık kuşağın ortasında kusursuz ve saf, Tanrının lekesiz çocukları olasınız. Öyle ki, boşuna koşmadığımı, boşuna emek vermediğimi görerek Mesihin gününde övünecek bir nedenim olsun."));
trsuperobject.biblemap.put("b50_2_17", new trsuperobject(50,2,17,"Kanım imanınızın sunusu ve hizmeti üzerine adak şarabı gibi dökülecek olsa da seviniyor, hepinizin sevincine katılıyorum."));
trsuperobject.biblemap.put("b50_2_18", new trsuperobject(50,2,18,"Aynı şekilde siz de sevinin ve benim sevincime katılın."));
trsuperobject.biblemap.put("b50_2_19", new trsuperobject(50,2,19,"Durumunuzu öğrenmek, böylece içimi rahatlatmak üzere yakında Timoteosu yanınıza gönderebileceğime ilişkin Rab İsada umudum var."));
trsuperobject.biblemap.put("b50_2_20", new trsuperobject(50,2,20,"Timoteos gibi düşünen, durumunuzla içtenlikle ilgilenecek başka kimsem yok."));
trsuperobject.biblemap.put("b50_2_21", new trsuperobject(50,2,21,"Herkes kendi işini düşünüyor, Mesih İsanınkini değil."));
trsuperobject.biblemap.put("b50_2_22", new trsuperobject(50,2,22,"Ama Timoteosun, değerini kanıtlamış biri olduğunu, babasının yanında hizmet eden çocuk gibi, Müjdenin yayılması için benim yanımda hizmet ettiğini bilirsiniz."));
trsuperobject.biblemap.put("b50_2_23", new trsuperobject(50,2,23,"Durumum belli olur olmaz onu size göndermeyi umuyorum."));
trsuperobject.biblemap.put("b50_2_24", new trsuperobject(50,2,24,"Ben de yakında geleceğim, bu konuda Rabbe güveniyorum."));
trsuperobject.biblemap.put("b50_2_25", new trsuperobject(50,2,25,"Ama muhtaç anımda bana yardım etmek üzere gönderdiğiniz elçiyi, omuz omuza mücadele verdiğim kardeşim ve emektaşım Epafroditusu size geri yollamayı gerekli gördüm."));
trsuperobject.biblemap.put("b50_2_26", new trsuperobject(50,2,26,"Çünkü hepinizi özlüyor, hasta olduğunu öğrendiğiniz için çok üzülüyordu."));
trsuperobject.biblemap.put("b50_2_27", new trsuperobject(50,2,27,"Gerçekten de ölecek kadar hastaydı. Ama Tanrı ona acıdı; yalnız ona değil, acı üstüne acı duymayayım diye bana da acıdı."));
trsuperobject.biblemap.put("b50_2_28", new trsuperobject(50,2,28,"İşte bu nedenle, onu tekrar görüp sevinesiniz diye kendisini daha büyük bir istekle yanınıza gönderiyorum. Böylelikle benim de kaygılarım hafifleyecek."));
trsuperobject.biblemap.put("b50_2_29", new trsuperobject(50,2,29,"Onu Rabde tam bir sevinçle kabul edin, onun gibi kişileri onurlandırın."));
trsuperobject.biblemap.put("b50_2_30", new trsuperobject(50,2,30,"Çünkü sizin bana yapamadığınız yardımı yapmak için canını tehlikeye atarak Mesih'in işi uğruna neredeyse ölüyordu."));
trsuperobject.biblemap.put("b50_3_1", new trsuperobject(50,3,1,"Sonuç olarak, kardeşlerim, Rabde sevinin. Size aynı şeyleri yazmak bana usanç vermez; hem bu sizin için bir güvencedir."));
trsuperobject.biblemap.put("b50_3_2", new trsuperobject(50,3,2,"Kötülük yapan o adamlardan, o köpeklerden sakının; o sünnet bağnazlarından sakının!"));
trsuperobject.biblemap.put("b50_3_3", new trsuperobject(50,3,3,"Çünkü gerçek sünnetliler Tanrının Ruhu aracılığıyla tapınan, Mesih İsayla övünen, insansal özelliklere güvenmeyen bizleriz."));
trsuperobject.biblemap.put("b50_3_4", new trsuperobject(50,3,4,"Ben aslında bunlara da güvenebilirdim. Eğer başka biri bunlara güvenebileceğini sanıyorsa, ben daha çok güvenebilirim."));
trsuperobject.biblemap.put("b50_3_5", new trsuperobject(50,3,5,"Sekiz günlükken sünnet oldum. İsrail soyundan, Benyamin oymağından, özbeöz İbraniyim. Kutsal Yasaya bağlılık derseniz, Ferisiydim."));
trsuperobject.biblemap.put("b50_3_6", new trsuperobject(50,3,6,"Gayret derseniz, kiliseye zulmeden biriydim. Yasaya dayanan doğruluk derseniz, kusursuzdum."));
trsuperobject.biblemap.put("b50_3_7", new trsuperobject(50,3,7,"Ama benim için kazanç olan her şeyi Mesih uğruna zarar saydım."));
trsuperobject.biblemap.put("b50_3_8-9", new trsuperobject(50,3,8-9,"Dahası var, uğruna her şeyi yitirdiğim Rabbim İsa Mesihi tanımanın üstün değeri yanında her şeyi zarar sayıyorum, süprüntü sayıyorum. Öyle ki, Mesihi kazanayım ve Kutsal Yasaya dayanan kişisel doğruluğa değil, Mesihe iman etmekle kazanılan, iman sonucu Tanrıdan gelen doğruluğa sahip olarak Mesihte bulunayım."));
trsuperobject.biblemap.put("b50_3_1-011", new trsuperobject(50,3,1-011,"Ölümünde Onunla özdeşleşerek Onu tanımak, dirilişinin gücünü ve acılarına ortak olmanın ne demek olduğunu bilmek ve böylece ne yapıp yapıp ölümden dirilişe erişmek istiyorum."));
trsuperobject.biblemap.put("b50_3_12", new trsuperobject(50,3,12,"Bunlara şimdiden kavuştuğumu ya da yetkinliğe eriştiğimi söylemiyorum. Ama Mesih İsanın beni kazanmakla benim için öngördüğü ödülü kazanmak için koşuyorum."));
trsuperobject.biblemap.put("b50_3_13-14", new trsuperobject(50,3,13-14,"Kardeşler, kendimi bunu kazanmış saymıyorum. Ancak şunu yapıyorum: Geride kalan her şeyi unutup ileride olanlara uzanarak, Tanrının Mesih İsa aracılığıyla yaptığı göksel çağrıda öngörülen ödülü kazanmak için hedefe doğru koşuyorum."));
trsuperobject.biblemap.put("b50_3_15", new trsuperobject(50,3,15,"Bunun için olgun olanlarımızın hepsi bu düşüncede olsun. Herhangi bir konuda farklı bir düşünceniz varsa, Tanrı bunu da size açıkça gösterecek."));
trsuperobject.biblemap.put("b50_3_16", new trsuperobject(50,3,16,"Ancak, eriştiğimiz düzeye uygun bir yaşam sürelim."));
trsuperobject.biblemap.put("b50_3_17", new trsuperobject(50,3,17,"Kardeşler, hepiniz beni örnek alın. Size verdiğimiz örnek uyarınca yaşayanlara dikkatle bakın."));
trsuperobject.biblemap.put("b50_3_18", new trsuperobject(50,3,18,"Size defalarca söylediğim gibi, şimdi gözyaşları içinde tekrar söylüyorum: Birçok kişi Mesihin çarmıhına düşman olarak yaşıyor."));
trsuperobject.biblemap.put("b50_3_19", new trsuperobject(50,3,19,"Onların sonu yıkımdır; tanrıları mideleridir. Ayıplarıyla övünür, yalnız bu dünyayı düşünürler."));
trsuperobject.biblemap.put("b50_3_20", new trsuperobject(50,3,20,"Oysa bizim vatanımız göklerdedir. Oradan Kurtarıcıyı, Rab İsa Mesihi bekliyoruz."));
trsuperobject.biblemap.put("b50_3_21", new trsuperobject(50,3,21,"O her şeyi kendine bağlı kılmaya yeten gücünün etkinliğiyle zavallı bedenlerimizi değiştirip kendi yüce bedenine benzer hale getirecektir."));
trsuperobject.biblemap.put("b50_4_1", new trsuperobject(50,4,1,"Bu nedenle, ey sevgililer, sevincim, başımın tacı, içten özlediğim sevgili kardeşlerim, böylece Rabde dimdik durun."));
trsuperobject.biblemap.put("b50_4_2", new trsuperobject(50,4,2,"Evodiyaya rica ediyorum, Sintihiye rica ediyorum, Rab yolunda aynı düşüncede olun."));
trsuperobject.biblemap.put("b50_4_3", new trsuperobject(50,4,3,"Evet, gerçek yoldaşım, sana da yalvarırım, bu kadınlara yardım et. Çünkü onlar benimle, Klementle ve adları yaşam kitabında bulunan öbür emektaşlarımla birlikte Müjdeyi yaymak için mücadele ettiler."));
trsuperobject.biblemap.put("b50_4_4", new trsuperobject(50,4,4,"Rabde her zaman sevinin; yine söylüyorum, sevinin!"));
trsuperobject.biblemap.put("b50_4_5", new trsuperobject(50,4,5,"Uysallığınız bütün insanlarca bilinsin. Rabbin gelişi yakındır."));
trsuperobject.biblemap.put("b50_4_6", new trsuperobject(50,4,6,"Hiç kaygılanmayın; her konudaki dileklerinizi, Tanrıya dua edip yalvararak şükranla bildirin."));
trsuperobject.biblemap.put("b50_4_7", new trsuperobject(50,4,7,"O zaman Tanrının her kavrayışı aşan esenliği Mesih İsa aracılığıyla yüreklerinizi ve düşüncelerinizi koruyacaktır."));
trsuperobject.biblemap.put("b50_4_8", new trsuperobject(50,4,8,"Sonuç olarak, kardeşlerim, gerçek, saygıdeğer, doğru, pak, sevimli, hayranlık uyandıran, erdemli ve övülmeye değer ne varsa, onu düşünün."));
trsuperobject.biblemap.put("b50_4_9", new trsuperobject(50,4,9,"Benden öğrendiğiniz, kabul ettiğiniz, işittiğiniz, bende gördüğünüz ne varsa, onu yapın. O zaman esenlik veren Tanrı sizinle olacaktır."));
trsuperobject.biblemap.put("b50_4_10", new trsuperobject(50,4,10,"Bana duyduğunuz ilgiyi sonunda tazelediğiniz için Rabde çok sevindim. Aslında ilgi duyuyordunuz, ama bunu göstermeye fırsatınız olmadı."));
trsuperobject.biblemap.put("b50_4_11", new trsuperobject(50,4,11,"Bunu ihtiyacım olduğu için söylemiyorum. Çünkü ben her durumda eldekiyle yetinmeyi öğrendim."));
trsuperobject.biblemap.put("b50_4_12", new trsuperobject(50,4,12,"Yoksulluk çekmeyi de bilirim, bolluk içinde yaşamayı da. İster tok ister aç, ister bolluk ister ihtiyaç içinde olayım, her durumda, her koşulda yaşamanın sırrını öğrendim."));
trsuperobject.biblemap.put("b50_4_13", new trsuperobject(50,4,13,"Beni güçlendirenin aracılığıyla her şeyi yapabilirim."));
trsuperobject.biblemap.put("b50_4_14", new trsuperobject(50,4,14,"Yine de sıkıntılarıma ortak olmakla iyi ettiniz."));
trsuperobject.biblemap.put("b50_4_15", new trsuperobject(50,4,15,"Siz de bilirsiniz, ey Filipililer, Müjde yayılmaya başladığında, Makedonyadan ayrılışımdan sonra sizden başka hiçbir topluluk karşılıklı yardımlaşma konusunda benimle işbirliği yapmadı."));
trsuperobject.biblemap.put("b50_4_16", new trsuperobject(50,4,16,"Ben Selanikteyken de, ihtiyacım olduğunda birkaç kez bana yardımda bulundunuz."));
trsuperobject.biblemap.put("b50_4_17", new trsuperobject(50,4,17,"Armağan peşinde değilim, ama ruhsal kazancın hesabınızda birikmesini istiyorum."));
trsuperobject.biblemap.put("b50_4_18", new trsuperobject(50,4,18,"Benim her şeyim var, bolluk içindeyim. Epafroditusun eliyle gönderdiğiniz armağanları alınca bir eksiğim kalmadı. Bunlar güzel kokulu sunular, Tanrının beğenisini kazanan, Onu hoşnut eden kurbanlardır."));
trsuperobject.biblemap.put("b50_4_19", new trsuperobject(50,4,19,"Tanrım da her ihtiyacınızı kendi zenginliğiyle Mesih İsada görkemli bir biçimde karşılayacaktır."));
trsuperobject.biblemap.put("b50_4_20", new trsuperobject(50,4,20,"Babamız Tanrıya sonsuzlara dek yücelik olsun! Amin."));
trsuperobject.biblemap.put("b50_4_21", new trsuperobject(50,4,21,"Mesih İsaya ait bütün kutsallara selam söyleyin. Yanımdaki kardeşler size selam ederler."));
trsuperobject.biblemap.put("b50_4_22", new trsuperobject(50,4,22,"Bütün kutsallar, özellikle Sezarın ev halkından olanlar size selam ederler."));
trsuperobject.biblemap.put("b50_4_23", new trsuperobject(50,4,23,"Rab İsa Mesih'in lütfu ruhunuzla birlikte olsun."));

}

}