private void putmapbible51() {

trsuperobject.biblemap.put("b51_1_1-2", new trsuperobject(51,1,1-2,"Tanrının isteğiyle Mesih İsanın elçisi atanan ben Pavlus ve kardeşimiz Timoteostan, Kolosede bulunan, Mesihe ait kutsal ve sadık kardeşlere selam! Babamız Tanrıdan sizlere lütuf ve esenlik olsun."));
trsuperobject.biblemap.put("b51_1_3", new trsuperobject(51,1,3,"Sizler için dua ederken Tanrıya, Rabbimiz İsa Mesihin Babasına her zaman şükrediyoruz."));
trsuperobject.biblemap.put("b51_1_4", new trsuperobject(51,1,4,"Çünkü Mesih İsaya iman ettiğinizi ve bütün kutsalları sevdiğinizi duyduk."));
trsuperobject.biblemap.put("b51_1_5-6", new trsuperobject(51,1,5-6,"İmanınız ve sevginiz göklerde sizin için saklı bulunan umuttan kaynaklanıyor. Bu umudun haberini gerçeğin bildirisinden, size daha önce ulaşan Müjdeden aldınız. Müjde, onu işittiğiniz ve Tanrının lütfunu gerçekten anladığınız günden beri aranızda olduğu gibi, bütün dünyada da meyve vermekte, yayılmaktadır."));
trsuperobject.biblemap.put("b51_1_7", new trsuperobject(51,1,7,"Müjdeyi bizim adımıza Mesihin güvenilir hizmetkârı olan sevgili emektaşımız Epafrastan öğrendiniz."));
trsuperobject.biblemap.put("b51_1_8", new trsuperobject(51,1,8,"Ruhtan kaynaklanan sevginizi de bize o bildirdi."));
trsuperobject.biblemap.put("b51_1_9", new trsuperobject(51,1,9,"Bunu işittiğimiz günden beri biz de sizler için dua etmekten, tam bir bilgelik ve ruhsal anlayışla Tanrının isteğini bütünüyle bilmenizi dilemekten geri kalmadık."));
trsuperobject.biblemap.put("b51_1_10", new trsuperobject(51,1,10,"Rabbe yaraşır biçimde yaşamanız, Onu her yönden hoşnut etmeniz, her iyi işte meyve vererek Tanrıyı tanımakta ilerlemeniz için dua ediyoruz."));
trsuperobject.biblemap.put("b51_1_11", new trsuperobject(51,1,11,"Her şeye sevinçle katlanıp sabredebilmeniz için Onun yüce gücüne dayanarak bütün kudretle güçlenmenizi diliyoruz."));
trsuperobject.biblemap.put("b51_1_12", new trsuperobject(51,1,12,"Bizi kutsalların ışıktaki mirasına ortak olmaya yeterli kılan Babaya şükretmeniz için dua ediyoruz."));
trsuperobject.biblemap.put("b51_1_13", new trsuperobject(51,1,13,"O bizi karanlığın hükümranlığından kurtarıp sevgili Oğlunun egemenliğine aktardı."));
trsuperobject.biblemap.put("b51_1_14", new trsuperobject(51,1,14,"Onda kurtuluşa, günahlarımızın bağışına sahibiz."));
trsuperobject.biblemap.put("b51_1_15", new trsuperobject(51,1,15,"Görünmez Tanrının görünümü, bütün yaratılışın ilk doğanı Odur."));
trsuperobject.biblemap.put("b51_1_16", new trsuperobject(51,1,16,"Nitekim yerde ve gökte, görünen ve görünmeyen her şey -tahtlar, egemenlikler, yönetimler, hükümranlıklar- Onda yaratıldı. Her şey Onun aracılığıyla ve Onun için yaratıldı."));
trsuperobject.biblemap.put("b51_1_17", new trsuperobject(51,1,17,"Her şeyden önce var olan Odur ve her şey varlığını Onda sürdürmektedir."));
trsuperobject.biblemap.put("b51_1_18", new trsuperobject(51,1,18,"Bedenin, yani kilisenin başı Odur. Her şeyde ilk yeri alsın diye başlangıç olan ve ölüler arasından ilk doğan Odur."));
trsuperobject.biblemap.put("b51_1_19", new trsuperobject(51,1,19,"Çünkü Tanrı bütün doluluğunun Onda bulunmasını uygun gördü."));
trsuperobject.biblemap.put("b51_1_20", new trsuperobject(51,1,20,"Mesihin çarmıhta akıtılan kanı aracılığıyla esenliği sağlamış olarak yerdeki ve gökteki her şeyi Onun aracılığıyla kendisiyle barıştırmaya razı oldu."));
trsuperobject.biblemap.put("b51_1_21", new trsuperobject(51,1,21,"Yaptığınız kötülükler yüzünden bir zamanlar düşüncelerinizde Tanrıya yabancı ve düşmandınız."));
trsuperobject.biblemap.put("b51_1_22", new trsuperobject(51,1,22,"Şimdiyse Mesih sizi Tanrının önüne kutsal, lekesiz ve kusursuz olarak çıkarmak için öz bedeninin ölümü sayesinde sizi Tanrıyla barıştırdı."));
trsuperobject.biblemap.put("b51_1_23", new trsuperobject(51,1,23,"Yeter ki, duyduğunuz Müjdenin verdiği umuttan kopmadan, imanda temellenip yerleşmiş olarak kalın. Ben Pavlus, göğün altındaki bütün yaratılışa duyurulan bu Müjdenin hizmetkârı oldum."));
trsuperobject.biblemap.put("b51_1_24", new trsuperobject(51,1,24,"Sizin için acı çektiğime şimdi seviniyorum. Mesihin, kendi bedeni, yani kilise uğruna çektiği sıkıntılardan eksik kalanlarını kendi bedenimde tamamlıyorum."));
trsuperobject.biblemap.put("b51_1_25-26", new trsuperobject(51,1,25-26,"Tanrının sizin yararınıza bana verdiği görevle kilisenin hizmetkârı oldum. Görevim, Tanrının sözünü, yani geçmiş çağlardan ve kuşaklardan gizlenmiş, ama şimdi Onun kutsallarına açıklanmış olan sırrı eksiksiz duyurmaktır."));
trsuperobject.biblemap.put("b51_1_27", new trsuperobject(51,1,27,"Tanrı kutsallarına bu sırrın uluslar arasında ne denli yüce ve zengin olduğunu bildirmek istedi. Bu sırrın özü şudur: Mesih içinizdedir. Bu da size yüceliğe kavuşma umudunu veriyor."));
trsuperobject.biblemap.put("b51_1_28", new trsuperobject(51,1,28,"Her insanı Mesihte yetkinleşmiş olarak Tanrıya sunmak için herkesi uyararak ve herkesi tam bir bilgelikle eğiterek Mesihi tanıtıyoruz."));
trsuperobject.biblemap.put("b51_1_29", new trsuperobject(51,1,29,"O'nun kudretle bende etkin olan gücüne dayanarak uğraşıp emek vermemin amacı da budur."));
trsuperobject.biblemap.put("b51_2_1", new trsuperobject(51,2,1,"Gerek sizler, gerek Laodikyadakiler, gerekse sizler gibi yüzümü hiç görmemiş olanlar için ne denli büyük bir uğraş verdiğimi bilmenizi isterim."));
trsuperobject.biblemap.put("b51_2_2-3", new trsuperobject(51,2,2-3,"Yüreklerinin cesaret bulmasını, sevgide birleşmelerini dilerim. Öyle ki, anlayışın verdiği tam güvenliğin bütün zenginliğine kavuşsunlar ve Tanrının sırrını, yani bilginin ve bilgeliğin bütün hazinelerinin saklı olduğu Mesihi tanısınlar."));
trsuperobject.biblemap.put("b51_2_4", new trsuperobject(51,2,4,"Kimse sizi kulağı okşayan sözlerle aldatmasın diye söylüyorum bunu."));
trsuperobject.biblemap.put("b51_2_5", new trsuperobject(51,2,5,"Çünkü her ne kadar bedence aranızda değilsem de, ruhça sizinle birlikteyim. Düzenliliğinizi, Mesihe imanınızın sağlamlığını görüp seviniyorum."));
trsuperobject.biblemap.put("b51_2_6", new trsuperobject(51,2,6,"Bu nedenle Rab Mesih İsayı nasıl kabul ettinizse, Onda öylece yaşayın."));
trsuperobject.biblemap.put("b51_2_7", new trsuperobject(51,2,7,"Şükranla dolup taşarak Onda köklenin ve gelişin, size öğretildiği gibi imanda güçlenin."));
trsuperobject.biblemap.put("b51_2_8", new trsuperobject(51,2,8,"Dikkatli olun! Mesihe değil de, insanların geleneğine, dünyanın temel ilkelerine dayanan felsefeyle, boş ve aldatıcı sözlerle kimse sizi tutsak etmesin."));
trsuperobject.biblemap.put("b51_2_9", new trsuperobject(51,2,9,"Çünkü Tanrılığın bütün doluluğu bedence Mesihte bulunuyor."));
trsuperobject.biblemap.put("b51_2_10", new trsuperobject(51,2,10,"Siz de her yönetim ve hükümranlığın başı olan Mesihte doluluğa kavuştunuz."));
trsuperobject.biblemap.put("b51_2_11", new trsuperobject(51,2,11,"Ayrıca Mesihin gerçekleştirdiği sünnet sayesinde bedenin benliğinden soyunarak elle yapılmayan sünnetle Onda sünnet edildiniz."));
trsuperobject.biblemap.put("b51_2_12", new trsuperobject(51,2,12,"Vaftizde Onunla birlikte gömüldünüz, Onu ölümden dirilten Tanrının gücüne iman ederek Onunla birlikte dirildiniz."));
trsuperobject.biblemap.put("b51_2_13", new trsuperobject(51,2,13,"Sizler suçlarınız ve benliğinizin sünnetsizliği yüzünden ölüyken, Tanrı sizi Mesihle birlikte yaşama kavuşturdu. Bütün suçlarımızı O bağışladı."));
trsuperobject.biblemap.put("b51_2_14", new trsuperobject(51,2,14,"Kurallarıyla bize karşı ve aleyhimizde olan yazılı antlaşmayı sildi, onu çarmıha çakarak ortadan kaldırdı."));
trsuperobject.biblemap.put("b51_2_15", new trsuperobject(51,2,15,"Yönetimlerin ve hükümranlıkların elindeki silahları alıp onları çarmıhta yenerek açıkça gözler önüne serdi."));
trsuperobject.biblemap.put("b51_2_16", new trsuperobject(51,2,16,"Bu nedenle kimse yiyecek içecek, bayram, yeni ay ya da Şabat Günü konusunda sizi yargılamasın."));
trsuperobject.biblemap.put("b51_2_17", new trsuperobject(51,2,17,"Bunlar gelecek şeylerin gölgesidir, aslı ise Mesihtedir."));
trsuperobject.biblemap.put("b51_2_18-19", new trsuperobject(51,2,18-19,"Sözde alçakgönüllülükte ve meleklere tapınmakta direnen, gördüğü düşlerin üzerinde durarak benliğin düşünceleriyle boş yere böbürlenen, Başa tutunmayan hiç kimse sizi ödülünüzden yoksun bırakmasın. Bütün beden eklemler ve bağlar yardımıyla bu Baştan beslenip bütünlenmekte, Tanrının sağladığı büyümeyle gelişmektedir."));
trsuperobject.biblemap.put("b51_2_2-021", new trsuperobject(51,2,2-021,"Mesihle birlikte ölüp dünyanın temel ilkelerinden kurtulduğunuza göre, niçin dünyada yaşayanlar gibi, ‹‹Şunu elleme››, ‹‹Bunu tatma››, ‹‹Şuna dokunma›› gibi kurallara uyuyorsunuz?"));
trsuperobject.biblemap.put("b51_2_22", new trsuperobject(51,2,22,"Bu kuralların hepsi, kullanıldıkça yok olacak nesnelerle ilgilidir; insanların buyruklarına, öğretilerine dayanır."));
trsuperobject.biblemap.put("b51_2_23", new trsuperobject(51,2,23,"Kuşkusuz bu kuralların gönüllü tapınma, sözde alçakgönüllülük, bedene eziyet açısından bilgece bir görünüşü vardır; ama benliğin tutkularını denetlemekte hiçbir yararları yoktur."));
trsuperobject.biblemap.put("b51_3_1", new trsuperobject(51,3,1,"Mesihle birlikte dirildiğinize göre, gökteki değerlerin ardından gidin. Mesih orada, Tanrının sağında oturuyor."));
trsuperobject.biblemap.put("b51_3_2", new trsuperobject(51,3,2,"Yeryüzündeki değil, gökteki değerleri düşünün."));
trsuperobject.biblemap.put("b51_3_3", new trsuperobject(51,3,3,"Çünkü siz öldünüz, yaşamınız Mesihle birlikte Tanrıda saklıdır."));
trsuperobject.biblemap.put("b51_3_4", new trsuperobject(51,3,4,"Yaşamınız olan Mesih göründüğü zaman, siz de Onunla birlikte yücelmiş olarak görüneceksiniz."));
trsuperobject.biblemap.put("b51_3_5", new trsuperobject(51,3,5,"Bu nedenle bedenin dünyasal eğilimlerini -fuhşu, pisliği, şehveti, kötü arzuları ve putperestlikle eş olan açgözlülüğü- öldürün."));
trsuperobject.biblemap.put("b51_3_6", new trsuperobject(51,3,6,"Bunlar yüzünden Tanrının gazabı söz dinlemeyenlerin üzerine geliyor."));
trsuperobject.biblemap.put("b51_3_7", new trsuperobject(51,3,7,"Geçmişte bunlarla iç içe yaşadığınız zaman siz de bu yollarda yürüdünüz."));
trsuperobject.biblemap.put("b51_3_8", new trsuperobject(51,3,8,"Ama şimdi öfke, kızgınlık, kötü niyet dahil, hepsini üzerinizden sıyırıp atın. Ağzınızdan hiçbir iftira ya da edepsiz söz çıkmasın."));
trsuperobject.biblemap.put("b51_3_9", new trsuperobject(51,3,9,"Birbirinize yalan söylemeyin. Çünkü eski yaradılışı kötü alışkanlıklarıyla birlikte üzerinizden çıkarıp attınız;"));
trsuperobject.biblemap.put("b51_3_10", new trsuperobject(51,3,10,"eksiksiz bilgiye erişmek için Yaratıcısına benzer olmak üzere yenilenen yeni yaradılışı giyindiniz."));
trsuperobject.biblemap.put("b51_3_11", new trsuperobject(51,3,11,"Bu yenilikte Grek ve Yahudi, sünnetli ve sünnetsiz, barbar, İskit, köle ve özgür ayrımı yoktur. Mesih her şeydir ve her şeydedir."));
trsuperobject.biblemap.put("b51_3_12", new trsuperobject(51,3,12,"Öyleyse, Tanrının kutsal ve sevgili seçilmişleri olarak yürekten sevecenliği, iyiliği, alçakgönüllülüğü, sabrı, yumuşaklığı giyinin."));
trsuperobject.biblemap.put("b51_3_13", new trsuperobject(51,3,13,"Birbirinize hoşgörülü davranın. Birinizin ötekinden bir şikâyeti varsa, Rabbin sizi bağışladığı gibi, siz de birbirinizi bağışlayın."));
trsuperobject.biblemap.put("b51_3_14", new trsuperobject(51,3,14,"Bunların hepsinin üzerine yetkin birliğin bağı olan sevgiyi giyinin."));
trsuperobject.biblemap.put("b51_3_15", new trsuperobject(51,3,15,"Mesihin esenliği yüreklerinizde hakem olsun. Tek bir bedenin üyeleri olarak bu esenliğe çağrıldınız. Şükredici olun!"));
trsuperobject.biblemap.put("b51_3_16", new trsuperobject(51,3,16,"Mesihin sözü bütün zenginliğiyle içinizde yaşasın. Tam bir bilgelikle birbirinize öğretin, öğüt verin, mezmurlar, ilahiler, ruhsal ezgiler söyleyerek yüreklerinizde şükranla Tanrıya nağmeler yükseltin."));
trsuperobject.biblemap.put("b51_3_17", new trsuperobject(51,3,17,"Söylediğiniz, yaptığınız her şeyi Rab İsanın adıyla, Onun aracılığıyla Baba Tanrıya şükrederek yapın."));
trsuperobject.biblemap.put("b51_3_18", new trsuperobject(51,3,18,"Ey kadınlar, Rabbe ait olanlara yaraşır biçimde kocalarınıza bağımlı olun."));
trsuperobject.biblemap.put("b51_3_19", new trsuperobject(51,3,19,"Ey kocalar, karılarınızı sevin. Onlara sert davranmayın."));
trsuperobject.biblemap.put("b51_3_20", new trsuperobject(51,3,20,"Ey çocuklar, her konuda anne babalarınızın sözünü dinleyin. Çünkü bu Rabbi hoşnut eder."));
trsuperobject.biblemap.put("b51_3_21", new trsuperobject(51,3,21,"Ey babalar, çocuklarınızı incitmeyin, yoksa cesaretleri kırılır."));
trsuperobject.biblemap.put("b51_3_22", new trsuperobject(51,3,22,"Ey köleler, dünyadaki efendilerinizin her sözünü dinleyin. Bunu, yalnız insanları hoşnut etmek isteyenler gibi göze hoş görünen hizmetle değil, saf yürekle, Rab korkusuyla yapın."));
trsuperobject.biblemap.put("b51_3_23-24", new trsuperobject(51,3,23-24,"Rabden miras ödülünü alacağınızı bilerek, her ne yaparsanız, insanlar için değil, Rab için yapar gibi candan yapın. Rab Mesihe kulluk ediyorsunuz."));
trsuperobject.biblemap.put("b51_3_25", new trsuperobject(51,3,25,"Haksızlık eden ettiği haksızlığın karşılığını alacak, hiçbir ayrım yapılmayacaktır."));
trsuperobject.biblemap.put("b51_4_1", new trsuperobject(51,4,1,"Ey efendiler, gökte sizin de bir Efendiniz olduğunu bilerek kölelerinize adalet ve eşitlikle davranın."));
trsuperobject.biblemap.put("b51_4_2", new trsuperobject(51,4,2,"Kendinizi duaya verin. Duada uyanık kalın, şükredin."));
trsuperobject.biblemap.put("b51_4_3", new trsuperobject(51,4,3,"Aynı zamanda bizim için de dua edin ki Tanrı, sözünü yaymamız ve uğruna hapsedildiğim Mesih sırrını açıklamamız için bize bir kapı açsın."));
trsuperobject.biblemap.put("b51_4_4", new trsuperobject(51,4,4,"Bu sırrı gerektiği gibi açıklıkla bildirebilmem için dua edin."));
trsuperobject.biblemap.put("b51_4_5", new trsuperobject(51,4,5,"Sizden olmayanlara karşı bilgece davranın. Fırsatı değerlendirin."));
trsuperobject.biblemap.put("b51_4_6", new trsuperobject(51,4,6,"Sözünüz tuzla terbiye edilmiş gibi her zaman lütufla dolu olsun. Böylece herkese nasıl karşılık vermek gerektiğini bileceksiniz."));
trsuperobject.biblemap.put("b51_4_7", new trsuperobject(51,4,7,"Rab yolunda emektaşım ve güvenilir bir hizmetkâr olan sevgili kardeşimiz Tihikos, benimle ilgili her şeyi size bildirecektir."));
trsuperobject.biblemap.put("b51_4_8", new trsuperobject(51,4,8,"İşte bu amaçla, durumumuzu iletmesi ve yüreklerinize cesaret vermesi için kendisini size gönderiyorum."));
trsuperobject.biblemap.put("b51_4_9", new trsuperobject(51,4,9,"Onunla birlikte, sizden biri olan, güvenilir ve sevgili kardeş Onisimosu da gönderiyorum. Burada olup biten her şeyi size bildirecekler."));
trsuperobject.biblemap.put("b51_4_10", new trsuperobject(51,4,10,"Hapishane arkadaşım Aristarhus ve Barnabanın yeğeni Markos size selam ederler. Markosla ilgili buyruklar aldınız; yanınıza gelirse kendisini kabul edin."));
trsuperobject.biblemap.put("b51_4_11", new trsuperobject(51,4,11,"Yustus diye tanınan Yeşu da size selam eder. Tanrının Egemenliği için çalışan emektaşım Yahudiler yalnız bunlardır. Bunlar bana teselli oldular."));
trsuperobject.biblemap.put("b51_4_12", new trsuperobject(51,4,12,"Sizden biri ve Mesih İsanın kulu olan Epafras size selam eder. Tanrının her isteğinden emin, yetkin kişiler olarak ayakta kalasınız diye sizin için her zaman duayla mücadele ediyor."));
trsuperobject.biblemap.put("b51_4_13", new trsuperobject(51,4,13,"Gerek sizin gerekse Laodikya ve Hierapolistekiler için çok emek verdiğine tanıklık ederim."));
trsuperobject.biblemap.put("b51_4_14", new trsuperobject(51,4,14,"Sevgili hekim Lukayla Dimas da size selam ederler."));
trsuperobject.biblemap.put("b51_4_15", new trsuperobject(51,4,15,"Laodikyadaki kardeşlere, Nimfaya ve evindeki topluluğa selam edin."));
trsuperobject.biblemap.put("b51_4_16", new trsuperobject(51,4,16,"Bu mektup aranızda okunduktan sonra Laodikya kilisesine de okutun. Siz de Laodikyadan gelecek mektubu okuyun."));
trsuperobject.biblemap.put("b51_4_17", new trsuperobject(51,4,17,"Arhippusa, ‹‹Rab yolunda üstlendiğin görevi tamamlamaya dikkat et!›› deyin."));
trsuperobject.biblemap.put("b51_4_18", new trsuperobject(51,4,18,"Ben Pavlus bu selamı elimle yazıyorum. Zincire vurulduğumu unutmayın. Tanrı'nın lütfu sizinle birlikte olsun."));

}

