trsuperobject.biblemap.put("b52_4_16", new trsuperobject(52,4,16,"Rabbin kendisi, bir emir çağrısıyla, başmeleğin seslenmesiyle, Tanrının borazanıyla gökten inecek. Önce Mesihe ait ölüler dirilecek."));
