public class trbible53 {

public trbible53() {

putmapbible53();

}

private void putmapbible53() {

trsuperobject.biblemap.put("b53_1_1", new trsuperobject(53,1,1,"Pavlus, Silvanus ve Timoteostan Babamız Tanrıya ve Rab İsa Mesihe ait Selanik kilisesine selam!"));
trsuperobject.biblemap.put("b53_1_2", new trsuperobject(53,1,2,"Baba Tanrıdan ve Rab İsa Mesihten sizlere lütuf ve esenlik olsun."));
trsuperobject.biblemap.put("b53_1_3", new trsuperobject(53,1,3,"Kardeşler, sizin için her zaman Tanrıya şükran borçluyuz. Böyle yapmamız da yerindedir. Çünkü imanınız büyüdükçe büyüyor, her birinizin öbürüne olan sevgisi artıyor."));
trsuperobject.biblemap.put("b53_1_4", new trsuperobject(53,1,4,"Bu nedenle bizler, katlandığınız bütün zulüm ve sıkıntılar karşısındaki sabır ve imanınızdan ötürü Tanrının kiliseleri arasında sizinle övünüyoruz."));
trsuperobject.biblemap.put("b53_1_5", new trsuperobject(53,1,5,"Bütün bunlar Tanrının adil yargısının belirtisidir. Sonuç olarak, uğrunda acı çektiğiniz Tanrı Egemenliğine layık sayılacaksınız."));
trsuperobject.biblemap.put("b53_1_6-8", new trsuperobject(53,1,6-8,"Tanrı adil olanı yapacak: Size sıkıntı çektirenlere sıkıntı ile karşılık verecek, sıkıntı çeken sizleriyse bizimle birlikte rahata kavuşturacaktır. Bütün bunlar Rab İsa alev alev yanan ateş içinde güçlü melekleriyle gökten gelip göründüğü zaman olacak. Rabbimiz İsa, Tanrıyı tanımayanları ve kendisiyle ilgili Müjdeye uymayanları cezalandıracak."));
trsuperobject.biblemap.put("b53_1_9-10", new trsuperobject(53,1,9-10,"Böyleleri Rabbin varlığından ve yüce gücünden uzak kalarak sonsuza dek mahvolma cezasına çarptırılacaklar. Bütün bunlar Rabbin kendi kutsalları arasında yüceltilmek ve bütün imanlılarda hayranlık uyandırmak üzere geldiği gün olacak. Sizler ise iman edenlerdensiniz. Çünkü size ettiğimiz tanıklığa inandınız."));
trsuperobject.biblemap.put("b53_1_11", new trsuperobject(53,1,11,"İşte bu nedenle Tanrımız sizi çağrısına layık görsün, iyiliğe yönelik her dileğinizi, imana dayanan her uğraşınızı kendi gücüyle sonuçlandırsın diye sizin için her zaman dua ediyoruz."));
trsuperobject.biblemap.put("b53_1_12", new trsuperobject(53,1,12,"Öyle ki, Tanrımız'ın ve Rab İsa Mesih'in lütfuyla Rabbimiz İsa'nın adı sizde yüceltilsin, siz de O'nda yüceltilesiniz."));
trsuperobject.biblemap.put("b53_2_1-2", new trsuperobject(53,2,1-2,"Rabbimiz İsa Mesihin gelişine ve Onunla birlikte olmak üzere toplanmamıza gelince: Kardeşler, size rica ediyoruz, Rabbin gününün geldiğini ileri süren herhangi bir ruh, bir söz ya da bizden gelmiş gibi gösterilen bir mektup hemen aklınızı karıştırmasın, sizi telaşlandırmasın."));
trsuperobject.biblemap.put("b53_2_3", new trsuperobject(53,2,3,"Hiç kimse hiçbir şekilde sizi aldatmasın. Çünkü imandan dönüş başlamadıkça, mahvolacak olan o yasa tanımaz adam ortaya çıkmadıkça o gün gelmeyecektir."));
trsuperobject.biblemap.put("b53_2_4", new trsuperobject(53,2,4,"Bu adam, tanrı diye anılan ya da tapılan her şeye karşı gelerek kendini hepsinden yüce gösterecek, hatta kendisini Tanrı ilan ederek Tanrının Tapınağında oturacaktır."));
trsuperobject.biblemap.put("b53_2_5", new trsuperobject(53,2,5,"Daha yanınızdayken bunları size söylediğimi hatırlamıyor musunuz?"));
trsuperobject.biblemap.put("b53_2_6", new trsuperobject(53,2,6,"Zamanı gelince ortaya çıkarılacak olan bu adamı şimdilik neyin engellediğini biliyorsunuz."));
trsuperobject.biblemap.put("b53_2_7", new trsuperobject(53,2,7,"Evet, yasa tanımazlığın gizli gücü şu anda bile etkindir; ama bu gücü şimdilik engelleyen ortadan kaldırılıncaya dek görevini sürdürecektir."));
trsuperobject.biblemap.put("b53_2_8", new trsuperobject(53,2,8,"Sonra yasa tanımaz adam ortaya çıkacak. Rab İsa onu ağzının soluğuyla öldürecek, gelişinin görkemiyle yok edecek."));
trsuperobject.biblemap.put("b53_2_9-10", new trsuperobject(53,2,9-10,"Yasa tanımaz adam, her türlü mucizede, yanıltıcı belirtilerle harikalarda ve mahvolanları aldatan her türlü kötülükte sergilenen Şeytanın etkinliğiyle gelecek. Mahvolanlar, gerçeği sevmeye ve böylece kurtulmaya yanaşmadıklarından mahvoluyorlar."));
trsuperobject.biblemap.put("b53_2_11", new trsuperobject(53,2,11,"İşte bu nedenle Tanrı yalana kanmaları için onların üzerine yanıltıcı bir güç gönderiyor."));
trsuperobject.biblemap.put("b53_2_12", new trsuperobject(53,2,12,"Öyle ki, gerçeğe inanmayan ve kötülükten hoşlananların hepsi yargılansın."));
trsuperobject.biblemap.put("b53_2_13", new trsuperobject(53,2,13,"Ama biz, ey Rabbin sevdiği kardeşler, sizler için her zaman Tanrıya şükran borçluyuz. Çünkü Tanrı, Ruh aracılığıyla kutsal kılınıp gerçeğe inanarak kurtulmanız için sizi ta başlangıçtan seçti."));
trsuperobject.biblemap.put("b53_2_14", new trsuperobject(53,2,14,"Rabbimiz İsa Mesihin yüceliğine kavuşmanız için, bildirdiğimiz Müjdeyle sizi bu kurtuluşa çağırdı."));
trsuperobject.biblemap.put("b53_2_15", new trsuperobject(53,2,15,"Öyleyse dayanın, kardeşlerim! İster sözle ister mektupla, size ilettiğimiz öğretilere sımsıkı tutunun."));
trsuperobject.biblemap.put("b53_2_16", new trsuperobject(53,2,16,"Rabbimiz İsa Mesih'in kendisi ve bizi sevip lütfuyla bize sonsuz cesaret ve sağlam bir umut veren Babamız Tanrı sizi yüreklendirsin, her iyi eylem ve sözde pekiştirsin."));
trsuperobject.biblemap.put("b53_3_1", new trsuperobject(53,3,1,"Son olarak, kardeşler, Rabbin sözü aranızda olduğu gibi hızla yayılıp yüceltilsin diye bizim için dua edin."));
trsuperobject.biblemap.put("b53_3_2", new trsuperobject(53,3,2,"Ahlaksız, kötü insanlardan kurtulmamız için de dua edin. Çünkü herkes iman etmiş değildir."));
trsuperobject.biblemap.put("b53_3_3", new trsuperobject(53,3,3,"Ama Rab güvenilirdir. O sizi pekiştirecek, kötü olandan koruyacaktır."));
trsuperobject.biblemap.put("b53_3_4", new trsuperobject(53,3,4,"Buyurduklarımızı yaptığınıza ve yapacağınıza dair Rabde size güveniyoruz."));
trsuperobject.biblemap.put("b53_3_5", new trsuperobject(53,3,5,"Rab yüreklerinizi Tanrının sevgisine, Mesihin sabrına yöneltsin."));
trsuperobject.biblemap.put("b53_3_6", new trsuperobject(53,3,6,"Kardeşler, bizden aldıkları öğretilere uymayıp boş gezen bütün kardeşlerden uzak durmanızı Rab İsa Mesihin adıyla buyuruyoruz."));
trsuperobject.biblemap.put("b53_3_7", new trsuperobject(53,3,7,"Bizleri nasıl örnek almanız gerektiğini kendiniz biliyorsunuz. Çünkü biz aranızdayken boş gezenler değildik."));
trsuperobject.biblemap.put("b53_3_8", new trsuperobject(53,3,8,"Kimsenin ekmeğini karşılıksız yemedik. Herhangi birinize yük olmamak için uğraşıp didindik, gece gündüz çalıştık."));
trsuperobject.biblemap.put("b53_3_9", new trsuperobject(53,3,9,"Yardımlarınızı hak etmediğimiz için değil, izleyebileceğiniz bir örnek bırakmak için böyle yaptık."));
trsuperobject.biblemap.put("b53_3_10", new trsuperobject(53,3,10,"Hatta sizinle birlikteyken şu buyruğu vermiştik: ‹‹Çalışmak istemeyen yemek de yemesin!››"));
trsuperobject.biblemap.put("b53_3_11", new trsuperobject(53,3,11,"Çünkü aranızda bazılarının boş gezdiğini duyuyoruz. Bunlar hiçbir iş yapmıyor, başkalarının işine karışıp duruyorlarmış."));
trsuperobject.biblemap.put("b53_3_12", new trsuperobject(53,3,12,"Böylelerine Rab İsa Mesih adına yalvarıyor, şunu buyuruyoruz: Sakin bir şekilde çalışıp kendi kazançlarından yesinler."));
trsuperobject.biblemap.put("b53_3_13", new trsuperobject(53,3,13,"Sizlerse kardeşler, iyilik yapmaktan usanmayın."));
trsuperobject.biblemap.put("b53_3_14", new trsuperobject(53,3,14,"Eğer bu mektuptaki sözlerimize uymayan olursa onu mimleyin. Yaptıklarından utanması için onunla ilişkinizi kesin."));
trsuperobject.biblemap.put("b53_3_15", new trsuperobject(53,3,15,"Yine de onu düşman saymayın, bir kardeş olarak uyarın."));
trsuperobject.biblemap.put("b53_3_16", new trsuperobject(53,3,16,"Esenlik kaynağı olan Rabbin kendisi size her zaman, her durumda esenlik versin. Rab hepinizle birlikte olsun."));
trsuperobject.biblemap.put("b53_3_17", new trsuperobject(53,3,17,"Ben Pavlus bu selamı kendi elimle yazıyorum. Her mektubumun özel işaretidir bu; böyle yazarım."));
trsuperobject.biblemap.put("b53_3_18", new trsuperobject(53,3,18,"Rabbimiz İsa Mesih'in lütfu hepinizle birlikte olsun."));

}

}