package tr.bible;

public class trbible55 {

public trbible55() {

putmapbible55();

}

private void putmapbible55() {

trsuperobject.biblemap.put("b55_1_1-2", new trsuperobject(55,1,1-2,"Mesih İsadaki yaşam vaadi uyarınca Tanrının isteğiyle Mesih İsanın elçisi atanan ben Pavlustan sevgili oğlum Timoteosa selam! Baba Tanrıdan ve Rabbimiz Mesih İsadan sana lütuf, merhamet ve esenlik olsun."));
trsuperobject.biblemap.put("b55_1_3", new trsuperobject(55,1,3,"Durmadan, gece gündüz dualarımda seni anarak atalarım gibi temiz vicdanla kulluk ettiğim Tanrıya şükrediyorum."));
trsuperobject.biblemap.put("b55_1_4", new trsuperobject(55,1,4,"Gözyaşlarını anımsıyor, sevinçle dolmak için seni görmeyi özlemle bekliyorum."));
trsuperobject.biblemap.put("b55_1_5", new trsuperobject(55,1,5,"Sendeki içten imanı anımsıyorum. Önce büyükannen Loisin ve annen Evnikinin sahip olduğu imana şimdi senin de sahip olduğuna eminim."));
trsuperobject.biblemap.put("b55_1_6", new trsuperobject(55,1,6,"Bu nedenle, ellerimi senin üzerine koymamla Tanrının sana verdiği armağanı alevlendirmen gerektiğini hatırlatıyorum."));
trsuperobject.biblemap.put("b55_1_7", new trsuperobject(55,1,7,"Çünkü Tanrı bize korkaklık ruhu değil, güç, sevgi ve özdenetim ruhu vermiştir."));
trsuperobject.biblemap.put("b55_1_8", new trsuperobject(55,1,8,"Bunun için Rabbimize tanıklık etmekten de Onun uğruna tutuklu bulunan benden de utanma. Tanrının gücüyle Müjde uğruna benimle birlikte sıkıntıya göğüs ger."));
trsuperobject.biblemap.put("b55_1_9-10", new trsuperobject(55,1,9-10,"Tanrı bizi yaptıklarımıza göre değil, kendi amacına ve lütfuna göre kurtarıp kutsal bir yaşama çağırdı. Bu lütuf bize zamanın başlangıcından önce Mesih İsada bağışlanmış, şimdi de Onun gelişiyle açığa çıkarılmıştır. Kurtarıcımız Mesih İsa ölümü etkisiz kılmış, yaşamı ve ölümsüzlüğü Müjde aracılığıyla ışığa çıkarmıştır."));
trsuperobject.biblemap.put("b55_1_11", new trsuperobject(55,1,11,"Ben Müjdenin habercisi, elçisi ve öğretmeni atandım."));
trsuperobject.biblemap.put("b55_1_12", new trsuperobject(55,1,12,"Bu acıları çekmemin nedeni de budur. Ama bundan utanmıyorum. Çünkü kime inandığımı biliyorum. Onun bana emanet ettiğini o güne dek koruyacak güçte olduğuna eminim."));
trsuperobject.biblemap.put("b55_1_13", new trsuperobject(55,1,13,"Benden işitmiş olduğun doğru sözleri örnek alarak imanla ve Mesih İsada olan sevgiyle bunlara bağlı kal."));
trsuperobject.biblemap.put("b55_1_14", new trsuperobject(55,1,14,"Sana emanet edilen iyi öğretileri içimizde yaşayan Kutsal Ruh aracılığıyla koru."));
trsuperobject.biblemap.put("b55_1_15", new trsuperobject(55,1,15,"Biliyorsun, Asya İlindekilerin hepsi beni terk edip gittiler. Figelosla Hermogenis de bunlardandır."));
trsuperobject.biblemap.put("b55_1_16", new trsuperobject(55,1,16,"Rab, Onisiforosun ev halkına merhamet etsin. Çünkü o çok kez içimi ferahlattı ve zincire vurulmuş olmamdan utanmadı."));
trsuperobject.biblemap.put("b55_1_17", new trsuperobject(55,1,17,"Tersine, Romaya geldiğinde beni gayretle arayıp buldu."));
trsuperobject.biblemap.put("b55_1_18", new trsuperobject(55,1,18,"O gün Rab'den merhamet bulmasını dilerim. Efes'te onun bana ne kadar hizmet ettiğini sen de çok iyi bilirsin."));
trsuperobject.biblemap.put("b55_2_1", new trsuperobject(55,2,1,"Oğlum, Mesih İsada olan lütufla güçlen."));
trsuperobject.biblemap.put("b55_2_2", new trsuperobject(55,2,2,"Birçok tanık önünde benden işittiğin sözleri, başkalarına da öğretmeye yeterli olacak güvenilir kişilere emanet et."));
trsuperobject.biblemap.put("b55_2_3", new trsuperobject(55,2,3,"Mesih İsanın iyi bir askeri olarak benimle birlikte sıkıntıya göğüs ger."));
trsuperobject.biblemap.put("b55_2_4", new trsuperobject(55,2,4,"Askerlik yapan kişi günlük yaşamla ilgili işlere karışmaz; kendisini askerliğe çağıranı hoşnut etmeye çalışır."));
trsuperobject.biblemap.put("b55_2_5", new trsuperobject(55,2,5,"Bunun gibi, spor yarışmasına katılan kişi de kurallar uyarınca yarışmazsa zafer tacını giyemez."));
trsuperobject.biblemap.put("b55_2_6", new trsuperobject(55,2,6,"Emek veren çiftçi üründen ilk payı alan kişi olmalıdır."));
trsuperobject.biblemap.put("b55_2_7", new trsuperobject(55,2,7,"Dediklerimi iyi düşün. Rab sana her konuda anlayış verecektir."));
trsuperobject.biblemap.put("b55_2_8", new trsuperobject(55,2,8,"Yaydığım Müjdede açıklandığı gibi, Davutun soyundan olup ölümden dirilmiş olan İsa Mesihi anımsa."));
trsuperobject.biblemap.put("b55_2_9", new trsuperobject(55,2,9,"Bu Müjde uğruna bir suçlu gibi zincire vurulmaya kadar varan sıkıntılara katlanıyorum. Ama Tanrının sözü zincire vurulmuş değildir."));
trsuperobject.biblemap.put("b55_2_10", new trsuperobject(55,2,10,"Bunun içindir ki, seçilmişler uğruna her şeye dayanıyorum. Öyle ki, onlar da sonsuz yüceliğin yanısıra Mesih İsada olan kurtuluşa kavuşsunlar."));
trsuperobject.biblemap.put("b55_2_11", new trsuperobject(55,2,11,"Şu güvenilir bir sözdür:  ‹‹Onunla birlikte öldüysek,  Onunla birlikte yaşayacağız."));
trsuperobject.biblemap.put("b55_2_12", new trsuperobject(55,2,12,"Dayanırsak,  Onunla birlikte egemenlik süreceğiz.  Onu inkâr edersek,  O da bizi inkâr edecek."));
trsuperobject.biblemap.put("b55_2_13", new trsuperobject(55,2,13,"Biz sadık kalmasak da,  O sadık kalacak.  Çünkü kendi özüne aykırı davranamaz.››"));
trsuperobject.biblemap.put("b55_2_14", new trsuperobject(55,2,14,"Bu konuları imanlılara anımsat. Dinleyenleri felakete sürüklemekten başka yararı olmayan kelime kavgaları çıkarmamaları için onları Tanrının önünde uyar."));
trsuperobject.biblemap.put("b55_2_15", new trsuperobject(55,2,15,"Kendini Tanrıya makbul, gerçeğin bildirisini doğru kullanan, alnı ak bir işçi olarak sunmaya gayret et."));
trsuperobject.biblemap.put("b55_2_16", new trsuperobject(55,2,16,"Bayağı, boş sözlerden sakın. Çünkü bunlara dalanlar tanrısızlıkta daha da ileri gidecekler."));
trsuperobject.biblemap.put("b55_2_17", new trsuperobject(55,2,17,"Sözleri kangren gibi yayılacak. Himeneosla Filitos bunlardandır."));
trsuperobject.biblemap.put("b55_2_18", new trsuperobject(55,2,18,"Diriliş olup bitti diyerek gerçek yoldan saptılar. Şimdi de bazılarının imanını altüst ediyorlar."));
trsuperobject.biblemap.put("b55_2_19", new trsuperobject(55,2,19,"Ne var ki, Tanrının attığı sağlam temel, ‹‹Rab kendine ait olanları bilir›› ve ‹‹Rabbin adını anan herkes kötülükten uzak dursun›› sözleriyle mühürlenmiş olarak duruyor."));
trsuperobject.biblemap.put("b55_2_20", new trsuperobject(55,2,20,"Büyük bir evde yalnız altın ve gümüş kaplar bulunmaz; tahta ve toprak kaplar da vardır. Kimi onurlu, kimi bayağı iş için kullanılır."));
trsuperobject.biblemap.put("b55_2_21", new trsuperobject(55,2,21,"Bunun gibi, kişi de kendini bayağı işlerden arıtırsa, onurlu amaçlara uygun, kutsal kılınmış, efendisine yararlı, her iyi işe hazır bir kap olur."));
trsuperobject.biblemap.put("b55_2_22", new trsuperobject(55,2,22,"Gençlik arzularından kaç. Temiz yürekle Rabbe yakaranlarla birlikte doğruluğun, imanın, sevginin ve esenliğin ardından koş."));
trsuperobject.biblemap.put("b55_2_23", new trsuperobject(55,2,23,"Saçma, cahilce tartışmalara girmeyi reddet. Bunların kavga doğurduğunu bilirsin."));
trsuperobject.biblemap.put("b55_2_24", new trsuperobject(55,2,24,"Rabbin kulu kavgacı olmamalı. Tersine, herkese şefkatle davranmalı, öğretme yeteneği olmalı, haksızlıklara sabırla dayanmalıdır."));
trsuperobject.biblemap.put("b55_2_25", new trsuperobject(55,2,25,"Kendisine karşı olanları yumuşak huyla yola getirmeli. Gerçeği anlamaları için Tanrı belki onlara bir tövbe yolu açar."));
trsuperobject.biblemap.put("b55_2_26", new trsuperobject(55,2,26,"Böylelikle ayılabilir, isteğini yerine getirmeleri için kendilerini tutsak eden İblis'in tuzağından kurtulabilirler."));
trsuperobject.biblemap.put("b55_3_1", new trsuperobject(55,3,1,"Şunu bil ki, son günlerde çetin anlar olacaktır."));
trsuperobject.biblemap.put("b55_3_2-3", new trsuperobject(55,3,2-3,"İnsanlar kendilerini seven, para düşkünü, övüngen, kibirli, küfürbaz, anne baba sözü dinlemez, nankör, kutsallıktan ve sevgiden yoksun, uzlaşmaz, iftiracı, özünü denetleyemeyen, azgın, iyilik düşmanı olacaklar."));
trsuperobject.biblemap.put("b55_3_4-5", new trsuperobject(55,3,4-5,"Hain, aceleci, kendini beğenmiş, Tanrıdan çok eğlenceyi seven, Tanrı yolundaymış gibi görünüp bu yolun gücünü inkâr edenler olacaklar. Böylelerinden uzak dur."));
trsuperobject.biblemap.put("b55_3_6-7", new trsuperobject(55,3,6-7,"Bunların arasında evlerin içine sokulup günahla yüklü, çeşitli arzularla sürüklenen, her zaman öğrenen, ama gerçeğin bilgisine bir türlü erişemeyen zayıf iradeli kadınları adeta tutsak eden adamlar var."));
trsuperobject.biblemap.put("b55_3_8", new trsuperobject(55,3,8,"Yannisle Yambris nasıl Musaya karşı geldilerse, bunlar da gerçeğe karşı gelirler. Düşünceleri yozlaşmış, iman konusunda reddedilmiş insanlardır."));
trsuperobject.biblemap.put("b55_3_9", new trsuperobject(55,3,9,"Ama daha ileri gidemeyecekler. Çünkü Yannisle Yambris örneğindeki gibi, bunların da akılsızlığını herkes açıkça görecektir."));
trsuperobject.biblemap.put("b55_3_1-011", new trsuperobject(55,3,1-011,"Sense benim öğretimi, davranışımı, amacımı, imanımı, sabrımı, sevgimi, dayanma gücümü, çektiğim zulüm ve acıları, örneğin Antakyada, Konyada ve Listrada başıma gelenleri yakından izledin. Ne zulümlere katlandım! Ama Rab beni hepsinden kurtardı."));
trsuperobject.biblemap.put("b55_3_12", new trsuperobject(55,3,12,"Mesih İsaya ait olup Tanrı yoluna yaraşır bir yaşam sürmek isteyenlerin hepsi zulüm görecek."));
trsuperobject.biblemap.put("b55_3_13", new trsuperobject(55,3,13,"Ama kötüler ve sahtekârlar, aldatarak ve aldanarak gittikçe daha beter olacaklar."));
trsuperobject.biblemap.put("b55_3_14-15", new trsuperobject(55,3,14-15,"Sense öğrendiğin ve güvendiğin ilkelere bağlı kal. Çünkü bunları kimlerden öğrendiğini biliyorsun. Mesih İsaya iman aracılığıyla seni bilge kılıp kurtuluşa kavuşturacak güçte olan Kutsal Yazıları da çocukluğundan beri biliyorsun."));
trsuperobject.biblemap.put("b55_3_16", new trsuperobject(55,3,16,"Kutsal Yazıların tümü Tanrı esinlemesidir ve öğretmek, azarlamak, yola getirmek, doğruluk konusunda eğitmek için yararlıdır."));
trsuperobject.biblemap.put("b55_3_17", new trsuperobject(55,3,17,"Bunlar sayesinde Tanrı adamı her iyi iş için donatılmış olarak yetkin olur."));
trsuperobject.biblemap.put("b55_4_1-2", new trsuperobject(55,4,1-2,"Tanrının ve dirilerle ölüleri yargılayacak olan Mesih İsanın önünde, Onun gelişi ve egemenliği hakkı için sana buyuruyorum: Tanrı sözünü duyur. Zaman uygun olsun olmasın, bu görevi sürdür. İnsanları tam bir sabırla eğiterek ikna et, uyar, isteklendir."));
trsuperobject.biblemap.put("b55_4_3", new trsuperobject(55,4,3,"Çünkü öyle bir zaman gelecek ki, sağlam öğretiye katlanamayacaklar. Kulaklarını okşayan sözler duymak için çevrelerine kendi arzularına uygun öğretmenler toplayacaklar."));
trsuperobject.biblemap.put("b55_4_4", new trsuperobject(55,4,4,"Kulaklarını gerçeğe tıkayıp masallara sapacaklar."));
trsuperobject.biblemap.put("b55_4_5", new trsuperobject(55,4,5,"Ama sen her durumda ayık ol, sıkıntıya göğüs ger, müjdeci olarak işini yap, görevini tamamla."));
trsuperobject.biblemap.put("b55_4_6", new trsuperobject(55,4,6,"Çünkü kanım adak şarabı gibi dökülmek üzere. Benim için ayrılma zamanı geldi."));
trsuperobject.biblemap.put("b55_4_7", new trsuperobject(55,4,7,"Yüce mücadeleyi sürdürdüm, yarışı bitirdim, imanı korudum."));
trsuperobject.biblemap.put("b55_4_8", new trsuperobject(55,4,8,"Bundan böyle doğruluk tacı benim için hazır duruyor. Adil yargıç olan Rab o gün bu tacı bana, yalnız bana değil, Onun gelişini özlemle beklemiş olanların hepsine verecektir."));
trsuperobject.biblemap.put("b55_4_9", new trsuperobject(55,4,9,"Yanıma tez gelmeye gayret et."));
trsuperobject.biblemap.put("b55_4_10", new trsuperobject(55,4,10,"Çünkü Dimas bu dünyayı sevdiği için beni terk edip Selanike gitti. Kriskis Galatyaya, Titus Dalmaçyaya gitti."));
trsuperobject.biblemap.put("b55_4_11", new trsuperobject(55,4,11,"Yanımda yalnız Luka var. Markosu alıp beraberinde getir, yapacağım hizmette bana yardım eder."));
trsuperobject.biblemap.put("b55_4_12", new trsuperobject(55,4,12,"Tihikosu Efese gönderdim."));
trsuperobject.biblemap.put("b55_4_13", new trsuperobject(55,4,13,"Troasta Karpın yanında bıraktığım abayı, kitapları, özellikle yazı derilerini gelirken beraberinde getir."));
trsuperobject.biblemap.put("b55_4_14", new trsuperobject(55,4,14,"Bakırcı İskender bana çok kötülük etti. Rab ona yaptıklarının karşılığını verecektir."));
trsuperobject.biblemap.put("b55_4_15", new trsuperobject(55,4,15,"Sen de ondan sakın. Çünkü söylediklerimize şiddetle karşı koydu."));
trsuperobject.biblemap.put("b55_4_16", new trsuperobject(55,4,16,"İlk savunmamda benden yana çıkan olmadı, hepsi beni terk etti. Bunun hesabı onlardan sorulmasın."));
trsuperobject.biblemap.put("b55_4_17", new trsuperobject(55,4,17,"Ama Tanrı bildirisi aracılığımla tam olarak açıklansın, bütün uluslar bunu duysun diye Rab yardımıma gelip beni güçlendirdi. Aslanın ağzından böyle kurtuldum!"));
trsuperobject.biblemap.put("b55_4_18", new trsuperobject(55,4,18,"Rab beni her kötülükten kurtarıp güvenlik içinde göksel egemenliğine ulaştıracak. Sonsuzlara dek Ona yücelik olsun! Amin."));
trsuperobject.biblemap.put("b55_4_19", new trsuperobject(55,4,19,"Priska, Akvila ve Onisiforosun ev halkına selam söyle."));
trsuperobject.biblemap.put("b55_4_20", new trsuperobject(55,4,20,"Erastus, Korintte kaldı. Trofimosu da Milette hasta bıraktım."));
trsuperobject.biblemap.put("b55_4_21", new trsuperobject(55,4,21,"Kış bastırmadan gelmeye gayret et. Evvulus, Pudens, Linus, Klavdiya ve bütün kardeşler sana selam ederler."));
trsuperobject.biblemap.put("b55_4_22", new trsuperobject(55,4,22,"Rab ruhunla birlikte olsun. Tanrı'nın lütfu sizlerle olsun."));

}

}