package tr.bible;

public class trbible57 {

public trbible57() {

putmapbible57();

}

private void putmapbible57() {

trsuperobject.biblemap.put("b57_1_1-2", new trsuperobject(57,1,1-2,"Sevgili emektaşımız Filimon, Mesih İsa uğruna tutuklu olan ben Pavlus ve kardeşimiz Timoteostan sana, kızkardeşimiz Afiyaya, birlikte mücadele verdiğimiz Arhippusa ve senin evindeki inanlılar topluluğuna selam!"));
trsuperobject.biblemap.put("b57_1_3", new trsuperobject(57,1,3,"Babamız Tanrıdan ve Rab İsa Mesihten sizlere lütuf ve esenlik olsun."));
trsuperobject.biblemap.put("b57_1_4-5", new trsuperobject(57,1,4-5,"Rab İsaya olan imanını ve bütün kutsallara beslediğin sevgiyi duydukça dualarımda seni anıyor, Tanrıma sürekli şükrediyorum."));
trsuperobject.biblemap.put("b57_1_6", new trsuperobject(57,1,6,"Mesihte sahip olduğumuz her iyiliğin bilincine vararak imanını başkalarıyla paylaşmakta etkin olman için dua ediyorum."));
trsuperobject.biblemap.put("b57_1_7", new trsuperobject(57,1,7,"Sevgin benim için büyük sevinç ve teselli kaynağı oldu. Çünkü kutsalların yürekleri senin sayende ferahladı, kardeşim."));
trsuperobject.biblemap.put("b57_1_8-9", new trsuperobject(57,1,8-9,"Bu nedenle, gerekeni sana buyurmaya Mesihte büyük cesaretim olduğu halde, şimdi Mesih İsa uğruna tutuklu bulunan ben yaşlı Pavlus sana sevgiyle rica etmeyi yeğliyorum."));
trsuperobject.biblemap.put("b57_1_1-011", new trsuperobject(57,1,1-011,"Tutukluluğum sırasında kendisine ruhsal baba olduğum oğlum Onisimosla ilgili bir ricam var. Bir zamanlar sana yararsızdı; ama şimdi hem sana hem de bana yararlıdır."));
trsuperobject.biblemap.put("b57_1_12", new trsuperobject(57,1,12,"Kendisini, yani can ciğerimi sana geri gönderiyorum."));
trsuperobject.biblemap.put("b57_1_13-14", new trsuperobject(57,1,13-14,"Müjdenin uğruna tutuklu kaldığım sürece senin yerine bana hizmet etmesi için onu yanımda alıkoymak isterdim; ama senin onayın olmadan bir şey yapmak istemedim. Öyle ki, yapacağın iyilik zorunluluktanmış gibi görünmesin, gönülden olsun."));
trsuperobject.biblemap.put("b57_1_15", new trsuperobject(57,1,15,"Onisimosun bir süre senden ayrılması belki de onu temelli geri alman içindi."));
trsuperobject.biblemap.put("b57_1_16", new trsuperobject(57,1,16,"Onu artık köle değil, köleden üstün, sevgili bir kardeş olarak geri alacaksın. O, özellikle benim için çok değerlidir. Ama hem bir insan, hem de Rabbe ait biri olarak senin için daha da çok sevilecek bir kardeştir."));
trsuperobject.biblemap.put("b57_1_17", new trsuperobject(57,1,17,"Bu nedenle, eğer beni yoldaşın sayıyorsan, kendisini beni kabul eder gibi kabul et."));
trsuperobject.biblemap.put("b57_1_18", new trsuperobject(57,1,18,"Sana herhangi bir haksızlık etmişse ya da bir borcu varsa, bunu benim hesabıma say."));
trsuperobject.biblemap.put("b57_1_19", new trsuperobject(57,1,19,"Ben Pavlus bunu kendi elimle yazıyorum, bedelini ben öderim. Senin kendi yaşamını bile bana borçlu olduğunu söylememe gerek yok."));
trsuperobject.biblemap.put("b57_1_20", new trsuperobject(57,1,20,"Evet, kardeş, Rab yolunda bana bir yardımın olsun. Mesihte yüreğimi ferahlat."));
trsuperobject.biblemap.put("b57_1_21", new trsuperobject(57,1,21,"Sözümü dinleyeceğinden emin olarak ve istediğimden fazlasını da yapacağını bilerek sana yazıyorum."));
trsuperobject.biblemap.put("b57_1_22", new trsuperobject(57,1,22,"Bu arada bana kalabileceğim bir yer hazırla. Çünkü dualarınız aracılığıyla sizlere bağışlanacağımı umuyorum."));
trsuperobject.biblemap.put("b57_1_23-24", new trsuperobject(57,1,23-24,"Mesih İsa uğruna kendisiyle birlikte tutuklu bulunduğum Epafras, emektaşlarım Markos, Aristarhus, Dimas ve Luka sana selam ederler."));
trsuperobject.biblemap.put("b57_1_25", new trsuperobject(57,1,25,"Rab İsa Mesih'in lütfu ruhunuzla birlikte olsun."));

}

}