trsuperobject.biblemap.put("b58_1_1", new trsuperobject(58,1,1,"Tanrı eski zamanlarda peygamberler aracılığıyla birçok kez çeşitli yollardan atalarımıza seslendi."));
trsuperobject.biblemap.put("b58_1_2", new trsuperobject(58,1,2,"Bu son çağda da her şeye mirasçı kıldığı ve aracılığıyla evreni yarattığı kendi Oğluyla bize seslenmiştir."));
trsuperobject.biblemap.put("b58_1_3", new trsuperobject(58,1,3,"Oğul, Tanrı yüceliğinin parıltısı, Onun varlığının öz görünümüdür. Güçlü sözüyle her şeyi devam ettirir. Günahlardan arınmayı sağladıktan sonra, yücelerde ulu Tanrının sağında oturdu."));
trsuperobject.biblemap.put("b58_1_4", new trsuperobject(58,1,4,"Meleklerden ne denli üstün bir adı miras aldıysa, onlardan o denli üstün oldu."));
trsuperobject.biblemap.put("b58_1_5", new trsuperobject(58,1,5,"Çünkü Tanrı meleklerin herhangi birine,  ‹‹Sen benim Oğlumsun,  Bugün ben sana Baba oldum››  Ya da, ‹‹Ben Ona Baba olacağım,  O da bana Oğul olacak››"));
trsuperobject.biblemap.put("b58_1_6", new trsuperobject(58,1,6,"Yine Tanrı ilk doğanı dünyaya gönderirken diyor ki, ‹‹Tanrının bütün melekleri Ona tapınsın.››"));
trsuperobject.biblemap.put("b58_1_7", new trsuperobject(58,1,7,"Melekler için,  ‹‹Kendi meleklerini rüzgar,  Hizmetkârlarını ateş alevi yapar›› diyor."));
trsuperobject.biblemap.put("b58_1_8", new trsuperobject(58,1,8,"Ama Oğul için şöyle diyor:  ‹‹Ey Tanrı, tahtın sonsuzluklar boyunca kalıcıdır,  Egemenliğinin asası adalet asasıdır."));
trsuperobject.biblemap.put("b58_1_9", new trsuperobject(58,1,9,"Doğruluğu sevdin, kötülükten nefret ettin.  Bunun için Tanrı, senin Tanrın,  Seni sevinç yağıyla  Arkadaşlarından daha çok meshetti.››"));
trsuperobject.biblemap.put("b58_1_10", new trsuperobject(58,1,10,"Yine diyor ki,  ‹‹Ya Rab, başlangıçta  Dünyanın temellerini sen attın.  Gökler de senin ellerinin yapıtıdır."));
trsuperobject.biblemap.put("b58_1_11", new trsuperobject(58,1,11,"Onlar yok olacak, ama sen kalıcısın.  Hepsi bir giysi gibi eskiyecek."));
trsuperobject.biblemap.put("b58_1_12", new trsuperobject(58,1,12,"Bir kaftan gibi düreceksin onları,  Bir giysi gibi değiştirilecekler.  Ama sen hep aynısın,  Yılların tükenmeyecek.››"));
trsuperobject.biblemap.put("b58_1_13", new trsuperobject(58,1,13,"Tanrı meleklerin herhangi birine,  ‹‹Ben düşmanlarını  Ayaklarının altına serinceye dek,  Sağımda otur››"));
trsuperobject.biblemap.put("b58_1_14", new trsuperobject(58,1,14,"Bütün melekler kurtuluşu miras alacaklara hizmet etmek için gönderilen görevli ruhlar değil midir?"));
trsuperobject.biblemap.put("b58_2_1", new trsuperobject(58,2,1,"Bu nedenle, akıntıya kapılıp sürüklenmemek için işittiklerimizi daha çok önemsemeliyiz."));
trsuperobject.biblemap.put("b58_2_2-3", new trsuperobject(58,2,2-3,"Çünkü melekler aracılığıyla bildirilen söz geçerli olduysa, her suç ve her sözdinlemezlik hak ettiği karşılığı aldıysa, bu denli büyük kurtuluşu görmezlikten gelirsek nasıl kurtulabiliriz? Başlangıçta Rab tarafından bildirilen bu kurtuluş, Rabbi dinlemiş olanlarca bize doğrulandı."));
trsuperobject.biblemap.put("b58_2_4", new trsuperobject(58,2,4,"Tanrı da belirtiler, harikalar, çeşitli mucizeler ve kendi isteği uyarınca dağıttığı Kutsal Ruh armağanlarıyla buna tanıklık etti."));
trsuperobject.biblemap.put("b58_2_5", new trsuperobject(58,2,5,"Tanrı, sözünü ettiğimiz gelecek dünyayı meleklere bağlı kılmadı."));
trsuperobject.biblemap.put("b58_2_6", new trsuperobject(58,2,6,"Ama biri bir yerde şöyle tanıklık etmiştir:  ‹‹Ya Rab, insan ne ki, onu anasın,  Ya da insanoğlu ne ki, ona ilgi gösteresin?"));
trsuperobject.biblemap.put("b58_2_7", new trsuperobject(58,2,7,"Onu meleklerden biraz aşağı kıldın,  Başına yücelik ve onur tacını koydun,  Ellerinin yapıtları üzerine onu görevlendirdin."));
trsuperobject.biblemap.put("b58_2_8", new trsuperobject(58,2,8,"Her şeyi ayakları altına sererek  Ona bağımlı kıldın.›› bırakmadı. Ne var ki, her şeyin insana bağımlı kılındığını henüz görmüyoruz."));
trsuperobject.biblemap.put("b58_2_9", new trsuperobject(58,2,9,"Ama meleklerden biraz aşağı kılınmış olan İsayı, Tanrının lütfuyla herkes için ölümü tatsın diye çektiği ölüm acısı sonucunda yücelik ve onur tacı giydirilmiş olarak görüyoruz."));
trsuperobject.biblemap.put("b58_2_10", new trsuperobject(58,2,10,"Birçok oğulu yüceliğe eriştirirken onların kurtuluş öncüsünü acılarla yetkinliğe erdirmesi, her şeyi kendisi için ve kendi aracılığıyla var eden Tanrıya uygun düşüyordu."));
trsuperobject.biblemap.put("b58_2_11", new trsuperobject(58,2,11,"Çünkü hepsi -kutsal kılan da kutsal kılınanlar da- aynı Babadandır. Bunun içindir ki, İsa onlara ‹‹kardeşlerim›› demekten utanmıyor."));
trsuperobject.biblemap.put("b58_2_12", new trsuperobject(58,2,12,"‹‹Adını kardeşlerime duyuracağım,  Topluluğun ortasında  Seni ilahilerle öveceğim›› diyor."));
trsuperobject.biblemap.put("b58_2_13", new trsuperobject(58,2,13,"Yine,  ‹‹Ben Ona güveneceğim›› ve yine,  ‹‹İşte ben ve Tanrının bana verdiği çocuklar›› diyor."));
trsuperobject.biblemap.put("b58_2_14", new trsuperobject(58,2,14,"Bu çocuklar etten ve kandan oldukları için İsa, ölüm gücüne sahip olanı, yani İblisi, ölüm aracılığıyla etkisiz kılmak üzere onlarla aynı insan yapısını aldı."));
trsuperobject.biblemap.put("b58_2_15", new trsuperobject(58,2,15,"Bunu, ölüm korkusu yüzünden yaşamları boyunca köle olanların hepsini özgür kılmak için yaptı."));
trsuperobject.biblemap.put("b58_2_16", new trsuperobject(58,2,16,"Kuşkusuz O, meleklere değil, İbrahimin soyundan olanlara yardım ediyor."));
trsuperobject.biblemap.put("b58_2_17", new trsuperobject(58,2,17,"Bunun için her yönden kardeşlerine benzemesi gerekiyordu. Öyle ki, Tanrıya hizmetinde merhametli ve sadık bir başkâhin olup halkın günahlarını bağışlatabilsin."));
trsuperobject.biblemap.put("b58_2_18", new trsuperobject(58,2,18,"Çünkü kendisi denenip acı çektiği için denenenlere yardım edebilir."));
trsuperobject.biblemap.put("b58_3_1", new trsuperobject(58,3,1,"Bunun için, göksel çağrıya ortak olan kutsal kardeşlerim, dikkatinizi açıkça benimsediğimiz inancın elçisi ve başkâhini İsaya çevirin."));
trsuperobject.biblemap.put("b58_3_2", new trsuperobject(58,3,2,"Musa Tanrının bütün evinde Tanrıya nasıl sadık kaldıysa, İsa da kendisini görevlendirene sadıktır."));
trsuperobject.biblemap.put("b58_3_3", new trsuperobject(58,3,3,"Evi yapan nasıl evden daha çok saygı görürse, İsa da Musadan daha büyük yüceliğe layık sayıldı."));
trsuperobject.biblemap.put("b58_3_4", new trsuperobject(58,3,4,"Her evin bir yapıcısı vardır, her şeyin yapıcısı ise Tanrıdır."));
trsuperobject.biblemap.put("b58_3_5", new trsuperobject(58,3,5,"Musa, gelecekte söylenecek sözlere tanıklık etmek için Tanrının bütün evinde bir hizmetkâr olarak sadık kaldı."));
trsuperobject.biblemap.put("b58_3_6", new trsuperobject(58,3,6,"Oysa Mesih, Onun evi üzerinde yetkili oğul olarak sadıktır. Eğer cesaretimizi ve övündüğümüz umudu gevşemeden sonuna dek sürdürürsek, Onun evi biziz."));
trsuperobject.biblemap.put("b58_3_7-8", new trsuperobject(58,3,7-8,"Bu nedenle, Kutsal Ruhun dediği gibi,  ‹‹Bugün Onun sesini duyarsanız,  Atalarınızın başkaldırdığı,  Çölde Onu sınadığı günkü gibi  Yüreklerinizi nasırlaştırmayın."));
trsuperobject.biblemap.put("b58_3_9", new trsuperobject(58,3,9,"Atalarınız beni orada sınayıp denediler  Ve kırk yıl boyunca yaptıklarımı gördüler."));
trsuperobject.biblemap.put("b58_3_10", new trsuperobject(58,3,10,"Bu nedenle o kuşağa darıldım  Ve dedim ki,  ‹Yürekleri hep kötüye sapar,  Yollarımı öğrenmediler."));
trsuperobject.biblemap.put("b58_3_11", new trsuperobject(58,3,11,"Öfkelendiğimde ant içtiğim gibi,  Onlar huzur diyarıma asla girmeyecekler.››"));
trsuperobject.biblemap.put("b58_3_12", new trsuperobject(58,3,12,"Ey kardeşler, hiçbirinizde diri Tanrıyı terk eden kötü, imansız bir yüreğin bulunmamasına dikkat edin."));
trsuperobject.biblemap.put("b58_3_13", new trsuperobject(58,3,13,"‹‹Gün bugündür›› denildikçe birbirinizi her gün yüreklendirin. Öyle ki, hiçbirinizin yüreği günahın aldatıcılığıyla nasırlaşmasın."));
trsuperobject.biblemap.put("b58_3_14", new trsuperobject(58,3,14,"Çünkü Mesihe ortak olduk. Yalnız başlangıçtaki güvenimizi gevşemeden sonuna dek sürdürmeliyiz."));
trsuperobject.biblemap.put("b58_3_15", new trsuperobject(58,3,15,"Yukarıda belirtildiği gibi,  ‹‹Bugün Onun sesini duyarsanız,  Atalarınızın başkaldırdığı günkü gibi  Yüreklerinizi nasırlaştırmayın.››"));
trsuperobject.biblemap.put("b58_3_16", new trsuperobject(58,3,16,"Onun sesini işitip başkaldıran kimlerdi? Musa önderliğinde Mısırdan çıkanların hepsi değil mi?"));
trsuperobject.biblemap.put("b58_3_17", new trsuperobject(58,3,17,"Tanrı kimlere kırk yıl dargın kaldı? Günah işleyip cesetleri çöle serilenlere değil mi?"));
trsuperobject.biblemap.put("b58_3_18", new trsuperobject(58,3,18,"Sözünü dinlemeyenler dışında kendi huzur diyarına kimlerin girmeyeceğine ant içti?"));
trsuperobject.biblemap.put("b58_3_19", new trsuperobject(58,3,19,"Görüyoruz ki, imansızlıklarından ötürü oraya giremediler."));
trsuperobject.biblemap.put("b58_4_1", new trsuperobject(58,4,1,"Bu nedenle Tanrının huzur diyarına girme vaadi hâlâ geçerliyken, herhangi birinizin buna erişmemiş sayılmasından korkalım."));
trsuperobject.biblemap.put("b58_4_2", new trsuperobject(58,4,2,"Çünkü onlar gibi biz de iyi haberi aldık. Ama onlar duydukları sözü imanla birleştirmedikleri için bunun kendilerine bir yararı olmadı."));
trsuperobject.biblemap.put("b58_4_3", new trsuperobject(58,4,3,"Biz inanmış olanlar huzur diyarına gireriz. Nitekim Tanrı şöyle demiştir:  ‹‹Öfkelendiğimde ant içtiğim gibi,  Onlar huzur diyarıma asla girmeyecekler.››"));
trsuperobject.biblemap.put("b58_4_4", new trsuperobject(58,4,4,"Çünkü bir yerde yedinci günle ilgili şunu demiştir:  ‹‹Tanrı bütün işlerinden yedinci gün dinlendi.››"));
trsuperobject.biblemap.put("b58_4_5", new trsuperobject(58,4,5,"Bu konuda yine diyor ki,  ‹‹Onlar huzur diyarıma asla girmeyecekler.››"));
trsuperobject.biblemap.put("b58_4_6", new trsuperobject(58,4,6,"Demek ki, bazılarının huzur diyarına gireceği kesindir. Daha önce iyi haberi almış olanlar söz dinlemedikleri için o diyara giremediler."));
trsuperobject.biblemap.put("b58_4_7", new trsuperobject(58,4,7,"Bu yüzden Tanrı, uzun zaman sonra Davutun aracılığıyla, ‹‹bugün›› diyerek yine bir gün belirliyor. Daha önce denildiği gibi,  ‹‹Bugün Onun sesini duyarsanız,  Yüreklerinizi nasırlaştırmayın.››"));
trsuperobject.biblemap.put("b58_4_8", new trsuperobject(58,4,8,"Eğer Yeşu onları huzura kavuştursaydı, Tanrı daha sonra bir başka günden söz etmezdi."));
trsuperobject.biblemap.put("b58_4_9", new trsuperobject(58,4,9,"Böylece Tanrı halkı için bir Şabat Günü rahatı kalıyor."));
trsuperobject.biblemap.put("b58_4_10", new trsuperobject(58,4,10,"Tanrı işlerinden nasıl dinlendiyse, Onun huzur diyarına giren de kendi işlerinden öylece dinlenir."));
trsuperobject.biblemap.put("b58_4_11", new trsuperobject(58,4,11,"Bu nedenle o huzur diyarına girmeye gayret edelim; öyle ki, hiçbirimiz aynı tür sözdinlemezlikten ötürü düşmesin."));
trsuperobject.biblemap.put("b58_4_12", new trsuperobject(58,4,12,"Tanrının sözü diri ve etkilidir, iki ağızlı kılıçtan daha keskindir. Canla ruhu, ilikle eklemleri birbirinden ayıracak kadar derinlere işler; yüreğin düşüncelerini, amaçlarını yargılar."));
trsuperobject.biblemap.put("b58_4_13", new trsuperobject(58,4,13,"Tanrının görmediği hiçbir yaratık yoktur. Kendisine hesap vereceğimiz Tanrının gözü önünde her şey çıplak ve açıktır."));
trsuperobject.biblemap.put("b58_4_14", new trsuperobject(58,4,14,"Tanrı Oğlu İsa gökleri aşan büyük başkâhinimiz olduğu için açıkça benimsediğimiz inanca sımsıkı sarılalım."));
trsuperobject.biblemap.put("b58_4_15", new trsuperobject(58,4,15,"Çünkü başkâhinimiz zayıflıklarımızda bize yakınlık duyamayan biri değildir; tersine, her alanda bizim gibi denenmiş, ama günah işlememiştir."));
trsuperobject.biblemap.put("b58_4_16", new trsuperobject(58,4,16,"Onun için Tanrı'nın lütuf tahtına cesaretle yaklaşalım; öyle ki, yardım gereksindiğimizde merhamet görelim ve lütuf bulalım."));
trsuperobject.biblemap.put("b58_5_1", new trsuperobject(58,5,1,"İnsanlar arasından seçilen her başkâhin, günahlara karşılık sunular ve kurbanlar sunmak üzere Tanrıyla ilgili konularda insanları temsil etmek için atanır."));
trsuperobject.biblemap.put("b58_5_2", new trsuperobject(58,5,2,"Bilgisizlere, yoldan sapanlara yumuşak davranabilir. Çünkü kendisi de zayıflıklarla kuşatılmıştır."));
trsuperobject.biblemap.put("b58_5_3", new trsuperobject(58,5,3,"Bundan ötürü, halk için olduğu gibi, kendisi için de günah sunusu sunmak zorundadır."));
trsuperobject.biblemap.put("b58_5_4", new trsuperobject(58,5,4,"Kimse başkâhin olma onurunu kendi kendine alamaz; ancak Harun gibi, Tanrı tarafından çağrılırsa alır."));
trsuperobject.biblemap.put("b58_5_5", new trsuperobject(58,5,5,"Nitekim Mesih de başkâhin olmak için kendi kendini yüceltmedi. Ona,  ‹‹Sen benim Oğlumsun,  Bugün ben sana Baba oldum››"));
trsuperobject.biblemap.put("b58_5_6", new trsuperobject(58,5,6,"Başka bir yerde de diyor ki,  ‹‹Melkisedek düzeni uyarınca  Sen sonsuza dek kâhinsin.››"));
trsuperobject.biblemap.put("b58_5_7", new trsuperobject(58,5,7,"Mesih, yeryüzünde olduğu günlerde kendisini ölümden kurtaracak güçte olan Tanrıya büyük feryat ve gözyaşlarıyla dua etti, yakardı ve Tanrı korkusu nedeniyle işitildi."));
trsuperobject.biblemap.put("b58_5_8", new trsuperobject(58,5,8,"Oğul olduğu halde, çektiği acılarla söz dinlemeyi öğrendi."));
trsuperobject.biblemap.put("b58_5_9", new trsuperobject(58,5,9,"Yetkin kılınınca, sözünü dinleyen herkes için sonsuz kurtuluş kaynağı oldu."));
trsuperobject.biblemap.put("b58_5_10", new trsuperobject(58,5,10,"Çünkü Tanrı tarafından Melkisedek düzeni uyarınca başkâhin atanmıştı."));
trsuperobject.biblemap.put("b58_5_11", new trsuperobject(58,5,11,"Bu konuda söyleyecek çok sözümüz var, ama kulaklarınız uyuştuğu için anlatmak zor."));
trsuperobject.biblemap.put("b58_5_12", new trsuperobject(58,5,12,"Şimdiye dek öğretmen olmanız gerekirken, Tanrı sözlerinin temel ilkelerini size yeni baştan öğretecek birine ihtiyacınız var. Size yine süt gerekli, katı yiyecek değil!"));
trsuperobject.biblemap.put("b58_5_13", new trsuperobject(58,5,13,"Sütle beslenen herkes bebektir ve doğruluk sözünde deneyimsizdir."));
trsuperobject.biblemap.put("b58_5_14", new trsuperobject(58,5,14,"Katı yiyecek, yetişkinler içindir; onlar duyularını iyi ile kötüyü ayırt etmek üzere alıştırmayla eğitmiş kişilerdir."));
trsuperobject.biblemap.put("b58_6_1-2", new trsuperobject(58,6,1-2,"Bunun için, ölü işlerden tövbe etmenin ve Tanrıya inanmanın temelini, vaftizler, elle kutsama, ölülerin dirilişi ve sonsuz yargıyla ilgili öğretinin temelini yeni baştan atmadan Mesihle ilgili ilk öğretileri aşarak yetkinliğe doğru ilerleyelim."));
trsuperobject.biblemap.put("b58_6_3", new trsuperobject(58,6,3,"Tanrı izin verirse, bunu yapacağız."));
trsuperobject.biblemap.put("b58_6_4-6", new trsuperobject(58,6,4-6,"Bir kez aydınlatılmış, göksel armağanı tatmış ve Kutsal Ruha ortak edilmiş, Tanrı sözünün iyiliğini ve gelecek çağın güçlerini tatmış oldukları halde yoldan sapanları yeniden tövbe edecek duruma getirmeye olanak yoktur. Çünkü onlar Tanrının Oğlunu adeta yeniden çarmıha geriyor, herkesin önünde aşağılıyorlar."));
trsuperobject.biblemap.put("b58_6_7", new trsuperobject(58,6,7,"Üzerine sık sık yağan yağmuru emen ve kimler için işleniyorsa onlara yararlı bitkiler üreten toprağı Tanrı bereketli kılar."));
trsuperobject.biblemap.put("b58_6_8", new trsuperobject(58,6,8,"Ama dikenli bitki, devedikeni üreten toprak yararsızdır; lanetlenmeye yakındır, sonu yanmaktır."));
trsuperobject.biblemap.put("b58_6_9", new trsuperobject(58,6,9,"Size gelince, sevgili kardeşler, böyle konuştuğumuz halde, durumunuzun daha iyi olduğuna, kurtuluşa uygun düştüğüne eminiz."));
trsuperobject.biblemap.put("b58_6_10", new trsuperobject(58,6,10,"Tanrı adaletsiz değildir; emeğinizi ve kutsallara hizmet etmiş olarak ve etmeye devam ederek Onun adına gösterdiğiniz sevgiyi unutmaz."));
trsuperobject.biblemap.put("b58_6_11", new trsuperobject(58,6,11,"Umudunuzdan doğan tam güvenceye kavuşmanız için her birinizin sona dek aynı gayreti göstermesini diliyoruz."));
trsuperobject.biblemap.put("b58_6_12", new trsuperobject(58,6,12,"Tembel olmamanızı, vaat edilenleri iman ve sabır aracılığıyla miras alanların örneğine uymanızı istiyoruz."));
trsuperobject.biblemap.put("b58_6_13", new trsuperobject(58,6,13,"Tanrı İbrahime vaatte bulunduğu zaman, üzerine ant içecek daha üstün biri olmadığı için kendi üzerine ant içerek şöyle dedi:"));
trsuperobject.biblemap.put("b58_6_14", new trsuperobject(58,6,14,"‹‹Seni kutsadıkça kutsayacağım,  Soyunu çoğalttıkça çoğaltacağım.››"));
trsuperobject.biblemap.put("b58_6_15", new trsuperobject(58,6,15,"Böylece İbrahim sabırla dayanarak vaade erişti."));
trsuperobject.biblemap.put("b58_6_16", new trsuperobject(58,6,16,"İnsanlar kendilerinden üstün biri üzerine ant içerler. Onlar için ant, söyleneni doğrular ve her tartışmayı sona erdirir."));
trsuperobject.biblemap.put("b58_6_17", new trsuperobject(58,6,17,"Tanrı da amacının değişmezliğini vaadin mirasçılarına daha açıkça belirtmek istediği için vaadini antla pekiştirdi."));
trsuperobject.biblemap.put("b58_6_18", new trsuperobject(58,6,18,"Öyle ki, önümüze konan umuda tutunmak için Tanrıya sığınan bizler, Tanrının yalan söylemesi olanaksız olan bu iki değişmez şey aracılığıyla büyük cesaret bulalım."));
trsuperobject.biblemap.put("b58_6_19", new trsuperobject(58,6,19,"Canlarımız için gemi demiri gibi sağlam ve güvenilir olan bu umut, perdenin arkasındaki iç bölmeye geçer."));
trsuperobject.biblemap.put("b58_6_20", new trsuperobject(58,6,20,"Melkisedek düzeni uyarınca sonsuza dek başkâhin olan İsa oraya uğrumuza öncü olarak girdi."));
trsuperobject.biblemap.put("b58_7_1", new trsuperobject(58,7,1,"Bu Melkisedek, Şalem Kralı ve yüce Tanrının kâhiniydi. Kralları bozguna uğratmaktan dönen İbrahimi karşılamış ve onu kutsamıştı."));
trsuperobject.biblemap.put("b58_7_2", new trsuperobject(58,7,2,"İbrahim de ona her şeyin ondalığını verdi. Melkisedek, adının anlamına göre, önce ‹‹Doğruluk Kralı››dır; sonra da ‹‹Şalem Kralı››, yani ‹‹Esenlik Kralı››dır."));
trsuperobject.biblemap.put("b58_7_3", new trsuperobject(58,7,3,"Babasız, annesizdir; soyağacı yoktur. Ne günlerinin başlangıcı, ne yaşamının sonu vardır. Tanrının Oğlu gibi sonsuza dek kâhin kalacaktır."));
trsuperobject.biblemap.put("b58_7_4", new trsuperobject(58,7,4,"Bakın, büyük ata İbrahimin ganimetten ondalık verdiği bu adam ne kadar büyüktür!"));
trsuperobject.biblemap.put("b58_7_5", new trsuperobject(58,7,5,"Levioğullarından olup kâhinlik görevini üstlenenlere Kutsal Yasa uyarınca halktan, yani İbrahimin soyundan oldukları halde, kardeşlerinden ondalık almaları buyrulmuştur."));
trsuperobject.biblemap.put("b58_7_6", new trsuperobject(58,7,6,"Melkisedek ise Levili kâhinlerin soyundan olmadığı halde, vaatleri alan İbrahimden ondalık kabul etmiş ve onu kutsamıştır."));
trsuperobject.biblemap.put("b58_7_7", new trsuperobject(58,7,7,"Hiç kuşkusuz, kutsayan kutsanandan üstündür."));
trsuperobject.biblemap.put("b58_7_8", new trsuperobject(58,7,8,"Birinde ölümlü insanlar ondalık alıyor, ötekinde yaşadığına tanıklık edilen biri alıyor."));
trsuperobject.biblemap.put("b58_7_9", new trsuperobject(58,7,9,"Ondalık alan Levi bile İbrahim aracılığıyla ondalık vermiştir denebilir."));
trsuperobject.biblemap.put("b58_7_10", new trsuperobject(58,7,10,"Çünkü Melkisedek İbrahimi karşıladığı zaman, Levi hâlâ atasının bedenindeydi."));
trsuperobject.biblemap.put("b58_7_11", new trsuperobject(58,7,11,"Eğer Levililerin kâhinliği aracılığıyla yetkinliğe erişilebilseydi -nitekim Kutsal Yasa bu kâhinliği öngörerek halka verildi- Harun düzenine göre değil de, Melkisedek düzenine göre başka bir kâhinin gelmesinden söz etmeye ne gerek kalırdı?"));
trsuperobject.biblemap.put("b58_7_12", new trsuperobject(58,7,12,"Çünkü kâhinlik değişince, Yasa da zorunlu olarak değişir."));
trsuperobject.biblemap.put("b58_7_13", new trsuperobject(58,7,13,"Kendisinden böyle söz edilen kişi başka bir oymaktandır. Bu oymaktan hiç kimse sunakta hizmet etmemiştir."));
trsuperobject.biblemap.put("b58_7_14", new trsuperobject(58,7,14,"Rabbimizin Yahuda oymağından geldiği açıktır. Musa bu oymaktan söz ederken kâhinlere ilişkin bir şey söylemedi."));
trsuperobject.biblemap.put("b58_7_15", new trsuperobject(58,7,15,"Melkisedek benzeri başka bir kâhin ortaya çıktığından, bu söylediğimiz artık daha da açıktır."));
trsuperobject.biblemap.put("b58_7_16", new trsuperobject(58,7,16,"O, Yasanın soyla ilgili önkoşuluna göre değil, yok edilemez bir yaşamın gücüne göre kâhin olmuştur."));
trsuperobject.biblemap.put("b58_7_17", new trsuperobject(58,7,17,"Çünkü,  ‹‹Melkisedek düzeni uyarınca  Sen sonsuza dek kâhinsin››"));
trsuperobject.biblemap.put("b58_7_18", new trsuperobject(58,7,18,"Önceki buyruk, zayıflığı ve yararsızlığı nedeniyle geçersiz kılındı."));
trsuperobject.biblemap.put("b58_7_19", new trsuperobject(58,7,19,"Çünkü Yasa hiçbir şeyi yetkinleştiremedi. Bunun yerine, aracılığıyla Tanrıya yaklaştığımız daha sağlam bir umut verildi."));
trsuperobject.biblemap.put("b58_7_20", new trsuperobject(58,7,20,"Bu da antsız olmadı. Öbürleri ant içilmeden kâhin olmuşlardı."));
trsuperobject.biblemap.put("b58_7_21", new trsuperobject(58,7,21,"Ama O kendisine,  ‹‹Rab ant içti, kararından dönmez,  Sen sonsuza dek kâhinsin››"));
trsuperobject.biblemap.put("b58_7_22", new trsuperobject(58,7,22,"Böylece İsa daha iyi bir antlaşmanın kefili olmuştur."));
trsuperobject.biblemap.put("b58_7_23", new trsuperobject(58,7,23,"Önceki düzende çok sayıda kâhin görev aldı. Çünkü ölüm, görevlerini sürdürmelerini engelliyordu."));
trsuperobject.biblemap.put("b58_7_24", new trsuperobject(58,7,24,"Ama İsa sonsuza dek yaşadığı için kâhinliği süreklidir."));
trsuperobject.biblemap.put("b58_7_25", new trsuperobject(58,7,25,"Bu nedenle Onun aracılığıyla Tanrıya yaklaşanları tümüyle kurtaracak güçtedir. Çünkü onlara aracılık etmek için hep yaşamaktadır."));
trsuperobject.biblemap.put("b58_7_26", new trsuperobject(58,7,26,"Böyle bir başkâhinimiz -kutsal, suçsuz, lekesiz, günahkârlardan ayrılmış, göklerden daha yücelere çıkarılmış bir başkâhinimiz- olması uygundur."));
trsuperobject.biblemap.put("b58_7_27", new trsuperobject(58,7,27,"O, öbür başkâhinler gibi her gün önce kendi günahları, sonra da halkın günahları için kurbanlar sunmak zorunda değildir. Çünkü kendini sunmakla bunu ilk ve son kez yaptı."));
trsuperobject.biblemap.put("b58_7_28", new trsuperobject(58,7,28,"Kutsal Yasa, zayıflıkları olan insanları başkâhin atamaktadır. Ama Yasa'dan sonra gelen ant sözü, sonsuza dek yetkin kılınmış olan Oğul'u başkâhin atamıştır."));
trsuperobject.biblemap.put("b58_8_1-2", new trsuperobject(58,8,1-2,"Söylediklerimizin özü şudur: Göklerde, Yüce Olanın tahtının sağında oturan, kutsal yerde, insanın değil, Rabbin kurduğu asıl tapınma çadırında görev yapan böyle bir başkâhinimiz vardır."));
trsuperobject.biblemap.put("b58_8_3", new trsuperobject(58,8,3,"Her başkâhin sunular, kurbanlar sunmak için atanır. Bu nedenle bizim başkâhinimizin de sunacak bir şeyi olması gerekir."));
trsuperobject.biblemap.put("b58_8_4", new trsuperobject(58,8,4,"Eğer kendisi yeryüzünde olsaydı, kâhin olamazdı. Çünkü Kutsal Yasa uyarınca sunuları sunanlar var."));
trsuperobject.biblemap.put("b58_8_5", new trsuperobject(58,8,5,"Bunlar göktekinin örneği ve gölgesi olan tapınakta hizmet ediyorlar. Nitekim Musa tapınma çadırını kurmak üzereyken Tanrı tarafından şöyle uyarıldı: ‹‹Her şeyi sana dağda gösterilen örneğe göre yapmaya dikkat et.››"));
trsuperobject.biblemap.put("b58_8_6", new trsuperobject(58,8,6,"Şimdiyse, İsa daha iyi vaatler üzerine kurulmuş daha iyi bir antlaşmanın aracısı olduğu kadar, daha üstün bir göreve de sahip olmuştur."));
trsuperobject.biblemap.put("b58_8_7", new trsuperobject(58,8,7,"Eğer o ilk antlaşma kusursuz olsaydı, ikincisine gerek duyulmazdı."));
trsuperobject.biblemap.put("b58_8_8", new trsuperobject(58,8,8,"Oysa halkını kusurlu bulan Tanrı şöyle diyor:  ‹‹İsrail halkıyla ve Yahuda halkıyla  Yeni bir antlaşma yapacağım günler geliyor›  Diyor Rab."));
trsuperobject.biblemap.put("b58_8_9", new trsuperobject(58,8,9,"‹Atalarını Mısırdan çıkarmak için  Ellerinden tuttuğum gün  Onlarla yaptığım antlaşmaya benzemeyecek.  Çünkü onlar antlaşmama bağlı kalmadılar,  Ben de onlardan yüz çevirdim›  Diyor Rab."));
trsuperobject.biblemap.put("b58_8_10", new trsuperobject(58,8,10,"‹O günlerden sonra İsrail halkıyla  Yapacağım antlaşma şudur› diyor Rab,  ‹Yasalarımı zihinlerine işleyeceğim,  Yüreklerine yazacağım.  Ben onların Tanrısı olacağım,  Onlar da benim halkım olacak."));
trsuperobject.biblemap.put("b58_8_11", new trsuperobject(58,8,11,"Hiç kimse yurttaşını, kardeşini,  Rabbi tanı diye eğitmeyecek.  Çünkü küçük büyük hepsi tanıyacak beni."));
trsuperobject.biblemap.put("b58_8_12", new trsuperobject(58,8,12,"Çünkü suçlarını bağışlayacağım,  Günahlarını artık anmayacağım.››"));
trsuperobject.biblemap.put("b58_8_13", new trsuperobject(58,8,13,"Tanrı, ‹‹Yeni bir antlaşma›› demekle ilkini eskimiş saymıştır. Eskiyip köhneleşense çok geçmeden yok olur."));
trsuperobject.biblemap.put("b58_9_1", new trsuperobject(58,9,1,"İlk antlaşmanın tapınma kuralları ve dünyasal tapınağı vardı."));
trsuperobject.biblemap.put("b58_9_2", new trsuperobject(58,9,2,"Bir çadır kurulmuştu. Kutsal Yer denen birinci bölmede kandillik, masa ve adak ekmekleri bulunurdu."));
trsuperobject.biblemap.put("b58_9_3", new trsuperobject(58,9,3,"İkinci perdenin arkasında En Kutsal Yer denen bir bölme vardı."));
trsuperobject.biblemap.put("b58_9_4", new trsuperobject(58,9,4,"Altın buhur sunağıyla her yanı altınla kaplanmış Antlaşma Sandığı buradaydı. Sandığın içinde altından yapılmış man testisi, Harunun filizlenmiş değneği ve antlaşma levhaları vardı."));
trsuperobject.biblemap.put("b58_9_5", new trsuperobject(58,9,5,"Sandığın üstünde Bağışlanma Kapağını gölgeleyen yüce Keruvlar dururdu. Ama şimdi bunların ayrıntılarına giremeyiz."));
trsuperobject.biblemap.put("b58_9_6", new trsuperobject(58,9,6,"Her şey böyle düzenlendikten sonra kâhinler her zaman çadırın ilk bölmesine girer, tapınma görevlerini yerine getirirler."));
trsuperobject.biblemap.put("b58_9_7", new trsuperobject(58,9,7,"Ama iç bölmeye yılda bir kez yalnız başkâhin girebilir. Üstelik kendisi için ve halkın bilmeden işlediği suçlar için sunacağı kurban kanı olmaksızın giremez."));
trsuperobject.biblemap.put("b58_9_8", new trsuperobject(58,9,8,"Kutsal Ruh bununla çadırın ilk bölmesi durdukça, kutsal yere giden yolun henüz açıkça gösterilmediğini belirtiyor."));
trsuperobject.biblemap.put("b58_9_9", new trsuperobject(58,9,9,"Bu, şimdiki çağ için bir örnektir; sunulan kurbanlarla sunuların tapınan kişinin vicdanını yetkinleştiremediğini gösteriyor."));
trsuperobject.biblemap.put("b58_9_10", new trsuperobject(58,9,10,"Bunlar yalnız yiyecek, içecek, çeşitli dinsel yıkanmalarla ilgilidir; yeni düzenin başlangıcına kadar geçerli olan bedensel kurallardır."));
trsuperobject.biblemap.put("b58_9_11", new trsuperobject(58,9,11,"Ama Mesih, gelecek iyi şeylerin başkâhini olarak ortaya çıktı. İnsan eliyle yapılmamış, yani bu yaratılıştan olmayan daha büyük, daha yetkin çadırdan geçti."));
trsuperobject.biblemap.put("b58_9_12", new trsuperobject(58,9,12,"Tekelerle danaların kanıyla değil, sonsuz kurtuluşu sağlayarak kendi kanıyla kutsal yere ilk ve son kez girdi."));
trsuperobject.biblemap.put("b58_9_13", new trsuperobject(58,9,13,"Tekelerle boğaların kanı ve serpilen düve külü murdar olanları kutsal kılıyor, bedensel açıdan temizliyor."));
trsuperobject.biblemap.put("b58_9_14", new trsuperobject(58,9,14,"Öyleyse sonsuz Ruh aracılığıyla kendini lekesiz olarak Tanrıya sunmuş olan Mesihin kanının, diri Tanrıya kulluk edebilmemiz için vicdanımızı ölü işlerden temizleyeceği ne kadar daha kesindir!"));
trsuperobject.biblemap.put("b58_9_15", new trsuperobject(58,9,15,"Bu nedenle, çağrılmış olanların vaat edilen sonsuz mirası almaları için Mesih yeni antlaşmanın aracısı oldu. Kendisi onları ilk antlaşmafx1 zamanında işledikleri suçlardan kurtarmak için fidye olarak öldü."));
trsuperobject.biblemap.put("b58_9_16", new trsuperobject(58,9,16,"Ortada bir vasiyetfx1 varsa, vasiyet edenin ölümünün kanıtlanması gerekir."));
trsuperobject.biblemap.put("b58_9_17", new trsuperobject(58,9,17,"Çünkü vasiyet ancak ölümden sonra geçerli olur. Vasiyet eden yaşadıkça, vasiyetin hiçbir etkinliği yoktur."));
trsuperobject.biblemap.put("b58_9_18", new trsuperobject(58,9,18,"Bu nedenle ilk antlaşma bile kan akıtılmadan yürürlüğe girmedi."));
trsuperobject.biblemap.put("b58_9_19", new trsuperobject(58,9,19,"Musa, Kutsal Yasanın her buyruğunu bütün halka bildirdikten sonra su, al yapağı, mercanköşkotu ile danaların ve tekelerin kanını alıp hem kitabın hem de bütün halkın üzerine serpti."));
trsuperobject.biblemap.put("b58_9_20", new trsuperobject(58,9,20,"‹‹Tanrının uymanızı buyurduğu antlaşmanın kanı budur›› dedi."));
trsuperobject.biblemap.put("b58_9_21", new trsuperobject(58,9,21,"Aynı biçimde çadırın ve tapınmada kullanılan bütün eşyaların üzerine kan serpti."));
trsuperobject.biblemap.put("b58_9_22", new trsuperobject(58,9,22,"Nitekim Kutsal Yasa uyarınca hemen her şey kanla temiz kılınır, kan dökülmeden bağışlama olmaz."));
trsuperobject.biblemap.put("b58_9_23", new trsuperobject(58,9,23,"Böylelikle aslı göklerde olan örneklerin bu kurbanlarla, ama gökteki asıllarının bunlardan daha iyi kurbanlarla temiz kılınması gerekti."));
trsuperobject.biblemap.put("b58_9_24", new trsuperobject(58,9,24,"Çünkü Mesih, asıl kutsal yerin örneği olup insan eliyle yapılan kutsal yere değil, ama şimdi bizim için Tanrının önünde görünmek üzere asıl göğe girdi."));
trsuperobject.biblemap.put("b58_9_25", new trsuperobject(58,9,25,"Başkâhin her yıl kendisinin olmayan kanla En Kutsal Yere girer; oysa Mesih kendisini tekrar tekrar sunmak için göğe girmedi."));
trsuperobject.biblemap.put("b58_9_26", new trsuperobject(58,9,26,"Öyle olsaydı, dünyanın kuruluşundan beri Mesihin tekrar tekrar acı çekmesi gerekirdi. Oysa Mesih, kendisini bir kez kurban ederek günahı ortadan kaldırmak için çağların sonunda ortaya çıkmıştır."));
trsuperobject.biblemap.put("b58_9_27", new trsuperobject(58,9,27,"Bir kez ölmek, sonra da yargılanmak nasıl insanların kaderiyse, Mesih de birçoklarının günahlarını yüklenmek için bir kez kurban edildi. İkinci kez, günah yüklenmek için değil, kurtuluş getirmek için kendisini bekleyenlere görünecektir."));
trsuperobject.biblemap.put("b58_10_1", new trsuperobject(58,10,1,"Kutsal Yasada gelecek iyi şeylerin aslı yoktur, sadece gölgesi vardır. Bu nedenle Yasa, her yıl sürekli aynı kurbanları sunarak Tanrıya yaklaşanları asla yetkinliğe erdiremez."));
trsuperobject.biblemap.put("b58_10_2", new trsuperobject(58,10,2,"Erdirebilseydi, kurban sunmaya son verilmez miydi? Çünkü tapınanlar bir kez günahlarından arındıktan sonra artık günahlılık duygusu kalmazdı."));
trsuperobject.biblemap.put("b58_10_3", new trsuperobject(58,10,3,"Ancak o kurbanlar insanlara yıldan yıla günahlarını anımsatıyor."));
trsuperobject.biblemap.put("b58_10_4", new trsuperobject(58,10,4,"Çünkü boğalarla tekelerin kanı günahları ortadan kaldıramaz."));
trsuperobject.biblemap.put("b58_10_5", new trsuperobject(58,10,5,"Bunun için Mesih dünyaya gelirken şöyle diyor:  ‹‹Kurban ve sunu istemedin,  Ama bana bir beden hazırladın."));
trsuperobject.biblemap.put("b58_10_6", new trsuperobject(58,10,6,"Yakmalık sunudan ve günah sunusundan  Hoşnut olmadın."));
trsuperobject.biblemap.put("b58_10_7", new trsuperobject(58,10,7,"O zaman şöyle dedim:  ‹Kutsal Yazı tomarında  Benim için yazıldığı gibi,  Senin isteğini yapmak üzere,  Ey Tanrı, işte geldim.››"));
trsuperobject.biblemap.put("b58_10_8", new trsuperobject(58,10,8,"Mesih ilkin, ‹‹Kurban, sunu, yakmalık sunu, günah sunusu istemedin ve bunlardan hoşnut olmadın›› dedi. Oysa bunlar Yasanın bir gereği olarak sunulur."));
trsuperobject.biblemap.put("b58_10_9", new trsuperobject(58,10,9,"Sonra, ‹‹Senin isteğini yapmak üzere işte geldim›› dedi. Yani ikinciyi geçerli kılmak için birinciyi ortadan kaldırıyor."));
trsuperobject.biblemap.put("b58_10_10", new trsuperobject(58,10,10,"Tanrının bu isteği uyarınca, İsa Mesihin bedeninin ilk ve son kez sunulmasıyla kutsal kılındık."));
trsuperobject.biblemap.put("b58_10_11", new trsuperobject(58,10,11,"Her kâhin her gün ayakta durup görevini yapar ve günahları asla ortadan kaldıramayan aynı kurbanları tekrar tekrar sunar."));
trsuperobject.biblemap.put("b58_10_12", new trsuperobject(58,10,12,"Oysa Mesih günahlar için sonsuza dek geçerli tek bir kurban sunduktan sonra Tanrının sağında oturdu."));
trsuperobject.biblemap.put("b58_10_13", new trsuperobject(58,10,13,"O zamandan beri düşmanlarının, kendi ayaklarının altına serilmesini bekliyor."));
trsuperobject.biblemap.put("b58_10_14", new trsuperobject(58,10,14,"Çünkü kutsal kılınanları tek bir sunuyla sonsuza dek yetkinliğe erdirmiştir."));
trsuperobject.biblemap.put("b58_10_15", new trsuperobject(58,10,15,"Kutsal Ruh da bu konuda bize tanıklık ediyor. Önce diyor ki,"));
trsuperobject.biblemap.put("b58_10_16", new trsuperobject(58,10,16,"‹‹Rab, ‹O günlerden sonra  Onlarla yapacağım antlaşma şudur:  Yasalarımı yüreklerine koyacağım,  Zihinlerine yazacağım› diyor.››"));
trsuperobject.biblemap.put("b58_10_17", new trsuperobject(58,10,17,"Sonra şunu ekliyor:  ‹‹Onların günahlarını ve suçlarını artık anmayacağım.››"));
trsuperobject.biblemap.put("b58_10_18", new trsuperobject(58,10,18,"Bunların bağışlanması durumunda artık günah için sunuya gerek yoktur."));
trsuperobject.biblemap.put("b58_10_19-20", new trsuperobject(58,10,19-20,"Bu nedenle, ey kardeşler, İsanın kanı sayesinde perdede, yani kendi bedeninde bize açtığı yeni ve diri yoldan kutsal yere girmeye cesaretimiz vardır."));
trsuperobject.biblemap.put("b58_10_21", new trsuperobject(58,10,21,"Tanrının evinden sorumlu büyük bir kâhinimiz bulunmaktadır."));
trsuperobject.biblemap.put("b58_10_22", new trsuperobject(58,10,22,"Öyleyse yüreklerimiz serpmeyle kötü vicdandan arınmış, bedenlerimiz temiz suyla yıkanmış olarak, imanın verdiği tam güvenceyle, yürekten bir içtenlikle Tanrıya yaklaşalım."));
trsuperobject.biblemap.put("b58_10_23", new trsuperobject(58,10,23,"Açıkça benimsediğimiz umuda sımsıkı tutunalım. Çünkü vaat eden Tanrı güvenilirdir."));
trsuperobject.biblemap.put("b58_10_24", new trsuperobject(58,10,24,"Birbirimizi sevgi ve iyi işler için nasıl gayrete getirebileceğimizi düşünelim."));
trsuperobject.biblemap.put("b58_10_25", new trsuperobject(58,10,25,"Bazılarının alıştığı gibi, bir araya gelmekten vazgeçmeyelim; o günün yaklaştığını gördükçe birbirimizi daha da çok yüreklendirelim."));
trsuperobject.biblemap.put("b58_10_26-27", new trsuperobject(58,10,26-27,"Gerçeği öğrenip benimsedikten sonra, bile bile günah işlemeye devam edersek, günahlar için artık kurban kalmaz; geriye sadece yargının dehşetli beklenişi ve düşmanları yiyip bitirecek kızgın ateş kalır."));
trsuperobject.biblemap.put("b58_10_28", new trsuperobject(58,10,28,"Musanın Yasasını hiçe sayan, iki ya da üç tanığın sözüyle acımasızca öldürülür."));
trsuperobject.biblemap.put("b58_10_29", new trsuperobject(58,10,29,"Eğer bir kimse Tanrı Oğlunu ayaklar altına alır, kendisini kutsal kılan antlaşma kanını bayağı sayar ve lütufkâr Ruha hakaret ederse, bundan ne kadar daha ağır bir cezaya layık görülecek sanırsınız?"));
trsuperobject.biblemap.put("b58_10_30", new trsuperobject(58,10,30,"Çünkü, ‹‹Öç benimdir, karşılığını ben vereceğim›› ve yine, ‹‹Rab halkını yargılayacak›› diyeni tanıyoruz."));
trsuperobject.biblemap.put("b58_10_31", new trsuperobject(58,10,31,"Diri Tanrının eline düşmek korkunç bir şeydir."));
trsuperobject.biblemap.put("b58_10_32", new trsuperobject(58,10,32,"Sizlerse aydınlandıktan sonra acılarla dolu büyük bir mücadeleye dayandığınız o ilk günleri anımsayın."));
trsuperobject.biblemap.put("b58_10_33", new trsuperobject(58,10,33,"Bazen sitemlere, sıkıntılara uğrayıp seyirlik oldunuz, bazen de aynı durumda olanlarla dayanışma içine girdiniz."));
trsuperobject.biblemap.put("b58_10_34", new trsuperobject(58,10,34,"Hem hapistekilerin dertlerine ortak oldunuz, hem de daha iyi ve kalıcı bir malınız olduğunu bilerek mallarınızın yağma edilmesini sevinçle karşıladınız."));
trsuperobject.biblemap.put("b58_10_35", new trsuperobject(58,10,35,"Onun için cesaretinizi yitirmeyin; bu cesaretin ödülü büyüktür."));
trsuperobject.biblemap.put("b58_10_36", new trsuperobject(58,10,36,"Çünkü Tanrının isteğini yerine getirmek ve vaat edilene kavuşmak için dayanma gücüne ihtiyacınız vardır."));
trsuperobject.biblemap.put("b58_10_37", new trsuperobject(58,10,37,"Artık,  ‹‹Gelecek olan pek yakında gelecek  Ve gecikmeyecek."));
trsuperobject.biblemap.put("b58_10_38", new trsuperobject(58,10,38,"Doğru adamım, imanla yaşayacaktır.  Ama geri çekilirse, ondan hoşnut olmayacağım.››"));
trsuperobject.biblemap.put("b58_10_39", new trsuperobject(58,10,39,"Bizler geri çekilip mahvolanlardan değiliz; iman edip canlarının kurtuluşuna kavuşanlardanız."));
trsuperobject.biblemap.put("b58_11_1", new trsuperobject(58,11,1,"İman, umut edilenlere güvenmek, görünmeyen şeylerin varlığından emin olmaktır."));
trsuperobject.biblemap.put("b58_11_2", new trsuperobject(58,11,2,"Atalarımız bununla Tanrının beğenisini kazandılar."));
trsuperobject.biblemap.put("b58_11_3", new trsuperobject(58,11,3,"Evrenin Tanrının buyruğuyla yaratıldığını, böylece görülenlerin görünmeyenlerden oluştuğunu iman sayesinde anlıyoruz."));
trsuperobject.biblemap.put("b58_11_4", new trsuperobject(58,11,4,"Habilin Tanrıya Kayinden daha iyi bir kurban sunması iman sayesinde oldu. İmanı sayesinde doğru biri olarak Tanrının beğenisini kazandı. Çünkü Tanrı onun sunduğu adakları kabul etti. Nitekim Habil ölmüş olduğu halde, iman sayesinde hâlâ konuşmaktadır."));
trsuperobject.biblemap.put("b58_11_5", new trsuperobject(58,11,5,"İman sayesinde Hanok ölümü tatmamak üzere yukarı alındı. Kimse onu bulamadı, çünkü Tanrı onu yukarı almıştı. Yukarı alınmadan önce Tanrıyı hoşnut eden biri olduğuna tanıklık edildi."));
trsuperobject.biblemap.put("b58_11_6", new trsuperobject(58,11,6,"İman olmadan Tanrıyı hoşnut etmek olanaksızdır. Tanrıya yaklaşan, Onun var olduğuna ve kendisini arayanları ödüllendireceğine iman etmelidir."));
trsuperobject.biblemap.put("b58_11_7", new trsuperobject(58,11,7,"İman sayesinde Nuh, henüz olmamış olaylarla ilgili olarak Tanrı tarafından uyarılınca, Tanrı korkusuyla ev halkının kurtuluşu için bir gemi yaptı. Bununla dünyayı yargıladı ve imana dayanan doğruluğun mirasçısı oldu."));
trsuperobject.biblemap.put("b58_11_8", new trsuperobject(58,11,8,"İman sayesinde İbrahim miras alacağı yere gitmesi için çağrılınca, Tanrının sözünü dinledi ve nereye gideceğini bilmeden yola çıktı."));
trsuperobject.biblemap.put("b58_11_9", new trsuperobject(58,11,9,"İman sayesinde bir yabancı olarak vaat edilen ülkeye yerleşti. Aynı vaadin ortak mirasçıları olan İshak ve Yakupla birlikte çadırlarda yaşadı."));
trsuperobject.biblemap.put("b58_11_10", new trsuperobject(58,11,10,"Çünkü mimarı ve kurucusu Tanrı olan temelli kenti bekliyordu."));
trsuperobject.biblemap.put("b58_11_11", new trsuperobject(58,11,11,"İman sayesinde Saranın kendisi de kısır ve yaşı geçmiş olduğu halde vaat edeni güvenilir saydığından çocuk sahibi olmak için güç buldu."));
trsuperobject.biblemap.put("b58_11_12", new trsuperobject(58,11,12,"Böylece tek bir adamdan, üstelik ölüden farksız birinden gökteki yıldızlar, deniz kıyısındaki kum kadar sayısız torun meydana geldi."));
trsuperobject.biblemap.put("b58_11_13", new trsuperobject(58,11,13,"Bu kişilerin hepsi imanlı olarak öldüler. Vaat edilenlere kavuşamadılarsa da bunları uzaktan görüp selamladılar, yeryüzünde yabancı ve konuk olduklarını açıkça kabul ettiler."));
trsuperobject.biblemap.put("b58_11_14", new trsuperobject(58,11,14,"Böyle konuşanlar bir vatan aradıklarını gösteriyorlar."));
trsuperobject.biblemap.put("b58_11_15", new trsuperobject(58,11,15,"Ayrıldıkları ülkeyi düşünselerdi, geri dönmeye fırsatları olurdu."));
trsuperobject.biblemap.put("b58_11_16", new trsuperobject(58,11,16,"Ama onlar daha iyisini, yani göksel olanı arzu ediyorlardı. Bunun içindir ki, Tanrı onların Tanrısı olarak anılmaktan utanmıyor. Çünkü onlara bir kent hazırladı."));
trsuperobject.biblemap.put("b58_11_17", new trsuperobject(58,11,17,"İbrahim sınandığı zaman imanla İshakı kurban olarak sundu. Vaatleri almış olan İbrahim biricik oğlunu kurban etmek üzereydi."));
trsuperobject.biblemap.put("b58_11_18", new trsuperobject(58,11,18,"Oysa Tanrı ona, ‹‹Senin soyun İshakla sürecek›› demişti."));
trsuperobject.biblemap.put("b58_11_19", new trsuperobject(58,11,19,"İbrahim Tanrının ölüleri bile diriltebileceğini düşündü; nitekim İshakı simgesel şekilde ölümden geri aldı."));
trsuperobject.biblemap.put("b58_11_20", new trsuperobject(58,11,20,"İman sayesinde İshak gelecek olaylarla ilgili olarak Yakupla Esavı kutsadı."));
trsuperobject.biblemap.put("b58_11_21", new trsuperobject(58,11,21,"Yakup ölürken iman sayesinde Yusufun iki oğlunu da kutsadı, değneğinin ucuna yaslanarak Tanrıya tapındı."));
trsuperobject.biblemap.put("b58_11_22", new trsuperobject(58,11,22,"Yusuf ölürken iman sayesinde İsrailoğullarının Mısırdan çıkacağını anımsattı ve kemiklerine ilişkin buyruk verdi."));
trsuperobject.biblemap.put("b58_11_23", new trsuperobject(58,11,23,"Musa doğduğunda annesiyle babası onu imanla üç ay gizlediler. Çünkü çocuğun güzel olduğunu gördüler ve kralın fermanından korkmadılar."));
trsuperobject.biblemap.put("b58_11_24", new trsuperobject(58,11,24,"Musa büyüyünce iman sayesinde firavunun kızının oğlu olarak tanınmayı reddetti."));
trsuperobject.biblemap.put("b58_11_25", new trsuperobject(58,11,25,"Bir süre için günahın sefasını sürmektense, Tanrının halkıyla birlikte baskı görmeyi yeğledi."));
trsuperobject.biblemap.put("b58_11_26", new trsuperobject(58,11,26,"Mesih uğruna aşağılanmayı Mısır hazinelerinden daha büyük zenginlik saydı. Çünkü alacağı ödülü düşünüyordu."));
trsuperobject.biblemap.put("b58_11_27", new trsuperobject(58,11,27,"Kralın öfkesinden korkmadan imanla Mısırdan ayrıldı. Görünmez Olanı görür gibi dayandı."));
trsuperobject.biblemap.put("b58_11_28", new trsuperobject(58,11,28,"İlk doğanları öldüren melek İsraillilere dokunmasın diye Musa imanla, Fısıh kurbanının kesilmesini ve kanının kapılara sürülmesini sağladı."));
trsuperobject.biblemap.put("b58_11_29", new trsuperobject(58,11,29,"İman sayesinde İsrailliler karadan geçer gibi Kızıldenizden geçtiler. Mısırlılar bunu deneyince boğuldular."));
trsuperobject.biblemap.put("b58_11_30", new trsuperobject(58,11,30,"İsrailliler yedi gün boyunca Eriha surları çevresinde dolandılar; sonunda imanları sayesinde surlar yıkıldı."));
trsuperobject.biblemap.put("b58_11_31", new trsuperobject(58,11,31,"Fahişe Rahav casusları dostça karşıladığı için imanı sayesinde söz dinlemeyenlerle birlikte öldürülmedi."));
trsuperobject.biblemap.put("b58_11_32", new trsuperobject(58,11,32,"Daha ne diyeyim? Gidyon, Barak, Şimşon, Yiftah, Davut, Samuel ve peygamberlerle ilgili olanları anlatsam, zaman yetmeyecek."));
trsuperobject.biblemap.put("b58_11_33", new trsuperobject(58,11,33,"Bunlar iman sayesinde ülkeler ele geçirdiler, adaleti sağladılar, vaat edilenlere kavuştular, aslanların ağzını kapadılar."));
trsuperobject.biblemap.put("b58_11_34", new trsuperobject(58,11,34,"Kızgın ateşi söndürdüler, kılıcın ağzından kaçıp kurtuldular. Güçsüzlükte kuvvet buldular, savaşta güçlendiler, yabancı orduları bozguna uğrattılar."));
trsuperobject.biblemap.put("b58_11_35", new trsuperobject(58,11,35,"Kadınlar dirilen ölülerini geri aldılar. Başkalarıysa salıverilmeyi reddederek dirilip daha iyi bir yaşama kavuşma umuduyla işkencelere katlandılar."));
trsuperobject.biblemap.put("b58_11_36", new trsuperobject(58,11,36,"Daha başkaları alaya alınıp kamçılandı, hatta zincire vurulup hapsedildi."));
trsuperobject.biblemap.put("b58_11_37", new trsuperobject(58,11,37,"Taşlandılar, testereyle biçildiler, kılıçtan geçirilip öldürüldüler. Koyun postu, keçi derisi içinde dolaştılar, yoksulluk çektiler, sıkıntılara uğradılar, baskı gördüler."));
trsuperobject.biblemap.put("b58_11_38", new trsuperobject(58,11,38,"Dünya onlara layık değildi. Çöllerde, dağlarda, mağaralarda, yeraltı oyuklarında dolanıp durdular."));
trsuperobject.biblemap.put("b58_11_39", new trsuperobject(58,11,39,"İmanları sayesinde bunların hepsi Tanrının beğenisini kazandıkları halde, hiçbiri vaat edilene kavuşmadı."));
trsuperobject.biblemap.put("b58_11_40", new trsuperobject(58,11,40,"Bizden ayrı olarak yetkinliğe ermesinler diye, Tanrı bizim için daha iyi bir şey hazırlamıştı."));
trsuperobject.biblemap.put("b58_12_1", new trsuperobject(58,12,1,"İşte çevremizi bu denli büyük bir tanıklar bulutu sardığına göre, biz de her yükü ve bizi kolayca kuşatan günahı üzerimizden sıyırıp atalım ve önümüze konan yarışı sabırla koşalım."));
trsuperobject.biblemap.put("b58_12_2", new trsuperobject(58,12,2,"Gözümüzü imanımızın öncüsü ve tamamlayıcısı İsaya dikelim. O kendisini bekleyen sevinç uğruna utancı hiçe sayıp çarmıhta ölüme katlandı ve Tanrının tahtının sağında oturdu."));
trsuperobject.biblemap.put("b58_12_3", new trsuperobject(58,12,3,"Yorulup cesaretinizi yitirmemek için, günahkârların bunca karşı koymasına katlanmış Olanı düşünün."));
trsuperobject.biblemap.put("b58_12_4", new trsuperobject(58,12,4,"Günaha karşı verdiğiniz mücadelede henüz kanınızı akıtacak kadar direnmiş değilsiniz."));
trsuperobject.biblemap.put("b58_12_5", new trsuperobject(58,12,5,"Size oğullar diye seslenen şu öğüdü de unuttunuz:  ‹‹Oğlum, Rabbin terbiye edişini hafife alma,  Rab seni azarlayınca cesaretini yitirme."));
trsuperobject.biblemap.put("b58_12_6", new trsuperobject(58,12,6,"Çünkü Rab sevdiğini terbiye eder,  Oğulluğa kabul ettiği herkesi cezalandırır.››"));
trsuperobject.biblemap.put("b58_12_7", new trsuperobject(58,12,7,"Terbiye edilmek uğruna acılara katlanmalısınız. Tanrı size oğullarına davranır gibi davranıyor. Hangi oğul babası tarafından terbiye edilmez?"));
trsuperobject.biblemap.put("b58_12_8", new trsuperobject(58,12,8,"Herkesin gördüğü terbiyeden yoksunsanız, oğullar değil, yasadışı evlatlarsınız."));
trsuperobject.biblemap.put("b58_12_9", new trsuperobject(58,12,9,"Kaldı ki, bizi terbiye eden dünyasal babalarımız vardı ve onlara saygı duyardık. Öyleyse Ruhlar Babasına bağımlı olup yaşamamız çok daha önemli değil mi?"));
trsuperobject.biblemap.put("b58_12_10", new trsuperobject(58,12,10,"Babalarımız bizi kısa bir süre için, uygun gördükleri gibi terbiye ettiler. Ama Tanrı, kutsallığına ortak olalım diye bizi kendi yararımıza terbiye ediyor."));
trsuperobject.biblemap.put("b58_12_11", new trsuperobject(58,12,11,"Terbiye edilmek başlangıçta hiç tatlı gelmez, acı gelir. Ne var ki, böyle eğitilenler için bu sonradan esenlik veren doğruluğu üretir."));
trsuperobject.biblemap.put("b58_12_12-13", new trsuperobject(58,12,12-13,"Bunun için sarkık ellerinizi kaldırın, bükük dizlerinizi doğrultun, ayaklarınız için düz yollar yapın. Öyle ki, kötürüm olan parça eklemden çıkmasın, tersine şifa bulsun."));
trsuperobject.biblemap.put("b58_12_14", new trsuperobject(58,12,14,"Herkesle barış içinde yaşamaya, kutsal olmaya gayret edin. Kutsallığa sahip olmadan kimse Rabbi göremeyecek."));
trsuperobject.biblemap.put("b58_12_15", new trsuperobject(58,12,15,"Dikkat edin, kimse Tanrının lütfundan yoksun kalmasın. İçinizde sizi rahatsız edecek ve birçoklarını zehirleyecek acı bir kök filizlenmesin."));
trsuperobject.biblemap.put("b58_12_16", new trsuperobject(58,12,16,"Kimse fuhuş yapmasın ya da ilk oğulluk hakkını bir yemeğe karşılık satan Esav gibi kutsal değerlere saygısızlık etmesin."));
trsuperobject.biblemap.put("b58_12_17", new trsuperobject(58,12,17,"Biliyorsunuz, Esav daha sonra kutsanma hakkını miras almak istediyse de geri çevrildi. Kutsanmak için gözyaşı döküp yalvarmasına karşın, vermiş olduğu kararın sonucunu değiştiremedi."));
trsuperobject.biblemap.put("b58_12_18-19", new trsuperobject(58,12,18-19,"Sizler dokunulabilen, alev alev yanan dağa, karanlığa, koyu karanlık ve kasırgaya, gürleyen çağrı borusuna, tanrısal sözleri ileten sese yaklaşmış değilsiniz. O sesi işitenler, kendilerine bir sözcük daha söylenmesin diye yalvardılar."));
trsuperobject.biblemap.put("b58_12_20", new trsuperobject(58,12,20,"‹‹Dağa bir hayvan bile dokunsa taşlanacak›› buyruğuna dayanamadılar."));
trsuperobject.biblemap.put("b58_12_21", new trsuperobject(58,12,21,"Görünüm öyle korkunçtu ki, Musa, ‹‹Çok korkuyorum, titriyorum›› dedi."));
trsuperobject.biblemap.put("b58_12_22-24", new trsuperobject(58,12,22-24,"Oysa sizler Siyon Dağına, yaşayan Tanrının kenti olan göksel Yeruşalime, bir bayram şenliği içindeki on binlerce meleğe, adları göklerde yazılmış ilk doğanların topluluğuna yaklaştınız. Herkesin yargıcı olan Tanrıya, yetkinliğe erdirilmiş doğru kişilerin ruhlarına, yeni antlaşmanın aracısı olan İsaya ve Habilin kanından daha üstün bir anlam taşıyan serpmelik kana yaklaştınız."));
trsuperobject.biblemap.put("b58_12_25", new trsuperobject(58,12,25,"Bunları söyleyeni reddetmemeye dikkat edin. Çünkü yeryüzünde kendilerini uyaranı reddedenler kurtulamadılarsa, göklerden bizi uyarandan yüz çevirirsek, bizim de kurtulamayacağımız çok daha kesindir."));
trsuperobject.biblemap.put("b58_12_26", new trsuperobject(58,12,26,"O zaman Onun sesi yeri sarsmıştı. Ama şimdi, ‹‹Bir kez daha yalnız yeri değil, göğü de sarsacağım›› diye söz vermiştir."));
trsuperobject.biblemap.put("b58_12_27", new trsuperobject(58,12,27,"‹‹Bir kez daha›› sözü, sarsılanların, yani yaratılmış olan şeylerin ortadan kaldırılacağını, böylelikle sarsılmayanların kalacağını anlatıyor."));
trsuperobject.biblemap.put("b58_12_28", new trsuperobject(58,12,28,"Böylece sarsılmaz bir egemenliğe kavuştuğumuz için minnettar olalım. Öyle ki, Tanrıyı hoşnut edecek biçimde saygı ve korkuyla tapınalım."));
trsuperobject.biblemap.put("b58_12_29", new trsuperobject(58,12,29,"Çünkü Tanrımız yakıp yok eden bir ateştir."));
trsuperobject.biblemap.put("b58_13_1", new trsuperobject(58,13,1,"Kardeş sevgisi sürekli olsun."));
trsuperobject.biblemap.put("b58_13_2", new trsuperobject(58,13,2,"Konuksever olmaktan geri kalmayın. Çünkü bu sayede bazıları bilmeden melekleri konuk ettiler."));
trsuperobject.biblemap.put("b58_13_3", new trsuperobject(58,13,3,"Hapiste olanları, onlarla birlikte hapsedilmiş gibi anımsayın. Sizin de bir bedeniniz olduğunu düşünerek baskı görenleri hatırlayın."));
trsuperobject.biblemap.put("b58_13_4", new trsuperobject(58,13,4,"Herkes evliliğe saygı göstersin. Evlilik yatağı günahla lekelenmesin. Çünkü Tanrı fuhuş yapanları, zina edenleri yargılayacak."));
trsuperobject.biblemap.put("b58_13_5", new trsuperobject(58,13,5,"Yaşayışınız para sevgisinden uzak olsun. Sahip olduklarınızla yetinin. Çünkü Tanrı şöyle dedi:  ‹‹Seni asla terk etmeyeceğim,  Seni asla yüzüstü bırakmayacağım.››"));
trsuperobject.biblemap.put("b58_13_6", new trsuperobject(58,13,6,"Böylece cesaretle diyoruz ki,  ‹‹Rab benim yardımcımdır, korkmam;  İnsan bana ne yapabilir?››"));
trsuperobject.biblemap.put("b58_13_7", new trsuperobject(58,13,7,"Tanrının sözünü size iletmiş olan önderlerinizi anımsayın. Yaşayışlarının sonucuna bakarak onların imanını örnek alın."));
trsuperobject.biblemap.put("b58_13_8", new trsuperobject(58,13,8,"İsa Mesih dün, bugün ve sonsuza dek aynıdır."));
trsuperobject.biblemap.put("b58_13_9", new trsuperobject(58,13,9,"Çeşitli garip öğretilerin etkisine kapılıp sürüklenmeyin. Yüreğin yiyeceklerle değil, Tanrı lütfuyla güçlenmesi iyidir. Yiyeceklere güvenenler hiçbir yarar görmediler."));
trsuperobject.biblemap.put("b58_13_10", new trsuperobject(58,13,10,"Bir sunağımız var ki, tapınma çadırında hizmet edenlerin ondan yemeye hakları yoktur."));
trsuperobject.biblemap.put("b58_13_11", new trsuperobject(58,13,11,"Başkâhin günah sunusu olarak hayvanların kanını kutsal yere taşır, ama bu hayvanların cesetleri ordugahın dışında yakılır."));
trsuperobject.biblemap.put("b58_13_12", new trsuperobject(58,13,12,"Bunun gibi, İsa da kendi kanıyla halkı kutsal kılmak için kent kapısının dışında acı çekti."));
trsuperobject.biblemap.put("b58_13_13", new trsuperobject(58,13,13,"Öyleyse biz de Onun uğradığı aşağılanmaya katlanarak ordugahtan dışarıya çıkıp yanına gidelim."));
trsuperobject.biblemap.put("b58_13_14", new trsuperobject(58,13,14,"Çünkü burada kalıcı bir kentimiz yoktur, biz gelecekteki kenti özlüyoruz."));
trsuperobject.biblemap.put("b58_13_15", new trsuperobject(58,13,15,"Bu nedenle, İsa aracılığıyla Tanrıya sürekli övgü kurbanları, yani Onun adını açıkça anan dudakların meyvesini sunalım."));
trsuperobject.biblemap.put("b58_13_16", new trsuperobject(58,13,16,"İyilik yapmayı, sizde olanı başkalarıyla paylaşmayı unutmayın. Çünkü Tanrı bu tür kurbanlardan hoşnut olur."));
trsuperobject.biblemap.put("b58_13_17", new trsuperobject(58,13,17,"Önderlerinizin sözünü dinleyin, onlara bağlı kalın. Çünkü onlar canlarınız için hesap verecek kişiler olarak sizi kollarlar. Onların sözünü dinleyin ki, görevlerini inleyerek değil -bunun size yararı olmaz- sevinçle yapsınlar."));
trsuperobject.biblemap.put("b58_13_18", new trsuperobject(58,13,18,"Bizim için dua edin. Vicdanımızı temiz tuttuğumuza, her bakımdan olumlu bir yaşam sürmek istediğimize eminiz."));
trsuperobject.biblemap.put("b58_13_19", new trsuperobject(58,13,19,"Yanınıza tez zamanda dönebilmem için dua etmenizi özellikle rica ediyorum."));
trsuperobject.biblemap.put("b58_13_20", new trsuperobject(58,13,20,"Esenlik veren Tanrı, koyunların büyük Çobanını, Rabbimiz İsayı sonsuza dek sürecek antlaşmanın kanıyla ölümden diriltti."));
trsuperobject.biblemap.put("b58_13_21", new trsuperobject(58,13,21,"Tanrı, isteğini yerine getirebilmeniz için sizi her iyilikle donatsın; kendisini hoşnut eden şeyi İsa Mesih aracılığıyla bizlerde gerçekleştirsin. Mesihe sonsuzlara dek yücelik olsun! Amin."));
trsuperobject.biblemap.put("b58_13_22", new trsuperobject(58,13,22,"Kardeşler, size rica ediyorum, öğütlerimi hoş görün. Zaten size kısaca yazdım."));
trsuperobject.biblemap.put("b58_13_23", new trsuperobject(58,13,23,"Kardeşimiz Timoteosun salıverildiğini bilmenizi istiyorum. Yakında yanıma gelirse, onunla birlikte sizi görmeye geleceğim."));
trsuperobject.biblemap.put("b58_13_24", new trsuperobject(58,13,24,"Önderlerinizin hepsine ve bütün kutsallara selam söyleyin. İtalyadan olanlar size selam ederler."));
trsuperobject.biblemap.put("b58_13_25", new trsuperobject(58,13,25,"Tanrı'nın lütfu hepinizle birlikte olsun! Amin."));

