trsuperobject.biblemap.put("b6_5_4", new trsuperobject(6,5,4,"Bunu yapmasının nedeni şuydu: İsrailliler Mısırdan çıktıklarında savaşabilecek yaştaki bütün erkekler, Mısırdan çıktıktan sonra çölden geçerken ölmüşlerdi."));
trsuperobject.biblemap.put("b6_5_5", new trsuperobject(6,5,5,"Mısırdan çıkan erkeklerin hepsi sünnetliydi. Ama Mısırdan çıktıktan sonra yolda, çölde doğan erkeklerin hiçbiri sünnet olmamıştı."));
trsuperobject.biblemap.put("b6_5_6", new trsuperobject(6,5,6,"İsrailliler Mısırdan çıktıklarında savaşacak yaşta olanların tümü ölünceye dek çölde kırk yıl dolaştılar. Çünkü RABbin sözünü dinlememişlerdi. RAB bize verilmek üzere atalarımıza söz verdiği süt ve bal akan ülkeyi onlara göstermeyeceğine ant içmişti."));
trsuperobject.biblemap.put("b6_5_8", new trsuperobject(6,5,8,"Bütün erkekler sünnet edildikten sonra yaraları iyileşinceye dek ordugahta kaldılar."));
trsuperobject.biblemap.put("b6_5_10", new trsuperobject(6,5,10,"Gilgalda, Eriha ovalarında konaklamış olan İsrail halkı, ayın on dördüncü gününün akşamı Fısıh Bayramını kutladı."));
trsuperobject.biblemap.put("b6_5_11", new trsuperobject(6,5,11,"Bayramın ertesi günü, tam o gün, ülkenin ürününden mayasız ekmek yaptılar ve kavrulmuş başak yediler."));
trsuperobject.biblemap.put("b6_5_12", new trsuperobject(6,5,12,"Ülkenin ürününden yemeleri üzerine ertesi gün man kesildi. Man kesilince İsrailliler o yıl Kenan topraklarının ürünüyle beslendiler."));
trsuperobject.biblemap.put("b6_5_13", new trsuperobject(6,5,13,"Yeşu Erihanın yakınındaydı. Başını kaldırınca önünde, kılıcını çekmiş bir adam gördü. Ona yaklaşarak, ‹‹Sen bizden misin, karşı taraftan mı?›› diye sordu."));
trsuperobject.biblemap.put("b6_5_14", new trsuperobject(6,5,14,"Adam, ‹‹Hiçbiri›› dedi, ‹‹Ben RABbin ordusunun komutanıyım. Şimdi geldim.›› O zaman Yeşu yüzüstü yere kapanıp ona tapındı. ‹‹Efendimin kuluna buyruğu nedir?›› diye sordu."));
trsuperobject.biblemap.put("b6_5_15", new trsuperobject(6,5,15,"RAB'bin ordusunun komutanı, ‹‹Çarığını çıkar›› dedi, ‹‹Çünkü bastığın yer kutsaldır.›› Yeşu söyleneni yaptı."));
trsuperobject.biblemap.put("b6_6_1", new trsuperobject(6,6,1,"Eriha Kentinin kapıları İsrailliler yüzünden sımsıkı kapatılmıştı. Ne giren vardı, ne de çıkan."));
trsuperobject.biblemap.put("b6_6_3", new trsuperobject(6,6,3,"‹‹Siz savaşçılar, kentin çevresini günde bir kez olmak üzere altı gün dolanacaksınız."));
trsuperobject.biblemap.put("b6_6_4", new trsuperobject(6,6,4,"Koç boynuzundan yapılmış birer boru taşıyan yedi kâhin sandığın önünden gitsin. Yedinci gün kentin çevresini yedi kez dolanın; bu arada kâhinler borularını çalsınlar."));
trsuperobject.biblemap.put("b6_6_5", new trsuperobject(6,6,5,"Kâhinlerin koç boynuzu borularını uzun uzun çaldıklarını işittiğinizde, bütün halk yüksek sesle bağırsın. O zaman kentin surları çökecek ve herkes bulunduğu yerden dosdoğru kente girecek.››"));
trsuperobject.biblemap.put("b6_6_6", new trsuperobject(6,6,6,"Nun oğlu Yeşu kâhinleri çağırıp, ‹‹RABbin Antlaşma Sandığını alın›› dedi, ‹‹Yedi kâhin, ellerinde koç boynuzu borularla sandığın önünde yürüsün.››"));
trsuperobject.biblemap.put("b6_6_7", new trsuperobject(6,6,7,"Sonra halka, ‹‹Kalkın, kentin çevresini dolanmaya başlayın›› dedi, ‹‹Silahlı öncüler RABbin Sandığının önünden gitsin.››"));
trsuperobject.biblemap.put("b6_6_8", new trsuperobject(6,6,8,"Yeşunun bunları halka söylemesinden sonra, koç boynuzu birer boru taşıyan yedi kâhin borularını çalarak RABbin önünde ilerlemeye başladılar. Onları RABbin Antlaşma Sandığı izliyordu."));
trsuperobject.biblemap.put("b6_6_9", new trsuperobject(6,6,9,"Silahlı öncüler boru çalan kâhinlerin önünden, artçılar da sandığın arkasından ilerliyor, bu arada borular çalınıyordu."));
trsuperobject.biblemap.put("b6_6_10", new trsuperobject(6,6,10,"Yeşu halka şu buyruğu verdi: ‹‹Savaş naraları atmayın, sesinizi yükseltmeyin. ‹Bağırın› diyeceğim güne dek ağzınızdan tek bir söz çıkmasın. Buyruğumu duyunca bağırın.››"));
trsuperobject.biblemap.put("b6_6_11", new trsuperobject(6,6,11,"Halk RABbin Sandığıyla birlikte kentin çevresini bir kez dolandı, sonra ordugaha dönüp geceyi orada geçirdi."));
trsuperobject.biblemap.put("b6_6_12", new trsuperobject(6,6,12,"Ertesi sabah Yeşu erkenden kalktı. Kâhinler de RABbin Sandığını yüklendiler."));
trsuperobject.biblemap.put("b6_6_13", new trsuperobject(6,6,13,"Koç boynuzu borular taşıyan yedi kâhin RABbin Sandığının önünde ilerliyor, bir yandan da borularını çalıyorlardı. Silahlı öncüler onların önünden gidiyor, artçılar da RABbin Sandığını izliyordu. Bu arada borular sürekli çalınıyordu."));
trsuperobject.biblemap.put("b6_6_14", new trsuperobject(6,6,14,"Böylece ikinci gün de kentin çevresini bir kez dolanıp ordugaha döndüler. Aynı şeyi altı gün yinelediler."));
trsuperobject.biblemap.put("b6_6_15", new trsuperobject(6,6,15,"Yedinci gün erkenden, şafak sökerken kalkıp kentin çevresini aynı şekilde yedi kez dolandılar. Kentin çevresini yalnız o gün yedi kez dolandılar."));
trsuperobject.biblemap.put("b6_6_16", new trsuperobject(6,6,16,"Kâhinler yedinci turda borularını çalınca, Yeşu halka, ‹‹Bağırın! RAB kenti size verdi›› dedi,"));
trsuperobject.biblemap.put("b6_6_17", new trsuperobject(6,6,17,"‹‹Kent, içindeki her şeyle birlikte, RABbe koşulsuz adanmıştır. Yalnız gönderdiğimiz ulakları saklamış olan fahişe Rahavla evindekiler sağ bırakılacak."));
trsuperobject.biblemap.put("b6_6_18", new trsuperobject(6,6,18,"Sakın RABbe adanan herhangi bir şeye el sürmeyin. Adadığınız şeyleri alırsanız İsrailin ordugahını felakete ve yıkıma sürüklersiniz."));
trsuperobject.biblemap.put("b6_6_19", new trsuperobject(6,6,19,"Bütün altınla gümüş, tunç ve demir eşya RABbe ayrılmıştır. Bunlar RABbin hazinesine girecek.››"));
trsuperobject.biblemap.put("b6_6_20", new trsuperobject(6,6,20,"Halk bağırmaya başladı, kâhinler de borularını çaldılar. Boru sesini işiten halk daha yüksek sesle bağırdı. Kentin surları çöktü. Herkes bulunduğu yerden dosdoğru kente girdi. Böylece kenti ele geçirdiler."));
trsuperobject.biblemap.put("b6_6_21", new trsuperobject(6,6,21,"Kadın erkek, genç yaşlı, küçük ve büyük baş hayvanlardan eşeklere dek, kentte ne kadar canlı varsa, hepsini kılıçtan geçirip yok ettiler."));
trsuperobject.biblemap.put("b6_6_22", new trsuperobject(6,6,22,"Yeşu ülkeye casus olarak gönderdiği iki adama, ‹‹O fahişenin evine gidin, ant içtiğiniz gibi, kadını ve bütün yakınlarını dışarı çıkarın›› dedi."));
trsuperobject.biblemap.put("b6_6_23", new trsuperobject(6,6,23,"Eve giren genç casuslar Rahavı, annesini, babasını, erkek kardeşleriyle bütün akrabalarını ve kendisine ait olan her şeyi alıp İsrail ordugahının yakınına getirdiler."));
trsuperobject.biblemap.put("b6_6_24", new trsuperobject(6,6,24,"Sonra kenti içindekilerle birlikte ateşe verdiler. Ancak altını ve gümüşü, tunç ve demir eşyayı RABbin Tapınağının hazinesine koydular."));
trsuperobject.biblemap.put("b6_6_25", new trsuperobject(6,6,25,"Yeşu fahişe Rahava, babasının ev halkıyla yakınlarına dokunmadı. Yeşunun Erihayı araştırmak için gönderdiği ulakları saklayan Rahav, bugün de İsraillilerin arasında yaşıyor."));
trsuperobject.biblemap.put("b6_6_26", new trsuperobject(6,6,26,"Bundan sonra Yeşu şöyle ant içti: ‹‹Bu kenti, Erihayı yeniden kurmaya kalkışan, RABbin lanetine uğrasın. Buna kalkışan kişi büyük oğlunu kaybetme pahasına temel atacak, en küçük oğlunu kaybetme pahasına da kentin kapılarını yerine takacak.››"));
trsuperobject.biblemap.put("b6_6_27", new trsuperobject(6,6,27,"RAB Yeşu'yla birlikteydi. Yeşu'nun ünü ülkenin her yanına yayıldı."));
trsuperobject.biblemap.put("b6_7_1", new trsuperobject(6,7,1,"Ne var ki, İsrailliler adanan eşyalar konusunda RABbe ihanet ettiler. Yahuda oymağından Zerah oğlu, Zavdi oğlu, Karmi oğlu Akan adanmış eşyaların bazılarını alınca, RAB İsraillilere öfkelendi."));
trsuperobject.biblemap.put("b6_7_2", new trsuperobject(6,7,2,"Yeşu, Erihadan Beytelin doğusunda, Beytaven yakınındaki Ay Kentine adamlar göndererek, ‹‹Gidip ülkeyi araştırın›› dedi. Adamlar da gidip Ay Kentini araştırdılar."));
trsuperobject.biblemap.put("b6_7_3", new trsuperobject(6,7,3,"Sonra Yeşunun yanına dönerek ona, ‹‹Bütün halkın oraya gidip yorulmasına gerek yok›› dediler, ‹‹Sayısı az olan Ay halkını yenmeye iki üç bin kişi yeter.››"));
trsuperobject.biblemap.put("b6_7_4", new trsuperobject(6,7,4,"Kentin üzerine yürüyen üç bin kadar İsrailli, Ay halkının önünde kaçmaya başladı."));
trsuperobject.biblemap.put("b6_7_5", new trsuperobject(6,7,5,"Ay halkı onlardan otuz altı kadarını öldürdü, sağ kalanları da kentin kapısından Şevarime dek kovaladı. Bayırdan aşağı kaçanları öldürdü. Korkudan İsraillilerin dizlerinin bağı çözüldü."));
trsuperobject.biblemap.put("b6_7_6", new trsuperobject(6,7,6,"Bunun üzerine Yeşu giysilerini yırtarak İsrailin ileri gelenleriyle birlikte başından aşağı toprak döküp RABbin Sandığının önünde yüzüstü yere kapandı ve akşama dek bu durumda kaldı."));
trsuperobject.biblemap.put("b6_7_7", new trsuperobject(6,7,7,"Ardından şöyle dedi: ‹‹Ey Egemen RAB, bizi Amorluların eline teslim edip yok etmek için mi Şeria Irmağından geçirdin? Keşke halimize razı olup ırmağın ötesinde kalsaydık."));
trsuperobject.biblemap.put("b6_7_8", new trsuperobject(6,7,8,"Ya Rab, İsrail halkı dönüp düşmanlarının önünden kaçtıktan sonra ben ne diyebilirim!"));
trsuperobject.biblemap.put("b6_7_9", new trsuperobject(6,7,9,"Kenanlılar ve ülkede yaşayan öbür halklar bunu duyunca çevremizi kuşatacak, adımızı yeryüzünden silecekler. Ya sen, ya Rab, kendi yüce adın için ne yapacaksın?››"));
trsuperobject.biblemap.put("b6_7_10", new trsuperobject(6,7,10,"RAB Yeşuya şöyle karşılık verdi: ‹‹Ayağa kalk! Neden böyle yüzüstü yere kapanıyorsun?"));
trsuperobject.biblemap.put("b6_7_11", new trsuperobject(6,7,11,"İsrailliler günah işlediler. Onlarla yaptığım ve yerine getirmelerini buyurduğum antlaşmayı bozdular. Koşulsuz adanmış eşyaların bir kısmını çalıp kendi eşyaları arasına gizlediler ve yalan söylediler."));
trsuperobject.biblemap.put("b6_7_12", new trsuperobject(6,7,12,"İşte bu yüzden İsrailliler düşmana karşı tutunamıyor, arkalarını dönüp düşmanlarının önünden kaçıyor. Çünkü lanete uğradılar. Sizde bulunan adanmış eşyaları yok etmezseniz, artık sizinle birlikte olmayacağım."));
trsuperobject.biblemap.put("b6_7_13", new trsuperobject(6,7,13,"Kalk, halkı kutsa ve onlara de ki, ‹Kendinizi yarın için kutsayın. Çünkü İsrailin Tanrısı RAB şöyle diyor: Ey İsrail, adanmış eşyaların bir kısmını aldınız. Bunları yok etmedikçe düşmanlarınızın karşısında dayanamazsınız.›"));
trsuperobject.biblemap.put("b6_7_14", new trsuperobject(6,7,14,"Sabah olunca oymak oymak dizilip sırayla öne çıkacaksınız. RABbin belirleyeceği oymak, boy boy öne çıkacak. RABbin belirleyeceği boy, aile aile öne çıkacak. Yine RABbin belirleyeceği ailenin erkekleri teker teker öne çıkacak."));
trsuperobject.biblemap.put("b6_7_15", new trsuperobject(6,7,15,"Adanmış eşyaları aldığı belirlenen kişi, kendisine ait her şeyle birlikte ateşe atılacak. Çünkü RABbin Antlaşmasını bozup İsrailde iğrenç bir günah işledi.››"));
trsuperobject.biblemap.put("b6_7_16", new trsuperobject(6,7,16,"Sabah erkenden kalkan Yeşu, İsrail halkını oymak oymak öne çıkardı. Bunlardan Yahuda oymağı belirlendi."));
trsuperobject.biblemap.put("b6_7_17", new trsuperobject(6,7,17,"Yahuda boylarını teker teker öne çıkardığında, Zerah boyu belirlendi. Zerahlılar aile aile öne çıkarıldığında Zavdi ailesi belirlendi."));
trsuperobject.biblemap.put("b6_7_18", new trsuperobject(6,7,18,"Zavdi ailesinin erkekleri teker teker öne çıkarıldığında Yahuda oymağından Zerah oğlu, Zavdi oğlu, Karmi oğlu Akan belirlendi."));
trsuperobject.biblemap.put("b6_7_19", new trsuperobject(6,7,19,"O zaman Yeşu Akana, ‹‹Oğlum›› dedi, ‹‹İsrailin Tanrısı RABbin hakkı için doğruyu söyle, ne yaptın, söyle bana, benden gizleme.›› İbranice ‹‹İsrailin Tanrısı RABbe yücelik ver››. Bu deyim birine ant içirmek için kullanılırdı."));
trsuperobject.biblemap.put("b6_7_20", new trsuperobject(6,7,20,"Akan, ‹‹Doğru›› diye karşılık verdi, ‹‹İsrailin Tanrısı RABbe karşı günah işledim. Yaptığım şu:"));
trsuperobject.biblemap.put("b6_7_21", new trsuperobject(6,7,21,"Ganimetin içinde Şinarfç işi güzel bir kaftan, iki yüz şekel gümüş, elli şekel ağırlığında bir külçe altın görünce dayanamayıp aldım. En altta gümüş olmak üzere, tümünü çadırımın ortasında toprağa gömdüm.››"));
trsuperobject.biblemap.put("b6_7_22", new trsuperobject(6,7,22,"Yeşunun görevlendirdiği adamlar hemen çadıra koştular. Gömülmüş eşyaları orada buldular. Gümüş en alttaydı."));
trsuperobject.biblemap.put("b6_7_23", new trsuperobject(6,7,23,"Tümünü çadırdan çıkardılar, Yeşuya ve İsrail halkına getirip RABbin önünde yere serdiler."));
trsuperobject.biblemap.put("b6_7_24", new trsuperobject(6,7,24,"Yeşu ile İsrail halkı, Zerah oğlu Akanı, gümüşü, altın külçeyi, kaftanı, Akanın oğullarıyla kızlarını, sığır ve davarlarıyla eşeğini, çadırıyla bütün eşyalarını alıp Akor Vadisine götürdüler."));
trsuperobject.biblemap.put("b6_7_25", new trsuperobject(6,7,25,"Yeşu Akana, ‹‹Bizi neden bu felakete sürükledin?›› dedi, ‹‹RAB de bugün seni felakete sürükleyecek.›› Ardından bütün İsrail halkı Akanı taşa tuttu; kendisine ait ne varsa taşlayıp yaktı."));
trsuperobject.biblemap.put("b6_7_26", new trsuperobject(6,7,26,"Akan'ın üzerine taşlardan büyük bir yığın yaptılar. Bu yığın bugün de duruyor. Bunun üzerine RAB'bin öfkesi dindi. Oranın bugün de Akor Vadisi diye anılmasının nedeni budur."));
trsuperobject.biblemap.put("b6_8_2", new trsuperobject(6,8,2,"Erihaya ve kralına ne yaptıysan, Ay Kentine ve kralına da aynısını yap. Ama mal ve hayvanlardan oluşan ganimeti kendinize ayırın. Kentin gerisinde pusu kur.››"));
trsuperobject.biblemap.put("b6_8_3", new trsuperobject(6,8,3,"Böylece Yeşu bütün savaşçılarıyla birlikte Ay Kentinin üzerine yürümeye hazırlandı. Seçtiği otuz bin yiğit savaşçıyı geceleyin yola çıkarırken"));
trsuperobject.biblemap.put("b6_8_4", new trsuperobject(6,8,4,"onlara şöyle buyurdu: ‹‹Gidip kentin gerisinde pusuya yatın. Kentin çok uzağında durmayın. Hepiniz her an hazır olun."));
trsuperobject.biblemap.put("b6_8_5", new trsuperobject(6,8,5,"Ben yanımdaki halkla birlikte kente yaklaşacağım. Bir önceki gibi, düşman kentten çıkıp üzerimize gelince, önlerinde kaçar gibi yapıp"));
trsuperobject.biblemap.put("b6_8_6", new trsuperobject(6,8,6,"onları kentten uzaklaştırıncaya dek ardımızdan sürükleyeceğiz. Önceki gibi onlardan kaçtığımızı sanacaklar. Biz kaçar gibi yaparken,"));
trsuperobject.biblemap.put("b6_8_7", new trsuperobject(6,8,7,"siz de pusu kurduğunuz yerden çıkıp kenti ele geçirirsiniz. Tanrımız RAB orayı elinize teslim edecek."));
trsuperobject.biblemap.put("b6_8_8", new trsuperobject(6,8,8,"Kenti ele geçirince ateşe verin. RABbin buyruğuna göre hareket edin. İşte buyruğum budur.››"));
trsuperobject.biblemap.put("b6_8_9", new trsuperobject(6,8,9,"Ardından Yeşu onları yolcu etti. Adamlar gidip Beytel ile Ay Kenti arasında, Ay Kentinin batısında pusuya yattılar. Yeşu ise geceyi halkla birlikte geçirdi."));
trsuperobject.biblemap.put("b6_8_10", new trsuperobject(6,8,10,"Yeşu sabah erkenden kalkarak halkı topladı. Sonra kendisi ve İsrailin ileri gelenleri önde olmak üzere Ay Kentine doğru yola çıktılar."));
trsuperobject.biblemap.put("b6_8_11", new trsuperobject(6,8,11,"Yeşu, yanındaki bütün savaşçılarla kentin üzerine yürüdü. Yaklaşıp kentin kuzeyinde ordugah kurdular. Kentle aralarında bir vadi vardı."));
trsuperobject.biblemap.put("b6_8_12", new trsuperobject(6,8,12,"Yeşu beş bin kişi kadar bir güce Beytel ile Ay Kenti arasında, kentin batısında pusu kurdurdu."));
trsuperobject.biblemap.put("b6_8_13", new trsuperobject(6,8,13,"Ardından hem kuzeyde ordugah kuranlar, hem batıda pusuya yatanlar savaş düzenine girdiler. Yeşu o gece vadide ilerledi."));
trsuperobject.biblemap.put("b6_8_14", new trsuperobject(6,8,14,"Bunu gören Ay Kralı, kent halkıyla birlikte sabah erkenden kalktı. Zaman yitirmeden, İsraillilere karşı savaşmak üzere Arava bölgesinin karşısında belirlenen yere çıktı. Ne var ki, kentin gerisinde kendisine karşı kurulan pusudan habersizdi."));
trsuperobject.biblemap.put("b6_8_15", new trsuperobject(6,8,15,"Yeşu ile yanındaki İsrailliler, kent halkı önünde bozguna uğramış gibi, çöle doğru kaçmaya başladılar."));
trsuperobject.biblemap.put("b6_8_16", new trsuperobject(6,8,16,"Kentteki bütün halk İsraillileri kovalamaya çağrıldı. Ama Yeşuyu kovalarken kentten uzaklaştılar."));
trsuperobject.biblemap.put("b6_8_17", new trsuperobject(6,8,17,"Ay Kentiyle Beytelden İsraillileri kovalamaya çıkmayan tek kişi kalmamıştı. İsraillileri kovalamaya çıkarlarken kent kapılarını açık bıraktılar."));
trsuperobject.biblemap.put("b6_8_18", new trsuperobject(6,8,18,"RAB Yeşuya, ‹‹Elindeki palayı Ay Kentine doğru uzat; orayı senin eline teslim ediyorum›› dedi. Yeşu elindeki palayı kente doğru uzattı."));
trsuperobject.biblemap.put("b6_8_19", new trsuperobject(6,8,19,"Elini uzatır uzatmaz, pusudakiler yerlerinden fırlayıp kente girdiler; kenti ele geçirip hemen ateşe verdiler."));
trsuperobject.biblemap.put("b6_8_20", new trsuperobject(6,8,20,"Kentliler arkalarına dönüp bakınca, yanan kentten göklere yükselen dumanı gördüler. Çöle doğru kaçan İsrailliler de geri dönüp onlara saldırınca artık kaçacak hiçbir yerleri kalmadı."));
trsuperobject.biblemap.put("b6_8_21", new trsuperobject(6,8,21,"Pusuya yatmış olanların kenti ele geçirdiğini, kentten dumanlar yükseldiğini gören Yeşu ile yanındaki İsrailliler, geri dönüp Ay halkına saldırdılar."));
trsuperobject.biblemap.put("b6_8_22", new trsuperobject(6,8,22,"Kenti ele geçirenler de çıkıp saldırıya katılınca, kent halkı iki yönden gelen İsraillilerin ortasında kaldı. İsrailliler tek canlı bırakmadan hepsini öldürdüler."));
trsuperobject.biblemap.put("b6_8_23", new trsuperobject(6,8,23,"Sağ olarak tutsak aldıkları Ay Kralını Yeşunun önüne çıkardılar."));
trsuperobject.biblemap.put("b6_8_24", new trsuperobject(6,8,24,"İsrailliler Ay Kentinden çıkıp kendilerini kırsal alanlarda ve çölde kovalayanların hepsini kılıçtan geçirdikten sonra kente dönüp geri kalanları da kılıçtan geçirdiler."));
trsuperobject.biblemap.put("b6_8_25", new trsuperobject(6,8,25,"O gün Ay halkının tümü öldürüldü. Öldürülenlerin toplamı, kadın erkek, on iki bin kişiydi."));
trsuperobject.biblemap.put("b6_8_26", new trsuperobject(6,8,26,"Yeşu kentte yaşayanların tümü yok edilinceye dek pala tutan elini indirmedi."));
trsuperobject.biblemap.put("b6_8_27", new trsuperobject(6,8,27,"İsrailliler, RABbin Yeşuya verdiği buyruk uyarınca, kentin yalnız hayvanlarıyla mallarını yağmaladılar."));
trsuperobject.biblemap.put("b6_8_28", new trsuperobject(6,8,28,"Ardından Yeşu Ay Kentini ateşe verdi, yakıp yıkıp viraneye çevirdi. Yıkıntıları bugün de duruyor."));
trsuperobject.biblemap.put("b6_8_29", new trsuperobject(6,8,29,"Ay Kralını ağaca asıp akşama dek orada bırakan Yeşu, güneş batarken cesedi ağaçtan indirerek kent kapısının dışına attırdı. Cesedin üzerine taşlardan büyük bir yığın yaptılar. Bu yığın bugün de duruyor."));
trsuperobject.biblemap.put("b6_8_30", new trsuperobject(6,8,30,"Bundan sonra Yeşu Eval Dağında İsrailin Tanrısı RABbe bir sunak yaptı."));
trsuperobject.biblemap.put("b6_8_31", new trsuperobject(6,8,31,"Sunak, RABbin kulu Musanın İsrail halkına verdiği buyruk uyarınca, Musanın Yasa Kitabında yazıldığı gibi yontulmamış, demir alet değmemiş taşlardan yapıldı. RABbe orada yakmalık sunular sundular, esenlik kurbanları kestiler."));
trsuperobject.biblemap.put("b6_8_32", new trsuperobject(6,8,32,"Yeşu Musanın İsrail halkının önünde yazmış olduğu Kutsal Yasanın kopyasını orada taş levhalara yazdı."));
trsuperobject.biblemap.put("b6_8_33", new trsuperobject(6,8,33,"Bütün İsrailliler, ileri gelenleriyle, görevlileriyle ve hakimleriyle birlikte -yabancılar dahil- RABbin Antlaşma Sandığının iki yanında, yüzleri, sandığı taşıyan Levili kâhinlere dönük olarak dizildiler. Halkın yarısı sırtını Gerizim Dağına, öbür yarısı da Eval Dağına verdi. Çünkü RABbin kulu Musa kutsanmaları için bu şekilde durmalarını daha önce buyurmuştu."));
trsuperobject.biblemap.put("b6_8_34", new trsuperobject(6,8,34,"Ardından Yeşu yasanın tümünü, kutsama ve lanetle ilgili bölümleri Yasa Kitabında yazılı olduğu gibi okudu."));
trsuperobject.biblemap.put("b6_8_35", new trsuperobject(6,8,35,"Böylece Yeşu'nun, yabancıların da aralarında bulunduğu kadınlı, çocuklu bütün İsrail topluluğuna, Musa'nın buyruklarından okumadığı tek bir söz kalmadı."));
trsuperobject.biblemap.put("b6_9_1", new trsuperobject(6,9,1,"Şeria Irmağının ötesinde, dağlık bölgede, Şefelada ve Lübnana kadar uzanan Akdeniz kıyısındaki bütün krallar -Hitit, Amor, Kenan, Periz, Hiv ve Yevus kralları- olup bitenleri duyunca,"));
trsuperobject.biblemap.put("b6_9_2", new trsuperobject(6,9,2,"Yeşuya ve İsrail halkına karşı hep birlikte savaşmak için bir araya geldiler."));
trsuperobject.biblemap.put("b6_9_3", new trsuperobject(6,9,3,"Givon halkı ise Yeşunun Eriha ve Ay kentlerine yaptıklarını duyunca"));
trsuperobject.biblemap.put("b6_9_4", new trsuperobject(6,9,4,"hileye başvurdu. Kendilerine elçi süsü vererek eşeklerinin sırtına yıpranmış heybeler, eski, yırtık ve yamalı şarap tulumları yüklediler."));
trsuperobject.biblemap.put("b6_9_5", new trsuperobject(6,9,5,"Ayaklarında yıpranmış, yamalı çarıklar, sırtlarında da eski püskü giysiler vardı. Azık torbalarındaki bütün ekmekler kurumuş, küflenmişti."));
trsuperobject.biblemap.put("b6_9_6", new trsuperobject(6,9,6,"Adamlar Gilgaldaki ordugaha, Yeşunun yanına gittiler. Ona ve İsrail halkına, ‹‹Uzak bir ülkeden geldik›› dediler, ‹‹Bizimle bir barış antlaşması yapmanızı istiyoruz.››"));
trsuperobject.biblemap.put("b6_9_7", new trsuperobject(6,9,7,"Ama İsrailliler Hivlilere, ‹‹Sizinle neden antlaşma yapalım?›› diye karşılık verdiler, ‹‹Belki de yakınımızda yaşıyorsunuz.››"));
trsuperobject.biblemap.put("b6_9_8", new trsuperobject(6,9,8,"Givonlular Yeşuya, ‹‹Biz senin kullarınız›› dediler. Yeşu, ‹‹Kimsiniz, nereden geliyorsunuz?›› diye sordu."));
trsuperobject.biblemap.put("b6_9_9", new trsuperobject(6,9,9,"Onlar da, ‹‹Çok uzak bir ülkeden kalkıp geldik›› dediler. ‹‹Çünkü Tanrın RABbin ününü duyduk. Tanrınla ilgili haberleri, Mısırda yaptığı her şeyi,"));
trsuperobject.biblemap.put("b6_9_10", new trsuperobject(6,9,10,"Şeria Irmağının ötesindeki Amorlu iki krala, Heşbon Kralı Sihona ve Aştarotta egemenlik süren Başan Kralı Oga neler yaptığını da duyduk."));
trsuperobject.biblemap.put("b6_9_11", new trsuperobject(6,9,11,"Bunun üzerine önderlerimiz ve ülkemizin bütün halkı bize şöyle dediler: ‹Onları karşılamak için yanınıza yiyecek alıp yola çıkın ve onlara, biz sizin kullarınızız; bunun için bizimle bir barış antlaşması yapmanızı istiyoruz deyin.›"));
trsuperobject.biblemap.put("b6_9_12", new trsuperobject(6,9,12,"Size gelmek için yola çıktığımız gün azık olarak evden aldığımız şu ekmekler sıcacıktı. Bakın şimdi, kurumuş, küflenmişler."));
trsuperobject.biblemap.put("b6_9_13", new trsuperobject(6,9,13,"Şarap doldurduğumuz şu tulumlar yeniydi, bakın nasıl sıyrılıp yırtılmış. Bunca yol geldiğimiz için giysilerimiz ve çarıklarımız yıprandı.››"));
trsuperobject.biblemap.put("b6_9_14", new trsuperobject(6,9,14,"İsrailliler, RABbe danışmadan Givonluların sunduğu yiyecekleri aldılar."));
trsuperobject.biblemap.put("b6_9_15", new trsuperobject(6,9,15,"Yeşu da onları sağ bırakacağına söz verip onlarla bir barış antlaşması yaptı. Topluluğun önderleri de antlaşmaya bağlı kalacaklarına ant içtiler."));
trsuperobject.biblemap.put("b6_9_16", new trsuperobject(6,9,16,"Ne var ki, antlaşmadan üç gün sonra Givonluların yakında, komşu topraklarda yaşadıklarını öğrendiler."));
trsuperobject.biblemap.put("b6_9_17", new trsuperobject(6,9,17,"Bunun üzerine yola çıkıp üç gün sonra onların kentlerine vardılar. Bu kentler Givon, Kefira, Beerot ve Kiryat-Yearimdi."));
trsuperobject.biblemap.put("b6_9_18", new trsuperobject(6,9,18,"Ancak İsrailliler bunlara dokunmadılar. Çünkü topluluğun önderleri, İsrailin Tanrısı RAB adına ant içmişlerdi. Bu yüzden topluluk önderlere karşı söylenmeye başladı."));
trsuperobject.biblemap.put("b6_9_19", new trsuperobject(6,9,19,"Önderler ise, ‹‹Biz İsrailin Tanrısı RAB adına ant içtik; bu yüzden onlara el süremeyiz›› diye karşılık verdiler,"));
trsuperobject.biblemap.put("b6_9_20", new trsuperobject(6,9,20,"‹‹Ant içtiğimiz için onları sağ bırakacağız; yoksa Tanrının gazabına uğrarız.››"));
trsuperobject.biblemap.put("b6_9_21", new trsuperobject(6,9,21,"Sonra halka, ‹‹Onları sağ bırakalım›› dediler, ‹‹Ama bütün topluluk için odun kesip su çekmekle görevlendirilsinler.›› Böylece önderler vermiş oldukları sözü tuttular."));
trsuperobject.biblemap.put("b6_9_22", new trsuperobject(6,9,22,"Ardından Yeşu Givonluları çağırıp, ‹‹Yakınımızda yaşadığınız halde neden çok uzaktan geldiğinizi söyleyip bizi aldattınız?›› dedi,"));
trsuperobject.biblemap.put("b6_9_23", new trsuperobject(6,9,23,"‹‹Bunun için artık lanetlisiniz. Hep köle kalacaksınız. Tanrımın Tapınağı için odun kesip su çekeceksiniz.››"));
trsuperobject.biblemap.put("b6_9_24", new trsuperobject(6,9,24,"Givonlular, ‹‹Efendimiz, Tanrın RABbin kulu Musaya verdiği buyruğu duyduk›› diye karşılık verdiler, ‹‹Musaya bütün ülkeyi size vermesini, ülkede yaşayanların hepsini yok etmenizi buyurduğunu duyduk. Sizden çok korktuk, can korkusuyla böyle davrandık."));
trsuperobject.biblemap.put("b6_9_25", new trsuperobject(6,9,25,"Şimdi senin elindeyiz. Sana göre adil ve doğru olanı yap.››"));
trsuperobject.biblemap.put("b6_9_26", new trsuperobject(6,9,26,"Bunun üzerine Yeşu onları İsraillilerin elinden kurtardı, öldürülmelerine izin vermedi."));
trsuperobject.biblemap.put("b6_9_27", new trsuperobject(6,9,27,"O gün onları topluluk için ve gelecekte RAB'bin seçeceği yerde yapılacak RAB'bin sunağı için odun kesip su çekmekle görevlendirdi. Bugün de bu işi yapıyorlar."));
trsuperobject.biblemap.put("b6_10_1", new trsuperobject(6,10,1,"Yeruşalim Kralı Adoni-Sedek, Yeşunun Erihayı ele geçirip kralını ortadan kaldırdığı gibi, Ay Kentini de ele geçirip tümüyle yıktığını, kralını öldürdüğünü, Givon halkının da İsraillilerle bir barış antlaşması yapıp onlarla birlikte yaşadığını duyunca,"));
trsuperobject.biblemap.put("b6_10_2", new trsuperobject(6,10,2,"büyük korkuya kapıldı. Çünkü Givon, kralların yaşadığı kentler gibi büyük bir kentti; Ay Kentinden de büyüktü ve yiğit bir halkı vardı."));
trsuperobject.biblemap.put("b6_10_3", new trsuperobject(6,10,3,"Bu yüzden Yeruşalim Kralı Adoni-Sedek, Hevron Kralı Hoham, Yarmut Kralı Piram, Lakiş Kralı Yafia ve Eglon Kralı Devire şu haberi gönderdi:"));
trsuperobject.biblemap.put("b6_10_4", new trsuperobject(6,10,4,"‹‹Gelin bana yardım edin, Givona saldıralım. Çünkü Givon halkı Yeşu ve İsrail halkıyla bir barış antlaşması yaptı.››"));
trsuperobject.biblemap.put("b6_10_5", new trsuperobject(6,10,5,"Böylece Amorlu beş kral -Yeruşalim, Hevron, Yarmut, Lakiş ve Eglon kralları- ordularını topladılar, hep birlikte gidip Givonun karşısında ordugah kurdular; sonra saldırıya geçtiler."));
trsuperobject.biblemap.put("b6_10_6", new trsuperobject(6,10,6,"Givonlular Gilgalda ordugahta bulunan Yeşuya şu haberi gönderdiler: ‹‹Biz kullarını yalnız bırakma. Elini çabuk tutup yardımımıza gel, bizi kurtar. Çünkü dağlık bölgedeki bütün Amorlu krallar bize karşı birleşti.››"));
trsuperobject.biblemap.put("b6_10_7", new trsuperobject(6,10,7,"Bunun üzerine Yeşu bütün savaşçıları ve yiğit adamlarıyla birlikte Gilgaldan yola çıktı."));
trsuperobject.biblemap.put("b6_10_8", new trsuperobject(6,10,8,"Bu arada RAB Yeşuya, ‹‹Onlardan korkma›› dedi, ‹‹Onları eline teslim ediyorum. Hiçbiri sana karşı koyamayacak.››"));
trsuperobject.biblemap.put("b6_10_9", new trsuperobject(6,10,9,"Gilgaldan çıkıp bütün gece yol alan Yeşu, Amorlulara ansızın saldırdı."));
trsuperobject.biblemap.put("b6_10_10", new trsuperobject(6,10,10,"RAB Amorluları İsraillilerin önünde şaşkına çevirdi. İsrailliler de onları Givonda büyük bir bozguna uğrattılar; Beythorona çıkan yol boyunca, Azeka ve Makkedaya dek kovalayıp öldürdüler."));
trsuperobject.biblemap.put("b6_10_11", new trsuperobject(6,10,11,"RAB İsraillilerden kaçan Amorluların üzerine Beythorondan Azekaya inen yol boyunca gökten iri iri dolu yağdırdı. Yağan dolunun altında can verenler, İsraillilerin kılıçla öldürdüklerinden daha çoktu."));
trsuperobject.biblemap.put("b6_10_12", new trsuperobject(6,10,12,"RABbin Amorluları İsraillilerin karşısında bozguna uğrattığı gün Yeşu halkın önünde RABbe şöyle seslendi:  ‹‹Dur, ey güneş, Givon üzerindeVe ay, sen de Ayalon Vadisinde.››"));
trsuperobject.biblemap.put("b6_10_13", new trsuperobject(6,10,13,"Halk, düşmanlarından öcünü alıncaya dek güneş durdu, ay da yerinde kaldı. Bu olay Yaşar Kitabında da yazılıdır. Güneş, yaklaşık bir gün boyunca göğün ortasında durdu, batmakta gecikti."));
trsuperobject.biblemap.put("b6_10_14", new trsuperobject(6,10,14,"Ne bundan önce, ne de sonra RABbin bir insanın dileğini işittiği o günkü gibi bir gün olmamıştır. Çünkü RAB İsrailden yana savaştı."));
trsuperobject.biblemap.put("b6_10_15", new trsuperobject(6,10,15,"Yeşu bundan sonra İsrail halkıyla birlikte Gilgaldaki ordugaha döndü."));
trsuperobject.biblemap.put("b6_10_16", new trsuperobject(6,10,16,"Amorlu beş kral kaçıp Makkedadaki bir mağarada gizlenmişlerdi."));
trsuperobject.biblemap.put("b6_10_17", new trsuperobject(6,10,17,"Yeşuya, ‹‹Beş kral Makkedadaki bir mağarada gizlenirken bulundu›› diye haber verildi."));
trsuperobject.biblemap.put("b6_10_18", new trsuperobject(6,10,18,"Yeşu, ‹‹Mağaranın ağzına büyük taşlar yuvarlayın, orayı korumak için adamlar görevlendirin›› dedi,"));
trsuperobject.biblemap.put("b6_10_19", new trsuperobject(6,10,19,"‹‹Ama siz durmayın, düşmanı kovalayın; arkadan saldırıp kentlere ulaşmalarına engel olun. Tanrınız RAB onları elinize teslim etmiştir.››"));
trsuperobject.biblemap.put("b6_10_20", new trsuperobject(6,10,20,"Yeşu ve İsrailliler düşmanı çok ağır bir yenilgiye uğratıp tamamını yok ettiler. Kurtulabilenler surlu kentlere sığındı."));
trsuperobject.biblemap.put("b6_10_21", new trsuperobject(6,10,21,"Sonra bütün halk güvenlik içinde Makkedadaki ordugaha, Yeşunun yanına döndü. Hiç kimse ağzını açıp İsraillilere karşı bir şey söyleyemedi."));
trsuperobject.biblemap.put("b6_10_22", new trsuperobject(6,10,22,"Sonra Yeşu adamlarına, ‹‹Mağaranın ağzını açın, beş kralı çıkarıp bana getirin›› dedi."));
trsuperobject.biblemap.put("b6_10_23", new trsuperobject(6,10,23,"Onlar da beş kralı -Yeruşalim, Hevron, Yarmut, Lakiş ve Eglon krallarını- mağaradan çıkarıp Yeşuya getirdiler."));
trsuperobject.biblemap.put("b6_10_24", new trsuperobject(6,10,24,"Krallar getirilince, Yeşu bütün İsrail halkını topladı. Savaşta kendisine eşlik etmiş olan komutanlara, ‹‹Yaklaşın, ayaklarınızı bu kralların boyunları üzerine koyun›› dedi. Komutanlar yaklaşıp ayaklarını kralların boyunları üzerine koydular."));
trsuperobject.biblemap.put("b6_10_25", new trsuperobject(6,10,25,"Yeşu onlara, ‹‹Korkmayın, yılmayın; güçlü ve yürekli olun›› dedi, ‹‹RAB savaşacağınız düşmanların hepsini bu duruma getirecek.››"));
trsuperobject.biblemap.put("b6_10_26", new trsuperobject(6,10,26,"Ardından beş kralı vurup öldürdü ve her birini bir ağaca astı. Akşama dek öylece ağaçlara asılı kaldılar."));
trsuperobject.biblemap.put("b6_10_27", new trsuperobject(6,10,27,"Yeşunun buyruğu üzerine gün batımında kralların cesetlerini ağaçlardan indirdiler, gizlendikleri mağaraya atıp mağaranın ağzını büyük taşlarla kapadılar. Bu taşlar bugün de orada duruyor."));
trsuperobject.biblemap.put("b6_10_28", new trsuperobject(6,10,28,"Yeşu aynı gün Makkedayı aldı, kralını ve halkını kılıçtan geçirdi. Kentte tek canlı bırakmadı, hepsini öldürdü. Makkeda Kralına da Eriha Kralına yaptığının aynısını yaptı."));
trsuperobject.biblemap.put("b6_10_29", new trsuperobject(6,10,29,"Yeşu İsrail halkıyla birlikte Makkedadan Livnanın üzerine yürüyüp kente saldırdı."));
trsuperobject.biblemap.put("b6_10_30", new trsuperobject(6,10,30,"RAB kenti ve kralını İsraillilerin eline teslim etti. Yeşu kentin bütün halkını kılıçtan geçirdi. Tek canlı bırakmadı. Kentin kralına da Eriha Kralına yaptığının aynısını yaptı."));
trsuperobject.biblemap.put("b6_10_31", new trsuperobject(6,10,31,"Bundan sonra Yeşu İsrail halkıyla birlikte Livnadan Lakiş üzerine yürüdü. Kentin karşısında ordugah kurup saldırıya geçti."));
trsuperobject.biblemap.put("b6_10_32", new trsuperobject(6,10,32,"RAB Lakişi İsraillilerin eline teslim etti. Yeşu ertesi gün kenti aldı. Livnada yaptığı gibi, halkı ve kentteki bütün canlıları kılıçtan geçirdi."));
trsuperobject.biblemap.put("b6_10_33", new trsuperobject(6,10,33,"Bu arada Gezer Kralı Horam Lakişe yardıma geldi. Yeşu onu ve ordusunu yenilgiye uğrattı; kimseyi sağ bırakmaksızın hepsini öldürdü."));
trsuperobject.biblemap.put("b6_10_34", new trsuperobject(6,10,34,"İsrail halkıyla birlikte Lakişten Eglon üzerine yürüyen Yeşu, kentin karşısında ordugah kurup saldırıya geçti."));
trsuperobject.biblemap.put("b6_10_35", new trsuperobject(6,10,35,"Kenti aynı gün ele geçirdiler. Lakişte yaptığı gibi, halkı ve kentteki bütün canlıları o gün kılıçtan geçirip yok ettiler."));
trsuperobject.biblemap.put("b6_10_36", new trsuperobject(6,10,36,"Ardından Yeşu İsrail halkıyla birlikte Eglondan Hevron üzerine yürüyüp saldırıya geçti."));
trsuperobject.biblemap.put("b6_10_37", new trsuperobject(6,10,37,"Kenti aldılar, kralını, halkını ve köylerindeki bütün canlıları kılıçtan geçirdiler. Eglonda yaptıkları gibi, herkesi öldürdüler; kimseyi sağ bırakmadılar."));
trsuperobject.biblemap.put("b6_10_38", new trsuperobject(6,10,38,"Bundan sonra Yeşu İsrail halkıyla birlikte geri dönüp Devire saldırdı."));
trsuperobject.biblemap.put("b6_10_39", new trsuperobject(6,10,39,"Kralıyla birlikte Deviri ve köylerini alıp bütün halkı kılıçtan geçirdi; tek canlı bırakmadı, hepsini öldürdü. Hevrona, Livnaya ve kralına ne yaptıysa, Devire ve kralına da aynısını yaptı."));
trsuperobject.biblemap.put("b6_10_40", new trsuperobject(6,10,40,"Böylece Yeşu dağlık bölge, Negev, Şefela ve dağ yamaçları dahil, bütün ülkeyi ele geçirip buralardaki kralların tümünü yenilgiye uğrattı. Hiç kimseyi esirgemedi. İsrailin Tanrısı RABbin buyruğu uyarınca kimseyi sağ bırakmadı, hepsini öldürdü."));
trsuperobject.biblemap.put("b6_10_41", new trsuperobject(6,10,41,"Kadeş-Barneadan Gazzeye kadar, Givona kadar uzanan bütün Goşen bölgesini egemenliği altına aldı."));
trsuperobject.biblemap.put("b6_10_42", new trsuperobject(6,10,42,"Bütün bu kralları ve topraklarını tek bir savaşta ele geçirdi. Çünkü İsrailin Tanrısı RAB İsrailden yana savaşmıştı."));
trsuperobject.biblemap.put("b6_10_43", new trsuperobject(6,10,43,"Ardından Yeşu İsrail halkıyla birlikte Gilgal'daki ordugaha döndü."));
trsuperobject.biblemap.put("b6_11_1", new trsuperobject(6,11,1,"Olup bitenleri duyan Hasor Kralı Yavin, Madon Kralı Yovava, Şimron ve Akşaf krallarına,"));
trsuperobject.biblemap.put("b6_11_2", new trsuperobject(6,11,2,"dağlık kuzey bölgesinde, Kinneret Gölünün güneyindeki Aravada, Şefelada ve batıda Dor Kenti sırtlarındaki krallara,"));
trsuperobject.biblemap.put("b6_11_3", new trsuperobject(6,11,3,"doğu ve batı bölgelerindeki Kenan, Amor, Hitit, Periz halklarına ve dağlık bölgedeki Yevuslularla Hermon Dağının eteğindeki Mispa bölgesinde yaşayan Hivlilere haber gönderdi."));
trsuperobject.biblemap.put("b6_11_4", new trsuperobject(6,11,4,"Bu krallar bütün ordularıyla, kıyıların kumu kadar sayısız askerleriyle, çok sayıdaki at ve savaş arabalarıyla yola çıktılar."));
trsuperobject.biblemap.put("b6_11_5", new trsuperobject(6,11,5,"Bütün bu krallar İsraillilere karşı savaşmak üzere birleşerek Merom suları kıyısına gelip hep birlikte ordugah kurdular."));
trsuperobject.biblemap.put("b6_11_6", new trsuperobject(6,11,6,"Bu arada RAB Yeşuya, ‹‹Onlardan korkma›› diye seslendi, ‹‹Onların hepsini yarın bu saatlerde İsrailin önünde yere sereceğim. Atlarını sakatlayıp savaş arabalarını ateşe ver.››"));
trsuperobject.biblemap.put("b6_11_7", new trsuperobject(6,11,7,"Böylece Yeşu bütün ordusuyla birlikte Merom suları kıyısındaki kralların üzerine beklenmedik bir anda yürüdü ve onlara saldırdı."));
trsuperobject.biblemap.put("b6_11_9", new trsuperobject(6,11,9,"Yeşu, RABbin kendisine buyurduğu gibi yaptı, atlarını sakatladı, savaş arabalarını ateşe verdi."));
trsuperobject.biblemap.put("b6_11_10", new trsuperobject(6,11,10,"Yeşu bundan sonra geri dönüp Hasoru ele geçirdi, Hasor Kralını kılıçla öldürdü. Çünkü Hasor eskiden bütün bu krallıkların başıydı."));
trsuperobject.biblemap.put("b6_11_11", new trsuperobject(6,11,11,"İsrailliler kentteki bütün canlıları kılıçtan geçirip yok ettiler. Soluk alan bir tek kişiyi esirgemediler. Ardından Yeşu Hasoru ateşe verdi."));
trsuperobject.biblemap.put("b6_11_12", new trsuperobject(6,11,12,"Böylece bütün bu kentlerle krallarını ele geçirdi. RABbin kulu Musanın buyruğu uyarınca hepsini kılıçtan geçirip yok etti."));
trsuperobject.biblemap.put("b6_11_13", new trsuperobject(6,11,13,"Ancak, İsrailliler, Yeşunun ateşe verdiği Hasor dışında, tepe üzerinde kurulu kentlerden hiçbirini ateşe vermediler."));
trsuperobject.biblemap.put("b6_11_14", new trsuperobject(6,11,14,"Bu kentlerdeki bütün mal ve hayvanları ganimet olarak aldılar, insanların tümünü ise kılıçtan geçirip öldürdüler; soluk alan bir tek kişiyi esirgemediler."));
trsuperobject.biblemap.put("b6_11_15", new trsuperobject(6,11,15,"RABbin kulu Musa RABden aldığı buyrukları Yeşuya aktarmıştı. Yeşu bunlara uydu ve RABbin Musaya buyurduklarını eksiksiz yerine getirdi."));
trsuperobject.biblemap.put("b6_11_16-17", new trsuperobject(6,11,16-17,"Böylece Yeşu, dağlık bölge, bütün Negev ve Goşen bölgesi, Şefela, Arava ve İsrail dağlarıyla bu dağların etekleri, Seir yönünde yükselen Halak Dağından Hermon Dağının altındaki Lübnan Vadisinde bulunan Baal-Gata varıncaya dek bütün toprakları ele geçirdi. Buraların krallarını yakalayıp öldürdü."));
trsuperobject.biblemap.put("b6_11_18", new trsuperobject(6,11,18,"Yeşu bu krallarla uzun süre savaştı."));
trsuperobject.biblemap.put("b6_11_19", new trsuperobject(6,11,19,"Givonda yaşayan Hivliler dışında, İsraillilerle barış antlaşması yapan bir kent olmadı. İsrailliler öbür kentlerin hepsini savaşarak aldılar."));
trsuperobject.biblemap.put("b6_11_20", new trsuperobject(6,11,20,"Çünkü onları İsraile karşı savaşmaya kararlı yapan RABbin kendisiydi. Böylece RABbin Musaya buyurduğu gibi, İsrailliler onlara acımadı, hepsini öldürüp yok ettiler."));
trsuperobject.biblemap.put("b6_11_21", new trsuperobject(6,11,21,"Yeşu bundan sonra Anaklıların üzerine yürüdü. Onları dağlık bölgeden, Hevron, Devir ve Anavdan, Yahuda ve İsrailin bütün dağlık bölgelerinden söküp attı. Kentleriyle birlikte onları tümüyle yok etti."));
trsuperobject.biblemap.put("b6_11_22", new trsuperobject(6,11,22,"İsraillilerin elindeki topraklarda hiç Anaklı kalmadı. Yalnız Gazze, Gat ve Aşdotta sağ kalanlar oldu."));
trsuperobject.biblemap.put("b6_11_23", new trsuperobject(6,11,23,"RAB'bin Musa'ya söylediği gibi, Yeşu bütün ülkeyi ele geçirdi ve İsrail oymakları arasında mülk olarak bölüştürdü. Böylece savaş sona erdi, ülke barışa kavuştu."));
trsuperobject.biblemap.put("b6_12_1", new trsuperobject(6,12,1,"İsraillilerin bozguna uğrattığı, Şeria Irmağının doğusunda, Aravanın bütün doğusu ile Arnon Vadisinden Hermon Dağına kadar topraklarını ele geçirdiği krallar şunlardır:"));
trsuperobject.biblemap.put("b6_12_2-3", new trsuperobject(6,12,2-3,"Heşbonda oturan Amorluların Kralı Sihon: Krallığı Arnon Vadisi kıyısındaki Aroerden -vadinin ortasından- başlıyor, Ammonluların sınırı olan Yabbuk Irmağına dek uzanıyor, Gilatın yarısını içine alıyordu. Arava bölgesinin doğusu da ona aitti. Burası Kinneret Gölünden Arava -Lut- Gölüne uzanıyor, doğuda Beytyeşimota, güneyde de Pisga Dağının yamaçlarına varıyordu."));
trsuperobject.biblemap.put("b6_12_4", new trsuperobject(6,12,4,"Sağ kalan Refalılardan, Aştarot ve Edreide oturan Başan Kralı Og:"));
trsuperobject.biblemap.put("b6_12_5", new trsuperobject(6,12,5,"Kral Og, Hermon Dağı, Salka, Geşurlularla Maakalıların sınırına kadar bütün Başanı ve Heşbon Kralı Sihonun sınırına kadar uzanan Gilatın yarısını yönetiyordu."));
trsuperobject.biblemap.put("b6_12_7-8", new trsuperobject(6,12,7-8,"Lübnan Vadisindeki Baal-Gattan, Seir yönünde yükselen Halak Dağına kadar Şeria Irmağının batısında bulunan toprakların kralları -Yeşu ve İsraillilerin yenilgiye uğrattığı kralları- şunlardır: -Yeşu, Hitit, Amor, Kenan, Periz, Hiv ve Yevus halklarına ait dağlık bölgeyi, Şefelayı, Arava bölgesini, dağ yamaçlarını, çölü ve Negevi İsrail oymakları arasında mülk olarak bölüştürdü.-"));
trsuperobject.biblemap.put("b6_12_9", new trsuperobject(6,12,9,"Eriha Kralı, Beytel yakınındaki Ay Kentinin Kralı,"));
trsuperobject.biblemap.put("b6_12_10", new trsuperobject(6,12,10,"Yeruşalim Kralı, Hevron Kralı,"));
trsuperobject.biblemap.put("b6_12_11", new trsuperobject(6,12,11,"Yarmut Kralı, Lakiş Kralı,"));
trsuperobject.biblemap.put("b6_12_12", new trsuperobject(6,12,12,"Eglon Kralı, Gezer Kralı,"));
trsuperobject.biblemap.put("b6_12_13", new trsuperobject(6,12,13,"Devir Kralı, Geder Kralı,"));
trsuperobject.biblemap.put("b6_12_14", new trsuperobject(6,12,14,"Horma Kralı, Arat Kralı,"));
trsuperobject.biblemap.put("b6_12_15", new trsuperobject(6,12,15,"Livna Kralı, Adullam Kralı,"));
trsuperobject.biblemap.put("b6_12_16", new trsuperobject(6,12,16,"Makkeda Kralı, Beytel Kralı,"));
trsuperobject.biblemap.put("b6_12_17", new trsuperobject(6,12,17,"Tappuah Kralı, Hefer Kralı,"));
trsuperobject.biblemap.put("b6_12_18", new trsuperobject(6,12,18,"Afek Kralı, Şaron Kralı,"));
trsuperobject.biblemap.put("b6_12_19", new trsuperobject(6,12,19,"Madon Kralı, Hasor Kralı,"));
trsuperobject.biblemap.put("b6_12_20", new trsuperobject(6,12,20,"Şimron-Meron Kralı, Akşaf Kralı,"));
trsuperobject.biblemap.put("b6_12_21", new trsuperobject(6,12,21,"Taanak Kralı, Megiddo Kralı,"));
trsuperobject.biblemap.put("b6_12_22", new trsuperobject(6,12,22,"Kedeş Kralı, Karmeldeki Yokneam Kralı,"));
trsuperobject.biblemap.put("b6_12_23", new trsuperobject(6,12,23,"Dor sırtlarındaki Dor Kralı, Gilgaldaki Goyim Kralı"));
trsuperobject.biblemap.put("b6_12_24", new trsuperobject(6,12,24,"ve Tirsa Kralı. Toplam otuz bir kral."));
trsuperobject.biblemap.put("b6_13_1", new trsuperobject(6,13,1,"Yeşu kocamış, yaşı hayli ilerlemişti. RAB ona, ‹‹Artık yaşlandın, yaşın hayli ilerledi›› dedi, ‹‹Ama mülk olarak alınacak daha çok toprak var."));
trsuperobject.biblemap.put("b6_13_2", new trsuperobject(6,13,2,"‹‹Alınacak topraklar şunlardır: Bütün Filist ve Geşur bölgeleri;"));
trsuperobject.biblemap.put("b6_13_3", new trsuperobject(6,13,3,"Mısırın doğusundaki Şihor Irmağından, kuzeyde Ekron sınırlarına kadar uzanan bölge Kenanlılara ait sayılırdı.- Gazze, Aşdot, Aşkelon, Gat ve Ekron adlı beş Filist beyliği ve Avlıların toprakları;"));
trsuperobject.biblemap.put("b6_13_4", new trsuperobject(6,13,4,"güneyde bütün Kenan toprakları; Afeke, yani Amor sınırına kadar, Saydalılara ait olan Meara;"));
trsuperobject.biblemap.put("b6_13_5", new trsuperobject(6,13,5,"Gevalıların toprakları; Hermon Dağı eteğindeki Baal-Gattan Levo-Hamata kadar doğu yönündeki bütün Lübnan toprakları."));
trsuperobject.biblemap.put("b6_13_6", new trsuperobject(6,13,6,"Lübnandan Misrefot-Mayime dek uzanan dağlık bölgede yaşayanları, bütün Saydalıları İsraillilerin önünden söküp atacağım. Sana buyurduğum gibi, buraları kura ile İsrailliler arasında mülk olarak bölüştür."));
trsuperobject.biblemap.put("b6_13_7", new trsuperobject(6,13,7,"‹‹Bu toprakları şimdiden dokuz oymakla Manaşşe oymağının yarısı arasında mülk olarak bölüştür.››"));
trsuperobject.biblemap.put("b6_13_8", new trsuperobject(6,13,8,"Manaşşe oymağının öbür yarısı ile Ruben ve Gad oymakları, RABbin kulu Musanın Şeria Irmağının doğusundaki toprakları kendilerine vermesiyle mülkten paylarını almışlardı."));
trsuperobject.biblemap.put("b6_13_9", new trsuperobject(6,13,9,"Bu topraklar şunlardır: Arnon Vadisi kıyısında Aroerden vadinin ortasındaki kentle Divona kadar uzanan Medeva Yaylası;"));
trsuperobject.biblemap.put("b6_13_10", new trsuperobject(6,13,10,"Heşbonda egemenlik sürmüş olan Amor Kralı Sihonun Ammon sınırına kadar uzanan bütün kentleri;"));
trsuperobject.biblemap.put("b6_13_11", new trsuperobject(6,13,11,"Gilat, Geşur ve Maaka toprakları, Hermon Dağıyla Salkaya kadar bütün Başan;"));
trsuperobject.biblemap.put("b6_13_12", new trsuperobject(6,13,12,"sağ kalan Refalılardan biri olup Aştarot ve Edreide egemenlik sürmüş olan Kral Ogun Başanda kalan topraklarının tümü. Musanın, krallarını yenilgiye uğratıp ele geçirdiği topraklar bunlardı."));
trsuperobject.biblemap.put("b6_13_13", new trsuperobject(6,13,13,"İsrailliler Geşurluları ve Maakalıları topraklarından sürmediler; bunlar bugün de İsrailliler arasında yaşıyorlar."));
trsuperobject.biblemap.put("b6_13_14", new trsuperobject(6,13,14,"Musa, yalnız Levi oymağına topraktan pay vermedi. RABden aldığı buyruğa göre, Levililerin payı İsrailin Tanrısı RAB için yakılan sunulardı."));
trsuperobject.biblemap.put("b6_13_15", new trsuperobject(6,13,15,"Musanın boy sayısına göre Ruben oymağına verdiği topraklar şunlardır:"));
trsuperobject.biblemap.put("b6_13_16", new trsuperobject(6,13,16,"Arnon Vadisi kıyısında Aroerden vadinin ortasındaki kente kadar uzanan bölgeyle Medevanın çevresindeki yaylanın tümü;"));
trsuperobject.biblemap.put("b6_13_17", new trsuperobject(6,13,17,"Heşbon ve buna bağlı yayladaki bütün kentler; Divon, Bamot-Baal, Beytbaal-Meon,"));
trsuperobject.biblemap.put("b6_13_18", new trsuperobject(6,13,18,"Yahsa, Kedemot, Mefaat,"));
trsuperobject.biblemap.put("b6_13_19", new trsuperobject(6,13,19,"Kiryatayim ve Sivma, vadideki tepede kurulu Seret-Şahar,"));
trsuperobject.biblemap.put("b6_13_20", new trsuperobject(6,13,20,"Beytpeor, Pisga yamaçları, Beytyeşimot,"));
trsuperobject.biblemap.put("b6_13_21", new trsuperobject(6,13,21,"yayladaki kentlerle Heşbonda egemenlik sürmüş olan Amor Kralı Sihonun bütün ülkesi. Musa Sihonu ve Sihonun egemenliği altındaki topraklarda yaşayan Midyan beylerini -Evi, Rekem, Sur, Hur ve Revayı- yenilgiye uğratmıştı."));
trsuperobject.biblemap.put("b6_13_22", new trsuperobject(6,13,22,"Öldürülenler arasında İsraillilerin kılıçtan geçirdiği Beor oğlu falcı Balam da vardı."));
trsuperobject.biblemap.put("b6_13_23", new trsuperobject(6,13,23,"Rubenoğullarının sınırı Şeria Irmağına dayanıyordu. Rubenoğullarına, boy sayısına göre köyleriyle birlikte mülk olarak verilen kentler bunlardı."));
trsuperobject.biblemap.put("b6_13_24", new trsuperobject(6,13,24,"Musa Gad oymağına da boy sayısına göre miras verdi."));
trsuperobject.biblemap.put("b6_13_25", new trsuperobject(6,13,25,"Verdiği topraklar şunlardı: Yazer bölgesi, bütün Gilat kentleri, Rabba yakınındaki Aroere kadar uzanan Ammonlulara ait toprakların yarısı;"));
trsuperobject.biblemap.put("b6_13_26", new trsuperobject(6,13,26,"Heşbondan Ramat-Mispeye ve Betonime, Mahanayimden Devir sınırına kadarki bölge;"));
trsuperobject.biblemap.put("b6_13_27", new trsuperobject(6,13,27,"Şeria Ovasındaki Beytharam, Beytnimra, Sukkot, Safon, Heşbon Kralı Sihonun topraklarından geri kalan bölüm, Kinneret Gölünün güney ucuna kadar uzanan Şeria Irmağının doğu yakası."));
trsuperobject.biblemap.put("b6_13_28", new trsuperobject(6,13,28,"Gadoğullarına, boy sayısına göre köyleriyle birlikte mülk olarak verilen kentler bunlardı."));
trsuperobject.biblemap.put("b6_13_29", new trsuperobject(6,13,29,"Musa, Manaşşe oymağının yarısına boy sayısına göre topraktan miras vermişti."));
trsuperobject.biblemap.put("b6_13_30", new trsuperobject(6,13,30,"Bu topraklar Mahanayimden başlıyor, Başan Kralı Ogun ülkesini -bütün Başanı- ve Yairin Başandaki yerleşim birimlerinin tümünü, yani toplam altmış kenti,"));
trsuperobject.biblemap.put("b6_13_31", new trsuperobject(6,13,31,"Gilatın yarısını, Başan Kralı Ogun egemenliğindeki Aştarot ve Edrei kentlerini içine alıyordu. Buralar, Manaşşe oğlu Makirin soyuna, boy sayısına göre Makiroğullarının yarısına ayrılmıştı."));
trsuperobject.biblemap.put("b6_13_32", new trsuperobject(6,13,32,"Musanın, Erihanın doğusunda, Şeria Irmağının ötesinde kalan Moav ovalarındayken bölüştürdüğü topraklar bunlardır."));
trsuperobject.biblemap.put("b6_13_33", new trsuperobject(6,13,33,"Ama Levi oymağına topraktan pay vermedi. Söz verdiği gibi, onların mirası İsrail'in Tanrısı RAB'bin kendisidir."));
trsuperobject.biblemap.put("b6_14_1", new trsuperobject(6,14,1,"İsraillilerin Kenanda mülk edindiği topraklara gelince, bu topraklar Kâhin Elazar, Nun oğlu Yeşu ve İsrail oymaklarının boy başları tarafından miras olarak İsrailliler arasında bölüştürülmüştür."));
trsuperobject.biblemap.put("b6_14_3", new trsuperobject(6,14,3,"Çünkü Musa iki oymakla yarım oymağın payını Şeria Irmağının doğusunda vermişti. Ama onlarla birlikte Levililere mülkten pay vermemişti."));
trsuperobject.biblemap.put("b6_14_4", new trsuperobject(6,14,4,"Yusufun soyundan gelenler, Manaşşe ve Efrayim diye iki oymak oluşturuyordu. Levililere de yerleşecekleri kentler ve bu kentlerin çevresinde büyük ve küçük baş hayvanlarına ayrılan otlaklar dışında topraktan pay verilmedi."));
trsuperobject.biblemap.put("b6_14_5", new trsuperobject(6,14,5,"İsrailliler, RABbin Musaya verdiği buyruğa göre hareket edip ülkeyi paylaştılar."));
trsuperobject.biblemap.put("b6_14_6", new trsuperobject(6,14,6,"Bu arada Yahudaoğulları Gilgalda bulunan Yeşunun yanına geldiler. Kenizli Yefunne oğlu Kalev Yeşuya şöyle dedi: ‹‹RABbin Kadeş-Barneada Tanrı adamı Musaya senin ve benim hakkımda neler söylediğini biliyorsun."));
trsuperobject.biblemap.put("b6_14_8", new trsuperobject(6,14,8,"Ne var ki, benimle gelmiş olan soydaşlarım halkı korkuya düşürdüler. Ama ben tümüyle Tanrım RABbin yolundan gittim."));
trsuperobject.biblemap.put("b6_14_9", new trsuperobject(6,14,9,"Bu nedenle Musa o gün, ‹Tümüyle Tanrım RABbin yolundan gittiğin için ayak bastığın topraklar sonsuza dek sana ve oğullarına mülk olacak› diye ant içti."));
trsuperobject.biblemap.put("b6_14_10", new trsuperobject(6,14,10,"RAB sözünü tuttu, beni yaşattı. İsrailliler çölden geçerken RABbin Musaya bu sözleri söylediği günden bu yana kırk beş yıl geçti. Şimdi seksen beş yaşındayım."));
trsuperobject.biblemap.put("b6_14_11", new trsuperobject(6,14,11,"Bugün de Musanın beni gönderdiği günkü kadar güçlüyüm. O günkü gibi hâlâ savaşa gidip gelecek güçteyim."));
trsuperobject.biblemap.put("b6_14_12", new trsuperobject(6,14,12,"RABbin o gün söz verdiği gibi, bu dağlık bölgeyi şimdi bana ver. Orada Anaklıların yaşadığını ve surlarla çevrili büyük kentleri olduğunu o gün sen de duymuştun. Belki RAB bana yardım eder de, Onun dediği gibi, onları oradan sürerim.››"));
trsuperobject.biblemap.put("b6_14_13", new trsuperobject(6,14,13,"Yeşu Yefunne oğlu Kalevi kutsadı ve Hevronu ona mülk olarak verdi."));
trsuperobject.biblemap.put("b6_14_14", new trsuperobject(6,14,14,"Böylece Hevron bugün de Kenizli Yefunne oğlu Kalevin mülküdür. Çünkü o, tümüyle İsrailin Tanrısı RABbin yolundan gitti."));
