trsuperobject.biblemap.put("b6_17_4", new trsuperobject(6,17,4,"Bunlar, Kâhin Elazara, Nun oğlu Yeşuya ve önderlere gidip şöyle dediler: ‹‹RAB, Musaya erkek akrabalarımızla birlikte bize de mirastan pay verilmesini buyurdu.›› RABbin bu buyruğu üzerine Yeşu, amcalarıyla birlikte onlara da mirastan pay verdi."));
trsuperobject.biblemap.put("b6_17_5", new trsuperobject(6,17,5,"Böylece Manaşşe oymağına Şeria Irmağının doğusundaki Gilat ve Başan bölgelerinden başka on pay verildi."));
