trsuperobject.biblemap.put("b6_17_10", new trsuperobject(6,17,10,"Güneydeki topraklar Efrayimin, kuzeydeki topraklarsa Manaşşenindi. Böylece Manaşşe bölgesi Akdenizle, kuzeyde Aşerle ve doğuda İssakarla sınırlanmıştı."));
trsuperobject.biblemap.put("b6_17_11", new trsuperobject(6,17,11,"İssakar ve Aşere ait topraklardaki Beytşean ve köyleri, Yivleamla köyleri, Dor, yani Dor sırtları halkıyla köyleri, Eyn- Dor halkıyla köyleri, Taanak halkıyla köyleri, Megiddo halkıyla köyleri Manaşşeye aitti."));
