trsuperobject.biblemap.put("b6_17_13", new trsuperobject(6,17,13,"İsrailliler güçlenince, Kenanlıları sürecek yerde, onları angaryasına çalıştırmaya başladılar."));
trsuperobject.biblemap.put("b6_17_14", new trsuperobject(6,17,14,"Yusufoğulları Yeşuya gelip, ‹‹Mülk olarak bize neden tek kurayla tek pay verdin?›› dediler, ‹‹Çok kalabalığız. Çünkü RAB bizi bugüne dek alabildiğine çoğalttı.››"));
trsuperobject.biblemap.put("b6_17_15", new trsuperobject(6,17,15,"Yeşu, ‹‹O kadar kalabalıksanız ve Efrayimin dağlık bölgesi size dar geliyorsa, Perizlilerin ve Refalıların topraklarındaki ormanlara çıkıp kendinize yer açın›› diye karşılık verdi."));
