trsuperobject.biblemap.put("b6_17_17", new trsuperobject(6,17,17,"Yeşu Yusufoğullarına, Efrayim ve Manaşşe oymaklarına şöyle dedi: ‹‹Kalabalıksınız ve çok güçlüsünüz. Tek kuraya kalmayacaksınız."));
trsuperobject.biblemap.put("b6_17_18", new trsuperobject(6,17,18,"Dağlık bölge de sizin olacak. Orası ormanlıktır, ama ağaçları kesip açacağınız bütün topraklar sizin olur. Kenanlılar güçlüdür, demirden savaş arabalarına sahiptirler ama, yine de onları sürersiniz.››"));
trsuperobject.biblemap.put("b6_18_1", new trsuperobject(6,18,1,"Ülkenin denetimini eline geçiren İsrail topluluğu Şiloda bir araya geldi. Orada Buluşma Çadırını kurdular."));
