trsuperobject.biblemap.put("b6_18_4", new trsuperobject(6,18,4,"‹‹Her oymaktan üçer adam seçin. Onları, ülkeyi incelemeye göndereceğim. Mülk edinecekleri yerlerin sınırlarını belirleyip kayda geçirerek yanıma dönsünler."));
trsuperobject.biblemap.put("b6_18_5", new trsuperobject(6,18,5,"Bu toprakları yedi bölgeye ayırsınlar. Yahuda güney bölgesinde, Yusufoğulları kuzey bölgesinde kalsın."));
trsuperobject.biblemap.put("b6_18_6", new trsuperobject(6,18,6,"Yedi bölgeyi belirleyip kayda geçirdikten sonra, sonucu bana getirin. Burada, Tanrımız RABbin önünde aranızda kura çekeceğim."));
trsuperobject.biblemap.put("b6_18_7", new trsuperobject(6,18,7,"Levililere gelince, onların aranızda payı yoktur; mirasları RAB için kâhinlik yapmaktır. Gad ve Ruben oymaklarıyla Manaşşe oymağının yarısı ise RABbin kulu Musanın Şeria Irmağının doğusunda kendilerine verdiği mülkü almış bulunuyorlar.››"));
trsuperobject.biblemap.put("b6_18_8", new trsuperobject(6,18,8,"Yeşu, toprakları kayda geçirmek için yola çıkmak üzere olan adamlara, ‹‹Gidip toprakları inceleyin, kayda geçirip yanıma dönün›› diye buyurdu, ‹‹Sonra burada, Şiloda, RABbin önünde sizin için kura çekeceğim.››"));
trsuperobject.biblemap.put("b6_18_9", new trsuperobject(6,18,9,"Adamlar yola çıkıp ülkeyi dolaştılar; kent kent, yedi bölge halinde kayda geçirdikten sonra Şiloda, ordugahta bulunan Yeşunun yanına döndüler."));
trsuperobject.biblemap.put("b6_18_10", new trsuperobject(6,18,10,"Yeşu Şiloda RABbin önünde onlar için kura çekti ve toprakları İsrail oymakları arasında bölüştürdü."));
trsuperobject.biblemap.put("b6_18_11", new trsuperobject(6,18,11,"Boy sayısına göre Benyaminoğulları oymağı için kura çekildi. Paylarına düşen bölge Yahudaoğullarıyla Yusufoğullarının toprakları arasında kalıyordu."));
