trsuperobject.biblemap.put("b6_20_6", new trsuperobject(6,20,6,"Bu kişi topluluğun önüne çıkıp yargılanıncaya ve o dönemde görevli başkâhin ölünceye dek o kentte kalmalıdır. Ondan sonra kaçıp geldiği kente, kendi evine dönebilir.››"));
trsuperobject.biblemap.put("b6_20_7", new trsuperobject(6,20,7,"Böylece Naftalinin dağlık bölgesinde bulunan Celiledeki Kedeşi, Efrayimin dağlık bölgesindeki Şekemi ve Yahudanın dağlık bölgesindeki Kiryat-Arbayı -Hevronu- seçtiler."));
trsuperobject.biblemap.put("b6_20_8", new trsuperobject(6,20,8,"Ayrıca Şeria Irmağının kıyısındaki Erihanın doğusunda, Ruben oymağının sınırları içindeki kırsal bölgede bulunan Beser Kentini, Gad oymağının sınırları içinde Gilattaki Ramotu, Manaşşe oymağı sınırları içinde de Başandaki Golanı belirlediler."));
trsuperobject.biblemap.put("b6_20_9", new trsuperobject(6,20,9,"Birini kazayla öldürüp kaçan bir İsrailli'nin ya da İsrailliler arasında yaşayan bir yabancının, topluluğun önünde yargılanmadan öç almak isteyenlerce öldürülmesini önlemek için belirlenen kentler bunlardı."));
trsuperobject.biblemap.put("b6_21_1-2", new trsuperobject(6,21,1-2,"Levili boy başları, Kenan topraklarında, Şiloda, Kâhin Elazar, Nun oğlu Yeşu ve İsrail oymaklarının boy başlarına giderek, ‹‹RAB, Musa aracılığıyla bize oturmak için kentler, hayvanlarımız için de otlaklar verilmesini buyurmuştu›› dediler."));
trsuperobject.biblemap.put("b6_21_3", new trsuperobject(6,21,3,"Bunun üzerine İsrailliler RABbin buyruğu uyarınca kendi paylarından Levililere otlaklarıyla birlikte şu kentleri verdiler:"));
trsuperobject.biblemap.put("b6_21_4", new trsuperobject(6,21,4,"İlk kura Kehat boylarına düştü. Levililerden olan Kâhin Harunun oğullarına kurayla Yahuda, Şimon ve Benyamin oymaklarından on üç kent verildi."));
trsuperobject.biblemap.put("b6_21_5", new trsuperobject(6,21,5,"Geri kalan Kehatoğullarına Efrayim, Dan ve Manaşşe oymağının yarısına ait boylardan alınan on kent kurayla verildi."));
trsuperobject.biblemap.put("b6_21_6", new trsuperobject(6,21,6,"Gerşonoğullarına kurayla İssakar, Aşer, Naftali oymaklarına ait boylardan ve Başanda Manaşşe oymağının yarısından alınan on üç kent verildi."));
