trsuperobject.biblemap.put("b6_21_28-29", new trsuperobject(6,21,28-29,"İssakar oymağından alınan Kişyon, Daverat, Yarmut ve Eyn-Gannim olmak üzere otlaklarıyla birlikte dört kent;"));
trsuperobject.biblemap.put("b6_21_3-031", new trsuperobject(6,21,3-031,"Aşer oymağından alınan Mişal, Avdon, Helkat ve Rehov olmak üzere otlaklarıyla birlikte dört kent;"));
trsuperobject.biblemap.put("b6_21_32", new trsuperobject(6,21,32,"Naftali oymağından alınan ve kazayla adam öldürenler için sığınak kent seçilen Celiledeki Kedeş, Hammot-Dor, Kartan ve otlakları olmak üzere toplam üç kent."));
trsuperobject.biblemap.put("b6_21_33", new trsuperobject(6,21,33,"Boy sayısına göre Gerşonoğullarına otlaklarıyla birlikte verilen kentlerin toplam sayısı on üçü buldu."));
trsuperobject.biblemap.put("b6_21_34", new trsuperobject(6,21,34,"Merarioğulları boylarına, geri kalan Levililere, Zevulun oymağından alınan Yokneam, Karta,"));
trsuperobject.biblemap.put("b6_21_35", new trsuperobject(6,21,35,"Dimna ve Nahalal olmak üzere otlaklarıyla birlikte toplam dört kent;"));
trsuperobject.biblemap.put("b6_21_36-37", new trsuperobject(6,21,36-37,"Ruben oymağından alınan Beser, Yahsa, Kedemot ve Mefaat olmak üzere otlaklarıyla birlikte dört kent;"));
trsuperobject.biblemap.put("b6_21_38-39", new trsuperobject(6,21,38-39,"Gad oymağından alınan ve kazayla adam öldürenler için sığınak kent seçilen Gilattaki Ramot, Mahanayim, Heşbon ve Yazer olmak üzere otlaklarıyla birlikte toplam dört kent verildi."));
trsuperobject.biblemap.put("b6_21_40", new trsuperobject(6,21,40,"Boy sayısına göre Merarioğullarına, yani Levili boyların geri kalanlarına kurayla verilen kentlerin sayısı on ikiydi."));
trsuperobject.biblemap.put("b6_21_41", new trsuperobject(6,21,41,"İsraillilerin toprakları içinde olup otlaklarıyla birlikte Levililere verilen kentlerin toplamı kırk sekizi buluyordu."));
trsuperobject.biblemap.put("b6_21_42", new trsuperobject(6,21,42,"Bu kentlerin hepsinin çevresinde otlakları vardı. Otlaksız kent yoktu."));
trsuperobject.biblemap.put("b6_21_43", new trsuperobject(6,21,43,"Böylece RAB atalarına vermeye ant içtiği bütün ülkeyi İsraillilere vermiş oldu. İsrailliler de ülkeyi mülk edinip buraya yerleştiler."));
trsuperobject.biblemap.put("b6_21_44", new trsuperobject(6,21,44,"RAB atalarına ant içtiği gibi, onları her yönden rahata erdirdi. Düşmanlarından hiçbiri onların önünde tutunamadı. RAB hepsini onların eline teslim etti."));
trsuperobject.biblemap.put("b6_21_45", new trsuperobject(6,21,45,"RAB'bin İsrail halkına verdiği sözlerden hiçbiri boş çıkmadı; hepsi yerine geldi."));
trsuperobject.biblemap.put("b6_22_1", new trsuperobject(6,22,1,"Bundan sonra Yeşu, Ruben ve Gad oymaklarıyla Manaşşe oymağının yarısını topladı."));
trsuperobject.biblemap.put("b6_22_2", new trsuperobject(6,22,2,"Onlara, ‹‹RABbin kulu Musanın size buyurduğu her şeyi yaptınız›› dedi, ‹‹Benim bütün buyruklarımı da yerine getirdiniz."));
trsuperobject.biblemap.put("b6_22_3", new trsuperobject(6,22,3,"Bugüne dek, bunca zaman kardeşlerinizi yalnız bırakmadınız; Tanrınız RABbin sizi yükümlü saydığı buyruğu yerine getirdiniz."));
trsuperobject.biblemap.put("b6_22_4", new trsuperobject(6,22,4,"Görüyorsunuz, Tanrınız RAB, kardeşlerinize söylediği gibi, onları rahata kavuşturdu. Şimdi kalkın, RABbin kulu Musanın, Şeria Irmağının ötesinde size mülk olarak verdiği topraklardaki evlerinize dönün."));
trsuperobject.biblemap.put("b6_22_6", new trsuperobject(6,22,6,"Sonra onları kutsayıp yolcu etti. Onlar da evlerine döndüler."));
trsuperobject.biblemap.put("b6_22_7", new trsuperobject(6,22,7,"Musa Manaşşe oymağının yarısına Başanda toprak vermişti. Yeşu da oymağın öbür yarısına Şeria Irmağının batısında, öbür kardeşleri arasında toprak vermişti. Bu oymakları kutsayıp evlerine gönderirken,"));
trsuperobject.biblemap.put("b6_22_8", new trsuperobject(6,22,8,"‹‹Evlerinize büyük servetle, çok sayıda hayvanla, altın, gümüş, tunç, demir ve çok miktarda giysiyle dönün›› dedi, ‹‹Düşmanlarınızdan elde ettiğiniz ganimeti kardeşlerinizle paylaşın.››"));
trsuperobject.biblemap.put("b6_22_9", new trsuperobject(6,22,9,"Böylece Rubenlilerle Gadlılar ve Manaşşe oymağının yarısı, Kenan topraklarındaki Şilodan, İsraillilerin yanından ayrıldılar; RABbin buyruğu uyarınca, Musa aracılığıyla yurt edindikleri Gilat topraklarına -kendi mülkleri olan topraklara- dönmek üzere yola çıktılar."));
trsuperobject.biblemap.put("b6_22_10", new trsuperobject(6,22,10,"Rubenlilerle Gadlılar ve Manaşşe oymağının yarısı, Şeria Irmağının Kenan topraklarında kalan kesimine varınca, ırmak kıyısında büyük ve gösterişli bir sunak yaptılar."));
trsuperobject.biblemap.put("b6_22_11", new trsuperobject(6,22,11,"Rubenlilerle Gadlılar ve Manaşşe oymağının yarısının Kenan sınırında, Şeria Irmağı kıyısında, İsraillilere ait topraklarda bir sunak yaptıklarını"));
trsuperobject.biblemap.put("b6_22_12", new trsuperobject(6,22,12,"duyan İsrail topluluğu, onlara karşı savaşmak üzere Şiloda toplandı."));
trsuperobject.biblemap.put("b6_22_13", new trsuperobject(6,22,13,"Ardından İsrailliler Kâhin Elazarın oğlu Pinehası Gilat bölgesine, Rubenlilerle Gadlılara ve Manaşşe oymağının yarısına gönderdiler."));
trsuperobject.biblemap.put("b6_22_14", new trsuperobject(6,22,14,"İsrailin her oymağından birer temsilci olmak üzere on oymak önderini de onunla birlikte gönderdiler. Bunların her biri bir İsrail boyunun başıydı."));
trsuperobject.biblemap.put("b6_22_15", new trsuperobject(6,22,15,"Gilat topraklarına, Rubenlilerle Gadlılara ve Manaşşe oymağının yarısına gelen temsilciler şunları bildirdiler:"));
trsuperobject.biblemap.put("b6_22_16", new trsuperobject(6,22,16,"‹‹RABbin topluluğu, ‹Bugün kendinize bir sunak yaparak RABbe başkaldırdınız, Onu izlemekten vazgeçtiniz› diyor, ‹İsrailin Tanrısına karşı bu hainliği nasıl yaparsınız?"));
trsuperobject.biblemap.put("b6_22_17", new trsuperobject(6,22,17,"Peorun günahı bize yetmedi mi? RABbin topluluğu onun yüzünden felakete uğradı. Bugüne dek kendimizi bu günahtan temizleyebilmiş değiliz."));
trsuperobject.biblemap.put("b6_22_18", new trsuperobject(6,22,18,"Bugün RABbi izlemekten vaz mı geçiyorsunuz? Eğer bugün RABbe isyan ederseniz, O da yarın bütün İsrail topluluğuna öfkelenir."));
trsuperobject.biblemap.put("b6_22_19", new trsuperobject(6,22,19,"Eğer size ait olan topraklar murdarsa, RABbin Tapınağının bulunduğu RABbe ait topraklara gelip aramızda mülk edinin. Kendinize, Tanrımız RABbin sunağından başka bir sunak yaparak RABbe ve bize karşı isyan etmeyin."));
trsuperobject.biblemap.put("b6_22_20", new trsuperobject(6,22,20,"Zerah oğlu Akan RABbe adanan ganimete ihanet ettiğinde, bütün İsrail topluluğu RABbin öfkesine uğramadı mı? Akanın günahı yalnız kendisini ölüme götürmekle kalmadı!››"));
trsuperobject.biblemap.put("b6_22_21", new trsuperobject(6,22,21,"Rubenlilerle Gadlılar ve Manaşşe oymağının yarısı, İsrail boy başlarına şöyle karşılık verdiler:"));
trsuperobject.biblemap.put("b6_22_22", new trsuperobject(6,22,22,"‹‹Tanrıların Tanrısı RAB, tanrıların Tanrısı RAB her şeyi biliyor; İsrail de bilecek. Eğer yaptığımızı, RABbe isyan etmek ya da Ona ihanet etmek için yaptıysak, ya RAB, bugün bizi esirgeme!"));
trsuperobject.biblemap.put("b6_22_23", new trsuperobject(6,22,23,"Eğer sunağı, RABbi izlemekten vazgeçip yakmalık sunular ve tahıl ya da esenlik sunuları sunmak için yaptıysak, RAB bizden hesap sorsun."));
trsuperobject.biblemap.put("b6_22_24", new trsuperobject(6,22,24,"Bunu yaparken kaygımız şuydu: Oğullarınız ilerde bizim oğullarımıza, ‹İsrailin Tanrısı RAB ile ne ilginiz var?"));
trsuperobject.biblemap.put("b6_22_25", new trsuperobject(6,22,25,"Ey Rubenliler ve Gadlılar, RAB Şeria Irmağını sizinle bizim aramızda sınır yaptı. Sizin RABde hiçbir payınız yoktur› diyebilir, oğullarımızı RABbe tapmaktan alıkoyabilirler."));
trsuperobject.biblemap.put("b6_22_26", new trsuperobject(6,22,26,"Bu nedenle, kendimize bir sunak yapalım dedik. Yakmalık sunu ya da kurban sunmak için değil,"));
trsuperobject.biblemap.put("b6_22_27", new trsuperobject(6,22,27,"yalnız sizinle bizim aramızda ve bizden sonra gelecek kuşaklar arasında bir tanık olması için yaptık. Böylece RABbin Tapınağında yakmalık sunularla, kurbanlarla ve esenlik sunularıyla RABbe tapınacağız. Oğullarınız da ilerde bizim oğullarımıza, ‹RABde hiçbir payınız yok› diyemeyecekler."));
trsuperobject.biblemap.put("b6_22_28", new trsuperobject(6,22,28,"Şöyle düşündük: İlerde bize ya da gelecek kuşaklarımıza böyle bir şey diyecek olurlarsa, biz de, ‹Atalarımızın RAB için yaptığı sunağın örneğine bakın› deriz. ‹Yakmalık sunu ya da kurban sunmak için değildir bu. Sizinle bizim aramızdaki birliğin tanığıdır.›"));
trsuperobject.biblemap.put("b6_22_29", new trsuperobject(6,22,29,"RABbe isyan etmek, bugün RABbi izlemekten vazgeçmek, yakmalık sunu, tahıl sunusu ya da kurban sunmak için Tanrımız RABbin sunağından, tapınağının önündeki sunaktan başka bir sunak yapmak bizden uzak olsun.››"));
trsuperobject.biblemap.put("b6_22_30", new trsuperobject(6,22,30,"Kâhin Pinehas ve onunla birlikte olan topluluk önderleri, yani İsrailin boy başları, Rubenlilerle Gadlıların ve Manaşşelilerin söylediklerini duyunca hoşnut kaldılar."));
trsuperobject.biblemap.put("b6_22_31", new trsuperobject(6,22,31,"Bunun üzerine Kâhin Elazarın oğlu Pinehas, Rubenlilerle Gadlılara ve Manaşşelilere, ‹‹Şimdi RABbin aramızda olduğunu biliyoruz›› dedi, ‹‹Çünkü Ona ihanet etmediniz. Böylece İsraillileri Onun elinden kurtardınız.››"));
trsuperobject.biblemap.put("b6_22_32", new trsuperobject(6,22,32,"Kâhin Elazarın oğlu Pinehas ve önderler, Rubenlilerle Gadlıların bulunduğu Gilat topraklarından Kenan topraklarına, İsraillilerin yanına dönüp olan biteni anlattılar."));
trsuperobject.biblemap.put("b6_22_33", new trsuperobject(6,22,33,"Anlatılanlardan hoşnut kalan İsrailliler Tanrıya övgüler sundular. Rubenlilerle Gadlıların yaşadıkları toprakların üzerine yürüyüp savaşmaktan ve orayı yakıp yıkmaktan bir daha söz etmediler."));
trsuperobject.biblemap.put("b6_22_34", new trsuperobject(6,22,34,"Rubenliler'le Gadlılar, ‹‹Bu sunak RAB'bin Tanrı olduğuna sizinle bizim aramızda tanıktır›› diyerek sunağa ‹‹Tanık›› adını verdiler."));
trsuperobject.biblemap.put("b6_23_2", new trsuperobject(6,23,2,"Bu nedenle ileri gelenleri, boy başlarını, hakimleri, görevlileri, bütün İsrail halkını topladı. Onlara, ‹‹Kocadım, yaşım hayli ilerledi›› dedi,"));
trsuperobject.biblemap.put("b6_23_3", new trsuperobject(6,23,3,"‹‹Tanrınız RABbin sizin yararınıza bütün bu uluslara neler yaptığını gördünüz. Çünkü sizin için savaşan Tanrınız RABdi."));
trsuperobject.biblemap.put("b6_23_4", new trsuperobject(6,23,4,"İşte Şeria Irmağından gün batısındaki Akdenize dek yok ettiğim bütün bu uluslarla birlikte, geri kalan ulusların topraklarını da kurayla oymaklarınıza mülk olarak böldüm."));
trsuperobject.biblemap.put("b6_23_5", new trsuperobject(6,23,5,"Tanrınız RAB bu ulusları önünüzden püskürtüp sürecektir. Tanrınız RABbin size söz verdiği gibi, onların topraklarını mülk edineceksiniz."));
trsuperobject.biblemap.put("b6_23_6", new trsuperobject(6,23,6,"Musanın Yasa Kitabında yazılı olan her şeyi korumak ve yerine getirmek için çok güçlü olun. Yazılanlardan sağa sola sapmayın."));
trsuperobject.biblemap.put("b6_23_7", new trsuperobject(6,23,7,"Aranızda kalan uluslarla hiçbir ilişkiniz olmasın; ilahlarının adını anmayın; kimseye onların adıyla ant içirmeyin; onlara kulluk edip tapmayın."));
trsuperobject.biblemap.put("b6_23_8", new trsuperobject(6,23,8,"Bugüne dek yaptığınız gibi, Tanrınız RABbe sımsıkı bağlı kalın."));
trsuperobject.biblemap.put("b6_23_9", new trsuperobject(6,23,9,"Çünkü RAB büyük ve güçlü ulusları önünüzden sürdü. Bugüne dek hiçbiri önünüzde tutunamadı."));
trsuperobject.biblemap.put("b6_23_10", new trsuperobject(6,23,10,"Biriniz bin kişiyi kovalayacak. Çünkü Tanrınız RAB, size söylediği gibi, yerinize savaşacak."));
trsuperobject.biblemap.put("b6_23_11", new trsuperobject(6,23,11,"Bunun için Tanrınız RABbi sevmeye çok dikkat edin."));
trsuperobject.biblemap.put("b6_23_12", new trsuperobject(6,23,12,"Çünkü Ona sırt çevirir, sağ kalıp aranızda yaşayan bu uluslarla birlik olur, onlara kız verip onlardan kız alır, onlarla oturup kalkarsanız,"));
trsuperobject.biblemap.put("b6_23_13", new trsuperobject(6,23,13,"iyi bilin ki, Tanrınız RAB bu ulusları artık önünüzden sürmeyecek. Ve sizler Tanrınız RABbin size verdiği bu güzel topraklardan yok oluncaya dek bu uluslar sizin için tuzak, kapan, sırtınızda kırbaç, gözlerinizde diken olacaklar."));
trsuperobject.biblemap.put("b6_23_14", new trsuperobject(6,23,14,"‹‹İşte her insan gibi ben de bu dünyadan göçüp gitmek üzereyim. Bütün varlığınızla ve yüreğinizle biliyorsunuz ki, Tanrınız RABbin size verdiği sözlerden hiçbiri boş çıkmadı; hepsi gerçekleşti, boş çıkan olmadı."));
trsuperobject.biblemap.put("b6_23_15", new trsuperobject(6,23,15,"Tanrınız RABbin size verdiği sözlerin tümü nasıl gerçekleştiyse, Tanrınız RAB verdiği bu güzel topraklardan sizi yok edene dek sözünü ettiği bütün kötülükleri de öylece başınıza getirecektir."));
trsuperobject.biblemap.put("b6_23_16", new trsuperobject(6,23,16,"Tanrınız RAB'bin size buyurduğu antlaşmayı bozarsanız, gidip başka ilahlara kulluk eder, taparsanız, RAB'bin öfkesi size karşı alevlenecek; RAB'bin size verdiği bu güzel ülkeden çabucak yok olup gideceksiniz.››"));
trsuperobject.biblemap.put("b6_24_1", new trsuperobject(6,24,1,"Yeşu İsrail oymaklarının tümünü Şekemde topladıktan sonra, İsrailin ileri gelenlerini, boy başlarını, hakimlerini, görevlilerini yanına çağırdı. Hepsi gelip Tanrının önünde durdular."));
trsuperobject.biblemap.put("b6_24_2", new trsuperobject(6,24,2,"Yeşu bütün halka, ‹‹İsrailin Tanrısı RAB şöyle diyor›› diye söze başladı, ‹‹İbrahimin ve Nahorun babası Terah ve öbür atalarınız eski çağlarda Fırat Irmağının ötesinde yaşar, başka ilahlara kulluk ederlerdi."));
trsuperobject.biblemap.put("b6_24_3", new trsuperobject(6,24,3,"Ama ben atanız İbrahimi ırmağın öte yakasından alıp bütün Kenan topraklarında dolaştırdım; soyunu çoğalttım, ona İshakı verdim."));
trsuperobject.biblemap.put("b6_24_4", new trsuperobject(6,24,4,"İshaka da Yakup ve Esavı verdim. Esava mülk edinmesi için Seir dağlık bölgesini bağışladım. Yakupla oğulları ise Mısıra gittiler."));
trsuperobject.biblemap.put("b6_24_5", new trsuperobject(6,24,5,"Ardından Musa ile Harunu Mısıra gönderdim. Orada yaptıklarımla Mısırlıları felakete uğrattım; sonra sizi Mısırdan çıkardım."));
trsuperobject.biblemap.put("b6_24_6", new trsuperobject(6,24,6,"Evet, atalarınızı Mısırdan çıkardım; gelip denize dayandılar. Mısırlılar savaş arabalarıyla, atlılarıyla atalarınızı Kızıldenize dek kovaladılar."));
trsuperobject.biblemap.put("b6_24_7", new trsuperobject(6,24,7,"Atalarınız bana yakarınca, onlarla Mısırlıların arasına karanlık çöktürdüm. Mısırlıları deniz sularıyla örttüm. Mısırda yaptıklarımı gözlerinizle gördünüz. ‹‹Uzun zaman çölde yaşadınız."));
trsuperobject.biblemap.put("b6_24_8", new trsuperobject(6,24,8,"Sonra sizi Şeria Irmağının ötesinde yaşayan Amorluların topraklarına götürdüm. Size karşı savaştıklarında onları elinize teslim ettim. Topraklarını yurt edindiniz. Onları önünüzden yok ettim."));
trsuperobject.biblemap.put("b6_24_9", new trsuperobject(6,24,9,"Moav Kralı Sippor oğlu Balak, İsraile karşı savaşmaya hazırlandığında, haber gönderip Beor oğlu Balamı size lanet etmeye çağırdı."));
trsuperobject.biblemap.put("b6_24_10", new trsuperobject(6,24,10,"Ama ben Balamı dinlemeyi reddettim. O da sizi tekrar tekrar kutsadı; böylece sizi onun elinden kurtardım."));
trsuperobject.biblemap.put("b6_24_11", new trsuperobject(6,24,11,"Sonra Şeria Irmağını geçip Erihaya geldiniz. Size karşı savaşan Erihalıları, Amor, Periz, Kenan, Hitit, Girgaş, Hiv ve Yevus halklarını elinize teslim ettim."));
trsuperobject.biblemap.put("b6_24_12", new trsuperobject(6,24,12,"Önden gönderdiğim eşekarısı Amorlu iki kralı önünüzden kovdu. Bu işi kılıcınız ya da yayınız yapmadı."));
trsuperobject.biblemap.put("b6_24_13", new trsuperobject(6,24,13,"Böylece, emek vermediğiniz toprakları, kurmadığınız kentleri size verdim. Buralarda yaşıyor, dikmediğiniz bağlardan, zeytinliklerden yiyorsunuz.›› bilinmiyor. Dehşet ya da bir çeşit hastalık anlamına da gelebilir."));
trsuperobject.biblemap.put("b6_24_14", new trsuperobject(6,24,14,"Yeşu, ‹‹Bunun için RABden korkun, içtenlik ve bağlılıkla Ona kulluk edin›› diye devam etti, ‹‹Atalarınızın Fırat Irmağının ötesinde ve Mısırda kulluk ettikleri ilahları atın, RABbe kulluk edin."));
trsuperobject.biblemap.put("b6_24_15", new trsuperobject(6,24,15,"İçinizden RABbe kulluk etmek gelmiyorsa, atalarınızın Fırat Irmağının ötesinde kulluk ettikleri ilahlara mı, yoksa topraklarında yaşadığınız Amorluların ilahlarına mı kulluk edeceksiniz, bugün karar verin. Ben ve ev halkım RABbe kulluk edeceğiz.››"));
trsuperobject.biblemap.put("b6_24_16", new trsuperobject(6,24,16,"Halk, ‹‹RABbi bırakıp başka ilahlara kulluk etmek bizden uzak olsun!›› diye karşılık verdi,"));
trsuperobject.biblemap.put("b6_24_17", new trsuperobject(6,24,17,"‹‹Çünkü bizi ve atalarımızı Mısırda kölelikten kurtarıp oradan çıkaran, gözümüzün önünde o büyük mucizeleri yaratan, bütün yolculuğumuz ve uluslar arasından geçişimiz boyunca bizi koruyan Tanrımız RABdir."));
trsuperobject.biblemap.put("b6_24_18", new trsuperobject(6,24,18,"RAB bu ülkede yaşayan bütün ulusları, yani Amorluları önümüzden kovdu. Biz de Ona kulluk edeceğiz. Çünkü Tanrımız Odur.››"));
