trsuperobject.biblemap.put("b60_5_1-3", new trsuperobject(60,5,1-3,"Bu nedenle aranızdaki ihtiyarlara, onlar gibi bir ihtiyar, Mesihin çektiği acıların tanığı, açığa çıkacak olan yüceliğin paydaşı olarak rica ediyorum: Tanrının size verdiği sürüyü güdün. Zorunluymuş gibi değil, Tanrının istediği gibi gönüllü gözetmenlik yapın. Para hırsıyla değil, gönül rızasıyla, size emanet edilenlere egemenlik taslamadan, sürüye örnek olarak görevinizi yapın."));
trsuperobject.biblemap.put("b60_5_4", new trsuperobject(60,5,4,"Baş Çoban göründüğü zaman yüceliğin solmaz tacına kavuşacaksınız."));
trsuperobject.biblemap.put("b60_5_5", new trsuperobject(60,5,5,"Ey gençler, siz de ihtiyarlara bağımlı olun. Hepiniz birbirinize karşı alçakgönüllülüğü kuşanın. Çünkü,  ‹‹Tanrı kibirlilere karşıdır,  Ama alçakgönüllülere lütfeder.››"));
trsuperobject.biblemap.put("b60_5_6", new trsuperobject(60,5,6,"Uygun zamanda sizi yüceltmesi için, Tanrının kudretli eli altında kendinizi alçaltın."));
trsuperobject.biblemap.put("b60_5_7", new trsuperobject(60,5,7,"Bütün kaygılarınızı Ona yükleyin, çünkü O sizi kayırır."));
trsuperobject.biblemap.put("b60_5_8", new trsuperobject(60,5,8,"Ayık ve uyanık olun. Düşmanınız İblis kükreyen aslan gibi yutacak birini arayarak dolaşıyor."));
trsuperobject.biblemap.put("b60_5_9", new trsuperobject(60,5,9,"Dünyanın her yerindeki kardeşlerinizin de aynı acıları çektiğini bilerek imanda sarsılmadan İblise karşı direnin."));
trsuperobject.biblemap.put("b60_5_10", new trsuperobject(60,5,10,"Sizleri Mesihte sonsuz yüceliğine çağıran ve bütün lütfun kaynağı olan Tanrının kendisi kısa bir süre acı çekmenizden sonra sizi yetkinleştirip pekiştirecek, güçlendirip temellendirecektir."));
trsuperobject.biblemap.put("b60_5_11", new trsuperobject(60,5,11,"Kudret sonsuzlara dek Onun olsun! Amin."));
trsuperobject.biblemap.put("b60_5_12", new trsuperobject(60,5,12,"Kendisini güvenilir bir kardeş saydığım Silvanus aracılığıyla size kısaca yazmış bulunuyorum. Sizi yüreklendiriyor ve sözünü ettiğim lütfun Tanrının gerçek lütfu olduğuna tanıklık ediyorum. Buna bağlı kalın."));
trsuperobject.biblemap.put("b60_5_13", new trsuperobject(60,5,13,"Sizler gibi seçilmiş olan Babildeki kilise ve oğlum Markos size selam ederler."));
trsuperobject.biblemap.put("b60_5_14", new trsuperobject(60,5,14,"Birbirinizi sevgiyle öperek selamlayın. Sizlere, Mesih'e ait olan herkese esenlik olsun."));

